INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '1', '¶ LIVRO da geração de Jesus Cristo, filho de David, filho de Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '2', 'Abraão gerou a Isaque; e Isaque gerou a Jacob; e Jacob gerou a Judas e a seus irmãos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '3', 'E Judas gerou de Tamar a Fares e a Zara; e Fares gerou a Esrom; e Esrom gerou a Aarão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '4', 'E Aarão gerou a Aminadab; e Aminadab gerou a Naasson; e Naasson gerou a Salmon;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '5', 'E Salmon gerou de Racab a Booz, e Booz gerou de Rut a Obed; e Obed gerou a Jessé;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '6', 'E Jessé gerou ao rei David; e o rei David gerou a Salomão da que foi mulher de Urias;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '7', 'E Salomão gerou a Roboão; e Roboão gerou a Abias; e Abias gerou a Asafe;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '8', 'E Asafe gerou a Josafá; e Josafá gerou a Jorão; e Jorão gerou a Ozias;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '9', 'E Ozias gerou a Joatão; e Joatão gerou a Acaz; e Acaz gerou a Ezequias;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '10', 'E Ezequias gerou a Manassés; e Manassés gerou a Amon; e Amon gerou a Josias;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '11', 'E Josias gerou a Jeconias e a seus irmãos na deportação para a Babilónia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '12', 'E, depois da deportação para a Babilónia, Jeconias gerou a Salatiel; e Salatiel gerou a Zorobabel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '13', 'E Zorobabel gerou a Abiud; e Abiud gerou a Eliaquim; e Eliaquim gerou a Azor;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '14', 'E Azor gerou a Sadoque; e Sadoque gerou a Aquim; e Aquim gerou a Eliud;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '15', 'E Eliud gerou a Eleazar; e Eleazar gerou a Matan; e Matan gerou a Jacob;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '16', 'E Jacob gerou a José, marido de Maria, da qual nasceu Jesus que se chama o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '17', 'De sorte que todas as gerações desde Abraão até David, são catorze gerações; e desde David até à deportação para a Babilónia, catorze gerações; e desde a deportação para a Babilónia até Cristo, catorze gerações;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '18', 'Ora o nascimento de Jesus Cristo foi assim: Estando Maria, sua mãe desposada com José, antes de se ajuntarem, achou-se ter concebido do Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '19', 'Então José, seu marido, como era justo, e a não queria infamar, intentou deixá-la secretamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '20', 'E, projectando ele isto, eis que em sonho lhe apareceu um anjo do SENHOR, dizendo: José, filho de David, não temas receber a Maria tua mulher, porque o que nela está gerado é do Espírito Santo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '21', 'E dará à luz um filho e chamarás o seu nome Jesus; porque ele salvará o seu povo dos seus pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '22', 'Tudo isto aconteceu para que se cumprisse o que foi dito da parte do SENHOR, pelo profeta que diz:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '23', 'Eis que a virgem conceberá e dará à luz um filho, e chamá-lo-ão pelo nome de Emanuel, que traduzido é: Deus connosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '24', 'E José, despertando do sonho, fez como o anjo do SENHOR lhe ordenara, e recebeu a sua mulher;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '1', '25', 'E não a conheceu até que deu à luz seu filho, o primogénito, e pôs-lhe por nome Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '1', 'E, TENDO nascido Jesus, em Belém de Judeia, no tempo do rei Herodes, eis que uns magos vieram do oriente a Jerusalém,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '2', 'Dizendo: Onde está aquele que é nascido rei dos judeus? Porque vimos a sua estrela no oriente e viemos a adorá-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '3', 'E o rei Herodes, ouvindo isto, perturbou-se, e toda Jerusalém com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '4', 'E, congregados todos os príncipes dos sacerdotes, e os escribas do povo, perguntou-lhes onde havia de nascer o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '5', 'E eles lhe disseram: Em Belém de Judeia; porque assim está escrito pelo profeta');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '6', 'E tu, Belém, terra de Judá, de modo nenhum és a menor entre as capitais de Judá; porque de ti sairá o Guia que há-de apascentar o meu povo de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '7', 'Então Herodes, chamando secretamente os magos, inquiriu exactamente deles acerca do tempo em que a estrela lhes aparecera.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '8', 'E, enviando-os a Belém, disse: Ide, e perguntai diligentemente pelo menino, e, quando o achardes, participai-mo, para que também eu vá e o adore.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '9', 'E, tendo eles ouvido o rei, partiram; e eis que a estrela, que tinham visto no oriente, ia adiante deles, até que, chegando, se deteve sobre o lugar onde estava o menino.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '10', 'E, vendo eles a estrela, alegraram-se muito com grande alegria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '11', 'E, entrando na casa acharam o menino com Maria sua mãe, e, prostrando-se, o adoraram; e, abrindo os seus tesouros, lhe ofertaram dádivas: ouro, incenso e mirra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '12', 'E, sendo por divina revelação avisados em sonhos para que não voltassem para junto de Herodes, partiram para a sua terra por outro caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '13', 'E, tendo-se eles retirado, eis que o anjo do SENHOR apareceu a José em sonhos, dizendo: Levanta-te, e toma o menino e sua mãe, e foge para o Egipto, e demora-te lá até que eu te diga: porque Herodes há-de procurar o menino para o matar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '14', 'E, levantando-se ele, tomou o menino e sua mãe de noite, e foi para o Egipto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '15', 'E esteve lá até á morte de Herodes, para que se cumprisse o que foi dito da parte do SENHOR pelo profeta que diz: Do Egipto chamei o meu Filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '16', 'Então Herodes, vendo que tinha sido iludido pelos magos, irritou-se muito, e mandou matar todos os meninos que havia em Belém, e em todos os seus contornos, de dois anos para baixo, segundo o tempo que diligentemente inquirira dos magos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '17', 'Então se cumpriu o que foi dito pelo profeta Jeremias, que diz:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '18', 'Em Ramá se ouviu uma voz, lamentação, choro e grande pranto: Raquel chorando os seus filhos, e não querendo ser consolada, porque já não existem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '19', 'Morto porém Herodes, eis que o anjo do SENHOR apareceu num sonho a José no Egipto,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '20', 'Dizendo: Levanta-te, e toma o menino e sua mãe, e vai para a terra de Israel; porque já estão mortos os que procuravam a morte do menino.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '21', 'Então ele se levantou, e tomou o menino e sua mãe, e foi para a terra de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '22', 'E, ouvindo que Arquelau reinava na Judeia em lugar de Herodes, seu pai, receou ir para lá: mas avisado em sonhos por divina revelação, foi para as partes da Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '2', '23', 'E chegou, e habitou numa cidade chamada Nazaré, para que se cumprisse o que fora dito pelos profetas: Ele será chamado Nazareno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '1', 'E NAQUELES dias apareceu João Baptista pregando no deserto da Judeia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '2', 'E dizendo: Arrependei-vos, porque é chegado o reino dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '3', 'Porque este é o anunciado pelo profeta Isaías, que disse: Voz do que clama no deserto: preparai o caminho do SENHOR, endireitai as suas veredas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '4', 'E este João tinha o seu vestido de pêlos de camelo, e um cinto de couro em torno de seus lombos; e alimentava-se de gafanhotos e de mel silvestre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '5', 'Então ia ter com ele Jerusalém, e toda a Judeia, e toda a província adjacente ao Jordão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '6', 'E eram por ele baptizados no rio Jordão, confessando os seus pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '7', 'E, vendo ele muitos dos fariseus e dos saduceus, que vinham ao seu baptismo, dizia-lhes: Raça de víboras, quem vos ensinou a fugir da ira futura?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '8', 'Produzi pois frutos dignos de arrependimento;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '9', 'E não presumais de vós mesmos, dizendo: Temos por pai a Abraão; porque eu vos digo que mesmo destas pedras Deus pode suscitar filhos a Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '10', 'E também agora está posto o machado à raiz das árvores; toda a árvore, pois, que não produz bom fruto, é cortada e lançada no fogo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '11', 'E eu, em verdade, vos baptizo com água, para o arrependimento; mas aquele que vem após mim é mais poderoso do que eu; cujas alparcas não sou digno de levar; ele vos baptizará com o Espírito Santo, e com fogo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '12', 'Em sua mão tem a pá, e limpará a sua eira, e recolherá no celeiro o seu trigo, e queimará a palha com fogo que nunca se apagará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '13', 'Então veio Jesus da Galileia ter com João, junto do Jordão, para ser baptizado por ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '14', 'Mas João opunha-se-lhe, dizendo: Eu careço de ser baptizado por ti, e vens tu a mim?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '15', 'Jesus, porém, respondendo, disse-lhe: Deixa por agora, porque assim nos convém cumprir toda a justiça. Então ele o permitiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '16', 'E, sendo Jesus baptizado, saiu logo da água, e eis que se lhe abriram os céus, e viu o Espírito de Deus descendo como pomba e vindo sobre ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '3', '17', 'E eis que uma voz dos céus, dizia: Este é o meu Filho amado, em quem me comprazo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '1', 'ENTÃO foi conduzido Jesus pelo Espírito ao deserto, para ser tentado pelo diabo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '2', 'E, tendo jejuado quarenta dias, e quarenta noites, depois teve fome;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '3', 'E chegando-se a ele o tentador, disse: Se tu és o Filho de Deus, manda que estas pedras se tornem em pães.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '4', 'Ele, porém, respondendo, disse: Está escrito: Nem só de pão viverá o homem, mas de toda a palavra que sai da boca de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '5', 'Então o diabo o transportou à cidade santa, e colocou-o sobre o pináculo do templo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '6', 'E disse-lhe: Se tu és o Filho de Deus, lança-te de aqui abaixo; porque está escrito: Que aos seus anjos dará ordens a teu respeito; e tomar-te-ão nas mãos, para que nunca tropeces em alguma pedra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '7', 'Disse-lhe Jesus: Também está escrito: Não tentarás o SENHOR teu Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '8', 'Novamente o transportou o diabo a um monte muito alto; e mostrou-lhe todos os reinos do mundo, e a glória deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '9', 'E disse-lhe: Tudo isto te darei se, prostrado, me adorares.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '10', 'Então disse-lhe Jesus: Vai-te, Satanás, porque está escrito: Ao SENHOR, teu Deus adorarás, e só a ele servirás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '11', 'Então o diabo o deixou; e, eis que chegaram os anjos e o serviram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '12', 'Jesus, porém, ouvindo que João estava preso, voltou para a Galileia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '13', 'E, deixando Nazaré, foi habitar em Cafarnaum, cidade marítima, nos confins de Zabulon e Naftali;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '14', 'Para que se cumprisse o que foi dito pelo profeta Isaías, que diz:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '15', 'A terra de Zabulon, e a terra de Naftali, junto ao caminho do mar, além do Jordão, a Galileia das nações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '16', 'O povo, que estava assentado em trevas, viu uma grande luz; e aos que estavam assentados na região e sombra da morte a luz raiou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '17', 'Desde então começou Jesus a pregar, e a dizer: Arrependei-vos, porque é chegado o reino dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '18', 'E Jesus, andando junto ao mar da Galileia, viu a dois irmãos, Simão, chamado Pedro, e André, os quais lançavam as redes ao mar, porque eram pescadores;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '19', 'E disse-lhes: Vinde após mim, e eu vos farei pescadores de homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '20', 'Então eles, deixando logo as redes, seguiram-no.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '21', 'E, adiantando-se dali, viu outros dois irmãos, Tiago, filho de Zebedeu, e João, seu irmão, num barco com seu pai Zebedeu, consertando as redes; e chamou-os;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '22', 'Eles, deixando imediatamente o barco e seu pai, seguiram-no.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '23', 'E percorria Jesus toda a Galileia, ensinando nas suas sinagogas e pregando o Evangelho do reino, e curando todas as enfermidades e moléstias entre o povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '24', 'E a sua fama correu por toda a Síria, e traziam-lhe todos os que padeciam, acometidos de várias enfermidades e tormentos, os endemoninhados, os lunáticos, e os paralíticos, e ele os curava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '4', '25', 'E seguia-o uma grande multidão da Galileia, de Decápolis, de Jerusalém, da Judeia, e de além do Jordão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '1', 'E JESUS, vendo a multidão, subiu a um monte, e, assentando-se, aproximaram-se dele os seus discípulos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '2', 'E, abrindo a sua boca, os ensinava, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '3', 'Bem-aventurados os pobres de espírito, porque deles é o reino dos céus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '4', 'Bem-aventurados os que choram, porque eles serão consolados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '5', 'Bem-aventurados os mansos, porque eles herdarão a terra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '6', 'Bem-aventurados os que têm fome e sede de justiça, porque eles serão fartos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '7', 'Bem-aventurados os misericordiosos, porque eles alcançarão misericórdia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '8', 'Bem-aventurados os limpos de coração, porque eles verão a Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '9', 'Bem-aventurados os pacificadores, porque eles serão chamados filhos de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '10', 'Bem-aventurados os que sofrem perseguição por causa da justiça, porque deles é o reino dos céus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '11', 'Bem-aventurados sois vós, quando vos injuriarem e perseguirem, e mentindo, disserem todo o mal contra vós por minha causa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '12', 'Exultai e alegrai-vos, porque é grande o vosso galardão nos céus; porque assim perseguiram os profetas que foram antes de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '13', 'Vós sois o sal da terra; e se o sal for insípido, com que se há-de salgar? Para nada mais presta senão para se lançar fora, e ser pisado pelos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '14', 'Vós sois a luz do mundo; não se pode esconder uma cidade edificada sobre um monte;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '15', 'Nem se acende a candeia e se coloca debaixo do alqueire, mas no velador, e dá luz a todos que estão na casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '16', 'Assim resplandeça a vossa luz diante dos homens, para que vejam as vossas boas obras e glorifiquem a vosso Pai, que está nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '17', 'Não cuideis que vim destruir a lei ou os profetas: não vim abrogar, mas cumprir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '18', 'Porque em verdade vos digo que, até que o céu e a terra passem, nem um jota ou um til se omitirá da lei, sem que tudo seja cumprido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '19', 'Qualquer pois que violar um destes mais pequenos mandamentos, e assim ensinar aos homens, será chamado o menor no reino dos céus; aquele, porém, que os cumprir e ensinar será chamado grande no reino dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '20', 'Porque vos digo que, se a vossa justiça não exceder a dos escribas e fariseus, de modo nenhum entrareis no reino dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '21', 'Ouvistes que foi dito aos antigos: Não matarás; mas qualquer que matar será réu de juízo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '22', 'Eu, porém, vos digo que qualquer que, sem motivo, se encolerizar contra seu irmão, será réu de juízo; e qualquer que disser a seu irmão: Raca, será réu do sinédrio; e qualquer que lhe disser: Louco, será réu do fogo do inferno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '23', 'Portanto, se trouxeres a tua oferta ao altar, e aí te lembrares de que teu irmão tem alguma coisa contra ti,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '24', 'Deixa ali diante do altar a tua oferta, e vai reconciliar-te primeiro com teu irmão, e depois vem e apresenta a tua oferta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '25', 'Concilia-te depressa com o teu adversário, enquanto estás no caminho com ele, para que não aconteça que o adversário te entregue ao juiz, e o juiz te entregue ao oficial, e te encerrem na prisão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '26', 'Em verdade te digo que de maneira nenhuma sairás dali enquanto não pagares o último ceitil.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '27', 'Ouvistes que foi dito aos antigos: Não cometerás adultério.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '28', 'Eu, porém, vos digo, que qualquer que atentar numa mulher para a cobiçar, já em seu coração cometeu adultério com ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '29', 'Portanto, se o teu olho direito te escandalizar, arranca-o e atira-o para longe de ti, pois te é melhor que se perca um dos teus membros do que seja todo o teu corpo lançado no inferno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '30', 'E, se a tua mão direita te escandalizar, corta-a e atira-a para longe de ti, porque te é melhor que um dos teus membros se perca do que seja todo o teu corpo lançado no inferno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '31', 'Também foi dito: Qualquer que deixar sua mulher, dê-lhe carta de desquite.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '32', 'Eu, porém, vos digo, que qualquer que repudiar sua mulher, a não ser por causa da prostituição faz que ela cometa adultério, e qualquer que casar com a repudiada comete adultério.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '33', 'Outrossim, ouvistes que foi dito aos antigos: Não perjurarás, mas cumprirás teus juramentos ao SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '34', 'Eu, porém, vos digo que de maneira nenhuma jureis: nem pelo céu, porque é o trono de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '35', 'Nem pela terra, porque é o escabelo de seus pés; nem por Jerusalém, porque é a cidade do grande Rei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '36', 'Nem jurarás pela tua cabeça, porque não podes tornar um cabelo branco ou preto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '37', 'Seja, porém, o vosso falar: Sim, sim; Não, não, porque o que passa disto é de procedência maligna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '38', 'Ouvistes que foi dito: Olho por olho, e dente por dente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '39', 'Eu, porém, vos digo que não resistais ao mal; mas se qualquer te bater na face direita, oferece-lhe também a outra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '40', 'E ao que quiser pleitear contigo, e tirar-te o vestido, larga-lhe também a capa;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '41', 'E, se qualquer te obrigar a caminhar uma milha, vai com ele duas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '42', 'Dá a quem te pedir, e não te desvies daquele que quiser que lhe emprestes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '43', 'Ouvistes que foi dito: Amarás o teu próximo, e aborrecerás o teu inimigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '44', 'Eu, porém vos digo: Amai a vossos inimigos, bendizei os que vos maldizem, fazei bem aos que vos odeiam, e orai pelos que vos maltratam e vos perseguem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '45', 'Para que sejais filhos do vosso Pai que está nos céus; porque faz que o seu sol se levante sobre maus e bons, e a chuva desça sobre justos e injustos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '46', 'Pois, se amardes os que vos amam, que galardão havereis? Não fazem os publicanos também o mesmo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '47', 'E, se saudares unicamente os vossos irmãos, que fazeis de mais? Não fazem os publicanos também assim?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '5', '48', 'Sede vós pois perfeitos, como é perfeito o vosso Pai que está nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '1', 'GUARDAI-VOS de fazer a vossa esmola diante dos homens, para serdes vistos por eles: aliás não tereis galardão junto de vosso Pai, que está nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '2', 'Quando pois deres esmola, não faças tocar trombeta diante de ti, como fazem os hipócritas nas sinagogas e nas ruas, para serem glorificados pelos homens. Em verdade vos digo que já receberam o seu galardão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '3', 'Mas, quando tu deres esmola, não saiba a tua mão esquerda o que faz a tua direita;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '4', 'Para que a tua esmola seja dada ocultamente: teu Pai, que vê em segredo, te recompensará publicamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '5', 'E, quando orares, não sejas como os hipócritas: pois se comprazem em orar em pé nas sinagogas, e às esquinas das ruas, para serem vistos pelos homens. Em verdade vos digo que já receberam o seu galardão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '6', 'Mas tu, quando orares, entra no teu aposento, e, fechando a tua porta, ora a teu Pai que está em oculto; e teu Pai que vê secretamente, te recompensará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '7', 'E, orando, não useis de vãs repetições, como os gentios, que pensam que por muito falarem serão ouvidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '8', 'Não vos assemelheis pois a eles; porque vosso Pai sabe o que vos é necessário, antes de vós lho pedirdes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '9', 'Portanto, vós orareis assim: Pai nosso, que estás nos céus, santificado seja o teu nome;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '10', 'Venha o teu reino, seja feita a tua vontade, assim na terra como no céu;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '11', 'O pão nosso de cada dia nos dá hoje;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '12', 'E perdoa-nos as nossas dívidas, assim como nós perdoamos aos nossos devedores;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '13', 'E não nos induzas à tentação; mas livra-nos do mal; porque teu é o reino, e o poder, e a glória, para sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '14', 'Porque se perdoardes aos homens as suas ofensas, também vosso Pai celestial vos perdoará a vós;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '15', 'Se, porém, não perdoardes aos homens as suas ofensas, também vosso Pai vos não perdoará as vossas ofensas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '16', 'E, quando jejuardes, não vos mostreis contristados como os hipócritas; porque desfiguram os seus rostos, para que aos homens pareça que jejuam. Em verdade vos digo que já receberam o seu galardão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '17', 'Porém tu, quando jejuares, unge a tua cabeça, e lava o teu rosto,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '18', 'Para não pareceres aos homens que jejuas, mas a teu Pai, que está em oculto; e teu Pai, que vê em oculto, te recompensará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '19', 'Não ajunteis tesouros na terra, onde a traça e a ferrugem tudo consomem, e onde os ladrões minam e roubam;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '20', 'Mas ajuntai tesouros no céu, onde nem a traça nem a ferrugem consomem, e onde os ladrões não minam nem roubam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '21', 'Porque onde estiver o vosso tesouro, aí estará também o vosso coração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '22', 'A candeia do corpo são os olhos; de sorte que, se os teus olhos forem bons, todo o teu corpo terá luz;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '23', 'Se, porém, os teus olhos forem maus, o teu corpo será tenebroso. Se, portanto, a luz que em ti há são trevas, quão grandes serão tais trevas!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '24', 'Ninguém pode servir a dois senhores; porque ou há-de odiar um e amar o outro, ou se dedicará a um e desprezará o outro. Não podeis servir a Deus e a Mamon.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '25', 'Por isso vos digo: Não andeis cuidadosos quanto à vossa vida, pelo que haveis de comer ou pelo que haveis de beber; nem, quanto ao vosso corpo, pelo que haveis de vestir. Não é a vida mais do que o mantimento e o corpo mais do que o vestido?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '26', 'Olhai para as aves do céu, que nem semeiam, nem segam, nem ajuntam em celeiros; e vosso Pai celestial as alimenta. Não tendes vós muito mais valor do que elas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '27', 'E qual de vós poderá, com todos os seus cuidados, acrescentar um côvado à sua estatura?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '28', 'E, quanto ao vestido, porque andais solícitos? Olhai para os lírios do campo, como eles crescem: não trabalham nem fiam;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '29', 'E eu vos digo que nem mesmo Salomão, em toda a sua glória, se vestiu como qualquer deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '30', 'Pois, se Deus assim veste a erva do campo, que hoje existe e amanhã é lançada no forno, não vos vestirá muito mais a vós, homens de pouca fé?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '31', 'Não andeis pois inquietos, dizendo: Que comeremos, ou que beberemos, ou com que nos vestiremos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '32', '(Porque todas estas coisas os gentios procuram). De certo vosso Pai celestial bem sabe que necessitais de todas estas coisas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '33', 'Mas buscai primeiro o reino de Deus, e a sua justiça, e todas estas coisas vos serão acrescentadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '6', '34', 'Não vos inquieteis pois pelo dia de amanhã, porque o dia de amanhã cuidará de si mesmo. Basta a cada dia o seu mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '1', 'Não julgueis, para que não sejais julgados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '2', 'Porque com o juízo com que julgardes sereis julgados, e com a medida com que tiverdes medido vos hão-de medir a vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '3', 'E porque reparas tu no argueiro que está no olho do teu irmão, e não vês a trave que está no teu olho?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '4', 'Ou como dirás a teu irmão: Deixa-me tirar o argueiro do teu olho; estando uma trave no teu?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '5', 'Hipócrita, tira primeiro a trave do teu olho, e então cuidarás em tirar o argueiro do olho do teu irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '6', 'Não deis aos cães as coisas santas, nem deites aos porcos as vossas pérolas, não aconteça que as pisem com os pés, e, voltando-se vos despedacem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '7', 'Pedi, e dar-se-vos-á; buscai, e encontrareis; batei, e abrir-se-vos-á.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '8', 'Porque, aquele que pede, recebe; e, o que busca encontra; e, ao que bate, se abre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '9', 'E qual de entre vós é o homem que, pedindo-lhe pão o seu filho, lhe dará uma pedra?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '10', 'E, pedindo-lhe peixe, lhe dará uma serpente?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '11', 'Se, vós, pois, sendo maus, sabeis dar boas coisas aos vossos filhos, quanto mais vosso Pai, que está nos céus, dará bens aos que lhos pedirem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '12', 'Portanto, tudo o que vós quereis que os homens vos façam, fazei-lho também vós, porque esta é a lei e os profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '13', 'Entrai pela porta estreita; porque larga é a porta, e espaçoso o caminho que conduz à perdição, e muitos são os que entram por ela;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '14', 'E porque estreita é a porta, e apertado o caminho que leva à vida, e poucos há que a encontrem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '15', 'Acautelai-vos, porém, dos falsos profetas, que vêm até vós vestidos como ovelhas, mas interiormente são lobos devoradores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '16', 'Por seus frutos os conhecereis. Porventura colhem-se uvas dos espinheiros ou figos dos abrolhos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '17', 'Assim, toda a árvore boa produz bons frutos, e toda a árvore má produz frutos maus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '18', 'Não pode a árvore boa dar maus frutos; nem a árvore má dar frutos bons.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '19', 'Toda a árvore que não dá bom fruto corta-se e lança-se no fogo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '20', 'Portanto, pelos seus frutos os conhecereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '21', 'Nem todo o que me diz: Senhor, Senhor! Entrará no reino dos céus, mas aquele que faz a vontade de meu Pai, que está nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '22', 'Muitos me dirão naquele dia: Senhor, Senhor, não profetizámos nós em teu nome? E em teu nome não expulsámos demónios? E em teu nome não fizemos muitas maravilhas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '23', 'E então lhes direi abertamente: Nunca vos conheci; apartai-vos de mim, vós que praticais a iniquidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '24', 'Todo aquele, pois, que escuta estas minhas palavras, e as pratica, assemelhá-lo-ei ao homem prudente, que edificou a sua casa sobre a rocha;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '25', 'E desceu a chuva, e correram rios, e assopraram ventos, e combateram aquela casa, e não caiu, porque estava edificada sobre a rocha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '26', 'E aquele que ouve estas minhas palavras, e não as cumpre, compará-lo-ei ao homem insensato, que edificou a sua casa sobre a areia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '27', 'E desceu a chuva, e correram rios, e assopraram ventos, e combateram aquela casa, e caiu, e foi grande a sua queda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '28', 'E aconteceu que, concluindo Jesus este discurso, a multidão se admirou da sua doutrina;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '7', '29', 'Porquanto os ensinava como tendo autoridade; e não como os escribas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '1', 'E DESCENDO ele do monte, seguiu-o uma grande multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '2', 'E, eis que veio um leproso, e o adorou, dizendo: Senhor, se quiseres podes tornar-me limpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '3', 'E Jesus, estendendo a mão, tocou-o dizendo: Quero; sê limpo. E logo ficou purificado da lepra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '4', 'Disse-lhe então Jesus: Olha não o digas a alguém, mas vai, mostra-te ao sacerdote, e apresenta a oferta que Moisés determinou, para lhes servir de testemunho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '5', 'E, entrando Jesus em Cafarnaum, chegou junto dele um centurião, rogando-lhe,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '6', 'E dizendo: Senhor, o meu criado jaz em casa paralítico, e violentamente atormentado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '7', 'E Jesus lhe disse: Eu irei, e lhe darei saúde.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '8', 'E o centurião, respondendo, disse: Senhor, não sou digno de que entres debaixo do meu telhado, mas diz somente uma palavra, e o meu criado sarará;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '9', 'Pois também eu sou homem sob autoridade, e tenho soldados às minhas ordens; e digo a este: Vai, e ele vai; e a outro: Vem, e ele vem; e ao meu criado: Faz isto, e ele o faz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '10', 'E maravilhou-se Jesus, ouvindo isto, e disse aos que o seguiam: Em verdade vos digo que nem mesmo em Israel encontrei tanta fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '11', 'Mas eu vos digo que muitos virão do oriente e do ocidente, e assentar-se-ão à mesa com Abraão, e Isaque, e Jacob, no reino dos céus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '12', 'E os filhos do reino serão lançados nas trevas exteriores: ali haverá pranto e ranger de dentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '13', 'Então disse Jesus ao centurião: Vai, e como creste te seja feito. E naquela mesma hora o seu criado sarou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '14', 'E Jesus, entrando em casa de Pedro, viu a sogra deste, jazendo com febre,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '15', 'E tocou-lhe na mão, e a febre a deixou; e levantou-se e serviu-os.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '16', 'E, chegada a tarde, trouxeram-lhe muitos endemoninhados, e ele com a sua palavra expulsou deles os espíritos, e curou todos os que estavam enfermos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '17', 'Para que se cumprisse o que fora dito, pelo profeta Isaías, que diz: Ele tomou sobre si as nossas enfermidades, e levou as nossas doenças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '18', 'E Jesus, vendo em torno de si uma grande multidão, ordenou que passassem para a banda dalém ;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '19', 'E, aproximando-se dele um escriba, disse-lhe: Mestre, onde quer que fores, eu te seguirei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '20', 'E disse Jesus: As raposas têm covis, e as aves do céu têm ninhos, mas o Filho do homem não tem onde reclinar a cabeça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '21', 'E outro de seus discípulos lhe disse: Senhor, permite-me que primeiramente vá sepultar meu pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '22', 'Jesus, porém, disse-lhe: Segue-me, e deixa aos mortos sepultar os seus mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '23', 'E, entrando ele no barco, seus discípulos o seguiram;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '24', 'E eis que no mar se levantou uma tempestade, tão grande que o barco era coberto pelas ondas; ele, porém, estava dormindo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '25', 'E os seus discípulos, aproximando-se, o despertaram, dizendo: Senhor, salva-nos, que perecemos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '26', 'E ele disse-lhes: Porque temeis, homens de pouca fé? Então, levantando-se, repreendeu os ventos e o mar, e seguiu-se uma grande bonança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '27', 'E aqueles homens se maravilharam, dizendo: Que homem é este, que até os ventos e o mar lhe obedecem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '28', 'E, tendo chegado à outra banda, à província dos gergesenos, saíram-lhe ao encontro dois endemoninhados, vindos dos sepulcros; tão ferozes eram que ninguém podia passar por aquele caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '29', 'E eis que clamaram, dizendo: Que temos nós contigo, Jesus Filho de Deus? Vieste aqui atormentar-nos antes de tempo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '30', 'E andava pastando distante deles uma manada de muitos porcos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '31', 'E os demónios rogaram-lhe, dizendo: Se nos expulsas, permite-nos que entremos naquela manada de porcos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '32', 'E ele lhes disse: Ide. E, saindo eles, se introduziram na manada dos porcos; e eis que toda aquela manada de porcos se precipitou no mar por um despenhadeiro, e morreram nas águas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '33', 'Os porqueiros fugiram, e, chegando à cidade, divulgaram tudo o que acontecera aos endemoninhados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '8', '34', 'E eis que toda aquela cidade saiu ao encontro de Jesus, e, vendo-o, rogaram-lhe que se retirasse dos seus termos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '1', 'E, ENTRANDO no barco, passou para a outra banda, e chegou à sua cidade. E eis que lhe trouxeram um paralítico deitado numa cama.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '2', 'E Jesus, vendo a fé deles, disse ao paralítico: Filho, tem bom ânimo perdoados te são os teus pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '3', 'E eis que alguns dos escribas diziam entre si: Ele blasfema.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '4', 'Mas Jesus, conhecendo os seus pensamentos, disse: Porque pensais mal em vossos corações?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '5', 'Pois qual é mais fácil? Dizer: Perdoados te são os teus pecados; ou dizer: Levanta-te e anda?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '6', 'Ora, para que saibais que o Filho do homem tem na terra autoridade para perdoar pecados (disse então ao paralítico): Levanta-te; toma a tua cama, e vai para tua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '7', 'E, levantando-se, foi para sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '8', 'E a multidão, vendo isto, maravilhou-se, e glorificou a Deus, que dera tal poder aos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '9', 'E Jesus, passando adiante dali, viu assentado na alfândega um homem, chamado Mateus, e disse-lhe: Segue-me. E ele, levantando-se, o seguiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '10', 'E aconteceu que, estando ele em casa sentado a mesa, chegaram muitos publicanos e pecadores, e sentaram-se juntamente com Jesus e seus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '11', 'E os fariseus, vendo isto, disseram aos seus discípulos: Porque come o vosso Mestre com os publicanos e pecadores?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '12', 'Jesus, porem, ouvindo, disse-lhes: Não necessitam de médico os sãos, mas sim os doentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '13', 'Ide, porém, e aprendei o que significa: Misericórdia quero, e não sacrifício. Porque eu não vim a chamar os justos, mas os pecadores, ao arrependimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '14', 'Então chegaram ao pé dele os discípulos de João, dizendo: Porque jejuamos nós e os fariseus muitas vezes, e os teus discípulos não jejuam?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '15', 'E disse-lhes Jesus: Podem porventura andar tristes os filhos das bodas, enquanto o esposo está com eles? Dias, porém, virão em que lhes será tirado o esposo, e então jejuarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '16', 'Ninguém deita remendo de pano novo em vestido velho, porque semelhante remendo rompe o vestido, e faz-se maior a rotura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '17', 'Nem se deita vinho novo em odres velhos; aliás rompem-se os odres e entorna-se o vinho, e os odres estragam-se; mas deita-se vinho novo em odres novos, e assim ambos se conservam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '18', 'Dizendo-lhes ele estas coisas, eis que chegou um chefe, e o adorou, dizendo: Minha filha faleceu agora mesmo; mas vem, impõe-lhe a tua mão, e ela viverá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '19', 'E Jesus, levantando-se, seguiu-o, ele e os seus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '20', 'E eis que uma mulher que havia já doze anos padecia de um fluxo de sangue, chegando por detrás dele, tocou a orla do seu vestido;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '21', 'Porque dizia consigo: Se eu somente tocar o seu vestido, ficarei sã.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '22', 'E Jesus, voltando-se, e vendo-a, disse: Tem ânimo, filha, a tua fé te salvou. E imediatamente a mulher ficou sã.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '23', 'E Jesus, chegando a casa daquele chefe, e vendo os instrumentalistas, e o povo em alvoroço,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '24', 'Disse-lhes: Retirai-vos, que a menina não está morta, mas dorme. E riam-se dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '25', 'E, logo que o povo foi posto fora, entrou Jesus, e pegou-lhe na mão, e a menina levantou-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '26', 'E espalhou-se aquela notícia por todo aquele país.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '27', 'E, partindo Jesus dali, seguiram-no dois cegos, clamando, e dizendo: Tem compaixão de nós, filho de David.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '28', 'E, quando chegou a casa, os cegos se aproximaram dele; e Jesus disse-lhes: Credes vós que eu possa fazer isto? Disseram-lhe eles: Sim, Senhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '29', 'Tocou então os olhos deles, dizendo: Seja-vos feito segundo a vossa fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '30', 'E os olhos se lhes abriram. E Jesus ameaçou-os, dizendo: Olhai que ninguém o saiba.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '31', 'Mas, tendo ele saído, divulgaram a sua fama por toda aquela terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '32', 'E, havendo-se eles retirado, trouxeram-lhe um homem mudo e endemoninhado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '33', 'E, expulso o demónio, falou o mudo; e a multidão se maravilhou, dizendo: Nunca tal se viu em Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '34', 'Mas os fariseus diziam: Ele expulsa os demónios pelo príncipe dos demónios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '35', 'E percorria Jesus todas as cidades e aldeias, ensinando nas sinagogas deles, e pregando o evangelho do reino, e curando todas as enfermidades e moléstias entre o povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '36', 'E, vendo a multidão, teve grande compaixão deles, porque andavam desgarrados e errantes, como ovelhas que não têm pastor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '37', 'Então disse aos seus discípulos: A seara é realmente grande, mas poucos os ceifeiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '9', '38', 'Rogai pois ao SENHOR da seara que mande ceifeiros para a sua seara.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '1', 'E, CHAMANDO os seus doze discípulos, deu-lhes poder sobre os espíritos imundos, para os expulsarem, e para curarem toda a enfermidade e todo o mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '2', 'Ora os nomes dos doze apóstolos são estes: O primeiro, Simão, chamado Pedro, e André, seu irmão; Tiago, filho de Zebedeu, e João, seu irmão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '3', 'Filipe e Bartolomeu: Tomé e Mateus, o publicano; Tiago, filho de Alfeu, e Lebeu, apelidado Tadeu .');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '4', 'Simão Cananita, e Judas Iscariotes, aquele que o traiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '5', 'Jesus enviou estes doze, e lhes ordenou, dizendo: Não ireis pelo caminho das gentes, nem entrareis em cidade de samaritanos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '6', 'Mas ide antes às ovelhas perdidas da casa de Israel;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '7', 'E, indo, pregai, dizendo: É chegado o reino dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '8', 'Curai os enfermos, limpai os leprosos, ressuscitai os mortos, expulsai os demónios: de graça recebestes, de graça dai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '9', 'Não possuais ouro, nem prata, nem cobre, em vossos cintos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '10', 'Nem alforges para o caminho, nem duas túnicas, nem alparcas, nem bordão; porque digno é o operário do seu alimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '11', 'E, em qualquer cidade ou aldeia em que entrardes, procurai saber quem nela seja digno, e hospedai-vos aí até que vos retireis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '12', 'E, quando entrardes nalguma casa, saudai-a;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '13', 'E, se a casa for digna, desça sobre ela a vossa paz; mas, se não for digna, torne para vós a vossa paz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '14', 'E, se ninguém vos receber, nem escutar as vossas palavras, saindo daquela casa ou cidade, sacudi o pó dos vossos pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '15', 'Em verdade vos digo que, no dia do juízo, haverá menos rigor para o país de Sodoma e Gomorra do que para aquela cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '16', 'Eis que vos envio como ovelhas no meio de lobos; portanto sede prudentes como as serpentes e símplices como as pombas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '17', 'Acautelai-vos, porém, dos homens; porque eles vos entregarão aos sinédrios, e vos açoitarão nas suas sinagogas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '18', 'E sereis até conduzidos à presença dos governadores e dos reis por causa de mim, para lhes servir de testemunho a eles e aos gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '19', 'Mas, quando vos entregarem: Não vos dê cuidado como, ou o que haveis de falar, porque naquela mesma hora vos será ministrado o que haveis de dizer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '20', 'Porque não sois vós quem falará mas o Espírito de vosso Pai é que fala em vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '21', 'E o irmão entregará à morte o irmão, e o pai o filho; e os filhos se levantarão contra os pais, e os matarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '22', 'E odiados de todos sereis por causa do meu nome: mas aquele que perseverar até ao fim será salvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '23', 'Quando pois vos perseguirem nesta cidade, fugi para outra; porque em verdade vos digo que não acabareis de percorrer as cidades de Israel sem que venha o Filho do homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '24', 'Não é o discípulo mais do que o mestre, nem o servo mais do que o seu senhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '25', 'Basta ao discípulo ser como seu mestre, e ao servo como seu senhor. Se chamaram Belzebu ao pai de família, quanto mais aos seus domésticos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '26', 'Portanto, não os temais; porque nada há encoberto que não haja de revelar-se, nem oculto que não haja de saber-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '27', 'O que vos digo em trevas dizei-o em luz; e o que escutais ao ouvido pregai-o sobre os telhados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '28', 'E não temais os que matam o corpo, e não podem matar a alma temei antes aquele que pode fazer perecer no inferno a alma e o corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '29', 'Não se vendem dois passarinhos por um ceitil? e nenhum deles cairá em terra sem a vontade de vosso Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '30', 'E até mesmo os cabelos da vossa cabeça estão todos contados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '31', 'Não temais pois: mais valeis vós do que muitos passarinhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '32', 'Portanto, qualquer que me confessar diante dos homens, eu o confessarei diante de meu Pai, que está nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '33', 'Mas qualquer que me negar diante dos homens, eu o negarei também diante de meu Pai que está nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '34', 'Não cuideis que vim trazer a paz à terra; Não vim trazer paz, mas espada;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '35', 'Porque eu vim pôr em dissensão o homem contra seu pai, e a filha contra sua mãe, e a nora contra sua sogra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '36', 'E assim os inimigos do homem serão os seus familiares.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '37', 'Quem ama o pai ou a mãe mais do que a mim, não é digno de mim; e quem ama o filho ou a filha mais do que a mim, não é digno de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '38', 'E quem não toma a sua cruz, e não segue após mim, não é digno de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '39', 'Quem achar a sua vida perde-la-à, quem perder a sua vida por amor de mim achá-la-à.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '40', 'Quem vos recebe, a mim me recebe; e quem me recebe a mim, recebe aquele que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '41', 'Quem recebe um profeta em qualidade de profeta, receberá galardão de profeta; quem recebe um justo na qualidade de justo, receberá galardão de justo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '10', '42', 'E qualquer que tiver dado só que seja um copo de água fria a um destes pequenos, em nome de discípulo, em verdade vos digo que de modo algum perderá o seu galardão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '1', 'E, ACONTECEU que, acabando Jesus de dar instruções aos seus doze discípulos, partiu dali, a ensinar e a pregar nas cidades deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '2', 'E João, ouvindo no cárcere falar dos feitos de Cristo, enviou dois dos seus discípulos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '3', 'A dizer-lhe: És tu aquele que havia de vir, ou esperamos outro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '4', 'E Jesus, respondendo, disse-lhes: Ide, e anunciai a João as coisas, que ouvis e vedes;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '5', 'Os cegos vêem, e os coxos andam; os leprosos são limpos, e os surdos ouvem; os mortos são ressuscitados, e aos pobres é anunciado o evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '6', 'E bem-aventurado é aquele que se não escandalizar em mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '7', 'E, partindo eles, começou Jesus a dizer às turbas, a respeito de João: Que fostes ver no deserto? Uma cana agitada pelo vento?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '8', 'Sim, que fostes ver? Um homem ricamente vestido? Os que trajam ricamente estão nas casas dos reis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '9', 'Mas então que fostes ver? Um profeta? Sim, vos digo eu, e muito mais do que profeta;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '10', 'Porque é este de quem está escrito: Eis que diante da tua face envio o meu anjo, que preparará diante de ti o teu caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '11', 'Em verdade vos digo que, entre os que de mulher têm nascido, não apareceu alguém maior do que João Baptista; mas aquele que é o menor no reino dos céus é maior do que ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '12', 'E, desde os dias de João Baptista até agora, se faz violência ao reino dos céus, e pela força se apoderam dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '13', 'Porque todos os profetas e a lei profetizaram até João.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '14', 'E se quereis dar crédito, é este o Elias que havia de vir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '15', 'Quem tem ouvidos para ouvir ouça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '16', 'Mas, a quem assemelharei esta geração? É semelhante aos meninos que se assentam nas praças, e clamam aos seus companheiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '17', 'E dizem: Tocámo-vos flauta, e não dançastes; cantámo-vos lamentações, e não chorastes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '18', 'Porquanto veio João, não comendo nem bebendo, e dizem: Tem demónio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '19', 'Veio o Filho do homem, comendo e bebendo, e dizem: Eis aí um homem comilão e beberrão, amigo de publicanos e pecadores. Mas a sabedoria é justificada por seus filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '20', 'Então começou ele a lançar em rosto às cidades, onde se operou a maior parte dos seus prodígios, o não se haverem arrependido, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '21', 'Ai de ti, Corazim! Ai de ti, Betsaida! Porque, se em Tiro e em Sidon fossem feitos os prodígios que em vós se fizeram, há muito que se teriam arrependido, com saco e com cinza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '22', 'Por isso eu vos digo que haverá menos rigor para Tiro e Sidon, no dia do juízo, do que para vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '23', 'E tu, Cafarnaum, que te ergues até aos céus, serás abatia até aos infernos; porque, se em Sodoma tivessem sido feitos os prodígios que em ti se operaram, teria ela permanecido até hoje.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '24', 'Porém eu vos digo que haverá menos rigor para os de Sodoma, no dia do juízo, do que para ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '25', 'Naquele tempo, respondendo Jesus, disse: Graças te dou, ó Pai, Senhor do céu e da terra, que ocultastes estas coisas aos sábios e entendidos e as revelaste aos pequeninos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '26', 'Sim, ó Pai, porque assim te aprouve.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '27', 'Todas as coisas me foram entregues por meu Pai: e ninguém conhece o Filho, senão o Pai; e ninguém conhece o Pai, senão o Filho, e aquele a quem o Filho o quiser revelar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '28', 'Vinde a mim, todos os que estais cansados e oprimidos, e eu vos aliviarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '29', 'Tomai sobre vós o meu jugo, e aprendei de mim que sou manso e humilde de coração; e encontrareis descanso para as vossas almas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '11', '30', 'Porque o meu jugo é suave e o meu fardo é leve.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '1', 'NAQUELE tempo passou Jesus pelas searas, em um sábado; e os seus discípulos, tendo fome, começaram a colher espigas, e a comer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '2', 'E os fariseus, vendo isto, disseram-lhe: Eis que os teus discípulos fazem o que não é lícito fazer num sábado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '3', 'Ele, porém, lhes disse: Não tendes lido o que fez David, quando teve fome, ele e os que com ele estavam?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '4', 'Como entrou na casa de Deus, e comeu os pães da proposição, que não lhe era lícito comer, nem aos que com ele estavam, mas só aos sacerdotes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '5', 'Ou não tendes lido na lei que, aos sábados, os sacerdotes no templo violam o sábado, e ficam sem culpa?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '6', 'Pois que eu vos digo que está aqui quem é maior do que o templo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '7', 'Mas, se vós soubésseis o que significa: Misericórdia quero, e não sacrifício, não condenaríeis os inocentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '8', 'Porque o Filho do homem até do sábado é Senhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '9', 'E, partindo dali, chegou à sinagoga deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '10', 'E, estava ali um homem que tinha uma das mãos mirrada; e eles, para o acusarem, o interrogaram, dizendo: É lícito curar nos sábados?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '11', 'E ele lhes disse: Qual de entre vós será o homem que tendo uma ovelha, se num sábado ela cair numa cova, não lançará mão dela, e a levantará?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '12', 'Pois quanto mais vale um homem do que uma ovelha? É, por consequência, lícito fazer bem nos sábados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '13', 'Então disse àquele homem: Estende a tua mão. E ele a estendeu, e ficou sã como a outra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '14', 'E os fariseus, tendo saído, formaram conselho contra ele, para o matarem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '15', 'Jesus, sabendo isso, retirou-se dali, e acompanhou-o uma grande multidão de gente, e ele curou a todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '16', 'E recomendava-lhes rigorosamente que o não descobrissem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '17', 'Para que se cumprisse o que fora dito pelo profeta Isaías, que diz:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '18', 'Eis aqui o meu servo, que escolhi, o meu amado, em quem a minha alma se compraz: Porei sobre ele o meu espírito, e anunciará aos gentios o juízo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '19', 'Não contenderá, nem clamará, nem alguém ouvirá pelas ruas a sua voz;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '20', 'Não esmagará a cana quebrada, e não apagará o morrão que fumega, até que faça triunfar o juízo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '21', 'E no seu nome os gentios esperarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '22', 'Trouxeram-lhe então um endemoninhado cego e mudo; e, de tal modo o curou, que o cego e mudo falava e via.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '23', 'E toda a multidão se admirava e dizia: Não é este o Filho de David?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '24', 'Mas os fariseus, ouvindo isto, diziam: Este não expulsa os demónios senão por Belzebu, príncipe dos demónios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '25', 'Jesus, porém, conhecendo os seus pensamentos, disse-lhes: Todo o reino dividido contra si mesmo é devastado; e toda a cidade, ou casa, dividida contra si mesma não subsistirá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '26', 'E, se Satanás expulsa a Satanás, está dividido contra si mesmo; como subsistirá pois o seu reino?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '27', 'E, se eu expulso os demónios por Belzebu, por quem os expulsam então os vossos filhos? Portanto eles mesmos serão, os vossos juízes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '28', 'Mas, se eu expulso os demónios pelo Espírito de Deus, é conseguintemente chegado a vós o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '29', 'Ou, como pode alguém entrar em casa do homem valente, e furtar os seus bens, se primeiro não maniatar o valente, saqueando então a sua casa?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '30', 'Quem não é comigo é contra mim; e quem comigo não ajunta espalha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '31', 'Portanto eu vos digo: Todo o pecado e blasfémia se perdoará aos homens; mas a blasfémia contra o Espírito não será perdoada aos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '32', 'E, se qualquer disser alguma palavra contra o Filho do homem, ser-lhe-á perdoado, mas, se alguém falar contra o Espírito Santo, não lhe será perdoado, nem neste século nem no futuro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '33', 'Ou fazei a árvore boa, e o seu fruto bom, ou fazei a árvore má, e o seu fruto mau; porque pelo fruto se conhece a árvore.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '34', 'Raça de víboras, como podeis vós dizer boas coisas, sendo maus? Pois do que há em abundância no coração, disso fala a boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '35', 'O homem bom tira boas coisas do seu bom tesouro, e o homem mau do mau tesouro tira coisas más.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '36', 'Mas eu vos digo que de toda a palavra ociosa que os homens disserem hão-de dar conta no dia do juízo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '37', 'Porque por tuas palavras serás justificado, e por tuas palavras serás condenado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '38', 'Então alguns dos escribas e dos fariseus tomaram a palavra, dizendo: Mestre, quiséramos ver da tua parte algum sinal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '39', 'Mas ele lhes respondeu, e disse: Uma geração má e adúltera pede um sinal, porém não se lhe dará outro sinal senão o do profeta Jonas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '40', 'Pois, como Jonas esteve três dias e três noites no ventre da baleia, assim estará o Filho do homem três dias e três noites: no seio da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '41', 'Os ninivitas ressurgirão no juízo com esta geração, e a condenarão, porque se arrependeram com a pregação de Jonas. E eis que está aqui quem é mais do que Jonas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '42', 'A rainha do meio dia se levantará no dia do juízo com esta geração, e a condenará; porque veio dos confins da terra para ouvir a sabedoria de Salomão. E eis que está aqui quem é mais do que Salomão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '43', 'E, quando o espírito imundo tem saído do homem, anda por lugares áridos, buscando repouso, e não o encontra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '44', 'Então diz: Voltarei para a minha casa donde saí. E, voltando, acha-a desocupada, varrida e adornada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '45', 'Então vai e leva consigo outros sete espíritos piores do que ele, e, entrando, habitam ali; e são os últimos actos desse homem piores do que os primeiros. Assim acontecerá também a esta geração má.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '46', 'E, falando ele ainda à multidão eis que estavam fora sua mãe e seus irmãos, pretendendo falar-lhe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '47', 'E disse-lhe alguém: Eis que estão ali fora tua mãe e teus irmãos, que querem falar-te.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '48', 'Porém ele, respondendo, disse ao que lhe falara. Quem é minha mãe? E quem são meus irmãos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '49', 'E, estendendo a sua mão para os seus discípulos, disse: Eis aqui minha mãe e meus irmãos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '12', '50', 'Porque, qualquer que fizer a vontade de meu Pai que está nos céus, este é meu irmão, e irmã e mãe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '1', 'TENDO Jesus saído de casa naquele dia, estava assentado junto ao mar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '2', 'E ajuntou-se muita gente ao pé dele, de sorte que, entrando num barco se assentou; e toda a multidão estava em pé na praia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '3', 'E falou-lhe de muitas coisas por parábolas, dizendo: Eis que o semeador saiu a semear.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '4', 'E, quando semeava, uma parte da semente caiu ao pé do caminho, e vieram as aves, e comeram-na;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '5', 'E outra parte caiu em pedregais, onde não havia terra bastante, e logo nasceu, porque não tinha terra funda;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '6', 'Mas, vindo o sol, queimou-se e secou-se, porque não tinha raiz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '7', 'E outra caiu entre espinhos, e os espinhos cresceram, e sufocaram-na.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '8', 'E outra caiu em boa terra, e deu fruto: um a cem, outro a sessenta e outro a trinta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '9', 'Quem tem ouvidos para ouvir, ouça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '10', 'E, acercando-se dele os discípulos, disseram-lhe: Porque lhes falas por parábolas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '11', 'Ele respondendo, disse-lhes: Porque a vós é dado conhecer os mistérios do reino dos céus, mas a eles não lhes é dado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '12', 'Porque àquele que tem, se dará e terá em abundância; mas aquele que não tem, até aquilo que tem lhe será tirado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '13', 'Por isso lhes falo por parábolas; porque eles, vendo, não vêem; e, ouvindo, não ouvem nem compreendem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '14', 'E neles se cumpre a profecia de Isaías, que diz: Ouvindo, ouvireis, mas não compreendereis, e vendo, vereis, mas não percebereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '15', 'Porque o coração deste povo está endurecido, e ouviram de mau grado com seus ouvidos, e fecharam seus olhos; para que não vejam com os olhos, e ouçam com o ouvido, e compreendam com o coração, e se convertam, e eu os cure.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '16', 'Mas bem-aventurados os vossos olhos, porque vêem, e os vossos ouvidos, porque ouvem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '17', 'Porque em verdade vos digo que muitos profetas e justos desejaram ver o que vós vedes, e não o viram; e ouvir o que vós ouvis, e não o ouviram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '18', 'Escutai vós, pois, a parábola do semeador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '19', 'Ouvindo alguém a palavra do reino, e não a entendendo, vem o maligno, e arrebata o que foi semeado no seu coração; este é o que foi semeado ao pé do caminho;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '20', 'Porém o que foi semeado em pedregais é o que ouve a palavra, e logo a recebe com alegria;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '21', 'Mas não tem raiz em si mesmo, antes é de pouca duração; e, chegada a angústia e a perseguição por causa da palavra, logo se ofende;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '22', 'E o que foi semeado entre espinhos é o que ouve a palavra, mas os cuidados deste mundo, e a sedução das riquezas, sufocam a palavra, e fica infrutífera;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '23', 'Mas o que foi semeado em boa terra é o que ouve e compreende a palavra; e dá fruto, e um produz cem, outro sessenta, e outro trinta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '24', 'Propôs-lhes outra parábola, dizendo: O reino dos céus é semelhante ao homem que semeia boa semente no seu campo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '25', 'Mas, dormindo os homens, veio o seu inimigo, e semeou joio no meio do trigo, e retirou-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '26', 'E, quando a erva cresceu e frutificou, apareceu também o joio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '27', 'E os servos do pai de família, indo ter com ele, disseram-lhe: Senhor, não semeaste tu no teu campo boa semente? Porque tem então joio?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '28', 'E ele lhes disse: Um inimigo é quem fez isso. E os servos lhe disseram: Quereis pois que vamos arrancá-lo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '29', 'Porém ele lhes disse: Não; para que ao colher o joio não arranqueis também o trigo com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '30', 'Deixai crescer ambos juntos até à ceifa; e, por ocasião da ceifa, direi aos ceifeiros: Colhei primeiro o joio, e atai-o em molhos para o queimar, mas o trigo ajuntai-o no meu celeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '31', 'Outra parábola lhes propôs, dizendo: O reino dos céus é semelhante ao grão de mostarda que o homem, pegando dele, semeou no seu campo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '32', 'O qual é realmente a mais pequena de todas as sementes; mas, crescendo, é a maior das plantas, e faz-se uma árvore, de sorte que vêm as aves do céu, e se aninham nos seus ramos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '33', 'Outra parábola lhes disse: O reino dos céus é semelhante ao fermento, que uma mulher toma e introduz em três medidas de farinha, até que tudo esteja levedado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '34', 'Tudo isto disse Jesus por parábolas à multidão, e nada lhes falava sem parábolas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '35', 'Para que se cumprisse o que fora dito pelo profeta, que disse: Abrirei em parábolas a minha boca; publicarei coisas ocultas desde a fundação do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '36', 'Então, tendo despedido a multidão, foi Jesus para casa. E chegaram ao pé dele os seus discípulos, dizendo: Explica-nos a parábola do joio do campo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '37', 'E ele, respondendo, disse: O que semeia a boa semente, é o Filho do homem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '38', 'O campo é o mundo; e a boa semente são os filhos do reino, e o joio são os filhos do maligno;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '39', 'O inimigo, que o semeou, é o diabo; e a ceifa é o fim do mundo; e os ceifeiros são os anjos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '40', 'Assim como o joio é colhido e queimado no fogo, assim será na consumação deste mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '41', 'Mandará o Filho do homem os seus anjos, e eles colherão do seu reino tudo o que causa escândalo, e os que cometem iniquidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '42', 'E lançá-los-ão na fornalha de fogo; ali haverá pranto e ranger de dentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '43', 'Então os justos resplandecerão como o sol, no reino de seu Pai. Quem tem ouvidos para ouvir, ouça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '44', 'Também o reino dos céus é semelhante a um tesouro escondido num campo que um homem achou e escondeu; e, pelo gozo dele, vai, vende tudo quanto tem, e compra aquele campo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '45', 'Outrossim o reino dos céus é semelhante ao homem, negociante, que busca boas pérolas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '46', 'E, encontrando uma pérola de grande valor, foi, vendeu tudo quanto tinha, e comprou-a.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '47', 'Igualmente o reino dos céus é semelhante a uma rede lançada ao mar, e que apanha toda a qualidade de peixes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '48', 'E, estando cheia, a puxam para a praia; e, assentando-se, apanham para os cestos os bons; os ruins, porém, lançam fora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '49', 'Assim será na consumação dos séculos: Virão os anjos, e separarão os maus de entre os justos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '50', 'E lançá-los-ão na fornalha de fogo: ali haverá pranto e ranger de dentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '51', 'E disse-lhes Jesus: Entendestes todas estas coisas? Disseram-lhe eles: Sim, Senhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '52', 'E ele disse-lhes: Por isso, todo o escriba instruído acerca do reino dos céus é semelhante a um pai de família, que tira do seu tesouro coisas novas e velhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '53', 'E aconteceu que Jesus, concluindo estas parábolas, se retirou dali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '54', 'E, chegando à sua pátria, ensinava-os na sinagoga deles, de sorte que se maravilharam, e diziam: Donde veio a este a sabedoria, e estas maravilhas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '55', 'Não é este o filho do carpinteiro? E não se chama sua mãe Maria, e seus irmãos Tiago, e José, e Simão, e Judas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '56', 'E não estão entre nós todas as suas irmãs? Donde lhe veio pois tudo isto?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '57', 'E escandalizavam-se nele. Jesus, porém, lhes disse: Não há profeta sem honra, a não ser na sua pátria e na sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '13', '58', 'E não fez ali muitas maravilhas, por causa da incredulidade deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '1', 'NAQUELE tempo ouviu Herodes, o tetrarca, a fama de Jesus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '2', 'E disse aos seus criados: Este é João Baptista; ressuscitou dos mortos e por isso estas maravilhas operam nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '3', 'Porque Herodes tinha prendido João, e tinha-o maniatado e encerrado no cárcere, por causa de Herodias, mulher de seu irmão Filipe;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '4', 'Porque João lhe dissera: Não te é lícito possuí-la.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '5', 'E, querendo matá-lo, temia o povo; porque o tinham como profeta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '6', 'Festejando-se, porém, o dia natalício de Herodes, dançou a filha de Herodias diante dele, e agradou a Herodes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '7', 'Pelo que prometeu com juramento dar-lhe tudo o que pedisse;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '8', 'E ela, instruída previamente por sua mãe, disse: Dá-me aqui num prato a cabeça de João Baptista.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '9', 'E o rei afligiu-se, mas, por causa de juramento, dos que estavam à mesa com ele, ordenou que se lhe desse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '10', 'E mandou degolar João no cárcere,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '11', 'E a sua cabeça foi trazida num prato, e dada à jovem, e ela a levou a sua mãe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '12', 'E chegaram os seus discípulos, e levaram o corpo, e o sepultaram; e foram anunciá-lo a Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '13', 'E Jesus, ouvindo isto retirou-se dali num barco, para um lugar deserto, apartado; e, sabendo-o o povo, seguiu-o a pé desde as cidades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '14', 'E Jesus, saindo, viu uma grande multidão, e possuído de íntima compaixão para com ela, curou os seus enfermos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '15', 'E, sendo chegada a tarde, os seus discípulos aproximaram-se dele, dizendo: O lugar é deserto, e a hora é já avançada; despede a multidão, para que vão pelas aldeias, e comprem comida para si.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '16', 'Jesus, porém, lhes disse: Não é mister que vão: dai-lhes vós de comer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '17', 'Então eles lhe disseram: Não temos aqui senão cinco pães e dois peixes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '18', 'E ele disse: Trazei-mos aqui.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '19', 'E, tendo mandado que a multidão se assentasse sobre a erva tomou os cinco pães e os dois peixes, e erguendo os olhos ao céu, os abençoou, e, partindo os pães, deu-os aos discípulos, e os discípulos à multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '20', 'E comeram todos, e saciaram-se; e levantaram dos pedaços que sobejaram, doze alcofas cheias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '21', 'E os que comeram foram quase cinco mil homens, além das mulheres e crianças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '22', 'E logo ordenou Jesus que os seus discípulos entrassem no barco, e fossem adiante para a outra banda, enquanto despedia a multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '23', 'E, despedida a multidão, subiu ao monte para orar à parte. E, chegada já a tarde, estava ali só.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '24', 'E o barco estava já no meio do mar, açoitado pelas ondas, porque o vento era contrário;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '25', 'Mas, à quarta vigília da noite, dirigiu-se Jesus para eles, caminhando por cima do mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '26', 'E os discípulos, vendo-o caminhar sobre o mar, assustaram-se, dizendo: É um fantasma. E gritaram, com medo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '27', 'Jesus, porém, lhes falou logo, dizendo: Tende bom ânimo, sou eu, não temais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '28', 'E respondeu-lhe Pedro, e disse: Senhor, se és tu, manda-me ir ter contigo por cima das águas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '29', 'E ele disse: Vem. E Pedro, descendo do barco, andou sobre as águas para ir ter com Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '30', 'Mas, sentindo o vento forte, teve medo; e, começando a ir para o fundo clamou, dizendo: Senhor; salva-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '31', 'E logo Jesus, estendendo a mão, segurou-o, e disse-lhe: Homem de pouca fé, porque duvidaste?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '32', 'E, quando subiram para o barco, acalmou o vento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '33', 'Então aproximaram-se os que estavam no barco, e adoraram-no, dizendo: És verdadeiramente o Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '34', 'E, tendo passado para a outra banda, chegaram à terra de Genezaré.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '35', 'E, quando os homens daquele lugar o conheceram, mandaram por todas aquelas terras em redor, e trouxeram-lhe todos os que estavam enfermos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '14', '36', 'E rogavam-lhe que ao menos eles pudessem tocar a orla do seu vestido; e todos os que a tocavam ficavam sãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '1', 'ENTÃO chegaram ao pé de Jesus uns escribas e fariseus de Jerusalém, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '2', 'Porque transgridem os teus discípulos a tradição dos anciãos? Pois não lavam as mãos quando comem pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '3', 'Ele, porém, respondendo, disse-lhes: Porque transgredis vós também o mandamento de Deus pela vossa tradição?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '4', 'Porque Deus ordenou, dizendo: Honra a teu pai e a tua mãe; e quem maldisser ao pai ou à mãe, morra de morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '5', 'Mas vós dizeis: Qualquer que disser ao pai ou à mãe: É oferta ao SENHOR o que poderias aproveitar de mim; esse não precisa honrar nem a seu pai nem a sua mãe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '6', 'E assim invalidastes, pela vossa tradição, o mandamento de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '7', 'Hipócritas, bem profetizou Isaías a vosso respeito, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '8', 'Este povo honra-me com os seus lábios, mas o seu coração está longe de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '9', 'Mas em vão me adoram, ensinando doutrinas que são preceitos dos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '10', 'E, chamando a si a multidão, disse-lhes: Ouvi, e entendei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '11', 'O que contamina o homem não é o que entra na boca, mas o que sai da boca, isso é o que contamina o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '12', 'Então, acercando-se dele os seus discípulos, disseram-lhe: Sabes que os fariseus, ouvindo essas palavras, se escandalizaram?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '13', 'Ele, porém, respondendo, disse: Toda a planta, que meu Pai celestial não plantou, será arrancada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '14', 'Deixai-os: são condutores cegos: ora, se um cego guiar outro cego, ambos cairão na cova.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '15', 'E Pedro, tomando a palavra, disse-lhe: Explica-nos essa parábola.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '16', 'Jesus, porém, disse: Até vós mesmos estais ainda sem entender?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '17', 'Ainda não compreendeis que tudo o que entra pela boca desce para o ventre, e é lançado fora?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '18', 'Mas o que sai da boca procede do coração, e isso contamina o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '19', 'Porque do coração procedem os maus pensamentos, mortes, adultérios, prostituição, furtos, falsos testemunhos e blasfémias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '20', 'São estas coisas que contaminam o homem; mas comer sem lavar as mãos, isso não contamina o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '21', 'E, partindo Jesus dali, foi para as partes de Tiro e de Sidon.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '22', 'E eis que uma mulher cananeia que saíra daquelas cercanias, clamou, dizendo: Senhor, Filho de David, tem misericórdia de mim que minha filha está miseravelmente endemoninhada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '23', 'Mas ele não lhe respondeu palavra. E os seus discípulos, chegando ao pé dele, rogaram-lhe, dizendo: Despede-a, que vem gritando atrás de nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '24', 'E ele, respondendo, disse: Eu não fui enviado senão às ovelhas perdidas da casa de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '25', 'Então chegou ela, e adorou-o, dizendo: Senhor, socorre-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '26', 'Ele, porém, respondendo, disse: Não é bom pegar no pão dos filhos e deitá-lo aos cachorrinhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '27', 'E ela disse: Sim, Senhor, mas também os cachorrinhos comem das migalhas que caem da mesa dos seus senhores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '28', 'Então respondeu Jesus, e disse-lhe: Ó mulher! Grande é a tua fé seja isso feito para contigo como tu desejas. E desde aquela hora a sua filha ficou sã.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '29', 'Partindo Jesus dali, chegou ao pé do mar da Galileia, e, subindo a um monte, assentou-se lá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '30', 'E veio ter com ele muito povo, que trazia coxos, cegos, mudos aleijados, e outros muitos, e os puseram aos pés de Jesus, e ele os sarou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '31', 'De tal sorte, que a multidão se maravilhou vendo os mudos a falar, os aleijados sãos, os coxos a andar, e os cegos a ver; e glorificava o Deus de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '32', 'E Jesus chamando os seus discípulos, disse: Tenho compaixão da multidão, porque já está comigo há três dias, e não tem que comer; e não quero despedi-la em jejum, para que não desfaleça no caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '33', 'E os seus discípulos disseram-lhe: Donde nos viriam num deserto tantos pães, para saciar tal multidão?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '34', 'E Jesus disse-lhes: Quantos pães tendes? E eles disseram: Sete, e uns poucos de peixinhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '35', 'Então mandou à multidão que se assentasse no chão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '36', 'E, tomando os sete pães e os peixes, e dando graças, partiu-os, e deu-os aos seus discípulos, e os discípulos à multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '37', 'E todos comeram e se saciaram; e levantaram, do que sobejou, sete cestos cheios de pedaços.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '38', 'Ora os que tinham comido eram quatro mil homens, além de mulheres e crianças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '15', '39', 'E, tendo despedido a multidão, entrou no barco, e dirigiu-se ao território de Magdala.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '1', 'E, CHEGANDO-SE os fariseus e os saduceus, para o tentarem, pediram-lhe que lhes mostrasse algum sinal do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '2', 'Mas ele, respondendo, disse-lhes: Quando é chegada a tarde, dizeis: Haverá bom tempo, porque o céu está rubro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '3', 'E pela manhã: Hoje haverá tempestade, porque o céu está de um vermelho sombrio. Hipócritas, sabeis diferençar a face do céu, e não conheceis os sinais dos tempos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '4', 'Uma geração má e adúltera pede um sinal, e nenhum sinal lhe será dado, senão o sinal do profeta Jonas. E, deixando-os, retirou-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '5', 'E, passando seus discípulos para a outra banda, tinham-se esquecido de fornecer-se de pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '6', 'E Jesus disse-lhes: Adverti, e acautelai-vos do fermento dos fariseus e saduceus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '7', 'E eles arrazoavam entre si, dizendo: É porque não nos fornecemos de pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '8', 'E Jesus, percebendo isso, disse: Porque arrazoais entre vós, homens de pouca fé, sobre o não vos terdes fornecido de pão?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '9', 'Não compreendeis ainda, nem vos lembrais dos cinco pães para cinco mil homens, e de quantas alcofas levantastes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '10', 'Nem dos sete pães para quatro mil, e de quantos cestos levantastes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '11', 'Como não compreendestes que não vos falei a respeito do pão, mas que vos guardásseis do fermento dos fariseus e saduceus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '12', 'Então compreenderam que não dissera que se guardassem do fermento do pão, mas da doutrina dos fariseus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '13', 'E, chegando Jesus às partes de Cesareia de Filipo, interrogou os seus discípulos, dizendo: Quem dizem os homens ser o Filho do homem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '14', 'E eles disseram: Uns João Baptista, outros Elias, e outros Jeremias ou um dos profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '15', 'Disse-lhes ele: E vós, quem dizeis que eu sou?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '16', 'E Simão Pedro, respondendo, disse: Tu és o Cristo, o Filho do Deus vivo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '17', 'E Jesus, respondendo, disse-lhe: Bem-aventurado és tu, Simão Barjonas, porque to não revelou a carne e o sangue, mas meu Pai, que está nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '18', 'Pois também eu te digo que tu és Pedro, e sobre esta pedra edificarei a minha igreja, e as portas do inferno não prevalecerão contra ela;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '19', 'E eu te darei as chaves do reino dos céus; e tudo o que ligares na terra será ligado nos céus, e tudo o que desligares na terra será desligado nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '20', 'Então mandou aos seus discípulos que a ninguém dissessem que ele era o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '21', 'Desde então começou Jesus a mostrar aos seus discípulos que convinha ir a Jerusalém, e padecer muito dos anciãos, e dos principais dos sacerdotes, e dos escribas, e ser morto, e ressuscitar ao terceiro dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '22', 'E Pedro, tomando-o de parte, começou a repreendê-lo, dizendo: Senhor, tem compaixão de ti; de modo nenhum te acontecerá isso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '23', 'Ele, porém, voltando-se, disse a Pedro: Para trás de mim, Satanás, que me serves de escândalo; porque não compreendes as coisas que são de Deus, mas só as que são dos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '24', 'Então disse Jesus aos seus discípulos: Se alguém quiser vir após mim renuncie-se a si mesmo, tome sobre si a sua cruz e siga-me;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '25', 'Porque aquele que quiser salvar a sua vida, perdê-la-á, e quem perder a sua vida por amor de mim, achá-la-á.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '26', 'Pois que aproveita ao homem ganhar o mundo inteiro, se perder a sua alma? Ou que dará o homem em recompensa da sua alma?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '27', 'Porque o Filho do homem virá na glória de seu Pai, com os seus anjos; e então dará a cada um segundo as suas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '16', '28', 'Em verdade vos digo que alguns há, dos que aqui estão, que não provarão a morte até que vejam vir o Filho do homem no seu reino.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '1', 'SEIS dias depois, tomou Jesus consigo a Pedro, e a Tiago, e a João, seu irmão, e os conduziu em particular a um alto monte,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '2', 'E transfigurou-se diante deles; e o seu rosto resplandeceu como o sol, e os seus vestidos se tornaram brancos como a luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '3', 'E eis que lhes apareceram Moisés e Elias, falando com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '4', 'E Pedro, tomando a palavra, disse a Jesus: Senhor, bom é estarmos aqui; se queres, façamos aqui três tabernáculos, um para ti, um para Moisés, e um para Elias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '5', 'E, estando ele ainda a falar, eis que uma nuvem luminosa os cobriu. E da nuvem saiu uma voz que dizia: Este é o meu amado Filho, em quem me comprazo! Escutai-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '6', 'E os discípulos, ouvindo isto, caíram sobre os seus rostos, e tiveram grande medo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '7', 'E aproximando-se Jesus, tocou-lhes, e disse: Levantai-vos; e não tenhais medo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '8', 'E, erguendo eles os olhos, ninguém viram senão unicamente a Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '9', 'E, descendo eles do monte, Jesus lhes ordenou, dizendo: A ninguém conteis a visão, até que o Filho do homem seja ressuscitado dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '10', 'E os seus discípulos o interrogaram, dizendo: Porque dizem então os escribas que é mister que Elias venha primeiro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '11', 'E Jesus, respondendo, disse-lhes: Em verdade Elias virá primeiro, e restaurará todas as coisas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '12', 'Mas digo-vos que Elias já veio, e não o conheceram, mas fizeram-lhe tudo o que quiseram. Assim farão eles também padecer o Filho do homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '13', 'Então entenderam os discípulos que lhes falara de João Baptista.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '14', 'E, quando chegaram à multidão, aproximou-se-lhe um homem, pondo-se de joelhos diante dele, e dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '15', 'Senhor, tem misericórdia de meu filho, que é lunático e sofre muito pois muitas vezes cai no fogo, e muitas vezes na água;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '16', 'E trouxe-o aos teus discípulos; e não puderam curá-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '17', 'E Jesus, respondendo, disse: Ó geração incrédula e perversa! Até quando estarei eu convosco, e até quando vos sofrerei? Trazei-mo aqui.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '18', 'E repreendeu Jesus o demónio, que saiu dele, e desde aquela hora o menino sarou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '19', 'Então os discípulos, aproximando-se de Jesus em particular, disseram: Porque não pudemos nós expulsá-lo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '20', 'E Jesus lhes disse: Por causa da vossa pouca fé; porque em verdade vos digo que, se tiverdes fé como um grão de mostarda, direis a este monte: Passa daqui para acolá - e há-de passar; e nada vos será impossível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '21', 'Mas esta casta de demónios não se expulsa senão pela oração e pelo jejum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '22', 'Ora, achando-se eles na Galileia, disse-lhes Jesus: O Filho do homem será entregue nas mãos dos homens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '23', 'E matá-lo-ão, e ao terceiro dia ressuscitará. E eles se entristeceram muito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '24', 'E, chegando eles a Cafarnaum, aproximaram-se de Pedro os que cobravam as didracmas, e disseram: O vosso mestre não paga as didracmas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '25', 'Disse ele: Sim. E, entrando em casa, Jesus se lhe antecipou, dizendo: Que te parece, Simão? De quem cobram os reis da terra os tributos, ou o censo? Dos seus filhos, ou dos alheios?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '26', 'Disse-lhe Pedro: Dos alheios. Disse-lhes Jesus: Logo, estão livres os filhos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '17', '27', 'Mas, para que os não escandalizemos, vai ao mar, lança o anzol, tira o primeiro peixe que subir, e, abrindo-lhe a boca, encontrarás um estater; toma-o, e dá-o por mim e por ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '1', 'NAQUELA mesma hora chegaram os discípulos ao pé de Jesus, dizendo: Quem é o maior no reino dos céus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '2', 'E Jesus, chamando um menino, o pôs no meio deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '3', 'E disse: Em verdade vos digo que, se não vos converterdes e não vos fizerdes como meninos, de modo algum entrareis no reino dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '4', 'Portanto, aquele que se tornar humilde como este menino, esse é o maior no reino dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '5', 'E qualquer que receber em meu nome um menino tal como este, a mim me recebe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '6', 'Mas qualquer que escandalizar um destes pequeninos, que crêem em mim, melhor lhe fora que se lhe pendurasse ao pescoço uma mó de azenha, e se submergisse na profundeza do mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '7', 'Ai do mundo, por causa dos escândalos; porque é mister que venham escândalos, mas ai daquele homem por quem o escândalo vem!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '8', 'Portanto, se a tua mão ou o teu pé te escandalizar, corta-o, e atira-o para longe de ti: melhor te é entrar na vida coxo, ou aleijado, do que, tendo duas mãos ou dois pés, seres lançado no fogo eterno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '9', 'E se o teu olho te escandalizar, arranca-o, e atira-o para longe de ti. Melhor te é entrar na vida com um só olho, do que, tendo dois olhos, seres lançado no fogo do inferno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '10', 'Vede, não desprezeis algum destes pequeninos, porque eu vos digo que os seus anjos nos céus sempre vêem a face de meu Pai que está nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '11', 'Porque o Filho do homem veio salvar o que se tinha perdido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '12', 'Que vos parece? Se algum homem tiver cem ovelhas, e uma delas se desgarrar, não irá pelos montes, deixando as noventa e nove, em busca da que se desgarrou?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '13', 'E, se porventura a acha, em verdade vos digo que maior prazer tem por aquela, do que pelas noventa e nove que se não desgarraram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '14', 'Assim também não é a vontade de vosso Pai, que está nos céus, que um destes pequeninos se perca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '15', 'Ora, se teu irmão pecar contra ti, vai, e repreende-o entre ti e ele só; se te ouvir, ganhaste a teu irmão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '16', 'Mas se não te ouvir, leva ainda contigo um ou dois, para que pela boca de duas ou três testemunhas toda a palavra seja confirmada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '17', 'E, se não as escutar, di-lo à igreja, considera-o como um gentio e publicano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '18', 'Em verdade vos digo que tudo o que ligardes na terra será ligado no céu, e tudo o que desligardes na terra será desligado no céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '19', 'Também vos digo que, se dois de vós concordarem na terra acerca de qualquer coisa que pedirem, isso lhes será feito por meu Pai, que está nos céus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '20', 'Porque onde estiverem dois ou três reunidos em meu nome, aí estou eu no meio deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '21', 'Então Pedro, aproximando-se dele, disse: Senhor, até quantas vezes pecará meu irmão contra mim, e eu lhe perdoarei? Até sete?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '22', 'Jesus lhe disse: Não te digo que até sete, mas, até setenta vezes sete.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '23', 'Por isso o reino dos céus pode comparar-se a um certo rei que quis fazer contas com os seus servos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '24', 'E, começando a fazer contas, foi-lhe apresentado um que lhe devia dez mil talentos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '25', 'E, não tendo ele com que pagar, o seu Senhor mandou que ele, e sua mulher e seus filhos fossem vendidos, com tudo quanto tinha, para que a dívida se lhe pagasse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '26', 'Então aquele servo, prostrando-se, o reverenciava, dizendo: Senhor, sê generoso para comigo, e tudo te pagarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '27', 'Então o SENHOR daquele servo, movido de íntima compaixão, soltou-o, e perdoou-lhe a dívida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '28', 'Saindo, porém, aquele servo, encontrou um dos seus conservos, que lhe devia cem dinheiros, e, lançando mão dele, sufocava-o, dizendo: Paga-me o que me deves.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '29', 'Então o seu companheiro, prostrando-se a seus pés, rogava-lhe, dizendo: Sê generoso para comigo, e tudo te pagarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '30', 'Ele, porém, não quis, antes foi encerrá-lo na prisão, até que pagasse a dívida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '31', 'Vendo pois os seus conservos o que acontecia, contristaram-se muito, e foram declarar ao seu Senhor tudo o que se passara.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '32', 'Então o seu senhor, chamando-o à sua presença, disse-lhe: Servo malvado, perdoei-te toda aquela dívida, porque me suplicaste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '33', 'Não devias tu igualmente ter compaixão do teu companheiro, como eu também tive misericórdia de ti?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '34', 'E, indignado, o seu Senhor o entregou aos atormentadores, até que pagasse tudo o que devia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '18', '35', 'Assim vos fará também meu Pai celestial, se do coração não perdoardes, cada um a seu irmão, as suas ofensas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '1', 'E ACONTECEU que, concluindo Jesus estes discursos, saiu da Galileia, e dirigiu-se aos confins da Judeia, além do Jordão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '2', 'E seguiram-no muitas gentes, e curou-as ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '3', 'Então chegaram ao pé dele os fariseus, tentando-o, e dizendo: É lícito ao homem repudiar a sua mulher por qualquer motivo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '4', 'Ele, porém, respondendo, disse-lhes: Não tendes lido que aquele que os fez no princípio macho e fêmea os fez,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '5', 'E disse: Portanto deixará o homem pai e mãe, e se unirá a sua mulher, e serão dois numa só carne?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '6', 'Assim não são mais dois, mas uma só carne. Portanto o que Deus ajuntou não o separe o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '7', 'Disseram-lhe eles: Então porque mandou Moisés dar-lhe carta de divórcio, e repudiá-la?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '8', 'Disse-lhes ele: Moisés por causa da dureza dos vossos corações vos permitiu repudiar vossas mulheres; mas ao princípio não foi assim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '9', 'Eu vos digo, porém, que qualquer que repudiar sua mulher, não sendo por causa de prostituição, e casar com outra, comete adultério; e o que casar com a repudiada também comete adultério.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '10', 'Disseram-lhe seus discípulos: Se assim é a condição do homem relativamente à mulher, não convém casar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '11', 'Ele, porém, lhes disse: Nem todos podem receber esta palavra, mas só aqueles a quem foi concedido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '12', 'Porque há eunucos que assim nasceram do ventre da mãe; e há eunucos que foram castrados pelos homens; e há eunucos que se castraram a si mesmos por causa do reino dos céus. Quem pode receber isto, receba-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '13', 'Trouxeram-lhe então alguns meninos, para que sobre eles pusesse as mãos, e orasse; mas os discípulos os repreendiam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '14', 'Jesus, porém, disse: Deixai os meninos, e não os estorveis de vir a mim; porque dos tais é o reino dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '15', 'E, tendo-lhes imposto as mãos, partiu dali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '16', 'E eis que, aproximando-se dele um mancebo, disse-lhe: Bom Mestre, que bem farei, para conseguir a vida eterna?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '17', 'E ele disse-lhe: Porque me chamas bom? Não há bom senão um só, que é Deus. Se queres, porém, entrar na vida, guarda os mandamentos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '18', 'Disse-lhe ele: Quais? E Jesus disse: Não matarás, não cometerás adultério, não furtarás, não dirás falso testemunho;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '19', 'Honra teu pai e tua mãe, e amarás o teu próximo como a ti mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '20', 'Disse-lhe o mancebo: Tudo isso tenho guardado desde a minha mocidade; que me falta ainda?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '21', 'Disse-lhe Jesus: Se queres ser perfeito, vai, vende tudo o que tens e dá-o aos pobres, e terás um tesouro no céu; e vem, e segue-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '22', 'E o mancebo, ouvindo esta palavra, retirou-se triste, porque possuía muitas propriedades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '23', 'Disse então Jesus aos seus discípulos: Em verdade vos digo que é difícil entrar um rico no reino dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '24', 'E outra vez vos digo que é mais fácil passar um camelo pelo fundo duma agulha do que entrar um rico no reino dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '25', 'Os seus discípulos, ouvindo isto, admiraram-se muito, dizendo: Quem poderá pois salvar-se?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '26', 'E Jesus, olhando para eles, disse-lhes: Aos homens é isso impossível, mas a Deus tudo é possível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '27', 'Então Pedro, tomando a palavra, disse-lhe: Eis que nós deixámos tudo, e te seguimos; que receberemos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '28', 'E Jesus disse-lhes: Em verdade vos digo que vós, que me seguistes, quando, na regeneração, o Filho do homem se assentar no trono da sua glória, também vos assentareis sobre doze tronos, para julgar as doze tribos de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '29', 'E todo aquele que tiver deixado casas, ou irmãos, ou irmãs, ou pai, ou mãe, ou mulher, ou filhos, ou terras, por amor do meu nome, receberá cem vezes tanto, e herdará a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '19', '30', 'Porém muitos primeiros serão os derradeiros, e muitos derradeiros serão os primeiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '1', 'PORQUE o reino dos céus é semelhante a um homem, pai de família, que saiu de madrugada a assalariar trabalhadores para a sua vinha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '2', 'E, ajustando com os trabalhadores a um dinheiro por dia, mandou-os para a sua vinha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '3', 'E, saindo perto da hora terceira, viu outros que estavam ociosos na praça,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '4', 'E disse-lhes: Ide vós também para a vinha, e dar-vos-ei o que for justo. E eles foram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '5', 'Saindo outra vez, perto da hora sexta e nona, fez o mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '6', 'E, saindo perto da hora undécima, encontrou outros que estavam ociosos, e perguntou-lhes : Porque estais ociosos todo o dia?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '7', 'Disseram-lhe eles: Porque ninguém nos assalariou. Diz-lhes ele: Ide vós também para a vinha, e recebereis o que for justo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '8', 'E, aproximando-se a noite, diz o SENHOR da vinha ao seu mordomo: Chama os trabalhadores, e paga-lhes o jornal, começando pelos derradeiros até aos primeiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '9', 'E, chegando os que tinham ido perto da hora undécima, receberam um dinheiro cada um.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '10', 'Vindo, porém, os primeiros, cuidaram que haviam de receber mais; mas do mesmo modo receberam um dinheiro cada um;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '11', 'E, recebendo-o, murmuravam contra o pai de família.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '12', 'Dizendo: Estes derradeiros trabalharam só uma hora, e tu os igualaste connosco, que suportámos a fadiga e a calma do dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '13', 'Mas ele, respondendo, disse a um deles: Amigo, não te faço agravo: não ajustaste tu comigo um dinheiro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '14', 'Toma o que é teu, e retira-te; eu quero dar a este derradeiro tanto como a ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '15', 'Ou não me é lícito fazer o que quiser do que é meu? Ou é mau o teu olho porque eu sou bom?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '16', 'Assim os derradeiros serão primeiros, e os primeiros derradeiros; porque muitos são chamados, mas poucos escolhidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '17', 'E, subindo Jesus a Jerusalém, chamou de parte os seus doze discípulos, e no caminho disse-lhes:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '18', 'Eis que vamos para Jerusalém, e o Filho do homem será entregue aos príncipes dos sacerdotes, e aos escribas, e condená-lo-ão à morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '19', 'E o entregarão aos gentios para que dele escarneçam, e o açoitem e crucifiquem, e ao terceiro dia ressuscitará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '20', 'Então se aproximou dele a mãe dos filhos de Zebedeu, com seus filhos, adorando-o, e fazendo-lhe um pedido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '21', 'E ele diz-lhe: Que queres? Ela respondeu: Diz que estes meus dois filhos se assentem, um à tua direita e outro à tua esquerda, no teu reino.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '22', 'Jesus, porém, respondendo, disse: Não sabeis o que pedis; podeis vós beber o cálix que eu hei-de beber, e ser baptizados com o baptismo com que eu sou baptizado? Dizem-lhe eles: Podemos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '23', 'E diz-lhes ele: Na verdade bebereis o meu cálix, mas o assentar-se à minha direita ou à minha esquerda não me pertence dá-lo, mas é para aqueles para quem meu Pai o tem preparado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '24', 'E, quando os dez ouviram isto, indignaram-se contra os dois irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '25', 'Então Jesus, chamando-os para junto de si disse: Bem sabeis que pelos príncipes dos gentios são estes dominados, e que os grandes exercem autoridade sobre eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '26', 'Não será assim entre vós; mas todo aquele que quiser entre vós fazer-se grande seja vosso serviçal;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '27', 'E qualquer que entre vós quiser ser o primeiro seja vosso servo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '28', 'Bem como o Filho do homem não veio para ser servido, mas para servir, e para dar a sua vida em resgate de muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '29', 'E, saindo eles de Jerico, seguiu-o grande multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '30', 'E eis que dois cegos, assentados junto do caminho, ouvindo que Jesus passava, clamaram, dizendo: Senhor, Filho de David, tem misericórdia de nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '31', 'E a multidão os repreendia, para que se calassem; eles, porém, cada vez clamavam mais, dizendo: Senhor, Filho de David, tem misericórdia de nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '32', 'E Jesus, parando, chamou-os, e disse: Que quereis que vos faça?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '33', 'Disseram-lhe eles: Senhor, que os nossos olhos sejam abertos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '20', '34', 'Então Jesus, movido de íntima compaixão, tocou-lhe nos olhos, e logo viram; e eles o seguiram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '1', 'E, QUANDO se aproximaram de Jerusalém, e chegaram a Betfagé, ao monte das Oliveiras, enviou então Jesus dois discípulos, dizendo-lhes:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '2', 'Ide à aldeia que está defronte de vós, e logo encontrareis uma jumenta presa, e um jumentinho com ela; desprendei-a, e trazei-mos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '3', 'E, se alguém vos disser alguma coisa, direis que o SENHOR os há de mister: e logo os enviará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '4', 'Ora tudo isto aconteceu para que se cumprisse o que foi dito pelo profeta, que diz:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '5', 'Dizei à filha de Sião: Eis que o teu rei aí te vem, manso, e assentado sobre uma jumenta, e sobre um jumentinho, filho de animal de carga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '6', 'E, indo os discípulos, e fazendo como Jesus lhes ordenara,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '7', 'Trouxeram a jumenta e o jumentinho, e sobre eles puseram os seus vestidos, e fizeram-no assentar em cima.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '8', 'E muitíssima gente estendia os seus vestidos pelo caminho, e outras cortavam ramos de árvores, e os espalhavam pelo caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '9', 'E a multidão que ia adiante, e a que seguia, clamava, dizendo: Hosana ao Filho de David; bendito o que vem em nome do SENHOR. Hosana nas alturas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '10', 'E entrando ele em Jerusalém, toda a cidade se alvoroçou, dizendo: Quem é este?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '11', 'E a multidão dizia: Este é Jesus o profeta de Nazaré da Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '12', 'E entrou Jesus no templo de Deus, e expulsou todos os que vendiam e compravam no templo, e derribou as mesas dos cambistas e as cadeiras dos que vendiam pombas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '13', 'E disse-lhes: Está escrito: A minha casa será chamada casa de oração - mas vós a tendes convertido em covil de ladrões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '14', 'E foram ter com ele ao templo cegos e coxos, e curou-os.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '15', 'Vendo então os principais dos sacerdotes e os escribas as maravilhas que fazia, e os meninos clamando no templo, Hosana ao Filho de David, indignaram-se,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '16', 'E disseram-lhe: Ouves o que estes dizem? E Jesus lhes disse: Sim; nunca lestes: Pela boca dos meninos e das criancinhas de peito tiraste o perfeito louvor?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '17', 'E, deixando-os, saiu da cidade para Betânia, e ali passou a noite.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '18', 'E, de manhã, voltando para a cidade, teve fome;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '19', 'E, avistando uma figueira perto do caminho, dirigiu-se a ela, e não achou nela senão folhas. E disse-lhe: Nunca mais nasça fruto de ti. E a figueira secou imediatamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '20', 'E os discípulos, vendo isto, maravilharam-se, dizendo: Como secou imediatamente a figueira?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '21', 'Jesus, porém, respondendo, disse-lhes: Em verdade vos digo que, se tiverdes fé e não duvidardes, não só fareis o que foi feito à figueira, mas até se a este monte disserdes: Ergue-te e precipita-te no mar, assim será feito;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '22', 'E tudo o que pedirdes na oração, crendo, o recebereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '23', 'E, chegando ao templo, acercaram-se dele, estando já ensinando, os príncipes dos sacerdotes e os anciãos do povo, dizendo: Com que autoridade fazes isto? E quem te deu tal autoridade?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '24', 'E Jesus, respondendo, disse-lhes: Eu também vos perguntarei uma coisa; se ma disserdes, também eu vos direi com que autoridade faço isto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '25', 'O baptismo de João donde era? Do céu, ou dos homens? E pensavam entre si, dizendo: Se dissermos: Do céu; ele nos dirá: Então porque não o crestes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '26', 'E, se dissermos: Dos homens, tememos o povo, porque todos consideram João como profeta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '27', 'E, respondendo a Jesus, disseram: Não sabemos. Ele disse-lhes: Nem eu vos digo com que autoridade faço isto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '28', 'Mas que vos parece? Um homem tinha dois filhos e, dirigindo-se ao primeiro, disse: Filho, vai trabalhar hoje na minha vinha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '29', 'Ele, porém, respondendo, disse: Não quero. Mas depois, arrependendo-se, foi.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '30', 'E, dirigindo-se ao segundo, falou-lhe de igual modo; Eu vou, senhor; e não foi.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '31', 'Qual dos dois fez a vontade do pai? Disseram-lhe eles: O primeiro. Disse-lhes Jesus: Na verdade vos digo que os publicanos e as meretrizes entram adiante de vós no reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '32', 'Porque João veio a vós no caminho da justiça, e não o crestes, mas os publicanos e as meretrizes o creram; vós, porém, vendo isto, nem depois vos arrependestes para o crer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '33', 'Ouvi ainda outra parábola: Houve um homem, pai de família, que plantou uma vinha, e circundou-a de um valado, e construiu nela um lagar, e edificou uma torre, e arrendou-a a uns lavradores, e ausentou-se para longe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '34', 'E, chegando o tempo dos frutos, enviou os seus servos aos lavradores, para receber os seus frutos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '35', 'E os lavradores, apoderando-se dos servos, feriram um, mataram outro, e apedrejaram outro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '36', 'Depois enviou outros servos, em maior número do que os primeiros; e eles fizeram-lhes o mesmo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '37', 'E por último enviou-lhes seu filho, dizendo: Terão respeito a meu filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '38', 'Mas os lavradores, vendo o filho, disseram entre si: Este é o herdeiro; vinde, matemo-lo, e apoderamo-nos da sua herança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '39', 'E, lançando mão dele, o arrastaram para fora da vinha, e o mataram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '40', 'Quando pois vier o SENHOR da vinha, que fará àqueles lavradores?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '41', 'Dizem-lhe eles: Dará afrontosa morte aos maus, e arrendará a vinha a outros lavradores, que a seu tempo lhe dêem os frutos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '42', 'Diz-lhes Jesus: Nunca lestes nas Escrituras: A pedra, que os edificadores rejeitaram, essa foi posta por cabeça do ângulo; pelo SENHOR foi feito isto, e é maravilhoso aos nossos olhos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '43', 'Portanto eu vos digo que o reino de Deus vos será tirado, e será dado a uma nação que dê os seus frutos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '44', 'E quem cair sobre esta pedra despedaçar-se-á; e aquele sobre quem ela cair ficará reduzido a pó.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '45', 'E os príncipes dos sacerdotes e os fariseus, ouvindo estas palavras, entenderam que falava deles;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '21', '46', 'E, pretendendo prendê-lo, recearam o povo, porquanto o tinham por profeta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '1', 'ENTÃO Jesus, tomando a palavra, tornou a falar-lhes em parábolas, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '2', 'O reino dos céus é semelhante a um certo rei que celebrou as bodas de seu filho;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '3', 'E enviou os seus servos a chamar os convidados para as bodas, e estes não quiseram vir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '4', 'Depois enviou outros servos, dizendo: Dizei aos convidados: Eis que tenho o meu jantar preparado, os meus bois e cevados já mortos, e tudo já pronto; vinde às bodas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '5', 'Porém eles, não fazendo caso, foram, um para o seu campo, outro para o seu tráfico;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '6', 'E os outros, apoderando-se dos servos, os ultrajaram e mataram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '7', 'E o rei, tendo notícia disto, encolerizou-se e, enviando os seus exércitos, destruiu aqueles homicidas, e incendiou a sua cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '8', 'Então diz aos servos: As bodas, na verdade, estão preparadas, mas os convidados não eram dignos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '9', 'Ide pois às saídas dos caminhos, e convidai para as bodas a todos os que encontrardes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '10', 'E os servos, saindo pelos caminhos, ajuntaram todos quantos encontraram, tanto maus como bons; e a festa nupcial foi cheia de convidados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '11', 'E o rei, entrando para ver os convidados, viu ali um homem que não estava trajado com vestido de núpcias,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '12', 'E disse-lhe: Amigo, como entraste aqui, não tendo vestido nupcial? E ele emudeceu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '13', 'Disse então o rei aos servos: Amarrai-o de pés e mãos, levai-o, e lançai-o nas trevas exteriores; ali haverá pranto e ranger de dentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '14', 'Porque muitos são chamados, mas poucos escolhidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '15', 'Então, retirando-se os fariseus, consultaram entre si como o surpreenderiam nalguma palavra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '16', 'E enviaram-lhe os seus discípulos, com os herodianos, dizendo: Mestre, bem sabemos que és verdadeiro, e ensinas o caminho de Deus, segundo a verdade, e de ninguém se te dá, porque não olhas à aparência dos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '17', 'Diz-nos, pois, que te parece? É lícito pagar o tributo a César, ou não?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '18', 'Jesus, porém, conhecendo a sua malícia, disse: Porque me experimentais, hipócritas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '19', 'Mostrai-me a moeda do tributo. E eles lhe apresentaram um dinheiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '20', 'E ele diz-lhes: De quem é esta efígie e esta inscrição?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '21', 'Dizem-lhe eles: De César. Então ele lhes diz: Dai pois a César o que é de César, e a Deus o que é de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '22', 'E eles, ouvindo isto, maravilharam-se, e, deixando-o, se retiraram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '23', 'No mesmo dia chegaram junto dele os saduceus, que dizem não haver ressurreição, e o interrogaram,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '24', 'Dizendo: Mestre, Moisés disse: Se morrer alguém, não tendo filhos, casará o seu irmão com a mulher dele, e suscitará descendência a seu irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '25', 'Ora houve entre nós sete irmãos; e o primeiro, tendo casado, morreu, e, não tendo descendência, deixou sua mulher a seu irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '26', 'Da mesma sorte o segundo, e o terceiro, até ao sétimo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '27', 'Por fim, depois de todos, morreu também a mulher.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '28', 'Portanto, na ressurreição, de qual dos sete será a mulher, visto que todos a possuíram?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '29', 'Jesus, porém, respondendo, disse-lhes: Errais, não conhecendo as Escrituras, nem o poder de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '30', 'Porque na ressurreição nem casam nem são dados em casamento; mas serão como os anjos de Deus no céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '31', 'E, acerca da ressurreição dos mortos, não tendes lido o que Deus vos declarou, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '32', 'Eu sou o Deus de Abraão, o Deus de Isaque, e o Deus de Jacob? Ora Deus não é Deus dos mortos, mas dos vivos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '33', 'E as turbas, ouvindo isto, ficaram maravilhadas da sua doutrina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '34', 'E os fariseus, ouvindo que ele fizera emudecer os saduceus, reuniram-se no mesmo lugar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '35', 'E um deles, doutor da lei, interrogou-o para o experimentar, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '36', 'Mestre, qual é o grande mandamento na lei?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '37', 'E Jesus disse-lhe: Amarás o SENHOR teu Deus de todo o teu coração, e de toda a tua alma, e de todo o teu pensamento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '38', 'Este é o primeiro e grande mandamento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '39', 'E o segundo, semelhante a este, é: Amarás o teu próximo como a ti mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '40', 'Destes dois mandamentos depende toda a lei e os profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '41', 'E, estando reunidos os fariseus, interrogou-os Jesus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '42', 'Dizendo: Que pensais vós do Cristo? De quem é filho? Eles disseram-lhe: De David.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '43', 'Disse-lhes ele: Como é então que David, em espírito, lhe chama Senhor, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '44', 'Disse o SENHOR ao meu Senhor: Assenta-te à minha direita, até que eu ponha os teus inimigos por escabelo de teus pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '45', 'Se David pois lhe chama Senhor, como é seu filho?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '22', '46', 'E ninguém podia responder-lhe uma palavra: nem desde aquele dia ousou mais alguém interrogá-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '1', 'ENTÃO falou Jesus à multidão, e aos seus discípulos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '2', 'Dizendo: Na cadeira de Moisés estão assentados os escribas e fariseus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '3', 'Observai e praticai tudo o que vos disserem; mas não procedais em conformidade com as suas obras, porque dizem e não praticam;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '4', 'Pois atam fardos pesados e difíceis de suportar, e os põem aos ombros dos homens; eles, porém, nem com o dedo querem movê-los;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '5', 'E fazem todas as obras a fim de serem vistos pelos homens; pois trazem largas filactérias, e alargam as franjas dos seus vestidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '6', 'E amam os primeiros lugares nas ceias e as primeiras cadeiras nas sinagogas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '7', 'E as saudações nas praças, e o serem chamados, pelos homens - Rabi, Rabi.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '8', 'Vós porém, não queirais ser chamados Rabi, porque um só é o vosso Mestre, a saber, o Cristo, e todos vós sois irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '9', 'E a ninguém na terra chameis vosso Pai, porque um só é o vosso Pai, o qual está nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '10', 'Nem vos chameis mestres, porque um só é vosso Mestre, que é o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '11', 'Porém o maior de entre vós será vosso servo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '12', 'E o que a si mesmo se exaltar será humilhado; e o que a si mesmo se humilhar será exaltado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '13', 'Mas ai de vós, escribas e fariseus, hipócritas! Pois que fechais aos homens o reino dos céus; e nem vós entrais nem deixais entrar aos que estão entrando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '14', 'Ai de vós, escribas e fariseus, hipócritas! Pois que devorais as casas das viúvas, sob pretexto de prolongadas orações; por isso sofrereis mais rigoroso juízo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '15', 'Ai de vós, escribas e fariseus, hipócritas! Pois que percorreis o mar e a terra para fazer um prosélito; e, depois de o terdes feito, o fazeis filho do inferno duas vezes mais do que vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '16', 'Ai de vós, condutores cegos! Pois que dizeis: Qualquer que jurar pelo templo, isso nada é; mas o que jurar pelo ouro do templo esse é devedor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '17', 'Insensatos e cegos! Pois qual é maior: o ouro, ou o templo, que santifica o ouro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '18', 'E aquele que jurar pelo altar isto nada é; mas o que jurar pela oferta que está sobre o altar, esse é devedor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '19', 'Insensatos e cegos! Pois qual é maior: a oferta ou o altar, que santifica a oferta?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '20', 'Portanto, o que jurar pelo altar jura por ele e por tudo o que sobre ele está;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '21', 'E o que jurar pelo templo jura por ele e por aquele que nele habita;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '22', 'E o que jurar pelo céu jura pelo trono de Deus e por aquele que está assentado nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '23', 'Ai de vós, escribas e fariseus, hipócritas! Pois que dizimais a hortelã, o endro e o cominho, e desprezais o mais importante da lei, o juízo, a misericórdia e a fé; deveis, porém, fazer estas coisas, e não omitir aquelas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '24', 'Condutores cegos! Que coais um mosquito e engolis um camelo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '25', 'Ai de vós, escribas e fariseus, hipócritas! Pois que limpais o exterior do copo e do prato, mas o interior está cheio de rapina e de iniquidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '26', 'Fariseu cego! Limpa primeiro o interior do copo e do prato, para que também o exterior fique limpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '27', 'Ai de vós, escribas e fariseus, hipócritas! Pois que sois semelhantes aos sepulcros caiados, que por fora realmente parecem formosos, mas interiormente estão cheios de ossos de mortos e de toda a imundície.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '28', 'Assim também vós exteriormente pareceis justos aos homens, mas interiormente estais cheios de hipocrisia e de iniquidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '29', 'Ai de vós, escribas e fariseus, hipócritas! Pois que edificais os sepulcros dos profetas e adornais os monumentos dos justos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '30', 'E dizeis: Se, existíssemos no tempo de nossos pais, nunca nos associaríamos com eles para derramar o sangue dos profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '31', 'Assim, vós mesmos testificais que sois filhos dos que mataram os profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '32', 'Enchei vós pois a medida de vossos pais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '33', 'Serpentes, raça de víboras! Como escapareis da condenação do inferno?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '34', 'Portanto, eis que eu vos envio profetas, sábios e escribas; e a uns deles matareis e crucificareis; e a outros deles açoitareis nas vossas sinagogas e os perseguireis de cidade em cidade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '35', 'Para que sobre vós caia todo o sangue justo, que foi derramado sobre a terra, desde o sangue de Abel, o justo, até ao sangue de Zacarias, filho de Baraquias, que mataste entre o santuário e o altar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '36', 'Em verdade vos digo que todas estas coisas hão-de vir sobre esta geração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '37', 'Jerusalém, Jerusalém, que matas os profetas, e apedrejas os que te são enviados! Quantas vezes quis eu ajuntar os teus filhos, como a galinha ajunta os seus pintos debaixo das asas, e tu não quiseste!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '38', 'Eis que a vossa casa vai ficar-vos deserta;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '23', '39', 'Porque eu vos digo que desde agora me não vereis mais, até que digais: Bendito o que vem em nome do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '1', 'E QUANDO Jesus ia saindo do templo, aproximaram-se dele os seus discípulos para lhe mostrarem a estrutura do templo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '2', 'Jesus, porém lhes disse: Não vedes tudo isto? Em verdade vos digo que não ficará aqui pedra sobre pedra que não seja derribada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '3', 'E, estando assentado no monte das oliveiras, chegaram-se a ele os seus discípulos em particular, dizendo: Diz-nos quando serão essas coisas, e que sinal haverá da tua vinda e do fim do mundo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '4', 'E Jesus, respondendo, disse-lhes: Acautelai-vos, que ninguém vos engane;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '5', 'Porque muitos virão em meu nome, dizendo: Eu sou o Cristo; e enganarão a muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '6', 'E ouvireis de guerras e de rumores de guerras; olhai não vos assusteis, porque é mister que isso tudo aconteça, mas ainda não é o fim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '7', 'Porquanto se levantará nação contra nação, e reino contra reino, e haverá fomes, e pestes, e terramotos, em vários lugares.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '8', 'Mas todas estas coisas são o princípio de dores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '9', 'Então vos hão-de entregar para serdes atormentados, e matar-vos-ão; e sereis odiados de todas as gentes por causa do meu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '10', 'Nesse tempo muitos serão escandalizados, e trair-se-ão uns aos outros, e uns aos outros se aborrecerão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '11', 'E surgirão muitos falsos profetas, e enganarão a muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '12', 'E, por se multiplicar a iniquidade, o amor de muitos esfriará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '13', 'Mas aquele que perseverar até ao fim será salvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '14', 'E este evangelho do reino será pregado em todo o mundo, em testemunho a todas as gentes, e então virá o fim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '15', 'Quando pois virdes que a abominação da desolação, de que falou o profeta Daniel, está no lugar santo; quem lê, atenda;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '16', 'Então, os que estiverem na Judeia, fujam para os montes;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '17', 'E quem estiver sobre o telhado não desça a tirar alguma coisa de sua casa,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '18', 'E quem estiver no campo não volte atrás a buscar os seus vestidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '19', 'Mas ai das grávidas e das que amamentam naqueles dias!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '20', 'E orai para que a vossa fuga não aconteça no inverno nem no sábado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '21', 'Porque haverá então grande aflição, como nunca houve desde o princípio do mundo até agora, nem tampouco há-de haver.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '22', 'E, se aqueles dias não fossem abreviados, nenhuma carne se salvaria; mas por causa dos escolhidos serão abreviados aqueles dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '23', 'Então, se alguém vos disser: Eis que o Cristo está aqui, ou ali, não lhe deis crédito;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '24', 'Porque surgirão falsos cristos e falsos profetas, e farão tão grandes sinais e prodígios que, se possível fora, enganariam até os escolhidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '25', 'Eis que eu vo-lo tenho predito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '26', 'Portanto, se vos disserem: Eis que ele está no deserto, não saiais. Eis que ele está no interior da casa; não acrediteis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '27', 'Porque, assim como o relâmpago sai do oriente e se mostra até ao ocidente, assim será também a vinda do Filho do homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '28', 'Pois onde estiver o cadáver, aí se ajuntarão as águias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '29', 'E, logo depois da aflição daqueles dias, o sol escurecerá, e a lua não dará a sua luz, e as estrelas cairão do céu, e as potências dos céus serão abaladas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '30', 'Então aparecerá no céu o sinal do Filho do homem; e todas as tribos da terra se lamentarão, e verão o Filho do homem, vindo sobre as nuvens do céu, com poder e grande glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '31', 'E ele enviará os seus anjos com rijo clamor de trombeta, os quais ajuntarão os seus escolhidos desde os quatro ventos, de uma à outra extremidade dos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '32', 'Aprendei pois esta parábola da figueira: Quando já os seus ramos se tornam tenros e brotam folhas, sabeis que está próximo o verão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '33', 'Igualmente, quando virdes todas estas coisas, sabei que ele está próximo às portas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '34', 'Em verdade vos digo que não passará esta geração sem que todas estas coisas aconteçam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '35', 'O céu e a terra passarão, mas as minhas palavras não hão-de passar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '36', 'Porém daquele dia e hora ninguém sabe, nem os anjos do céu, nem o Filho, mas unicamente meu Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '37', 'E, como foi nos dias de Noé, assim será também a vinda do Filho do homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '38', 'Porquanto, assim como, nos dias anteriores ao dilúvio, comiam, bebiam, casavam e davam-se em casamento, até ao dia em que Noé entrou na arca,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '39', 'E não o perceberam, até que veio o dilúvio e os levou a todos, assim será também a vinda do Filho do homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '40', 'Então, estando dois no campo, será levado um, e deixado o outro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '41', 'Estando duas moendo no moinho, será levada uma, e deixada outra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '42', 'Vigiai, pois, porque não sabeis a que hora há-de vir o vosso SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '43', 'Mas considerai isto: se o pai de família soubesse a que vigília da noite havia de vir o ladrão, vigiaria e não deixaria minar a sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '44', 'Por isso, estai vós apercebidos também; porque o Filho do homem há-de vir à hora em que não penseis');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '45', 'Quem é pois o servo fiel e prudente, que o SENHOR constituiu sobre a sua casa, para dar o sustento a seu tempo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '46', 'Bem-aventurado aquele servo que o SENHOR, quando vier, achar servindo assim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '47', 'Em verdade vos digo que o porá sobre todos os seus bens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '48', 'Porém, se aquele mau servo disser consigo: O meu Senhor tarde virá;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '49', 'E começar a espancar os seus conservos, e a comer e a beber com os temulentos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '50', 'Virá o SENHOR daquele servo num dia em que o não espera, e à hora em que ele não sabe,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '24', '51', 'E separá-lo-á, e destinará a sua parte com os hipócritas; ali haverá pranto e ranger de dentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '1', 'ENTÃO o reino dos céus será semelhante a dez virgens que, tomando as suas lâmpadas; saíram ao encontro do esposo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '2', 'E cinco delas eram prudentes, e cinco loucas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '3', 'As loucas, tomando as suas lâmpadas, não levaram azeite consigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '4', 'Mas as prudentes levaram azeite em suas vasilhas, com as suas lâmpadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '5', 'E, tardando o esposo, tosquenejaram todas, e adormeceram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '6', 'Mas à meia-noite ouviu-se um clamor: Aí vem o esposo, saí-lhe ao encontro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '7', 'Então todas aquelas virgens se levantaram, e prepararam as suas lâmpadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '8', 'E as loucas disseram às prudentes: Dai-nos do vosso azeite, porque as nossas lâmpadas se apagam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '9', 'Mas as prudentes responderam, dizendo: Não seja caso que nos falte a nós e a vós, ide antes aos que o vendem, e comprai-o para vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '10', 'E, tendo elas ido comprá-lo, chegou o esposo, e as que estavam preparadas entraram com ele para as bodas, e fechou-se a porta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '11', 'E depois chegaram também as outras virgens, dizendo: Senhor, Senhor, abre-nos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '12', 'E ele, respondendo, disse: Em verdade vos digo que vos não conheço.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '13', 'Vigiai pois, porque não sabeis o dia nem a hora em que o Filho do homem há-de vir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '14', 'Porque isto é também como um homem que, partindo para fora da terra, chamou os seus servos, e entregou-lhes os seus bens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '15', 'E a um deu cinco talentos, e a outro dois, e a outro um, a cada um segundo a sua capacidade, e ausentou-se logo para longe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '16', 'E, tendo ele partido, o que recebera cinco talentos negociou com eles, e granjeou outros cinco talentos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '17', 'Da mesma sorte, o que recebera dois, granjeou também outros dois;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '18', 'Mas o que recebera um, foi e cavou na terra e escondeu o dinheiro do seu senhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '19', 'E muito tempo depois veio o SENHOR daqueles servos, e fez contas com eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '20', 'Então aproximou-se o que recebera cinco talentos, e trouxe-lhe outros cinco talentos, dizendo: Senhor, entregaste-me cinco talentos; eis aqui outros cinco talentos que granjeei com eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '21', 'E o seu Senhor lhe disse: Bem está, servo bom e fiel. Sobre o pouco foste fiel, sobre muito te colocarei; entra no gozo do teu Senhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '22', 'E, chegando também o que tinha recebido dois talentos, disse: Senhor, entregaste-me dois talentos; eis que com eles granjeei outros dois talentos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '23', 'Disse-lhe o seu senhor: Bem está, bom e fiel servo. Sobre o pouco foste fiel, sobre muito te colocarei; entra no gozo do teu Senhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '24', 'Mas, chegando também o que recebera um talento, disse: Senhor, eu conhecia-te, que és um homem duro, que ceifas onde não semeaste e ajuntas onde não espalhaste;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '25', 'E, atemorizado, escondi na terra o teu talento; aqui tens o que é teu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '26', 'Respondendo, porém, o seu senhor, disse-lhe: Mau e negligente servo; sabes que ceifo onde não semeei e ajunto onde não espalhei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '27', 'Devias então ter dado o meu dinheiro aos banqueiros, e, quando eu viesse, receberia o meu com os juros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '28', 'Tirai-lhe pois o talento, e dai-o ao que tem os dez talentos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '29', 'Porque a qualquer que tiver será dado, e terá em abundância; mas ao que não tiver até o que tem ser-lhe-á tirado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '30', 'Lançai pois o servo inútil nas trevas exteriores; ali haverá pranto e ranger de dentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '31', 'E quando o Filho do homem vier em sua glória, e todos os santos anjos com ele, então se assentará no trono da sua glória;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '32', 'E todas as nações serão reunidas diante dele, e apartará uns dos outros, como o pastor aparta dos bodes as ovelhas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '33', 'E porá as ovelhas à sua direita, mas os bodes à esquerda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '34', 'Então dirá o Rei aos que estiverem à sua direita: Vinde, benditos de meu Pai, possuí por herança o reino que vos está preparado desde a fundação do mundo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '35', 'Porque tive fome, e destes-me de comer; tive sede, e destes-me de beber; era estrangeiro, e hospedastes-me;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '36', 'Estava nu, e vestistes-me; adoeci, e visitastes-me; estive na prisão, e fostes ver-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '37', 'Então os justos lhe responderão, dizendo: Senhor, quando te vimos com fome, e te demos de comer? ou com sede, e te demos de beber?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '38', 'E quando te vimos estrangeiro, e te hospedamos? Ou nu, e te vestimos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '39', 'E quando te vimos enfermo, ou na prisão, e fomos ver-te?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '40', 'E, respondendo o Rei, lhes dirá: Em verdade vos digo que, quando o fizestes a um destes meus pequeninos irmãos, a mim o fizestes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '41', 'Então dirá também aos que estiverem à sua esquerda: Apartai-vos de mim, malditos, para o fogo eterno, preparado para o diabo e seus anjos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '42', 'Porque tive fome, e não me destes de comer, tive sede, e não me destes de beber.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '43', 'Sendo estrangeiro, não me recolhestes; estando nu, não me vestistes; e enfermo, e na prisão, não me visitastes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '44', 'Então eles também lhe responderão, dizendo: Senhor, quando te vimos com fome, ou com sede, ou estrangeiro, ou nu, ou enfermo, ou na prisão, e não te servimos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '45', 'Então lhes responderá, dizendo: Em verdade vos digo que, quando a um destes pequeninos o não fizestes, não o fizestes a mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '25', '46', 'E irão estes para o tormento eterno, mas os justos para a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '1', 'E ACONTECEU que, quando Jesus concluiu todos estes discursos, disse aos seus discípulos:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '2', 'Bem sabeis que daqui a dois dias é a Páscoa; e o Filho do homem será entregue para ser crucificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '3', 'Depois os príncipes dos sacerdotes, e os escribas, e os anciãos do povo reuniram-se na sala do sumo sacerdote, o qual se chamava Caifás,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '4', 'E consultaram-se mutuamente para prenderem Jesus com dolo e o matarem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '5', 'Mas diziam: Não durante a festa, para que não haja alvoroço entre o povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '6', 'E, estando Jesus em Betânia, em casa de Simão, o leproso,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '7', 'Aproximou-se dele uma mulher com um vaso de alabastro, com unguento de grande valor, e derramou-lho sobre a cabeça, quando ele estava assentado à mesa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '8', 'E os seus discípulos, vendo isto, indignaram-se, dizendo: Porque é este desperdício?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '9', 'Pois este unguento podia vender-se por grande preço, e dar-se o dinheiro aos pobres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '10', 'Jesus, porém, conhecendo isto, disse-lhes: Porque afligis esta mulher? pois praticou uma boa acção para comigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '11', 'Porquanto sempre tendes convosco os pobres, mas a mim não me haveis de ter sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '12', 'Ora, derramando ela este unguento sobre o meu corpo, fê-lo preparando-me para o meu enterramento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '13', 'Em verdade vos digo que, onde quer que este Evangelho for pregado, em todo o mundo, também será referido o que ela fez, para memória sua.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '14', 'Então um dos doze, chamado Judas Iscariotes, foi ter com os príncipes dos sacerdotes,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '15', 'E disse: Que me quereis dar, e eu vo-lo entregarei? E eles lhe pesaram trinta moedas de prata,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '16', 'E desde então buscava oportunidade para o entregar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '17', 'E, no primeiro dia da festa dos pães asmos, chegaram os discípulos junto de Jesus, dizendo: Onde queres que façamos os preparativos para comeres a páscoa?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '18', 'E ele disse: Ide à cidade a um certo homem, e dizei-lhe: O Mestre diz: O meu tempo está próximo; em tua casa celebrarei a páscoa com os meus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '19', 'E os discípulos fizeram como Jesus lhes ordenara, e prepararam a páscoa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '20', 'E, chegada a tarde, assentou-se à mesa com os doze.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '21', 'E, comendo eles, disse: Em verdade vos digo que um de vós me há-de trair.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '22', 'E eles, entristecendo-se muito, começaram cada um a dizer-lhe: Porventura sou eu, Senhor?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '23', 'E ele, respondendo, disse: O que mete comigo a mão no prato, esse me há-de trair.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '24', 'Em verdade o Filho do homem vai, como acerca dele está escrito, mas ai daquele homem por quem o Filho do homem é traído! Bom seria para esse homem se não houvera nascido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '25', 'E, respondendo Judas, o que o traía, disse: Porventura sou eu, Rabi? Ele disse: Tu o disseste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '26', 'E, quando comiam, Jesus tomou o pão, e, abençoando-o, o partiu, e o deu aos discípulos, e disse: Tomai, comei, isto é o meu corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '27', 'E, tomando o cálix, e dando graças, deu-lho, dizendo: Bebei dele todos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '28', 'Porque isto é o meu sangue, o sangue do Novo Testamento, que é derramado por muitos, para remissão dos pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '29', 'E digo-vos que, desde agora, não beberei deste fruto da vide até àquele dia em que o beba de novo convosco no reino de meu Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '30', 'E, tendo cantado o hino, saíram para o monte das Oliveiras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '31', 'Então Jesus lhes disse: Todos vós esta noite vos escandalizareis em mim; porque está escrito: Ferirei o pastor, e as ovelhas do rebanho se dispersarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '32', 'Mas, depois de eu ressuscitar, irei adiante de vós para a Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '33', 'Mas Pedro, respondendo, disse-lhe: Ainda que todos se escandalizem em ti, eu nunca me escandalizarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '34', 'Disse-lhe Jesus: Em verdade te digo que, nesta mesma noite, antes que o galo cante, três vezes me negarás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '35', 'Disse-lhe Pedro: Ainda que me seja mister morrer contigo, não te negarei. E todos os discípulos disseram o mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '36', 'Então chegou Jesus com eles a um lugar chamado Getsémani, e disse a seus discípulos: Assentai-vos aqui enquanto eu vou além orar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '37', 'E, levando consigo Pedro e os dois filhos de Zebedeu; começou a entristecer-se e a angustiar-se muito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '38', 'Então lhes disse: A minha alma está cheia de tristeza até à morte; ficai aqui, e velai comigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '39', 'E, indo um pouco mais para diante, prostrou-se sobre o seu rosto, orando e dizendo: Meu Pai, se é possível, passe de mim este cálix; todavia, não seja como eu quero, mas como tu queres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '40', 'E voltando para os seus discípulos, achou-os adormecidos; e disse a Pedro: Então nem uma hora pudeste velar comigo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '41', 'Vigiai e orai, para que não entreis em tentação: na verdade, o espírito está pronto, mas a carne é fraca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '42', 'E, indo segunda vez, orou, dizendo: Pai Meu, se este cálix não pode passar de mim sem eu o beber, faça-se a tua vontade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '43', 'E, voltando, achou-os outra vez adormecidos; porque os seus olhos estavam carregados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '44', 'E deixando-os de novo, foi orar pela terceira vez, dizendo as mesmas palavras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '45', 'Então chegou junto dos seus discípulos, e disse-lhes: Dormi agora, e repousai; eis que é chegada a hora, e o Filho do homem será entregue nas mãos dos pecadores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '46', 'Levantai-vos, partamos; eis que é chegado o que me trai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '47', 'E, estando ele ainda a falar, eis que chegou Judas, um dos doze, e com ele grande multidão com espadas e varapaus, enviada pelos príncipes dos sacerdotes e pelos anciãos do povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '48', 'E o que o traía tinha-lhes dado um sinal, dizendo: O que eu beijar é esse; prendei-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '49', 'E logo, aproximando-se de Jesus, disse: Eu te saúdo Rabi. E beijou-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '50', 'Jesus, porém, lhe disse: Amigo, a que vieste? Então, aproximando-se eles, lançaram mão de Jesus, e o prenderam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '51', 'E eis que um dos que estavam com Jesus, estendeu a mão, puxou da espada e, ferindo o servo do sumo sacerdote, cortou-lhe uma orelha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '52', 'Então Jesus disse-lhe: Mete no seu lugar a tua espada; porque todos os que lançarem mão da espada à espada morrerão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '53', 'Ou pensas tu que eu não poderia agora orar a meu Pai, e que ele não me daria mais de doze legiões de anjos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '54', 'Como pois se cumpririam as Escrituras, que dizem que assim convém que aconteça?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '55', 'Então disse Jesus à multidão: Saístes, como para um salteador, com espadas e varapaus para me prender? Todos os dias me assentava junto de vós, ensinando no templo, e não me prendestes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '56', 'Mas tudo isto aconteceu para que se cumpram as escrituras dos profetas. Então todos os discípulos, deixando-o, fugiram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '57', 'E, os que prenderam a Jesus, o conduziram a casa do sumo sacerdote Caifás, onde os escribas e os anciãos estavam reunidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '58', 'E Pedro o seguiu de longe até ao pátio do sumo sacerdote e, entrando, assentou-se entre os criados, para ver o fim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '59', 'Ora os príncipes dos sacerdotes, e os anciãos, e todo o conselho, buscavam falso testemunho contra Jesus, para poderem dar-lhe a morte,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '60', 'E não o achavam, apesar de se apresentarem muitas testemunhas falsas; mas por fim chegaram duas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '61', 'E disseram: Este disse: Eu posso derribar o templo de Deus, e reedificá-lo em três dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '62', 'E, levantando-se o sumo sacerdote, disse-lhe: Não respondes coisa alguma ao que estes depõem contra ti?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '63', 'Jesus, porém, guardava silêncio. E, insistindo o sumo sacerdote, disse-lhe: Conjuro-te pelo Deus vivo que nos digas se tu és o Cristo, o Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '64', 'Disse-lhe Jesus: Tu o disseste; digo-vos, porém, que vereis em breve o Filho do homem assentado à direita do Poder, e vindo sobre as nuvens do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '65', 'Então o sumo sacerdote rasgou os seus vestidos, dizendo: Blasfemou; para que precisamos ainda de testemunhas? Eis que bem ouvistes agora a sua blasfémia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '66', 'Que vos parece? E eles, respondendo, disseram: É réu de morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '67', 'Então cuspiram-lhe no rosto e lhe davam punhadas, e outros o esbofeteavam,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '68', 'Dizendo: Profetiza-nos, Cristo, quem é que te bateu?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '69', 'Ora Pedro estava assentado fora, no pátio; e, aproximando-se dele uma criada, disse: Tu também estavas com Jesus, o galileu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '70', 'Mas ele negou diante de todos, dizendo: Não sei o que dizes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '71', 'E, saindo para o vestíbulo, outra criada o viu, e disse aos que ali estavam: Este também estava com Jesus, o nazareno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '72', 'E ele negou outra vez com juramento: Não conheço tal homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '73', 'E, daí a pouco, aproximando-se os que ali estavam, disseram a Pedro: Verdadeiramente também tu és deles, pois a tua fala te denuncia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '74', 'Então começou ele a praguejar e a jurar, dizendo: Não conheço esse homem. E imediatamente o galo cantou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '26', '75', 'E lembrou-se Pedro das palavras de Jesus, que lhe dissera: Antes que o galo cante, três vezes me negarás. E, saindo dali, chorou amargamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '1', 'E, CHEGANDO a manhã, todos os príncipes dos sacerdotes, e os anciãos do povo, formaram juntamente conselho contra Jesus, para o matarem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '2', 'E maniatando-o, o levaram e entregaram ao presidente Pôncio Pilatos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '3', 'Então Judas, o que o traíra, vendo que fora condenado, trouxe, arrependido, as trinta moedas de prata aos príncipes dos sacerdotes e aos anciãos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '4', 'Dizendo: Pequei, traindo o sangue inocente. Eles, porém, disseram: Que nos importa? Isso é contigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '5', 'E ele, atirando para o templo as moedas de prata, retirou-se e foi-se enforcar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '6', 'E os príncipes dos sacerdotes, tomando as moedas de prata, disseram: Não é lícito metê-las no cofre das ofertas, porque são preço de sangue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '7', 'E, tendo, deliberado em conselho, compraram com elas o campo dum oleiro, para sepultura dos estrangeiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '8', 'Por isso foi chamado aquele campo até ao dia de hoje, Campo de sangue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '9', 'Então se realizou o que vaticinara o profeta Jeremias: Tomaram as trinta moedas de prata, preço do que foi avaliado, que certos filhos de Israel avaliaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '10', 'E deram-nas pelo campo do oleiro, segundo o que o SENHOR determinou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '11', 'E foi Jesus apresentado ao presidente, e o presidente o interrogou, dizendo: És tu o rei dos Judeus? E disse-lhe Jesus: Tu o dizes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '12', 'E, sendo acusado pelos príncipes dos sacerdotes e pelos anciãos, nada respondeu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '13', 'Disse-lhe então Pilatos: Não ouves quanto testificam contra ti?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '14', 'E nem uma palavra lhe respondeu, de sorte que o presidente estava muito maravilhado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '15', 'Ora, por ocasião da festa, costumava o presidente soltar um preso, escolhendo o povo aquele que quisesse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '16', 'E tinham então um preso bem conhecido, chamado Barrabás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '17', 'Portanto, estando eles reunidos, disse-lhes Pilatos: Qual quereis que vos solte? Barrabás, ou Jesus, chamado Cristo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '18', 'Porque sabia que por inveja o haviam entregado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '19', 'E, estando ele assentado no tribunal, sua mulher mandou-lhe dizer: Não entres na questão desse justo, porque num sonho muito sofri por causa dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '20', 'Mas os príncipes dos sacerdotes e os anciãos persuadiram à multidão que pedisse Barrabás e matasse Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '21', 'E, respondendo o presidente, disse-lhes: Qual desses dois quereis vós que eu solte? E eles disseram: Barrabás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '22', 'Disse-lhes Pilatos: Que farei então de Jesus, chamado Cristo? Disseram-lhe todos: Seja crucificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '23', 'O presidente, porém, disse: Mas que mal fez ele? E eles mais clamavam, dizendo: Seja crucificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '24', 'Então Pilatos, vendo que nada aproveitava, antes o tumulto crescia, tomando água, lavou as mãos diante da multidão, dizendo: Estou inocente do sangue deste justo; considerai isso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '25', 'E, respondendo todo o povo, disse: O seu sangue caia sobre nós e sobre nossos filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '26', 'Então soltou-lhes Barrabás, e, tendo mandado açoitar a Jesus, entregou-o para ser crucificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '27', 'E logo os soldados do presidente conduzindo Jesus à audiência, reuniram junto dele toda a coorte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '28', 'E despindo-o, o cobriram com uma capa de escarlate;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '29', 'E, tecendo uma coroa de espinhos, puseram-lha na cabeça, e em sua mão direita uma cana; e, ajoelhando diante dele, o escarneciam, dizendo: Salve, Rei dos judeus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '30', 'E, cuspindo nele, tiraram-lhe a cana, e batiam-lhe com ela na cabeça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '31', 'E, depois de o haverem escarnecido, tiraram-lhe a capa, vestiram-lhe as suas vestes e o levaram para ser crucificado');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '32', 'E, quando saíam, encontraram um homem cireneu, chamado Simão, a quem constrangeram a levar a sua cruz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '33', 'E, chegando ao lugar chamado Gólgota, que se diz: Lugar da Caveira,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '34', 'Deram-lhe a beber vinho misturado com fel; mas ele, provando-o, não quis beber.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '35', 'E, havendo-o crucificado, repartiram os seus vestidos, lançando sortes, para que se cumprisse o que foi dito pelo profeta: Repartiram entre si os meus vestidos, e sobre a minha túnica lançaram sortes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '36', 'E, assentados, o guardavam ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '37', 'E por cima da sua cabeça puseram escrita a sua acusação: ESTE É JESUS, O REI DOS JUDEUS.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '38', 'E foram crucificados com ele dois salteadores, um à direita, e outro à esquerda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '39', 'E os que passavam blasfemavam dele, meneando as cabeças,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '40', 'E dizendo: Tu, que destróis o templo, e em três dias o reedificas, salva-te a ti mesmo; se és Filho de Deus, desce da cruz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '41', 'E da mesma maneira também os príncipes dos sacerdotes, com os escribas, e anciãos, e fariseus, escarnecendo, diziam:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '42', 'Salvou os outros, e a si mesmo não pode salvar-se. Se é o Rei de Israel, desça agora da cruz, e creremos nele ;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '43', 'Confiou em Deus; livre-o agora, se o ama; porque disse: Sou Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '44', 'E o mesmo lhe lançaram também em rosto os salteadores que com ele estavam crucificados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '45', 'E desde a hora sexta houve trevas sobre toda a terra, até à hora nona.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '46', 'E perto da hora nona exclamou Jesus em alta voz, dizendo: Eli, Eli, lama sabactani; isto é, Deus meu, Deus meu, porque me desamparaste?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '47', 'E alguns dos que ali estavam, ouvindo isto, diziam: Este chama por Elias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '48', 'E logo um deles, correndo, tomou uma esponja, e embebeu-a em vinagre, e, pondo-a numa cana, dava-lhe de beber.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '49', 'Os outros, porém, diziam: Deixa, vejamos se Elias vem livrá-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '50', 'E Jesus, clamando outra vez com grande voz, rendeu o espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '51', 'E eis que o véu do templo se rasgou em dois, de alto a baixo; e tremeu a terra, e fenderam-se as pedras,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '52', 'E abriram-se os sepulcros, e muitos corpos de santos que dormiam foram ressuscitados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '53', 'E, saindo dos sepulcros, depois da ressurreição dele entraram na cidade santa e apareceram a muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '54', 'E o centurião e os que com ele guardavam a Jesus, vendo o terramoto, e as coisas que haviam sucedido, tiveram grande temor, e disseram: verdadeiramente este era o Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '55', 'E estavam ali olhando de longe, muitas mulheres que tinham seguido Jesus desde a Galileia, para o servir,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '56', 'Entre as quais estavam Maria Madalena, e Maria, mãe de Tiago e de José, e a mãe dos filhos de Zebedeu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '57', 'E vinda já a tarde, chegou um homem rico de Arimateia, por nome José, que também era discípulo de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '58', 'Este foi ter com Pilatos, e pediu-lhe o corpo de Jesus. Então Pilatos mandou que o corpo lhe fosse dado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '59', 'E José, tomando o corpo, envolveu-o num fino e limpo lençol.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '60', 'E o pôs no seu sepulcro novo, que havia aberto em rocha, e, rodando uma grande pedra para a porta do sepulcro, foi-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '61', 'E estavam ali Maria Madalena e a outra Maria, assentadas defronte do sepulcro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '62', 'E no dia seguinte, que é o dia da Preparação, reuniram-se os príncipes dos sacerdotes e os fariseus em casa de Pilatos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '63', 'Dizendo: Senhor, lembramo-nos de que aquele enganador, vivendo ainda, disse: Depois de três dias ressuscitarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '64', 'Manda pois que o sepulcro seja guardado com segurança até ao terceiro dia não se dê o caso que os seus discípulos vão de noite e o furtem, e digam ao povo: Ressuscitou dos mortos; e assim o último erro será pior do que o primeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '65', 'Disse-lhes Pilatos: Tendes a guarda; ide, guardai-o como entenderdes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '27', '66', 'E, indo eles, seguraram o sepulcro com a guarda, selando a pedra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '1', 'E, NO fim do sábado, quando já despontava o primeiro dia da semana, Maria Madalena e a outra Maria foram ver o sepulcro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '2', 'E eis que houvera um grande terramoto, porque um anjo do SENHOR, descendo do céu, chegou removendo a pedra, e sentou-se sobre ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '3', 'E o seu aspecto era como um relâmpago, e o seu vestido branco como neve.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '4', 'E os guardas com medo dele, ficaram muito assombrados, e como mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '5', 'Mas o anjo, respondendo, disse às mulheres: Não tenhais medo; pois eu sei que buscais a Jesus, que foi crucificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '6', 'Ele não está aqui, porque já ressuscitou, como havia dito. Vinde, vede o lugar onde o SENHOR jazia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '7', 'Ide pois imediatamente, e dizei aos seus discípulos que já ressuscitou dos mortos. E eis que ele vai adiante de vós para a Galileia; ali o vereis. Eis que eu vo-lo tenho dito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '8', 'E, saindo elas pressurosamente do sepulcro, com temor e grande alegria, correram a anunciá-lo aos seus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '9', 'E, indo elas, eis que Jesus lhes sai ao encontro, dizendo: Eu vos saúdo. E elas, chegando, abraçaram os seus pés, e o adoraram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '10', 'Então Jesus disse-lhes: Não temais; ide dizer a meus irmãos que vão a Galileia, e lá me verão:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '11', 'E, quando iam, eis que alguns da guarda, chegando à cidade, anunciaram aos príncipes dos sacerdotes, todas as coisas que haviam acontecido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '12', 'E, congregados eles com os anciãos, e tomando conselho entre si, deram muito dinheiro aos soldados, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '13', 'Dizei: Vieram de noite os seus discípulos, e, dormindo nós, o furtaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '14', 'E, se isto chegar a ser ouvido pelo presidente, nós o persuadiremos, e vos poremos em segurança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '15', 'E eles recebendo o dinheiro, fizeram como estavam instruídos. E foi divulgado este dito entre os judeus, até ao dia de hoje.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '16', 'E os onze discípulos partiram para a Galileia, para o monte que Jesus lhes tinha designado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '17', 'E, quando o viram o adoraram; mas alguns duvidaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '18', 'E, chegando-se Jesus, falou-lhes, dizendo: É-me dado todo o poder no céu e na terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '19', 'Portanto ide, ensinai todas as nações, baptizando-as em nome do Pai, e do filho e do Espírito Santo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('1', '28', '20', 'Ensinando-as a guardar todas as coisas que eu vos tenho mandado; e eis que eu estou convosco todos os dias, até à consumação dos séculos. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '1', '¶ PRINCIPIO do Evangelho de Cristo, Filho de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '2', 'Como está escrito no profeta Isaías: Eis que eu envio o meu anjo ante a tua face, o qual preparará o teu caminho diante de ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '3', 'Voz do que clama no deserto: Preparai o caminho do SENHOR, endireitai as suas veredas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '4', 'Apareceu João baptizando no deserto, e pregando o baptismo de arrependimento, para remissão dos pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '5', 'E toda a província da Judeia e os de Jerusalém iam ter com ele; e todos eram baptizados por ele no rio Jordão, confessando os seus pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '6', 'E João andava vestido de pêlos de camelo, e com um cinto de couro em redor de seus lombos, e comia gafanhotos e mel silvestre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '7', 'E pregava, dizendo: Após mim vem aquele, que é mais forte do que eu, ao qual não sou digno de, abaixando-me, desatar a correia das suas alparcas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '8', 'Eu, em verdade, tenho-vos baptizado com água; ele, porém, vos baptizará com o Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '9', 'E aconteceu naqueles dias que Jesus, tendo ido de Nazaré, da Galileia, foi baptizado por João, no Jordão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '10', 'E, logo que saiu da água, viu os céus abertos, e o Espírito, que como pomba descia sobre ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '11', 'E ouviu-se uma voz dos céus, que dizia: Tu és o meu Filho amado em quem me comprazo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '12', 'E logo o Espírito o impeliu para o deserto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '13', 'E ali esteve no deserto quarenta dias, tentado por Satanás. E vivia entre as feras, e os anjos o serviam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '14', 'E depois que João foi entregue à prisão, veio Jesus para a Galileia, pregando o Evangelho do reino de Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '15', 'E dizendo: O tempo está cumprido, e o reino de Deus está próximo. Arrependei-vos, e crede no Evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '16', 'E, andando junto do mar da Galileia, viu Simão, e André, seu irmão, que lançavam a rede ao mar, pois eram pescadores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '17', 'E Jesus lhes disse: Vinde após mim, e eu farei que sejais pescadores de homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '18', 'E, deixando logo as suas redes, o seguiram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '19', 'E, passando dali um pouco mais adiante, viu Tiago filho de Zebedeu, e João, seu irmão, que estavam no barco consertando as redes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '20', 'E logo os chamou. E eles, deixando o seu pai Zebedeu no barco com os jornaleiros, foram após ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '21', 'Entraram em Cafarnaum, e, logo no sábado, indo ele à sinagoga, ali ensinava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '22', 'E maravilharam-se da sua doutrina, porque os ensinava como tendo autoridade, e não como os escribas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '23', 'E estava na sinagoga deles um homem com um espírito imundo, o qual exclamou, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '24', 'Ah! que temos contigo, Jesus nazareno? Vieste destruir-nos? Bem sei quem és: o Santo de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '25', 'E repreendeu-o Jesus, dizendo: Cala-te, e sai dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '26', 'Então o espírito imundo, convulsionando-o, e clamando com grande voz, saiu dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '27', 'E todos se admiraram, a ponto de perguntarem entre si, dizendo: Que é isto? Que nova doutrina é esta? Pois com autoridade ordena aos espíritos imundos, e eles lhe obedecem!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '28', 'E logo correu a sua fama por toda a província da Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '29', 'E logo, saindo da sinagoga, foram a casa de Simão e de André com Tiago e João.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '30', 'E a sogra de Simão estava deitada com febre; e logo lhe falaram dela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '31', 'Então, chegando-se a ela, tomou-a pela mão, e levantou-a; e a febre a deixou, e servia-os.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '32', 'E, tendo chegado a tarde, quando já se estava pondo o sol, trouxeram-lhe todos os que se achavam enfermos, e os endemoninhados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '33', 'E toda a cidade se ajuntou à porta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '34', 'E curou muitos que se achavam enfermos de diversas enfermidades, e expulsou muitos demónios, porém não deixava falar os demónios, porque o conheciam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '35', 'E, levantando-se de manhã muito cedo, fazendo ainda escuro, saiu, e foi para um lugar deserto, e ali orava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '36', 'E seguiram-no Simão e os que com ele estavam;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '37', 'E, achando-o, lhe disseram: Todos te buscam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '38', 'E ele lhes disse: Vamos às aldeias vizinhas, para que eu ali também pregue; porque para isso vim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '39', 'E pregava nas sinagogas deles por toda a Galileia, e expulsava os demónios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '40', 'E aproximando-se dele um leproso, que rogando-lhe, e pondo-se de joelhos diante dele, lhe dizia: Se queres, bem podes limpar-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '41', 'E Jesus, movido de grande compaixão, estendeu a mão, e tocou-o, e disse-lhe: Quero, sê limpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '42', 'E, tendo ele dito isto, logo a lepra desapareceu, e ficou limpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '43', 'E, advertindo-o severamente, logo o despediu,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '44', 'E disse-lhe: Olha, não digas nada a ninguém; porém vai, mostra-te ao sacerdote, e oferece pela tua purificação o que Moisés determinou, para lhes servir de testemunho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '1', '45', 'Mas, tendo ele saído, começou a apregoar muitas coisas, e a divulgar o que acontecera; de sorte que Jesus já não podia entrar publicamente na cidade, mas conservava-se fora em lugares desertos; e de todas as partes iam ter com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '1', 'E ALGUNS dias depois entrou outra vez em Cafarnaum, e soube-se que estava em casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '2', 'E logo se ajuntaram tantos, que nem ainda nos lugares junto à porta cabiam; e anunciava-lhes a palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '3', 'E vieram ter com ele conduzindo um paralítico, trazido por quatro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '4', 'E, não podendo aproximar-se dele, por causa da multidão, descobriram o telhado onde estava, e, fazendo um buraco, baixaram o leito em que jazia o paralítico.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '5', 'E Jesus, vendo a fé deles, disse ao paralítico: Filho, perdoados estão os teus pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '6', 'E estavam ali assentados alguns dos escribas, que arrazoavam em seus corações, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '7', 'Porque diz este assim blasfémias? Quem pode perdoar pecados, senão Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '8', 'E Jesus, conhecendo logo em seu espírito que assim arrazoavam entre si, lhes disse: Porque arrazoais sobre estas coisas em vossos corações?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '9', 'Qual é mais fácil? dizer ao paralítico: Estão perdoados os teus pecados; ou dizer-lhe: Levanta-te, e toma o teu leito, e anda?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '10', 'Ora para que saibais que o Filho do homem tem na terra poder para perdoar pecados (disse ao paralítico),');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '11', 'A ti te digo: Levanta-te, toma o teu leito, e vai para tua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '12', 'E levantou-se, e, tomando logo o leito, saiu em presença de todos, de sorte que todos se admiraram e glorificaram a Deus, dizendo: Nunca tal vimos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '13', 'E tornou a sair para o mar, e toda a multidão ia ter com ele, e ele os ensinava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '14', 'E, passando, viu Levi, filho de Alfeu, sentado na alfândega, e disse-lhe: Segue-me. E, levantando-se, o seguiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '15', 'E aconteceu que, estando sentado à mesa em casa deste, também estavam sentados à mesa com Jesus e seus discípulos muitos publicanos e pecadores; porque eram muitos, e o tinham seguido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '16', 'E os escribas e fariseus, vendo-o comer com os publicanos e pecadores disseram aos seus discípulos: Porque come e bebe ele com os publicanos e pecadores?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '17', 'E Jesus, tendo ouvido isto, disse-lhes: Os sãos não necessitam de médico, mas, sim, os que estão doentes; eu não vim chamar os justos, mas sim os pecadores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '18', 'Ora os discípulos de João e os fariseus jejuavam; e foram e disseram-lhe: Porque jejuam os discípulos de João e os dos fariseus, e não jejuam os teus discípulos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '19', 'E Jesus disse-lhes: Podem porventura os filhos das bodas jejuar enquanto está com eles o esposo? Enquanto têm consigo o esposo, não podem jejuar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '20', 'Mas dias virão em que lhes será tirado o esposo, e então jejuarão naqueles dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '21', 'Ninguém deita remendo de pano novo em vestido velho; doutra sorte o mesmo remendo novo rompe o velho, e a rotura fica maior;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '22', 'E ninguém deita vinho novo em odres velhos; doutra sorte, o vinho novo rompe os odres, e entorna-se o vinho, e os odres estragam-se; o vinho novo deve ser deitado em odres novos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '23', 'E aconteceu que, passando ele num sábado pelas searas, os seus discípulos, caminhando, começaram a colher espigas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '24', 'E os fariseus lhe disseram: Vês? Porque fazem no sábado o que não é lícito?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '25', 'Mas ele disse-lhes: Nunca lestes o que fez David quando estava em necessidade e teve fome, ele e os que com ele estavam?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '26', 'Como entrou na casa de Deus, no templo de Abiatar, sumo sacerdote, e comeu os pães da proposição dos quais não era lícito comer senão aos sacerdotes, dando também aos que com ele estavam?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '27', 'E disse-lhes: O sábado foi feito por causa do homem, e não o homem por causa do sábado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '2', '28', 'Assim o Filho do homem até do sábado é Senhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '1', 'E OUTRA vez entrou na sinagoga, e estava ali um homem que tinha uma das mãos mirradas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '2', 'E estavam observando-o se curaria no sábado, para o acusarem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '3', 'E disse ao homem que tinha a mão mirrada: Levanta-te e vem para o meio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '4', 'E perguntou-lhes: É lícito no sábado fazer bem, ou fazer mal? Salvar a vida, ou matar? E eles calaram-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '5', 'E, olhando para eles, em redor com indignação, condoendo-se da dureza do seu coração, disse ao homem: Estende a tua mão. E ele a estendeu, e foi-lhe restituída a sua mão, sã como a outra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '6', 'E, tendo saído os fariseus, tomaram logo conselho com os herodianos contra ele, procurando ver como o matariam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '7', 'E retirou-se Jesus com os seus discípulos para o mar, e seguia-o uma grande multidão da Galileia e da Judeia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '8', 'E de Jerusalém, e da Idumeia, e de além do Jordão, e de perto de Tiro e de Sidon; uma grande multidão, que ouvindo quão grandes coisas fazia, vinha ter com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '9', 'E ele disse aos seus discípulos que lhe tivessem sempre pronto um barquinho junto dele, por causa da multidão, para que o não oprimisse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '10', 'Porque tinha curado a muitos, de tal maneira que todos quantos tinham algum mal se arrojavam sobre ele, para lhe tocarem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '11', 'E os espíritos imundos, vendo-o, prostravam-se diante dele, e clamavam, dizendo: Tu és o Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '12', 'E ele os ameaçava muito, para que não o manifestassem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '13', 'E subiu ao monte, e chamou para si os que ele quis; e vieram a ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '14', 'E nomeou doze para que estivessem com ele e os mandasse a pregar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '15', 'E para que tivessem o poder de curar as enfermidades e expulsar os demónios:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '16', 'A Simão, a quem pôs o nome de Pedro,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '17', 'E a Tiago, filho de Zebedeu, e a João, irmão de Tiago, aos quais pôs o nome de Boanerges, que significa Filhos do trovão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '18', 'E a André, e a Filipe, e a Bartolomeu, e a Mateus, e a Tomé, e a Tiago, filho de Alfeu, e a Tadeu, e a Simão, o cananeu,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '19', 'E a Judas Iscariotes, o que o entregou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '20', 'E foram para uma casa. E afluiu outra vez a multidão, de tal maneira que nem sequer podiam comer pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '21', 'E, quando os seus ouviram isto, saíram para o prender; porque diziam: Está fora de si.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '22', 'E os escribas, que tinham descido de Jerusalém, diziam: Tem Belzebu, e pelo príncipe dos demónios expulsa os demónios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '23', 'E, chamando-os a si, disse-lhes por parábolas: Como pode Satanás expulsar Satanás?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '24', 'E, se um reino se dividir contra si mesmo, tal reino não pode subsistir;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '25', 'E, se uma casa se dividir contra si mesma, tal casa não pode subsistir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '26', 'E, se Satanás se levantar contra si mesmo, e for dividido, não pode subsistir, antes tem fim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '27', 'Ninguém pode roubar os bens do valente, entrando-lhe em sua casa, se primeiro não maniatar o valente; e então roubará a sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '28', 'Na verdade vos digo que todos os pecados serão perdoados aos filhos dos homens, e toda a sorte de blasfémias, com que blasfemarem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '29', 'Qualquer porém, que blasfemar contra o Espírito Santo, nunca obterá perdão, mas será réu do eterno juízo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '30', '(Porque diziam: Tem espírito imundo).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '31', 'Chegaram então seus irmãos e sua mãe; e, estando de fora mandaram-no chamar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '32', 'E a multidão estava assentada ao redor dele, e disseram-lhe: Eis que tua mãe e teus irmãos te procuram e estão lá fora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '33', 'E ele lhes respondeu, dizendo: Quem é minha mãe e meus irmãos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '34', 'E, olhando em redor para os que estavam assentados junto dele, disse: Eis aqui minha mãe e meus irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '3', '35', 'Porquanto qualquer que fizer a vontade de Deus esse é meu irmão, e minha irmã, e minha mãe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '1', 'E OUTRA vez começou a ensinar junto do mar, e ajuntou-se a ele grande multidão, de sorte que ele entrou e assentou-se num barco, sobre o mar; e toda a multidão estava em terra junto do mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '2', 'Ensinava-lhes muitas coisas por parábolas e lhes dizia na sua doutrina:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '3', 'Ouvi: Eis que saiu o semeador a semear;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '4', 'E aconteceu que, semeando ele, uma parte da semente caiu junto do caminho, e vieram as aves do céu, e a comeram;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '5', 'E outra caiu sobre pedregais, onde não havia muita terra, e nasceu logo, porque não tinha terra profunda;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '6', 'Mas, saindo o sol, queimou-se; e, porque não tinha raiz, secou-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '7', 'E outra caiu entre espinhos, e crescendo os espinhos, a sufocaram e não deu fruto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '8', 'E outra caiu em boa terra e deu fruto, que vingou e cresceu; e um produziu trinta, outro sessenta, e outro cem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '9', 'E disse-lhe: Quem tem ouvidos para ouvir, ouça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '10', 'E, quando se achou só, os que estavam junto dele com os doze interrogaram-no acerca da parábola.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '11', 'E ele disse-lhes: A vós vos é dado saber os mistérios do reino de Deus, mas aos que estão de fora todas estas coisas se dizem por parábolas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '12', 'Para que, vendo, vejam, e não percebam; e, ouvindo, ouçam, e não entendam; para que se não convertam, e lhes sejam perdoados os pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '13', 'E disse-lhes: Não percebeis esta parábola? como pois entendereis todas as parábolas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '14', 'O que semeia, semeia a palavra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '15', 'E os que estão junto do caminho são aqueles em quem a palavra é semeada; mas, tendo-a eles ouvido, vem logo Satanás e tira a palavra que foi semeada nos seus corações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '16', 'E da mesma sorte os que recebem a semente sobre pedregais; os quais, ouvindo a palavra, logo com prazer a recebem,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '17', 'Mas não têm raiz em si mesmos, antes são temporãos; depois, sobrevindo tribulação ou perseguição por causa da palavra, logo se escandalizam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '18', 'E outros são os que recebem a semente entre espinhos, os quais ouvem a palavra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '19', 'Mas os cuidados deste mundo, e os enganos das riquezas e as ambições doutras coisas, entrando, sufocam a palavra, e fica infrutífera.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '20', 'E os que recebem a semente em boa terra são os que ouvem a palavra e a recebem, e dão fruto, um a trinta, outro a sessenta, outro a cem, por um.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '21', 'E disse-lhes: Vem porventura a candeia para se meter debaixo do alqueire, ou debaixo da cama? Não vem antes para se colocar no velador?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '22', 'Porque nada há encoberto que não haja de ser manifesto; e nada se faz para ficar oculto, mas para ser descoberto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '23', 'Se alguém tem ouvidos para ouvir, ouça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '24', 'E disse-lhes: Atendei ao que ides ouvir. Com a medida com que medirdes vos medirão a vós, e ser-vos-á ainda acrescentada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '25', 'Porque ao que tem, ser-lhe-á dado; e, ao que não tem, até o que tem lhe será tirado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '26', 'E dizia: O reino de Deus é assim como se um homem lançasse semente à terra,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '27', 'E dormisse, e se levantasse de noite ou de dia, e a semente brotasse e crescesse, não sabendo ele como.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '28', 'Porque a terra por si mesma frutifica, primeiro a erva, depois a espiga, por último o grão cheio na espiga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '29', 'E, quando já o fruto se mostra, mete-lhe logo a foice, porque está chegada a ceifa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '30', 'E dizia: A que assemelharemos o reino de Deus? Ou com que parábola o representaremos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '31', 'É como um grão de mostarda, que, quando se semeia na terra, é a mais pequena de todas as sementes que há na terra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '32', 'Mas, tendo sido semeado, cresce; e faz-se a maior de todas as hortaliças, e cria grandes ramos, de tal maneira que as aves do céu podem aninhar-se debaixo da sua sombra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '33', 'E com muitas parábolas tais lhes dirigia a palavra, segundo o que podiam compreender.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '34', 'E sem parábolas nunca lhes falava; Porém tudo declarava em particular aos seus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '35', 'E, naquele dia, sendo já tarde, disse-lhes: Passemos para a outra banda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '36', 'E eles, deixando a multidão, o levaram consigo, assim como estava, no barco; e havia também com ele outros barquinhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '37', 'E levantou-se grande temporal de vento, e subiam as ondas por cima do barco, de maneira que já se enchia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '38', 'E ele estava na popa dormindo sobre uma almofada, e despertaram-no, dizendo-lhe: Mestre, não se te dá que pereçamos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '39', 'E ele, despertando, repreendeu o vento, e disse ao mar: Cala-te, aquieta-te. E o vento se aquietou, e houve grande bonança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '40', 'E disse-lhes: Porque sois tão tímidos? Ainda não tendes fé?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '4', '41', 'E sentiram um grande temor, e diziam uns aos outros: Mas quem é este, que até o vento e o mar lhe obedecem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '1', 'E CHEGARAM à outra banda do mar, à província dos gadarenos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '2', 'E, saindo ele do barco, lhe saiu logo ao seu encontro, dos sepulcros, um homem com espírito imundo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '3', 'O qual tinha a sua morada nos sepulcros, e nem ainda com cadeias o podia alguém prender;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '4', 'Porque, tendo sido muitas vezes preso com grilhões e cadeias, as cadeias foram por ele feitas em pedaços, e os grilhões em migalhas, e ninguém o podia amansar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '5', 'E andava sempre, de dia e de noite, clamando pelos montes, e pelos sepulcros, e ferindo-se com pedras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '6', 'E, quando viu Jesus ao longe, correu e adorou-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '7', 'E clamando com grande voz, disse: Que tenho eu contigo, Jesus, Filho do Deus Altíssimo? Conjuro-te por Deus que não me atormentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '8', '(Porque lhe dizia: Sai deste homem, espírito imundo).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '9', 'E perguntou-lhe: Qual é o teu nome? E lhe respondeu, dizendo: Legião é o meu nome, porque somos muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '10', 'E rogava-lhe muito que os não enviasse para fora daquela província.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '11', 'E andava ali pastando no monte uma grande manada de porcos');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '12', 'E todos aqueles demónios lhe rogaram, dizendo: Manda-nos para aqueles porcos, para que entremos neles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '13', 'E Jesus logo lho permitiu. E, saindo aqueles espíritos imundos, entraram nos porcos; e a manada se precipitou por um despenhadeiro no mar (eram quase dois mil), e afogaram-se no mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '14', 'E os que apascentavam os porcos fugiram, e o anunciaram na cidade e nos campos; e saíram muitos a ver o que era aquilo que tinha acontecido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '15', 'E foram ter com Jesus, e viram o endemoninhado, o que tivera a legião, assentado, vestido e em perfeito juízo, e temeram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '16', 'E os que aquilo tinham visto contaram-lhes o que acontecera ao endemoninhado; e acerca dos porcos:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '17', 'E começaram a rogar-lhe que saísse dos seus termos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '18', 'E, entrando ele no barco, rogava-lhe o que fora endemoninhado que o deixasse estar com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '19', 'Jesus, porém, não lho permitiu, mas disse-lhes: Vai para tua casa, para os teus, e anuncia-lhes quão grandes coisas o SENHOR te fez, e como teve misericórdia de ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '20', 'E ele foi, e começou a anunciar em Dacápolis quão grandes coisas Jesus lhe fizera; e todos se maravilhavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '21', 'E, passando Jesus outra vez num barco para a outra banda, ajuntou-se a ele uma grande multidão; e ele estava junto do mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '22', 'E, eis que chegou um dos principais da sinagoga, por nome Jairo, e, vendo-o, prostrou-se aos seus pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '23', 'E rogava-lhe muito, dizendo: Minha filha está moribunda; rogo-te que venhas e lhe imponhas as mãos para que sare, e viva.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '24', 'E foi com ele, e seguia-o uma grande multidão, que o apertava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '25', 'E certa mulher, que havia doze anos tinha um fluxo de sangue,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '26', 'E que havia padecido muito com muitos médicos, e despendido tudo quanto tinha, nada lhe aproveitando isso, antes indo a pior;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '27', 'Ouvindo falar de Jesus, veio por detrás entre a multidão, e tocou no seu vestido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '28', 'Porque dizia: Se tão somente tocar nos seus vestidos, sararei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '29', 'E logo se lhe secou a fonte do seu sangue; e sentiu no seu corpo estar já curada daquele mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '30', 'E logo Jesus, conhecendo que a virtude de si mesmo, saíra, voltou-se para a multidão, e disse: Quem tocou nos meus vestidos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '31', 'E disseram-lhe os seus discípulos: Vês que a multidão te aperta, e dizes: Quem me tocou?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '32', 'E ele olhava em redor, para ver a que isto fizera.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '33', 'Então a mulher, que sabia o que lhe tinha acontecido, temendo e tremendo, aproximou-se dele, e disse-lhe toda a verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '34', 'E ele lhe disse: Filha, a tua fé te salvou; vai em paz, e sê curada deste teu mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '35', 'E estando ele ainda falando, chegaram alguns do principal da sinagoga; a quem disseram: A tua filha está morta; para que enfadas mais o Mestre?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '36', 'E Jesus, tendo ouvido estas palavras, disse ao principal da sinagoga: Não temas, crê somente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '37', 'E não permitiu que alguém o seguisse, a não ser Pedro, Tiago, e João, irmão de Tiago.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '38', 'E, tendo chegado a casa do principal da sinagoga, viu o alvoroço, e os que choravam muito e pranteavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '39', 'E, entrando, disse-lhes: Porque vos alvoroçais e chorais? A menina não está morta, mas dorme.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '40', 'E riam-se dele; porém, ele, tendo-os feito sair, tomou consigo o pai e a mãe da menina, e os que com ele estavam, e entrou onde a menina estava deitada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '41', 'E, tomando a mão da menina, disse-lhe: Talita cumi - que, traduzido, é: Menina, a ti te digo, levanta-te.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '42', 'E logo a menina se levantou, e andava, pois já tinha doze anos; e assombraram-se com grande espanto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '5', '43', 'E mandou-lhes expressamente que ninguém o soubesse; e disse que lhe dessem de comer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '1', 'E PARTINDO dali, chegou à sua pátria, e os seus discípulos o seguiram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '2', 'E, chegando o sábado, começou a ensinar na sinagoga; e muitos, ouvindo-o, se admiravam, dizendo: Donde lhe vêm estas coisas? e que sabedoria é esta que lhe foi dada? e como se fazem tais maravilhas por suas mãos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '3', 'Não é este o carpinteiro, filho de Maria, e irmão de Tiago, e de José, e de Judas e de Simão? E não estão aqui connosco, suas irmãs? E escandalizavam-se nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '4', 'E Jesus lhes dizia: Não há profeta sem honra senão na sua pátria, entre os seus parentes, e na sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '5', 'E não podia fazer ali obras maravilhosas; somente curou alguns poucos enfermos, impondo-lhes as mãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '6', 'E estava admirado da incredulidade deles. E percorreu as aldeias vizinhas, ensinando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '7', 'Chamou a si os doze, e começou a enviá-los dois a dois, e deu-lhes poder sobre os espíritos imundos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '8', 'E ordenou-lhes que nada tomassem para o caminho, senão somente um bordão; nem alforge, nem pão, nem dinheiro no cinto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '9', 'Mas que calçassem alparcas, e que não vestissem duas túnicas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '10', 'E dizia-lhes: Na casa em que entrardes, ficai nela até partirdes dali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '11', 'E, quando alguns vos não receberem, nem vos ouvirem, saindo dali, sacudi o pó que estiver debaixo dos vossos pés, em testemunho contra eles. Em verdade vos digo que haverá mais tolerância no dia de juízo para Sodoma e Gomorra do que para os daquela cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '12', 'E, saindo eles, pregavam que se arrependessem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '13', 'E expulsavam muitos demónios, e ungiam muitos enfermos com óleo, e os curavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '14', 'E ouviu isto o rei Herodes (porque o nome de Jesus se tornara notório), e disse: João, o que baptizava, ressuscitou dos mortos, e por isso estas maravilhas operam nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '15', 'Outros diziam: É Elias. E diziam outros: É um profeta, ou como um dos profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '16', 'Herodes, porém, ouvindo isto, disse: Este é João, que mandei degolar; ressuscitou dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '17', 'Porquanto o mesmo Herodes mandara prender a João, e encerrá-lo maniatado no cárcere, por causa de Herodias, mulher de Filipe, seu irmão, porquanto tinha casado com ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '18', 'Pois João dizia a Herodes: Não te é lícito possuir a mulher de teu irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '19', 'E Herodias o espiava, e queria matá-lo, mas não podia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '20', 'Porque Herodes temia a João, sabendo que era varão justo e santo; e guardava-o com segurança, e fazia muitas coisas, atendendo-o, e de boamente o ouvia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '21', 'E, chegando uma ocasião favorável em que Herodes, no dia dos seus anos, dava uma ceia aos grandes, e tribunos, e príncipes da Galileia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '22', 'Entrou a filha da mesma Herodias, e dançou, e agradou a Herodes e aos que estavam com ele à mesa; disse então o rei à menina: Pede-me o que quiseres, e eu to darei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '23', 'E jurou-lhe, dizendo: Tudo o que me pedires te darei, até metade do meu reino.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '24', 'E, saindo ela, perguntou a sua mãe: Que pedirei? Ela disse: A cabeça de João Baptista.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '25', 'E, entrando logo apressadamente, pediu ao rei, dizendo: Quero que imediatamente me dês num prato a cabeça de João Baptista.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '26', 'E o rei entristeceu-se muito; todavia, por causa do juramento e dos que estavam com ele à mesa, não lha quis negar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '27', 'E, enviando logo o rei o executor, mandou que lhe trouxessem ali a cabeça de João. E ele foi, e degolou-o na prisão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '28', 'E trouxe a cabeça num prato, e deu-a à menina, e a menina a deu a sua mãe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '29', 'E os seus discípulos, tendo ouvido isto, foram, tomaram o seu corpo, e o puseram num sepulcro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '30', 'E os apóstolos ajuntaram-se a Jesus, e contaram-lhe tudo, tanto o que tinham feito como o que tinham ensinado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '31', 'E ele disse-lhes: Vinde vós, aqui à parte, a um lugar deserto, e repousai um pouco. Porque havia muitos que iam e vinham, e não tinham tempo para comer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '32', 'E foram sós num barco para um lugar deserto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '33', 'E a multidão viu-os partir, e muitos o conheciam; e correram para lá a pé de todas as cidades, e ali chegaram primeiro do que eles, e aproximavam-se dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '34', 'E Jesus, saindo, viu uma grande multidão, e teve compaixão deles, porque eram como ovelhas que não têm pastor; e começou a ensinar-lhes muitas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '35', 'E, como o dia fosse já muito adiantado, os seus discípulos se aproximaram dele e lhe disseram: O lugar é deserto, e o dia está já muito adiantado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '36', 'Despede-os, para que vão aos lugares e aldeias circunvizinhas, e comprem pão para si; porque não têm que comer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '37', 'Ele, porém, respondendo, lhes disse: Dai-lhes vós de comer. E eles disseram-lhe: Iremos nós; e compraremos duzentos dinheiros de pão para lhes darmos de comer?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '38', 'E ele disse-lhes: Quantos pães tendes? Ide ver. E, sabendo-o eles, disseram: Cinco pães e dois peixes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '39', 'E ordenou-lhes que fizessem assentar a todos, em ranchos, sobre a erva verde.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '40', 'E assentaram-se repartidos de cem em cem, e de cinquenta em cinquenta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '41', 'E, tomando ele os cinco pães e os dois peixes, levantou os olhos ao céu, abençoou e partiu os pães, e deu-os aos seus discípulos para que os pusessem diante deles. E repartiu os dois peixes por todos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '42', 'E todos comeram, e ficaram fartos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '43', 'E levantaram doze cestos cheios de pedaços de pão e de peixe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '44', 'E os que comeram os pães eram quase cinco mil homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '45', 'E logo obrigou os seus discípulos a subir para o barco, e passar adiante, para a outra banda, a Betsaida, enquanto ele despedia a multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '46', 'E, tendo-se despedido, foi ao monte orar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '47', 'E, sobrevindo a tarde estava o barco no meio do mar e ele sozinho em terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '48', 'E vendo que se fatigavam a remar, porque o vento lhes era contrário, perto da quarta vigília da noite aproximou-se deles, andando sobre o mar, e queria passar-lhes adiante.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '49', 'Mas, quando eles o viram andar sobre o mar, cuidaram que era um fantasma, e deram grandes gritos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '50', 'Porque todos o viam, e perturbaram-se; mas logo falou com eles, e disse-lhes: Tende bom ânimo; sou eu, não temais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '51', 'E subiu para o barco para estar com eles, e o vento se aquietou; e entre si ficaram muito assombrados e maravilhados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '52', 'Pois não tinham compreendido o milagre dos pães; antes o seu coração estava endurecido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '53', 'E, quando já estavam na outra banda, dirigiram-se à terra de Genezaré, e ali atracaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '54', 'E, saindo eles do barco, logo o conheceram;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '55', 'E, correndo toda a terra em redor, começaram a trazer em leitos, aonde quer que sabiam que ele estava, os que se achavam enfermos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '6', '56', 'E, onde quer que entrava, ou em cidade, ou aldeias, ou no campo, apresentavam os enfermos nas praças, e rogavam-lhe que os deixasse tocar ao menos na orla do seu vestido; e todos os que lhe tocavam saravam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '1', 'E AJUNTARAM-SE a eles os fariseus, e alguns dos escribas que tinham vindo de Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '2', 'E, vendo que alguns dos seus discípulos comiam pão com as mãos impuras, isto é, por lavar, os repreendiam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '3', 'Porque os fariseus, e todos os judeus, conservando a tradição dos antigos, não comem sem lavar as mãos muitas vezes;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '4', 'E, quando voltam do mercado, se não se lavarem, não comem. E muitas outras coisas há que receberam para observar, como lavar os copos, e os jarros, e os vasos de metal e as camas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '5', 'Depois perguntaram-lhe os fariseus e os escribas: Porque não andam os teus discípulos conforme a tradição dos antigos, mas comem o pão com as mãos por lavar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '6', 'E ele, respondendo, disse-lhes: Bem profetizou Isaías acerca de vós, hipócritas, como está escrito: Este povo honra-me com os lábios, mas o seu coração está longe de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '7', 'Em vão, porém, me honram, ensinando doutrinas que são mandamentos de homens:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '8', 'Porque; deixando o mandamento de Deus, retendes a tradição dos homens; como o lavar dos jarros e dos copos; e fazeis muitas outras coisas semelhantes a estas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '9', 'E dizia-lhes: Bem invalidais o mandamento de Deus para guardardes a vossa tradição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '10', 'Porque Moisés disse: Honra a teu pai e a tua mãe; e quem maldisser, ou o pai ou a mãe, morrerá de morte:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '11', 'Porém vós dizeis: Se um homem disser ao pai ou à mãe: Aquilo que poderias aproveitar de mim é Corbã, isto é, oferta ao SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '12', 'Nada mais lhe deixais fazer por seu pai ou por sua mãe,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '13', 'Invalidando assim a palavra de Deus pela vossa tradição, que vós ordenastes. E muitas coisas fazeis semelhantes a estas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '14', 'E, chamando outra vez a multidão, disse-lhes: Ouvi-me vós todos, e compreendei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '15', 'Nada há fora do homem que, entrando nele, o possa contaminar; mas o que sai dele isso é que contamina o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '16', 'Se alguém tem ouvidos para ouvir, ouça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '17', 'Depois, quando deixou a multidão, e entrou em casa, os seus discípulos o interrogavam acerca desta parábola.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '18', 'E ele disse-lhes: Assim também vós estais sem entendimento? Não compreendeis que tudo o que de fora entra no homem não o pode contaminar,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '19', 'Porque não entra no seu coração, mas no ventre, e é lançado fora, ficando puras todas as comidas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '20', 'E dizia: O que sai do homem isso contamina o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '21', 'Porque do interior do coração dos homens saem os maus pensamentos, os adultérios, as prostituições, os homicídios,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '22', 'Os furtos, a avareza, as maldades, o engano, a dissolução, a inveja, a blasfémia, a soberba, a loucura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '23', 'Todos estes males procedem de dentro e contaminam o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '24', 'E, levantando-se dali, foi para os termos de Tiro e de Sidon. E, entrando numa casa, não queria que alguém o soubesse, mas não pôde esconder-se;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '25', 'Porque uma mulher, cuja filha tinha um espírito imundo, ouvindo falar dele, foi, e lançou-se aos seus pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '26', 'E esta mulher era grega, sirofenícia de nação, e rogava-lhe que expulsasse de sua filha o demónio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '27', 'Mas Jesus disse-lhe: Deixa primeiro saciar os filhos; porque não convém tomar o pão dos filhos e lançá-lo aos cachorrinhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '28', 'Ela, porém, respondeu, e disse-lhe: Sim, Senhor; mas também os cachorrinhos comem, debaixo da mesa, as migalhas dos filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '29', 'Então ele disse-lhe: Por essa palavra, vai; o demónio já saiu de tua filha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '30', 'E, indo ela para sua casa; achou a filha deitada sobre a cama, e que o demónio já tinha saído.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '31', 'E ele, tornando a sair dos termos de Tiro e de Sidon, foi até ao mar da Galileia, pelos confins de Decápolis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '32', 'E trouxeram-lhe um surdo, que falava dificilmente; e rogaram que pusesse a mão sobre ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '33', 'E, tirando-o à parte de entre a multidão, meteu-lhe os dedos nos ouvidos; e, cuspindo-lhe, tocou-lhe na língua.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '34', 'E, levantando os olhos ao céu, suspirou, e disse: Efata; isto é: Abre-te.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '35', 'E logo se abriram os seus ouvidos, e a prisão da língua se desfez, e falava perfeitamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '36', 'E ordenou-lhes que a ninguém o dissessem; mas, quanto mais lho proibia, tanto mais o divulgavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '7', '37', 'E, admirando-se sobremaneira, diziam: Tudo faz bem; faz ouvir os surdos e falar os mudos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '1', 'NAQUELES dias, havendo mui grande multidão, e não tendo de comer, Jesus chamou a si os seus discípulos, e disse-lhes:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '2', 'Tenho compaixão da multidão, porque há já três dias que estão comigo, e não têm que comer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '3', 'E, se os deixar ir em jejum para suas casas, desfalecerão no caminho, porque alguns deles vieram de longe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '4', 'E os seus discípulos responderam-lhe: Donde poderá alguém satisfazê-los de pão aqui no deserto?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '5', 'E perguntou-lhes: Quantos pães tendes? E disseram-lhe: Sete.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '6', 'E ordenou à multidão que se assentasse no chão. E, tomando os sete pães, e tendo dado graças, partiu-os, e deu-os aos seus discípulos, para que os pusessem diante deles, e puseram-nos diante da multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '7', 'Tinham também uns poucos de peixinhos; e tendo dado graças, ordenou que também lhos pusessem diante.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '8', 'E comeram, e saciaram-se; e dos pedaços que sobejaram levantaram sete alcofas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '9', 'E os que comeram eram quase quatro mil; e despediu-os.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '10', 'E, entrando logo no barco com os seus discípulos, foi para as partes de Dalmanuta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '11', 'E saíram os fariseus, e começaram a disputar com ele, pedindo-lhe, para o tentarem, um sinal do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '12', 'E, suspirando profundamente em seu espírito disse: Porque pede esta geração um sinal? Em verdade vos digo que a esta geração não se dará sinal algum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '13', 'E, deixando-os, tornou a entrar no barco, e foi para a outra banda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '14', 'E eles se esqueceram de levar pão, e no barco não tinham consigo senão um pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '15', 'E ordenou-lhes, dizendo: Olhai, guardai-vos do fermento dos fariseus e do fermento de Herodes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '16', 'E arrazoavam entre si, dizendo: É porque não temos pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '17', 'E Jesus, conhecendo isto, disse-lhes: Para que arrazoais, que não tendes pão? Não considerastes ainda? Tendes ainda o vosso coração endurecido?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '18', 'Tendo olhos, não vedes? E, tendo ouvidos, não ouvis? E não vos lembrais,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '19', 'Quando parti os cinco pães entre os cinco mil, quantos cestos cheios de pedaços levantastes? Disseram-lhe: Doze.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '20', 'E, quando parti os sete entre os quatro mil, quantas alcofas cheias de pedaços levantastes. E disseram-lhe: Sete');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '21', 'E ele lhes disse: Como não entendeis ainda?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '22', 'E chegou a Betsaida, e trouxeram-lhe um cego, e rogaram-lhe que lhe tocasse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '23', 'E, tomando o cego pela mão, levou-o para fora da aldeia; e, cuspindo-lhe nos olhos, e impondo-lhe as mãos, perguntou-lhe se via alguma coisa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '24', 'E, levantando ele os olhos, disse: Vejo os homens, pois os vejo como árvores que andam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '25', 'Depois tornou a pôr-lhe as mãos nos olhos, e ele, olhando firmemente ficou restabelecido, e já via ao longe e distintamente a todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '26', 'E mandou-o para sua casa, dizendo: Não entres na aldeia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '27', 'E saiu Jesus e os seus discípulos para as aldeias de Cesareia de Filipo; e no caminho perguntou aos seus discípulos, dizendo: Quem dizem os homens que eu sou?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '28', 'E eles responderam: João Baptista; e outros, Elias; mas outros: Um dos profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '29', 'E ele lhes disse: Mas vós quem dizeis que eu sou? E, respondendo Pedro, lhe disse: Tu és o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '30', 'E admoestou-os, para que a ninguém dissessem aquilo dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '31', 'E começou a ensinar-lhes que importava que o Filho do homem padecesse muito, e fosse rejeitado pelos anciãos e príncipes dos sacerdotes, e pelos escribas, e que fosse morto, mas que depois de três dias ressuscitaria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '32', 'E dizia abertamente estas palavras. E Pedro o tomou à parte, e começou a repreendê-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '33', 'Mas ele, virando-se, e olhando para os seus discípulos, repreendeu a Pedro, dizendo: Retira-te de diante de mim, Satanás; porque não compreendes as coisas que são de Deus, mas as que são dos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '34', 'E, chamando a si a multidão, com os seus discípulos, disse-lhes: Se alguém quiser vir após mim, negue-se a si mesmo, e tome a sua cruz, e siga-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '35', 'Porque qualquer que quiser salvar a sua vida perdê-la-á, mas, qualquer que perder a sua vida por amor de mim e do evangelho, esse a salvará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '36', 'Pois que aproveitaria ao homem ganhar todo o mundo e perder a sua alma?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '37', 'Ou que daria o homem pelo resgate da sua alma?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '8', '38', 'Porquanto, qualquer que, entre esta geração adúltera e pecadora, se envergonhar de mim e das minhas palavras, também o Filho do homem se envergonhará dele, quando vier na glória de seu Pai, com os santos anjos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '1', 'DIZIA-LHES também: Em verdade vos digo que, dos que aqui estão, alguns há que não provarão a morte sem que vejam chegado o reino de Deus com poder.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '2', 'E seis dias depois Jesus tomou consigo a Pedro, a Tiago, e a João, e os levou sós, em particular, a um alto monte; e transfigurou-se diante deles;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '3', 'E os seus vestidos tornaram-se resplandecentes, em extremo brancos como a neve, tais como nenhum lavadeiro sobre a terra os poderia branquear.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '4', 'E apareceu-lhes Elias com Moisés, e falavam com Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '5', 'E Pedro, tomando a palavra, disse a Jesus: Mestre, bom é que nós estejamos aqui, e façamos três cabanas, uma para ti, outra para Moisés, e outra para Elias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '6', 'Pois não sabia o que dizia, porque estavam assombrados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '7', 'E desceu uma nuvem que os cobriu com a sua sombra, e saiu da nuvem uma voz que dizia: Este é o meu filho amado; a ele ouvi.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '8', 'E, tendo olhado em roda, ninguém mais viram, se não só Jesus com eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '9', 'E, descendo eles do monte, ordenou-lhes que a ninguém contassem o que tinham visto, até que o Filho do homem ressuscitasse dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '10', 'E eles retiveram o caso entre si, perguntando uns aos outros que seria aquilo, ressuscitar dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '11', 'E interrogaram-no, dizendo: Porque dizem os escribas que é necessário que Elias venha primeiro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '12', 'E, respondendo ele, disse-lhes: Em verdade Elias virá primeiro, e todas as coisas restaurará; e, como está escrito do Filho do homem, ele deve padecer muito e ser aviltado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '13', 'Digo-vos, porém, que Elias já veio, e fizeram-lhe tudo o que quiseram, como dele está escrito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '14', 'E, quando se aproximou dos discípulos, viu ao redor deles grande multidão, e alguns escribas que disputavam com eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '15', 'E logo toda a multidão, vendo-o, ficou espantada, e, correndo para ele, o saudaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '16', 'E perguntou aos escribas: Que é que discutis com eles?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '17', 'E um da multidão, respondendo, disse: Mestre, trouxe-te o meu filho, que tem um espírito mudo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '18', 'E este, onde quer que o apanha, despedaça-o, e ele escuma, e range os dentes, e vai-se secando; e eu disse aos teus discípulos que o expulsassem, e não puderam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '19', 'E ele, respondendo-lhes, disse: ó geração incrédula! Até quando estarei convosco? Até quando vos sofrerei ainda? Trazei-mo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '20', 'E trouxeram-lho; e, quando ele o viu, logo o espírito o agitou com violência, e, caindo o endemoninhado por terra, revolvia-se, escumando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '21', 'E perguntou ao pai dele: Quanto tempo há que lhe sucede isto? E ele disse-lhe: Desde a infância.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '22', 'E muitas vezes o tem lançado no fogo, e na água, para o destruir; mas, se tu podes fazer alguma coisa, tem compaixão de nós, e ajuda-nos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '23', 'E Jesus disse-lhes: Se tu podes crer, tudo é possível ao que crê.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '24', 'E logo o pai do menino, clamando com lágrimas, disse: Eu creio, Senhor! Ajuda a minha incredulidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '25', 'E Jesus, vendo que a multidão concorria, repreendeu o espírito imundo, dizendo-lhe: Espírito mudo e surdo, eu te ordeno: Sai dele, e não entres mais nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '26', 'E ele, clamando, e agitando-o com violência, saiu; e ficou o menino como morto, de tal maneira que muitos diziam que estava morto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '27', 'Mas Jesus, tomando-o pela mão, o ergueu, e ele se levantou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '28', 'E, quando entrou em casa, os seus discípulos lhe perguntaram aparte : Porque o não pudemos nós expulsar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '29', 'E disse-lhes: Esta casta não pode sair com coisa alguma, a não ser com oração e jejum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '30', 'E, tendo partido dali, caminharam pela Galileia, e não queria que alguém o soubesse;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '31', 'Porque ensinava os seus discípulos, e lhe dizia: O Filho do homem será entregue nas mãos dos homens, e matá-lo-ão; e, morto ele, ressuscitará ao terceiro dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '32', 'Mas eles não entendiam esta palavra, e receavam interrogá-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '33', 'E chegou a Cafarnaum, e, entrando em casa, perguntou-lhes: Que estáveis vós discutindo pelo caminho?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '34', 'Mas eles calaram-se; porque pelo caminho tinham disputado entre si qual era o maior.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '35', 'E ele, assentando-se, chamou os doze, e disse-lhes: Se alguém quiser ser o primeiro, será o derradeiro de todos e o servo de todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '36', 'E, lançando mão de um menino, pô-lo no meio deles, e, tomando-o nos seus braços, disse-lhes:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '37', 'Qualquer que receber um destes meninos em meu nome a mim me recebe; e qualquer que a mim me receber, recebe, não é a mim, mas ao que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '38', 'E João lhe respondeu, dizendo: Mestre, vimos um que em teu nome expulsava demónios, o qual não nos segue; e nós lho proibimos, porque não nos segue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '39', 'Jesus, porém, disse: Não lho proibais; porque ninguém há que faça milagre em meu nome e possa logo falar mal de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '40', 'Porque quem não é contra nós é por nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '41', 'Porquanto qualquer que vos der a beber um copo de água em meu nome, porque sois discípulos de Cristo, em verdade vos digo que não perderá o seu galardão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '42', 'E qualquer que escandalizar um destes pequeninos que crêem em mim melhor lhe fora que lhe pusessem ao pescoço uma mó de atafona, e que fosse lançado no mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '43', 'E, se a tua mão te escandalizar, corta-a; melhor é para ti entrares na vida aleijado do que, tendo duas mãos, ires para o inferno, para o fogo que nunca se apaga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '44', 'Onde o seu bicho não morre, e o fogo nunca se apaga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '45', 'E, se o teu pé te escandalizar, corta-o; melhor é para ti entrares coxo na vida do que, tendo dois pés, seres lançado no inferno, no fogo que nunca se apaga;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '46', 'Onde o seu bicho não morre, e o fogo nunca se apaga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '47', 'E, se o teu olho te escandalizar, lança-o fora; melhor é para ti entrares no reino de Deus com um só olho do que, tendo dois olhos, ser lançado no fogo do inferno;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '48', 'Onde o seu bicho não morre, e o fogo nunca se apaga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '49', 'Porque cada um será salgado com fogo, e cada sacrifício será salgado com sal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '9', '50', 'Bom é o sal; mas, se o sal se tornar insulso, com que o adubareis? Tende sal em vós mesmos, e paz uns com os outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '1', 'E, LEVANTANDO-SE dali, foi para os termos da Judeia, além do Jordão, e a multidão se reuniu em torno dele; e tornou a ensiná-los, como tinha por costume.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '2', 'E, aproximando-se dele os fariseus, perguntaram-lhe, tentando-o: É lícito ao homem repudiar sua mulher?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '3', 'Mas ele, respondendo, disse-lhes: Que vos mandou Moisés?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '4', 'E eles disseram: Moisés permitiu escrever carta de divórcio, e repudiar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '5', 'E Jesus, respondendo, disse-lhes: Pela dureza dos vossos corações vos deixou ele escrito esse mandamento;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '6', 'Porém, desde o princípio da criação, Deus os fez macho e fêmea.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '7', 'Por isso deixará o homem a seu pai e a sua mãe, e unir-se-á à sua mulher,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '8', 'E serão os dois uma só carne: e assim já não serão dois, mas uma só carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '9', 'Portanto o que Deus ajuntou não o separe o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '10', 'E em casa tornaram os discípulos a interrogá-lo acerca disto mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '11', 'E ele lhes disse: Qualquer que deixar a sua mulher e casar com outra adultera contra ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '12', 'E, se a mulher deixar a seu marido, e casar com outro, adultera.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '13', 'E traziam-lhe meninos para que lhes tocasse, mas os discípulos repreendiam aos que lhos traziam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '14', 'Jesus, porém, vendo isto, indignou-se, e disse-lhes: Deixai vir os meninos a mim, e não os empeçais; porque dos tais é o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '15', 'Em verdade vos digo que qualquer que não receber o reino de Deus como menino, de maneira nenhuma entrará nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '16', 'E, tomando-os nos seus braços, e impondo-lhes as mãos, os abençoou;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '17', 'E, pondo-se a caminho, correu para ele um homem, o qual se ajoelhou diante dele, e lhe perguntou: Bom Mestre, que farei para herdar a vida eterna?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '18', 'E Jesus lhe disse: Porque me chamas bom? Ninguém há bom senão um, que é Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '19', 'Tu sabes os mandamentos: Não adulterarás; não matarás; não furtarás; não dirás falsos testemunhos; não defraudarás alguém; honra a teu pai e a tua mãe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '20', 'Ele, porém, respondendo, lhe disse: Mestre, tudo isso guardei desde a minha mocidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '21', 'E Jesus, olhando para ele, o amou e lhe disse: Falta-te uma coisa: Vai, vende tudo quanto tens, e dá-o aos pobres, e terás um tesouro no céu; e vem, e segue-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '22', 'Mas ele, pesaroso desta palavra, retirou-se triste; porque possuía muitas propriedades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '23', 'Então Jesus, olhando em redor, disse aos seus discípulos: Quão dificilmente entrarão no reino de Deus os que têm riquezas!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '24', 'E os discípulos se admiraram destas suas palavras; mas Jesus, tornando a falar, disse-lhes: Filhos, quão difícil é, para os que confiam nas riquezas, entrar no reino de Deus!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '25', 'É mais fácil passar um camelo pelo fundo de uma agulha do que entrar um rico no reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '26', 'E eles se admiravam ainda mais, dizendo entre si: Quem poderá pois salvar-se ?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '27', 'Jesus, porém, olhando para eles, disse: Para os homens é impossível, mas não para Deus, porque para Deus todas as coisas são possíveis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '28', 'E Pedro começou a dizer-lhe: Eis que nós tudo deixámos, e te seguimos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '29', 'E Jesus, respondendo, disse: Em verdade vos digo que ninguém há, que tenha deixado casa, ou irmãos, ou irmãs, ou pai, ou mãe, ou mulher, ou filhos, ou campos, por amor de mim e do Evangelho,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '30', 'Que não receba cem vezes tanto, já neste tempo, em casas, e irmãos, e irmãs, e mães, e filhos, e campos, com perseguições; e no século futuro a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '31', 'Porém muitos primeiros serão derradeiros, e muitos derradeiros serão primeiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '32', 'E iam no caminho, subindo para Jerusalém; e Jesus ia adiante deles. E eles maravilhavam-se, e seguiam-no atemorizados. E, tornando a tomar consigo os doze, começou a dizer-lhes as coisas que lhe deviam sobrevir,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '33', 'Dizendo: Eis que nós subimos a Jerusalém, e o Filho do homem será entregue aos príncipes dos sacerdotes, e aos escribas, e o condenarão à morte, e o entregarão aos gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '34', 'E o escarnecerão, e açoitarão, e cuspirão nele, e o matarão; e ao terceiro dia ressuscitará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '35', 'E aproximaram-se dele Tiago e João, filhos de Zebedeu, dizendo: Mestre, queríamos que nos fizesses o que pedirmos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '36', 'Ele lhes disse: Que quereis que vos faça?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '37', 'E eles lhe disseram: Concede-nos que na tua glória nos assentemos, um à tua direita, e outro à tua esquerda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '38', 'Mas Jesus lhes disse: Não sabeis o que pedis; podeis vós beber o cálix que eu bebo, e ser baptizados com o baptismo com que eu sou baptizado?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '39', 'E eles lhe disseram: Podemos. Jesus, porém, disse-lhes: Em verdade, vós bebereis o cálix que eu beber, e sereis baptizados com o baptismo com que eu sou baptizado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '40', 'Mas o assentar-se à minha direita, ou à minha esquerda, não me pertence a mim concedê-lo, mas isso é para aqueles a quem está reservado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '41', 'E os dez, tendo ouvido isto, começaram a indignar-se contra Tiago e João.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '42', 'Mas Jesus, chamando-os a si, disse-lhes: Sabeis que os que julgam ser príncipes das gentes, delas se assenhoreiam, e os seus grandes usam de autoridade sobre elas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '43', 'Mas entre vós não será assim; antes, qualquer que entre vós quiser ser grande, será vosso serviçal;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '44', 'E qualquer que de entre vós quiser ser o primeiro será servo de todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '45', 'Porque o Filho do homem também não veio para ser servido, mas para servir e dar a sua vida em resgate de muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '46', 'Depois foram para Jericó. E, saindo ele de Jericó com seus discípulos, e uma grande multidão, Bartimeu, o cego, filho de Timeu, estava assentado junto do caminho, mendigando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '47', 'E, ouvindo que era Jesus de Nazaré, começou a clamar, e a dizer: Jesus, filho de David, tem misericórdia de mim!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '48', 'E muitos o repreendiam, para que se calasse; mas ele clamava cada vez mais: Filho de David! Tem misericórdia de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '49', 'E Jesus, parando, disse que o chamassem; e chamaram o cego, dizendo-lhe: Tem bom ânimo; levanta-te, que ele te chama.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '50', 'E ele, lançando de si a sua capa, levantou-se, e foi ter com Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '51', 'E Jesus, falando disse-lhe: Que queres que te faça? E o cego lhe disse: Mestre, que eu tenha vista.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '10', '52', 'E Jesus lhe disse: Vai, a tua fé te salvou. E logo viu, e seguiu a Jesus pelo caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '1', 'E LOGO que se aproximaram de Jerusalém, de Betfagé e de Betânia, junto do monte das Oliveiras, enviou dois dos seus discípulos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '2', 'E disse-lhes: Ide à aldeia que está defronte de vós; e, logo que ali entrardes, encontrareis preso um jumentinho, sobre o qual ainda não montou homem algum; soltai-o, e trazei-mo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '3', 'E, se alguém vos disser: Porque fazeis isso? Dizei-lhe que o SENHOR precisa dele, e logo o deixará trazer para aqui.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '4', 'E foram, e encontraram o jumentinho preso fora da porta, entre dois caminhos, e o soltaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '5', 'E alguns dos que ali estavam lhes disseram: Que fazeis, soltando o jumentinho?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '6', 'Eles, porém, disseram-lhes como Jesus lhes tinha mandado; e deixaram-nos ir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '7', 'E levaram o jumentinho a Jesus, e lançaram sobre ele os seus vestidos, e assentou-se sobre ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '8', 'E muitos estendiam os seus vestidos pelo caminho, e outros cortavam ramos das árvores, e os espalhavam pelo caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '9', 'E aqueles que iam adiante, e os que seguiam, clamavam, dizendo: Hosana, bendito o que vem em nome do SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '10', 'Bendito o reino do nosso pai David, que vem em nome do SENHOR - Hosana nas alturas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '11', 'E Jesus entrou em Jerusalém, no templo, e, tendo visto tudo em redor, como fosse já tarde, saiu para Betânia com os doze.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '12', 'E, no dia seguinte, quando saíram de Betânia, teve fome,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '13', 'E, vendo de longe uma figueira que tinha folhas, foi ver se nela acharia alguma coisa; e, chegando a ela, não achou senão folhas, porque não era tempo de figos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '14', 'E Jesus, falando, disse à figueira: Nunca mais coma alguém fruto de ti. E os seus discípulos ouviram isto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '15', 'E vieram a Jerusalém; e Jesus, entrando no templo, começou a expulsar os que vendiam e compravam no templo; e derribou as mesas dos cambiadores e as cadeiras dos que vendiam pombas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '16', 'E não consentia que alguém levasse algum vaso pelo templo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '17', 'E os ensinava, dizendo: Não está escrito - A minha casa será chamada por todas as nações casa de oração? Mas vós a tendes feito covil de ladrões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '18', 'E os escribas e príncipes dos sacerdotes, tendo ouvido isto; buscavam ocasião para o matar; pois eles o temiam, porque toda a multidão estava admirada acerca da sua doutrina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '19', 'E, sendo já tarde, saiu para fora da cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '20', 'E eles, passando pela manhã, viram que a figueira se tinha secado desde as raízes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '21', 'E Pedro, lembrando-se, disse-lhe: Mestre, eis que a figueira, que tu amaldiçoaste, se secou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '22', 'E Jesus, respondendo, disse-lhes: Tende fé em Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '23', 'Porque em verdade vos digo que qualquer que disser a este monte: Ergue-te e lança-te no mar; e não duvidar em seu coração, mas crer que se fará aquilo que diz, tudo o que disser lhe será feito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '24', 'Por isso vos digo que tudo o que pedirdes, orando, crede que o recebereis, e tê-lo-eis;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '25', 'E, quando estiverdes orando, perdoai, se tendes alguma coisa contra alguém, para que vosso Pai, que está nos céus, vos perdoe as vossas ofensas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '26', 'Mas, se vós não perdoardes, também vosso Pai, que está nos céus, vos não perdoará as vossas ofensa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '27', 'E tornaram a Jerusalém, e, andando ele pelo templo, os principais dos sacerdotes, e os escribas, e os anciãos se aproximaram dele,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '28', 'E lhe disseram: Com que autoridade fazes tu estas coisas? Ou, quem te deu tal autoridade para fazeres estas coisas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '29', 'Mas Jesus, respondendo, disse-lhes: Também eu vos perguntarei uma coisa, e respondei-me; e então vos direi com que autoridade faço estas coisas:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '30', 'O baptismo de João era do céu ou dos homens? Respondei-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '31', 'E eles arrazoavam entre si, dizendo: Se dissermos: Do céu, ele nos dirá: Então porque o não crestes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '32', 'Se, porém, dissermos: Dos homens tememos o povo: porque todos sustentavam que João verdadeiramente era profeta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '11', '33', 'E, respondendo, disseram a Jesus: Não sabemos. E Jesus lhes replicou: Também eu vos não direi com que autoridade faço estas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '1', 'E começou a falar-lhes por parábolas: Um homem plantou uma vinha, e cercou-a de um valado, e fundou nela um lagar, e edificou uma torre, e arrendou-a a uns lavradores, e partiu para fora da terra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '2', 'E, chegado o tempo, mandou um servo aos lavradores para que recebesse dos lavradores, do fruto da vinha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '3', 'Mas estes, apoderando-se dele, o feriram e o mandaram embora vazio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '4', 'E tornou a enviar-lhes outro servo; e eles, apedrejando-o, o feriram na cabeça, e o mandaram embora, tendo-o afrontado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '5', 'E tornou a enviar-lhes outro, e a este mataram; e a outros muitos, dos quais a uns feriram e a outros mataram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '6', 'Tendo ele pois ainda um seu filho amado, enviou-lho também por derradeiro, dizendo: Ao menos terão respeito ao meu filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '7', 'Mas aqueles lavradores disseram entre si: Este é o herdeiro; vamos, matemo-lo, e a herança será nossa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '8', 'E, pegando dele, o mataram, e o lançaram fora da vinha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '9', 'Que fará pois o SENHOR da vinha? Virá, e destruirá os lavradores, e dará a vinha a outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '10', 'Ainda não lestes esta Escritura: A pedra, que os edificadores rejeitaram, esta foi posta por cabeça da esquina;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '11', 'Isto foi feito pelo SENHOR e é coisa maravilhosa aos nossos olhos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '12', 'E buscavam prendê-lo, mas temiam a multidão; porque entendiam que contra eles dizia esta parábola; e, deixando-o, foram-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '13', 'E enviaram-lhe alguns dos fariseus e dos herodianos, para que o apanhassem nalguma palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '14', 'E, chegando eles, disseram-lhe: Mestre, sabemos que és homem de verdade, e de ninguém se te dá, porque não olhas à aparência dos homens, antes com verdade ensinas o caminho de Deus; é lícito dar o tributo a César, ou não? Daremos, ou não daremos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '15', 'Então ele, conhecendo a sua hipocrisia, disse-lhes: Porque me tentais? Trazei-me uma moeda, para que a veja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '16', 'E eles lha trouxeram. E disse-lhes: De quem é esta imagem e inscrição? E eles lhe disseram: De César.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '17', 'E Jesus, respondendo, disse-lhes: Dai pois a César o que é de César, e a Deus o que é de Deus. E maravilharam-se dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '18', 'Então os saduceus, que dizem que não há ressurreição, aproximaram-se dele, e perguntaram-lhe, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '19', 'Mestre, Moisés nos escreveu que, se morresse o irmão de alguém, e deixasse a mulher e não deixasse filhos, seu irmão tomasse a mulher dele, e suscitasse descendência a seu irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '20', 'Ora havia sete irmãos, e o primeiro tomou mulher, e morreu sem deixar descendência;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '21', 'E o segundo também a tomou e morreu, e nem este deixou descendência; e o terceiro da mesma maneira;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '22', 'E tomaram-na os sete, sem, contudo, terem deixado descendência. Finalmente, depois de todos, morreu também a mulher.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '23', 'Na ressurreição, pois, quando ressuscitarem, de qual destes será a mulher? Porque os sete a tiveram por mulher.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '24', 'E Jesus, respondendo, disse-lhes: Porventura não errais vós em razão de não saberdes as Escrituras nem o poder de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '25', 'Porquanto, quando ressuscitarem dos mortos, nem casarão, nem se darão em casamento, mas serão como os anjos que estão nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '26', 'E, acerca dos mortos que houverem de ressuscitar, não tendes lido no livro de Moisés como Deus lhe falou na sarça, dizendo: Eu sou o Deus de Abraão, e o Deus de Isaque, e o Deus de Jacob?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '27', 'Ora Deus não é de mortos, mas sim é Deus dos vivos. Por isso vós errais muito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '28', 'Aproximou-se dele um dos escribas que os tinha ouvido disputar, e sabendo que lhes tinha respondido bem, perguntou-lhe: Qual é o primeiro de todos os mandamentos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '29', 'E Jesus respondeu-lhe: O primeiro de todos os mandamentos é: Ouve, Israel, o SENHOR nosso Deus é o único SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '30', 'Amarás, pois, ao SENHOR teu Deus de todo o coração, e de toda a tua alma, e de todo o teu entendimento, e de todas as tuas forças: este é o primeiro mandamento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '31', 'E o segundo, semelhante a este, é: Amarás o teu próximo como a ti mesmo. Não há outro mandamento maior do que estes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '32', 'E o escriba lhe disse: Muito bem, Mestre, e com verdade disseste que há um só Deus, e que não há outro além dele;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '33', 'E que amá-lo de todo o coração, e de todo o entendimento, e de toda a alma, e de todas as forças, e amar o próximo como a si mesmo, é mais do que todos os holocaustos e sacrifícios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '34', 'E, Jesus, vendo que havia respondido sabiamente, disse-lhe: Não estás longe do reino de Deus. E já ninguém ousava perguntar-lhe mais nada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '35', 'E, falando Jesus, dizia, ensinando no templo: Como dizem os escribas que o Cristo é filho de David?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '36', 'O próprio David disse pelo Espírito Santo: O SENHOR disse ao meu Senhor: Assenta-te à minha direita até que eu ponha os teus inimigos por escabelo dos teus pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '37', 'Pois, se David mesmo lhe chama Senhor, como é logo seu filho? E a grande multidão o ouvia de boa vontade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '38', 'E, ensinando-os, dizia-lhes: Guardai-vos dos escribas, que gostam de andar com vestidos compridos, e das saudações nas praças,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '39', 'E das primeiras cadeiras nas sinagogas, e dos primeiros assentos nas ceias;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '40', 'Que devoram as casas das viúvas, e isso com pretexto de largas orações. Estes receberão mais grave condenação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '41', 'E, estando Jesus assentado defronte da arca do tesouro, observava a maneira como a multidão lançava o dinheiro na arca do tesouro; e muitos ricos deitavam muito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '42', 'Vindo, porém, uma pobre viúva, deitou duas pequenas moedas, que valiam meio centavo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '43', 'E, chamando os seus discípulos, disse-lhes: Em verdade vos digo que esta pobre viúva deitou mais do que todos os que deitaram na arca do tesouro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '12', '44', 'Porque todos ali deitaram do que lhes sobejava, mas esta, da sua pobreza, deitou tudo o que tinha, todo o seu sustento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '1', 'E, SAINDO ele do templo, disse-lhe um dos seus discípulos: Mestre, olha que pedras, e que edifícios!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '2', 'E, respondendo Jesus, disse-lhe: Vês estes grandes edifícios? Não ficará pedra sobre pedra que não seja derribada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '3', 'E, assentando-se ele no monte das Oliveiras, defronte do templo, Pedro, e Tiago, e João e André lhe perguntaram em particular:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '4', 'Diz-nos, quando serão essas coisas, e que sinal haverá quando todas elas estiverem para se cumprir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '5', 'E Jesus, respondendo-lhes, começou a dizer: Olhai que ninguém vos engane;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '6', 'Porque muitos virão em meu nome, dizendo: Eu sou o Cristo; e enganarão a muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '7', 'E, quando ouvirdes de guerras e de rumores de guerras, não vos perturbeis; porque assim deve acontecer; mas ainda não será o fim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '8', 'Porque se levantará nação contra nação, e reino contra reino, e haverá terramotos em diversos lugares, e haverá fomes. Isto será o princípio de dores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '9', 'Mas olhai por vós mesmos, porque vos entregarão aos concílios e às sinagogas: sereis açoitados, e sereis apresentados ante presidentes e reis, por amor de mim, para lhes servir de testemunho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '10', 'Mas importa que o evangelho seja primeiramente pregado entre todas as gentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '11', 'Quando pois vos conduzirem para vos entregarem, não estejais solícitos de antemão pelo que haveis de dizer mas, o que vos for dado naquela hora, isso falai por que não sois vós os que falais, mas o Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '12', 'E o irmão entregará à morte o irmão, e o pai o filho; e levantar-se-ão os filhos contra os pais, e os farão morrer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '13', 'E sereis aborrecidos por todos por amor do meu nome; mas quem perseverar até ao fim esse será salvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '14', 'Ora, quando vós virdes a abominação do assolamento; que foi predito, estar onde não deve estar (quem lê, entenda), então os que estiverem na Judeia fujam para os montes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '15', 'E o que estiver sobre o telhado não desça para casa, nem entre a tomar coisa alguma de sua casa;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '16', 'E o que estiver no campo não volte atrás, para tomar o seu vestido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '17', 'Mas ai das grávidas, e das que criarem naqueles dias!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '18', 'Orai pois, para que a vossa fugida não suceda no inverno;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '19', 'Porque naqueles dias haverá uma aflição tal, qual nunca houve desde o princípio da criação, que Deus criou, até agora, nem jamais haverá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '20', 'Se o SENHOR não abreviasse aqueles dias, nenhuma carne se salvaria; mas, por causa dos escolhidos que escolheu, abreviou aqueles dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '21', 'E então, se alguém vos disser: Eis aqui o Cristo; ou: Ei-lo ali; não acrediteis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '22', 'Porque se levantarão falsos cristos, e falsos profetas, e farão sinais e prodígios, para enganarem, se for possível, até os escolhidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '23', 'Mas vós vede; eis que de antemão vos tenho dito tudo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '24', 'Ora, naqueles dias, depois daquela aflição, o sol se escurecerá, e a lua não dará a sua luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '25', 'E as estrelas cairão do céu, e as forças que estão nos céus serão abaladas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '26', 'E então verão vir o Filho do homem nas nuvens, com grande poder e glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '27', 'E ele enviará os seus anjos, e ajuntará os seus escolhidos, desde os quatro ventos, da extremidade da terra até à extremidade do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '28', 'Aprendei pois a parábola da figueira: Quando já o seu ramo se torna tenro, e brota folhas, bem sabeis que já está próximo o verão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '29', 'Assim também vós, quando virdes sucederem estas coisas, sabeis que já está perto, às portas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '30', 'Na verdade vos digo que não passará esta geração, sem que todas estas coisas aconteçam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '31', 'Passará o céu e a terra, mas as minhas palavras não passarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '32', 'Mas daquele dia e hora ninguém sabe, nem os anjos que estão no céu, nem o Filho, senão o Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '33', 'Olhai, vigiai e orai; porque não sabeis quando chegará o tempo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '34', 'É como se um homem, partindo para fora da terra, deixasse a sua casa, e desse autoridade aos seus servos, e a cada um a sua obra, e mandasse ao porteiro que vigiasse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '35', 'Vigiai pois, porque não sabeis quando virá o SENHOR da casa; se à tarde, se à meia noite, se ao cantar do galo, se pela manhã,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '36', 'Para que, vindo de improviso, não vos ache dormindo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '13', '37', 'E as coisas que vos digo digo-as a todos: Vigiai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '1', 'E DALI a dois dias era a páscoa, e a festa dos pães asmos; e os principais dos sacerdotes e os escribas buscavam como o prenderiam com dolo, e o matariam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '2', 'Mas eles diziam: Não na festa, para que porventura se não faça alvoroço entre o povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '3', 'E, estando ele em Betânia, assentado à mesa, em casa de Simão, o leproso, veio uma mulher, que trazia um vaso de alabastro, com unguento de nardo puro, de muito preço e, quebrando o vaso, lho derramou sobre a cabeça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '4', 'E alguns houve que em si mesmos se indignaram, e disseram: Para que se fez este desperdício de unguento?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '5', 'Porque podia vender-se por mais de trezentos dinheiros, e dá-lo aos pobres. E bramavam contra ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '6', 'Jesus, porém, disse: Deixai-a, para que a molestais? Ela fez-me boa obra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '7', 'Porque sempre tendes os pobres convosco, e podeis fazer-lhes bem, quando quiserdes; mas a mim nem sempre me tendes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '8', 'Esta fez o que podia; antecipou-se a ungir o meu corpo para a sepultura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '9', 'Em verdade vos digo, que em todas as partes do mundo onde este evangelho for pregado, também o que ela fez será contado para sua memória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '10', 'E Judas Iscariotes, um dos doze, foi ter com os principais dos sacerdotes para lho entregar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '11', 'E eles, ouvindo-o, folgaram, e prometeram dar-lhe dinheiro; e buscava como o entregaria em ocasião oportuna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '12', 'E, no primeiro dia dos pães asmos, quando sacrificavam a páscoa, disseram-lhe os discípulos: Aonde queres que vamos fazer os preparativos para comer a páscoa?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '13', 'Enviou dois dos seus discípulos, e disse-lhes: Ide à cidade, e um homem, que leva um cântaro de água, vos encontrará; segui-o;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '14', 'E, onde quer que entrar, dizei ao SENHOR da casa: O Mestre diz: Onde está o aposento em que hei-de comer a páscoa com os meus discípulos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '15', 'E ele vos mostrará um grande cenáculo mobilado e preparado; preparai-a ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '16', 'E, saindo os seus discípulos, foram à cidade, e acharam como lhes tinha dito, e prepararam a páscoa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '17', 'E, chegada a tarde, foi com os doze,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '18', 'E, quando estavam assentados a comer, disse Jesus: Em verdade vos digo que um de vós, que comigo come há-de trair-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '19', 'E eles começaram a entristecer-se e a dizer um após outro: Porventura sou eu, Senhor? e outro: Porventura sou eu, Senhor?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '20', 'Mas ele, respondendo, disse-lhes: É um dos doze, que mete comigo a mão no prato.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '21', 'Na verdade o Filho do homem vai, como dele esta escrito, mas ai daquele homem por quem o Filho do homem é traído! Bom seria para o tal homem não haver nascido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '22', 'E, comendo eles, tomou Jesus pão, e, abençoando-o o partiu e deu-lho, e disse: Tomai, comei, isto é o meu corpo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '23', 'E, tomando o cálix, e dando graças, deu-lho; e todos beberam dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '24', 'E disse-lhes: Isto é o meu sangue, o sangue do Novo Testamento, que por muitos é derramado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '25', 'Em verdade vos digo que não beberei mais do fruto da vide, até àquele dia em que o beber novo no reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '26', 'E, tendo cantado o hino, saíram para o monte das Oliveiras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '27', 'E disse-lhes Jesus: Todos vós esta noite vos escandalizareis em mim; porque escrito está: Ferirei o pastor, e as ovelhas se dispersarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '28', 'Mas, depois que eu houver ressuscitado, irei adiante de vós para a Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '29', 'E disse-lhe Pedro: Ainda que todos se escandalizem, nunca, porém, eu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '30', 'E disse-lhe Jesus: Em verdade te digo que hoje, nesta noite, antes que o galo cante duas vezes, três vezes me negarás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '31', 'Mas ele disse com mais veemência: Ainda que me seja necessário morrer contigo, de modo nenhum te negarei. E da mesma maneira diziam todos também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '32', 'E foram a um lugar chamado Getsémani, e disse aos discípulos: Assentai-vos aqui, enquanto eu oro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '33', 'E tomou consigo a Pedro, e a Tiago, e a João, e começou a ter pavor, e a angustiar-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '34', 'E disse-lhes: A minha alma está profundamente triste até à morte: ficai aqui, e vigiai:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '35', 'E, tendo ido um pouco mais adiante, prostrou-se em terra; e orou para que, se fosse possível, passasse dele aquela hora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '36', 'E disse: Aba, Pai, todas as coisas te são possíveis; afasta de mim este cálix; não seja, porém, o que eu quero, mas o que tu queres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '37', 'E, chegando, achou-os dormindo; e disse a Pedro: Simão, dormes? Não podes vigiar uma hora?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '38', 'Vigiai e orai, para que não entreis em tentação; o espírito, na verdade, está pronto, mas a carne é fraca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '39', 'E foi outra vez e orou, dizendo as mesmas palavras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '40', 'E, voltando, achou-os outra vez dormindo, porque os seus olhos estavam carregados, e não sabiam que responder-lhe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '41', 'E voltou terceira vez, e disse-lhes: Dormi agora, e descansai. Basta; é chegada a hora. Eis que o Filho do homem vai ser entregue nas mãos dos pecadores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '42', 'Levantai-vos, vamos; eis que está perto o que me trai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '43', 'E logo, falando ele ainda, veio Judas, que era um dos doze, da parte dos principais dos sacerdotes, e dos escribas e dos anciãos, e com ele uma grande multidão com espadas e varapaus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '44', 'Ora, o que o traía, tinha-lhes dado um sinal, dizendo: Aquele que eu beijar, esse é; prendei-o, e levai-o com segurança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '45', 'E logo que chegou, aproximou-se dele, e disse-lhe: Rabi, Rabi. E beijou-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '46', 'E lançaram-lhe as mãos, e o prenderam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '47', 'E um dos que ali estavam presentes, puxando da espada, feriu o servo do sumo sacerdote, e cortou-lhe uma orelha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '48', 'E, respondendo Jesus, disse-lhes: Saístes com espadas e varapaus a prender-me, como a um salteador?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '49', 'Todos os dias estava convosco ensinando no templo, e não me prendestes; mas isto é para que as Escrituras se cumpram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '50', 'Então, deixando-o, todos fugiram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '51', 'E um certo mancebo o seguia, envolto em um lençol sobre o corpo nu. E lançaram-lhe a mão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '52', 'Mas ele, largando o lençol, fugiu nu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '53', 'E levaram Jesus ao sumo sacerdote, e ajuntaram-se todos os principais dos sacerdotes, e os anciãos e os escribas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '54', 'E Pedro o seguiu de longe até dentro do pátio do sumo sacerdote, e estava assentado com os servidores, aquentando-se ao lume.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '55', 'E os principais dos sacerdotes e todo o concílio buscavam algum testemunho contra Jesus, para o matar, e não o achavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '56', 'Porque muitos testificavam falsamente contra ele, mas os testemunhos não eram conformes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '57', 'E, levantando-se alguns, testificavam falsamente contra ele, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '58', 'Nós ouvimos-lhe dizer. Eu derribarei este templo, construído por mãos de homens, e em três dias edificarei outro, não feito por mãos de homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '59', 'E nem assim o seu testemunho era conforme.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '60', 'E, levantando-se o sumo sacerdote no sinédrio, perguntou a Jesus, dizendo: Nada respondes? Que testificam estes contra ti?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '61', 'Mas. ele calou-se, e nada respondeu. O sumo sacerdote lhe tornou a perguntar, e disse-lhe: És tu o Cristo, Filho do Deus Bendito?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '62', 'E Jesus disse-lhes: Eu o sou, e vereis o Filho do homem assentado à direita do poder de Deus, e vindo sobre as nuvens do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '63', 'E o sumo sacerdote, rasgando os seus vestidos, disse: Para que necessitamos de mais testemunhas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '64', 'Vós ouvistes a blasfémia; que vos parece? E todos o consideraram culpado de morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '65', 'E alguns começaram a cuspir nele, e a cobrir-lhe o rosto, e a dar-lhe unhadas, e a dizer-lhe: Profetiza. E os servidores davam-lhe bofetadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '66', 'E, estando Pedro em baixo, no átrio, chegou uma das criadas do sumo sacerdote;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '67', 'E, vendo a Pedro, que se estava aquentando, olhou para ele, e disse: Tu também estavas com Jesus Nazareno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '68', 'Mas ele negou-o, dizendo: Não o conheço, nem sei o que dizes. E saiu fora ao alpendre, e o galo cantou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '69', 'E a criada, vendo-o outra vez, começou a dizer aos que ali estavam: Este é um dos tais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '70', 'Mas ele o negou outra vez. E pouco depois os que ali estavam disseram outra vez a Pedro: Verdadeiramente tu és um deles, porque és também galileu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '71', 'E ele começou a imprecar, e a jurar: Não conheço esse homem de quem falais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '14', '72', 'E o galo cantou segunda vez. E Pedro lembrou-se da palavra que Jesus lhe tinha dito: Antes que o galo cante duas vezes, três vezes me negarás tu. E, retirando-se dali, chorou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '1', 'E, LOGO ao amanhecer, os principais dos sacerdotes, com os anciãos, e os escribas, e todo o sinédrio, tiveram conselho; e, ligando Jesus, o levaram e entregaram a Pilatos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '2', 'E Pilatos lhe perguntou: Tu és o Rei dos Judeus? E ele, respondendo, disse-lhe: Tu o dizes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '3', 'E os principais dos sacerdotes o acusavam de muitas coisas; porém ele nada respondia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '4', 'E Pilatos o interrogou outra vez, dizendo: Nada respondes? Vê quantas coisas testificam contra ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '5', 'Mas Jesus nada mais respondeu, de maneira que Pilatos se maravilhava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '6', 'Ora no dia da festa costumava soltar-lhes um preso qualquer que eles pedissem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '7', 'E havia um chamado Barrabás, que, preso com outros amotinadores, tinha num motim cometido uma morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '8', 'E a multidão, dando gritos, começou a pedir que fizesse como sempre lhes tinha feito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '9', 'E Pilatos lhes respondeu, dizendo: Quereis que vos solte o Rei dos Judeus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '10', 'Porque ele bem sabia que por inveja os principais dos sacerdotes o tinham entregado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '11', 'Mas os principais dos sacerdotes incitaram a multidão para que fosse solto antes Barrabás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '12', 'E Pilatos, respondendo-lhes disse outra vez: Que quereis pois que faça daquele a quem chamais Rei dos Judeus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '13', 'E eles tornaram a clamar: Crucifica-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '14', 'Mas Pilatos lhes disse: Mas que mal fez? E eles cada vez clamavam mais: Crucifica-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '15', 'Então Pilatos, querendo satisfazer a multidão, soltou-lhes Barrabás, e, açoitado Jesus, o entregou para que fosse crucificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '16', 'E os soldados o levaram dentro à sala, que é a da audiência, e convocaram toda a coorte;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '17', 'E vestiram-no de púrpura, e, tecendo uma coroa de espinhos, lha puseram na cabeça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '18', 'E começaram a saudá-lo, dizendo: Salve, Rei dos Judeus!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '19', 'E feriram-no na cabeça com uma cana, e cuspiram nele, e, postos de joelhos, o adoraram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '20', 'E, havendo-o escarnecido, despiram-lhe a púrpura, e o vestiram com os seus próprios vestidos; e o levaram para fora a fim de o crucificarem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '21', 'E constrangeram um certo Simão, cireneu, pai de Alexandre e de Rufo, que por ali passava, vindo do campo, a que levasse a cruz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '22', 'E levaram-no ao lugar do Gólgota, que se traduz por lugar da Caveira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '23', 'E deram-lhe a beber vinho com mirra, mas ele não o tomou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '24', 'E, havendo-o crucificado, repartiram os seus vestidos, lançando sobre eles sortes, para saber o que cada um levaria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '25', 'E era a hora terceira, e o crucificaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '26', 'E por cima dele estava escrita a sua acusação: O REI DOS JUDEUS.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '27', 'E crucificaram com ele dois salteadores, um à sua direita, e outro à esquerda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '28', 'E cumpriu-se a escritura que diz: E com os malfeitores foi contado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '29', 'E os que passavam blasfemavam dele, meneando as suas cabeças, e dizendo: Ah! tu que derribas o templo, e em três dias o edificas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '30', 'Salva-te a ti mesmo, e desce da cruz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '31', 'E da mesma maneira também os principais dos sacerdotes, com os escribas, diziam uns para os outros, zombando: Salvou os outros, e não pode salvar-se a si mesmo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '32', 'O Cristo, o Rei de Israel, desça agora da cruz, para que o vejamos e acreditemos. Também os que com ele foram crucificados o injuriavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '33', 'E, chegada a hora sexta, houve trevas sobre a terra até à hora nona.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '34', 'E, à hora nona, Jesus exclamou com grande voz, dizendo: Eloí, Eloí, lamá sabactani? que, traduzido é: Deus meu, Deus meu, porque me desamparaste?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '35', 'E alguns dos que ali estavam, ouvindo isto, diziam: Eis que chama por Elias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '36', 'E um deles correu a embeber uma esponja em vinagre, e, pondo-a numa cana, deu-lho a beber, dizendo: Deixai, vejamos se virá Elias tirá-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '37', 'E Jesus, dando um grande brado, expirou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '38', 'E o véu do templo se rasgou em dois, de alto a baixo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '39', 'E o centurião, que estava defronte dele, vendo que assim clamando expirara, disse: Verdadeiramente este homem era o Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '40', 'E também ali estavam algumas mulheres, olhando de longe, entre as quais também Maria Madalena, e Maria, mãe de Tiago, o menor, e de José, e Salomé;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '41', 'As quais também o seguiam, e o serviam, quando estava na Galileia; e muitas outras, que tinham subido com ele a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '42', 'E, chegada a tarde, porquanto era o dia da preparação, isto é, a véspera do sábado,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '43', 'Chegou José de Arimatéia, senador honrado, que também esperava o reino de Deus, e ousadamente foi a Pilatos, e pediu o corpo de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '44', 'E Pilatos se maravilhou de que já estivesse morto. E, chamando o centurião, perguntou-lhe se já havia muito que tinha morrido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '45', 'E, tendo-se certificado pelo centurião, deu o corpo a José;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '46', 'O qual comprara um lençol fino, e, tirando-o da cruz, o envolveu nele, e o depositou num sepulcro lavrado numa rocha; e revolveu uma pedra para a porta do sepulcro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '15', '47', 'E Maria Madalena e Maria, mãe de José, observavam onde o punham.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '1', 'E, PASSADO o sábado, Maria Madalena, e Maria, mãe de Tiago, e Salomé, compraram aromas para irem ungi-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '2', 'E, no primeiro dia da semana, foram ao sepulcro, de manhã cedo, ao nascer do sol;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '3', 'E diziam umas às outras: Quem nos revolverá a pedra da porta do sepulcro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '4', 'E, olhando, viram que já a pedra estava revolvida; e era ela muito grande.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '5', 'E, entrando no sepulcro, viram um mancebo assentado à direita, vestido de uma roupa comprida, branca; e ficaram espantadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '6', 'Porém ele disse-lhes: Não vos assusteis; buscais a Jesus Nazareno, que foi crucificado; já ressuscitou, não está aqui; eis aqui o lugar onde o puseram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '7', 'Mas, ide, dizei a seus discípulos, e a Pedro, que ele vai adiante de vós para a Galileia; ali o vereis, como ele vos disse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '8', 'E, saindo elas apressadamente, fugiram do sepulcro, porque estavam possuídas de temor e assombro; e nada diziam a ninguém, porque temiam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '9', 'E Jesus tendo ressuscitado na manhã do primeiro dia da semana, apareceu primeiramente a Maria Madalena, da qual tinha expulsado sete demónios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '10', 'E, partindo ela, anunciou-o àqueles que tinham estado com ele, os quais estavam tristes, e chorando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '11', 'E, ouvindo eles que vivia, e que tinha sido visto por ela, não o creram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '12', 'E depois manifestou-se noutra forma a dois deles, que iam de caminho para o campo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '13', 'E, indo estes, anunciaram-no aos outros, mas nem ainda estes creram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '14', 'Finalmente apareceu aos onze, estando eles assentados juntamente, e lançou-lhes em rosto a sua incredulidade e dureza de coração, por não haverem crido nos que o tinham visto já ressuscitado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '15', 'E disse-lhes: Ide por todo o mundo, pregai o evangelho a toda a criatura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '16', 'Quem crer e for baptizado será salvo; mas quem não crer será condenado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '17', 'E estes sinais seguirão aos que crerem: Em meu nome expulsarão os demónios; falarão novas línguas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '18', 'Pegarão nas serpentes; e, se beberem alguma coisa mortífera, não lhes fará dano algum; e porão as mãos sobre os enfermos e os curarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '19', 'Ora o SENHOR, depois de lhes ter falado, foi recebido no céu, e assentou-se à direita de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('2', '16', '20', 'E eles, tendo partido, pregaram por todas as partes, cooperando com eles o SENHOR, e confirmando a palavra com os sinais que se seguiam. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '1', '¶ TENDO pois muitos empreendido pôr em ordem a narração dos factos que entre nós se cumpriram,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '2', 'Segundo nos transmitiram os mesmos que os presenciaram desde o princípio, e foram ministros da palavra,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '3', 'Pareceu-me também a mim conveniente descrevê-los a ti, ó excelente Teófilo, por sua ordem, havendo-me já informado minuciosamente de tudo desde o princípio;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '4', 'Para que conheças a certeza das coisas de que já estás informado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '5', 'Existiu, no tempo de Herodes, rei da Judeia, um sacerdote chamado Zacarias, da ordem de Abias, e cuja mulher era das filhas de Aarão; e o seu nome era Isabel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '6', 'E eram ambos justos perante Deus, andando sem repreensão em todos os mandamentos e preceitos do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '7', 'E não tinham filhos, porque Isabel era estéril, e ambos eram avançados em idade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '8', 'E aconteceu que, exercendo ele o sacerdócio diante de Deus, na ordem da sua turma,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '9', 'Segundo o costume sacerdotal, coube em sorte entrar no templo do SENHOR para oferecer o incenso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '10', 'E toda a multidão do povo estava fora, orando, à hora do incenso;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '11', 'E um anjo do SENHOR lhe apareceu, posto em pé, à direita do altar do incenso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '12', 'E Zacarias, vendo-o, turbou-se, e caiu temor sobre ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '13', 'Mas o anjo lhe disse: Zacarias, não temas, porque a tua oração foi ouvida, e Isabel, tua mulher, dará à luz um filho, e lhe porás o nome de João;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '14', 'E terás prazer e alegria, e muitos se alegrarão no seu nascimento,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '15', 'Porque será grande diante do SENHOR, e não beberá vinho, nem bebida forte, e será cheio do Espírito Santo, já desde o ventre de sua mãe;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '16', 'E converterá muitos dos filhos de Israel ao SENHOR seu Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '17', 'E irá adiante dele no espírito e virtude de Elias, para converter os corações dos pais aos filhos, e os rebeldes à prudência dos justos; com o fim de preparar ao SENHOR um povo bem disposto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '18', 'Disse então Zacarias ao anjo: Como saberei isto? Pois eu já sou velho, e minha mulher avançada em idade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '19', 'E, respondendo o anjo, disse-lhe: Eu sou Gabriel, que assisto diante de Deus, e fui enviado a falar-te e dar-te estas alegres novas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '20', 'E eis que ficarás mudo, e não poderás falar até ao dia em que estas coisas aconteçam; porquanto não creste nas minhas palavras que a seu tempo se hão-de cumprir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '21', 'E o povo estava esperando a Zacarias, e maravilhava-se de que tanto se demorasse no templo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '22', 'E, saindo ele não lhes podia falar; e entenderam que tinha alguma visão no templo. E falava por acenos, e ficou mudo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '23', 'E sucedeu que, terminados os dias de seu ministério, voltou para sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '24', 'E depois daqueles dias Isabel, sua mulher, concebeu, e por cinco meses se ocultou, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '25', 'Assim me fez o SENHOR, nos dias em que atentou em mim, para destruir o meu opróbrio entre os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '26', 'E, no sexto mês, foi o anjo Gabriel enviado por Deus a uma cidade da Galileia, chamada Nazaré,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '27', 'A uma virgem desposada com um varão, cujo nome era José, da casa de David; e o nome da virgem era Maria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '28', 'E, entrando o anjo aonde ela estava, disse: Salve, agraciada; o SENHOR é contigo: bendita és tu entre as mulheres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '29', 'E, vendo-o ela, turbou-se muito com aquelas palavras, e considerava que saudação seria esta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '30', 'Disse-lhe então o anjo: Maria, não temas, porque achaste graça diante de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '31', 'E eis que em teu ventre conceberás e darás à luz um filho, e pôr-lhe-ás o nome de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '32', 'Este será grande, e será chamado filho do Altíssimo; e o SENHOR Deus lhe dará o trono de David, seu pai;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '33', 'E reinará eternamente na casa de Jacob, e o seu reino não terá fim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '34', 'E disse Maria ao anjo: Como se fará isto, visto que não conheço varão?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '35', 'E, respondendo o anjo, disse-lhe: Descerá sobre ti o Espírito Santo, e a virtude do Altíssimo te cobrirá com a sua sombra; pelo que também o Santo, que de ti há-de nascer, será chamado Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '36', 'E eis que também Isabel, tua prima, concebeu um filho em sua velhice; e é este o sexto mês para aquela que era chamada estéril.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '37', 'Porque para Deus nada é impossível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '38', 'Disse então Maria: Eis aqui a serva do SENHOR; cumpra-se em mim segundo a tua palavra. E o anjo ausentou-se dela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '39', 'E naqueles dias, levantando-se Maria, foi apressada às montanhas, a uma cidade de Judá,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '40', 'E entrou em casa de Zacarias, e saudou a Isabel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '41', 'E aconteceu que, ao ouvir Isabel a saudação de Maria, a criancinha saltou no seu ventre; e Isabel foi cheia do Espírito Santo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '42', 'E exclamou com grande voz, e disse: Bendita és tu entre as mulheres, e bendito é o fruto do teu ventre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '43', 'E donde me provém isto a mim, que venha visitar-me a mãe do meu Senhor?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '44', 'Pois eis que, ao chegar aos meus ouvidos a voz da tua saudação, a criancinha saltou de alegria no meu ventre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '45', 'Bem-aventurada a que creu, pois hão-de cumprir-se as coisas que da parte do SENHOR lhe foram ditas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '46', 'Disse então Maria: A minha alma engrandece ao SENHOR,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '47', 'E o meu espírito se alegra em Deus meu Salvador;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '48', 'Porque atentou na baixeza de sua serva; pois eis que desde agora todas as gerações me chamarão bem-aventurada:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '49', 'Porque me fez grandes coisas o Poderoso; e santo é o seu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '50', 'E a sua misericórdia é de geração em geração sobre os que o temem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '51', 'Com o seu braço obrou valorosamente; dissipou os soberbos no pensamento de seus corações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '52', 'Depôs dos tronos os poderosos, e elevou os humildes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '53', 'Encheu de bens os famintos, e despediu vazios os ricos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '54', 'Auxiliou a Israel seu servo, recordando-se da sua misericórdia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '55', '(Como falou a nossos pais) para com Abraão e sua posteridade, para sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '56', 'E Maria ficou com ela quase três meses, e depois voltou para sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '57', 'E completou-se para Isabel o tempo de dar à luz, e teve um filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '58', 'E os seus vizinhos e parentes ouviram que tinha Deus usado para com ela de grande misericórdia, e alegraram-se com ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '59', 'E aconteceu que, ao oitavo dia, vieram circuncidar o menino, e lhe chamavam Zacarias, o nome de seu pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '60', 'E, respondendo sua mãe, disse: Não, porém será chamado João.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '61', 'E disseram-lhe: Ninguém há na tua parentela que se chame por este nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '62', 'E perguntaram por acenos ao pai como queria que lhe chamassem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '63', 'E, pedindo ele uma tabuinha de escrever, escreveu, dizendo: O seu nome é João. E todos se maravilharam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '64', 'E logo a boca se lhe abriu, e a língua se lhe soltou; e falava, louvando a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '65', 'E veio temor sobre todos os seus vizinhos, e em todas as montanhas da Judeia foram divulgadas todas estas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '66', 'E todos os que as ouviam as conservavam em seus corações, dizendo: Quem será pois este menino? E a mão do SENHOR estava com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '67', 'E Zacarias, seu pai, foi cheio do Espírito Santo, e profetizou, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '68', 'Bendito o SENHOR Deus de Israel, porque visitou e remiu o seu povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '69', 'E nos levantou uma salvação poderosa na casa de David seu servo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '70', 'Como falou pela boca dos seus santos profetas, desde o principio do mundo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '71', 'Para nos livrar dos nossos inimigos e da mão de todos os que nos aborrecem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '72', 'Para manifestar misericórdia a nossos pais, e lembrar-se do seu santo concerto,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '73', 'E do juramento que jurou a Abraão nosso pai,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '74', 'De conceder-nos que, libertados da mão de nossos inimigos, o serviríamos sem temor,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '75', 'Em santidade e justiça perante ele todos os dias da nossa vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '76', 'E tu, ó menino, serás chamado profeta do Altíssimo, porque hás-de ir ante a face do SENHOR, a preparar os seus caminhos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '77', 'Para dar ao seu povo conhecimento da salvação na remissão dos seus pecados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '78', 'Pelas entranhas da misericórdia do nosso Deus, com que o Oriente do alto nos visitou;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '79', 'Para alumiar aos que estão assentados em trevas e sombra da morte; a fim de dirigir os nossos pés pelo caminho da paz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '1', '80', 'E o menino crescia, e se robustecia em espírito. E esteve nos desertos até ao dia em que havia de mostrar-se a Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '1', 'E ACONTECEU naqueles dias que saiu um decreto da parte de César Augusto para que todo o mundo se alistasse');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '2', '(Este primeiro alistamento foi feito sendo Quirino presidente da Síria).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '3', 'E todos iam alistar-se, cada um à sua própria cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '4', 'E subiu também José da Galileia, da cidade de Nazaré, à Judeia, à cidade de David chamada Belém (porque era da casa e família de David),');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '5', 'A fim de alistar-se com Maria, sua mulher, que estava grávida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '6', 'E aconteceu que, estando eles ali, se cumpriram os dias em que ela havia de dar à luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '7', 'E deu à luz a seu filho primogénito, e envolveu-o em panos, e deitou-o numa manjedoura, porque não havia lugar para eles na estalagem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '8', 'Ora havia naquela mesma comarca pastores que estavam no campo, e guardavam durante as vigílias da noite o seu rebanho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '9', 'E eis que o anjo do SENHOR veio sobre eles, e a glória do SENHOR os cercou de resplendor, e tiveram grande temor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '10', 'E o anjo lhes disse: Não temais, porque eis aqui vos trago novas de grande alegria, que será para todo o povo');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '11', 'Pois, na cidade de David, vos nasceu hoje o Salvador, que é Cristo, o SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '12', 'E isto vos será por sinal: Achareis o menino envolto em panos, e deitado numa manjedoura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '13', 'E, no mesmo instante apareceu com o anjo uma multidão dos exércitos celestiais, louvando a Deus, e dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '14', 'Glória a Deus nas alturas, paz na terra, boa vontade para com os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '15', 'E aconteceu que, ausentando-se deles os anjos para o céu, disseram os pastores uns aos outros: Vamos pois até Belém, e vejamos isso que aconteceu, e que o SENHOR nos fez saber.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '16', 'E foram apressadamente, e acharam Maria, e José, e o menino deitado na manjedoura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '17', 'E, vendo-o, divulgaram a palavra que acerca do menino lhes fora dita;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '18', 'E todos os que a ouviram se maravilharam do que os pastores lhes diziam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '19', 'Mas Maria guardava todas estas coisas, conferindo-as em seu coração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '20', 'E voltaram os pastores, glorificando e louvando a Deus por tudo o que tinham ouvido e visto, como lhes havia sido dito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '21', 'E, quando os oito dias foram cumpridos, para circuncidar o menino, foi-lhe dado o nome de Jesus, que pelo anjo lhe fora posto antes de ser concebido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '22', 'E, cumprindo-se os dias da purificação, segundo a lei de Moisés, o levaram a Jerusalém, para o apresentarem ao SENHOR,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '23', '(Segundo o que está escrito na lei do SENHOR: Todo o macho primogénito será consagrado ao SENHOR);');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '24', 'E para darem a oferta segundo o disposto na lei do SENHOR: um par de rolas ou dois pombinhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '25', 'Havia em Jerusalém um homem cujo nome era Simeão; e este homem era justo e temente a Deus, esperando a consolação de Israel; e o Espírito Santo estava sobre ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '26', 'E fora-lhe revelado pelo Espírito Santo que ele não morreria antes de ter visto o Cristo do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '27', 'E pelo Espírito foi ao templo, e, quando os pais trouxeram o menino Jesus, para com ele procederem segundo o uso da lei,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '28', 'Ele então o tomou em seus braços, e louvou a Deus, e disse:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '29', 'Agora, Senhor, despedes em paz o teu servo, segundo a tua palavra,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '30', 'Pois já os meus olhos viram a tua salvação,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '31', 'A qual tu preparaste perante a face de todos os povos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '32', 'Luz para alumiar as nações, e para glória de teu povo Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '33', 'E José, e sua mãe, se maravilharam das coisas que dele se diziam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '34', 'E Simeão os abençoou, e disse a Maria, sua mãe: Eis que este é posto para queda e elevação de muitos em Israel, e para sinal que é contraditado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '35', '(E uma espada trespassará também a tua própria alma); para que se manifestem os pensamentos de muitos corações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '36', 'E estava ali a profetiza Ana, filha de Fanuel, da tribo de Aser. Esta era já avançada em idade, e tinha vivido com o marido sete anos, desde a sua virgindade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '37', 'E era viúva, de quase oitenta e quatro anos, e não se afastava do templo, servindo a Deus em jejuns e orações, de noite e de dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '38', 'E sobrevindo na mesma hora, ela dava graças a Deus, e falava dele a todos os que esperavam a redenção em Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '39', 'E, quando acabaram de cumprir tudo segundo a lei do SENHOR, voltaram à Galileia, para a sua cidade de Nazaré.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '40', 'E o menino crescia, e se fortalecia em espírito, cheio de sabedoria; e a graça de Deus estava sobre ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '41', 'Ora, todos os anos, iam seus pais a Jerusalém, à festa da páscoa;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '42', 'E, tendo ele já doze anos, subiram a Jerusalém, segundo o costume do dia da festa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '43', 'E, regressando eles, terminados aqueles dias, ficou o menino Jesus em Jerusalém, e não o souberam seus pais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '44', 'Pensando, porém, eles que viria de companhia pelo caminho, andaram caminho de um dia, e procuravam-no entre os parentes e conhecidos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '45', 'E, como o não encontrassem, voltaram a Jerusalém em busca dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '46', 'E aconteceu que, passados três dias, o acharam no templo, assentado no meio dos doutores, ouvindo-os, e interrogando-os.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '47', 'E todos os que o ouviam admiravam a sua inteligência e respostas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '48', 'E quando o viram, maravilharam-se, e disse-lhe sua mãe: Filho, porque fizeste assim para connosco? Eis que teu pai e eu ansiosos te procurávamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '49', 'E ele lhes disse: Porque é que me procuráveis? Não sabeis que me convém tratar dos negócios de meu Pai?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '50', 'E eles não compreenderam as palavras que lhes dizia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '51', 'E desceu com eles, e foi para Nazaré, e era-lhes sujeito. E sua mãe guardava no seu coração todas estas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '2', '52', 'E crescia Jesus em sabedoria, e em estatura, e em graça para com Deus e os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '1', 'E NO ano quinze do império de Tibério César, sendo Pôncio Pilatos presidente da Judeia, e Herodes tetrarca. da Galileia, e seu irmão Filipe tetrarca da Itureia e da província de Traconites, e Lisânias tetrarca da Abilene;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '2', 'Sendo Anás e Caifás sumos sacerdotes, veio no deserto a palavra de Deus a João, filho de Zacarias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '3', 'E percorreu toda a terra ao redor do Jordão, pregando o baptismo de arrependimento, para o perdão dos pecados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '4', 'Segundo o que está escrito no livro das palavras do profeta Isaías, que diz: Voz do que clama no deserto: Preparai o caminho do SENHOR; endireitai as suas veredas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '5', 'Todo o vale se encherá, e se abaixará todo o monte e outeiro; e o que é tortuoso se endireitará, e os caminhos escabrosos se aplanarão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '6', 'E toda a carne verá a salvação de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '7', 'Dizia pois João à multidão que saía para ser baptizada por ele: Raça de víboras, quem vos ensinou a fugir da ira que está para vir?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '8', 'Produzi pois frutos dignos de arrependimento, e não comeceis a dizer em vós mesmos: Temos Abraão por pai; porque eu vos digo que até destas pedras pode Deus suscitar filhos a Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '9', 'E também já está posto o machado à raiz das árvores; toda a árvore, pois, que não dá bom fruto, corta-se e lança-se no fogo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '10', 'E a multidão o interrogava, dizendo: Que faremos pois?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '11', 'E, respondendo ele, disse-lhes: Quem tiver duas túnicas, reparta com o que não tem, e quem tiver alimentos faça da mesma maneira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '12', 'E chegaram também uns publicanos, para serem baptizados, e disseram-lhe: Mestre, que devemos fazer?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '13', 'E ele lhes disse: Não peçais mais do que o que vos está ordenado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '14', 'E uns soldados o interrogaram também dizendo: E nós que faremos? E ele lhes disse: A ninguém trateis mal nem defraudeis, e contentai-vos com o vosso soldo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '15', 'E, estando o povo em expectação, e pensando todos de João, em seus corações, se porventura seria o Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '16', 'Respondeu João a todos, dizendo: Eu, na verdade, baptizo-vos com água, mas eis que vem aquele que é mais poderoso do que eu, a quem eu não sou digno de desatar a correia das alparcas; esse vos baptizará com o Espírito Santo e com fogo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '17', 'Ele tem a pá na sua mão; e limpará a sua eira, e ajuntará o trigo no seu celeiro, mas queimará a palha com fogo que nunca se apaga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '18', 'E assim admoestando-os, muitas outras coisas também anunciava ao povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '19', 'Sendo, porém, o tetrarca Herodes repreendido por ele por causa de Herodias, mulher de seu irmão Filipe, e por todas as maldades que Herodes tinha feito,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '20', 'Acrescentou a todas as outras ainda esta, a de encerrar João num cárcere.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '21', 'E aconteceu que, como todo o povo se baptizava, sendo baptizado também Jesus, orando ele, o céu se abriu;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '22', 'E o Espírito Santo desceu sobre ele em forma corpórea, como uma pomba; e ouviu-se uma voz do céu, que dizia: Tu és o meu filho amado, em ti me tenho comprazido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '23', 'E o mesmo Jesus começava a ser de quase trinta anos, sendo (como se cuidava) filho de José, e José de Eli,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '24', 'e Eli de Matate, e Matate de Leví, e Leví de Melqui, e Melqui de Joana, e Joana de José,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '25', 'e José de Matatias, e Matatias de Amós, e Amós de Naum, e Naum de Esli, e Esli de Nagai,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '26', 'e Nagai de Maat, e Maat de Matatias, e Matatias de Semei, e Semei de Joseque, e Joseque de Jodá,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '27', 'e Jodá de Joanã, e Joanã de Resa, e Resa de Zorobabel, e Zorobabel de Salatiel, e Salatiel de Neri,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '28', 'e Neri de Melqui, e Melqui de Adi, e Adi de Cosão, e Cosão de Elmodã, e Elmodã de Er,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '29', 'e Er de José, e José de Eliezer, e Eliezer de Jorim, e Jorim de Matate, e Matate de Levi,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '30', 'e Levi de Simeão e Simeão de Judá, e Judá de José, e José de Jonã, e Jonã de Eliaquim,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '31', 'e Eliaquim de Meleá, e Meleá de Mená, e Mená de Matatá, e Matatá de Natã, e Natã de David,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '32', 'e Davl de Jessé, e Jessé de Obed, e Obed de Booz, e Booz de Salá, e Salá de Naassom,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '33', 'e Naassom de Aminadab, e Aminadab de Admim, e Admim de Arni, e Arni de Esrom, e Esrom de Farés, e Farés de Judá,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '34', 'e Judá de Jacob, e Jacob de Isaque, e Isaque de Abraão, e Abraão de Tará, e Tará de Naor,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '35', 'e Naor de Seruque, e Seruque de Ragaú, e Ragaú de Faleque, e Faleque de Eber, e Eber de Salá,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '36', 'e Salá de Cainã, e Cainã de Arfaxade, e Arfaxade de Sem, e Sem de Noé, e Noé de Lameque,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '37', 'e Lameque de Matusalá, e Matusalá de Enoque, e Enoque de Jarede, e Jarede de Maleleel, e Maleleel de Cainã,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '3', '38', 'e Cainã de Enos, e Enos de Sete, e Sete de Adão, e Adão de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '1', 'E JESUS, cheio do Espirito Santo, voltou do Jordão e foi levado pelo Espírito ao deserto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '2', 'E quarenta dias foi tentado pelo diabo e naqueles dias não comeu coisa alguma; e, terminados eles, teve fome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '3', 'E disse-lhe o diabo: Se tu és o Filho de Deus, diz a esta pedra que se transforme em pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '4', 'E Jesus lhe respondeu, dizendo: Escrito está que nem só de pão viverá o homem, mas de toda a palavra de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '5', 'E o diabo, levando-o a um alto monte, mostrou-lhe num momento de tempo todos os reinos do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '6', 'E disse-lhe o diabo: Dar-te-ei a ti todo este poder e a sua glória; porque a mim me foi entregue, e dou-o a quem quero;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '7', 'Portanto, se tu me adorares, tudo será teu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '8', 'E Jesus, respondendo, disse-lhe: Vai-te, Satanás; porque está escrito: Adorarás o SENHOR teu Deus, e só a Ele servirásl');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '9', 'Levou-o também a Jerusalém, e pô-lo sobre o pináculo do templo, e disse-lhe: Se tu és o Filho de Deus, lança-te daqui abaixo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '10', 'Porque está escrito: Mandará aos seus anjos, acerca de ti, que te guardem,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '11', 'E que te sustenham nas mãos, para que nunca tropeces com o teu pé em alguma pedra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '12', 'E Jesus, respondendo, disse-lhe: Dito está: Não tentarás ao SENHOR teu Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '13', 'E, acabando o diabo toda a tentação, ausentou-se dele por algum tempo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '14', 'Então, pela virtude do Espírito voltou Jesus para a Galileia, e a sua fama correu por todas as terras em derredor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '15', 'E ensinava nas suas sinagogas, e por todos era louvado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '16', 'E, chegando a Nazaré, onde fora criado, entrou num dia de sábado, segundo o seu costume, na sinagoga, e levantou-se para ler:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '17', 'E foi-lhe dado o livro do profeta Isaías; e, quando abriu o livro achou o lugar em que estava escrito:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '18', 'O Espírito Santo é sobre mim, pois que me ungiu para evangelizar os pobres, enviou-me a curar os quebrantados do coração,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '19', 'A apregoar liberdade aos cativos, e dar vista aos cegos; a pôr em liberdade os oprimidos; a anunciar o ano aceitável do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '20', 'E, cerrando o livro, e tornando-o a dar ao ministro, assentou-se; e os olhos de todos na sinagoga estavam fitos nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '21', 'Então começou a dizer-lhes: Hoje se cumpriu esta Escritura em vossos ouvidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '22', 'E todos lhe davam testemunho, e se maravilhavam das palavras de graça que saíam da sua boca; e diziam: Não é este o filho de José?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '23', 'E ele lhes disse: Sem dúvida, me direis este provérbio - Médico, cura-te a ti mesmo; faz também aqui na tua pátria tudo o que ouvimos ter sido feito em Cafarnaum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '24', 'E disse: Em verdade vos digo que nenhum profeta é bem recebido na sua pátria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '25', 'Em verdade, vos digo que muitas viúvas existiam em Israel nos dias de Elias, quando o céu se cerrou por três anos e seis meses, de sorte que em toda a terra houve grande fome;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '26', 'E a nenhuma delas foi enviado Elias, senão a Sarepta de Sidon, a uma mulher viúva.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '27', 'E muitos leprosos havia em Israel no tempo do profeta Eliseu, e nenhum deles foi purificado, senão Naamã, o siro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '28', 'E todos, na sinagoga, ouvindo estas coisas, se encheram de ira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '29', 'E, levantando-se, o expulsaram da cidade, e o levaram até ao cume do monte em que a cidade deles estava edificada, para dali o precipitarem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '30', 'Ele, porém, passando pelo meio deles retirou-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '31', 'E desceu a Cafarnaum, cidade da Galileia, e os ensinava nos sábados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '32', 'E admiravam a sua doutrina porque a sua palavra era com autoridade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '33', 'E estava na sinagoga um homem que tinha o espírito de um demónio imundo, e exclamou em alta voz,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '34', 'Dizendo, Ah! Que temos nós contigo, Jesus Nazareno? Vieste a destruir-nos? Bem sei quem és: o Santo de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '35', 'E Jesus o repreendeu, dizendo: Cala-te, e sai dele. E o demónio, lançando-o por terra no meio do povo, saiu dele sem lhe fazer mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '36', 'E veio espanto sobre todos, e falavam entre si uns e outros, dizendo: Que palavra é esta, que até aos espíritos imundos manda com autoridade e poder, e eles saem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '37', 'E a sua fama divulgava-se por todos os lugares, em redor daquela comarca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '38', 'Ora, levantando-se Jesus da sinagoga, entrou em casa de Simão; e a sogra de Simão estava enferma com muita febre, e rogaram-lhe por ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '39', 'E, inclinando-se para ela, repreendeu a febre, e esta a deixou. E ela, levantando-se logo, servia-os,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '40', 'E, ao pôr do sol, todos os que tinham enfermos de várias doenças lhos traziam; e, pondo as mãos sobre cada um deles, os curava:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '41', 'E também de muitos saíam demónios clamando e dizendo: Tu és o Cristo, o Filho de Deus. E ele, repreendendo-os, não os deixava falar, pois sabiam que ele era o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '42', 'E, sendo já dia, saiu, e foi para um lugar deserto; e a multidão o procurava, e chegou junto dele; e o detinham, para que não se ausentasse deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '43', 'Ele, porém, lhes disse: Também é necessário que eu anuncie a outras cidades o evangelho do reino de Deus; porque para isso fui enviado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '4', '44', 'E pregava nas sinagogas da Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '1', 'E ACONTECEU que, apertando-o a multidão para ouvir a palavra de Deus, estava ele junto ao lago de Genezaré;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '2', 'E viu estar dois barcos junto à praia do lago; e os pescadores, havendo descido deles, estavam lavando as redes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '3', 'E, entrando num dos barcos, que era o de Simão, pediu-lhe que o afastasse um pouco de terra; e, assentando-se, ensinava do barco a multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '4', 'E, quando acabou de falar, disse a Simão: Faz-te ao mar alto, e lançai as vossas redes para pescar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '5', 'E, respondendo Simão, disse-lhe: Mestre, havendo trabalhado toda a noite, nada apanhámos; mas, sobre a tua palavra, lançarei a rede.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '6', 'E, fazendo assim, colheram uma grande quantidade de peixes, e rompia-se-lhes a rede.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '7', 'E fizeram sinal aos companheiros que estavam no outro barco, para que os fossem ajudar. E foram, e encheram ambos os barcos, de maneira tal que quase iam a pique.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '8', 'E, vendo isto Simão Pedro, prostrou-se aos pés de Jesus, dizendo: Senhor, ausenta-te de mim, que sou um homem pecador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '9', 'Pois que o espanto se apoderara dele, e de todos os que com ele estavam, por causa da pesca de peixe que haviam feito;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '10', 'E, de igual modo, também de Tiago e João, filhos de Zebedeu, que eram companheiros de Simão. E disse Jesus a Simão: Não temas: de agora em diante serás pescador de homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '11', 'E, levando os barcos para terra, deixaram tudo, e o seguiram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '12', 'E aconteceu que, quando estava numa daquelas cidades, eis que um homem cheio de lepra, vendo a Jesus, prostrou-se sobre o rosto, e rogou-lhe, dizendo: Senhor, se quiseres, bem podes limpar-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '13', 'E ele, estendendo a mão, tocou-lhe, dizendo; Quero, sê limpo. E logo a lepra desapareceu dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '14', 'E ordenou-lhe que a ninguém o dissesse. Mas vai, disse, mostra-te ao sacerdote, e oferece, pela tua purificação, o que Moisés determinou, para que lhes sirva de testemunho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '15', 'Porém a sua fama se propagava ainda mais, e ajuntava-se muita gente para o ouvir e para ser por ele curada das suas enfermidades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '16', 'Porém ele retirava-se para os desertos; e ali orava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '17', 'E aconteceu que, num daqueles dias, estava ensinando, e estavam ali assentados fariseus e doutores da lei, que tinham vindo de todas as aldeias da Galileia, e da Judeia, e de Jerusalém. E a virtude do SENHOR estava com ele para curar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '18', 'E eis que uns homens transportaram numa cama um homem que estava paralítico, e procuravam fazê-lo entrar e pô-lo diante dele;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '19', 'E, não achando por onde o pudessem levar, por causa da multidão, subiram ao telhado, e por entre as telhas o baixaram com a cama, até ao meio, diante de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '20', 'E, vendo ele a fé deles, disse-lhe: Homem, os teus pecados te são perdoados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '21', 'E os escribas e os fariseus começaram a arrazoar, dizendo: Quem é este que diz blasfémias? Quem pode perdoar pecados, senão só Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '22', 'Jesus, porém, conhecendo os seus pensamentos, respondeu, e disse-lhes: Que arrazoais em vossos corações?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '23', 'Qual é mais fácil? Dizer: Os teus pecados te são perdoados; ou dizer: levanta-te, e anda?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '24', 'Ora, para que saibais que o Filho do homem tem sobre a terra poder para perdoar pecados (disse ao paralítico), a ti te digo: Levanta-te, toma a tua cama e vai para tua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '25', 'E, levantando-se logo diante deles, e tomando a cama em que estava deitado, foi para sua casa, glorificando a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '26', 'E todos ficaram maravilhados, e glorificaram a Deus; e ficaram cheios de temor, dizendo: Hoje vimos prodígios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '27', 'E, depois disto, saiu, e viu um publicano chamado Levi, assentado na recebedoria, e disse-lhe: Segue-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '28', 'E ele, deixando tudo, levantou-se e o seguiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '29', 'E fez-lhe Levi um grande banquete em sua casa; e havia ali uma multidão de publicanos e outros que estavam com eles à mesa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '30', 'E os escribas deles, e os fariseus, murmuravam contra os seus discípulos, dizendo: Porque comeis e bebeis com publicanos e pecadores?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '31', 'E Jesus, respondendo, disse-lhes: Não necessitam médico os que estão sãos, mas, sim, os que estão enfermos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '32', 'Eu não vim chamar os justos, mas, sim, os pecadores, ao arrependimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '33', 'Disseram-lhe então eles: Porque jejuam os discípulos de João muitas vezes, e fazem orações, como também os dos fariseus, mas os teus comem e bebem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '34', 'E ele lhes disse: Podeis vós fazer jejuar os filhos das bodas, enquanto o esposo está com eles?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '35', 'Dias virão, porém, em que o esposo lhes será tirado, e então, naqueles dias, jejuarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '36', 'E disse-lhes também uma parábola: Ninguém tira um pedaço dum vestido novo para o coser em vestido velho, pois que romperá o novo e o remendo não condiz com o velho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '37', 'E ninguém deita vinho novo em odres velhos; doutra sorte o vinho novo romperá os odres, e entornar-se-á o vinho, e os odres se estragarão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '38', 'Mas o vinho novo deve deitar-se em odres novos, e ambos juntamente se conservarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '5', '39', 'E ninguém tendo bebido o velho quer logo o novo, porque diz: Melhor é o velho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '1', 'E ACONTECEU que, no sábado segundo-primeiro, passou pelas searas, e os seus discípulos iam arrancando espigas, e, esfregando-as com as mãos, as comiam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '2', 'E alguns dos fariseus lhe disseram: Porque fazeis o que não é lícito fazer nos sábados?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '3', 'E Jesus, respondendo-lhes, disse: nunca lestes o que fez David quando teve fome, ele e os que com ele estavam?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '4', 'Como entrou na casa de Deus, e tomou os pães da proposição, e os comeu, e deu também aos que estavam com ele, os quais não é lícito comer senão só aos sacerdotes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '5', 'E dizia-lhes: O Filho do homem é Senhor até do sábado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '6', 'E aconteceu também noutro sábado que entrou na sinagoga, e estava ensinando; e havia ali um homem que tinha a mão direita mirrada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '7', 'E os escribas e fariseus atentavam nele, se o curaria no sábado, para acharem de que o acusar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '8', 'Mas ele bem conhecia os seus pensamentos, e disse ao homem que tinha a mão mirrada: Levanta-te, e fica em pé no meio. E, levantando-se ele, ficou em pé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '9', 'Então Jesus lhe disse: Uma coisa vos hei-de perguntar. É lícito nos sábados fazer bem, ou fazer mal? Salvar a vida, ou matar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '10', 'E, olhando para todos em redor, disse ao homem: Estende a tua mão. E ele assim o fez, e a mão lhe foi restituída sã como a outra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '11', 'E ficaram cheios de furor, e uns com os outros conferenciavam sobre o que fariam a Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '12', 'E aconteceu que naqueles dias subiu ao monte a orar, e passou a noite em oração a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '13', 'E, quando já era dia, chamou a si os seus discípulos, e escolheu doze deles, a quem também deu o nome de apóstolos');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '14', 'Simão, ao qual também chamou Pedro, e André, seu irmão; Tiago e João; Filipe e Bartolomeu;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '15', 'E Mateus e Tomé; Tiago, filho de Alfeu, e Simão, chamado o zelador;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '16', 'E Judas, filho de Tiago; e Judas Iscariotes, que foi o traidor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '17', 'E, descendo com eles, parou num lugar plano, e também um grande número de seus discípulos, e grande multidão de povo de toda a Judeia, e de Jerusalém, e da costa marítima de Tiro e de Sidon;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '18', 'Os quais tinham vindo para o ouvir, e serem curados das suas enfermidades, como também os atormentados dos espíritos imundos; e eram curados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '19', 'E toda a multidão procurava tocar-lhe; porque saía dele virtude, e curava a todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '20', 'E, levantando ele os olhos para os seus discípulos, dizia: Bem-aventurados vós, os pobres, porque vosso é o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '21', 'Bem-aventurados vós, que agora chorais, porque haveis de rir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '22', 'Bem-aventurados sereis quando os homens vos aborrecerem e quando vos separarem, e vos injuriarem, e rejeitarem o vosso nome como mau, por causa do Filho do homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '23', 'Folgai nesse dia, exultai; porque, eis que é grande o vosso galardão no céu, pois assim faziam os seus pais aos profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '24', 'Mas ai de vós, ricos! Porque já tendes a vossa consolação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '25', 'Ai de vós, os que estais fartos, porque tereis fome. Ai de vós, os que agora rides, porque vos lamentareis e chorareis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '26', 'Ai de vós quando todos os homens de vós disserem bem, porque assim faziam seus pais aos falsos profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '27', 'Mas a vós, que ouvis, digo: Amai a vossos inimigos, fazei bem aos que vos aborrecem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '28', 'Bendizei os que vos maldizem, e orai pelos que vos caluniam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '29', 'Ao que te ferir numa face, oferece-lhe também a outra; e, ao que te houver tirado a capa, nem a túnica recuses;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '30', 'E dá a qualquer que te pedir; e, ao que tomar o que é teu, não lho tornes a pedir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '31', 'E, como vós quereis que os homens vos façam, da mesma maneira lhes fazei vós também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '32', 'E, se amardes aos que vos amam, que recompensa tereis? Também os pecadores amam aos que os amam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '33', 'E, se fizerdes bem aos que vos fazem bem, que recompensa tereis? Também os pecadores fazem o mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '34', 'E, se emprestardes àquele de quem esperais tornar a receber, que recompensa tereis? Também os pecadores emprestam aos pecadores, para tornarem a receber outro tanto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '35', 'Amai pois a vossos inimigos, e fazei bem, e emprestai, sem nada esperardes, e será grande o vosso galardão, e sereis filhos do Altíssimo porque Ele é benigno até para com os ingratos e maus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '36', 'Sede pois misericordiosos, como também vosso Pai é misericordioso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '37', 'Não julgueis, e não sereis julgados; não condeneis, e não sereis condenados; soltai, e soltar-vos-ão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '38', 'Dai, e ser-vos-á dado; boa medida, recalcada, sacudida e transbordando, vos deitarão no vosso regaço; porque com a mesma medida com que medirdes também vos medirão de novo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '39', 'E dizia-lhes uma parábola: Pode porventura o cego guiar o cego? Não cairão ambos na cova?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '40', 'O discípulo não é superior a seu mestre, mas todo o que for perfeito será como o seu mestre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '41', 'E porque atentas tu no argueiro que está no olho de teu irmão, e não reparas na trave que está no teu próprio olho?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '42', 'Ou como podes dizer a teu irmão: Irmão, deixa-me tirar o argueiro que está no teu olho, não atentando tu mesmo na trave que está no teu olho? Hipócrita, tira primeiro a trave do teu olho, e então verás bem para tirar o argueiro que está no olho de teu irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '43', 'Porque não há boa árvore que dê mau fruto, nem má árvore que dê bom fruto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '44', 'Porque cada árvore se conhece pelo seu próprio fruto; pois não se colhem figos dos espinheiros, nem se vindimam uvas dos abrolhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '45', 'O homem bom do bom tesouro do seu coração tira o bem, e o homem mau do mau tesouro do seu coração tira o mal, porque da abundância do seu coração fala a boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '46', 'E porque me chamais, Senhor, Senhor, e não fazeis o que eu digo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '47', 'Qualquer que vem a mim e ouve as minhas palavras, e as observa, eu vos mostrarei a quem é semelhante,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '48', 'É semelhante ao homem que edificou uma casa, e cavou, e abriu bem fundo, e pôs os alicerces sobre rocha; e, vindo a enchente, bateu com ímpeto a corrente, naquela casa, e não a pôde abalar, porque estava fundada sobre rocha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '6', '49', 'Mas o que ouve e não pratica é semelhante ao homem que edificou uma casa sobre terra, sem alicerces, na qual bateu com ímpeto a corrente, e logo caiu; e foi grande a ruína daquela casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '1', 'E DEPOIS de concluir todos estes discursos perante o povo, entrou em Cafarnaúm.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '2', 'E o servo de um certo centurião, a quem muito estimava, estava doente, e moribundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '3', 'E, quando ouviu falar de Jesus, enviou-lhe uns anciãos dos judeus, rogando-lhe que viesse curar o seu servo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '4', 'E, chegando eles junto de Jesus, rogaram-lhe muito, dizendo: É digno de que lhe concedas isto,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '5', 'Porque ama a nossa nação, e ele mesmo nos edificou a sinagoga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '6', 'E foi Jesus com eles; mas, quando já estava perto da casa, enviou-lhe o centurião uns amigos, dizendo-lhe: Senhor, não te incomodes, porque não sou digno de que entres debaixo do meu telhado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '7', 'E por isso nem ainda me julguei digno de ir ter contigo; diz, porém, uma palavra, e o meu criado sarará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '8', 'Porque também eu sou homem sujeito à autoridade, e tenho soldados sob o meu poder, e digo a este: Vai; e ele vai; e a outro: Vem; e ele vem; e ao meu servo: Faz isto; e ele o faz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '9', 'E, ouvindo isto Jesus, maravilhou-se dele, e, voltando-se, disse à multidão que o seguia: Digo-vos que nem ainda em Israel tenho achado tanta fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '10', 'E, voltando para casa os que foram enviados, acharam são o servo enfermo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '11', 'E aconteceu pouco depois ir ele à cidade chamada Naim, e com ele iam muitos dos seus discípulos, e uma grande multidão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '12', 'E quando chegou perto da porta da cidade, eis que levavam um defunto, filho único de sua mãe, que era viúva; e com ela ia uma grande multidão da cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '13', 'E, vendo-a, o SENHOR moveu-se de íntima compaixão por ela, e disse-lhe: Não chores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '14', 'E, chegando-se tocou o esquife (e os que o levavam pararam), e disse: Mancebo, a ti te digo: Levanta-te.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '15', 'E o defunto assentou-se, e começou a falar. E entregou-o a sua mãe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '16', 'E de todos se apoderou o temor, e glorificavam a Deus, dizendo: Um grande profeta se levantou entre nós, e Deus visitou o seu povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '17', 'E correu dele esta fama por toda a Judeia e por toda a terra circunvizinha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '18', 'E os discípulos de João anunciaram-lhe todas estas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '19', 'E João, chamando dois dos seus discípulos enviou-os a Jesus, dizendo: És tu aquele que havia de vir, ou esperamos outro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '20', 'E, quando aqueles homens chegaram junto dele, disseram: João Baptista enviou-nos a perguntar-te: És tu aquele que havia de vir, ou esperamos outro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '21', 'E, na mesma hora, curou muitos de enfermidades, e males, e espíritos maus, e deu vista a muitos cegos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '22', 'Respondendo então Jesus, disse-lhes: Ide, e anunciai a João o que tendes visto e ouvido: que os cegos vêem, os coxos andam, os leprosos são purificados os surdos ouvem, os mortos ressuscitam e aos pobres anuncia-se o Evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '23', 'E bem-aventurado é aquele que em mim se não escandalizar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '24', 'E, tendo-se retirado os mensageiros de João, começou a dizer à multidão acerca de João: Que saístes a ver no deserto? Uma cana abalada pelo vento?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '25', 'Mas que saístes a ver? Um homem trajado de vestidos delicados? Eis que os que andam com preciosos vestidos, e em delícias, estão nos paços reais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '26', 'Mas que saístes a ver? Um profeta? Sim, vos digo, e muito mais do que profeta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '27', 'Este é aquele de quem está escrito: Eis que envio o meu anjo diante da tua face, o qual preparará diante de ti o teu caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '28', 'E eu vos digo que, entre os nascidos de mulheres, não há maior profeta do que João Baptista; mas o menor no reino de Deus é maior do que ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '29', 'E todo o povo que o ouviu e os publicanos, tendo sido baptizados com o baptismo de João, justificaram a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '30', 'Mas os fariseus e os doutores da lei rejeitaram o conselho de Deus contra si mesmos, não tendo sido baptizados por ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '31', 'E disse o SENHOR: A quem pois compararei os homens desta geração, e a quem são semelhantes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '32', 'São semelhantes aos meninos que, assentados nas praças, clamam uns aos outros, e dizem: Tocámo-vos flauta, e não dançastes; cantámo-vos lamentações, e não chorastes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '33', 'Porque veio João Baptista, que não comia pão nem bebia vinho, e dizeis: Tem demónio; /a>');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '34', 'Veio o Filho do homem, que come e bebe e dizeis: eis aí um homem comilão, e bebedor de vinho, amigo dos publicanos e dos pecadores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '35', 'Mas a sabedoria é justificada por todos os seus filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '36', 'E rogou-lhe um dos fariseus que comesse com ele; e, entrando em casa do fariseu, assentou-se à mesa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '37', 'E eis que uma mulher da cidade, uma pecadora, sabendo que ele estava à mesa em casa do fariseu, levou um vaso de alabastro com unguento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '38', 'E, estando por detrás aos seus pés, chorando, começou a regar-lhe os pés com lágrimas, e enxugava-lhos com os cabelos da sua cabeça; e beijava-lhe os pés e ungia-lhos com o unguento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '39', 'Quando isto viu o fariseu que o tinha convidado, falava consigo, dizendo: Se este fora profeta, bem saberia quem e qual é a mulher que lhe tocou, pois é uma pecadora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '40', 'E respondendo, Jesus, disse-lhe: Simão, uma coisa tenho a dizer-te. E ele disse: Di-la, Mestre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '41', 'Um certo credor tinha dois devedores: um devia-lhe quinhentos dinheiros, e outro cinquenta;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '42', 'E, não tendo eles com que pagar, perdoou-lhes a ambos. Diz pois qual deles o amará mais?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '43', 'E Simão, respondendo, disse: Tenho para mim que é aquele a quem mais perdoou. E ele lhe disse: Julgaste bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '44', 'E, voltando-se para a mulher, disse a Simão: Vês tu esta mulher? Entrei em tua casa, e não me deste água para os pés; mas esta regou-me os pés com lágrimas, e mos enxugou com os seus cabelos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '45', 'Não me deste ósculo, mas esta, desde que entrou, não tem cessado de me beijar os pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '46', 'Não me ungiste a cabeça com óleo; mas esta ungiu-me os pés com unguento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '47', 'Por isso te digo que os seus muitos pecados lhe são perdoados, porque muito amou; mas aquele a quem pouco é perdoado pouco ama.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '48', 'E disse-lhe a ela: Os teus pecados te são perdoados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '49', 'E os que estavam à mesa começaram a dizer entre si: Quem é este, que até perdoa pecados?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '7', '50', 'E disse à mulher: A tua fé te salvou: vai-te em paz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '1', 'E ACONTECEU, depois disto, que andava de cidade em cidade, e de aldeia em aldeia, pregando e anunciando o evangelho do reino, de Deus; e os doze iam com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '2', 'E algumas mulheres que haviam sido curadas de espíritos malignos e de enfermidades: Maria, chamada Madalena, da qual saíram sete demónios;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '3', 'E Joana, mulher de Cusa, procurador de Herodes, e Susana, e muitas outras que o serviam com suas fazendas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '4', 'E, ajuntando-se uma grande multidão, e vindo de todas as cidades ter com ele, disse por parábola:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '5', 'Um semeador saiu a semear a sua semente, e, quando semeava, caiu alguma junto do caminho, e foi pisada, e as aves do céu a comeram;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '6', 'E outra caiu sobre pedra, e nascida, secou-se, pois que não tinha humidade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '7', 'E outra caiu entre espinhos, e crescendo com ela os espinhos, a sufocaram;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '8', 'E outra caiu em boa terra, e, nascida, produziu fruto, cento por um. Dizendo ele estas coisas, clamava: Quem tem ouvidos para ouvir, ouça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '9', 'E os seus discípulos o interrogaram, dizendo: Que parábola é esta?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '10', 'E ele disse: A vós vos é dado conhecer os mistérios do reino de Deus, mas aos outros por parábolas, para que, vendo, não vejam, e, ouvindo, não entendam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '11', 'Esta é pois a parábola: A semente é a palavra de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '12', 'E os que estão junto do caminho, estes são os que ouvem; depois vem o diabo, e tira-lhes do coração a palavra, para que se não salvem, crendo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '13', 'E os que estão sobre pedra, estes são os que, ouvindo a palavra, a recebem com alegria, mas, como não têm raiz, apenas crêem por algum tempo, e no tempo da tentação se desviam;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '14', 'E a que caiu entre espinhos, esses são os que ouviram, e, indo por diante, são sufocados com os cuidados, e riquezas e deleites da vida, e não dão fruto com perfeição;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '15', 'E a que caiu em boa terra, esses são os que, ouvindo a palavra, a conservam num coração honesto e bom, e dão fruto com perseverança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '16', 'E ninguém, acendendo uma candeia, a cobre com algum vaso, ou a põe debaixo da cama; mas põe-na no velador, para que os que entram vejam a luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '17', 'Porque não há coisa oculta que não haja de manifestar-se, nem escondida que não haja de saber-se e vir à luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '18', 'Vede pois como ouvis: porque a qualquer que tiver lhe será dado, e a qualquer que não tiver até o que parece ter lhe será tirado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '19', 'E foram ter com ele sua mãe e seus irmãos, e não podiam aproximar-se dele, por causa da multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '20', 'E foi-lhe dito: Estão lá fora tua mãe e teus irmãos, que querem ver-te.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '21', 'Mas, respondendo ele, disse-lhes: Minha mãe e meus irmãos são aqueles que ouvem a palavra de Deus e a executam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '22', 'E aconteceu que, num daqueles dias, entrou num barco com seus discípulos, e disse-lhes: Passemos para a outra banda do lago. E partiram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '23', 'E, navegando eles, adormeceu; e sobreveio uma tempestade de vento no lago, e enchiam-se de água, estando em perigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '24', 'E, chegando-se a ele, o despertaram, dizendo: Mestre, Mestre, perecemos. E ele, levantando-se, repreendeu o vento e a fúria da água; e cessaram, e fez-se bonança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '25', 'E disse-lhes: Onde está a vossa fé? E eles, temendo, maravilharam-se, dizendo uns aos outros: Quem é este, que até aos ventos e à água manda, e lhe obedecem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '26', 'E navegaram para a terra dos gadarenos, que está defronte da Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '27', 'E, quando desceu para terra, saiu-lhe ao encontro, vindo da cidade, um homem que desde muito tempo estava possesso de demónios, e não andava vestido, nem habitava em qualquer casa, mas nos sepulcros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '28', 'E, quando viu a Jesus, prostrou-se diante dele, exclamando, e dizendo com grande voz: Que tenho eu contigo, Jesus, Filho do Deus Altíssimo? Peço-te que não me atormentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '29', 'Porque tinha ordenado ao espírito imundo que saísse daquele homem; pois já havia muito tempo que o arrebatava. E guardavam-no preso com grilhões e cadeias; mas, quebrando as prisões, era impelido pelo demónio para os desertos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '30', 'E perguntou-lhe Jesus, dizendo: Qual é o teu nome? E ele disse: Legião; porque tinham entrado nele muitos demónios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '31', 'E rogavam-lhe que os não mandasse para o abismo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '32', 'E andava ali pastando no monte uma vara de muitos porcos; e rogaram-lhe que lhes concedesse entrar neles, e concedeu-lho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '33', 'E, tendo saído os demónios do homem, entraram nos porcos, e a manada precipitou-se de um despenhadeiro no lago, e afogou-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '34', 'E aqueles que os guardavam, vendo o que acontecera, fugiram, e foram anunciá-lo na cidade e nos campos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '35', 'E saíram a ver o que tinha acontecido, e vieram ter com Jesus. Acharam então o homem, de quem haviam saído os demónios, vestido, e em seu juízo, assentado aos pés de Jesus, e temeram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '36', 'E os que tinham visto contaram-lhes também como fora salvo aquele endemoninhado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '37', 'E toda a multidão da terra dos gadarenos ao redor lhe rogou que se retirasse deles; porque estavam possuídos de grande temor. E, entrando ele no barco, voltou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '38', 'E aquele homem, de quem haviam saído os demónios, rogou-lhe que o deixasse estar com ele; mas Jesus o despediu, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '39', 'Torna para tua casa, e conta quão grandes coisas te fez Deus. E ele foi apregoando por toda a cidade quão grandes coisas Jesus lhe tinha feito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '40', 'E aconteceu que, quando voltou Jesus, a multidão o recebeu, porque todos o estavam esperando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '41', 'E eis que chegou um varão de nome Jairo, que era príncipe da sinagoga; e, prostrando-se aos pés de Jesus, rogava-lhe que entrasse em sua casa;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '42', 'Porque tinha uma filha única, quase de doze anos, que estava à morte. E, indo ele, apertava-o a multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '43', 'E uma mulher, que tinha um fluxo de sangue, havia doze anos, e gastara com os médicos todos os seus haveres, e por nenhum pudera ser curada,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '44', 'Chegando por detrás dele, tocou na orla do seu vestido, e logo estancou o fluxo do seu sangue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '45', 'E disse Jesus: Quem é que me tocou? E, negando todos, disse Pedro e os que estavam com ele: Mestre, a multidão te aperta e te oprime, e dizes: Quem é que me tocou?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '46', 'E disse Jesus: Alguém me tocou porque bem conheci que de mim saiu virtude.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '47', 'Então, vendo a mulher que não podia ocultar-se, aproximou-se tremendo, e, prostrando-se ante ele, declarou-lhe diante de todo o povo a causa por que lhe havia tocado, e como logo sarara.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '48', 'E ele lhe disse: Tem bom ânimo, filha, a tua fé te salvou; vai-te em paz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '49', 'Estando ele ainda falando, chegou um dos príncipes da sinagoga, dizendo: A tua filha já está morta, não incomodes o Mestre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '50', 'Jesus, porém, ouvindo-o, respondeu-lhe, dizendo: Não temas; crê somente, e será salva.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '51', 'E, entrando em casa, a ninguém deixou entrar, senão a Pedro, e a Tiago, e a João, e ao pai e à mãe da menina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '52', 'E todos choravam, e a pranteavam; e ele disse: Não choreis; não está morta, mas dorme.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '53', 'E riam-se dele, sabendo que estava morta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '54', 'Mas ele, pegando-lhe na mão, clamou, dizendo: Levanta-te, menina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '55', 'E o seu espírito voltou, e ela logo se levantou; e Jesus mandou que lhe dessem de comer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '8', '56', 'E seus pais ficaram maravilhados; e ele lhes mandou que a ninguém dissessem o que havia sucedido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '1', 'CONVOCANDO os seus doze discípulos, deu-lhes virtude e poder sobre todos os demónios, e para curarem enfermidades;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '2', 'E enviou-os a pregar o reino de Deus, e a curar os enfermos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '3', 'E disse-lhes: Nada leveis convosco para o caminho, nem bordões, nem alforge, nem pão, nem dinheiro; nem tenhais dois vestidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '4', 'E, em qualquer casa em que entrardes, ficai ali, e de lá saireis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '5', 'E, se em qualquer cidade vos não receberem, saindo vós dali, sacudi o pó dos vossos pés, em testemunho contra eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '6', 'E, saindo eles, percorreram todas as aldeias, anunciando o evangelho, e fazendo curas por toda a parte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '7', 'E o tetrarca Herodes ouvia tudo o que se passava, e estava em dúvida porque diziam alguns que João ressuscitara dos mortos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '8', 'E outros que Elias tinha aparecido; e outros que um profeta dos antigos havia ressuscitado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '9', 'E disse Herodes: A João mandei eu degolar: quem é pois este de quem ouço dizer tais coisas? E procurava vê-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '10', 'E, regressando os apóstolos, contaram-lhe tudo o que tinham feito. E, tomando-os consigo, retirou-se para um lugar deserto de uma cidade chamada Betsaida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '11', 'E, sabendo-o a multidão, o seguiu; e ele os recebeu, e falava-lhes do reino de Deus, e sarava os que necessitavam de cura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '12', 'E já o dia começava a declinar; então, chegando-se a ele os doze, disseram-lhe: Despede a multidão, para que, indo aos lugares e aldeias em redor, se agasalhem, e achem que comer; porque aqui estamos em lugar deserto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '13', 'Mas ele lhes disse: Dai-lhes vós de comer. E eles disseram: Não temos senão cinco pães e dois peixes; salvo se nós próprios formos comprar comida para todo este povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '14', 'Porquanto estavam ali quase cinco mil homens. Disse então aos seus discípulos: Fazei-os assentar, em ranchos de cinquenta em cinquenta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '15', 'E assim o fizeram, fazendo-os assentar a todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '16', 'E, tomando os cinco pães e os dois peixes, e olhando para o céu, abençoou-os, e partiu-os, e deu-os aos seus discípulos para os porem diante da multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '17', 'E comeram todos, e saciaram-se e levantaram, do que lhes sobejou, doze cestos de pedaços.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '18', 'E aconteceu que, estando ele só, orando, estavam com ele os discípulos; e perguntou-lhes, dizendo: Quem diz a multidão que eu sou?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '19', 'E, respondendo eles, disseram: João Baptista; outros, Elias, e outros que um dos antigos profetas ressuscitou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '20', 'E disse-lhes: E vós, quem dizeis que eu sou? E, respondendo Pedro, disse: O Cristo de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '21', 'E, admoestando-os, mandou que a ninguém referissem isso,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '22', 'Dizendo: É necessário que o Filho do homem padeça muitas coisas e seja rejeitado dos anciãos e dos escribas, e seja morto, e ressuscite ao terceiro dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '23', 'E dizia a todos: Se alguém quer vir após mim, negue-se a si mesmo, e tome cada dia a sua cruz, e siga-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '24', 'Porque, qualquer que quiser salvar a sua vida, perdê-la-á; mas qualquer que, por amor de mim, perder a sua vida, a salvará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '25', 'Porque, que aproveita ao homem granjear o mundo todo, perdendo-se ou prejudicando-se a si mesmo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '26', 'Porque, qualquer que de mim e das minhas palavras se envergonhar, dele se envergonhará o Filho do homem, quando vier na sua glória, e na do Pai e dos anjos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '27', 'E em verdade vos digo que, dos que aqui estão, alguns há que não provarão a morte até que vejam o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '28', 'E aconteceu que, quase oito dias depois destas palavras, tomou consigo a Pedro, a João e a Tiago, e subiu ao monte a orar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '29', 'E, estando ele orando, transfigurou-se a aparência do seu rosto, e o seu vestido ficou branco e mui resplandecente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '30', 'E eis que estavam falando, com ele dois varões, que eram Moisés e Elias,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '31', 'Os quais apareceram com glória, e falavam da sua morte a qual havia de cumprir-se em Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '32', 'E Pedro e os que estavam com ele estavam carregados de sono; e, quando despertaram, viram a sua glória e aqueles dois varões que estavam com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '33', 'E aconteceu que, quando aqueles se apartaram dele, disse Pedro a Jesus: Mestre, bom é que nós estejamos aqui, e façamos três tendas, uma para ti, uma para Moisés, e uma para Elias; não sabendo o que dizia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '34', 'E, dizendo ele isto, veio uma nuvem que os cobriu com a sua sombra; e, entrando eles na nuvem, temeram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '35', 'E saiu da nuvem uma voz que dizia: Este é o meu amado Filho; a ele ouvi.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '36', 'E, tendo soado aquela voz, Jesus foi achado só; e eles calaram-se, e por aqueles dias não contaram a ninguém nada do que tinham visto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '37', 'E aconteceu, no dia seguinte, que, descendo eles do monte, lhes saiu ao encontro uma grande multidão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '38', 'E eis que um homem da multidão clamou, dizendo: Mestre, peço-te que olhes para meu filho, porque é o único que eu tenho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '39', 'Eis que um espírito o toma, e de repente clama, e o despedaça até escumar; e só o larga depois de o ter quebrantado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '40', 'E roguei aos teus discípulos que o expulsassem, e não puderam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '41', 'E Jesus, respondendo, disse: Ó geração incrédula e perversa! Até quando estarei ainda convosco e vos sofrerei? Traz-me cá o teu filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '42', 'E, quando vinha chegando, o demónio o derribou e convulsionou; porém Jesus repreendeu o espírito imundo, e curou o menino, e o entregou a seu pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '43', 'E todos pasmavam da majestade de Deus. E, maravilhando-se todos de todas as coisas que Jesus fazia, disse aos seus discípulos:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '44', 'Ponde vós estas palavras em vossos ouvidos, porque o Filho do homem será entregue nas mãos dos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '45', 'Mas eles não entendiam esta palavra, que lhes era encoberta, para que não a compreendessem; e temiam interrogá-lo acerca desta palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '46', 'E suscitou-se entre eles uma discussão sobre qual deles seria o maior.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '47', 'Mas Jesus, vendo o pensamento de seus corações, tomou um menino, pô-lo junto a si,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '48', 'E disse-lhes: Qualquer que receber este menino em meu nome, recebe-me a mim; e qualquer que me recebe a mim, recebe o que me enviou; porque aquele que entre vós todos for o menor, esse mesmo é grande.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '49', 'E, respondendo João, disse: Mestre, vimos um que em teu nome expulsava os demónios, e lho proibimos porque não te segue connosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '50', 'E Jesus lhes disse: Não o proibais, porque quem não é contra nós é por nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '51', 'E aconteceu que, completando-se os dias para a sua assunção, manifestou o firme propósito de ir a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '52', 'E mandou mensageiros diante da sua face; e, indo eles, entraram numa aldeia de samaritanos, para lhe prepararem pousada,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '53', 'Mas não o receberam, porque o seu aspecto era como de quem ia a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '54', 'E os seus discípulos. Tiago e João, vendo isto, disseram: Senhor, queres que digamos que desça fogo do céu e os consuma, como Elias também fez?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '55', 'Voltando-se, porém repreendeu-os e disse: Vós não sabeis de que espírito sois,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '56', 'Porque o Filho do homem não veio para destruir as almas dos homens, mas para salvá-las. E foram para outra aldeia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '57', 'E aconteceu que, indo eles pelo caminho, lhe disse um: Senhor, seguir-te-ei para onde quer que fores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '58', 'E disse-lhe Jesus: As raposas têm covis, e as aves do céu ninhos, mas o Filho do homem não tem onde reclinar a cabeça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '59', 'E disse a outro: Segue-me. Mas ele respondeu: Senhor, deixa que primeiro eu vá a enterrar meu pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '60', 'Mas Jesus lhe observou: Deixa aos mortos o enterrar os seus mortos; porém tu vai e anuncia o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '61', 'Disse também outro: Senhor, eu te seguirei, mas deixa-me despedir primeiro dos que estão em minha casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '9', '62', 'E Jesus lhe disse: Ninguém, que lança mão do arado e olha para trás, é apto para o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '1', 'E DEPOIS disto designou o SENHOR ainda outros setenta, e mandou-os adiante da sua face, de dois em dois, a todas as cidades e lugares aonde ele havia de ir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '2', 'E dizia-lhes: Grande é, em verdade, a seara, mas os obreiros são poucos; rogai pois ao SENHOR da seara que envie obreiros para a sua seara.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '3', 'Ide; eis que vos mando como cordeiros ao meio de lobos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '4', 'Não leveis bolsa, nem alforge, nem alparcas; e a ninguém saudeis pelo caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '5', 'E, em qualquer casa onde entrardes, dizei primeiro: Paz seja nesta casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '6', 'E, se ali houver algum filho de paz, repousará sobre ele a vossa paz; e, se não, voltará para vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '7', 'E ficai na mesma casa, comendo e bebendo do que eles tiverem, pois digno é o obreiro do seu salário. Não andeis de casa em casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '8', 'E, em qualquer cidade em que entrardes, e vos receberem, comei do que vos puserem diante.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '9', 'E curai os enfermos que nela houver, e dizei-lhes. É chegado a vós o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '10', 'Mas em qualquer cidade em que entrardes e vos não receberem, saindo por suas ruas, dizei:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '11', 'Até o pó, que da vossa cidade se nos pegou, sacudimos sobre vós. Sabei, contudo, isto, que já o reino de Deus é chegado a vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '12', 'E digo-vos que mais tolerância haverá naquele dia para Sodoma do que para aquela cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '13', 'Ai de ti Corazim, ai de ti, Betsaida que, se em Tiro e em Sidon se fizessem as maravilhas que em vós foram feitas já há muito, assentadas em saco e cinza, se teriam arrependido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '14', 'Portanto, para Tiro e Sidon haverá menos rigor no juízo, do que para vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '15', 'E tu, Cafarnaum, serás levantada até ao céu? Até ao inferno serás abatida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '16', 'Quem vos ouve a vós, a mim me ouve; e, quem vos rejeita a vós, a mim me rejeita; e, quem a mim me rejeita, rejeita aquele que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '17', 'E voltaram os setenta com alegria, dizendo: Senhor, pelo teu nome, até os demónios se nos sujeitam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '18', 'E disse-lhes: Eu via Satanás, como raio, cair do céu,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '19', 'Eis que vos dou poder para pisar serpentes e escorpiões, e toda a força do inimigo, e nada vos fará dano algum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '20', 'Mas não vos alegreis por que se vos sujeitem os espíritos; alegrai-vos ainda por estarem os vossos nomes escritos nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '21', 'Naquela mesma hora se alegrou Jesus no Espírito Santo, e disse: Graças te dou, ó Pai, Senhor do céu e da terra, que escondestes estas coisas aos sábios e inteligentes, e as revelaste às criancinhas; assim é, ó Pai, porque assim te aprouve.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '22', 'Tudo por meu Pai me foi entregue; e ninguém conhece quem é o Filho, senão o Pai, nem quem é o Pai senão o Filho, e aquele a quem o Filho o quiser revelar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '23', 'E, voltando-se para os discípulos, disse-lhes em particular: Bem-aventurados os olhos que vêem o que vós vedes;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '24', 'Pois vos digo que muitos profetas e reis desejaram ver o que vós vedes, e não o viram; e ouvir o que ouvis, e não o ouviram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '25', 'E eis que se levantou um certo doutor da lei, tentando-o, e dizendo: Mestre, que farei para herdar a vida eterna?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '26', 'E ele lhe disse: Que está escrito na lei? Como lês?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '27', 'E, respondendo ele, disse: Amarás ao SENHOR teu Deus de todo o teu coração, e de toda a tua alma, e de todas as tuas forças, e de todo o teu entendimento, e ao teu próximo como a ti mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '28', 'E disse-lhe: Respondeste bem; faz isso, e viverás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '29', 'Ele, porém, querendo justificar-se a si mesmo, disse a Jesus: E quem é o meu próximo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '30', 'E, respondendo Jesus, disse: Descia um homem de Jerusalém para Jericó, e caiu nas mãos dos salteadores, os quais o despojaram, e, espancando-o, se retiraram, deixando-o meio morto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '31', 'E ocasionalmente descia pelo mesmo caminho certo sacerdote; e, vendo-o, passou de largo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '32', 'E de igual modo também um levita, chegando àquele lugar, e vendo-o, passou de largo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '33', 'Mas um samaritano, que ia de viagem, chegou ao pé dele, e, vendo-o, moveu-se de íntima compaixão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '34', 'E, aproximando-se, atou-lhe as feridas, deitando-lhes azeite e vinho; e, pondo-o sobre a sua cavalgadura, levou-o para uma estalagem, e cuidou dele;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '35', 'E, partindo ao outro dia, tirou dois dinheiros, e deu-os ao hospedeiro, e disse-lhe: Cuida dele; e tudo o que de mais gastares eu to pagarei quando voltar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '36', 'Qual, pois, destes três te parece que foi o próximo daquele que caiu nas mãos dos salteadores?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '37', 'E ele disse: O que usou de misericórdia para com ele. Disse, pois, Jesus: Vai, e faz da mesma maneira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '38', 'E aconteceu que, indo eles de caminho, entrou numa aldeia; e certa mulher, por nome Marta, o recebeu em sua casa;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '39', 'E tinha esta uma irmã chamada Maria, a qual, assentando-se também aos pés de Jesus, ouvia a sua palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '40', 'Marta, porém, andava distraída em muitos serviços, e, aproximando-se, disse: Senhor, não se te dá de que a minha irmã me deixe servir só? Diz-lhe que me ajude.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '41', 'E, respondendo Jesus, disse-lhe: Marta, Marta, estás ansiosa e afadigada com muitas coisas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '10', '42', 'Mas uma só é necessária; Maria escolheu a boa parte, a qual não lhe será tirada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '1', 'E ACONTECEU que, estando ele a orar num certo lugar, quando acabou, lhe disse um dos seus discípulos: Senhor, ensina-nos a orar, como também João ensinou aos seus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '2', 'E ele lhes disse: Quando orardes, dizei: Pai, santificado seja o teu nome; venha o teu reino;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '3', 'Dá-nos cada dia o nosso pão quotidiano;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '4', 'E perdoa-nos os nossos pecados, pois também nós perdoamos a qualquer que nos deve; e não nos conduzas em tentação, mas livra-nos do mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '5', 'Disse-lhes também: Qual de vós terá um amigo, e, se for procurá-lo à meia noite, e lhe disser: Amigo, empresta-me três pães,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '6', 'Pois que um amigo meu chegou a minha casa, vindo de caminho, e não tenho que apresentar-lhe;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '7', 'Se ele, respondendo de dentro, disser: Não me importunes; já está a porta fechada, e os meus filhos estão comigo na cama; não posso levantar-me para tos dar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '8', 'Digo-vos que, ainda que se não levante a dar-lhos, por ser seu amigo, levantar-se-á, todavia, por causa da sua importunação, e lhe dará tudo o que houver mister.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '9', 'E eu vos digo a vós: Pedi, e dar-se-vos-á; buscai, e achareis; batei, e abrir-se-vos-á;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '10', 'Porque qualquer que pede recebe; e quem busca acha; e a quem bate abrir-se-lhe-á.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '11', 'E qual o pai de entre vós que, se o filho lhe pedir pão, lhe dará uma pedra? Ou também, se lhe pedir peixe, lhe dará por peixe uma serpente?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '12', 'Ou também, se lhe pedir um ovo, lhe dará um escorpião?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '13', 'Pois se, sendo maus, sabeis dar boas dádivas aos vossos filhos, quanto mais dará o Pai celestial o Espírito Santo àqueles que lho pedirem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '14', 'E estava ele expulsando um demónio, o qual era mudo. E aconteceu que, saindo o demónio, o mudo falou; e maravilhou-se a multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '15', 'Mas alguns deles diziam: Ele expulsa os demónios por Belzebu, príncipe dos demónios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '16', 'E outros, tentando-o, pediam-lhe um sinal do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '17', 'Mas conhecendo ele os seus pensamentos, disse-lhes: Todo o reino, dividido contra si mesmo será assolado; e a casa, dividida contra si mesma, cairá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '18', 'E, se também Satanás está dividido contra si mesmo, como subsistirá o seu reino? Pois dizeis que eu expulso os demónios por Belzebu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '19', 'E, se eu expulso os demónios por Belzebu, por quem os expulsam vossos filhos? Eles pois serão os vossos juízes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '20', 'Mas, se eu expulso os demónios. pelo dedo de Deus, certamente a vós é chegado o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '21', 'Quando o valente guarda, armado, a sua casa, em segurança está tudo quanto tem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '22', 'Mas, sobrevindo outro mais valente do que ele, e vencendo-o, tira-lhe toda a sua armadura em que confiava, e reparte os seus despojos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '23', 'Quem não é comigo é contra mim; e quem comigo não ajunta espalha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '24', 'Quando o espirito imundo tem saído do homem, anda por lugares secos, buscando repouso; e, não o achando, diz: Tornarei para minha casa, donde saí.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '25', 'E, chegando, acha-a varrida e adornada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '26', 'Então vai, e leva consigo outros sete espíritos piores do que ele, e, entrando, habitam ali; e o último estado desse homem é pior do que o primeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '27', 'E aconteceu que, dizendo ele estas coisas, uma mulher de entre a multidão, levantando a voz, disse: Bem-aventurado o ventre que te trouxe e os peitos em que mamaste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '28', 'Mas ele disse: Antes bem-aventurados os que ouvem a palavra de Deus e a guardam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '29', 'E, ajuntando-se a multidão, começou a dizer: Maligna é esta geração; ela pede um sinal; e não lhe será dado outro sinal, senão o sinal do profeta Jonas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '30', 'Porquanto, assim como Jonas foi sinal para os ninivitas, assim o Filho do homem o será também para esta geração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '31', 'A rainha do sul se levantará no juízo com os homens desta geração, e os condenará; pois até dos confins da terra veio ouvir a sabedoria de Salomão; e eis aqui está quem é maior do que Salomão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '32', 'Os homens de Nínive se levantarão no juízo com esta geração, e a condenarão; pois se converteram com a pregação de Jonas; e eis aqui está quem é maior do que Jonas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '33', 'E ninguém, acendendo uma candeia, a põe em oculto, nem debaixo do alqueire, mas no velador, para que os que entram vejam a luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '34', 'A candeia do corpo é o olho. Sendo pois o teu olho simples, também todo o teu corpo será luminoso; mas, se for mau, também o teu corpo será tenebroso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '35', 'Vê pois que a luz que em ti há não sejam trevas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '36', 'Se, pois, todo o teu corpo é luminoso, não tendo em trevas parte alguma, todo será luminoso, como quando a candeia te alumia com o seu resplendor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '37', 'E, estando ele ainda falando, rogou-lhe um fariseu que fosse jantar com ele; e, entrando, assentou-se à mesa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '38', 'Mas o fariseu admirou-se, vendo que se não lavara antes de jantar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '39', 'E o SENHOR lhe disse: Agora vós, os fariseus, limpais o exterior do copo e do prato; mas o vosso interior está cheio de rapina e maldade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '40', 'Loucos! O que fez o exterior não fez também o interior?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '41', 'Dai antes esmola do que tiverdes, e eis que tudo vos será limpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '42', 'Mas ai de vós, fariseus, que dizimais a hortelã, e a arruda, e toda a hortaliça, e desprezais o juízo e o amor de Deus. Importava fazer estas coisas; e não deixar as outras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '43', 'Ai de vós, fariseus, que amais os primeiros assentos nas sinagogas, e as saudações nas praças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '44', 'Ai de vós, escribas e fariseus hipócritas, que sois como as sepulturas que não aparecem, e os homens que sobre elas andam não o sabem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '45', 'E, respondendo um dos doutores da lei, disse-lhe: Mestre, quando dizes isso, também nos afrontas a nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '46', 'E ele lhe disse: Ai de vós também, doutores da lei, que carregais os homens com cargas difíceis de transportar, e vós mesmos nem ainda com um dos vossos dedos tocais essas cargas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '47', 'Ai de vós que edificais os sepulcros dos profetas, e vossos pais os mataram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '48', 'Bem testificais, pois, que consentis nas obras de vossos pais; porque eles os mataram, e vós edificais os seus sepulcros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '49', 'Por isso diz também a sabedoria de Deus: Profetas e apóstolos lhes mandarei; e eles matarão uns, e perseguirão outros;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '50', 'Para que desta geração seja requerido o sangue de todos os profetas que, desde a fundação do mundo, foi derramado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '51', 'Desde o sangue de Abel, até ao sangue de Zacarias, que foi morto entre o altar e o templo; assim, vos digo, será requerido desta geração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '52', 'Ai de vós, doutores da lei, que tirastes a chave da ciência; vós mesmos não entrastes, e impedistes os que entraram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '53', 'E, dizendo-lhes ele isto, começaram os escribas e os fariseus a apertá-lo fortemente, e a fazê-lo falar acerca de muitas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '11', '54', 'Armando-lhe ciladas, a fim de apanharem da sua boca alguma coisa para o acusarem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '1', 'AJUNTANDO-SE entretanto muitos milhares de pessoas, de sorte que se atropelavam uns aos outros, começou a dizer aos seus discípulos: Acautelai-vos primeiramente do fermento dos fariseus, que é a hipocrisia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '2', 'Mas nada há encoberto que não haja de ser descoberto; nem oculto, que não haja de ser sabido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '3', 'Porquanto tudo o que em trevas dissestes à luz será ouvido; e o que falastes ao ouvido no gabinete, sobre os telhados será apregoado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '4', 'E digo-vos, amigos meus: Não temais os que matam o corpo, e depois não têm mais que fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '5', 'Mas eu vos mostrarei a quem deveis temer; temei aquele que, depois de matar, tem poder para lançar no inferno; sim, vos digo, a esse temei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '6', 'Não se vendem cinco passarinhos por dois ceitis? E nenhum deles está esquecido diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '7', 'E até os cabelos da vossa cabeça estão todos contados. Não temais pois; mais valeis vós do que muitos passarinhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '8', 'E digo-vos que todo aquele que me confessar diante dos homens também o Filho do homem o confessará diante dos anjos de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '9', 'Mas quem me negar diante dos homens, será negado diante dos anjos de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '10', 'E a todo aquele que disser uma palavra contra o Filho do homem ser-lhe-á perdoada, mas ao que blasfema contra o Espírito Santo não lhe será perdoado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '11', 'E, quando vos conduzirem às sinagogas, aos magistrados e potestades, não estejais solícitos de como ou do que haveis de responder nem do que haveis de dizer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '12', 'Porque na mesma hora vos ensinará o Espírito Santo o que vos convenha falar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '13', 'E disse-lhe um da multidão: Mestre, diz a meu irmão que reparta comigo a herança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '14', 'Mas ele lhe disse: Homem, quem me pôs a mim por juiz ou repartidor entre vós?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '15', 'E disse-lhes: Acautelai-vos e guardai-vos da avareza; porque a vida de qualquer não consiste na abundância do que possui.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '16', 'E propôs-lhes uma parábola, dizendo: A herdade de um homem rico tinha produzido com abundância;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '17', 'E arrazoava ele entre si dizendo: Que farei? Não tenho onde recolher os meus frutos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '18', 'E disse: Farei isto: Derribarei os meus celeiros, e edificarei outros maiores, e ali recolherei todas as minhas novidades e os meus bens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '19', 'E direi à minha alma: Alma, tens em depósito muitos bens para muitos anos: descansa, come, bebe, e folga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '20', 'Mas Deus lhe disse: Louco, esta noite te pedirão a tua alma; e o que tens preparado para quem será?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '21', 'Assim é aquele que para si ajunta tesouros, e não é rico para com Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '22', 'E disse aos seus discípulos: Portanto vos digo: Não estejais apreensivos pela vossa vida, sobre o que comereis, nem pelo corpo, sobre o que vestireis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '23', 'Mais é a vida do que o sustento, e o corpo mais do que o vestido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '24', 'Considerai os corvos, que nem semeiam, nem segam, nem têm dispensa nem celeiro, e Deus os aumenta; quanto mais valeis vós do que as aves.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '25', 'E qual de vós, sendo solícito pode acrescentar um côvado à sua estatura?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '26', '(Pois, se nem ainda podeis as coisas mínimas, porque estais ansiosos pelas outras?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '27', 'Considerai os lírios, como eles crescem; não trabalham, nem fiam; e digo-vos que nem ainda Salomão, em toda a sua glória, se vestiu como um deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '28', 'E, se Deus assim veste a erva que hoje está no campo e amanhã é lançada no forno, quanto mais a vós, homens de pouca fé?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '29', 'Não pergunteis, pois, que haveis de comer, ou que haveis de beber, e não andeis inquietos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '30', 'Porque as gentes do mundo buscam todas essas coisas; mas vosso Pai sabe que haveis mister delas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '31', 'Buscai antes o reino de Deus, e todas estas coisas vos serão acrescentadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '32', 'Não temas, ó pequeno rebanho, porque a vosso Pai agradou dar-vos o reino.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '33', 'Vendei o que tendes, e dai esmolas. Fazei para vós bolsas que não se envelheçam; tesouro nos céus que nunca acabe, aonde não chega ladrão e a traça não rói.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '34', 'Porque, onde estiver o vosso tesouro, ali estará também o vosso coração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '35', 'Estejam cingidos os vossos lombos, e acesas as vossas candeias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '36', 'E sede vós semelhantes aos homens que esperam o seu senhor, quando houver de voltar das bodas, para que quando vier, e bater, logo possam abrir-lhe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '37', 'Bem-aventurados aqueles servos, os quais, quando o SENHOR vier, achar vigiando! Em verdade vos digo que se cingirá, e os fará assentar à mesa, e, chegando-se, os servirá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '38', 'E, se vier na segunda vigília, e se vier na terceira vigília, e os achar assim, bem-aventurados são os tais servos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '39', 'Sabei, porém, isto: que, se o pai de família soubesse a que hora havia de vir o ladrão, vigiaria, e não deixaria minar a sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '40', 'Portanto, estai vós também apercebidos; porque virá o Filho do homem à hora que não imaginais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '41', 'E disse-lhe Pedro: Senhor, dizes essa parábola a nós, ou também a todos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '42', 'E disse o SENHOR: Qual é pois o mordomo fiel e prudente, a quem o SENHOR pôs sobre os seus servos, para lhes dar a tempo a ração?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '43', 'Bem-aventurado aquele servo a quem o senhor, quando vier, achar fazendo assim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '44', 'Em verdade vos digo que sobre todos os seus bens o porá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '45', 'Mas, se aquele servo disser em seu coração: O meu Senhor tarda em vir; e começar a espancar os criados e criadas, e a comer, e a beber, e a embriagar-se,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '46', 'Virá o SENHOR daquele servo no dia em que o não espera, e numa hora que ele não sabe, e separá-lo-á, e lhe dará a sua parte com os infiéis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '47', 'E o servo que soube a vontade do seu senhor, e não se aprontou, nem fez conforme a sua vontade, será castigado com muitos açoites;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '48', 'Mas o que a não soube, e fez coisas dignas de açoites, com poucos açoites será castigado. E, a qualquer que muito for dado, muito se lhe pedirá, e ao que muito se lhe confiou muito mais se lhe pedirá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '49', 'Vim lançar fogo na terra; e que mais quero, se já está aceso?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '50', 'Importa, porém, que seja baptizado com um certo baptismo; e como me angustio até que venha a cumprir-se!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '51', 'Cuidais vós que vim trazer paz à terra? Não, vos digo, mas antes dissensão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '52', 'Porque daqui em adiante estarão cinco divididos numa casa: três contra dois, e dois contra três.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '53', 'O pai estará dividido contra o filho, e o filho contra o pai; a mãe contra a filha, e a filha contra a mãe; a sogra contra sua nora, e a nora contra sua sogra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '54', 'E dizia também à multidão: Quando vedes a nuvem que vem do ocidente, logo dizeis: Lá vem chuva, e assim sucede.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '55', 'E, quando assopra o sul, dizeis: Haverá calma; e assim sucede.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '56', 'Hipócritas, sabeis discernir a face da terra e do céu; como não sabeis então discernir este tempo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '57', 'E porque não julgais também por vós mesmos o que é justo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '58', 'Quando pois vais com o teu adversário ao magistrado, procura livrar-te dele no caminho; para que não suceda que te conduza ao juiz, e o juiz te entregue ao meirinho, e o meirinho te encerre na prisão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '12', '59', 'Digo-te que não sairás dali enquanto não pagares o derradeiro ceitil.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '1', 'E NAQUELE mesmo tempo estavam presentes ali alguns que lhe falavam dos galileus cujo sangue Pilatos misturara com os seus sacrifícios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '2', 'E, respondendo Jesus, disse-lhes: Cuidais vós que esses galileus foram mais pecadores do que todos os galileus, por terem padecido tais coisas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '3', 'Não, vos digo antes, se vos não arrependerdes, todos de igual modo perecereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '4', 'E aqueles dezoito, sobre os quais caiu a torre de Siloé e os matou, cuidais que foram mais culpados do que todos quantos homens habitam em Jerusalém?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '5', 'Não, vos digo antes, se vos não arrependerdes, todos de igual modo perecereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '6', 'E dizia esta parábola: Um certo homem tinha uma figueira plantada na sua vinha, e foi procurar nela fruto, não o achando;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '7', 'E disse ao vinhateiro: Eis que há três anos venho procurar fruto nesta figueira, e não o acho; corta-a; porque ocupa ainda a terra inutilmente?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '8', 'E, respondendo ele, disse-lhe: Senhor, deixa-a este ano, até que eu a escave e a esterque;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '9', 'E, se der fruto, ficará, e, se não, depois a mandarás cortar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '10', 'E ensinava no sábado, numa das sinagogas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '11', 'E eis que estava ali uma mulher que tinha um espírito de enfermidade, havia já dezoito anos; e andava curvada, e não podia de modo algum endireitar-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '12', 'E, vendo-a Jesus, chamou-a a si, e disse-lhe: Mulher estás livre da tua enfermidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '13', 'E pôs as mãos sobre ela, e logo se endireitou, e glorificava a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '14', 'E, tomando a palavra o príncipe da sinagoga, indignado porque Jesus curava no sábado, disse à multidão: Seis dias há em que é mister trabalhar; nestes pois vinde para serdes curados, e não no dia de sábado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '15', 'Respondeu-lhe, porém, o SENHOR, e disse: Hipócrita, no sábado não desprende da manjedoura cada um de vós o seu boi, ou jumento, e não o leva a beber?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '16', 'E não convinha soltar desta prisão, no dia de sábado, esta filha de Abraão, a qual há dezoito anos Satanás tinha presa?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '17', 'E, dizendo ele isto, todos os seus adversários ficaram envergonhados, e todo o povo se alegrava por todas as coisas gloriosas que eram feitas por ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '18', 'E dizia: A que é semelhante o reino de Deus, e a que o compararei?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '19', 'É semelhante ao grão de mostarda que um homem, tomando-o, lançou na sua horta; e cresceu, e fez-se grande árvore, e em seus ramos se aninharam as aves do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '20', 'E disse outra vez: A que compararei o reino de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '21', 'É semelhante ao fermento que uma mulher, tomando-o, escondeu em três medidas de farinha, até que tudo levedou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '22', 'E percorria as cidades e as aldeias, ensinando, e caminhando para Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '23', 'E disse-lhe um: Senhor, são poucos os que se salvam? E ele lhe respondeu:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '24', 'Porfiai por entrar pela porta estreita; porque eu vos digo que muitos procurarão entrar e não poderão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '25', 'Quando o pai de família se levantar e cerrar a porta, e começardes a estar de fora, e a bater à porta, dizendo: Senhor, Senhor, abre-nos; e, respondendo ele, vos disser: Não sei de onde vós sois;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '26', 'Então começareis a dizer: Temos comido e bebido na tua presença, e tu tens ensinado nas nossas ruas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '27', 'E ele vos responderá: Digo-vos que não sei donde vós sois; apartai-vos de mim, vós todos os que praticais a iniquidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '28', 'Ali haverá choro e ranger de dentes, quando virdes Abraão, e Isaque, e Jacob, e todos os profetas no reino de Deus, e vós lançados fora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '29', 'E virão do oriente, e do ocidente, e do norte, e do sul, e assentar-se-ão à mesa no reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '30', 'E eis que derradeiros há que serão os primeiros; e primeiros há que serão os derradeiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '31', 'Naquele mesmo dia chegaram uns fariseus, dizendo-lhe: Sai, e retira-te daqui, porque Herodes quer matar-te.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '32', 'E lhes respondeu: Ide, e dizei àquela raposa: Eis que eu expulso demónios, e efectuo curas, hoje e amanhã, e no terceiro dia sou consumado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '33', 'Importa, porém, caminhar hoje, amanhã, e no dia seguinte, para que não suceda que morra um profeta fora de Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '34', 'Jerusalém, Jerusalém, que matas os profetas, e apedrejas os que te são enviados! Quantas vezes quis eu ajuntar os teus filhos, como a galinha os seus pintos debaixo das asas, e não quiseste?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '13', '35', 'Eis que a vossa casa se vos deixará deserta. E em verdade vos digo que não me vereis até que venha o tempo em que digais: Bendito aquele que vem em nome do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '1', 'ACONTECEU num sábado que, entrando ele em casa de um dos principais dos fariseus para comer pão, eles o estavam observando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '2', 'E eis que estava ali diante dele um certo homem hidrópico.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '3', 'E Jesus, tomando a palavra, falou aos doutores da lei, e aos fariseus, dizendo: É lícito curar no sábado?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '4', 'Eles, porém, calaram-se. E, tomando-o, o curou e despediu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '5', 'E disse-lhes: Qual será de vós o que, caindo-lhe num poço, em dia de sábado, o jumento ou o boi, o não tire logo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '6', 'E nada lhe podiam replicar sobre isto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '7', 'E disse aos convidados uma parábola, reparando como escolhiam os primeiros assentos, dizendo-lhes:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '8', 'Quando por alguém fores convidado às bodas, não te assentes no primeiro lugar, não aconteça que esteja convidado outro mais digno do que tu;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '9', 'E, vindo o que te convidou a ti e a ele, te diga: Dá o lugar a esse, e então com vergonha, tenhas de tomar o derradeiro lugar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '10', 'Mas, quando fores convidado, vai, e assenta-te no derradeiro lugar, para que, quando vier o que te convidou, te diga: Amigo, sobe mais para cima. Então terás honra diante dos que estiverem contigo à mesa:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '11', 'Porquanto qualquer que a si mesmo se exaltar será humilhado, e aquele que a si mesmo se humilhar será exaltado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '12', 'E dizia também ao que o tinha convidado: Quando deres um jantar, ou uma ceia, não chames os teus amigos, nem teus irmãos, nem os teus parentes nem vizinhos ricos, para que não suceda que também eles te tornem a convidar, e te seja isso recompensado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '13', 'Mas, quando fizeres convite, chama os pobres, aleijados, mancos e cegos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '14', 'E serás bem-aventurado; porque eles não têm com que to recompensar; mas recompensado te será na ressurreição dos justos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '15', 'E, ouvindo isto um dos que estavam com ele à mesa, disse-lhe: Bem-aventurado o que comer pão no reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '16', 'Porém ele lhe disse: Um certo homem fez uma grande ceia, e convidou a muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '17', 'E à hora da ceia mandou o seu servo dizer aos convidados: Vinde, que já tudo está preparado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '18', 'E todos à uma começaram a escusar-se. Disse-lhe o primeiro: Comprei um campo, e importa ir vê-lo; rogo-te que me hajas por escusado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '19', 'E outro disse: Comprei cinco juntas de bois, e vou experimentá-los; rogo-te que me hajas por escusado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '20', 'E outro disse: Casei, e portanto não posso ir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '21', 'E voltando aquele servo, anunciou estas coisas ao seu senhor. Então o pai de família, indignado, disse ao seu servo: Sai depressa pelas ruas e bairros da cidade, e traz aqui os pobres, e aleijados, e mancos e cegos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '22', 'E disse o servo: Senhor, feito está como mandaste; e ainda há lugar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '23', 'E disse o SENHOR ao servo: Sai pelos caminhos e valados, e força-os a entrar, para que a minha casa se encha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '24', 'Porque eu vos digo que nenhum daqueles varões que foram convidados provará a minha ceia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '25', 'Ora ia com ele uma grande multidão; e, voltando-se, disse-lhe:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '26', 'Se alguém vier a mim, e não aborrecer a seu pai, e mãe, e mulher, e filhos, e irmãos, e irmãs, e ainda também a sua própria vida, não pode ser meu discípulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '27', 'E qualquer que não levar a sua cruz, e não vier após mim, não pode ser meu discípulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '28', 'Pois qual de vós querendo edificar uma torre, não se assenta primeiro a fazer as contas dos gastos, para ver se tem com que a acabar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '29', 'Para que não aconteça que, depois de haver posto os alicerces, e não a podendo acabar, todos os que a virem comecem a escarnecer dele,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '30', 'Dizendo: Este homem começou a edificar e não pôde acabar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '31', 'Ou qual é o rei que, indo a guerra à pelejar contra outro rei, não se assenta primeiro a tomar conselho sobre se com dez mil pode sair ao encontro do que vem contra ele com vinte mil?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '32', 'Doutra maneira, estando o outro ainda longe, manda embaixadores, e pede condições de paz;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '33', 'Assim, pois, qualquer de vós, que não renunciar a tudo quanto tem, não pode ser meu discípulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '34', 'Bom é o sal; mas, se o sal degenerar, com que se adubará?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '14', '35', 'Nem presta para a terra, nem para o monturo; lançam-no fora. Quem tem ouvidos para ouvir, ouça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '1', 'E CHEGAVAM-SE a ele todos os publicanos e pecadores para o ouvir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '2', 'E os fariseus e os escribas murmuravam, dizendo: Este recebe pecadores, e come com eles,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '3', 'E ele lhes propôs esta parábola, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '4', 'Que homem de entre vós, tendo cem ovelhas, e perdendo uma delas, não deixa no deserto as noventa e nove, e não vai após a perdida até que venha a achá-la?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '5', 'E, achando-a, a põe sobre os seus ombros, gostoso;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '6', 'E, chegando a casa, convoca os amigos e vizinhos, dizendo-lhes: Alegrai-vos comigo, porque já achei a minha ovelha perdida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '7', 'Digo-vos que assim haverá mais alegria no céu por um pecador que se arrepende, do que por noventa e nove justos que não necessitam de arrependimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '8', 'Ou qual a mulher que, tendo dez dracmas, se perder uma dracma, não acende a candeia, e varre a casa, e busca com diligência até a achar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '9', 'E, achando-a, convoca as amigas e vizinhas, dizendo: Alegrai-vos comigo, porque já achei a dracma perdida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '10', 'Assim vos digo, que há alegria diante dos anjos de Deus por um pecador que se arrepende.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '11', 'E disse: Um certo homem tinha dois filhos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '12', 'E o mais moço deles disse ao pai: Pai, dá-me a parte da fazenda que me pertence. E ele repartiu por eles a fazenda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '13', 'E, poucos dias depois, o filho mais novo, ajuntando tudo, partiu para uma terra longínqua, e ali desperdiçou a sua fazenda, vivendo dissolutamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '14', 'E, havendo ele gastado tudo, houve naquela terra uma grande fome, e começou a padecer necessidades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '15', 'E foi, e chegou-se a um dos cidadãos daquela terra, o qual o mandou para os seus campos a apascentar porcos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '16', 'E desejava encher o seu estômago com as bolotas que os porcos comiam, e ninguém lhe dava nada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '17', 'E, tornando em si, disse: Quantos jornaleiros de meu pai têm abundância de pão, e eu aqui pereço de fome!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '18', 'Levantar-me-ei, e irei ter com meu pai, e dir-lhe-ei: Pai, pequei contra o céu e perante ti;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '19', 'Já não sou digno de ser chamado teu filho; faz-me como um dos teus jornaleiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '20', 'E, levantando-se, foi para seu pai; e, quando ainda estava longe, viu-o seu pai, e se moveu de íntima compaixão, e, correndo, lançou-se-lhe ao pescoço e o beijou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '21', 'E o filho lhe disse: Pai, pequei contra o céu e perante ti, e já não sou digno de ser chamado teu filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '22', 'Mas o pai disse aos seus servos: Trazei depressa o melhor vestido, e vesti-lho, e ponde-lhe um anel na mão, e alparcas nos pés;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '23', 'E trazei o bezerro cevado, e matai-o; e comamos, e alegremo-nos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '24', 'Porque este meu filho estava morto, e reviveu, tinha-se perdido, e foi achado. E começaram a alegrar-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '25', 'E o seu filho mais velho estava no campo; e quando veio, e chegou perto de casa, ouviu a música e as danças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '26', 'E, chamando um dos servos, perguntou-lhe que era aquilo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '27', 'E ele lhe disse: Veio teu irmão; e teu pai matou o bezerro cevado, porque o recebeu são e salvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '28', 'Mas ele se indignou, e não queria entrar. E, saindo o pai, instava com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '29', 'Mas, respondendo ele, disse ao pai: Eis que te sirvo há tantos anos, sem nunca transgredir o teu mandamento, e nunca me deste um cabrito para alegrar-me com os meus amigos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '30', 'Vindo, porém, este teu filho, que desperdiçou a tua fazenda com as meretrizes, mataste-lhe o bezerro cevado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '31', 'E ele lhe disse: Filho, tu sempre estás comigo, e todas as minhas coisas são tuas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '15', '32', 'Mas era justo alegrarmo-nos e folgarmos, porque este teu irmão estava morto, e reviveu; e tinha-se perdido, e achou-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '1', 'E DIZIA também aos seus discípulos: Havia um certo homem rico, o qual tinha um mordomo; e este foi acusado perante ele de dissipar os seus bens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '2', 'E ele, chamando-o, disse-lhe: Que é isto que ouço de ti? Dá contas da tua mordomia, porque já não poderás ser mais meu mordomo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '3', 'E o mordomo disse consigo: Que farei, pois que o meu Senhor me tira a mordomia? Cavar, não posso; de mendigar, tenho vergonha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '4', 'Eu sei o que hei-de fazer, para que, quando for desapossado da mordomia, me recebam em suas casas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '5', 'E, chamando a si cada um dos devedores do seu senhor, disse ao primeiro: Quanto deves ao meu senhor?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '6', 'E ele respondeu: Cem medidas de azeite. E disse-lhe: Toma a tua obrigação, e, assentando-te já, escreve cinquenta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '7', 'Disse depois a outro: E tu quanto deves? E ele respondeu: Cem alqueires de trigo. E disse-lhe: Toma a tua obrigação, e escreve oitenta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '8', 'E louvou aquele Senhor o injusto mordomo prudentemente, porque os filhos deste mundo são mais prudentes na sua geração do que os filhos da luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '9', 'E eu vos digo: Granjeai amigos com as riquezas da injustiça; para que, quando estas vos faltarem, vos recebam eles nos tabernáculos eternos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '10', 'Quem é fiel no mínimo, também é fiel no muito; quem é injusto no mínimo, também é injusto no muito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '11', 'Pois, se nas riquezas injustas não fostes fiéis, quem vos confiará as verdadeiras?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '12', 'E, se no alheio não fostes fiéis, quem vos dará o que é vosso?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '13', 'Nenhum servo pode servir dois senhores; porque, ou há-de aborrecer um e amar o outro, ou se há-de chegar a um e desprezar o outro. Não podeis servir a Deus e a Mamom.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '14', 'E os fariseus, que eram avarentos, ouviam todas estas coisas, e zombavam dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '15', 'E disse-lhes: Vós sois os que vos justificais a vós mesmos diante dos homens, mas Deus conhece os vossos corações, porque, o que entre os homens é elevado, perante Deus é abominação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '16', 'A lei e os profetas duraram até João; desde então é anunciado o reino de Deus, e todo o homem emprega força para entrar nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '17', 'E é mais fácil passar o céu e a terra do que cair um til da lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '18', 'Qualquer que deixa sua mulher, e casa com outra, adultera; e, aquele que casa com a repudiada pelo marido adultera também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '19', 'Ora, havia um homem rico, que se vestia de púrpura e de linho finíssimo, e vivia todos os dias regalada e esplendidamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '20', 'Havia também um certo mendigo, chamado Lázaro, que jazia cheio de chagas à porta daquele;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '21', 'E desejava alimentar-se com as migalhas que caíam da mesa do rico; e os próprios cães vinham lamber-lhe as chagas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '22', 'E aconteceu que o mendigo morreu, e foi levado pelos anjos, para o seio de Abraão; e morreu também o rico e foi sepultado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '23', 'E no Hades, ergueu os olhos, estando em tormentos, e viu ao longe Abraão, e Lázaro no seu seio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '24', 'E, clamando, disse: Pai Abraão, tem misericórdia de mim, e manda a Lázaro, que molhe na água a ponta do seu dedo e me refresque a língua, porque estou atormentado nesta chama.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '25', 'Disse, porém, Abraão: Filho, lembra-te de que recebeste os teus bens em tua vida, e Lázaro somente males; e agora este é consolado e tu atormentado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '26', 'E, além disso, está posto um grande abismo entre nós e vós, de sorte que os que quisessem passar daqui para vós não poderiam, nem tampouco os de lá passar para cá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '27', 'E disse ele: Rogo-te pois, ó pai, que o mandes a casa de meu pai,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '28', 'Pois tenho cinco irmãos; para que lhes dê testemunho, a fim de que não venham também para este lugar de tormento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '29', 'Disse-lhe Abraão: Têm Moisés e os profetas: ouçam-nos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '30', 'E disse ele: Não, pai Abraão; mas, se algum dos mortos fosse ter com eles arrepender-se-iam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '16', '31', 'Porém Abraão lhe disse: Se não ouvem a Moisés e aos profetas, tão pouco acreditarão, ainda que algum dos mortos ressuscite.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '1', 'E DISSE aos discípulos: É impossível que não venham escândalos, mas ai daquele por quem vierem!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '2', 'Melhor lhe fora que lhe pusessem ao pescoço uma mó de atafona, e fosse lançado ao mar, do que fazer tropeçar um destes pequenos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '3', 'Olhai por vós mesmos. E, se teu irmão pecar contra ti, repreende-o, e, se ele se arrepender, perdoa-lhe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '4', 'E, se pecar contra ti sete vezes no dia, e sete vezes no dia vier ter contigo, dizendo: Arrependo-me; perdoa-lhe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '5', 'Disseram então os apóstolos ao SENHOR: Acrescenta-nos a fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '6', 'E disse o SENHOR: Se tivésseis fé como um grão de mostarda, diríeis a esta amoreira: Desarraiga-te daqui, e planta-te no mar; e ela vos obedeceria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '7', 'E qual de vós terá um servo a lavrar ou a apascentar gado, a quem voltando ele do campo, diga: Chega-te, e assenta-te à mesa?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '8', 'E não lhe diga antes: Prepara-me a ceia, e cinge-te, e serve-me, até que tenha comido e bebido, e depois comerás e beberás tu?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '9', 'Porventura dá graças ao tal servo porque fez o que lhe foi mandado? Creio que não.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '10', 'Assim também vós quando fizerdes tudo o que vos for mandado, dizei: Somos servos inúteis, porque fizemos somente o que devíamos fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '11', 'E aconteceu que, indo ele a Jerusalém, passou pelo meio da Samaria e da Galileia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '12', 'E, entrando numa certa aldeia, saíram-lhe ao encontro dez homens leprosos, os quais pararam de longe,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '13', 'E levantaram a voz, dizendo: Jesus, Mestre, tem misericórdia de nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '14', 'E ele, vendo-os, disse-lhes: Ide, e mostrai-vos aos sacerdotes. E aconteceu que, indo eles, ficaram limpos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '15', 'E um deles, vendo que estava são, voltou glorificando a Deus em alta voz;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '16', 'E caiu aos seus pés, com o rosto em terra, dando-lhe graças; e este era samaritano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '17', 'E, respondendo Jesus, disse: Não foram dez os limpos? E onde estão os nove?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '18', 'Não houve quem voltasse para dar glória a Deus senão este estrangeiro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '19', 'E disse-lhe: Levanta-te, e vai; a tua fé te salvou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '20', 'E, interrogado pelos fariseus sobre quando havia de vir o reino de Deus, respondeu-lhes, e disse: O reino de Deus não vem com aparência exterior.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '21', 'Nem dirão: Ei-lo aqui, ou: Ei-lo ali; porque eis que o reino de Deus está entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '22', 'E disse aos discípulos: Dias virão em que desejareis ver um dos dias do Filho do homem, e não o vereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '23', 'E dir-vos-ão: Ei-lo aqui, ou: Ei-lo ali; não vades, nem os sigais;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '24', 'Porque, como o relâmpago ilumina desde uma extremidade inferior do céu até á outra extremidade, assim será também o Filho do homem no seu dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '25', 'Mas primeiro convém que ele padeça muito, e seja reprovado por esta geração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '26', 'E, como aconteceu nos dias de Noé, assim será também nos dias do Filho do homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '27', 'Comiam, bebiam, casavam e davam-se em casamento, até ao dia em que Noé entrou na arca, e veio o dilúvio, e os consumiu a todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '28', 'Como também da mesma maneira aconteceu nos dias de Lot: comiam bebiam, compravam, vendiam, plantavam e edificavam;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '29', 'Mas no dia em que Lot saiu de Sodoma choveu do céu fogo e enxofre, e os consumiu a todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '30', 'Assim será no dia em que o Filho do homem se há-de manifestar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '31', 'Naquele dia, quem estiver no telhado, tendo as suas alfaias em casa, não desça a tomá-las; e, da mesma sorte, o que estiver no campo não volte para trás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '32', 'Lembrai-vos da mulher de Lot.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '33', 'Qualquer que procurar salvar a sua vida, perdê-la-á, e qualquer que a perder, salvá-la-á.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '34', 'Digo-vos que naquela noite estarão dois numa cama; um será tomado, e outro será deixado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '35', 'Duas estarão juntas, moendo; uma será tomada, e outra será deixada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '36', 'Dois estarão no campo; um será tomado, o outro será deixado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '17', '37', 'E, respondendo, disseram-lhe: Onde, Senhor? E ele lhes disse: Onde estiver o corpo, aí se ajuntarão as águias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '1', 'E CONTOU-LHES também uma parábola sobre o dever de orar sempre, e nunca desfalecer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '2', 'Dizendo: Havia numa cidade um certo juiz, que nem a Deus temia nem respeitava o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '3', 'Havia também naquela cidade uma certa viúva, e ia ter com ele, dizendo: Faz-me justiça contra o meu adversário.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '4', 'E por algum tempo não quis; mas depois disse consigo : Ainda que não temo a Deus, nem respeito os homens,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '5', 'Todavia, como esta viúva me molesta, hei-de fazer-lhe justiça, para que enfim não volte, e me importune muito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '6', 'E disse o SENHOR: Ouvi o que diz o injusto juiz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '7', 'E Deus não fará justiça aos seus escolhidos, que clamam a ele de dia e de noite, ainda que tardio para com eles?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '8', 'Digo-vos que depressa lhes fará justiça. Quando porém vier o Filho do homem, porventura achará fé na terra?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '9', 'E disse também esta parábola a uns que confiavam em si mesmos, crendo que eram justos, e desprezavam os outros:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '10', 'Dois homens subiram ao templo, a orar; um fariseu, e o outro publicano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '11', 'O fariseu, estando em pé, orava consigo desta maneira: ó Deus, graças te dou, porque não sou como os demais homens, roubadores, injustos e adúlteros; nem ainda como este publicano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '12', 'Jejuo duas vezes na semana, e dou os dízimos de tudo quanto possuo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '13', 'O publicano, porém, estando em pé, de longe, nem ainda queria levantar os olhos ao céu, mas batia no peito, dizendo: ó Deus, tem misericórdia de mim pecador!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '14', 'Digo-vos que este desceu justificado para sua casa, e não aquele; porque qualquer que a si mesmo se exalta será humilhado, e qualquer que a si mesmo se humilha será exaltado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '15', 'E traziam-lhe também meninos, para que ele lhes tocasse; e os discípulos, vendo isto, repreendiam-nos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '16', 'Mas Jesus, chamando-os para si, disse: Deixai vir a mim os meninos, e não os impeçais, porque dos tais é o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '17', 'Em verdade vos digo que, qualquer que não receber o reino de Deus como menino, não entrará nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '18', 'E perguntou-lhe um certo príncipe, dizendo: Bom Mestre, que hei-de fazer para herdar a vida eterna?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '19', 'Jesus lhe disse: Porque me chamas bom? Ninguém há bom, senão um, que é Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '20', 'Sabes os mandamentos: Não adulterarás, não matarás, não furtarás, não dirás falso testemunho, honra a teu pai e a tua mãe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '21', 'E disse ele: Todas essas coisas tenho observado desde a minha mocidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '22', 'E quando Jesus ouviu isto, disse-lhe: Ainda te falta uma coisa: vende tudo quanto tens, reparte-o pelos pobres, e terás um tesouro no céu; vem, e segue-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '23', 'Mas, ouvindo ele isto, ficou muito triste, porque era muito rico.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '24', 'E, vendo Jesus que ele ficara muito triste, disse: Quão dificilmente entrarão no reino de Deus os que têm riquezas!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '25', 'Porque é mais fácil entrar um camelo pelo fundo duma agulha do que entrar um rico no reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '26', 'E os que ouviram isto disseram: Logo quem pode salvar-se?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '27', 'Mas ele respondeu: As coisas que são impossíveis aos homens são possíveis a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '28', 'E disse Pedro: Eis que nós deixámos tudo e te seguimos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '29', 'E ele lhes disse: Na verdade vos digo que ninguém há, que tenha deixado casa, ou pais, ou irmãos, ou mulher, ou filhos, pelo reino de Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '30', 'E não haja de receber muito mais neste mundo, e na idade vindoura a vida eterna');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '31', 'E, tomando consigo os doze, disse-lhes: Eis que subimos a Jerusalém, e se cumprirá no Filho do homem tudo o que pelos profetas foi escrito;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '32', 'Pois há-de ser entregue às gentes, e escarnecido, injuriado e cuspido;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '33', 'E, havendo-o açoitado, o matarão; e ao terceiro dia ressuscitará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '34', 'E eles nada disto entendiam, e esta palavra lhes era encoberta, não percebendo o que se lhes dizia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '35', 'E aconteceu que, chegando ele perto de Jerico, estava um cego assentado junto do caminho, mendigando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '36', 'E, ouvindo passar a multidão, perguntou que era aquilo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '37', 'E disseram-lhe que Jesus Nazareno passava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '38', 'Então clamou, dizendo: Jesus, Filho de David, tem misericórdia de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '39', 'E os que iam passando repreendiam-no para que se calasse; mas ele clamava ainda mais: Filho de David, tem misericórdia de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '40', 'Então Jesus, parando, mandou que lho trouxessem; e, chegando ele, perguntou-lhe,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '41', 'Dizendo: Que queres que te faça? E ele disse: Senhor, que eu veja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '42', 'E Jesus lhe disse: Vê; a tua fé te salvou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '18', '43', 'E logo viu, e seguia-o, glorificando a Deus. E todo o povo, vendo isto, dava louvores a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '1', 'E, TENDO Jesus entrado em Jerico, ia passando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '2', 'E eis que havia ali um varão chamado Zaqueu; e era este um chefe dos publicanos, e era rico.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '3', 'E procurava ver quem era Jesus, e não podia, por causa da multidão, pois era de pequena estatura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '4', 'E, correndo adiante, subiu a uma figueira brava para o ver; porque havia de passar por ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '5', 'E, quando Jesus chegou àquele lugar, olhando para cima, viu-o e disse-lhe: Zaqueu, desce depressa, porque hoje me convém pousar em tua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '6', 'E, apressando-se desceu, e recebeu-o gostoso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '7', 'E, vendo todos isto, murmuravam, dizendo que entrara para ser hóspede de um homem pecador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '8', 'E, levantando-se Zaqueu, disse ao SENHOR: Senhor, eis que eu dou aos pobres metade dos meus bens; e, se nalguma coisa tenho defraudado alguém, o restituo quadruplicado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '9', 'E disse-lhe Jesus: Hoje veio a salvação a esta casa, pois também este é filho de Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '10', 'Porque o Filho do homem veio buscar e salvar o que se havia perdido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '11', 'E, ouvindo ele estas coisas, ele prosseguiu, e contou uma parábola; porquanto estava perto de Jerusalém, e cuidavam que logo se havia de manifestar o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '12', 'Disse pois: Certo homem nobre partiu para uma terra remota, a fim de tomar para si um reino e voltar depois.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '13', 'E, chamando dez servos seus, deu-lhes dez minas, e disse-lhes: Negociai até que eu venha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '14', 'Mas os seus concidadãos aborreciam-no, e mandaram após ele embaixadores, dizendo: Não queremos que este reine sobre nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '15', 'E aconteceu que, voltando ele, depois de ter tomado o reino, disse que lhe chamassem aqueles servos, a quem tinha dado o dinheiro, para saber o que cada um tinha ganhado, negociando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '16', 'E veio o primeiro, dizendo: Senhor, a tua mina rendeu dez minas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '17', 'E ele lhe disse: Bem está, servo bom, porque no mínimo foste fiel, sobre dez cidades terás autoridade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '18', 'E veio o segundo, dizendo: Senhor, a tua mina rendeu cinco minas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '19', 'E a este disse também: Sê tu também sobre cinco cidades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '20', 'E veio outro, dizendo: Senhor, aqui está a tua mina, que guardei num lenço;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '21', 'Porque tive medo de ti, que és homem rigoroso, que tomas o que não puseste, e segas o que não semeaste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '22', 'Porém ele lhe disse: Mau servo, pela tua boca te julgarei; sabias que eu sou homem rigoroso, que tomo o que não pus, e sego o que não semeei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '23', 'Porque não meteste pois o meu dinheiro no banco, para que eu, vindo, o exigisse com os juros?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '24', 'E disse aos que estavam com ele: Tirai-lhe a mina, e dai-a ao que tem dez minas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '25', '(E disseram-lhe eles: Senhor, ele tem dez minas).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '26', 'Pois eu vos digo que a qualquer que tiver ser-lhe-á dado, mas ao que não tiver até o que tem lhe será tirado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '27', 'E, quanto àqueles meus inimigos que não quiseram que eu reinasse sobre eles, trazei-os aqui, e matai-os diante de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '28', 'E, dito isto, ia caminhando adiante, subindo para Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '29', 'E aconteceu que, chegando perto de Betfagé, e de Betânia, ao monte chamado das Oliveiras, mandou dois dos seus discípulos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '30', 'Dizendo: Ide à aldeia que está defronte, e aí, ao entrar, achareis preso um jumentinho em que nenhum homem ainda se assentou; soltai-o e trazei-o;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '31', 'E, se alguém vos perguntar: Porque o soltais? Assim lhe direis: Porque o SENHOR o há de mister.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '32', 'E, indo os que haviam sido mandados, acharam como lhes dissera.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '33', 'E, quando soltaram o jumentinho, seus donos lhes disseram: Porque soltais o jumentinho?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '34', 'E eles responderam: O SENHOR o há de mister.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '35', 'E trouxeram-no a Jesus: e, lançando sobre o jumentinho os seus vestidos; puseram Jesus em cima.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '36', 'E, indo ele, estendiam no caminho os seus vestidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '37', 'E, quando já chegava perto da descida do monte das Oliveiras, toda a multidão dos discípulos, regozijando-se, começou a dar louvores a Deus em alta voz, por todas as maravilhas que tinha visto,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '38', 'Dizendo: Bendito o Rei que vem em nome do SENHOR; paz no céu, e glória nas alturas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '39', 'E disseram-lhe de entre a multidão alguns dos fariseus: Mestre, repreende os teus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '40', 'E, respondendo ele, disse-lhes: Digo-vos que, se estes se calarem, as próprias pedras clamarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '41', 'E, quando ia chegando, vendo a cidade, chorou sobre ela,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '42', 'Dizendo: Ah! se tu conhecesses também, ao menos neste teu dia, o que à tua paz pertence! Mas agora isto está encoberto aos teus olhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '43', 'Porque dias virão sobre ti, em que os teus inimigos te cercarão de trincheiras, e te sitiarão, e te estreitarão de todas as bandas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '44', 'E te derribarão, a ti e aos teus filhos que dentro de ti estiverem; e não deixarão em ti pedra sobre pedra, pois que não conheceste o tempo da tua visitação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '45', 'E, entrando no templo, começou a expulsar todos os que nele vendiam e compravam,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '46', 'Dizendo-lhes: Está escrito: A minha casa é casa de oração; mas vós fizestes dela covil de salteadores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '47', 'E todos os dias ensinava no templo; mas os principais dos sacerdotes, e os escribas, e os principais do povo procuravam matá-lo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '19', '48', 'E não achavam meio de o fazer, porque todo o povo pendia para ele, escutando-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '1', 'E ACONTECEU num daqueles dias que, estando ele ensinando o povo no templo, e anunciando o evangelho, sobrevieram os principais dos sacerdotes e os escribas com os anciãos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '2', 'E falaram-lhe, dizendo: Diz-nos, com que autoridade fazes estas coisas? Ou, quem é que te deu esta autoridade?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '3', 'E, respondendo ele, disse-lhes: Também eu vos farei uma pergunta dizei-me pois:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '4', 'O baptismo de João era do céu ou dos homens?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '5', 'E eles arrazoavam entre si, dizendo: Se dissermos: Do céu, ele nos dirá: Então porque o não crestes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '6', 'E se dissermos: Dos homens; todo o povo nos apedrejará, pois têm por certo que João era profeta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '7', 'E responderam que não sabiam donde era.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '8', 'E Jesus lhes disse: Tampouco vos direi com que autoridade faço isto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '9', 'E começou a dizer ao povo esta parábola: Certo homem plantou uma vinha, e arrendou-a a uns lavradores, e partiu para fora da terra por muito tempo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '10', 'E no próprio tempo mandou um servo aos lavradores, para que lhe dessem dos frutos da vinha; mas os lavradores, espancando-o, mandaram-no vazio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '11', 'E tornou ainda a mandar outro servo; mas eles, espancando também a este, e afrontando-o, mandaram-no vazio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '12', 'E tornou ainda a mandar terceiro; mas eles, ferindo também a este, o expulsaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '13', 'E disse o SENHOR da vinha: Que farei? Mandarei o meu filho amado; talvez que, vendo-o, o respeitem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '14', 'Mas, vendo-o os lavradores, arrazoaram entre si, dizendo: Este é o herdeiro; vinde, matemo-lo, para que a herança seja nossa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '15', 'E, lançando-o fora da vinha, o mataram. Que lhes fará pois o SENHOR da vinha?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '16', 'Irá, e destruirá estes lavradores, e dará a outros a vinha. E, ouvindo eles isto, disseram: Não seja assim!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '17', 'Mas ele, olhando para eles, disse: Que é isto pois que está escrito? A pedra, que os edificadores reprovaram, essa foi feita cabeça da esquina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '18', 'Qualquer que cair sobre aquela pedra ficará em pedaços, e aquele sobre quem ela cair será feito em pó.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '19', 'E os principais dos sacerdotes e os escribas procuravam lançar mão dele naquela mesma hora; mas temeram o povo: porque entenderam que contra eles dissera esta parábola.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '20', 'E, trazendo-o debaixo de olho, mandaram espias, que se fingissem justos, para o apanharem nalguma palavra e o entregarem à jurisdição e poder do presidente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '21', 'E perguntaram-lhe, dizendo: Mestre, nós sabemos que falas e ensinas bem e rectamente, e que não consideras a aparência da pessoa, mas ensinas com verdade o caminho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '22', 'É-nos lícito dar o tributo a César ou não?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '23', 'E entendendo ele a sua astúcia, disse-lhes: Porque me tentais?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '24', 'Mostrai-me uma moeda. De quem tem a imagem e a inscrição? E respondendo eles disseram: De César.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '25', 'Disse-lhes então: Dai pois a César o que é de César, e a Deus o que é de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '26', 'E não puderam apanhá-lo em palavra alguma diante do povo; e, maravilhados da sua resposta, calaram-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '27', 'E, chegando-se alguns dos saduceus, que dizem não haver ressurreição, perguntaram-lhe,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '28', 'Dizendo: Mestre, Moisés nos deixou escrito que, se o irmão dalgum falecer, tendo mulher, e não deixar filhos, o irmão dele tome a mulher, e suscite posteridade a seu irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '29', 'Houve pois sete irmãos, e o primeiro tomou mulher, e morreu sem filhos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '30', 'E o segundo e o terceiro também a tomaram,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '31', 'E igualmente os sete: todos eles morreram, e não deixaram filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '32', 'E por último, depois de todos, morreu também a mulher.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '33', 'Portanto, na ressurreição, de qual deles será a mulher, pois que os sete por mulher a tiveram?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '34', 'E, respondendo Jesus, disse-lhes: Os filhos deste mundo casam-se, e dão-se em casamento;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '35', 'Mas os que forem havidos por dignos de alcançar o mundo vindouro, e a ressurreição dos mortos, nem hão-de casar, nem ser dados em casamento;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '36', 'Porque já não podem mais morrer; pois são iguais, aos anjos, e são filhos de Deus, sendo filhos da ressurreição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '37', 'E que os mortos hão-de ressuscitar também o mostrou Moisés junto da sarça, quando chama ao SENHOR, Deus de Abraão, e Deus de Isaque, e Deus de Jacob.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '38', 'Ora Deus não é Deus de mortos, mas de vivos; porque para ele vivem todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '39', 'E, respondendo alguns dos escribas, disseram: Mestre, disseste bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '40', 'E não ousavam perguntar-lhe mais coisa alguma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '41', 'E ele lhes disse: Como dizem que o Cristo é filho de David?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '42', 'Visto como o mesmo David diz no livro dos Salmos: Disse o SENHOR ao meu Senhor: Assenta-te à minha direita,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '43', 'Até que eu ponha os teus inimigos por escabelo de teus pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '44', 'Se David lhe chama Senhor como é ele seu filho?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '45', 'E, ouvindo-o todo o povo, disse Jesus aos seus discípulos:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '46', 'Guardai-vos dos escribas, que querem andar com vestidos compridos; e amam as saudações nas praças, e as principais cadeiras nas sinagogas, e os primeiros lugares nos banquetes;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '20', '47', 'Que devoram as casas das viúvas, fazendo, por pretexto, largas orações. Estes receberão maior condenação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '1', 'E, OLHANDO ele viu os ricos lançarem as suas ofertas na arca do tesouro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '2', 'E viu também uma pobre viúva lançar ali duas pequenas moedas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '3', 'E disse: Em verdade vos digo que lançou mais do que todos, esta pobre viúva;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '4', 'Porque todos aqueles deitaram para as ofertas de Deus, do que lhes sobeja; mas esta, da sua pobreza, deitou todo o sustento que tinha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '5', 'E, dizendo alguns a respeito do templo, que estava ornado de formosas pedras e dádivas, disse:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '6', 'Quanto a estas coisas que vedes, dias virão em que se não deixará pedra sobre pedra, que não seja derribada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '7', 'E perguntaram-lhe, dizendo: Mestre, quando serão pois estas coisas? E que sinal haverá quando isto estiver para acontecer?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '8', 'Disse então ele: Vede não vos enganem, porque virão muitos em meu nome, dizendo: Sou eu, e o tempo está próximo; não vades portanto após eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '9', 'E, quando ouvirdes de guerras e sedições, não vos assusteis. Porque é necessário que isto aconteça primeiro, mas o fim não será logo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '10', 'Então lhes disse: Levantar-se-á nação contra nação, e reino contra reino;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '11', 'E haverá em vários lugares grandes terramotos, e fomes e pestilências; haverá também coisas espantosas, grandes sinais do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '12', 'Mas antes de todas estas coisas lançarão mão de vós, e vos perseguirão, entregando-vos às sinagogas e às prisões, e conduzindo-vos à presença de reis e presidentes, por amor do meu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '13', 'E vos acontecerá isto para testemunho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '14', 'Proponde pois em vossos corações não premeditar como haveis de responder;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '15', 'Porque eu vos darei boca e sabedoria a que não poderão resistir nem contradizer todos quantos se vos opuserem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '16', 'E até pelos pais, e irmãos, e parentes, e amigos sereis entregues; e matarão alguns de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '17', 'E de todos sereis odiados por causa do meu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '18', 'Mas não perecerá um único cabelo da vossa cabeça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '19', 'Na vossa paciência possuí as vossas almas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '20', 'Mas, quando virdes Jerusalém cercada de exércitos, sabei então que é chegada a sua desolação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '21', 'Então, os que estiverem na Judeia, fujam para os montes; os que estiverem no meio da cidade, saiam; e, os que nos campos, não entrem nela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '22', 'Porque dias de vingança são estes, para que se cumpram todas as coisas que estão escritas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '23', 'Mas ai das grávidas, e das que criarem naqueles dias! Porque haverá grande aperto na terra, e ira sobre este povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '24', 'E cairão ao fio da espada, e para todas as nações serão levados cativos; e Jerusalém será pisada pelos gentios, até que os tempos dos gentios se completem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '25', 'E haverá sinais no sol e na lua e nas estrelas; e na terra angústia das nações, em perplexidade pelo bramido do mar e das ondas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '26', 'Homens desmaiando de terror, na expectação das coisas que sobrevirão ao mundo. Porquanto as virtudes do céu serão abaladas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '27', 'E então verão vir o Filho do homem numa nuvem, com poder e grande glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '28', 'Ora, quando estas coisas começarem a acontecer, olhai para cima e levantai as vossas cabeças, porque a vossa redenção está próxima.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '29', 'E disse-lhes uma parábola: Olhai para a figueira, e para todas as árvores;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '30', 'Quando já têm rebentado, vós sabeis por vós mesmos, vendo-as, que perto está já o verão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '31', 'Assim também vós quando virdes acontecer estas coisas, sabei que o reino de Deus está perto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '32', 'Em verdade vos digo que não passará esta geração até que tudo aconteça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '33', 'Passará o céu e a terra, mas as minhas palavras não hão-de passar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '34', 'E olhai por vós, não aconteça que os vossos corações se carreguem de glutonaria, de embriaguez, e dos cuidados da vida, e venha sobre vós de improviso aquele dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '35', 'Porque virá como um laço sobre todos os que habitam na face de toda a terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '36', 'Vigiai pois em todo o tempo, orando, para que sejais havidos por dignos de evitar todas estas coisas que hão-de acontecer, e de estar em pé diante do Filho do homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '37', 'E de dia ensinava no templo, e à noite, saindo, ficava no monte chamado das Oliveiras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '21', '38', 'E todo o povo ia ter com ele ao templo, de manhã cedo, para o ouvir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '1', 'ESTAVA pois perto a festa dos asmos, chamada a páscoa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '2', 'E os principais dos sacerdotes, e os escribas, andavam procurando como o matariam; porque temiam o povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '3', 'Entrou, porém, Satanás em Judas, que tinha por sobrenome Iscariotes, o qual era do número dos doze;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '4', 'E foi, e falou com os principais dos sacerdotes, e com os capitães, de como lho entregaria;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '5', 'Os quais se alegraram, e convieram em lhe dar dinheiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '6', 'E ele concordou; e buscava oportunidade para lho entregar sem alvoroço.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '7', 'Chegou, porém, o dia dos asmos, em que importava sacrificar a páscoa:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '8', 'E mandou a Pedro e a João, dizendo: Ide, preparai-nos a páscoa, para que a comamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '9', 'E eles lhe perguntaram: Onde queres que a preparemos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '10', 'E ele lhes disse: Eis que, quando entrardes na cidade, encontrareis um homem, levando um cântaro de água; segui-o até à casa em que ele entrar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '11', 'E direis ao pai de família da casa: O Mestre te diz: Onde está o aposento em que hei-de comer a páscoa com os meus discípulos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '12', 'Então ele vos mostrará um grande cenáculo mobilado; aí fazei preparativos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '13', 'E, indo eles, acharam como lhes havia sido dito; e prepararam a páscoa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '14', 'E, chegada a hora, pôs-se à mesa, e com ele os doze apóstolos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '15', 'E disse-lhes: Desejei muito comer convosco esta páscoa, antes que padeça;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '16', 'Porque vos digo que não a comerei mais até que ela se cumpra no reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '17', 'E tomando o cálix, e havendo dado graças, disse: Tomai-o, e reparti-o entre vós;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '18', 'Porque vos digo que já não beberei do fruto da vide, até que venha o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '19', 'E, tomando o pão, e havendo dado graças, partiu-o, e deu-lho, dizendo: Isto é o meu corpo, que por vós é dado; fazei isto em memória de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '20', 'Semelhantemente tomou o cálix, depois da ceia, dizendo: Este cálix é o Novo Testamento no meu sangue, que é derramado por vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '21', 'Mas eis que a mão do que me trai está comigo à mesa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '22', 'E, na verdade, o Filho do homem vai segundo o que está determinado; mas ai daquele homem por quem é traído!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '23', 'E começaram a perguntar entre si qual deles seria o que havia de fazer isto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '24', 'E houve também entre eles contenda, sobre qual deles parecia ser o maior.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '25', 'E ele lhes disse: Os reis dos gentios dominam sobre eles, e os que têm autoridade sobre eles são chamados benfeitores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '26', 'Mas não sereis vós assim; antes o maior entre vós seja como o menor; e quem governa como quem serve.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '27', 'Pois qual é maior: quem está à mesa, ou quem serve? Porventura não é quem está à mesa? Eu porém, entre vós sou como aquele que serve.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '28', 'E vós sois os que tendes permanecido comigo nas minhas tentações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '29', 'E eu vos destino o reino, como meu Pai mo destinou;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '30', 'Para que comais e bebais à minha mesa no meu reino, e vos assenteis sobre tronos, julgando as doze tribos de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '31', 'Disse também o SENHOR; Simão, Simão, eis que Satanás vos pediu para vos cirandar como trigo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '32', 'Mas eu roguei por ti, para que a tua fé não desfaleça; e tu, quando te converteres, confirma teus irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '33', 'E ele lhe disse: Senhor, estou pronto a ir contigo até à prisão e à morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '34', 'Mas ele disse: Digo-te, Pedro, que não cantará hoje o galo antes que três vezes negues que me conheces.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '35', 'E disse-lhes: Quando vos mandei sem bolsa, alforge, ou alparcas, faltou-vos porventura alguma coisa? Eles responderam: Nada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '36', 'Disse-lhes pois: Mas agora, aquele que tiver bolsa, tome-a, como também alforge; e, o que não tem espada, venda o seu vestido e compre-a;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '37', 'Porquanto vos digo que importa que em mim se cumpra aquilo que está escrito: E com os malfeitores foi contado. Porque o que está escrito de mim terá cumprimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '38', 'E eles disseram: Senhor, eis aqui duas espadas. E ele lhes disse: Basta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '39', 'E, saindo, foi, como costumava, para o Monte das Oliveiras; e também os seus discípulos o seguiram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '40', 'E, quando chegou àquele lugar, disse-lhes: Orai, para que não entreis em tentação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '41', 'E apartou-se deles cerca de um tiro de pedra; e, pondo-se de joelhos, orava,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '42', 'Dizendo: Pai, se queres, passa de mim este cálix, todavia não se faça a minha vontade, mas a tua.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '43', 'E apareceu-lhe um anjo do céu, que o confortava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '44', 'E, posto em agonia, orava mais intensamente. E o seu suor tornou-se em grandes gotas de sangue, que corriam até ao chão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '45', 'E, levantando-se da oração, veio para os seus discípulos, e achou-os dormindo de tristeza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '46', 'E disse-lhes: Porque estais dormindo? Levantai-vos e orai, para que não entreis em tentação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '47', 'E, estando ele ainda a falar, surgiu uma multidão; e um dos doze, que se chamava Judas, ia adiante dela, e chegou-se a Jesus para o beijar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '48', 'E Jesus lhe disse: Judas, com um beijo trais o Filho do homem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '49', 'E, vendo os que estavam com ele o que ia suceder, disseram-lhe: Senhor, feriremos à espada?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '50', 'E um deles feriu o servo do sumo sacerdote, e cortou-lhe a orelha direita.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '51', 'E, respondendo Jesus, disse: Deixai-os; basta. E, tocando-lhe a orelha, o curou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '52', 'E disse Jesus aos principais dos sacerdotes, e capitães do templo, e anciãos, que tinham ido contra ele: Saístes, como a um salteador, com espada e varapaus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '53', 'Tenho estado todos os dias convosco no templo, e não estendestes as mãos contra mim, mas esta é a vossa hora e o poder das trevas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '54', 'Então, prendendo-o, o levaram, e o meteram em casa do sumo sacerdote. E Pedro seguia-o de longe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '55', 'E, havendo-se acendido fogo no meio do pátio, estando todos sentados, assentou-se Pedro entre eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '56', 'E como certa criada, vendo-o estar assentado ao fogo, pusesse os olhos nele, disse: Este também estava com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '57', 'Porém ele negou-o, dizendo: Mulher, não o conheço.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '58', 'E, um pouco depois, vendo-o outro, disse: Tu és também deles. Mas Pedro disse: Homem, não sou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '59', 'E, passada quase uma hora, um outro afirmava, dizendo: Também este verdadeiramente estava com ele, pois também é galileu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '60', 'E Pedro disse: Homem, não sei o que dizes. E logo, estando ele ainda a falar, cantou o galo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '61', 'E, virando-se o SENHOR, olhou para Pedro, e Pedro lembrou-se da palavra do SENHOR, como lhe havia dito: Antes que o galo cante hoje, me negarás três vezes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '62', 'E, saindo Pedro para fora, chorou amargamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '63', 'E os homens que detinham Jesus zombavam dele, ferindo-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '64', 'E, vendando-lhe os olhos, feriam-no no rosto, e perguntavam-lhe, dizendo: Profetiza, quem é que te feriu?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '65', 'E outras muitas coisas diziam contra ele, blasfemando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '66', 'E logo que foi dia ajuntaram-se os anciãos do povo, e os principais dos sacerdotes e os escribas, e o conduziram ao seu concílio;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '67', 'E lhe perguntaram: És tu o Cristo? Di-no-lo. Ele replicou: Se vo-lo disser, não o crereis:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '68', 'E também, se vos perguntar, não me respondereis, nem me soltareis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '69', 'Desde agora o Filho do homem se assentará à direita do poder de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '70', 'E disseram todos: Logo, és tu o Filho de Deus? E ele lhes disse: Vós dizeis que eu sou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '22', '71', 'Então disseram: De que mais testemunho necessitamos? Pois nós mesmos o ouvimos da sua boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '1', 'E, LEVANTANDO-SE toda a multidão deles, o levaram a Pilatos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '2', 'E começaram a acusá-lo, dizendo: Havemos achado este, pervertendo a nossa nação, proibindo dar o tributo a César, e dizendo que ele mesmo é Cristo, o rei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '3', 'E Pilatos perguntou-lhe, dizendo: Tu és o Rei dos Judeus? E ele, respondendo, disse-lhe: Tu o dizes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '4', 'E disse Pilatos aos principais dos sacerdotes, e à multidão: Não acho culpa alguma neste homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '5', 'Mas eles insistiam cada vez mais, dizendo: Alvoroça o povo ensinando por toda a Judeia, começando desde a Galileia até aqui.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '6', 'Então Pilatos, ouvindo falar da Galileia, perguntou se aquele homem era galileu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '7', 'E sabendo que era da jurisdição de Herodes, remeteu-o a Herodes, que também naqueles dias estava em Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '8', 'E Herodes, quando viu a Jesus, alegrou-se muito; porque havia muito que desejava vê-lo, por ter ouvido dele muitas coisas; e esperava que lhe veria fazer algum sinal;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '9', 'E interrogava-o com muitas palavras, mas ele nada lhe respondia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '10', 'E estavam os principais dos sacerdotes, e os escribas, acusando-o com grande veemência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '11', 'E Herodes, com os seus soldados, desprezou-o, e, escarnecendo dele, vestiu-o de uma roupa resplandecente e tornou a enviá-lo a Pilatos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '12', 'E no mesmo dia Pilatos e Herodes entre si se fizeram amigos; pois dantes andavam em inimizade um com o outro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '13', 'E, convocando Pilatos os principais dos sacerdotes, e os magistrados, e o povo, disse-lhes:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '14', 'Haveis-me apresentado este homem como pervertedor do povo; e eis que, examinando-o na vossa presença, nenhuma culpa, das de que o acusais, acho neste homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '15', 'Nem mesmo Herodes, porque a ele vos remeti, e eis que não tem feito coisa alguma digna de morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '16', 'Castigá-lo-ei pois e soltá-lo-ei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '17', 'E era-lhe necessário soltar-lhes um pela festa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '18', 'Mas toda a multidão clamou à uma, dizendo: Fora daqui com este, e solta-nos Barrabás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '19', 'O qual fora lançado na prisão por causa de uma sedição feita na cidade, e de um homicídio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '20', 'Falou pois outra vez Pilatos, querendo soltar a Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '21', 'Mas eles clamavam em contrário, dizendo: Crucifica-o, crucifica-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '22', 'Então ele, pela terceira vez, lhes disse: Mas que mal fez este? Não acho nele culpa alguma de morte. Castigá-lo-ei pois, e soltá-lo-ei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '23', 'Mas eles instavam com grandes gritos, pedindo que fosse crucificado. E os seus gritos, e os dos principais dos sacerdotes, redobravam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '24', 'Então Pilatos julgou que devia fazer o que eles pediam,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '25', 'E soltou-lhes o que fora lançado na prisão por uma sedição e homicídio, que era o que pediam; mas entregou Jesus à vontade deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '26', 'E quando o iam levando, tomaram um certo Simão, cireneu, que vinha do campo, e puseram-lhe a cruz às costas, para que a levasse após Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '27', 'E seguia-o grande multidão de povo e de mulheres, as quais batiam nos peitos, e o lamentavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '28', 'Porém Jesus, voltando-se para elas, disse: Filhas de Jerusalém, não choreis por mim, chorai antes por vós mesmas, e por vossos filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '29', 'Porque eis que hão-de vir dias em que dirão: Bem-aventuradas as estéreis, e os ventres que não geraram, e os peitos que não amamentaram!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '30', 'Então começarão a dizer aos montes: Caí sobre nós, e aos outeiros: Cobri-nos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '31', 'Porque, se ao madeiro verde fazem isto, que se fará ao seco?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '32', 'E também conduziram outros dois, que eram malfeitores, para com ele serem mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '33', 'E, quando chegaram ao lugar chamado a Caveira, ali o crucificaram, e aos malfeitores, um à direita e outro à esquerda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '34', 'E dizia Jesus: Pai, perdoa-lhes porque não sabem o que fazem. E, repartindo os seus vestidos, lançaram sortes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '35', 'E o povo estava olhando. E também os príncipes zombavam dele, dizendo: Aos outros salvou, salve-se a si mesmo, se este é o Cristo, o escolhido de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '36', 'E também os soldados o escarneciam, chegando-se a ele, e apresentando-lhe vinagre,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '37', 'E dizendo: Se tu és o Rei dos Judeus salva-te a ti mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '38', 'E também por cima dele estava um título, escrito em letras gregas, romanas, e hebraicas: ESTE É O REI DOS JUDEUS.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '39', 'E um dos malfeitores que estavam pendurados blasfemava dele, dizendo: Se tu és o Cristo, salva-te a ti mesmo, e a nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '40', 'Respondendo, porém, o outro, repreendia-o, dizendo: Tu nem ainda temes a Deus, estando na mesma condenação?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '41', 'E nós, na verdade, com justiça, porque recebemos o que os nossos feitos mereciam; mas este nenhum mal fez.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '42', 'E disse a Jesus: Senhor, lembra-te de mim, quando entrares no teu reino.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '43', 'E disse-lhe Jesus: Em verdade te digo que hoje estarás comigo no Paraíso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '44', 'E era já quase a hora sexta, e houve trevas em toda a terra até à hora nona,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '45', 'Escurecendo-se o sol; e rasgou-se ao meio o véu do templo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '46', 'E, clamando Jesus com grande voz, disse: Pai, nas tuas mãos entrego o meu espírito. E, havendo dito isto, expirou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '47', 'E o centurião, vendo o que tinha acontecido, deu glória a Deus, dizendo: Na verdade, este homem era justo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '48', 'E toda a multidão que se ajuntara a este espectáculo, vendo o que havia acontecido, voltava batendo nos peitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '49', 'E todos os seus conhecidos, e as mulheres que juntamente o haviam seguido desde a Galileia, estavam de longe vendo estas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '50', 'E eis que um varão por nome José, senador, homem de bem e justo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '51', 'Que não tinha consentido no conselho e nos actos dos outros, de Arimatéia, cidade dos judeus, e que também esperava o reino de Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '52', 'Esse, chegando a Pilatos pediu o corpo de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '53', 'E havendo-o tirado, envolveu-o num lençol, e pô-lo num sepulcro escavado numa penha, onde ninguém ainda havia sido posto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '54', 'E era o dia da preparação, e amanhecia o sábado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '55', 'E as mulheres, que tinham vindo com ele da Galileia, seguiram-no também e viram o sepulcro, e como foi posto o seu corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '23', '56', 'E, voltando elas, prepararam especiarias e unguentos; e no sábado repousaram, conforme o mandamento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '1', 'E NO primeiro dia da semana, muito de madrugada, foram elas ao sepulcro, levando as especiarias que tinham preparado, e algumas outras com elas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '2', 'E acharam a pedra revolvida do sepulcro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '3', 'E, entrando, não acharam o corpo do SENHOR Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '4', 'E aconteceu que, estando elas perplexas a esse respeito, eis que pararam junto delas dois varões, com vestidos resplandecentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '5', 'E, estando elas muito atemorizadas, e abaixando o rosto para o chão, eles lhes disseram: Porque buscais o vivente entre os mortos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '6', 'Não está aqui, mas ressuscitou. Lembrai-vos como vos falou, estando ainda na Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '7', 'Dizendo: Convém que o Filho do homem seja entregue nas mãos de homens pecadores, e seja crucificado, e ao terceiro dia ressuscite.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '8', 'E lembraram-se das suas palavras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '9', 'E, voltando do sepulcro, anunciaram todas estas coisas aos onze e a todos os demais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '10', 'E eram Maria Madalena, e Joana, e Maria mãe de Tiago, e as outras que com elas estavam, as que diziam estas coisas aos apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '11', 'E as suas palavras lhes pareciam com desvario, e não as creram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '12', 'Pedro, porém, levantando-se, correu ao sepulcro, e, abaixando-se, viu só os lençóis ali postos; e retirou-se, admirando consigo aquele caso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '13', 'E eis que no mesmo dia iam dois deles para uma aldeia, que distava de Jerusalém sessenta estádios, cujo nome era Emaus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '14', 'E iam falando entre si de tudo aquilo que havia sucedido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '15', 'E aconteceu que, indo eles falando entre si, e fazendo perguntas um ao outro, o mesmo Jesus se aproximou, e ia com eles;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '16', 'Mas os olhos deles estavam como que fechados, para que o não conhecessem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '17', 'E ele lhes disse: Que palavras são essas que, caminhando trocais entre vós, e porque estais tristes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '18', 'E, respondendo um, cujo nome era Cléofas, disse-lhe: És tu só peregrino em Jerusalém, e não sabes as coisas que nela têm sucedido nestes dias?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '19', 'E ele lhes perguntou: Quais? E eles lhe disseram: As que dizem respeito a Jesus Nazareno, que foi varão profeta, poderoso em obras e palavras diante de Deus e de todo o povo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '20', 'E como os principais dos sacerdotes e os nossos príncipes o entregaram à condenação de morte, e o crucificaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '21', 'E nós esperávamos que fosse ele o que remisse Israel; mas agora, sobre tudo isso, é já hoje o terceiro dia desde que essas coisas aconteceram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '22', 'É verdade que também algumas mulheres de entre nós nos maravilharam, as quais de madrugada foram ao sepulcro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '23', 'E, não achando o seu corpo, voltaram, dizendo que também tinham visto uma visão de anjos, que dizem que ele vive.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '24', 'E alguns dos que estavam connosco foram ao sepulcro, e acharam ser assim como as mulheres haviam dito; porém a ele não o viram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '25', 'E ele lhes disse: Ó néscios, e tardos de coração para crer tudo o que os profetas disseram!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '26', 'Porventura não convinha que o Cristo padecesse estas coisas e entrasse na sua glória?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '27', 'E, começando por Moisés, e por todos os profetas, explicava-lhes o que dele se achava em todas as Escrituras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '28', 'E chegaram à aldeia para onde iam e ele fez como quem ia para mais longe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '29', 'E eles o constrangeram, dizendo: Fica connosco, porque já é tarde, e já declinou o dia. E entrou para ficar com eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '30', 'E aconteceu que, estando com eles à mesa, tomando o pão, o abençoou e partiu, e lho deu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '31', 'Abriram-se-lhe então os olhos, e o conheceram, e ele desapareceu-lhes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '32', 'E disseram um para o outro: Porventura não ardia em nós o nosso coração quando, pelo caminho, nos falava, e quando nos abria as Escrituras?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '33', 'E na mesma hora, levantando-se, tornaram para Jerusalém, e acharam congregados os onze, e os que estavam com eles;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '34', 'Os quais diziam: Ressuscitou verdadeiramente o SENHOR, e já apareceu a Simão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '35', 'E eles lhe contaram o que lhe acontecera no caminho, e como deles foi conhecido no partir do pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '36', 'E, falando eles destas coisas, o mesmo Jesus se apresentou no meio deles, e disse-lhes: Paz seja convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '37', 'E eles espantados e atemorizados, pensavam que viam algum espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '38', 'E ele lhes disse: Porque estais perturbados, e porque sobem tais pensamentos aos vossos corações?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '39', 'Vede as minhas mãos e os meus pés, que sou eu mesmo: apalpai-me e vede; pois um espírito não tem carne nem ossos, como vedes que eu tenho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '40', 'E, dizendo isto, mostrou-lhes as mãos e os pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '41', 'E, não o crendo eles ainda por causa da alegria, e estando maravilhados, disse-lhes: Tendes aqui alguma coisa de comer?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '42', 'Então eles apresentaram-lhe parte de um peixe assado, e um favo de mel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '43', 'O que ele tomou, e comeu diante deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '44', 'E disse-lhes: São estas as palavras que vos disse estando ainda convosco: Que convinha que se cumprisse tudo o que de mim estava escrito na lei de Moisés, e nos profetas e nos salmos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '45', 'Então abriu-lhes o entendimento para compreenderem as Escrituras,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '46', 'E disse-lhes: Assim está escrito, e assim convinha que o Cristo padecesse, e ao terceiro dia ressuscitasse dos mortos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '47', 'E em seu nome se pregasse o arrependimento e a remissão dos pecados, em todas as nações, começando por Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '48', 'E destas coisas sois vós testemunhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '49', 'E eis que sobre vós envio a promessa de meu Pai: Ficai, porém, na cidade de Jerusalém, até que do alto sejais revestidos de poder.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '50', 'E levou-os fora, até Betânia; e, levantando as suas mãos, os abençoou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '51', 'E aconteceu que, abençoando-os ele, se apartou deles e foi elevado ao céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '52', 'E, adorando-o eles, tornaram com grande júbilo para Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('3', '24', '53', 'E estavam sempre no templo, louvando e bendizendo a Deus. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '1', '¶ No princípio era o Verbo, e o Verbo estava com Deus, e o Verbo era Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '2', 'Ele estava no princípio com Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '3', 'Todas as coisas foram feitas por ele, e sem ele nada do que foi feito se fez.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '4', 'Nele estava a vida, e a vida era a luz dos homens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '5', 'E a luz resplandece nas trevas, e as trevas não a compreenderam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '6', 'Houve um homem enviado de Deus, cujo nome era João.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '7', 'Este veio para testemunho, para que testificasse da luz; para que todos cressem por ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '8', 'Não era ele a luz; mas para que testificasse da luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '9', 'Ali estava a luz verdadeira, que alumia a todo o homem que vem ao mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '10', 'Estava no mundo, e o mundo foi feito por ele, e o mundo não o conheceu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '11', 'Veio para o que era seu, e os seus não o receberam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '12', 'Mas, a todos quantos o receberam, deu-lhes o poder de serem feitos filhos de Deus; aos que crêem no seu nome;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '13', 'Os quais não nasceram do sangue, nem da vontade da carne, nem da vontade do varão, mas de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '14', 'E o Verbo se fez carne, e habitou entre nós, e vimos a sua glória, como a glória do unigénito do Pai, cheio de graça e de verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '15', 'João testificou dele; e clamou, dizendo: Este era aquele de quem eu dizia: O que vem depois de mim é antes de mim, porque foi primeiro do que eu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '16', 'E todos nós recebemos também da sua plenitude, e graça por graça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '17', 'Porque a lei foi dada por Moisés; a graça e a verdade vieram por Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '18', 'Deus nunca foi visto por alguém. O Filho unigénito, que está no seio do Pai, esse o fez conhecer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '19', 'E este é o testemunho de João quando os judeus mandaram de Jerusalém sacerdotes levitas para que lhe perguntassem: Quem és tu?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '20', 'E confessou, e não negou; confessou: Eu não sou o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '21', 'E perguntaram-lhe: Então quê? És tu Elias? E disse: Não sou. És tu profeta? E respondeu: Não.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '22', 'Disseram-lhe pois: Quem és? Para que demos resposta àqueles que nos enviaram; que dizes de ti mesmo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '23', 'Disse: Eu sou a voz do que clama no deserto: Endireitai o caminho do SENHOR, como disse o profeta Isaías,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '24', 'E os que tinham sido enviados eram dos fariseus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '25', 'E perguntaram-lhe, e disseram-lhe: Porque baptizas, pois, se tu não és o Cristo, nem Elias, nem o profeta?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '26', 'João respondeu-lhes, dizendo: Eu baptizo com água; mas no meio de vós está um a quem vós não conheceis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '27', 'Este é aquele que vem após mim, que foi antes de mim, do qual eu não sou digno de desatar a correia da alparca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '28', 'Estas coisas aconteceram em Betânia, da outra banda do Jordão, onde João estava baptizando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '29', 'No dia seguinte João viu a Jesus, que vinha para ele, e disse: Eis o Cordeiro de Deus, que tira o pecado do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '30', 'Este é aquele do qual eu disse: Após mim vem um varão que foi antes de mim; porque já era primeiro do que eu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '31', 'E eu não o conhecia; mas, para que ele fosse manifestado a Israel, vim eu, por isso, baptizando com água.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '32', 'E João testificou, dizendo: Eu vi o Espirito descer do céu como uma pomba, e repousar sobre ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '33', 'E eu não o conhecia, mas o que me mandou a baptizar com água, esse me disse: Sobre aquele que vires descer o Espírito, e sobre ele repousar, esse é o que baptiza com o Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '34', 'E eu vi, e tenho testificado que este é o Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '35', 'No dia seguinte João estava outra vez ali, e dois dos seus discípulos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '36', 'E, vendo passar a Jesus, disse: Eis aqui o Cordeiro de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '37', 'E os dois discípulos ouviram-no dizer isto, e seguiram a Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '38', 'E Jesus, voltando-se e vendo que eles o seguiam, disse-lhes: Que buscais? E eles disseram: Rabi, (que traduzido quer dizer Mestre) onde moras?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '39', 'Ele lhes disse: Vinde, e vede. Foram e viram onde morava, e ficaram com ele aquele dia: e era já quase a hora décima.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '40', 'Era André, irmão de Simão Pedro, um dos dois que ouviram aquilo de João, e o haviam seguido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '41', 'Este achou primeiro a seu irmão Simão, e disse-lhe: Achámos o Messias (que, traduzido, é o Cristo).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '42', 'E levou-o a Jesus. E, olhando Jesus para ele, disse: Tu és Simão, filho de Jonas; tu serás chamado Cefas (que quer dizer Pedro).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '43', 'No dia seguinte quis Jesus ir à Galileia; e achou a Filipe, e disse-lhe: Segue-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '44', 'E Filipe era de Betsaida, cidade de André e de Pedro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '45', 'Filipe achou Nataniel, e disse-lhe: Havemos achado aquele de quem Moisés escreveu na lei, e os profetas: Jesus de Nazaré, filho de José.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '46', 'Disse-lhe Nataniel: Pode vir alguma coisa boa de Nazaré? Disse-lhe Filipe: Vem, e vê.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '47', 'Jesus viu Nataniel vir ter com ele, e disse dele: Eis aqui um verdadeiro israelita, em quem não há dolo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '48', 'Disse-lhe Nataniel: Donde me conheces tu? Jesus respondeu, e disse-lhe: Antes que Filipe te chamasse, te vi eu, estando tu debaixo da figueira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '49', 'Nataniel respondeu, e disse-lhe: Rabi, tu és o Filho de Deus, tu és o Rei de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '50', 'Jesus respondeu, e disse-lhe: Porque te disse: Vi-te debaixo da figueira, crês? Coisas maiores do que estas verás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '1', '51', 'E disse-lhe: Na verdade, na verdade vos digo que daqui em diante vereis o céu aberto, e os anjos de Deus subirem e descerem sobre o Filho do homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '1', 'E, Ao terceiro dia, fizeram-se umas bodas em Caná da Galileia: e estava ali a mãe de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '2', 'E foi também convidado Jesus e os seus discípulos para as bodas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '3', 'E, faltando o vinho, a mãe de Jesus lhe disse: Não têm vinho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '4', 'Disse-lhe Jesus: Mulher, que tenho eu contigo? Ainda não é chegada a minha hora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '5', 'Sua mãe disse aos serventes: Fazei tudo quanto ele vos disser.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '6', 'E estavam ali postas seis talhas de pedra, para as purificações dos judeus, e em cada uma cabiam dois ou três almudes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '7', 'Disse-lhes Jesus: Enchei de água essas talhas. E encheram-nas até cima.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '8', 'E disse-lhes: Tirai agora, e levai ao mestre-sala. E levaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '9', 'E, logo que o mestre-sala provou a água feita vinho (não sabendo donde viera, se bem que o sabiam os serventes que tinham tirado a água), chamou o mestre-sala ao esposo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '10', 'E disse-lhe: Todo o homem põe primeiro o vinho bom, e, quando já têm bebido bem, então o inferior; mas tu guardaste até agora o bom vinho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '11', 'Jesus principiou assim os seus sinais em Caná da Galileia, e manifestou a sua glória; e os seus discípulos creram nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '12', 'Depois disto desceu a Cafarnaum, ele, e sua mãe, e seus irmãos, e seus discípulos, e ficaram ali não muitos dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '13', 'E estava próxima a páscoa dos judeus, e Jesus subiu a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '14', 'E achou no templo os que vendiam bois, e ovelhas, e pombos, e os cambiadores assentados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '15', 'E, tendo feito um azorrague de cordéis, lançou todos fora do templo, também os bois e ovelhas; e espalhou o dinheiro dos cambiadores, e derribou as mesas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '16', 'E disse aos que vendiam pombos: Tirai daqui estes, e não façais da casa de meu Pai casa de venda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '17', 'E os seus discípulos lembraram-se do que está escrito: O zelo da tua casa me devorará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '18', 'Responderam pois os judeus, e disseram-lhe: Que sinal nos mostras para fazeres isto?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '19', 'Jesus respondeu, e disse-lhes: Derribai este templo, e em três dias o levantarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '20', 'Disseram pois os judeus: Em quarenta e seis anos foi edificado este templo, e tu o levantarás em três dias?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '21', 'Mas ele falava do templo do seu corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '22', 'Quando, pois, ressuscitou dos mortos, os seus discípulos lembraram-se de que lhes dissera isto; e creram na Escritura, e na palavra que Jesus tinha dito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '23', 'E, estando ele em Jerusalém pela páscoa, durante a festa, muitos, vendo os sinais que fazia, creram no seu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '24', 'Mas o mesmo Jesus não confiava neles, porque a todos conhecia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '2', '25', 'E não necessitava de que alguém testificasse do homem, porque ele bem sabia o que havia no homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '1', 'E HAVIA entre os fariseus um homem, chamado Nicodemos, príncipe dos judeus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '2', 'Este foi ter de noite com Jesus, e disse-lhe: Rabi, bem sabemos que és Mestre, vindo de Deus: porque ninguém pode fazer estes sinais que tu fazes, se Deus não for com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '3', 'Jesus respondeu, e disse-lhe: Na verdade na verdade te digo que aquele que não nascer de novo, não pode ver o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '4', 'Disse-lhe Nicodemos : Como pode um homem nascer, sendo velho? Porventura pode tornar a entrar no ventre de sua mãe, e nascer?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '5', 'Jesus respondeu: Na verdade, na verdade, te digo que aquele que não nascer da água e do Espírito, não pode entrar no reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '6', 'O que é nascido da carne é carne, e o que é nascido do Espírito é espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '7', 'Não te maravilhes de te ter dito: Necessário vos é nascer de novo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '8', 'O vento assopra onde quer e ouves a sua voz; mas não sabes donde vem, nem para onde vai; assim é todo aquele que é nascido do Espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '9', 'Nicodemos respondeu, e disse-lhe: Como pode ser isso?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '10', 'Jesus respondeu, e disse-lhe: Tu és mestre de Israel, e não sabes isto?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '11', 'Na verdade, na verdade te digo que nós dizemos o que sabemos; e testificamos o que vimos; e não aceitais o nosso testemunho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '12', 'Se vos falei de coisas terrestres, e não crestes, como crereis, se vos falar das celestiais?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '13', 'Ora ninguém subiu ao céu, senão o que desceu do céu, o Filho do homem, que está no céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '14', 'E, como Moisés levantou a serpente no deserto, assim importa que o Filho do homem seja levantado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '15', 'Para que todo aquele que nele crê não pereça, mas tenha a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '16', 'Porque Deus amou o Mundo de tal maneira que deu o seu Filho unigénito, para que todo aquele que nele crê não pereça, mas tenha a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '17', 'Porque Deus enviou o seu Filho ao mundo, não para que condenasse o mundo, mas para que o mundo fosse salvo por ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '18', 'Quem crê nele não é condenado; mas quem não crê já está condenado; porquanto não crê no nome do Unigénito Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '19', 'E a condenação é esta: Que a luz veio ao mundo, e os homens amaram mais as trevas do que a luz, porque as suas obras eram más.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '20', 'Porque todo aquele que faz o mal aborrece a luz, e não vem para a luz, para que as suas obras não sejam reprovadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '21', 'Mas quem pratica a verdade vem para a luz, a fim de que as suas obras sejam manifestas, porque são feitas em Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '22', 'Depois disto foi Jesus com os seus discípulos para a terra da Judeia; e estava ali com eles, e baptizava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '23', 'Ora João baptizava também em Enon, junto a Salim, porque havia ali muitas águas; e vinham ali, e eram baptizados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '24', 'Porque ainda João não tinha sido lançado na prisão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '25', 'Houve então uma questão entre os discípulos de João e um judeu acerca da purificação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '26', 'E foram ter com João, e disseram-lhe: Rabi, aquele que estava contigo além do Jordão, do qual tu deste testemunho, ei-lo baptizando, e todos vão ter com ele .');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '27', 'João respondeu, e disse: O homem não pode receber coisa alguma, se lhe não for dado do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '28', 'Vós mesmos me sois testemunhas de que disse: Eu não sou o Cristo, mas sou enviado adiante dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '29', 'Aquele que tem a esposa é o esposo, mas o amigo do esposo, que lhe assiste e o ouve, alegra-se muito com a voz do esposo. Assim pois já este meu gozo está cumprido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '30', 'É necessário que ele cresça e que eu diminua.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '31', 'Aquele que vem de cima é sobre todos: aquele que vem da terra é da terra e fala da terra. Aquele que vem do céu é sobre todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '32', 'E aquilo que ele viu e ouviu isso testifica; e ninguém aceita o seu testemunho');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '33', 'Aquele que aceitou o seu testemunho, esse confirmou que Deus é verdadeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '34', 'Porque aquele que Deus enviou fala as palavras de Deus; pois não lhe dá Deus o Espírito por medida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '35', 'O Pai ama o Filho, e todas as coisas entregou nas suas mãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '3', '36', 'Aquele que crê no Filho tem a vida eterna; mas aquele que não crê no Filho não verá a vida; mas a ira de Deus sobre ele permanece.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '1', 'E QUANDO o SENHOR entendeu que os fariseus tinham ouvido que Jesus fazia e baptizava mais discípulos do que João');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '2', '(Ainda que Jesus mesmo não baptizava, mas os seus discípulos).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '3', 'Deixou a Judeia, e foi outra vez para a Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '4', 'E era-lhe necessário passar por Samaria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '5', 'Foi pois a uma cidade, de Samaria, chamada Sicar, junto da herdade que Jacob tinha dado a seu filho José.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '6', 'E estava ali a fonte de Jacob, Jesus, pois, cansado do caminho assentou-se assim junto da fonte. Era isto quase à hora sexta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '7', 'Veio uma mulher de Samaria tirar água, disse-lhe Jesus: Dá-me de beber.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '8', 'Porque os seus discípulos tinham ido à cidade comprar comida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '9', 'Disse-lhe pois a mulher samaritana: Como, sendo tu judeu, me pedes de beber a mim, que sou mulher samaritana? (porque os judeus não se comunicam com os samaritanos).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '10', 'Jesus respondeu, e disse-lhe: Se tu conheceras o dom de Deus, e quem é o que te diz: Dá-me de beber, tu lhe pedirias, e ele te daria água viva.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '11', 'Disse-lhe a mulher: Senhor, tu não tens com que a tirar, e o poço é fundo: onde pois tens a água viva?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '12', 'És tu maior do que o nosso pai Jacob, que nos deu o poço, bebendo ele próprio dele, e os seus filhos e o seu gado?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '13', 'Jesus , respondeu, e disse-lhe: Qualquer que beber desta água tornará a ter sede;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '14', 'Mas aquele, que beber da água que eu lhe der nunca terá sede, porque a água que eu lhe der se fará nele uma fonte de água que salte para a vida eterna:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '15', 'Disse-lhe a mulher: Senhor, dá-me dessa água, para que não mais tenha sede, e não venha aqui tirá-la.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '16', 'Disse-lhe Jesus: Vai, chama o teu marido, e vem cá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '17', 'A mulher respondeu, e disse: Não tenho marido. Disse-lhe Jesus: Disseste bem: Não tenho marido;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '18', 'Porque tiveste cinco maridos, e o que agora tens não é teu marido; isto disseste com verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '19', 'Disse-lhe a mulher: Senhor, vejo que és profeta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '20', 'Nossos pais adoraram neste monte, e vós dizeis que é em Jerusalém o lugar onde se deve adorar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '21', 'Disse-lhe Jesus: Mulher, crê-me que a hora vem, em que nem neste monte nem em Jerusalém adorareis o Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '22', 'Vós adorais o que não sabeis; nós adoramos o que sabemos, porque a salvação vem dos judeus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '23', 'Mas a hora vem, e agora é, em que os verdadeiros adoradores adorarão o Pai em espírito e em verdade; porque o Pai procura a tais que assim o adorem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '24', 'Deus é Espírito, e importa que os que o adoram o adorem em espírito e em verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '25', 'A mulher disse-lhe: Eu sei que o Messias (que se chama o Cristo) vem; quando ele vier, nos anunciará tudo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '26', 'Jesus disse-lhe: Eu o sou, eu que falo contigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '27', 'E nisto vieram os seus discípulos, e maravilharam-se de que estivesse falando com uma mulher; todavia nenhum lhe disse: Que perguntas? ou: Porque falas com ela?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '28', 'Deixou pois a mulher o seu cântaro, e foi à cidade, e disse àqueles homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '29', 'Vinde, vede um homem que me disse tudo quanto tenho feito: Porventura não é este o Cristo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '30', 'Saíram pois da cidade, e foram ter com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '31', 'E entretanto os seus discípulos lhe rogaram, dizendo: Rabi, come.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '32', 'Porém ele lhes disse: Uma comida tenho para comer, que vós não conheceis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '33', 'Então os discípulos diziam uns aos outros: Trouxe-lhe porventura alguém de comer?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '34', 'Jesus disse-lhes: A minha comida é fazer a vontade daquele que me enviou, e realizar a sua obra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '35', 'Não dizeis vós que ainda há quatro meses até que venha a ceifa? Eis que eu vos digo: Levantai os vossos olhos, e vede as terras, que já estão brancas para a ceifa');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '36', 'E o que ceifa recebe galardão, e ajunta fruto para a vida eterna; para que, assim o que semeia como o que ceifa, ambos se regozijem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '37', 'Porque nisto é verdadeiro o ditado, que um é o que semeia, e outro o que ceifa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '38', 'Eu vos enviei a ceifar onde vós não trabalhastes; outros trabalharam, e vós entrastes no seu trabalho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '39', 'E muitos dos samaritanos daquela cidade creram nele, pela palavra da mulher, que testificou: Disse-me tudo quanto tenho feito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '40', 'Indo pois ter com ele os samaritanos, rogaram-lhe que ficasse com eles; e ficou ali dois dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '41', 'E muitos mais creram nele, por causa da sua palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '42', 'E diziam à mulher: Já não é pelo teu dito que nós cremos; porque nós mesmos o temos ouvido, e sabemos que este é verdadeiramente o Cristo, o Salvador do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '43', 'E dois dias depois partiu dali, e foi para a Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '44', 'Porque Jesus mesmo testificou que um profeta não tem honra na sua própria pátria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '45', 'Chegando pois à Galileia, os galileus o receberam, vistas todas as coisas que fizera em Jerusalém no dia da festa; porque também eles tinham ido à festa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '46', 'Segunda vez foi Jesus a Caná da Galileia, onde da água fizera vinho. E havia ali um régulo, cujo filho estava enfermo em Cafarnaúm.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '47', 'Ouvindo este que Jesus vinha da Judeia para a Galileia, foi ter com ele, e rogou-lhe que descesse, e curasse o seu filho, porque já estava à morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '48', 'Então Jesus lhe disse: Se não virdes sinais e milagres, não crereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '49', 'Disse-lhe o régulo: Senhor, desce, antes que meu filho morra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '50', 'Disse-lhe Jesus: Vai, o teu filho vive. E o homem creu na palavra que Jesus lhe disse, e foi-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '51', 'E, descendo ele logo, saíram-lhe ao encontro os seus servos, e lhe anunciaram, dizendo: O teu filho vive.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '52', 'Perguntou-lhes pois a que hora se achara melhor; e disseram-lhe: Ontem às sete horas a febre o deixou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '53', 'Entendeu pois o pai que era aquela hora a mesma em que Jesus lhe disse: O teu filho vive; e creu ele, e toda a sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '4', '54', 'Jesus fez este segundo milagre, quando ia da Judeia para a Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '1', 'DEPOIS disto havia uma festa entre os judeus, e Jesus subiu a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '2', 'Ora em Jerusalém há, próximo à porta das ovelhas, um tanque, chamado em hebreu Betesda, o qual tem cinco alpendres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '3', 'Nestes jazia grande multidão de enfermos: cegos, mancos e ressicados, esperando o movimento das águas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '4', 'Porquanto um anjo descia em certo tempo ao tanque; e agitava a água; e o primeiro que ali descia, depois do movimento da água, sarava de qualquer enfermidade que tivesse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '5', 'E estava ali um homem que havia trinta e oito anos, se achava enfermo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '6', 'E Jesus, vendo este deitado; e sabendo que estava neste estado havia muito tempo, disse-lhe: Queres ficar são?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '7', 'O enfermo respondeu-lhe: Senhor; não tenho homem algum que, quando a água é agitada, me meta no tanque; mas, enquanto eu vou, desce outro antes de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '8', 'Jesus disse-lhe: Levanta-te, toma a tua cama, e anda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '9', 'Logo aquele homem ficou são; e tomou a sua cama, e partiu. E aquele dia era sábado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '10', 'Então os judeus disseram àquele que tinha sido curado: É sábado, não te é lícito levar a cama.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '11', 'Ele respondeu-lhes: Aquele que me curou, ele próprio disse: Toma a tua cama, e anda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '12', 'Perguntaram-lhe pois: Quem é o homem que te disse: Toma a tua cama, e anda?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '13', 'E o que fora curado não sabia quem era; porque Jesus se havia retirado, em razão de naquele lugar haver grande multidão');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '14', 'Depois Jesus encontrou-o no templo, e disse-lhe: Eis que já estás são; não peques mais, para que te não suceda alguma coisa pior.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '15', 'E aquele homem foi, e anunciou aos judeus que Jesus era o que o curara.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '16', 'E por esta causa os judeus perseguiram a Jesus, e procuravam matá-lo; porque fazia estas coisas no sábado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '17', 'E Jesus lhes respondeu: Meu Pai trabalha até agora, e eu trabalho também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '18', 'Por isso, pois os judeus ainda mais procuravam matá-lo, porque não só quebrantava o sábado, mas também dizia que Deus era seu próprio Pai, fazendo-se igual a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '19', 'Mas Jesus respondeu, e disse-lhes: Na verdade, na verdade vos digo que o Filho por si mesmo não pode fazer coisa alguma, se o não vir fazer ao Pai; porque tudo quanto ele faz, o Filho faz igualmente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '20', 'Porque o Pai ama o Filho, e mostra-lhe tudo o que faz; e ele lhe mostrará maiores obras do que estas para que vos maravilheis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '21', 'Pois, assim como o Pai ressuscita os mortos e os vivifica, assim também o filho vivifica aquele que quer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '22', 'E também o Pai a ninguém julga, mas deu ao Filho todo o juízo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '23', 'Para que todos honrem o Filho, como honram o Pai. Quem não honra o Filho não honra o Pai que o enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '24', 'Na verdade, na verdade vos digo que quem ouve a minha palavra, e crê naquele que me enviou, tem a vida eterna, e não entrará em condenação, mas passou da morte para a vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '25', 'Em verdade, em verdade vos digo que vem a hora, e agora é, em que os mortos ouvirão a voz do Filho de Deus, e os que a ouvirem viverão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '26', 'Porque, como o Pai tem a vida em si mesmo, assim deu também ao Filho ter a vida em si mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '27', 'E deu-lhe o poder de exercer o juízo, porque é o Filho do homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '28', 'Não vos maravilheis disto; porque vem a hora em que todos os que estão nos sepulcros ouvirão a sua voz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '29', 'E os que fizeram o bem sairão para a ressurreição da vida; e os que fizeram o mal para a ressurreição da condenação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '30', 'Eu não posso de mim mesmo fazer coisa alguma; como ouço, assim julgo; e o meu juízo é justo, porque não busco a minha vontade, mas a vontade do Pai que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '31', 'Se eu testifico de mim mesmo, o meu testemunho não é verdadeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '32', 'Há outro que testifica de mim, e sei que o testemunho que ele dá de mim é verdadeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '33', 'Vós mandastes a João, e ele deu testemunho da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '34', 'Eu, porém, não recebo testemunho de homem; mas digo isto, para que vos salveis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '35', 'Ele era a candeia que ardia e alumiava; e vós quisestes alegrar-vos por um pouco de tempo com a sua luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '36', 'Mas eu tenho maior testemunho do que o de João; porque as obras que o Pai me deu para realizar, as mesmas obras que eu faço, testificam de mim, que o Pai me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '37', 'E o Pai, que me enviou, ele mesmo testificou de mim. Vós nunca ouvistes a sua voz, nem vistes o seu parecer;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '38', 'E a sua palavra não permanece em vós, porque naquele que ele enviou não credes vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '39', 'Examinais as Escrituras, porque vós cuidais ter nelas a vida eterna, e são elas que de mim testificam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '40', 'E não quereis vir a mim para terdes vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '41', 'Eu não recebo glória dos homens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '42', 'Mas bem vos conheço que não tendes em vós o amor de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '43', 'Eu vim em nome de meu Pai, e não me aceitais; se outro vier em seu próprio nome, a esse aceitareis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '44', 'Como podeis vós crer, recebendo honra uns dos outros, e não buscando a honra que vem só de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '45', 'Não cuideis que eu vos hei-de acusar para com o Pai. Há um que vos acusa, Moisés, em quem vós esperais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '46', 'Porque, se vós crêsseis em Moisés, creríeis em mim; porque de mim escreveu ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '5', '47', 'Mas, se não credes nos seus escritos, como crereis nas minhas palavras?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '1', 'DEPOIS disto partiu Jesus para a outra banda do mar da Galileia, que é o de Tiberíades,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '2', 'E grande multidão o seguia; porque via os sinais que operava sobre os enfermos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '3', 'E Jesus subiu ao monte, e assentou-se ali com os seus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '4', 'E a páscoa, a festa dos judeus, estava próxima.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '5', 'Então Jesus, levantando os olhos, e vendo que uma grande multidão vinha ter com ele, disse a Filipe: Onde compraremos pão, para estes comerem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '6', 'Mas. dizia isto para o experimentar; porque ele bem sabia o que havia de fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '7', 'Filipe respondeu-lhe: Duzentos dinheiros de pão não lhes bastarão, para que cada um deles tome um pouco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '8', 'E um dos seus discípulos, André, irmão de Simão Pedro, disse-lhe:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '9', 'Está aqui um rapaz que tem cinco pães de cevada e dois peixinhos: mas que é isto para tantos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '10', 'E disse Jesus: Mandai assentar os homens. E havia muita relva naquele lugar. Assentaram-se pois os homens em número de quase cinco mil.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '11', 'E Jesus tomou os pães e, havendo dado graças, repartiu-os pelos discípulos, e os discípulos pelos que estavam assentados; e igualmente também dos peixes, quanto eles queriam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '12', 'E, quando estavam saciados, disse aos seus discípulos: Recolhei os pedaços que sobejaram, para que nada se perca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '13', 'Recolheram-nos pois, e encheram doze cestos de pedaços dos cinco pães de cevada, que sobejaram aos que haviam comido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '14', 'Vendo pois aqueles homens o milagre que Jesus tinha feito, diziam: Este é verdadeiramente o profeta que devia vir ao mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '15', 'Sabendo pois Jesus que haviam de vir arrebatá-lo, para o fazerem rei, tornou a retirar-se, ele só, para o monte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '16', 'E, quando veio a tarde, os seus discípulos desceram para o mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '17', 'E, entrando no barco, passaram o mar em direcção a Cafarnaum; e era já escuro, e ainda Jesus não tinha chegado ao pé deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '18', 'E o mar se levantou, porque um grande vento assoprava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '19', 'E, tendo navegado uns vinte e cinco ou trinta estádios, viram a Jesus, andando sobre o mar e aproximando-se do barco; e temeram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '20', 'Porém ele lhes disse: Sou eu, não temais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '21', 'Então eles de boamente o receberam no barco; e logo o barco chegou à terra para onde iam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '22', 'No dia seguinte, a multidão, que estava da outra banda do mar, vendo que não havia ali mais do que um barquinho, e que Jesus não entrara com seus discípulos naquele barquinho mas que os seus discípulos tinham ido sós');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '23', '(Contudo, outros barquinhos tinham chegado de Tiberíades, perto do lugar onde comeram o pão, havendo o SENHOR dado graças);');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '24', 'Vendo pois a multidão que Jesus não estava ali, nem os seus discípulos, entraram eles também nos barcos, e foram a Cafarnaúm, em busca de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '25', 'E, achando-o na outra banda do mar, disseram-lhe: Rabi, quando chegaste aqui?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '26', 'Jesus respondeu-lhes, e disse: Na verdade, na verdade vos digo que me buscais, não pelos sinais que vistes; mas porque comestes do pão e vos saciastes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '27', 'Trabalhai, não pela comida que perece, mas pela comida que permanece para a vida eterna, a qual o Filho do homem vos dará; porque a este o Pai, Deus, o selou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '28', 'Disseram-lhe pois: Que faremos para executarmos as obras de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '29', 'Jesus respondeu, e disse-lhes: A obra de Deus é esta: Que creiais naquele que ele enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '30', 'Disseram-lhe pois: Que sinal pois fazes tu, para que o vejamos, e creiamos em ti? Que operas tu?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '31', 'Nossos pais comeram o maná no deserto, como está escrito: Deu-lhes a comer o pão do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '32', 'Disse-lhes pois Jesus: Na verdade, na verdade vos digo: Moisés não vos deu o pão do céu; mas meu Pai vos dá o verdadeiro pão do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '33', 'Porque o pão de Deus é aquele que desce do céu e dá vida ao mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '34', 'Disseram-lhe pois: Senhor, dá-nos sempre desse pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '35', 'E Jesus lhes disse: Eu sou o pão da vida; aquele que vem a mim não terá fome; e quem crê em mim nunca terá sede.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '36', 'Mas já vos disse que também vós me vistes, e contudo não credes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '37', 'Todo o que o Pai me dá virá a mim, e o que vem a mim de maneira nenhuma o lançarei fora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '38', 'Porque eu desci do céu, não para fazer a minha vontade, mas a vontade daquele que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '39', 'E a vontade do Pai que me enviou é esta: que nenhum de todos aqueles que me deu se perca, mas que o ressuscite no último dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '40', 'Porquanto a vontade daquele que me enviou é esta: que todo aquele que vê o Filho, e crê nele, tenha a vida eterna; e eu o ressuscitarei no último dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '41', 'Murmuravam pois dele os judeus, porque dissera: Eu sou o pão que desceu do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '42', 'E diziam: Não é este Jesus, o filho de José, cujo pai e mãe nós conhecemos? Como pois diz ele: Desci do céu?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '43', 'Respondeu pois Jesus e disse-lhes: Não murmureis entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '44', 'Ninguém pode vir a mim, se o Pai que me enviou o não trouxer: e eu o ressuscitarei no último dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '45', 'Está escrito nos profetas: E serão todos ensinados por Deus. Portanto todo aquele que do Pai ouviu e aprendeu vem a mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '46', 'Não que alguém visse ao Pai, a não ser aquele que é de Deus: este tem visto ao Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '47', 'Na verdade, na verdade vos digo que aquele que crê em mim tem a vida eterna,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '48', 'Eu sou o pão da vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '49', 'Vossos pais comeram o maná no deserto, e morreram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '50', 'Este é o pão que desce do céu, para que o que dele comer não morra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '51', 'Eu sou o pão vivo que desceu do céu; se alguém comer deste pão, viverá para sempre: e o pão que eu der é a minha carne, que eu darei pela vida do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '52', 'Disputavam pois os judeus entre si, dizendo: Como nos pode dar este a sua carne a comer?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '53', 'Jesus pois lhes disse: Na verdade, na verdade vos digo que, se não comerdes a carne do Filho do homem, e não beberdes o seu sangue, não tereis vida em vós mesmos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '54', 'Quem come a minha carne e bebe o meu sangue tem a vida eterna, e eu o ressuscitarei no último dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '55', 'Porque a minha carne verdadeiramente é comida, e o meu sangue verdadeiramente é bebida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '56', 'Quem come a minha carne e bebe o meu sangue permanece em mim e eu nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '57', 'Assim como o Pai, que vive, me enviou, e eu vivo pelo Pai, assim, quem de mim se alimenta, também viverá por mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '58', 'Este é o pão que desceu do céu: não é o caso de vossos pais, que comeram o maná e morreram: quem comer este pão viverá para sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '59', 'Ele disse estas coisas na sinagoga, ensinando em Cafarnaúm.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '60', 'Muitos pois dos seus discípulos, ouvindo isto, disseram: Duro é este discurso; quem o pode ouvir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '61', 'Sabendo pois Jesus em si mesmo que os seus discípulos murmuravam disto, disse-lhes: Isto escandaliza-vos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '62', 'Que seria pois, se vísseis subir o Filho do homem para onde primeiro estava?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '63', 'O espírito é o que vivifica, a carne para nada aproveita; as palavras que eu vos disse são espírito e vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '64', 'Mas há alguns de vós que não crêem. Porque bem sabia Jesus, desde o princípio, quem eram os que não criam, e quem era o que o havia de entregar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '65', 'E dizia: Por isso eu vos disse que ninguém pode vir a mim, se por meu Pai lhe não for concedido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '66', 'Desde então muitos dos seus discípulos tornaram para trás, e já não andavam com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '67', 'Então disse Jesus aos doze: Quereis vós também retirar-vos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '68', 'Respondeu-lhe pois Simão Pedro: Senhor, para quem iremos nós? Tu tens as palavras da vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '69', 'E nós temos crido e conhecido que tu és o Cristo, o Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '70', 'Respondeu-lhe Jesus: Não vos escolhi a vós os doze? e um de vós é um diabo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '6', '71', 'E isto dizia ele de Judas Iscariotes, filho de Simão; porque este o havia de entregar, sendo um dos doze.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '1', 'E DEPOIS disto Jesus andava pela Galileia, e já não queria andar pela Judeia, pois os judeus procuravam matá-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '2', 'E estava próxima a festa dos judeus, a dos tabernáculos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '3', 'Disseram-lhe pois seus irmãos: Sai daqui, e vai para a Judeia, para que também os teus discípulos vejam as obras que fazes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '4', 'Porque não há ninguém que procure ser conhecido que faça coisa alguma em oculto. Se fazes estas coisas manifesta-te ao mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '5', 'Porque nem mesmo os seus irmãos criam nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '6', 'Disse-lhes pois Jesus: Ainda não é chegado o meu tempo, mas o vosso tempo sempre está pronto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '7', 'O mundo não vos pode aborrecer, mas ele me aborrece a mim, porquanto dele testifico que as suas obras são más.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '8', 'Subi vós a esta festa: eu não subo ainda a esta festa porque ainda o meu tempo não está cumprido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '9', 'E, havendo-lhes dito isto, ficou na Galileia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '10', 'Mas, quando seus irmãos tinham subido à festa, então subiu ele também não manifestamente, mas como em oculto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '11', 'Ora os judeus procuravam-no na festa e diziam: Onde está ele?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '12', 'E havia grande murmuração entre a multidão a respeito dele. Diziam alguns: Ele é bom. E outros diziam: Não, antes engana o povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '13', 'Todavia ninguém falava dele abertamente, por medo dos judeus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '14', 'Mas, no meio da festa, subiu Jesus ao templo, e ensinava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '15', 'E os judeus maravilhavam-se, dizendo: Como sabe este letras, não as tendo aprendido?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '16', 'Jesus lhes respondeu, e disse: A minha doutrina não é minha, mas daquele que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '17', 'Se alguém quiser fazer a vontade dele, pela mesma doutrina conhecerá se ela é de Deus, ou se eu falo de mim mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '18', 'Quem fala de si mesmo busca a sua própria glória, mas o que busca a glória daquele que o enviou, esse é verdadeiro, e não há nele injustiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '19', 'Não vos deu Moisés a lei? E nenhum de vós observa a lei. Porque procurais matar-me?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '20', 'A multidão respondeu, e disse: Tens demónio; quem procura matar-te?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '21', 'Respondeu Jesus e disse-lhes: Fiz uma só obra, e todos vos maravilhais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '22', 'Pelo motivo de que Moisés vos deu a circuncisão (não que fosse de Moisés; mas dos pais), no sábado circuncidais um homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '23', 'Se o homem recebe a circuncisão no sábado, para que a lei de Moisés não seja quebrantada, indignais-vos contra mim, porque no sábado curei de todo um homem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '24', 'Não julgueis segundo a aparência, mas julgai segundo a recta justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '25', 'Então alguns dos de Jerusalém diziam: Não é este o que procuram matar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '26', 'E ei-lo aí está falando abertamente, e nada lhe dizem. Porventura sabem verdadeiramente os príncipes que este é o Cristo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '27', 'Todavia bem sabemos de onde este é; mas quando vier o Cristo, ninguém saberá donde ele é.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '28', 'Clamava pois Jesus no templo, ensinando, e dizendo: Vós conheceis-me, e sabeis donde sou; e eu não vim de mim mesmo, mas aquele que me enviou é verdadeiro, o qual vós não conheceis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '29', 'Mas eu conheço-o, porque dele sou e ele me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '30', 'Procuravam pois prendê-lo, mas ninguém lançou mão dele, porque ainda não era chegada a sua hora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '31', 'E muitos da multidão creram nele, e diziam: Quando o Cristo vier, fará ainda mais sinais do que os que este tem feito?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '32', 'Os fariseus ouviram que a multidão murmurava dele estas coisas; e os fariseus e os principais dos sacerdotes mandaram servidores para o prenderem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '33', 'Disse-lhes pois Jesus: Ainda um pouco de tempo estou convosco, e depois vou para aquele que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '34', 'Vós me buscareis, e não me achareis; e onde eu estou vós não podeis vir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '35', 'Disseram pois os judeus uns para os outros: Para onde irá este, que o não acharemos? Irá porventura para os dispersos entre os gregos, e ensinará os gregos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '36', 'Que palavra é esta que disse: Buscar-me-eis, e não me achareis; e: Aonde eu estou vós não podeis ir?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '37', 'E no último dia, o grande dia da festa, Jesus pôs-se em pé, e clamou, dizendo: Se alguém tem sede, venha a mim, e beba.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '38', 'Quem crê em mim, como diz a Escritura, rios de água viva correrão do seu ventre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '39', 'E isto disse ele do Espírito que haviam de receber os que nele cressem; porque o Espírito Santo ainda não fora dado, por ainda Jesus não ter sido glorificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '40', 'Então muitos da multidão, ouvindo esta palavra diziam: Verdadeiramente este é o Profeta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '41', 'Outros diziam: Este é o Cristo; mas diziam outros: Vem pois o Cristo da Galileia?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '42', 'Não diz a Escritura que o Cristo vem da descendência de David, e de Belém, da aldeia donde era David?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '43', 'Assim entre o povo havia dissensão por causa dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '44', 'E alguns deles queriam prendê-lo, mas ninguém lançou mão dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '45', 'E os servidores foram ter com os principais dos sacerdotes e fariseus; e, eles lhes perguntaram: Porque o não trouxestes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '46', 'Responderam os servidores: Nunca homem algum falou assim como este homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '47', 'Responderam-lhe pois os fariseus: Também vós fostes enganados?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '48', 'Creu nele porventura algum dos principais ou dos fariseus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '49', 'Mas esta multidão, que não sabe a lei, é maldita.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '50', 'Nicodemos que era um deles (o que de noite fora ter com Jesus) disse-lhes:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '51', 'Porventura condena a nossa lei um homem sem primeiro o ouvir e ter conhecimento do que faz?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '52', 'Responderam eles, e disseram-lhe: És tu também da Galileia? Examina, e verás que da Galileia nenhum profeta surgiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '7', '53', 'E cada um foi para sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '1', 'PORÉM Jesus foi para o monte das Oliveiras;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '2', 'E pela manhã cedo tornou para o templo, e todo o povo vinha ter com ele, e, assentando-se, os ensinava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '3', 'E os escribas e fariseus trouxeram-lhe uma mulher apanhada em adultério;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '4', 'E, pondo-a no meio, disseram-lhe: Mestre, esta mulher foi apanhada, no próprio acto, adulterando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '5', 'E na lei nos mandou Moisés que as tais sejam apedrejadas. Tu pois que dizes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '6', 'Isto diziam eles, tentando-o, para que tivessem de que o acusar. Mas Jesus, inclinando-se, escrevia com o dedo na terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '7', 'E, como insistissem, perguntando-lhe, endireitou-se, e disse-lhes: Aquele que de entre vós está sem pecado seja o primeiro que atire pedra contra ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '8', 'E, tornando a inclinar-se, escrevia na terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '9', 'Quando ouviram isto, saíram um a um, a começar pelos mais velhos até aos últimos; ficou só Jesus e a mulher que estava no meio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '10', 'E, endireitando-se Jesus, e não vendo ninguém mais do que a mulher, disse-lhe: Mulher, onde estão aqueles teus acusadores? Ninguém te condenou?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '11', 'E ela disse: Ninguém, Senhor. E disse-lhe Jesus: Nem eu também te condeno: vai-te, e não peques mais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '12', 'Falou-lhes pois Jesus outra vez dizendo: Eu sou a luz do mundo; quem me segue não andará em trevas, mas terá a luz da vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '13', 'Disseram-lhe pois os fariseus: Tu testificas de ti mesmo: o teu testemunho não é verdadeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '14', 'Respondeu Jesus, e disse-lhes: Ainda que eu testifico de mim mesmo, o meu testemunho é verdadeiro, porque sei donde vim, e para onde vou; mas vós não sabeis donde venho, nem para onde vou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '15', 'Vós julgais segundo a carne, eu a ninguém julgo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '16', 'E, se na verdade julgo, o meu juízo é verdadeiro, porque não sou eu só, mas eu e o Pai que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '17', 'E na vossa lei está também escrito que o testemunho de dois homens é verdadeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '18', 'Eu sou o que testifico de mim mesmo, e de mim testifica também o Pai que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '19', 'Disseram-lhe pois: Onde está teu Pai? Jesus respondeu: Não me conheceis a mim, nem a meu Pai; se vós me conhecêsseis a mim; também conheceríeis a meu Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '20', 'Estas palavras disse Jesus no lugar do tesouro, ensinando no templo, e ninguém o prendeu, porque ainda não era chegada a sua hora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '21', 'Disse-lhes pois Jesus outra vez: Eu retiro-me, e buscar-me-eis, e morrereis no vosso pecado. Para onde eu vou não podeis vós vir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '22', 'Diziam pois os judeus: Porventura quererá matar-se a si mesmo, pois diz: Para onde eu vou não podeis vós vir?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '23', 'E dizia-lhes: Vós sois de baixo, eu sou de cima; vós sois deste mundo, eu não sou deste mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '24', 'Por isso vos disse que morrereis em vossos pecados, porque, se não crerdes que eu sou, morrereis em vossos pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '25', 'Disseram-lhe pois: Quem és tu? Jesus lhes disse: Isso mesmo que já desde o princípio vos disse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '26', 'Muito tenho que dizer e julgar de vós, mas aquele que me enviou é verdadeiro; e o que dele tenho ouvido isso falo ao mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '27', 'Mas não entenderam que ele lhes falava do Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '28', 'Disse-lhes pois Jesus: Quando levantardes o Filho do homem, então conhecereis quem eu sou, e que nada faço por mim mesmo; mas falo como o Pai me ensinou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '29', 'E aquele que me enviou está comigo; o Pai não me tem deixado só porque eu faço sempre o que lhe agrada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '30', 'Dizendo ele estas coisas, muitos creram nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '31', 'Jesus dizia pois aos judeus que criam nele: Se vós permanecerdes na minha palavra, verdadeiramente sereis meus discípulos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '32', 'E conhecereis a verdade, e a verdade vos libertará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '33', 'Responderam-lhe: Somos descendência de Abraão, e nunca servimos a ninguém; como dizes tu: Sereis livres?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '34', 'Respondeu-lhes Jesus: Em verdade, em verdade, vos digo que todo aquele que comete pecado é servo do pecado:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '35', 'Ora o servo não fica para sempre em casa; o Filho fica para sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '36', 'Se pois o Filho vos libertar, verdadeiramente sereis livres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '37', 'Bem sei que sois descendência de Abraão; contudo, procurais matar-me, porque a minha palavra não entra em vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '38', 'Eu falo do que vi junto de meu Pai, e vós fazeis o que também vistes junto de vosso pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '39', 'Responderam, e disseram-lhe: Nosso Pai é Abraão. Jesus disse-lhes: Se fôsseis filhos de Abraão, faríeis as obras de Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '40', 'Mas agora procurais matar-me, a mim, homem que vos tem dito a verdade que de Deus tem ouvido; Abraão não fez isto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '41', 'Vós fazeis as obras de vosso Pai. Disseram-lhe pois: Nós não somos nascidos de prostituição; temos um Pai, que é Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '42', 'Disse-lhes pois Jesus: Se Deus fosse o vosso Pai, certamente me amaríeis, pois que eu saí, e vim de Deus; não vim de mim mesmo, mas ele me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '43', 'Porque não entendeis a minha linguagem? Por não poderdes ouvir a minha palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '44', 'Vós tendes por pai ao diabo, e quereis satisfazer os desejos de vosso pai: ele foi homicida desde o princípio, e não se firmou na verdade, porque não há verdade nele; quando, ele profere mentira, fala do que é próprio, porque é mentiroso, e pai da mentira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '45', 'Mas, porque vos digo a verdade, não me credes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '46', 'Quem de entre vós me convence de pecado? E se vos digo a verdade, porque não credes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '47', 'Quem é de Deus escuta as palavras de Deus; por isso vós não as escutais, porque não sois de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '48', 'Responderam pois os judeus, e disseram-lhe: Não dizemos nós bem que és samaritano, e que tens demónio?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '49', 'Jesus respondeu: Eu não tenho demónio, antes honro a meu Pai, e vós me desonrais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '50', 'Eu não busco a minha glória; há quem a busque, e julgue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '51', 'Em verdade, em verdade vos digo que, se alguém guardar a minha palavra, nunca verá a morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '52', 'Disseram-lhe pois os judeus: Agora reconhecemos que tens demónio. Morreu Abraão e os profetas; e tu dizes: Se alguém guardar a minha palavra, nunca provará a morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '53', 'És tu maior do que o nosso pai Abraão, que morreu? E também os profetas morreram: quem te fazes tu ser?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '54', 'Jesus respondeu: Se eu me glorifico a mim mesmo, a minha glória não é nada: quem me glorifica é meu Pai, o qual dizeis que é vosso Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '55', 'E vós não o conheceis, mas eu conheço-o: e, se disser que o não conheço, serei mentiroso como vós; mas conheço-o e guardo a sua palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '56', 'Abraão, vosso pai, exultou por ver o meu dia, e viu-o, e alegrou-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '57', 'Disseram-lhe pois os judeus: Ainda não tens cinquenta anos, e viste Abraão?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '58', 'Disse-lhes Jesus: Em verdade, em verdade vos digo que antes que Abraão existisse eu sou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '8', '59', 'Então pegaram em pedras para lhe atirarem; mas Jesus ocultou-se, e saiu do templo, passando pelo meio deles, e assim se retirou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '1', 'E, passando Jesus, viu um homem cego de nascença.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '2', 'E os seus discípulos lhe perguntaram dizendo: Rabi quem pecou, este ou seus pais, para que nascesse cego?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '3', 'Jesus respondeu: Nem ele pecou nem seus pais; mas foi assim para que se manifestem nele as obras de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '4', 'Convém que eu faça as obras daquele que me enviou, enquanto é dia: a noite vem, quando ninguém pode trabalhar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '5', 'Enquanto estou no mundo, sou a luz do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '6', 'Tendo dito isto, cuspiu na terra, e com a saliva fez lodo, e untou com o lodo os olhos do cego,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '7', 'E disse-lhe: Vai, lava-te no tanque de Siloé (que significa o Enviado). Foi pois, e lavou-se, e voltou vendo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '8', 'Então os vizinhos, e aqueles que dantes tinham visto que era cego, diziam: Não é este aquele que estava assentado e mendigava?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '9', 'Uns diziam: É este. E outros: Parece-se com ele. Ele dizia: Sou eu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '10', 'Diziam-lhe pois: Como se te abriram os olhos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '11', 'Ele respondeu, e disse: O homem, chamado Jesus, fez lodo, e untou-me os olhos, e disse-me: Vai ao tanque de Siloé, e lava-te. Então fui, e lavei-me, e vi.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '12', 'Disseram-lhe pois: Onde está ele? Respondeu: Não sei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '13', 'Levaram pois aos fariseus o que dantes era cego.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '14', 'E era sábado, quando Jesus fez o lodo e lhe abriu os olhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '15', 'Tornaram pois também os fariseus a perguntar-lhe como vira, e ele lhes disse: Pôs-me lodo sobre os olhos, lavei-me, e vejo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '16', 'Então alguns dos fariseus diziam: este homem não é de Deus; pois não guarda o sábado. Diziam outros: Como pode um homem pecador fazer tais sinais? E havia dissensão entre eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '17', 'Tornaram pois a dizer ao cego: Tu que dizes daquele que te abriu os olhos? E ele respondeu: Que é profeta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '18', 'Os judeus, porém, não creram que ele tivesse sido cego, e que agora visse, enquanto não chamaram os pais do que agora via.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '19', 'E perguntaram-lhes, dizendo: É este o vosso filho, que vós dizeis ter nascido cego? Como pois vê agora?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '20', 'Seus pais lhes responderam, e disseram: Sabemos que este é o nosso filho, e que nasceu cego;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '21', 'Mas como agora vê, não sabemos; ou quem lhe tenha aberto os olhos, não sabemos: tem idade, perguntai-lho a ele mesmo; e ele falará por si mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '22', 'Seus pais disseram isto, porque temiam os judeus. Porquanto já os judeus tinham resolvido que, se alguém confessasse ser ele o Cristo, fosse expulso da sinagoga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '23', 'Por isso é que seus pais disseram: Tem idade, perguntai-lho a ele mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '24', 'Chamaram pois pela segunda vez o homem que tinha sido cego, e disseram-lhe: Dá glória a Deus; nós sabemos que esse homem é pecador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '25', 'Respondeu ele pois, e disse: Se é pecador, não sei; uma coisa sei, e é que, havendo eu sido cego, agora vejo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '26', 'E tornaram a dizer-lhe: Que te fez ele? Como te abriu os olhos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '27', 'Respondeu-lhes: Já vo-lo disse, e não ouvistes: para que o quereis tornar a ouvir? Quereis vós porventura fazer-vos também seus discípulos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '28', 'Então o injuriaram, e disseram: Discípulo dele sejas tu; nós, porém, somos discípulos de Moisés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '29', 'Nós bem sabemos que Deus falou a Moisés, mas este não sabemos donde é.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '30', 'O homem respondeu, e disse-lhes: Nisto pois está a maravilha, que vós não saibais donde ele é, e me abrisse os olhos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '31', 'Ora nós sabemos que Deus não ouve a pecadores; mas, se alguém é temente a Deus, e faz a sua vontade, a esse ouve.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '32', 'Desde o princípio do mundo nunca se ouviu que alguém abrisse os olhos a um cego de nascença.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '33', 'Se este não fosse de Deus, nada poderia fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '34', 'Responderam eles, e disseram-lhe: Tu és nascido todo em pecados, e nos ensinas a nós? E expulsaram-no.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '35', 'Jesus ouviu que o tinham expulsado, e, encontrando-o, disse-lhe: Crês tu no Filho de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '36', 'Ele respondeu, e disse: Quem é ele Senhor, para que nele creia?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '37', 'E Jesus lhe disse: Tu já o tens visto, e é aquele que fala contigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '38', 'Ele disse: Creio, Senhor. E o adorou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '39', 'E disse-lhe Jesus: Eu vim a este mundo para juízo, a fim de que os que não vêem vejam, e os que vêem sejam cegos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '40', 'Aqueles dos fariseus, que estavam com ele, ouvindo isto, disseram-lhe: Também nós somos cegos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '9', '41', 'Disse-lhes Jesus: Se fosseis cegos, não teríeis pecado; mas como agora dizeis: Vemos, por isso o vosso pecado permanece.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '1', 'NA VERDADE, na verdade vos digo que aquele que não entra pela porta no curral das ovelhas, mas sobe por outra parte, é ladrão e salteador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '2', 'Aquele, porém, que entra pela porta é o pastor das ovelhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '3', 'A este o porteiro abre, e as ovelhas ouvem a sua voz, e chama pelo nome às suas ovelhas, e as traz para fora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '4', 'E, quando tira para fora as suas ovelhas, vai adiante delas, e as ovelhas o seguem, porque conhecem a sua voz;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '5', 'Mas de modo nenhum seguirão o estranho, antes fugirão dele, porque não conhecem a voz dos estranhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '6', 'Jesus disse-lhes esta parábola; mas eles não entenderam o que era que lhes dizia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '7', 'Tornou pois Jesus a dizer-lhes: Em verdade vos digo que eu sou a porta das ovelhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '8', 'Todos quantos vieram antes de mim são ladrões e salteadores; mas as ovelhas não os ouviram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '9', 'Eu sou a porta; se alguém entrar por mim, salvar-se-á, e entrará, e sairá, e achará pastagens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '10', 'O ladrão não vem senão a roubar, a matar, e a destruir: eu vim para que tenham vida, e a tenham com abundância.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '11', 'Eu sou o bom Pastor: o bom Pastor dá a sua vida pelas ovelhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '12', 'Mas o mercenário, e o que não é pastor, de quem não são as ovelhas vê vir o lobo, e deixa as ovelhas, e foge; e o lobo as arrebata e dispersa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '13', 'Ora o mercenário foge, porque é mercenário, e não tem cuidado das ovelhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '14', 'Eu sou o bom Pastor, e conheço as minhas ovelhas, e das minhas ovelhas sou conhecido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '15', 'Assim como o Pai me conhece a mim, também eu conheço o Pai, e dou a minha vida pelas ovelhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '16', 'Ainda tenho outras ovelhas que não são deste aprisco; também me convém agregar estas, e elas ouvirão a minha voz, e haverá um rebanho e um Pastor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '17', 'Por isto o Pai me ama, porque dou a minha vida para tornar a tomá-la.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '18', 'Ninguém ma tira de mim, mas eu de mim mesmo a dou; tenho poder para a dar, e poder para tornar a tomá-la. Este mandamento recebi de meu Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '19', 'Tornou pois a haver divisão entre os judeus por causa destas palavras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '20', 'E muitos deles diziam: Tem demónio, e está fora de si; porque o ouvis?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '21', 'Diziam outros: Estas palavras não são de endemoninhado; pode porventura um demónio abrir os olhos aos cegos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '22', 'E em Jerusalém havia a festa da dedicação, e era inverno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '23', 'E Jesus andava passeando no templo, no alpendre de Salomão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '24', 'Rodearam-no pois os judeus, e disseram-lhe: Até quando terás a nossa alma suspensa? Se tu és o Cristo, di-no-lo abertamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '25', 'Respondeu-lhes Jesus: Já vo-lo tenho dito, e não o credes. As obras que eu faço, em nome de meu Pai, essas testificam de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '26', 'Mas vós não credes porque não sois das minhas ovelhas, como já vo-lo tenho dito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '27', 'As minhas ovelhas ouvem a minha voz, e eu conheço-as, e elas me seguem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '28', 'E dou-lhes a vida eterna, e nunca hão-de perecer, e ninguém as arrebatará da minha mão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '29', 'Meu Pai, que mas deu, é maior do que todos; e ninguém pode arrebatá-las da mão de meu Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '30', 'Eu e o Pai somos um.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '31', 'Os judeus pegaram então outra vez em pedras para o apedrejar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '32', 'Respondeu-lhes Jesus: Tenho-vos mostrado muitas obras boas procedentes de meu Pai; por qual destas obras me apedrejais?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '33', 'Os judeus responderam, dizendo-lhe: Não te apedrejamos por alguma obra boa, mas pela blasfémia; porque, sendo tu homem, te fazes Deus a ti mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '34', 'Respondeu-lhes Jesus: Não está escrito na vossa lei: Eu disse: Sois deuses?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '35', 'Pois, se a lei chamou deuses àqueles a quem a palavra de Deus foi dirigida (e a Escritura não pode ser anulada),');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '36', 'Aquele a quem o Pai santificou, e enviou ao mundo, vós dizeis: Blasfemas; porque disse: Sou Filho de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '37', 'Se não faço as obras de meu Pai, não me acrediteis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '38', 'Mas se as faço, e não credes em mim, crede nas obras; para que conheçais e acrediteis que o Pai está em mim e eu nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '39', 'Procuravam pois prendê-lo outra vez,mas ele escapou-se de suas mãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '40', 'E retirou-se outra vez para além do Jordão, para o lugar onde João tinha primeiramente baptizado; e ali ficou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '41', 'E muitos iam ter com ele, e diziam: Na verdade João não fez sinal algum, mas tudo quanto João disse deste era verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '10', '42', 'E muitos ali creram nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '1', 'Estava então enfermo um certo Lázaro, de Betânia, aldeia de Maria e de sua irmã Marta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '2', 'E Maria era aquela que tinha ungido o SENHOR com unguento, e lhe tinha enxugado os pés com os seus cabelos; cujo irmão Lázaro estava enfermo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '3', 'Mandaram-lhe pois suas irmãs, dizer: Senhor, eis que está enfermo aquele que tu amas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '4', 'E Jesus, ouvindo isto, disse: Esta enfermidade não é para morte, mas para glória de Deus; para que o Filho de Deus seja glorificado por ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '5', 'Ora Jesus amava a Marta, e a sua irmã, e a Lázaro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '6', 'Ouvindo pois que estava enfermo, ficou ainda dois dias no lugar onde estava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '7', 'Depois disto, disse aos seus discípulos: Vamos outra vez para a Judeia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '8', 'Disseram-lhe os discípulos: Rabi, ainda agora os judeus procuravam apedrejar-te, e tornas para lá?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '9', 'Jesus respondeu: Não há doze horas no dia? Se alguém andar de dia, não tropeça, porque vê a luz deste mundo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '10', 'Mas, se andar de noite, tropeça, porque nele não há luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '11', 'Assim falou; e depois disse-lhes: Lázaro, o nosso amigo, dorme, mas vou despertá-lo do sono.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '12', 'Disseram pois os seus discípulos, Senhor, se dorme, estará salvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '13', 'Mas Jesus dizia isto da sua morte; eles, porém, cuidavam que falava do repouso do sono.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '14', 'Então Jesus disse-lhes claramente: Lázaro está morto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '15', 'E folgo por amor de vós, de que eu lá não estivesse, para que acrediteis: mas vamos ter com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '16', 'Disse pois Tomé, chamado Dídimo, aos condiscípulos: Vamos nós também para morrermos com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '17', 'Chegando pois Jesus, achou que já havia quatro dias que estava na sepultura');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '18', '(Ora Betânia distava de Jerusalém quase quinze estádios).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '19', 'E muitos dos judeus tinham ido consolar a Marta e a Maria, acerca de seu irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '20', 'Ouvindo pois Marta que Jesus vinha, saiu-lhe ao encontro: Maria, porém, ficou assentada em casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '21', 'Disse pois Marta a Jesus: Senhor, se tu estivesses aqui, meu irmão não teria morrido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '22', 'Mas também agora sei que: tudo quanto pedires a Deus, Deus to concederá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '23', 'Disse-lhe Jesus: Teu irmão há-de ressuscitar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '24', 'Disse-lhe Marta: Eu sei que há-de ressuscitar na ressurreição do último dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '25', 'Disse-lhe Jesus: Eu sou a ressurreição e a vida; quem crê em mim ainda que esteja morto, viverá;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '26', 'E todo aquele que vive, e crê em mim, nunca morrerá. Crês tu isto?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '27', 'Disse-lhe ela: Sim, Senhor, creio que tu és o Cristo, o Filho de Deus, que havia de vir ao mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '28', 'E, dito isto, partiu, e chamou em segredo a Maria, sua irmã, dizendo: O Mestre está cá, e chama-te.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '29', 'Ela, ouvindo isto, levantou-se logo, e foi ter com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '30', '(Ainda Jesus não tinha chegado à aldeia, mas estava no lugar onde Marta o encontrara.)');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '31', 'Vendo pois os judeus, que estavam com ela em casa e a consolavam, que Maria apressadamente se levantara e saíra, seguiram-na, dizendo: Vai ao sepulcro para chorar ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '32', 'Tendo pois Maria chegado aonde Jesus estava, e vendo-o, lançou-se aos seus pés, dizendo-lhe: Senhor, se tu estivesses aqui, meu irmão não teria morrido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '33', 'Jesus pois, quando a viu chorar, e também chorando os judeus que com ela vinham, moveu-se muito em espírito, e perturbou-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '34', 'E disse: Onde o pusestes? Disseram-lhe: Senhor, vem e vê.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '35', 'Jesus chorou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '36', 'Disseram pois os judeus: Vede como o amava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '37', 'E alguns deles disseram: Não podia ele, que abriu os olhos ao cego, fazer também com que este não morresse?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '38', 'Jesus pois, movendo-se outra vez muito em si mesmo, veio ao sepulcro; e era uma caverna, e tinha uma pedra posta sobre ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '39', 'Disse Jesus: Tirai a pedra. Marta, irmã do defunto, disse-lhe: Senhor, já cheira mal, porque é já de quatro dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '40', 'Disse-lhe Jesus: Não te hei dito que, se creres, verás a glória de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '41', 'Tiraram pois a pedra. E Jesus, levantando os olhos para o céu; disse: Pai, graças te dou, por me haveres ouvido:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '42', 'Eu bem sei que sempre me ouves, mas eu disse isto por causa da multidão que está em redor, para que creiam que tu me enviaste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '43', 'E, tendo dito isto, clamou com grande voz: Lázaro, sai para fora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '44', 'E o defunto saiu; tendo as mãos e os pés ligados com faixas, e o seu rosto envolto num lenço. Disse-lhes Jesus: Desligai-o, e deixai-o ir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '45', 'Muitos pois dentre os judeus, que tinham vindo a Maria, e que tinham visto o que Jesus fizera, creram nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '46', 'Mas alguns deles foram ter com os fariseus, e disseram-lhe o que Jesus tinha feito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '47', 'Depois os principais dos sacerdotes e os fariseus formaram conselho e diziam: Que faremos? porquanto este homem faz muitos sinais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '48', 'Se o deixamos assim, todos crerão nele, e virão os romanos, e tirar-nos-ão o nosso lugar e a nação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '49', 'E Caifás, um deles que era sumo sacerdote naquele ano, lhes disse: Vós nada sabeis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '50', 'Nem considerais que nos convém que um homem morra pelo povo, e que não pereça toda a nação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '51', 'Ora ele não disse isto de si mesmo, mas, sendo o sumo sacerdote naquele ano, profetizou que Jesus devia morrer pela nação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '52', 'E não somente pela nação, mas também para reunir em um corpo os filhos de Deus, que andavam dispersos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '53', 'Desde aquele dia, pois, consultavam-se para o matarem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '54', 'Jesus, pois, já não andava manifestamente entre os judeus, mas retirou-se dali para a terra junto ao deserto, para uma cidade chamada Efraim; e ali andava com os seus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '55', 'E estava próxima a páscoa dos judeus, e muitos daquela região subiram a Jerusalém antes da páscoa para se purificarem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '56', 'Buscavam pois a Jesus, e diziam uns aos outros, estando no templo: Que vos parece? Não virá à festa?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '11', '57', 'Ora os principais dos sacerdotes e os fariseus tinham dado ordem para que, se alguém soubesse onde ele estava, o denunciasse, para o prenderem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '1', 'FOI pois Jesus seis dias antes da páscoa a Betânia, onde estava Lázaro, o que falecera, e a quem ressuscitara dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '2', 'Fizeram-lhe pois ali uma ceia, e Marta servia, e Lázaro era um dos que estavam à mesa com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '3', 'Então Maria tomando um arrátel de unguento de nardo puro, de muito preço, ungiu os pés de Jesus, e enxugou-lhe os pés com os seus cabelos; e encheu-se a casa do cheiro do unguento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '4', 'Então um dos seus discípulos, Judas Iscariotes, filho de Simão, o que havia de traí-lo, disse:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '5', 'Porque não se vendeu este unguento por trezentos dinheiros e não se deu aos pobres?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '6', 'Ora ele disse isto, não pelo cuidado que tivesse dos pobres, mas porque era ladrão, e tinha a bolsa, e tirava o que ali se lançava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '7', 'Disse pois Jesus: Deixai-a; para o dia da minha sepultura guardou isto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '8', 'Porque os pobres sempre os tendes convosco; mas a mim nem sempre me tendes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '9', 'E muita gente dos judeus soube que ele estava ali; e foram, não só por causa de Jesus, mas também para ver a Lázaro, a quem ressuscitara dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '10', 'E os principais dos sacerdotes tomaram deliberação para matar também a Lázaro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '11', 'Porque muitos dos judeus, por causa dele, iam e criam em Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '12', 'No dia seguinte, ouvindo uma grande multidão, que viera à festa, que Jesus vinha a Jerusalém,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '13', 'Tomaram ramos de palmeiras, e saíram-lhe ao encontro, e clamavam Hosana: Bendito o rei de Israel que vem em nome do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '14', 'E achou Jesus um jumentinho, e assentou-se sobre ele, como está escrito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '15', 'Não temas ó filha de Sião; eis que o teu Rei vem assentado sobre o filho de uma jumenta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '16', 'Os seus discípulos, porém não entenderam isto no principio: mas, quando Jesus foi glorificado, então se lembraram de que isto estava escrito dele, e que isto lhe fizeram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '17', 'A multidão, pois, que estava com ele quando Lázaro foi chamado da sepultura, testificava que ele o ressuscitara dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '18', 'Pelo que a multidão lhe saiu ao encontro, porque tinham ouvido que ele fizera este sinal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '19', 'Disseram pois os fariseus entre si: Vedes que nada aproveitais? eis que toda a gente vai após ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '20', 'Ora havia alguns gregos, entre os que tinham subido a adorar no dia da festa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '21', 'Estes, pois, dirigiram-se a Filipe, que era de Betsaida da Galileia, e rogaram-lhe, dizendo: Senhor, queríamos ver a Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '22', 'Filipe foi dizê-lo a André, e então André e Filipe o disseram a Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '23', 'E Jesus lhes respondeu, dizendo: É chegada a hora em que o Filho do homem há-de ser glorificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '24', 'Na verdade, na verdade vos digo que, se o grão de trigo, caindo na terra, não morrer, fica ele só; mas se morrer, dá muito fruto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '25', 'Quem ama a sua vida perdê-la-á, e quem neste mundo aborrece a sua vida, guardá-la-á para a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '26', 'Se alguém me serve, siga-me, e, onde eu estiver, ali estará também o meu servo. E, se alguém me servir, meu Pai o honrará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '27', 'Agora a minha alma está perturbada; e que direi eu? Pai, salva-me desta hora; mas para isto vim a esta hora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '28', 'Pai, glorifica o teu nome. Então veio uma voz do céu, que dizia: Já o tenho glorificado, e outra vez o glorificarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '29', 'Ora a multidão que ali estava, e que a tinha ouvido, dizia que havia sido um trovão. Outros diziam: Um anjo lhe falou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '30', 'Respondeu Jesus, e disse: Não veio esta voz por amor de mim, mas por amor de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '31', 'Agora é o juízo deste mundo; agora será expulso o príncipe deste mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '32', 'E eu, quando for levantado da terra, todos atrairei a mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '33', 'E dizia isto, significando de que morte havia de morrer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '34', 'Respondeu-lhe a multidão. Nós temos ouvido da lei, que o Cristo permanece para sempre; e como dizes tu que convém que o Filho do homem seja levantado? Quem é esse Filho do homem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '35', 'Disse-lhes pois Jesus: A luz ainda está convosco por um pouco de tempo; andai enquanto tendes luz, para que as trevas vos não apanhem; pois quem anda nas trevas não sabe para onde vai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '36', 'Enquanto tendes luz, crede na luz, para que sejais filhos da luz. Estas coisas disse Jesus; e, retirando-se, escondeu-se deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '37', 'E, ainda que tinha feito tantos sinais diante deles, não criam nele;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '38', 'Para que se cumprisse a palavra do profeta Isaías, que diz: Senhor quem creu na nossa pregação? e a quem foi revelado o braço do SENHOR?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '39', 'Por isso não podiam crer, pelo que Isaías disse outra vez.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '40', 'Cegou-lhes os olhos, e endureceu-lhes o coração, a fim de que não vejam com os olhos, e compreendam no coração, e se convertam, e eu os cure.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '41', 'Isaías disse isto quando viu a sua glória e falou dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '42', 'Apesar de tudo, até muitos dos principais creram nele; mas não o confessavam por causa dos fariseus, para não serem expulsos da sinagoga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '43', 'Porque amavam mais a glória dos homens do que a glória de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '44', 'E Jesus clamou, e disse: Quem crê em mim, crê, não em mim, mas naquele que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '45', 'E quem me vê a mim, vê aquele que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '46', 'Eu sou a luz que vim ao mundo, para que todo aquele que crê em mim não permaneça nas trevas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '47', 'E, se alguém ouvir as minhas palavras, e não crer, eu não o julgo porque eu vim, não para julgar o mundo, mas para salvar o mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '48', 'Quem me rejeitar a mim, e não receber as minhas palavras, já tem quem o julgue; a palavra que tenho pregado, essa o há-de julgar no último dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '49', 'Porque eu não tenho falado de mim mesmo; mas o Pai, que me enviou, ele me deu mandamento sobre o que hei-de dizer e sobre o que hei-de falar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '12', '50', 'E sei que o seu mandamento é a vida eterna. Portanto, o que eu falo, falo-o como o Pai mo tem dito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '1', 'ORA, antes da festa da páscoa, sabendo Jesus que já era chegada a sua hora de passar deste mundo para o Pai, como havia amado os seus que estavam no mundo, amou-os até ao fim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '2', 'E, acabada a ceia, tendo já o diabo posto no coração de Judas Iscariotes, filho de Simão, que o traísse,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '3', 'Jesus, sabendo que o Pai tinha depositado nas suas mãos todas as coisas, e que havia saído de Deus e ia para Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '4', 'Levantou-se da ceia, tirou os vestidos, e, tomando uma toalha, cingiu-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '5', 'Depois deitou água numa bacia, e começou a lavar os pés aos discípulos, e a enxugar-lhos com a toalha com que estava cingido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '6', 'Aproximou-se pois de Simão Pedro, que lhe disse: Senhor, tu lavas-me os pés a mim?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '7', 'Respondeu Jesus, e disse-lhe: O que eu faço não o sabes tu agora, mas tu o saberás depois.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '8', 'Disse-lhe Pedro: Nunca me lavarás os pés. Respondeu-lhe Jesus: Se eu te não lavar, não tens parte comigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '9', 'Disse-lhe Simão Pedro: Senhor, não só os meus pés, mas também as mãos e a cabeça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '10', 'Disse-lhe Jesus: Aquele que está lavado não necessita de lavar senão os pés, pois no mais todo está limpo. Ora vós estais limpos, mas não todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '11', 'Porque bem sabia ele quem o havia de trair; por isso disse: Nem todos estais limpos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '12', 'Depois que lhes lavou os pés, e tomou os seus vestidos, e se assentou outra vez à mesa, disse-lhes: Entendeis o que vos tenho feito?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '13', 'Vós me chamais Mestre e Senhor, e dizeis bem, porque eu o sou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '14', 'Ora se eu, Senhor e Mestre, vos lavei os pés, vós deveis também lavar os pés uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '15', 'Porque eu vos dei o exemplo, para que, como eu vos fiz, façais vós também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '16', 'Na verdade, na verdade vos digo que não é o servo maior do que o seu senhor, nem o enviado maior do que aquele que o enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '17', 'Se sabeis estas coisas, bem-aventurados sois se as fizerdes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '18', 'Não falo de todos vós; eu bem sei os que tenho escolhido; mas para que se cumpra a Escritura: o que come o pão comigo, levantou contra mim o seu calcanhar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '19', 'Desde agora vo-lo digo, antes que aconteça, para que, quando acontecer, acrediteis que eu sou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '20', 'Na verdade, na verdade vos digo: se alguém receber o que eu enviar, me recebe a mim, e quem me receber a mim recebe aquele que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '21', 'Tendo Jesus dito isto, turbou-se em espírito, e afirmou, dizendo: Na verdade, na verdade vos digo que um de vós me há-de trair.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '22', 'Então os discípulos olhavam uns para os outros, duvidando de quem ele falava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '23', 'Ora um de seus discípulos, aquele a quem Jesus amava, estava reclinado no seio de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '24', 'Então Simão Pedro fez sinal a este, para que perguntasse quem era aquele de quem ele falava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '25', 'E, inclinando-se ele sobre o peito de Jesus, disse-lhe: Senhor, quem é?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '26', 'Jesus respondeu: É aquele a quem eu der o bocado molhado. E, molhando o bocado, o deu a Judas Iscariotes, filho de Simão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '27', 'E após o bocado, entrou nele Satanás. Disse pois Jesus: O que fazes, fá-lo depressa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '28', 'E nenhum dos que estavam assentados à mesa compreendeu a que propósito lhe dissera isto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '29', 'Porque, como Judas tinha a bolsa, pensavam alguns que Jesus lhe tinha dito: Compra o que nos é necessário para a festa; ou que desse alguma coisa aos pobres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '30', 'E, tendo Judas tomado o bocado, saiu logo. E era já noite.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '31', 'Tendo ele pois saído, disse Jesus: Agora é glorificado o Filho do homem, e Deus é glorificado nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '32', 'Se Deus é glorificado nele, também Deus o glorificará em si mesmo, e logo o há-de glorificar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '33', 'Filhinhos, ainda por um pouco estou convosco. Vós me buscareis, e, como tinha dito aos judeus: para onde eu vou não podeis vós ir: eu vo-lo digo também agora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '34', 'Um novo mandamento vos dou: Que vos ameis uns aos outros; como eu vos amei a vós, que também vós uns aos outros vos ameis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '35', 'Nisto todos conhecerão que sois meus discípulos, se vos amardes uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '36', 'Disse-lhe Simão Pedro: Senhor, para onde vais? Jesus lhe respondeu: Para onde eu vou não podes agora seguir-me, mas depois me seguirás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '37', 'Disse-lhe Pedro: Porque não posso seguir-te agora? Por ti darei a minha vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '13', '38', 'Respondeu-lhe Jesus: Tu darás a tua vida por mim? Na verdade, na verdade te digo: não cantará o galo enquanto me não tiveres negado três vezes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '1', 'NÃO se turbe o vosso coração: credes em Deus, crede também em mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '2', 'Na casa de meu Pai há muitas moradas; se não fosse assim, eu vo-lo teria dito: vou preparar-vos lugar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '3', 'E, se eu for, e vos preparar lugar, virei outra vez, e vos levarei para mim mesmo, para que onde eu estiver estejais vós também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '4', 'Mesmo vós sabeis para onde vou, e conheceis o caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '5', 'Disse-lhe Tomé: Senhor, nós não sabemos para onde vais; e como podemos saber o caminho?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '6', 'Disse-lhe Jesus: Eu sou o caminho, e a verdade e a vida. Ninguém vem ao Pai, senão por mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '7', 'Se vós me conhecêsseis a mim, também conheceríeis a meu Pai; e já desde agora o conheceis, e o tendes visto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '8', 'Disse-lhe Filipe: Senhor, mostra-nos o Pai, o que nos basta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '9', 'Disse-lhe Jesus: Estou há tanto tempo convosco, e não me tendes conhecido, Filipe? Quem me vê a mim vê o Pai: e como dizes tu: Mostra-nos o Pai?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '10', 'Não crês tu que eu estou no Pai, e que o Pai está em mim? As palavras que eu vos digo não as digo de mim mesmo, mas o Pai, que está em mim, é quem faz as obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '11', 'Crede-me que estou no Pai, e o Pai em mim: Crede-me, ao menos, por causa das mesmas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '12', 'Na verdade, na verdade vos digo que aquele que crê em mim também fará as obras que eu faço, e ás fará maiores do que estas; porque eu vou para meu Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '13', 'E tudo quanto pedirdes em meu nome eu o farei, para que o Pai seja glorificado no Filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '14', 'Se pedirdes alguma coisa em meu nome, eu o farei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '15', 'Se me amardes, guardareis os meus mandamentos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '16', 'E eu rogarei ao Pai, e ele vos dará outro Consolador, para que fique convosco para sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '17', 'O Espírito de verdade, que o mundo não pode receber, porque não o vê nem o conhece: mas vós o conheceis, porque habita convosco, e estará em vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '18', 'Não vos deixarei órfãos; voltarei para vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '19', 'Ainda um pouco, e o mundo não me verá mais, mas vós me vereis; porque eu vivo, e vós vivereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '20', 'Naquele dia conhecereis que estou em meu Pai, e vós em mim, e eu em vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '21', 'Aquele que tem os meus mandamentos e os guarda esse é o que me ama; e aquele que me ama será amado de meu Pai, e eu o amarei, e me manifestarei a ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '22', 'Disse-lhe Judas (não o Iscariotes): Senhor, donde vem que te hás-de manifestar a nós, e não ao mundo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '23', 'Jesus respondeu, e disse-lhe: Se alguém me ama, guardará a minha palavra, e meu Pai o amará, e viremos para ele, e faremos nele morada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '24', 'Quem me não ama não guarda as minhas palavras; ora a palavra que ouvistes não é minha, mas do Pai que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '25', 'Tenho-vos dito isto, estando convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '26', 'Mas aquele Consolador, o Espírito Santo, que o Pai enviará em meu nome, esse vos ensinará todas as coisas, e vos fará lembrar de tudo quanto vos tenho dito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '27', 'Deixo-vos a paz, a minha paz vos dou: não vo-la dou como o mundo a dá. Não se turbe o vosso coração, nem se atemorize.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '28', 'Ouvistes que eu vos disse: Vou, e venho para vós. Se me amásseis, certamente exultaríeis por ter dito: Vou para o Pai; porque o Pai é maior do que eu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '29', 'Eu vo-lo disse agora antes que aconteça, para que, quando acontecer, vós acrediteis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '30', 'Já não falarei muito convosco; porque se aproxima o príncipe deste mundo, e nada tem em mim;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '14', '31', 'Mas é para que o mundo saiba que eu amo o Pai, e que faço como o Pai me mandou. Levantai-vos, vamo-nos daqui.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '1', 'EU SOU a videira verdadeira, e meu Pai é o lavrador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '2', 'Toda a vara em mim, que não dá fruto, a tira; e limpa toda aquela que dá fruto, para que dê mais fruto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '3', 'Vós já estais limpos, pela palavra que vos tenho falado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '4', 'Estai em mim, e eu em vós como a vara de si mesma não pode dar fruto, se não estiver na videira, assim também vós, se não estiverdes em mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '5', 'Eu sou a videira, vós as varas, quem está em mim, e eu nele, esse dá muito fruto; porque sem mim nada podeis fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '6', 'Se alguém não estiver em mim, será lançado fora como a vara, e secará; e os colhem e lançam no fogo, e ardem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '7', 'Se vós estiverdes em mim, e as minhas palavras estiverem em vós, pedireis tudo o que quiserdes, e vos será feito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '8', 'Nisto é glorificado meu Pai, que deis muito fruto; e assim sereis meus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '9', 'Como o Pai me amou, também eu vos amei a vós; permanecei no meu amor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '10', 'Se guardardes os meus mandamentos, permanecereis no meu amor; do mesmo modo que eu tenho guardado os mandamentos de meu Pai, e permaneço no seu amor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '11', 'Tenho-vos dito isto, para que o meu gozo permaneça em vós, e o vosso gozo seja completo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '12', 'O meu mandamento é este: Que vos ameis uns aos outros, assim como eu vos amei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '13', 'Ninguém tem maior amor do que este: de dar alguém a sua vida pelos seus amigos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '14', 'Vós sereis meus amigos, se fizerdes o que eu vos mando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '15', 'Já vos não chamarei servos, porque o servo não sabe o que faz o seu senhor, mas tenho-vos chamado amigos, porque tudo quanto ouvi de meu Pai vos tenho feito conhecer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '16', 'Não me escolhestes vós a mim, mas eu vos escolhi a vós, e vos nomeei, para que vades e deis fruto, e o vosso fruto permaneça; a fim de que tudo quanto em meu nome pedirdes ao Pai ele vo-lo conceda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '17', 'Isto vos mando: que vos ameis uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '18', 'Se o mundo vos aborrece, sabei que, primeiro do que a vós, me aborreceu a mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '19', 'Se vós fôsseis do mundo, o mundo amaria o que era seu, mas, porque não sois do mundo, antes eu vos escolhi do mundo, por isso é que o mundo vos aborrece.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '20', 'Lembrai-vos da palavra que vos disse: Não é o servo maior do que o seu senhor. Se a Mim me perseguiram, também vos perseguirão a vós; se guardaram a minha palavra, também guardarão a vossa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '21', 'Mas tudo isto vos farão por causa do meu nome; porque não conhecem aquele que me enviou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '22', 'Se eu não viera, nem lhes houvera falado, não teriam pecado, mas agora não têm desculpa do seu pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '23', 'Aquele que me aborrece, aborrece também a meu Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '24', 'Se eu entre eles não fizesse tais obras, quais nenhum outro tem feito, não teriam pecado; mas agora, viram-nas e me aborreceram a mim e a meu Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '25', 'Mas é para que se cumpra a palavra que está escrita na sua lei: Aborreceram-me sem causa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '26', 'Mas, quando vier o Consolador, que eu da parte do Pai vos hei-de enviar, aquele Espírito de verdade, que procede do Pai, ele testificará de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '15', '27', 'E vós também testificareis, pois estivestes comigo desde o princípio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '1', 'TENHO-VOS dito estas coisas para que vos não escandalizeis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '2', 'Expulsar-vos-ão das sinagogas; vem mesmo a hora em que qualquer que vos matar cuidará fazer um serviço a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '3', 'E isto vos farão, porque não conheceram ao Pai nem a mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '4', 'Mas tenho-vos dito isto, a fim de que, quando chegar aquela hora, vos lembreis de que já vo-lo tinha dito; e eu não vos disse isto desde o princípio, porque estava convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '5', 'E agora vou para aquele que me enviou: e nenhum de vós me pergunta: Para onde vais?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '6', 'Antes, porque isto vos tenho dito, o vosso coração se encheu de tristeza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '7', 'Todavia digo-vos a verdade, que vos convém que eu vá; porque, se eu não for, o Consolador não virá a vós; mas, se eu for, enviar-vo-lo-ei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '8', 'E, quando ele vier, convencerá o mundo do pecado, e da justiça e do juízo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '9', 'Do pecado, porque não crêem em mim;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '10', 'Da justiça, porque vou para meu Pai, e não me vereis mais;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '11', 'E do juízo, porque já o príncipe deste mundo está julgado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '12', 'Ainda tenho muito que vos dizer, mas vós não o podeis suportar agora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '13', 'Mas, quando vier aquele Espírito de verdade, ele vos guiará em toda a verdade; porque não falará de si mesmo, mas dirá tudo o que tiver ouvido, vos anunciará o que há-de vir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '14', 'Ele me glorificará, porque há-de receber do que é meu, e vo-lo há-de anunciar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '15', 'Tudo quanto o Pai tem é meu; por isso vos disse que há-de receber do que é meu e vo-lo há-de anunciar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '16', 'Um pouco, e não me vereis; e outra vez um pouco, e ver-me-eis; porquanto vou para o Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '17', 'Então alguns dos seus discípulos disseram uns para os outros: Que é isto que nos diz? Um pouco, e não me vereis; e outra vez um pouco, e ver-me-eis; e: porquanto vou para o Pai?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '18', 'Diziam pois: Que quer dizer isto: Um pouco? não sabemos o que diz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '19', 'Conheceu pois Jesus que o queriam interrogar, e disse-lhes: Indagais entre vós acerca disto que disse: Um pouco, e não me vereis, e outra vez um pouco, e ver-me-eis?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '20', 'Na verdade, na verdade, vos digo que vós chorareis e vos lamentareis, e o mundo se alegrará, e vós estareis tristes; mas a vossa tristeza se converterá em alegria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '21', 'A mulher, quando está para dar à luz, sente tristeza, porque é chegada a sua hora; mas, depois de ter dado à luz a criança, já se não lembra da aflição, pelo prazer de haver nascido um homem no mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '22', 'Assim também vós agora, na verdade, tendes tristeza; mas outra vez vos verei, e o vosso coração se alegrará, e a vossa alegria ninguém vo-la tirará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '23', 'E naquele dia nada me perguntareis. Na verdade, na verdade vos digo que tudo quanto pedirdes a meu Pai, em meu nome, ele vo-lo há-de dar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '24', 'Até agora nada pedistes em meu nome; pedi, e recebereis, para que o vosso gozo se cumpra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '25', 'Disse-vos isto por parábolas: chega, porém, a hora em que vos não falarei mais por parábolas, mas abertamente vos falarei acerca do Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '26', 'Naquele dia pedireis em meu nome, e não vos digo que eu rogarei por vós ao Pai;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '27', 'Pois o mesmo Pai vos ama; visto como vós me amastes, e crestes que saí de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '28', 'Saí do Pai, e vim ao mundo; outra vez deixo o mundo, e vou para o Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '29', 'Disseram-lhe os seus discípulos: Eis que agora falas abertamente, e não dizes parábola alguma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '30', 'Agora conhecemos tudo, e não hás mister de que alguém te interrogue. Por isso cremos que saíste de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '31', 'Respondeu-lhes Jesus: Credes agora?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '32', 'Eis que chega a hora, e já se aproxima, em que vós sereis dispersos cada um para sua parte, e me deixareis só; mas não estou só, porque o Pai está comigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '16', '33', 'Tenho-vos dito isto, para que em mim tenhais paz; no mundo tereis aflições, mas tende bom ânimo, eu venci o mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '1', 'JESUS falou assim, e, levantando os seus olhos ao céu, disse: Pai, é chegada a hora; glorifica a teu Filho, para que também te glorifique a ti;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '2', 'Assim como lhe deste poder sobre toda a carne, para que dê a vida eterna a todos quantos lhe deste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '3', 'E a vida eterna é esta: que te conheçam, a ti só, por único Deus verdadeiro e a Jesus Cristo, a quem enviaste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '4', 'Eu glorifiquei-te na terra, tendo consumado a obra que me deste a fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '5', 'E agora glorifica-me tu, ó Pai, junto de ti mesmo, com aquela glória que tinha contigo antes que o mundo existisse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '6', 'Manifestei o teu nome aos homens que do mundo me deste: eram teus, e tu mos deste, e guardaram a tua palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '7', 'Agora já têm conhecido que tudo quanto me deste provém de ti;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '8', 'Porque lhes dei as palavras que tu me deste; e eles as receberam, e têm verdadeiramente conhecido que saí de ti; e creram que me enviaste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '9', 'Eu rogo por eles; não rogo pelo mundo, mas por aqueles que me deste, porque são teus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '10', 'E todas as minhas coisas são tuas, e as tuas coisas são minhas; e nisso sou glorificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '11', 'E eu já não estou mais no mundo; mas eles estão no mundo, e eu vou para ti. Pai santo, guarda em teu nome aqueles que me deste, para que sejam um, assim como nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '12', 'Estando eu com eles no mundo, guardava-os em teu nome. Tenho guardado aqueles que tu me deste, e nenhum deles se perdeu, senão o filho da perdição, para que a Escritura se cumprisse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '13', 'Mas agora vou para ti, e digo isto no mundo, para que tenham a minha alegria completa em si mesmos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '14', 'Dei-lhes a tua palavra, e o mundo os aborreceu, porque não são do mundo, assim como eu não sou do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '15', 'Não peço que os tires do mundo, mas que os livres do mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '16', 'Não são do mundo, como eu do mundo não sou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '17', 'Santifica-os na verdade: a tua palavra é a verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '18', 'Assim como tu me enviaste ao mundo, também eu os enviei ao mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '19', 'E por eles me santifico a mim mesmo, para que também eles sejam santificados na verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '20', 'E não rogo somente por estes, mas também por aqueles que pela sua palavra hão-de crer em mim;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '21', 'Para que todos sejam um, como tu, ó Pai, o és em mim, e eu em ti que também eles sejam um em nós, para que o mundo creia que tu me enviaste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '22', 'E eu dei-lhes a glória que a mim me deste, para que sejam um, como nós somos um.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '23', 'Eu neles, e tu em mim, para que eles sejam perfeitos em unidade, e para que o mundo conheça que tu me enviaste a mim, e que os tens amado a eles como me tens amado a mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '24', 'Pai, aqueles que me deste quero que, onde eu estiver, também eles estejam comigo, para que vejam a minha glória que me deste: porque tu me hás amado antes da fundação do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '25', 'Pai justo, o mundo não te conheceu; mas eu te conheci, e estes conheceram que tu me enviaste a mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '17', '26', 'E eu lhes fiz conhecer o teu nome, e lho farei conhecer mais, para que o amor com que me tens amado esteja neles, e eu neles esteja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '1', 'TENDO Jesus dito isto, saiu com os seus discípulos para além do ribeiro de Cédron, onde havia um horto, no qual ele entrou e seus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '2', 'E Judas, que o traía, também conhecia aquele lugar, porque Jesus muitas vezes se ajuntava ali com os seus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '3', 'Tendo pois Judas recebido a coorte e oficiais dos principais sacerdotes e fariseus, veio para ali com lanternas, e archotes e armas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '4', 'Sabendo pois Jesus todas as coisas que sobre ele haviam de vir, adiantou-se, e disse-lhes: A quem buscais?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '5', 'Responderam-lhe: A Jesus Nazareno. Disse-lhes Jesus: Sou eu. E Judas, que o traía, estava com eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '6', 'Quando pois lhes disse: Sou eu, recuaram, e caíram por terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '7', 'Tornou-lhes pois a perguntar: A quem buscais? E eles disseram: A Jesus Nazareno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '8', 'Jesus respondeu: Já vos disse que sou eu; se pois me buscais a mim, deixai ir estes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '9', 'Para que se cumprisse a palavra que tinha dito: Dos que me deste nenhum deles perdi.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '10', 'Então Simão Pedro, que tinha espada, desembainhou-a, e feriu o servo do sumo sacerdote, cortando-lhe a orelha direita. E o nome do servo era Malco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '11', 'Mas Jesus disse a Pedro: Mete a tua espada na bainha; não beberei eu o cálix que o Pai me deu?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '12', 'Então a coorte, e o tribuno, e os servos dos judeus prenderam a Jesus e o maniataram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '13', 'E conduziram-no primeiramente a Anás, por ser sogro de Caifás, que era o sumo sacerdote daquele ano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '14', 'Ora Caifás era quem tinha aconselhado aos judeus que convinha que um homem morresse pelo povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '15', 'E Simão Pedro e outro discípulo seguiam a Jesus. E este discípulo era conhecido do sumo sacerdote, e entrou com Jesus na sala do sumo sacerdote.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '16', 'E Pedro estava da parte de fora, à porta. Saiu então o outro discípulo que era conhecido do sumo sacerdote, e falou à porteira, levando Pedro para dentro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '17', 'Então a porteira disse a Pedro: Não és tu também dos discípulos deste homem? Disse ele: Não sou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '18', 'Ora estavam ali os servos e os criados, que tinham feito brasas, e se aquentavam, porque fazia frio; e com eles estava Pedro, aquentando-se também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '19', 'E o sumo sacerdote interrogou Jesus acerca dos seus discípulos e da sua doutrina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '20', 'Jesus lhe respondeu: Eu falei abertamente ao mundo; eu sempre ensinei na sinagoga e no templo, onde todos os judeus se ajuntam, e nada disse em oculto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '21', 'Para que me perguntas a mim? pergunta aos que ouviram o que é que lhes ensinei; eis que eles sabem o que eu lhes tenho dito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '22', 'E, tendo dito isto, um dos criados que ali estavam deu uma bofetada em Jesus, dizendo: Assim respondes ao sumo sacerdote?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '23', 'Respondeu-lhe Jesus: Se falei mal, dá testemunho do mal; e, se bem, porque me feres?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '24', 'E Anás mandou-o, manietado, ao sumo sacerdote Caifás,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '25', 'E Simão Pedro estava ali, e aquentava-se. Disseram-lhe pois: Não és também tu um dos seus discípulos? Ele negou, e disse: Não sou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '26', 'E um dos servos do sumo sacerdote, parente daquele a quem Pedro cortara a orelha, disse: Não te vi eu no horto com ele?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '27', 'E Pedro negou outra vez, e logo o galo cantou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '28', 'Depois levaram Jesus da casa de Caifás para a audiência. E era pela manhã cedo. E não entraram na audiência, para não se contaminarem, mas poderem comer a páscoa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '29', 'Então Pilatos saiu fora e disse-lhes: Que acusação trazeis contra este homem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '30', 'Responderam, e disseram-lhe: Se este não fosse malfeitor, não to entregaríamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '31', 'Disse-lhes pois Pilatos: Levai-o vós, e julgai-o segundo a vossa lei. Disseram-lhe então os judeus: A nós não nos é lícito matar pessoa alguma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '32', '(Para que se cumprisse a palavra que Jesus tinha dito, significando de que morte havia de morrer),');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '33', 'Tornou pois a entrar Pilatos na audiência, e chamou a Jesus, e disse-lhe: Tu és o rei dos judeus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '34', 'Respondeu-lhe Jesus: Tu dizes isso de ti mesmo, ou disseram-to outros de mim?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '35', 'Pilatos respondeu: Porventura sou eu judeu? a tua nação e os principais dos sacerdotes entregaram-te a mim: que fizeste?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '36', 'Respondeu Jesus: O meu reino não é deste mundo: se o meu reino fosse deste mundo, pelejariam os meus servos, para que eu não fosse entregue aos judeus; mas agora o meu reino não é daqui.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '37', 'Disse-lhe pois Pilatos: Logo tu és rei? Jesus respondeu: Tu dizes que eu sou rei. Eu para isso nasci, e para isso vim ao mundo, a fim de dar testemunho da verdade. Todo aquele que é da verdade ouve a minha voz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '38', 'Disse-lhe Pilatos: Que é a verdade? E, dizendo isto, tornou a ir ter com os judeus, e disse-lhes: Não acho nele crime algum;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '39', 'Mas vós tendes por costume que eu vos solte alguém pela páscoa. Quereis pois que vos solte o Rei dos Judeus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '18', '40', 'Então todos tornaram a clamar, dizendo: Este não, mas Barrabás. E Barrabás era um salteador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '1', 'PILATOS pois tomou então a Jesus, e o açoitou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '2', 'E os soldados, tecendo uma coroa de espinhos, lha puseram sobre a cabeça, e lhe vestiram uma veste de púrpura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '3', 'E diziam: Salve, Rei dos Judeus. E davam-lhe bofetadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '4', 'Então Pilatos saiu outra vez fora, e disse-lhes: Eis aqui vo-lo trago fora, para que saibais que não acho nele crime algum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '5', 'Saiu pois Jesus fora, levando a coroa de espinhos e o vestido de púrpura. E disse-lhes Pilatos: Eis aqui o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '6', 'Vendo-o pois os principais dos sacerdotes e os servos, clamaram, dizendo: Crucifica-o, crucifica-o. Disse-lhes Pilatos: Tomai-o vós, e crucificai-o; porque eu nenhum crime acho nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '7', 'Responderam-lhe os judeus: Nós temos uma lei, e, seguindo a nossa lei, deve morrer, porque se fez Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '8', 'E Pilatos, quando ouviu esta palavra, mais atemorizado ficou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '9', 'E entrou outra vez na audiência, e disse a Jesus: Donde és tu? Mas Jesus não lhe deu resposta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '10', 'Disse-lhe pois Pilatos: Não me falas a mim? não sabes tu que tenho poder para te crucificar e tenho poder para te soltar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '11', 'Respondeu Jesus: Nenhum poder terias contra mim, se de cima te não fosse dado; mas aquele que me entregou a ti maior pecado tem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '12', 'Desde então Pilatos procurava soltá-lo; mas os judeus clamavam, dizendo: Se soltas este, não és amigo de César; qualquer que se faz rei é contra o César.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '13', 'Ouvindo pois Pilatos este dito, levou Jesus para fora, e assentou-se no tribunal, no lugar chamado Litóstrotos, e em hebraico Gabatá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '14', 'E era a preparação da páscoa, e quase à hora sexta; e disse aos judeus: Eis aqui o vosso Rei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '15', 'Mas eles bradaram: Tira, tira, crucifica-o. Disse-lhes Pilatos: Hei-de crucificar o vosso Rei? Responderam os principais dos sacerdotes: Não temos rei, senão o César.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '16', 'Então entregou-lho, para que fosse crucificado. E tomaram a Jesus, e o levaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '17', 'E, levando ele às costas a sua cruz, saiu para o lugar chamado Caveira, que em hebraico se chama Gólgota.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '18', 'Onde o crucificaram, e com ele outros dois, um de cada lado, e Jesus no meio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '19', 'E Pilatos escreveu também um título, e pô-lo em cima da cruz; e nele estava escrito: JESUS NAZARENO, REI DOS JUDEUS.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '20', 'E muitos dos judeus leram este título; porque o lugar onde Jesus estava crucificado era próximo da cidade; e estava escrito em hebraico, grego e latim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '21', 'Diziam pois os principais sacerdotes dos judeus a Pilatos: Não escrevas, Rei dos Judeus; mas que ele disse: Sou Rei dos Judeus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '22', 'Respondeu Pilatos: O que escrevi, escrevi.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '23', 'Tendo pois os soldados crucificado a Jesus, tomaram os seus vestidos, e fizeram quatro partes, para cada soldado uma parte; e também a túnica. A túnica, porém, tecida toda de alto a baixo, não tinha costura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '24', 'Disseram pois uns aos outros: Não a rasguemos, mas lancemos sortes sobre ela, para ver de quem será. Para que se cumprisse a Escritura que diz: Dividiram entre si os meus vestidos, e sobre a minha vestidura lançaram sortes. Os soldados, pois, fizeram estas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '25', 'E junto à cruz de Jesus estava sua mãe, e a irmã de sua mãe, Maria de Cléofas, e Maria Madalena.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '26', 'Ora Jesus, vendo ali sua mãe, e que o discípulo a quem ele amava estava presente, disse a sua mãe: Mulher, eis aí o teu filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '27', 'Depois disse ao discípulo: Eis aí tua mãe. E desde aquela hora o discípulo a recebeu em sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '28', 'Depois, sabendo Jesus que já todas as coisas estavam terminadas, para que a Escritura se cumprisse, disse: Tenho sede.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '29', 'Estava pois ali um vaso cheio de vinagre. E encheram de vinagre uma esponja, e, pondo-a num hissope, lha chegaram à boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '30', 'E, quando Jesus tomou o vinagre, disse: Está consumado. E, inclinando a cabeça, entregou o espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '31', 'Os judeus, pois, para que no sábado não ficassem os corpos na cruz, visto como era a preparação (pois era grande o dia de sábado), rogaram a Pilatos que se lhes quebrassem as pernas, e fossem tirados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '32', 'Foram pois os soldados, e, na verdade, quebraram as pernas ao primeiro, e ao outro que com ele fora crucificado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '33', 'Mas, vindo a Jesus, e vendo-o já morto, não lhe quebraram as pernas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '34', 'Contudo um dos soldados lhe furou o lado com uma lança, e logo saiu sangue e água.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '35', 'E aquele que o viu testificou, e o seu testemunho é verdadeiro; e sabe que é verdade o que diz, para que também vós o creiais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '36', 'Porque isto aconteceu para que se cumprisse a Escritura, que diz: Nenhum dos seus ossos será quebrado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '37', 'E outra vez diz a Escritura: Verão aquele que trespassaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '38', 'Depois disto, José de Arimateia (o que era discípulo de Jesus, mas oculto, por medo dos judeus) rogou a Pilatos que lhe permitisse tirar o corpo de Jesus. E Pilatos lho permitiu. Então foi e tirou o corpo de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '39', 'E foi também Nicodemos (aquele que anteriormente se dirigira de noite a Jesus), levando quase cem arráteis dum composto de mirra e aloés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '40', 'Tomaram pois o corpo de Jesus e o envolveram em lençóis com as especiarias, como os judeus costumam fazer, na preparação para o sepulcro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '41', 'E havia um horto naquele lugar onde fora crucificado, e no horto um sepulcro novo, em que ainda ninguém havia sido posto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '19', '42', 'Ali, pois, (por causa da preparação dos judeus, e por estar perto aquele sepulcro), puseram a Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '1', 'E NO primeiro dia da semana Maria Madalena foi ao sepulcro de madrugada, sendo ainda escuro, e viu a pedra tirada do sepulcro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '2', 'Correu pois, e foi a Simão Pedro, e ao outro discípulo, a quem Jesus amava e disse-lhes: Levaram o SENHOR do sepulcro, e não sabemos onde o puseram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '3', 'Então Pedro saiu com o outro discípulo, e foram ao sepulcro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '4', 'E os dois corriam juntos, mas o outro discípulo correu mais apressadamente do que Pedro, e chegou primeiro ao sepulcro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '5', 'E, abaixando-se, viu no chão os lençóis; todavia não entrou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '6', 'Chegou pois Simão Pedro, que o seguia, e entrou no sepulcro, e viu no chão os lençóis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '7', 'E que o lenço, que tinha estado sobre a sua cabeça, não estava com os lençóis, mas enrolado num lugar à parte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '8', 'Então entrou também o outro discípulo, que chegara primeiro ao sepulcro, e viu, e creu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '9', 'Porque ainda não sabiam a Escritura: que era necessário que ressuscitasse dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '10', 'Tornaram pois os discípulos para casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '11', 'E Maria estava chorando fora, junto ao sepulcro. Estando ela pois chorando, abaixou-se para o sepulcro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '12', 'E viu dois anjos vestidos de branco, assentados onde jazera o corpo de Jesus, um à cabeceira e outro aos pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '13', 'E disseram-lhe eles: Mulher, porque choras? Ela lhes disse: Porque levaram o meu Senhor, e não sei onde o puseram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '14', 'E, tendo dito isto, voltou-se para trás, e viu Jesus em pé, mas não sabia que era Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '15', 'Disse-lhe Jesus: Mulher, porque choras? Quem buscas? Ela, cuidando que era o hortelão, disse-lhe: Senhor, se tu o levaste, diz-me onde o puseste, e eu o levarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '16', 'Disse-lhe Jesus: Maria! Ela, voltando-se, disse-lhe: Rabboni (que quer dizer, Mestre).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '17', 'Disse-lhe Jesus: Não me detenhas, porque ainda não subi para meu Pai, mas vai para meus irmãos, e diz-lhes que eu subo para meu Pai e vosso Pai, meu Deus e vosso Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '18', 'Maria Madalena foi e anunciou aos discípulos que vira o SENHOR, e que ele lhe dissera isto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '19', 'Chegada pois a tarde daquele dia, o primeiro da semana, e cerradas as portas onde os discípulos, com medo dos judeus, se tinham ajuntado, chegou Jesus, e pôs-se no meio, e disse-lhes: Paz seja convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '20', 'E, dizendo isto, mostrou-lhes as suas mãos e o lado. De sorte que os discípulos se alegraram, vendo o SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '21', 'Disse-lhes pois Jesus outra vez: Paz seja convosco; assim como o Pai me enviou, também eu vos envio a vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '22', 'E, havendo dito isto, assoprou sobre eles e disse-lhes: Recebei o Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '23', 'Aqueles a quem perdoardes os pecados, lhes são perdoados: e àqueles a quem os retiverdes, lhes são retidos:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '24', 'Ora Tomé, um dos doze, chamado Dídimo não estava com eles quando veio Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '25', 'Disseram-lhe: Vimos o SENHOR. Mas ele disse-lhes: Se eu não vir o sinal dos cravos em suas mãos e não meter o dedo no lugar dos cravos, e não meter a minha mão no seu lado, de maneira nenhuma o crerei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '26', 'E oito dias depois estavam outra vez os seus discípulos dentro, e com eles Tomé. Chegou Jesus, estando as portas fechadas, e apresentou-se no meio, e disse: Paz seja convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '27', 'Depois disse a Tomé: Põe aqui o teu dedo, e vê as minhas mãos; e chega a tua mão, e mete-a no meu lado; e não sejas incrédulo, mas crente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '28', 'Tomé respondeu, e disse-lhe: Senhor meu, e Deus meu!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '29', 'Disse-lhe Jesus: Porque me viste, Tomé, creste; bem-aventurados os que não viram e creram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '30', 'Jesus pois operou também em presença de seus discípulos muitos outros sinais, que não estão escritos neste livro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '20', '31', 'Estes, porém, foram escritos para que creiais que Jesus é o Cristo, o Filho de Deus, e para que, crendo, tenhais vida em seu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '1', 'DEPOIS disto manifestou-se Jesus outra vez aos discípulos junto do mar de Tiberíades; e manifestou-se assim:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '2', 'Estavam juntos Simão Pedro, e Tomé, chamado Dídimo, e Natanael, que era de Caná da Galileia, os filhos de Zebedeu, e outros dois dos seus discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '3', 'Disse-lhes Simão Pedro: Vou pescar. Dizem-lhe eles: Também nós vamos contigo. Foram, e subiram logo para o barco, e naquela noite nada apanharam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '4', 'E, sendo já manhã, Jesus se apresentou na praia, mas os discípulos não conheceram que era Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '5', 'Disse-lhes pois Jesus: Filhos, tendes alguma coisa de comer? Responderam-lhe: Não.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '6', 'E ele lhes disse: Lançai a rede para a banda direita do barco, e achareis. Lançaram-na pois, e já não a podiam tirar, pela multidão dos peixes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '7', 'Então aquele discípulo a quem Jesus amava disse a Pedro: É o SENHOR. E, quando Simão Pedro ouviu que era o SENHOR cingiu-se com a túnica (porque estava nu) e lançou-se ao mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '8', 'E os outros discípulos foram com o barco (porque não estavam distantes da terra senão quase duzentos côvados), levando a rede cheia de peixes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '9', 'Logo que desceram para terra, viram ali brasas, e um peixe posto em cima, e pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '10', 'Disse-lhes Jesus: Trazei dos peixes que agora apanhastes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '11', 'Simão Pedro subiu e puxou a rede para terra, cheia de cento e cinquenta e três grandes peixes, e, sendo tantos, não se rompeu a rede.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '12', 'Disse-lhes Jesus: Vinde, jantai. E nenhum dos discípulos ousava perguntar-lhe: Quem és tu? sabendo que era o SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '13', 'Chegou pois Jesus, e tomou o pão, e deu-lho, e, semelhantemente o peixe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '14', 'E já era a terceira vez que Jesus se manifestava aos seus discípulos, depois de ter ressuscitado dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '15', 'E, depois de terem jantado, disse Jesus a Simão Pedro: Simão, filho de Jonas, amas-me mais do que estes? E ele respondeu: Sim, Senhor; tu sabes que te amo. Disse-lhe: Apascenta os meus cordeiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '16', 'Tornou a dizer-lhe segunda vez: Simão, filho de Jonas, amas-me? Disse-lhe: Sim Senhor; tu sabes que te amo. Disse-lhe: Apascenta as minhas ovelhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '17', 'Disse-lhe terceira vez: Simão, filho de Jonas, amas-me? Simão entristeceu-se por lhe ter dito terceira vez: Amas-me? E disse-lhe: Senhor, tu sabes tudo; tu sabes que eu te amo. Jesus disse-lhe: Apascenta as minhas ovelhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '18', 'Na verdade, na verdade te digo, que, quando eras mais moço, te cingias a ti mesmo, e andavas por onde querias; mas, quando já fores velho, estenderás as tuas mãos; e outro te cingirá, e te levará para onde tu não queiras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '19', 'E disse isto, significando com que morte havia ele de glorificar a Deus. E, dito isto, disse-lhe: Segue-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '20', 'E Pedro, voltando-se, viu que o seguia aquele discípulo a quem Jesus amava, e que na ceia se recostara também sobre o seu peito, e que dissera: Senhor, quem é que te há-de trair ?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '21', 'Vendo Pedro a este, disse a Jesus: Senhor, e deste que será?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '22', 'Disse-lhe Jesus: Se eu quero que ele fique até que eu venha, que te importa a ti? Segue-me tu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '23', 'Divulgou-se pois entre os irmãos este dito, que aquele discípulo não havia de morrer. Jesus, porém, não lhe disse que não morreria, mas: Se eu quero que ele fique até que eu venha, que te importa a ti?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '24', 'Este é o discípulo, que testifica destas coisas e as escreveu; e sabemos que o seu testemunho é verdadeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('4', '21', '25', 'Há, porém, ainda muitas outras coisas que Jesus fez; e se cada uma das quais fosse escrita, cuido que nem ainda o mundo todo poderia conter os livros que se escrevessem. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '1', '¶ FIZ o primeiro tratado, ó Teófilo, acerca de tudo que Jesus começou, não só a fazer, mas a ensinar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '2', 'Até ao dia em que foi recebido em cima, depois de ter dado mandamentos, pelo Espírito Santo, aos apóstolos que escolhera;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '3', 'Aos quais também, depois de ter padecido, se apresentou vivo, com muitas e infalíveis provas, sendo visto por eles por espaço de quarenta dias, e falando do que respeita ao reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '4', 'E, estando com eles, determinou-lhes que não se ausentassem de Jerusalém, mas que esperassem a promessa do Pai, que (disse ele) de mim ouvistes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '5', 'Porque, na verdade, João baptizou com água, mas vós sereis baptizados com o Espírito Santo, não muito depois destes dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '6', 'Aqueles pois que se haviam reunido perguntaram-lhe, dizendo: Senhor, restaurarás tu neste tempo o reino a Israel?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '7', 'E disse-lhes: Não vos pertence saber os tempos ou as estações que o Pai estabeleceu pelo seu próprio poder.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '8', 'Mas recebereis a virtude do Espírito Santo, que há-de vir sobre vós; e ser-me-eis testemunhas, tanto em Jerusalém como em toda a Judeia e Samaria, e até aos confins da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '9', 'E quando dizia isto, vendo-o eles, foi elevado às alturas, e uma nuvem o recebeu, ocultando-o a seus olhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '10', 'E, estando com os olhos fitos no céu, enquanto ele subia, eis que junto deles se puseram dois varões vestidos de branco,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '11', 'Os quais lhes disseram: Varões galileus, porque estais olhando para o céu? Esse Jesus, que de entre vós foi recebido em cima no céu, há-de vir assim como para o céu o vistes ir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '12', 'Então voltaram para Jerusalém, do monte chamado das Oliveiras, o qual está perto de Jerusalém, à distância do caminho de um sábado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '13', 'E, entrando, subiram ao cenáculo, onde habitavam, Pedro e Tiago, João e André, Filipe e Tomé, Bartolomeu e Mateus, Tiago, filho de Alfeu, Simão, o zelador, e Judas, de Tiago.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '14', 'Todos estes perseveravam unanimemente em oração e súplicas, com as mulheres, e Maria mãe de Jesus, e com seus irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '15', 'E naqueles dias, levantando-se Pedro no meio dos discípulos, (ora a multidão junta era de quase cento e vinte pessoas) disse:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '16', 'Varões irmãos, convinha que se cumprisse a escritura que o Espírito Santo predisse pela boca de David, acerca de Judas, que foi o guia daqueles que prenderam a Jesus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '17', 'Porque foi contado connosco e alcançou sorte neste ministério.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '18', 'Ora este adquiriu um campo com o galardão da iniquidade; e, precipitando-se, rebentou pelo meio, e todas as suas entranhas se derramaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '19', 'E foi notório a todos os que habitam em Jerusalém; de maneira que na sua própria língua esse campo se chama Aceldama, isto é, Campo de Sangue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '20', 'Porque no livro dos Salmos está escrito: Fique deserta a sua habitação, e não haja quem nela habite, e tome outro o seu bispado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '21', 'É necessário pois que, dos varões que conviveram connosco todo o tempo em que o SENHOR Jesus entrou e saiu de entre nós,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '22', 'Começando desde o baptismo de João até ao dia em que de entre nós foi recebido em cima, um deles se faça connosco testemunha da sua ressurreição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '23', 'E apresentaram dois: José, chamado Barsabás, que tinha por sobrenome o Justo, e Matias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '24', 'E, orando, disseram: Tu, Senhor, conhecedor dos corações de todos, mostra qual destes dois tens escolhido,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '25', 'Para que tome parte neste ministério e apostolado, de que Judas se desviou, para ir para o seu próprio lugar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '1', '26', 'E lançando-lhes sortes, caiu a sorte sobre Matias. E por voto comum foi contado com os onze apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '1', 'E CUMPRINDO-SE o dia de Pentecostes, estavam todos reunidos no mesmo lugar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '2', 'E de repente veio do céu um som, como de um vento veemente e impetuoso, e encheu toda a casa em que estavam assentados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '3', 'E foram vistas por eles línguas repartidas, como que de fogo, as quais pousaram sobre cada um deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '4', 'E todos foram cheios do Espírito Santo, e começaram a falar noutras línguas, conforme o Espírito Santo lhes concedia que falassem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '5', 'E em Jerusalém estavam habitando judeus, varões religiosos, de todas as nações que estão debaixo do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '6', 'E, correndo aquela voz, ajuntou-se uma multidão, e estava confusa, porque cada um os ouvia falar na sua própria língua.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '7', 'E logo todos pasmavam e se maravilhavam, dizendo uns aos outros: Pois quê? Não são galileus todos esses homens que estão falando?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '8', 'Como pois os ouvimos cada um, na nossa própria língua em que somos nascidos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '9', 'Partos e medas, elamitas e os que habitam na Mesopotâmia, e Judeia, e Capadócia, Ponto e Ásia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '10', 'E Frígia e Panfília, Egipto e partes da Líbia, junto a Cirene, e forasteiros romanos, tanto judeus como prosélitos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '11', 'Cretenses e árabes, todos os temos ouvido em nossas próprias línguas falar das grandezas de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '12', 'E todos se maravilhavam e estavam suspensos, dizendo uns para os outros: Que quer isto dizer?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '13', 'E outros, zombavam, dizendo: Estão cheios de mosto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '14', 'Pedro, porém, pondo-se em pé com os onze, levantou a sua voz, e disse-lhes: Varões judeus, e todos os que habitam em Jerusalém, seja-vos isto notório, e escutai as minhas palavras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '15', 'Estes homens não estão embriagados, como vós pensais, sendo a terceira hora do dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '16', 'Mas isto é o que foi dito pelo profeta Joel:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '17', 'E nos últimos dias acontecerá, diz Deus, que do meu Espírito derramarei sobre toda a carne; e os vossos filhos, e as vossas filhas profetizarão, os vossos mancebos terão visões, e os vossos velhos sonharão sonhos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '18', 'E também do meu Espírito derramarei sobre os meus servos e minhas servas naqueles dias, e profetizarão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '19', 'E farei aparecer prodígios em cima no céu, e sinais em baixo na terra, sangue, fogo e vapor de fumo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '20', 'O sol se converterá em trevas, e a lua em sangue, antes de chegar o grande e glorioso dia do SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '21', 'E acontecerá que todo aquele que invocar o nome do SENHOR será salvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '22', 'Varões israelitas, escutai estas palavras: A Jesus Nazareno, varão aprovado por Deus entre vós com maravilhas, prodígios e sinais, que Deus por ele fez no meio de vós, como vós mesmos bem sabeis;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '23', 'A este que vos foi entregue pelo determinado conselho e presciência de Deus, tomando-o vós, o crucificastes e matastes pelas mãos de injustos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '24', 'Ao qual Deus ressuscitou, soltas as ânsias da morte, pois não era possível que fosse retido por ela;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '25', 'Porque dele disse David: Sempre via diante de mim o SENHOR, porque está à minha direita, para que eu não seja comovido;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '26', 'Por isso se alegrou o meu coração, e a minha língua exultou; e ainda a minha carne há-de repousar em esperança;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '27', 'Pois não deixarás a minha alma no Hades, nem permitirás que o teu Santo veja a corrupção;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '28', 'Fizeste-me conhecidos os caminhos da vida; com a tua face me encherás de júbilo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '29', 'Varões irmãos, seja-me lícito dizer-vos livremente acerca do patriarca David, que ele morreu e foi sepultado, e entre nós está até hoje a sua sepultura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '30', 'Sendo pois ele profeta, e sabendo que Deus lhe havia prometido com juramento que do fruto de seus lombos, segundo a carne, levantaria o Cristo, para o assentar sobre o seu trono,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '31', 'Nesta previsão, disse da ressurreição de Cristo: que a sua alma não foi deixada no Hades, nem a sua carne viu a corrupção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '32', 'Deus ressuscitou a este Jesus, do que todos nós somos testemunhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '33', 'De sorte que, exaltado pela dextra de Deus, e tendo recebido do Pai a promessa do Espírito Santo, derramou isto que vós agora vedes e ouvis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '34', 'Porque David não subiu aos céus, mas ele próprio diz: Disse o SENHOR ao meu Senhor: Assenta-te à minha direita,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '35', 'Até que ponha os teus inimigos por escabelo de teus pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '36', 'Saiba pois com certeza toda a casa de Israel que a esse Jesus, a quem vós crucificastes, Deus o fez Senhor e Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '37', 'E, ouvindo eles isto, compungiram-se em seu coração, e perguntaram a Pedro e aos demais apóstolos: Que faremos, varões irmãos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '38', 'E disse-lhes Pedro: Arrependei-vos, e cada um de vós seja baptizado em nome de Jesus Cristo, para perdão dos pecados; e recebereis o dom do Espírito Santo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '39', 'Porque a promessa vos diz respeito a vós, a vossos filhos, e a todos os que estão longe: a tantos quantos Deus nosso SENHOR chamar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '40', 'E com muitas outras palavras isto testificava, e os exortava, dizendo: Salvai-vos desta geração perversa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '41', 'De sorte que foram baptizados os que de bom grado receberam a sua palavra; e naquele dia agregaram-se quase três mil almas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '42', 'E perseveravam na doutrina dos apóstolos, e na comunhão, e no partir do pão, e nas orações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '43', 'E em toda a alma havia temor, e muitas maravilhas e sinais se faziam pelos apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '44', 'E todos os que criam estavam juntos, e tinham tudo em comum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '45', 'E vendiam suas propriedades e fazendas, e repartiam com todos, segundo cada um havia de mister.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '46', 'E, perseverando unânimes todos os dias no templo, e partindo o pão em casa, comiam juntos com alegria e singeleza de coração,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '2', '47', 'Louvando a Deus, e caindo na graça de todo o povo. E todos os dias acrescentava o SENHOR à igreja aqueles que se haviam de salvar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '1', 'E PEDRO e João subiram juntos ao templo à hora da oração, a nona.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '2', 'E era trazido um varão que desde o ventre de sua mãe era coxo, o qual todos os dias punham à porta do templo, chamada Formosa, para pedir esmola aos que entravam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '3', 'O qual, vendo a Pedro e a João, que iam entrando no templo, pediu que lhe dessem uma esmola.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '4', 'E Pedro, com João, fitando os olhos nele, disse: Olha para nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '5', 'E olhou para eles, esperando receber deles alguma coisa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '6', 'E disse Pedro: Não tenho prata nem ouro; mas o que tenho isso te dou: Em nome de Jesus Cristo, o Nazareno, levanta-te e anda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '7', 'E, tomando-o pela mão direita, o levantou, e logo os seus pés e artelhos se firmaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '8', 'E, saltando ele, pôs-se em pé, e andou, e entrou com eles no templo, andando, e saltando, e louvando a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '9', 'E todo o povo o viu andar e louvar a Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '10', 'E conheciam-no, pois era ele o que se assentava a pedir esmola à porta Formosa do templo; e ficaram cheios de pasmo e assombro, pelo que lhe acontecera.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '11', 'E, apegando-se o coxo, que fora curado, a Pedro e João, todo o povo correu atónito para junto deles, ao alpendre chamado de Salomão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '12', 'E quando Pedro viu isto, disse ao povo: Varões israelitas, porque vos maravilhais disto? Ou, porque olhais tanto para nós, como se por nossa própria virtude ou santidade fizéssemos andar este homem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '13', 'O Deus de Abraão, e de Isaque e de Jacob, o Deus de nossos pais, glorificou a seu filho Jesus, a quem vós entregastes e perante a face de Pilatos negastes, tendo ele determinado que fosse solto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '14', 'Mas vós negastes o Santo e o Justo, e pedistes que se vos desse um homem homicida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '15', 'E matastes o Príncipe da vida, ao qual Deus ressuscitou dos mortos, do que nós somos testemunhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '16', 'E pela fé no seu nome fez o seu nome fortalecer a este que vedes e conheceis; e a fé, que é por ele, deu a este, na presença de todos vós, esta perfeita saúde.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '17', 'E agora, irmãos, eu sei que o fizestes por ignorância, como também os vossos príncipes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '18', 'Mas Deus assim cumpriu o que já dantes pela boca de todos os seus profetas havia anunciado; que o Cristo havia de padecer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '19', 'Arrependei-vos, pois, e convertei-vos, para que sejam apagados os vossos pecados, e venham assim os tempos do refrigério pela presença do SENHOR,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '20', 'E envie ele a Jesus Cristo, que já dantes vos foi pregado,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '21', 'O qual convém que o céu contenha até aos tempos da restauração de tudo, dos quais Deus falou pela boca de todos os seus santos profetas, desde principio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '22', 'Porque Moisés disse: O SENHOR vosso Deus levantará de entre vossos irmãos um profeta semelhante a mim; a ele ouvireis em tudo quanto vos disser.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '23', 'E acontecerá que toda a alma que não escutar esse profeta será exterminada de entre o povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '24', 'E todos os profetas, desde Samuel, todos quantos depois falaram, também anunciaram estes dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '25', 'Vós sois os filhos dos profetas e do concerto que Deus fez com nossos pais, dizendo a Abraão: Na tua descendência serão benditas todas as famílias da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '3', '26', 'Ressuscitando Deus a seu Filho Jesus, primeiro o enviou a vós, para que nisso vos abençoasse, e vos desviasse, a cada um, das vossas maldades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '1', 'E, ESTANDO eles falando ao povo, sobrevieram os sacerdotes, e o capitão do templo, e os saduceus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '2', 'Doendo-se muito de que ensinassem o povo, e anunciassem em Jesus a ressurreição dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '3', 'E lançaram mão deles, e os encerraram na prisão até ao dia seguinte, pois era já tarde.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '4', 'Muitos, porém, dos que ouviram a palavra creram, e chegou o número desses homens a quase cinco mil.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '5', 'E aconteceu, no dia seguinte, reunirem-se em Jerusalém os seus principais, os anciãos, os escribas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '6', 'E Anás, o sumo sacerdote, e Caifás, e João, e Alexandre, e todos quantos havia da linhagem do sumo sacerdote.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '7', 'E, pondo-os no meio, perguntaram: Com que poder ou em nome de quem fizestes isto?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '8', 'Então Pedro, cheio do Espírito Santo, lhes disse: Principais do povo, e vós, anciãos de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '9', 'Visto que hoje somos interrogados acerca do benefício feito a um homem enfermo, e do modo como foi curado,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '10', 'Seja conhecido de vós todos, e de todo o povo de Israel, que em nome de Jesus Cristo, o nazareno, aquele a quem vós crucificastes e a quem Deus ressuscitou dos mortos, em nome desse é que este está são diante de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '11', 'Ele é a pedra que foi rejeitada por vós, os edificadores, a qual foi posta por cabeça da esquina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '12', 'E em nenhum outro há salvação, porque também debaixo do céu nenhum outro nome há, dado entre os homens, pelo qual devamos ser salvos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '13', 'Então eles, vendo a ousadia de Pedro e João, e informados de que eram homens sem letras e indoutos, se maravilharam; e tinham conhecimento que eles haviam estado com Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '14', 'E, vendo estar com eles o homem que fora curado, nada tinham que dizer em contrário.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '15', 'Todavia, mandando-os sair fora do conselho, conferenciaram entre si,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '16', 'Dizendo: Que havemos de fazer a estes homens? Porque a todos os que habitam em Jerusalém é manifesto que por eles foi feito um sinal notório, e não o podemos negar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '17', 'Mas, para que não se divulgue mais entre o povo, ameacemo-los para que não falem mais nesse nome a homem algum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '18', 'E, chamando-os, disseram-lhes que absolutamente não falassem, nem ensinassem, no nome de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '19', 'Respondendo, porém, Pedro e João, lhes disseram: Julgai vós se é justo, diante de Deus, ouvir-vos antes a vós do que a Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '20', 'Porque não podemos deixar de falar do que temos visto e ouvido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '21', 'Mas eles ainda os ameaçaram mais, e, não achando motivo para os castigar, deixaram-nos ir, por causa do povo; porque todos glorificavam a Deus pelo que acontecera;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '22', 'Pois tinha mais de quarenta anos o homem em quem se operara aquele milagre de saúde.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '23', 'E, soltos eles, foram para os seus e contaram tudo o que lhes disseram os principais dos sacerdotes e os anciãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '24', 'E, ouvindo eles isto, unânimes levantaram a voz a Deus, e disseram Senhor, tu és o que fizeste o céu, e a terra, e o mar, e tudo o que neles há;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '25', 'Que disseste pela boca de David, teu servo: Porque bramaram as gentes, e os povos pensaram coisas vãs?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '26', 'Levantaram-se os reis da terra, e os príncipes se ajuntaram à uma, contra o SENHOR e contra o seu Ungido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '27', 'Porque verdadeiramente contra o teu santo Filho Jesus, que tu ungiste, se ajuntaram, não só Herodes, mas Pôncio Pilatos, com os gentios e os povos de Israel;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '28', 'Para fazerem tudo o que a tua mão e o teu conselho tinham anteriormente determinado que se havia de fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '29', 'Agora pois, ó Senhor, olha para as suas ameaças, e concede aos teus servos que falem com toda a ousadia a tua palavra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '30', 'Enquanto estendes a tua mão para curar, e para que se façam sinais e prodígios pelo nome do teu santo Filho Jesus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '31', 'E, tendo orado, moveu-se o lugar em que estavam reunidos; e todos foram cheios do Espírito Santo, e anunciavam com ousadia a palavra de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '32', 'E era um o coração e a alma da multidão dos que criam, e ninguém dizia que coisa alguma do que possuía era sua própria, mas todas as coisas lhes eram comuns.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '33', 'E os apóstolos davam, com grande poder, testemunho da ressurreição do SENHOR Jesus, e em todos eles havia abundante graça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '34', 'Não havia pois entre eles necessitado algum; porque todos os que possuíam herdades ou casas, vendendo-as, traziam o preço do que fora vendido, e o depositavam aos pés dos apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '35', 'E repartia-se por cada um, segundo a necessidade que cada um tinha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '36', 'Então José, cognominado pelos apóstolos Barnabé (que, traduzido, é Filho da consolação), levita, natural de Chipre,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '4', '37', 'Possuindo uma herdade, vendeu-a, e trouxe o preço, e o depositou aos pés dos apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '1', 'MAS um certo varão chamado Ananias, com Safira, sua mulher, vendeu uma propriedade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '2', 'E reteve parte do preço, sabendo-o também sua mulher; e, levando uma parte, a depositou aos pés dos apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '3', 'Disse então Pedro: Ananias, porque encheu Satanás o teu coração, para que mentisses ao Espírito Santo, e retivesses parte do preço da herdade?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '4', 'Guardando-a não ficava para ti? E, vendida, não estava em teu poder? Porque formaste este desígnio em teu coração? Não mentiste aos homens, mas a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '5', 'E Ananias, ouvindo estas palavras, caiu e expirou. E um grande temor veio sobre todos os que isto ouviram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '6', 'E, levantando-se os mancebos, cobriram o morto, e, transportando-o para fora, o sepultaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '7', 'E, passando um espaço quase de três horas, entrou também sua mulher, não sabendo o que havia acontecido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '8', 'E disse-lhe Pedro: Diz-me, vendestes por tanto aquela herdade? Ela disse: Sim; por tanto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '9', 'Então Pedro lhe disse: Porque é que entre vós vos concertastes para tentar o Espírito do SENHOR? Eis aí à porta os pés dos que sepultaram o teu marido, e também te levarão a ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '10', 'E logo caiu aos seus pés, e expirou. E, entrando os mancebos, acharam-na morta, e a sepultaram junto de seu marido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '11', 'E houve um grande temor em toda a igreja, e em todos os que ouviram estas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '12', 'E muitos sinais e prodígios eram feitos entre o povo pelas mãos dos apóstolos. E estavam todos unanimemente no alpendre de Salomão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '13', 'Quanto aos outros ninguém ousava ajuntar-se com eles: mas o povo tinha-os em grande estima.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '14', 'E a multidão dos que criam no SENHOR, tanto homens como mulheres, crescia cada vez mais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '15', 'De sorte que transportavam os enfermos para as ruas, e os punham em leitos e em camilhas para que ao menos a sombra de Pedro, quando este passasse, cobrisse alguns deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '16', 'E até das cidades circunvizinhas concorria muita gente a Jerusalém, conduzindo enfermos e atormentados de espíritos imundos, os quais todos eram curados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '17', 'E, levantando-se o sumo sacerdote, e todos os que estavam com ele (e eram eles da seita dos saduceus), encheram-se de inveja,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '18', 'E lançaram mão dos apóstolos, e os puseram na prisão pública.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '19', 'Mas de noite um anjo do SENHOR abriu as portas da prisão, e, tirando-os para fora, disse:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '20', 'Ide e apresentai-vos ao templo, e dizei ao povo todas as palavras desta vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '21', 'E, ouvindo eles isto, entraram de manhã cedo no templo, e ensinavam. Chegando, porém, o sumo sacerdote e os que estavam com ele, convocaram o conselho, e a todos os anciãos dos filhos de Israel, e enviaram ao cárcere, para que de lá os trouxessem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '22', 'Mas, tendo lá ido os servidores, não os acharam na prisão, e, voltando lho anunciaram,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '23', 'Dizendo: Achámos realmente o cárcere fechado, com toda a segurança, e os guardas, que estavam fora, diante das portas; mas, quando abrimos, ninguém achámos dentro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '24', 'Então o capitão do templo e os principais dos sacerdotes, ouvindo estas palavras, estavam perplexos acerca deles e do que viria a ser aquilo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '25', 'E, chegando um, anunciou-lhes, dizendo: Eis que os homens que encerrastes na prisão estão no templo e ensinam ao povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '26', 'Então foi o capitão com os servidores, e os trouxe, não com violência (porque temiam ser apedrejados pelo povo).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '27', 'E, trazendo-os, os apresentaram ao conselho. E o sumo sacerdote os interrogou, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '28', 'Não vos admoestámos nós expressamente que não ensinásseis nesse nome? E eis que enchestes Jerusalém dessa vossa doutrina, e quereis lançar sobre nós o sangue desse homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '29', 'Porém, respondendo Pedro e os apóstolos, disseram: Mais importa obedecer a Deus do que aos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '30', 'O Deus de nossos pais ressuscitou a Jesus, ao qual vós matastes, suspendendo-o no madeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '31', 'Deus com a sua dextra o elevou a Príncipe e Salvador, para dar a Israel o arrependimento e a remissão dos pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '32', 'E nós somos testemunhas acerca destas palavras, nós e também o Espírito Santo, que Deus deu àqueles que lhe obedecem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '33', 'E, ouvindo eles isto, se enfureciam, e deliberaram matá-los.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '34', 'Mas, levantando-se no conselho, um certo fariseu, chamado Gamaliel, doutor da lei, venerado por todo o povo, mandou que por um pouco levassem para fora os apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '35', 'E disse-lhes: Varões israelitas, acautelai-vos a respeito do que haveis de fazer a estes homens,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '36', 'Porque antes destes dias levantou-se Teudas, dizendo ser alguém; a este se ajuntou o número de uns quatrocentos homens; o qual foi morto, e todos os que lhe deram ouvidos foram dispersos e reduzidos a nada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '37', 'Depois deste levantou-se Judas, o galileu, nos dias do alistamento, e levou muito povo após si; mas também este pereceu, e todos os que lhe deram ouvidos foram dispersos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '38', 'E agora digo-vos: Dai de mão a estes homens, e deixai-os, porque, se este conselho ou esta obra é de homens se desfará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '39', 'Mas, se é de Deus, não podereis desfazê-la; para que não aconteça serdes também achados combatendo contra Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '40', 'E concordaram com ele. E, chamando os apóstolos, e tendo-os açoitado, mandaram que não falassem no nome de Jesus, e os deixaram ir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '41', 'Retiraram-se pois da presença do conselho, regozijando-se de terem sido julgados dignos de padecer afronta pelo nome de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '5', '42', 'E todos os dias, no templo e nas casas, não cessavam de ensinar, e de anunciar a Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '1', 'ORA naqueles dias, crescendo o número dos discípulos, houve uma murmuração dos gregos contra os hebreus, porque as suas viúvas eram desprezadas no ministério quotidiano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '2', 'E os doze, convocando a multidão dos discípulos, disseram: Não é razoável que nós deixemos a palavra de Deus e sirvamos às mesas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '3', 'Escolhei pois, irmãos, de entre vós sete varões de boa reputação, cheios do Espírito Santo e de sabedoria, aos quais constituamos sobre este importante negócio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '4', 'Mas nós perseveraremos na oração e no ministério da palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '5', 'E este parecer contentou a toda a multidão, e elegeram Estêvão, homem cheio de fé e do Espírito Santo, e Filipe, e Prócoro, e Nicanor, e Timon, e Pármenas e Nicolau, prosélito de Antioquia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '6', 'E os apresentaram ante os apóstolos, e estes, orando, lhes impuseram as mãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '7', 'E crescia a palavra de Deus, e em Jerusalém se multiplicava muito o número dos discípulos, e grande parte dos sacerdotes obedecia à fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '8', 'E Estêvão, cheio de fé e de poder, fazia prodígios e grandes sinais entre o povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '9', 'E levantaram-se alguns que eram da sinagoga chamada dos libertinos, e dos cireneus e dos alexandrinos, e dos que eram da Cilícia e da Ásia, e disputavam com Estêvão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '10', 'E não podiam resistir à sabedoria, e ao espírito com que falava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '11', 'Então subornaram uns homens, para que dissessem: Ouvimos-lhe proferir palavras blasfemas contra Moisés e contra Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '12', 'E excitaram o povo, os anciãos e os escribas; e, investindo com ele, o arrebataram e o levaram ao conselho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '13', 'E apresentaram falsas testemunhas, que diziam: Este homem não cessa de proferir palavras blasfemas contra este santo lugar e a lei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '14', 'Porque nós lhe ouvimos dizer que esse Jesus Nazareno há-de destruir este lugar e mudar os costumes que Moisés nos deu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '6', '15', 'Então todos os que estavam assentados no conselho, fixando os olhos nele, viram o seu rosto como o rosto de um anjo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '1', 'E DISSE o sumo sacerdote: Porventura é isto assim?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '2', 'E ele disse: Varões, irmãos, e pais, ouvi. O Deus da glória apareceu a nosso pai Abraão, estando na Mesopotâmia, antes de habitar em Harã,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '3', 'E disse-lhe: Sai da tua terra e de entre a tua parentela, e dirige-te a terra que eu te mostrar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '4', 'Então saiu da terra dos caldeus, e habitou em Harã. E dali, depois que seu pai faleceu, Deus o trouxe para esta terra em que habitais agora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '5', 'E não lhe deu nela herança, nem ainda o espaço de um pé: mas prometeu que lhe daria a posse dela, e depois dele à sua descendência, não tendo ele filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '6', 'E falou Deus assim: Que a sua descendência seria peregrina em terra alheia, e a sujeitariam à escravidão, e a maltratariam por quatrocentos anos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '7', 'E eu julgarei a nação que os tiver escravizado, disse Deus. E depois disto sairão e me servirão neste lugar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '8', 'E deu-lhe o pacto da circuncisão; e assim gerou a Isaque, e o circuncidou ao oitavo dia: e Isaque a Jacob e Jacob aos doze patriarcas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '9', 'E os patriarcas, movidos de inveja venderam a José para o Egipto; mas Deus era com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '10', 'E livrou-o de todas os suas tribulações, e lhe deu graça e sabedoria ante Faraó, rei do Egipto, que o constituiu governador sobre o Egipto e toda s sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '11', 'Sobreveio então a todo o país do Egipto e de Canaã fome e grande tribulação; e nossos pais não achavam alimentos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '12', 'Mas tendo ouvido Jacob, que no Egipto havia trigo, enviou ali nossos pais, a primeira vez.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '13', 'E na segunda vez foi José conhecido por seus irmãos, e a sua linhagem foi manifesta a Faraó.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '14', 'E José mandou chamar a seu pai Jacob, e a toda a sua parentela, que era de setenta e cinco almas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '15', 'E Jacob desceu ao Egipto, e morreu, ele e nossos pais;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '16', 'E foram transportados para Siquém, e depositados na sepultura que Abraão comprara por certa soma de dinheiro aos filhos de Hemor, pai de Siquém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '17', 'Aproximando-se, porém, o tempo da promessa que Deus tinha feito a Abraão, o povo cresceu e se multiplicou, no Egipto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '18', 'Até que se levantou outro rei, que não conhecia a José.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '19', 'Esse, usando de astúcia contra a nossa linhagem, maltratou nossos pais, ao ponto de os fazer enjeitar as suas crianças, para que não se multiplicassem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '20', 'Nesse tempo nasceu Moisés, e era mui formoso, e foi criado três meses em casa de seu pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '21', 'E, sendo enjeitado, tomou-o a filha de Faraó, e o criou como seu filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '22', 'E Moisés foi instruído em toda a ciência dos egípcios; e era poderoso em suas palavras e obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '23', 'E, quando completou a idade de quarenta anos, veio-lhe ao coração ir visitar seus irmãos, os filhos de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '24', 'E, vendo maltratado um deles, o defendeu, e vingou o ofendido, matando o egípcio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '25', 'E ele cuidava que seus irmãos entenderiam que Deus lhe havia de dar a liberdade pela sua mão; mas eles não entenderam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '26', 'E no dia seguinte, pelejando eles, foi por eles visto, e quis levá-los à paz, dizendo: Varões, sois irmãos porque vos agravais um ao outro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '27', 'E o que ofendia o seu próximo o repeliu, dizendo: Quem te constituiu príncipe e juiz sobre nós?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '28', 'Queres tu matar-me, como ontem mataste o egípcio?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '29', 'E a esta palavra fugiu Moisés, e esteve como estrangeiro na terra de Madian, onde gerou dois filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '30', 'E, completados quarenta anos, apareceu-lhe o anjo do SENHOR, no deserto do monte Sinai, numa chama de fogo de um sarçal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '31', 'Então Moisés, quando viu isto, se maravilhou da visão; e, aproximando-se para observar, foi-lhe dirigida a voz do SENHOR,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '32', 'Dizendo: Eu sou o Deus de teus pais, o Deus de Abraão, e o Deus de Isaque, e o Deus de Jacob. E Moisés, todo trémulo, não ousava olhar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '33', 'E disse-lhe o SENHOR: Tira as alparcas dos teus pés, porque o lugar em que estás é terra santa');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '34', 'Tenho visto atentamente a aflição do meu povo que está no Egipto, e ouvi os seus gemidos, e desci a livrá-los. Agora, pois, vem e enviar-te-ei ao Egipto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '35', 'A este Moisés, ao qual haviam negado, dizendo: Quem te constituiu príncipe e juiz? a este enviou Deus como príncipe e libertador, pela mão do anjo que lhe aparecera no sarçal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '36', 'Foi este que os conduziu para fora, fazendo prodígios e sinais na terra do Egipto, e no Mar Vermelho, e no deserto, por quarenta anos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '37', 'Este é aquele Moisés que disse aos filhos de Israel: O SENHOR vosso Deus vos levantará de entre vossos irmãos um profeta como eu: a ele ouvireis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '38', 'Este é o que esteve entre a congregação no deserto, com o anjo que lhe falava no monte Sinai, e com nossos pais, o qual recebeu as palavras de vida para no-las dar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '39', 'Ao qual nossos pais não quiseram obedecer, antes o rejeitaram, e em seu coração se tornaram ao Egipto,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '40', 'Dizendo a Arão: Faz-nos deuses que vão adiante de nós; porque a esse Moisés que nos tirou da terra do Egipto, não sabemos o que lhe aconteceu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '41', 'E naqueles dias fizeram o bezerro, e ofereceram sacrifícios ao ídolo, e se alegraram nas obras das suas mãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '42', 'Mas Deus se afastou, e os abandonou a que servissem ao exército do céu, como está escrito no livro dos profetas: Porventura me oferecestes vítimas e sacrifícios no deserto por quarenta anos, ó casa de Israel?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '43', 'Antes tomastes o tabernáculo de Moloque, e a estrela do vosso deus Refan, figuras que vós fizestes para as adorar. Transportar-vos-ei pois para além da Babilónia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '44', 'Estava entre nossos pais no deserto o tabernáculo do testemunho, como ordenara aquele que disse a Moisés que o fizesse segundo o modelo que tinha visto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '45', 'O qual nossos pais, recebendo-o também, o levaram com Josué quando entraram na posse das nações que Deus lançou para fora da presença de nossos pais, até aos dias de David.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '46', 'Que achou graça diante de Deus, e pediu que pudesse achar tabernáculo para o Deus de Jacob.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '47', 'E Salomão lhe edificou casa;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '48', 'Mas o Altíssimo não habita em templos feitos por mãos de homens, como diz o profeta');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '49', 'O céu é o meu trono, e a terra o estrado dos meus pés. Que casa me edificareis? Diz o SENHOR; ou qual é o lugar do meu repouso?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '50', 'Porventura não fez a minha mão todas estas coisas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '51', 'Homens de dura cerviz, e incircuncisos de coração e ouvido; vós sempre resistis ao Espírito Santo; assim vós sois como vossos pais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '52', 'A qual dos profetas não perseguiram vossos pais? Até mataram os que anteriormente anunciaram a vinda do Justo, do qual vós agora fostes traidores e homicidas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '53', 'Vós, que recebestes a lei por ordenação dos anjos, e não a guardastes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '54', 'E, ouvindo eles isto, enfureciam-se em seus corações, e rangiam os dentes contra ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '55', 'Mas ele, estando cheio do Espírito Santo, fixando os olhos no céu, viu a glória de Deus, e Jesus, que estava à direita de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '56', 'E disse: Eis que vejo os céus abertos, e o Filho do homem, que está em pé à mão direita de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '57', 'Mas eles gritaram com grande voz, taparam os seus ouvidos, e arremeteram unânimes contra ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '58', 'E, expulsando-o da cidade o apedrejavam. E as testemunhas depuseram os seus vestidos aos pés de um mancebo chamado Saulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '59', 'E apedrejaram a Estêvão, que em invocação dizia: Senhor Jesus, recebe o meu espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '7', '60', 'E, pondo-se de joelhos, clamou com grande voz: Senhor, não lhes imputes este pecado. E, tendo dito isto, adormeceu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '1', 'E TAMBÉM Saulo consentiu na morte dele. E fez-se naquele dia uma grande perseguição contra a igreja que estava em Jerusalém; e todos foram dispersos pelas terras da Judeia e da Samaria, excepto os apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '2', 'E uns varões piedosos foram enterrar Estêvão, e fizeram sobre ele grande pranto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '3', 'E Saulo assolava a igreja, entrando pelas casas; e, arrastando homens e mulheres, os encerrava na prisão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '4', 'Mas os que andavam dispersos iam por toda a parte, anunciando a palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '5', 'E, descendo Filipe à cidade de Samaria, lhes pregava a Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '6', 'E as multidões unanimemente prestavam atenção ao que Filipe dizia, porque ouviam e viam os sinais que ele fazia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '7', 'Pois que os espíritos imundos saíam de muitos que os tinham, clamando em alta voz; e muitos paralíticos e coxos eram curados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '8', 'E havia grande alegria naquela cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '9', 'E estava ali um certo homem, chamado Simão, que anteriormente exercera naquela cidade a arte mágica, e tinha iludido a gente de Samaria, dizendo que era uma grande personagem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '10', 'Ao qual todos atendiam, desde o mais pequeno até ao maior, dizendo: Este é a grande virtude de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '11', 'E atendiam-no a ele, porque já desde muito tempo os havia iludido com artes mágicas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '12', 'Mas, como cressem em Filipe, que lhes pregava acerca do reino de Deus e do nome de Jesus Cristo, se baptizavam, tanto homens como mulheres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '13', 'E creu até o próprio Simão; e, sendo baptizado, ficou de contínuo com Filipe; e, vendo os sinais e as grandes maravilhas que se faziam, estava atónito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '14', 'Os apóstolos, pois, que estavam em Jerusalém, ouvindo que Samaria recebera a palavra de Deus, enviaram para lá Pedro e João.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '15', 'Os quais, tendo descido, oraram por eles para que recebessem o Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '16', '(Porque sobre nenhum deles tinha ainda descido; mas somente eram baptizados em nome do SENHOR Jesus).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '17', 'Então lhes impuseram as mãos, e receberam o Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '18', 'E Simão, vendo que pela imposição das mãos dos apóstolos era dado o Espírito Santo, lhes ofereceu dinheiro,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '19', 'Dizendo: Dai-me também a mim esse poder, para que aquele sobre quem eu puser as mãos receba o Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '20', 'Mas disse-lhe Pedro: O teu dinheiro seja contigo para perdição, pois cuidaste que o dom de Deus se alcança por dinheiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '21', 'Tu não tens parte nem sorte nesta palavra, porque o teu coração não é recto diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '22', 'Arrepende-te pois dessa tua iniquidade, e ora a Deus, para que porventura te seja perdoado o pensamento do teu coração;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '23', 'Pois vejo que estás em fel de amargura, e em laço de iniquidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '24', 'Respondendo, porém, Simão, disse: Orai vós por mim ao SENHOR, para que nada do que disseste venha sobre mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '25', 'Tendo eles pois testificado e falado a palavra do SENHOR, voltaram para Jerusalém, e em muitas aldeias dos samaritanos anunciaram o evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '26', 'E o anjo do SENHOR falou a Filipe, dizendo: Levanta-te, e vai para a banda do sul, ao caminho que desce de Jerusalém para Gaza, que está deserta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '27', 'E levantou-se, e foi; e eis que um homem etíope, eunuco, mordomo-mor de Candace, rainha dos etíopes, o qual era superintendente de todos os seus tesouros, e tinha ido a Jerusalém para adoração,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '28', 'Regressava, e, assentado no seu carro, lia o profeta Isaías.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '29', 'E disse o Espírito a Filipe: Chega-te, e ajunta-te a esse carro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '30', 'E, correndo Filipe, ouviu que lia o profeta Isaías, e disse: Entendes tu o que lês?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '31', 'E ele disse: Como poderei eu entender, se alguém me não ensinar? E rogou a Filipe que subisse e com ele se assentasse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '32', 'E o lugar da Escritura que lia era este: Foi levado como a ovelha para o matadouro, e, como está mudo o cordeiro diante do que o tosquia, assim não abriu a sua boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '33', 'Na sua humilhação foi tirado o seu julgamento; e quem contará a sua geração? Porque a sua vida é tirada da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '34', 'E, respondendo o eunuco a Filipe, disse: Rogo-te, de quem diz isto o profeta? De si mesmo, ou dalgum outro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '35', 'Então Filipe, abrindo a sua boca, e começando nesta escritura, lhe anunciou a Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '36', 'E, indo eles caminhando, chegaram ao pé dalguma água, e disse o eunuco: Eis aqui água; que impede que eu seja baptizado?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '37', 'E disse Filipe: É lícito, se crês de todo o coração. E, respondendo ele, disse: Creio que Jesus Cristo é o Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '38', 'E mandou parar o carro, e desceram ambos à água, tanto Filipe como o eunuco, e o baptizou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '39', 'E, quando saíram da água, o Espírito do SENHOR arrebatou a Filipe, e não o viu mais o eunuco; e, jubiloso, continuou o seu caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '8', '40', 'E Filipe se achou em Azoto, e, indo passando, anunciava o Evangelho em todas as cidades, até que chegou a Cesareia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '1', 'E SAULO, respirando ainda ameaças e mortes contra os discípulos do SENHOR, dirigiu-se ao sumo sacerdote,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '2', 'E pediu-lhe cartas para Damasco para as sinagogas, a fim de que, se encontrasse alguns daquela seita, quer homens quer mulheres, os conduzisse presos a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '3', 'E, indo no caminho, aconteceu que, chegando perto de Damasco, subitamente o cercou um resplendor de luz do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '4', 'E, caindo em terra, ouviu uma voz que lhe dizia: Saulo, Saulo, porque me persegues?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '5', 'E ele disse: Quem és, Senhor? E disse o SENHOR: Eu sou Jesus a quem tu persegues. Duro é para ti recalcitrar contra os aguilhões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '6', 'E ele, tremendo e atónito, disse: Senhor, que queres que faça? E disse-lhe o SENHOR: Levanta-te, e entra na cidade, e lá te será dito o que te convém fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '7', 'E os varões, que iam com ele, pararam espantados, ouvindo a voz, mas não vendo ninguém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '8', 'E Saulo levantou-se da terra, e, abrindo os olhos, não via a ninguém. E, guiando-o pela mão, o conduziram a Damasco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '9', 'E esteve três dias sem ver, e não comeu nem bebeu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '10', 'E havia em Damasco um certo discípulo chamado Ananias; e disse-lhe o SENHOR em visão: Ananias. E ele respondeu: Eis-me aqui, Senhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '11', 'E disse-lhe o SENHOR: Levanta-te, e vai à rua chamada Direita, e pergunta em casa de Judas por um homem de Tarso chamado Saulo; pois eis que ele está orando;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '12', 'E numa visão ele viu que entrava um homem chamado Ananias, e punha sobre ele a mão, para que tornasse a ver.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '13', 'E respondeu Ananias: Senhor, a muitos ouvi acerca deste homem, quantos males tem feito aos teus santos em Jerusalém;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '14', 'E aqui tem poder dos principais dos sacerdotes para prender a todos os que invocam o teu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '15', 'Disse-lhe, porém, o SENHOR: Vai, porque este é para mim um vaso escolhido, para levar o meu nome diante dos gentios, e dos reis e dos filhos de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '16', 'E eu lhe mostrarei quanto deve padecer pelo meu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '17', 'E Ananias foi, e entrou na casa, e, impondo-lhe as mãos, disse: Irmão Saulo, o SENHOR Jesus, que te apareceu no caminho por onde vinhas, me enviou, para que tornes a ver e sejas cheio do Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '18', 'E logo lhe caíram dos olhos como que umas escamas, e recuperou a vista; e, levantando-se, foi baptizado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '19', 'E, tendo comido, ficou confortado. E esteve Saulo alguns dias com os discípulos que estavam em Damasco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '20', 'E logo nas sinagogas pregava a Jesus, que este era o Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '21', 'E todos os que o ouviam estavam atónitos, e diziam: Não é este o que em Jerusalém perseguia os que invocavam este nome, e para isso veio aqui para os levar presos aos principais dos sacerdotes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '22', 'Saulo, porém, se esforçava muito mais, e confundia os judeus que habitavam em Damasco, provando que aquele era o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '23', 'E, tendo passado muitos dias, os judeus tomaram conselho entre si para o matar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '24', 'Mas as suas ciladas vieram ao conhecimento de Saulo: e como eles guardavam as portas, tanto de dia como de noite, para poderem tirar-lhe a vida,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '25', 'Tomando-o de noite os discípulos, o desceram, dentro dum cesto, pelo muro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '26', 'E, quando Saulo chegou a Jerusalém, procurava ajuntar-se aos discípulos, mas todos o temiam, não crendo que fosse discípulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '27', 'Então Barnabé, tomando-o consigo, o trouxe aos apóstolos, e lhes contou como no caminho ele vira ao SENHOR e lhe falara, e como em Damasco falara ousadamente no nome de Jesus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '28', 'E andava com eles em Jerusalém, entrando e saindo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '29', 'E falava ousadamente no nome de Jesus. Falava e disputava também contra os gregos, mas eles procuravam matá-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '30', 'Sabendo-o, porém, os irmãos, o acompanharam até Cesareia, e o enviaram a Tarso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '31', 'Assim, pois, as igrejas em toda a Judeia, e Galileia e Samaria tinham paz, e eram edificadas; e se multiplicavam, andando no temor do SENHOR e consolação do Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '32', 'E aconteceu que, passando Pedro por toda a parte, veio também aos santos que habitavam em Lida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '33', 'E achou ali certo homem, chamado Eneias, jazendo numa cama havia oito anos, o qual era paralítico.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '34', 'E disse-lhe Pedro: Eneias, Jesus Cristo te dá saúde; levanta-te e faz a tua cama. E logo se levantou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '35', 'E viram-no todos os que habitavam em Lida e Sarona, os quais se converteram ao SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '36', 'E havia em Jope uma discípula chamada Tabitá, que traduzido se diz Dorcas. Esta estava cheia de boas obras e esmolas que fazia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '37', 'E aconteceu naqueles dias que, enfermando ela, morreu; e, tendo-a lavado, a depositaram num quarto alto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '38', 'E, como Lida era perto de Jope, ouvindo os discípulos que Pedro estava ali, lhe mandaram dois varões, rogando-lhe que não se demorasse em vir ter com eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '39', 'E, levantando-se Pedro, foi com eles; e quando chegou o levaram ao quarto alto, e todas as viúvas o rodearam, chorando e mostrando as túnicas e vestidos que Dorcas fizera quando estava com elas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '40', 'Mas Pedro, fazendo-as sair todas, pôs-se de joelhos e orou: e, voltando-se para o corpo, disse: Tabitá, levanta-te. E ela abriu os olhos, e, vendo a Pedro, assentou-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '41', 'E ele, dando-lhe a mão, a levantou, e, chamando os santos e as viúvas, apresentou-lha viva.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '42', 'E foi isto notório por toda a Jope, e muitos creram no SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '9', '43', 'E ficou muitos dias em Jope, com um certo Simão curtidor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '1', 'E HAVIA em Cesareia um varão por nome Cornélio, centurião da coorte chamada italiana,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '2', 'Piedoso e temente a Deus, com toda a sua casa, o qual fazia muitas esmolas ao povo, e de contínuo orava a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '3', 'Este, quase à hora nona do dia, viu claramente numa visão um anjo de Deus, que se dirigia para ele e dizia: Cornélio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '4', 'O qual, fixando os olhos nele, e muito atemorizado, disse: Que é, Senhor? E disse-lhe: As tuas orações e as tuas esmolas têm subido para memória diante de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '5', 'Agora, pois, envia homens a Jope, e manda chamar a Simão, que tem por sobrenome Pedro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '6', 'Este está com um certo Simão curtidor, que tem a sua casa junto do mar. Ele te dirá o que deves fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '7', 'E, retirando-se o anjo que lhe falava, chamou dois de seus criados, e a um piedoso soldado dos que estavam ao seu serviço.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '8', 'E, havendo-lhes contado tudo, os enviou a Jope.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '9', 'E no dia seguinte, indo eles seu caminho, e estando já perto de cidade, subiu Pedro ao terraço para orar, quase à hora sexta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '10', 'E, tendo fome, quis comer; e, enquanto lho preparavam, sobreveio-lhe um arrebatamento de sentidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '11', 'E viu o céu aberto, e que descia um vaso, como se fosse um grande lençol atado pelas quatro pontas, e vindo para a terra,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '12', 'No qual havia de todos os animais quadrúpedes e répteis da terra, e aves do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '13', 'E foi-lhe dirigida uma voz: Levanta-te, Pedro, mata e come.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '14', 'Mas Pedro disse: De modo nenhum, Senhor, porque nunca comi coisa alguma comum e imunda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '15', 'E segunda vez lhe disse a voz: Não faças tu comum ao que Deus purificou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '16', 'E aconteceu isto por três vezes; e o vaso tornou a recolher-se no céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '17', 'E, estando Pedro duvidando entre si acerca do que seria aquela visão que tinha visto, eis que os varões que foram enviados por Cornélio pararam à porta, perguntando pela casa de Simão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '18', 'E, chamando, perguntaram se Simão, que tinha por sobrenome Pedro, morava ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '19', 'E, pensando Pedro naquela visão, disse-lhe o Espírito: Eis que três varões te buscam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '20', 'Levanta-te pois, e desce, e vai com eles, não duvidando; porque eu os enviei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '21', 'E, descendo Pedro para junto dos varões que lhe foram enviados por Cornélio, disse: Sou eu a quem procurais; qual é a causa porque estais aqui?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '22', 'E eles disseram: Cornélio, o centurião, varão justo e temente a Deus, e que tem bom testemunho de toda a nação dos judeus, foi avisado por um santo anjo para que te chamasse a sua casa, e ouvisse as tuas palavras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '23', 'Então, chamando-os para dentro, os recebeu em casa. E no dia seguinte foi Pedro com eles, e foram com ele alguns irmãos de Jope.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '24', 'E no dia imediato chegaram a Cesareia. E Cornélio os estava esperando, tendo já convidado os seus parentes e amigos mais íntimos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '25', 'E aconteceu que entrando Pedro, saiu Cornélio a recebê-lo, e, prostrando-se a seus pés, o adorou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '26', 'Mas Pedro o levantou, dizendo: Levanta-te que eu também sou homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '27', 'E, falando com ele, entrou, e achou muitos que ali se haviam ajuntado,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '28', 'E disse-lhes: Vós bem sabeis que não é lícito, a um varão judeu ajuntar-se a estrangeiros; mas Deus mostrou-me que a nenhum homem chame comum ou imundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '29', 'Pelo que, sendo chamado, vim sem contradizer. Pergunto pois: porque razão mandastes chamar-me?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '30', 'E disse Cornélio: Há quatro dias estava eu em jejum até esta hora, orando em minha casa à hora nona.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '31', 'E eis que diante de mim se apresentou um varão com vestes resplandecentes, e disse: Cornélio, a tua oração foi ouvida, e as tuas esmolas estão em memória diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '32', 'Envia pois a Jope, e manda chamar Simão, o que tem por sobrenome Pedro; este está em casa de Simão o curtidor, junto do mar, e ele, vindo te falará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '33', 'E logo mandei chamar-te, e bem fizeste em vir. Agora pois estamos todos presentes diante de Deus, para ouvir tudo quanto por Deus te é mandado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '34', 'E, abrindo Pedro a boca, disse: Reconheço por verdade que Deus não faz acepção de pessoas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '35', 'Mas que lhe é agradável aquele que, em qualquer nação, o teme e obra o que é justo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '36', 'A palavra que ele enviou aos filhos de Israel, anunciando a paz por Jesus Cristo (este é o SENHOR de todos);');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '37', 'Esta palavra, vós bem sabeis, veio por toda a Judeia, começando pela Galileia, depois do baptismo que João pregou;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '38', 'Como Deus ungiu a Jesus de Nazaré com o Espírito Santo e com virtude; o qual andou fazendo bem, e curando a todos os oprimidos do diabo porque Deus era com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '39', 'E nós somos testemunhas de todas as coisas que fez, tanto na terra da Judeia como em Jerusalém: ao qual mataram, pendurando-o num madeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '40', 'A este ressuscitou Deus ao terceiro dia, e fez que se manifestasse,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '41', 'Não a todo o povo, mas às testemunhas que Deus antes ordenara; a nós, que comemos e bebemos juntamente com ele, depois que ressuscitou dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '42', 'E nos mandou pregar ao povo, e testificar que ele é o que por Deus foi constituído juiz dos vivos e dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '43', 'A este dão testemunho todos os profetas, de que todos os que nele crêem receberão o perdão dos pecados pelo seu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '44', 'E, dizendo Pedro ainda estas palavras, caiu o Espírito Santo sobre todos os que ouviam a palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '45', 'E os fiéis que eram da circuncisão, todos quantos tinham vindo com Pedro, maravilharam-se de que o dom do Espírito Santo se derramasse também sobre os gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '46', 'Porque os ouviam falar línguas, e magnificar a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '47', 'Respondeu então Pedro: Pode alguém porventura recusar a água, para que não sejam baptizados estes, que também receberam como nós o Espírito Santo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '10', '48', 'E mandou que fossem baptizados em nome do SENHOR. Então rogaram-lhe que ficasse com eles por alguns dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '1', 'E OUVIRAM os apóstolos, e os irmãos que estavam na Judeia, que também os gentios tinham recebido a palavra de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '2', 'E, subindo Pedro a Jerusalém, disputavam com ele os que eram da circuncisão,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '3', 'Dizendo: Entraste em casa de varões incircuncisos, e comeste com eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '4', 'Mas Pedro começou a fazer-lhes uma exposição por ordem, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '5', 'Estando eu orando na cidade de Jope, tive, num arrebatamento dos sentidos, uma visão: via um vaso, como um grande lençol que descia do céu e vinha até junto de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '6', 'E, pondo nele os olhos, considerei e vi animais da terra, quadrúpedes, e feras, e répteis, e aves do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '7', 'E ouvi uma voz que me dizia: Levanta-te, Pedro; mata e come.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '8', 'Mas eu disse: De maneira nenhuma, Senhor; pois nunca em minha boca entrou coisa alguma comum ou imunda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '9', 'Mas a voz respondeu-me do céu segunda vez: Não chames tu comum ao que Deus purificou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '10', 'E sucedeu isto por três vezes; e tudo tornou a recolher-se no céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '11', 'E eis que, na mesma hora, pararam junto da casa em que eu estava três varões que me foram enviados de Cesareia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '12', 'E disse-me o Espírito que fosse com eles, nada duvidando; e também estes seis irmãos foram comigo, e entrámos em casa daquele varão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '13', 'E contou-nos como vira em pé um anjo em sua casa, e lhe dissera: Envia varões a Jope, e manda chamar a Simão, que tem por sobrenome Pedro,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '14', 'O qual te dirá palavras com que te salves, tu e toda a tua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '15', 'E, quando comecei a falar, caiu sobre eles o Espírito Santo, como também sobre nós ao princípio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '16', 'E lembrei-me do dito do SENHOR, quando disse: João certamente baptizou com água; mas vós sereis baptizados com o Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '17', 'Portanto, se Deus lhes deu o mesmo dom que a nós, quando havemos crido no SENHOR Jesus Cristo, quem era então eu, para que pudesse resistir a Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '18', 'E, ouvindo estas coisas, apaziguaram-se, e glorificaram a Deus, dizendo: Na verdade até aos gentios deu Deus o arrependimento para a vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '19', 'E os que foram dispersos pela perseguição que sucedeu por causa de Estêvão caminharam até à Fenícia, Chipre e Antioquia, não anunciando a ninguém a palavra, senão somente aos judeus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '20', 'E havia entre eles alguns varões chíprios e cirenenses, os quais; entrando em Antioquia, falaram aos gregos, anunciando o SENHOR Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '21', 'E a mão do SENHOR era com eles; e grande número creu e se converteu ao SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '22', 'E chegou a fama destas coisas aos ouvidos da igreja que estava em Jerusalém; e enviaram Barnabé à Antioquia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '23', 'O qual, quando chegou, e viu a graça de Deus, se alegrou, e exortou a todos a que permanecessem no SENHOR com propósito do coração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '24', 'Porque era homem de bem, e cheio do Espírito Santo e de fé. E muita gente se uniu ao SENHOR,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '25', 'E partiu Barnabé para Tarso, a buscar Saulo; e, achando-o, o conduziu para Antioquia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '26', 'E sucedeu que todo um ano se reuniram naquela igreja, e ensinaram muita gente; e em Antioquia foram os discípulos, pela primeira vez, chamados cristãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '27', 'E naqueles dias desceram profetas de Jerusalém para Antioquia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '28', 'E, levantando-se um deles, por nome Ágabo, dava a entender, pelo Espírito, que haveria uma grande fome em todo o mundo, e isso aconteceu no tempo de Cláudio César.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '29', 'E os discípulos determinaram mandar, cada um conforme o que pudesse, socorro aos irmãos que habitavam na Judeia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '11', '30', 'O que eles com efeito fizeram, enviando-o aos anciãos por mão de Barnabé e de Saulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '1', 'E POR aquele mesmo tempo o rei Herodes estendeu as mãos sobre alguns da igreja, para os maltratar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '2', 'E matou à espada Tiago, irmão de João.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '3', 'E, vendo que isso agradara aos judeus, continuou, mandando prender também a Pedro. E eram os dias dos asmos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '4', 'E, havendo-o prendido, o encerrou na prisão, entregando-o a quatro quaternos de soldados, para que o guardassem, querendo apresentá-lo ao povo depois da páscoa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '5', 'Pedro, pois, era guardado na prisão; mas a igreja fazia contínua oração por ele a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '6', 'E quando Herodes estava para o fazer nessa mesma noite comparecer, estava Pedro dormindo entre dois soldados, ligado com duas cadeias, e os guardas diante da porta guardavam a prisão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '7', 'E eis que sobreveio o anjo do SENHOR, e resplandeceu uma luz na prisão; e, tocando a Pedro na ilharga, o despertou, dizendo: Levanta-te depressa. E caíram-lhe das mãos as cadeias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '8', 'E disse-lhe o anjo: Cinge-te, e ata as tuas alparcas. E ele o fez assim. Disse-lhe mais: Lança às costas a tua capa, e segue-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '9', 'E, saindo, o seguia. E não sabia que era real o que estava sendo feito pelo anjo, mas cuidava que via alguma visão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '10', 'E, quando passaram a primeira e segunda guarda, chegaram à porta de ferro, que dá para a cidade, a qual se lhes abriu por si mesma; e, tendo saído, percorreram uma rua, e logo o anjo se apartou dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '11', 'E Pedro, tornando a si, disse: Agora sei verdadeiramente que o SENHOR enviou o seu anjo, e me livrou da mão de Herodes, e de tudo o que o povo dos judeus esperava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '12', 'E, considerando ele nisto, foi a casa de Maria, mãe de João, que tinha por sobrenome Marcos, onde muitos estavam reunidos e oravam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '13', 'E, batendo Pedro à porta do pátio, uma menina chamada Rode saiu a escutar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '14', 'E, conhecendo a voz de Pedro, de gozo não abriu a porta, mas, correndo para dentro, anunciou que Pedro estava à porta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '15', 'E disseram-lhe: Estás fora de ti. Mas ela afirmava que assim era. E diziam: É o seu anjo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '16', 'Mas Pedro perseverava em bater, e, quando abriram, viram-no, e se espantaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '17', 'E, acenando-lhes ele com a mão para que se calassem, contou-lhes como o SENHOR o tirara da prisão, e disse: Anunciai isto a Tiago e aos irmãos. E, saindo, partiu para outro lugar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '18', 'E, sendo já dia, houve não pouco alvoroço entre os soldados sobre o que seria feito de Pedro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '19', 'E, quando Herodes o procurou e o não achou, feita inquirição aos guardas, mandou-os justiçar. E, partindo da Judeia para Cesareia, ficou ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '20', 'E ele estava irritado com os de Tiro e de Sidon; mas estes, vindo de comum acordo ter com ele, e obtendo a amizade de Blasto, que era o camarista do rei, pediam paz: porquanto o seu país se abastecia do país do rei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '21', 'E num dia designado, vestindo Herodes as vestes reais, estava assentado no tribunal, e lhes fez uma prática.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '22', 'E o povo exclamava: Voz de Deus, e não de homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '23', 'E no mesmo instante feriu-o o anjo do SENHOR, porque não deu glória a Deus, e, comido de bichos, expirou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '24', 'E a palavra de Deus crescia e se multiplicava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '12', '25', 'E Barnabé e Saulo havendo terminado aquele serviço, voltaram de Jerusalém, levando também consigo a João, que tinha por sobrenome Marcos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '1', 'E NA igreja que estava em Antioquia havia alguns profetas e doutores, a saber: Barnabé e Simeão, chamado Níger, e Lúcio, cireneu, e Manaen, que fora criado com Herodes o tetrarca, e Saulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '2', 'E, servindo ao SENHOR, e jejuando, disse o Espírito Santo: Apartai-me a Barnabé e a Saulo para a obra a que os tenho chamado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '3', 'Então, jejuando e orando, e pondo sobre eles as mãos, os despediram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '4', 'E assim estes, enviados pelo Espírito Santo, desceram a Selêucia e dali navegaram para Chipre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '5', 'E, chegados a Salamina, anunciavam a palavra de Deus nas sinagogas dos judeus; e tinham também a João como cooperador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '6', 'E, havendo atravessado a ilha até Pafos, acharam um certo judeu mágico, falso profeta, chamado Barjesus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '7', 'O qual estava com o procônsul Sérgio Paulo, varão prudente. Este, chamando a si Barnabé e Saulo, procurava muito ouvir a palavra de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '8', 'Mas resistia-lhes Elimas, o encantador (que assim se interpreta o seu nome), procurando apartar da fé o procônsul.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '9', 'Todavia, Saulo, que também se chama Paulo, cheio do Espírito Santo, e fixando os olhos nele disse:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '10', 'Ó filho do diabo, cheio de todo o engano e de toda a malícia, inimigo de toda a justiça, não cessarás de perturbar os rectos caminhos do SENHOR?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '11', 'Eis aí, pois, agora contra ti a mão do SENHOR, e ficarás cego, sem ver o sol por algum tempo. E no mesmo instante a escuridão e as trevas caíram sobre ele, e, andando à roda, buscava a quem o guiasse pela mão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '12', 'Então o procônsul, vendo o que havia acontecido, creu, maravilhado da doutrina do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '13', 'E, partindo de Pafos, Paulo e os que estavam com ele chegaram a Perge, da Panfília. Mas João, apartando-se deles, voltou para Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '14', 'E eles, saindo de Perge, chegaram a Antioquia, da Pisídia, e, entrando na sinagoga, num dia de sábado, assentaram-se;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '15', 'E, depois da lição da lei e dos profetas, lhes mandaram dizer os principais da sinagoga: Varões irmãos, se tendes alguma palavra de consolação para o povo, falai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '16', 'E, levantando-se Paulo, e pedindo silêncio com a mão, disse: Varões israelitas, e os que temeis a Deus, ouvi:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '17', 'O Deus deste povo de Israel escolheu a nossos pais, e exaltou o povo, sendo eles estrangeiros na terra do Egipto; e com braço poderoso os tirou dela;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '18', 'E suportou os seus costumes no deserto por espaço de quase quarenta anos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '19', 'E, destruindo a sete nações na terra de Canaã, deu-lhes por sorte a terra deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '20', 'E, depois disto, por quase quatrocentos e cinquenta anos, lhes deu juízes, até ao profeta Samuel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '21', 'E depois pediram um rei, e Deus lhes deu por quarenta anos, a Saul filho de Cis, varão da tribo de Benjamim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '22', 'E, quando este foi retirado, lhes levantou como rei a David, ao qual também deu testemunho, e disse: Achei a David, filho de Jessé, varão conforme o meu coração, que executará toda a minha vontade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '23', 'Da descendência deste, conforme a promessa, levantou Deus a Jesus para Salvador de Israel;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '24', 'Tendo primeiramente João, antes da vinda dele pregado a todo o povo de Israel o baptismo do arrependimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '25', 'Mas João, quando completava a carreira, disse: Quem pensais vós que eu sou? Eu não sou o Cristo; mas eis que após mim vem aquele a quem não sou digno de desatar as alparcas dos pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '26', 'Varões irmãos, filhos da geração de Abraão, e os que de entre vós temem a Deus, a vós vos é enviada a palavra desta salvação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '27', 'Por não terem conhecido a este, os que habitavam em Jerusalém, e os seus príncipes, condenaram-no, cumprindo assim as vozes dos profetas que se lêem todos os sábados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '28', 'Embora não achassem alguma causa de morte, pediram a Pilatos que ele fosse morto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '29', 'E, havendo eles cumprido todas as coisas que dele estavam escritas, tirando-o do madeiro, o puseram na sepultura;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '30', 'Mas Deus o ressuscitou dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '31', 'E ele por muitos dias foi visto pelos que subiram com ele da Galileia a Jerusalém, e são suas testemunhas para com o povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '32', 'E nós vos anunciamos que a promessa que foi feita aos pais Deus a cumpriu, a nós, seus filhos, ressuscitando a Jesus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '33', 'Como também está escrito no salmo segundo: Meu filho és tu, hoje te gerei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '34', 'E que o ressuscitaria dos mortos; para nunca mais tornar à corrupção, disse-o assim: As santas e fiéis bênçãos de David vos darei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '35', 'Pelo que também em outro Salmo diz: Não permitirás que o teu santo veja corrupção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '36', 'Porque, na verdade, tendo David no seu tempo servido conforme a vontade de Deus, dormiu, e foi posto junto de seus pais e viu a corrupção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '37', 'Mas aquele a quem Deus ressuscitou nenhuma corrupção viu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '38', 'Seja-vos pois notório, varões irmãos, que por este se vos anuncia a remissão dos pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '39', 'E de tudo o que, pela lei de Moisés, não pudestes ser justificados, por ele é justificado, todo aquele que crê.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '40', 'Vede pois que não venha sobre vós o que está dito nos profetas:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '41', 'Vede, ó desprezadores, e espantai-vos e desaparecei; porque opero uma obra em vossos dias. Obra tal que não crereis, se alguém vo-la contar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '42', 'E, saídos os judeus da sinagoga, os gentios rogaram que no sábado seguinte lhes fossem ditas as mesmas coisas:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '43', 'E, despedida a sinagoga, muitos dos judeus e dos prosélitos religiosos seguiram Paulo, e Barnabé; os quais, falando-lhes, os exortavam a que permanecessem na graça de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '44', 'E no sábado seguinte ajuntou-se quase toda a cidade a ouvir a palavra de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '45', 'Então os judeus, vendo a multidão, encheram-se de inveja e, blasfemando, contradiziam o que Paulo falava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '46', 'Mas Paulo e Barnabé, usando de ousadia, disseram: Era mister que a vós se vos pregasse primeiro a palavra de Deus; mas, visto que a rejeitais, e vos não julgais dignos da vida eterna, eis que nos voltamos para os gentios;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '47', 'Porque o SENHOR assim no-lo mandou: Eu te pus para luz dos gentios, para que sejas de salvação até aos confins da terra:,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '48', 'E os gentios, ouvindo isto, alegraram-se, e glorificavam a palavra do SENHOR; e creram todos quantos estavam ordenados para a vida eterna,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '49', 'E a palavra do SENHOR se divulgava por toda aquela província.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '50', 'Mas os judeus incitaram algumas mulheres religiosas e honestas, e os principais da cidade, e levantaram perseguição contra Paulo e Barnabé, e os lançaram fora dos seus termos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '51', 'Sacudindo, porém, contra eles o pó dos seus pés, partiram para Icónio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '13', '52', 'E os discípulos estavam cheios de alegria e do Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '1', 'E ACONTECEU que em Icónio entraram juntos na sinagoga dos judeus, e falaram de tal modo, que creu uma grande multidão, não só de judeus mas de gregos:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '2', 'Mas os judeus incrédulos incitaram e irritaram, contra os irmãos, os ânimos dos gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '3', 'Detiveram-se pois muito tempo, falando ousadamente acerca do SENHOR, o qual dava testemunho à palavra da sua graça, permitindo que por suas mãos se fizessem, sinais e prodígios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '4', 'E dividiu-se a multidão da cidade; e uns eram pelos judeus, e outros pelos apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '5', 'E, havendo um motim, tanto dos judeus como dos gentios, com os seus principais, para os insultarem e apedrejarem,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '6', 'Sabendo-o eles, fugiram para Listra e Derbe, cidades de Licaónia, e para a província circunvizinha;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '7', 'E ali pregavam o Evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '8', 'E estava assentado em Listra certo varão leso dos pés, coxo desde o ventre de sua mãe, o qual nunca tinha andado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '9', 'Este ouviu falar Paulo, que, fixando nele os olhos, e vendo que tinha fé para ser curado,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '10', 'Disse em voz alta: Levanta-te direito sobre teus pés. E ele saltou e andou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '11', 'E as multidões, vendo o que Paulo fizera, levantaram a sua voz, dizendo em língua licaónica: Fizeram-se os deuses semelhantes aos homens, e desceram até nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '12', 'E chamavam Júpiter a Barnabé, e Mercúrio a Paulo; porque este era o que falava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '13', 'E o sacerdote de Júpiter, cujo templo estava em frente da cidade, trazendo para a entrada da porta toiros e grinaldas, queria com a multidão, sacrificar-lhes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '14', 'Ouvindo, porém, isto, os apóstolos Barnabé e Paulo, rasgaram os seus vestidos, e saltaram para o meio da multidão, clamando,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '15', 'E dizendo: Varões, porque fazeis essas coisas? Nós também somos homens como vós, sujeitos às mesmas paixões, e vos anunciamos que vos convertais dessas vaidades ao Deus vivo, que fez o céu e a terra, e o mar, e tudo quanto há neles;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '16', 'O qual nos tempos passados deixou andar todas as gentes em seus próprios caminhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '17', 'E contudo, não se deixou a si mesmo sem testemunho, beneficiando-vos lá do céu, dando-vos chuvas e tempos frutíferos, enchendo de mantimento e de alegria os vossos corações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '18', 'E, dizendo isto, com dificuldade impediram que as multidões lhes sacrificassem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '19', 'Sobrevieram, porém, uns judeus de Antioquia e de Icónio, que, tendo convencido a multidão, apedrejaram a Paulo e o arrastaram para fora da cidade, cuidando que estava morto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '20', 'Mas, rodeando-o os discípulos, levantou-se, e entrou na cidade, e no dia seguinte saiu com Barnabé para Derbe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '21', 'E, tendo anunciado o evangelho naquela cidade, e feito muitos discípulos, voltaram para Listra, e Icónio e Antioquia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '22', 'Confirmando os ânimos dos discípulos, exortando-os a permanecer na fé, pois que por muitas tribulações nos importa entrar no reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '23', 'E, havendo-lhes, por comum consentimento, eleito anciãos em cada igreja, orando com jejuns, os encomendaram ao SENHOR em quem haviam crido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '24', 'Passando depois por Pisídia, dirigiram-se a Panfília.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '25', 'E, tendo anunciado a palavra em Perge, desceram a Atália.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '26', 'E dali navegaram para Antioquia donde tinham sido encomendados à graça de Deus para a obra que já haviam cumprido:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '27', 'E, quando chegaram e reuniram a igreja, relataram quão grandes coisas Deus fizera por eles, e como abrira aos gentios a porta da fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '14', '28', 'E ficaram ali não pouco tempo com os discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '1', 'ENTÃO alguns que tinham descido da Judeia ensinavam assim os irmãos: Se vos não circuncidares, conforme o uso de Moisés, não podeis salvar-vos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '2', 'Tendo tido Paulo e Barnabé não pequena discussão e contenda contra eles, resolveu-se que Paulo e Barnabé, e alguns de entre eles, subissem a Jerusalém, aos apóstolos e aos anciãos sobre aquela questão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '3', 'E eles, sendo acompanhados pela igreja, passavam pela Fenícia e por Samaria, contando a conversão dos gentios; e davam grande alegria a todos os irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '4', 'E, quando chegaram a Jerusalém, foram recebidos pela igreja e pelos apóstolos e anciãos, e lhes anunciaram quão grandes coisas Deus tinha feito com eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '5', 'Alguns, porém, da seita dos fariseus, que tinham crido, se levantaram, dizendo que era mister circuncidá-los e mandar-lhes que guardassem a lei de Moisés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '6', 'Congregaram-se pois os apóstolos e os anciãos para considerar este assunto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '7', 'E, havendo grande contenda, levantou-se Pedro e disse-lhes: Varões irmãos, bem sabeis que já há muito tempo Deus me elegeu de entre vós, para que os gentios ouvissem da minha boca a palavra do evangelho, e cressem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '8', 'E Deus, que conhece os corações, lhes deu testemunho, dando-lhes o Espírito Santo, assim como também a nós;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '9', 'E não fez diferença alguma entre eles e nós, purificando os seus corações pela fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '10', 'Agora, pois, porque tentais a Deus, pondo sobre a cerviz dos discípulos um jugo que nem nossos pais nem nós pudémos suportar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '11', 'Mas cremos que seremos salvos pela graça do SENHOR Jesus Cristo, como eles também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '12', 'Então toda a multidão se calou, e escutava a Barnabé e a Paulo, que contavam quão grandes sinais e prodígios Deus havia feito por meio deles entre os gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '13', 'E, havendo-se eles calado, tomou Tiago a palavra, dizendo: Varões irmãos, ouvi-me:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '14', 'Simão relatou como primeiramente Deus visitou os gentios, para tomar deles um povo para o seu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '15', 'E com isto concordam as palavras dos profetas, como está escrito:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '16', 'Depois disto voltarei, e reedificarei o tabernáculo de David que está caído, levantá-lo-ei das suas ruínas, e tornarei a edificá-lo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '17', 'Para que o resto dos homens busque ao SENHOR, e todos os gentios, sobre os quais o meu nome é invocado, diz o SENHOR, que faz todas estas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '18', 'Que são conhecidas desde toda a eternidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '19', 'Pelo que julgo que não se deve perturbar aqueles, de entre os gentios, que se convertem a Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '20', 'Mas escrever-lhes que se abstenham das contaminações dos ídolos, da prostituição, do que é sufocado e do sangue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '21', 'Porque Moisés, desde os tempos antigos, tem em cada cidade quem o pregue, e cada sábado é lido nas sinagogas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '22', 'Então pareceu bem aos apóstolos e aos anciãos, com toda a igreja, eleger varões de entre eles e enviá-los com Paulo e Barnabé a Antioquia, a saber: Judas, chamado Barsabás, e Silas, varões distintos entre os irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '23', 'E por intermédio deles escreveram o seguinte: Os apóstolos, e os anciãos e os irmãos, aos irmãos de entre os gentios que estão em Antioquia, e Síria e Cilícia, saúde.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '24', 'Porquanto ouvimos que alguns que saíram de entre nós vos perturbaram com palavras, e transtornaram as vossas almas, não lhes tendo nós dado mandamento,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '25', 'Pareceu-nos bem, reunidos concordemente, eleger alguns varões, e enviá-los com os nossos amados Barnabé e Paulo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '26', 'Homens que já expuseram as suas vidas pelo nome de nosso SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '27', 'Enviamos portanto Judas e Silas, os quais de boca vos anunciarão também o mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '28', 'Na verdade pareceu bem ao Espírito Santo, e a nós, não vos impor mais encargo algum, senão estas coisas necessárias:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '29', 'Que vos abstenhais das coisas sacrificadas aos ídolos, e do sangue, e da carne sufocada e da prostituição das quais coisas fazeis bem se vos guardardes. Bem vos vá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '30', 'Tendo-se eles então despedido, partiram para Antioquia, e, ajuntando a multidão, entregaram a carta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '31', 'E, quando a leram, alegraram-se pela exortação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '32', 'Depois Judas e Silas, que também eram profetas, exortaram e confirmaram os irmãos com muitas palavras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '33', 'E, detendo-se ali algum tempo, os irmãos os deixaram voltar em paz para os apóstolos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '34', 'Mas pareceu bem a Silas ficar ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '35', 'E Paulo e Barnabé ficaram em Antioquia, ensinando e pregando, com muitos outros, a palavra do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '36', 'E alguns dias depois disse Paulo a Barnabé: Tornemos a visitar nossos irmãos por todas as cidades em que já anunciámos a palavra do SENHOR, para ver como estão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '37', 'E Barnabé aconselhava que tomassem consigo a João chamado Marcos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '38', 'Mas a Paulo parecia razoável que não tomassem consigo aquele que desde Panfília se tinha apartado deles e não os acompanhou naquela obra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '39', 'E tal contenda houve entre eles, que se apartaram um do outro. Barnabé, levando consigo a Marcos, navegou para Chipre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '40', 'E Paulo, tendo escolhido a Silas, partiu, encomendado pelos irmãos à graça de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '15', '41', 'E passou pela Síria e Cilícia, confirmando as igrejas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '1', 'E CHEGOU a Derbe e Listra. E eis que estava ali um certo discípulo por nome Timóteo, filho de uma judia que era crente, mas de pai grego;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '2', 'Do qual davam bom testemunho os irmãos que estavam em Listra e em Icónio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '3', 'Paulo quis que este fosse com ele e tomando-o o circuncidou, por causa dos judeus que estavam naqueles lugares; porque todos sabiam que seu pai era grego.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '4', 'E, quando iam passando pelas cidades, lhes entregavam, para serem observados os decretos que haviam sido estabelecidos pelos apóstolos e anciãos em Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '5', 'De sorte que as igrejas eram confirmadas na fé, e cada dia cresciam em número.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '6', 'E, passando pela Frígia e pela província da Galácia, foram impedidos pelo Espírito Santo de anunciar a palavra na Ásia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '7', 'E, quando chegaram a Mísia, intentavam ir para Bitínia, mas o Espírito de Jesus não lho permitiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '8', 'E, tendo passado por Mísia, desceram a Tróade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '9', 'E Paulo teve de noite uma visão, em que se apresentou um varão da Macedónia, e lhe rogou, dizendo; Passa à Macedónia, e ajuda-nos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '10', 'E, logo depois desta visão, procurámos partir para a Macedónia, concluindo que o SENHOR nos chamava para lhes anunciarmos o evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '11', 'E, navegando de Tróade, fomos correndo em caminho direito para a Samotrácia, e no dia seguinte para Nápoles ;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '12', 'E dali para Filipos, que é a primeira cidade desta parte da Macedónia, e é uma colónia; e estivemos alguns dias nesta cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '13', 'E no dia de sábado saímos fora das portas, para a beira do rio, onde julgávamos ter lugar para oração; e, assentando-nos, falámos às mulheres que ali se juntaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '14', 'E uma certa mulher, chamada Lídia, vendedora de púrpura, da cidade de Tiatira, e que servia a Deus, nos ouvia, e o SENHOR lhe abriu o coração para que estivesse atenta ao que Paulo dizia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '15', 'E, depois que foi baptizada, ela e a sua casa, nos rogou, dizendo: Se haveis julgado que eu seja fiel ao SENHOR, entrai em minha casa, e ficai ali. E nos constrangeu a isso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '16', 'E aconteceu que, indo nós à oração, nos saiu ao encontro uma jovem, que tinha espírito de adivinhação, a qual, adivinhando, dava grande lucro aos seus senhores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '17', 'Esta, seguindo a Paulo e a nós, clamava, dizendo: Estes homens, que nos anunciam o caminho da salvação, são servos do Deus Altíssimo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '18', 'E isto fez ela por muitos dias. Mas Paulo, perturbado, voltou-se, e disse ao espírito. Em nome de Jesus Cristo, te mando que saias dela. E na mesma hora saiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '19', 'E, vendo seus senhores que a esperança do seu lucro estava perdida, prenderam Paulo e Silas; e os levaram a praça, à presença dos magistrados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '20', 'E, apresentando-os aos magistrados, disseram: Estes homens, sendo judeus, perturbaram a nossa cidade,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '21', 'E nos expõem costumes que nos não é lícito receber nem praticar, visto que somos romanos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '22', 'E a multidão se levantou unida contra eles, e os magistrados, rasgando-lhes os vestidos, mandaram açoitá-los com varas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '23', 'E, havendo-lhes dado muitos açoites, os lançaram na prisão, mandando ao carcereiro que os guardasse com segurança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '24', 'O qual, tendo recebido tal ordem, os lançou no cárcere interior, e lhes segurou os pés no tronco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '25', 'E, perto da meia noite, Paulo e Silas oravam e cantavam hinos a Deus, e os outros presos os escutavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '26', 'E de repente sobreveio um tão grande terremoto, que os alicerces do cárcere se moveram, e logo se abriram as portas, e foram soltas as prisões de todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '27', 'E, acordando o carcereiro, e vendo abertas as portas da prisão, tirou a espada, e quis matar-se, cuidando que os presos já tinham fugido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '28', 'Mas Paulo clamou com grande voz, dizendo: Não te faças nenhum mal, que todos aqui estamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '29', 'E, pedindo luz, saltou dentro, e, todo trémulo, se prostrou ante Paulo e Silas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '30', 'E, tirando-os para fora, disse: Senhores, que é necessário que eu faça para me salvar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '31', 'E eles disseram: Crê no SENHOR Jesus Cristo, e serás salvo, tu e a tua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '32', 'E lhe pregavam a palavra do SENHOR, e a todos os que estavam em sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '33', 'E, tomando-os ele consigo naquela mesma hora da noite, lavou-lhes os vergões; e logo foi baptizado, ele e todos os seus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '34', 'E, levando-os a sua casa, lhes pôs a mesa; e, na sua crença em Deus, alegrou-se com toda a sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '35', 'E, sendo já dia, os magistrados mandaram quadrilheiros, dizendo: Soltai aqueles homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '36', 'E o carcereiro anunciou a Paulo estas palavras, dizendo: Os magistrados mandaram que vos soltasse; agora pois saí, e ide em paz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '37', 'Mas Paulo replicou: Açoitaram-nos publicamente, e, sem sermos condenados, sendo homens romanos, nos lançaram na prisão, e agora encobertamente nos lançam fora? Não será assim; mas venham eles mesmos e tirem-nos para fora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '38', 'E os quadrilheiros foram dizer aos magistrados estas palavras; e eles temeram, ouvindo que eram romanos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '39', 'E, vindo, lhes dirigiram súplicas; e, tirando-os para fora, lhes pediram que saíssem da cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '16', '40', 'E, saindo da prisão, entraram em casa de Lídia, e, vendo os irmãos, os confortaram, e depois partiram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '1', 'E PASSANDO por Anfípolis e Apolónia, chegaram a Tessalónica, onde havia uma sinagoga de judeus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '2', 'E Paulo, como tinha por costume, foi ter com eles; e por três sábados disputou com eles sobre as Escrituras,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '3', 'Expondo e demonstrando que convinha que o Cristo padecesse e ressuscitasse dos mortos. E este Jesus, que vos anuncio, dizia ele, é o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '4', 'E alguns deles creram, e ajuntaram-se com Paulo e Silas; e também uma grande multidão de gregos religiosos, e não poucas mulheres principais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '5', 'Mas os judeus desobedientes, movidos de inveja, tomaram consigo alguns homens perversos, de entre os vadios, e, ajuntando o povo, alvoroçaram a cidade, e, assaltando a casa de Jason, procuravam tirá-los para junto do povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '6', 'E, não os achando, trouxeram Jason e alguns irmãos, à presença dos magistrados da cidade, clamando: Estes que têm alvoroçado o mundo, chegaram também aqui;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '7', 'Os quais Jason recolheu; e todos estes procedem contra os decretos de César, dizendo que há outro rei, Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '8', 'E alvoroçaram a multidão e os principais da cidade, que ouviram estas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '9', 'Tendo, porém, recebido satisfação de Jason, e dos demais, os soltaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '10', 'E logo os irmãos enviaram de noite Paulo e Silas a Bereia: e eles chegando lá, foram à sinagoga dos judeus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '11', 'Ora estes foram mais nobres do que os que estavam em Tessalónica, porque de bom grado receberam a palavra, examinando cada dia nas Escrituras se estas coisas eram assim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '12', 'De sorte que creram muitos deles, e também mulheres gregas da classe nobre, e não poucos varões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '13', 'Mas, logo que os judeus de Tessalónica souberam que a palavra de Deus também era anunciada por Paulo em Bereia, foram lá, e excitaram as multidões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '14', 'No mesmo instante os irmãos mandaram a Paulo que fosse até ao mar, mas Silas e Timóteo ficaram ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '15', 'E os que acompanhavam Paulo o levaram até Atenas, e, recebendo ordem para que Silas e Timóteo fossem ter com ele o mais depressa possível, partiram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '16', 'E, enquanto Paulo os esperava em Atenas, o seu espírito se comovia em si mesmo, vendo a cidade tão entregue à idolatria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '17', 'De sorte que disputava na sinagoga com os judeus e religiosos, e todos os dias na praça com os que se apresentavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '18', 'E alguns dos filósofos epicureus e estóicos contendiam com ele; e uns diziam: Que quer dizer este paroleiro? E outros: Parece que é pregador de deuses estranhos. Porque lhes anunciava a Jesus e a ressurreição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '19', 'E, tomando-o, o levaram ao Areópago, dizendo: Poderemos nós saber que nova doutrina é essa de que falas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '20', 'Pois coisas estranhas nos trazes aos ouvidos; queremos pois saber o que vem a ser isto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '21', '(Pois todos os atenienses e estrangeiros residentes, de nenhuma outra coisa se ocupavam, senão de dizer e ouvir alguma novidade).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '22', 'E, estando Paulo no meio do Areópago, disse: Varões atenienses, em tudo vos vejo um tanto supersticiosos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '23', 'Porque, passando eu e vendo os vossos santuários, achei também um altar em que estava escrito: AO DEUS DESCONHECIDO. Esse pois que vós honrais, é o que eu vos anuncio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '24', 'O Deus que fez o mundo e tudo que nele há, sendo SENHOR do céu e da terra, não habita em templos feitos por mãos de homens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '25', 'Nem tampouco é servido por mãos de homens, como que necessitando de alguma coisa; pois ele mesmo é quem dá a todos a vida, e a respiração, e todas as coisas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '26', 'E de um só fez toda a geração dos homens, para habitar sobre toda a face da terra, determinando os tempos já dantes ordenados, e os limites da sua habitação;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '27', 'Para que buscassem ao SENHOR, se porventura, tacteando, o pudessem achar; ainda que não está longe de cada um de nós;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '28', 'Porque nele vivemos, e nos movemos, e existimos; como também alguns dos vossos poetas disseram: Pois somos também sua geração:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '29', 'Sendo nós pois geração de Deus, não havemos de cuidar que a divindade seja semelhante ao ouro, ou à prata, ou à pedra esculpida por artifício e imaginação dos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '30', 'Mas Deus, não tendo em conta os tempos da ignorância, anuncia agora a todos os homens, e em todo o lugar, que se arrependam;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '31', 'Porquanto tem determinado um dia em que com justiça há-de julgar o mundo, por meio do varão que destinou; e disso deu certeza a todos, ressuscitando-o dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '32', 'E, como ouviram falar da ressurreição dos mortos, uns escarneciam, e outros diziam: Acerca disso te ouviremos outra vez.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '33', 'E assim Paulo saiu do meio deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '17', '34', 'Todavia, chegando alguns varões a ele, creram: entre os quais foi Dionísio, areopagita, e uma mulher por nome Dâmaris, e com eles outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '1', 'E DEPOIS disto partiu Paulo de Atenas, e chegou a Corinto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '2', 'E, achando um certo judeu por nome Áquila, natural do Ponto, que havia pouco tinha vindo da Itália, e Priscila, sua mulher (pois Cláudio tinha mandado que todos os judeus saíssem de Roma), se ajuntou com eles,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '3', 'E, como era do mesmo ofício, ficou com eles, e trabalhava; pois tinham por ofício fazer tendas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '4', 'E todos os sábados disputava na sinagoga, e convencia a judeus e gregos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '5', 'E, quando Silas e Timóteo desceram da Macedónia, foi Paulo impulsionado pela palavra, testificando aos judeus que Jesus era o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '6', 'Mas resistindo e blasfemando eles, sacudiu os vestidos, e disse-lhes: O vosso sangue seja sobre a vossa, cabeça; eu estou limpo, e desde agora parto para os gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '7', 'E, saindo dali, entrou em casa de um homem chamado Tito Justo, que servia a Deus, e cuja casa estava junto da sinagoga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '8', 'E Crispo, principal da sinagoga, creu no SENHOR com toda a sua casa; e muitos dos coríntios, ouvindo-o, creram e foram baptizados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '9', 'E disse o SENHOR em visão a Paulo: Não temas, mas fala, e não te cales;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '10', 'Porque eu sou contigo, e ninguém lançará mão de ti para te fazer mal, pois tenho muito povo nesta cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '11', 'E ficou ali um ano e seis meses, ensinando entre eles a palavra de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '12', 'Mas, sendo Gálio procônsul da Acaia, levantaram-se os judeus concordemente contra Paulo, e o levaram ao tribunal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '13', 'Dizendo: Este persuade os homens a servir a Deus contra a lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '14', 'E, querendo Paulo abrir a boca, disse Gálio aos judeus: Se houvesse, ó judeus, algum agravo ou crime enorme, com razão vos sofreria,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '15', 'Mas, se a questão é de palavras, e de nomes, e da lei que entre vós há, vede-o vós mesmos; porque eu não quero ser juiz dessas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '16', 'E expulsou-os do tribunal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '17', 'Então todos agarraram Sóstenes, principal da sinagoga, e o feriram diante do tribunal; e a Gálio nada destas coisas o incomodava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '18', 'E Paulo, ficando ainda ali muitos dias, despediu-se dos irmãos e dali navegou para a Síria, e com ele Priscila e Áquila, tendo rapado a cabeça em Cencreia, porque tinha voto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '19', 'E chegou a Éfeso, e deixou-os ali; mas ele, entrando na sinagoga, disputava com os judeus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '20', 'E, rogando-lhe eles que ficasse por mais algum tempo, não conveio nisso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '21', 'Antes se despediu deles, dizendo: Querendo Deus, outra vez voltarei a vós. E partiu de Éfeso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '22', 'E, chegando a Cesareia, subiu a Jerusalém, e, saudando a igreja, desceu a Antioquia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '23', 'E, estando ali algum tempo, partiu, passando sucessivamente pela província da Galácia e da Frígia, confirmando a todos os discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '24', 'E chegou a Éfeso um certo judeu chamado Apolo, natural de Alexandria, varão eloquente e poderoso nas Escrituras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '25', 'Este era instruído no Caminho do SENHOR, e, fervoroso de espírito, falava e ensinava diligentemente as coisas do SENHOR, conhecendo somente o baptismo de João.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '26', 'Ele começou a falar ousadamente na sinagoga; e, quando o ouviram Priscila e Áquila, o levaram consigo, e lhe declararam mais pontualmente o Caminho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '27', 'Querendo ele passar à Acaia, o animaram os irmãos, e escreveram aos discípulos que o recebessem; o qual, tendo chegado, aproveitou muito aos que pela graça criam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '18', '28', 'Porque com grande veemência convencia publicamente os judeus, mostrando pelas Escrituras que Jesus era o Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '1', 'E SUCEDEU que, enquanto Apolo estava em Corinto, Paulo, tendo passado por todas as regiões superiores, chegou a Éfeso; e, achando ali alguns discípulos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '2', 'Disse-lhes: Recebestes vós já o Espírito Santo quando crestes? E eles disseram-lhe: Nós nem ainda ouvimos que haja Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '3', 'Perguntou-lhes então: Em que sois baptizados então? E eles disseram: o baptismo de João,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '4', 'Mas Paulo disse: Certamente João baptizou com o baptismo do arrependimento, dizendo ao povo que cresse no que após ele havia de vir, isto é, em Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '5', 'E os que ouviram foram baptizados em nome do SENHOR Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '6', 'E, impondo-lhes Paulo as mãos, veio sobre eles o Espírito Santo; e falavam línguas, e profetizavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '7', 'E estes eram, ao todo, uns doze varões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '8', 'E, entrando na sinagoga, falou ousadamente e por espaço de três meses, disputando e persuadindo-os acerca do reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '9', 'Mas, como alguns deles se endurecessem e não obedecessem, falando mal do Caminho perante a multidão, retirou-se deles, e separou os discípulos, disputando todos os dias na escola de um certo Tirano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '10', 'E durou isto por espaço de dois anos; de tal maneira que todos os que habitavam na Ásia ouviram a palavra do SENHOR Jesus, assim judeus como gregos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '11', 'E Deus pelas mãos de Paulo fazia maravilhas extraordinárias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '12', 'De sorte que até os lenços e aventais se levavam do seu corpo aos enfermos, e as enfermidades fugiam deles, e os espíritos malignos saíam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '13', 'E alguns dos exorcistas judeus ambulantes tentavam invocar o nome do SENHOR Jesus sobre os que tinham espíritos malignos, dizendo: Esconjuro-vos por Jesus a quem Paulo prega.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '14', 'E os que faziam isto eram sete filhos de Ceva, judeu, principal dos sacerdotes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '15', 'Respondendo, porém, o espírito maligno, disse: Conheço a Jesus, e bem sei quem é Paulo; mas vós quem sois?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '16', 'E, saltando neles o homem que tinha o espírito maligno, e assenhoreando-se de dois, pôde mais do que eles; de tal maneira que, nus e feridos, fugiram daquela casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '17', 'E foi isto notório a todos os que habitavam em Éfeso, tanto judeus como gregos; e caiu temor sobre todos eles, e o nome do SENHOR Jesus era engrandecido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '18', 'E muitos dos que tinham crido vinham, confessando e publicando os seus feitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '19', 'Também muitos dos que seguiam artes mágicas trouxeram os seus livros, e os queimaram na presença de todos, e, feita a conta do seu preço, acharam que montava a cinquenta mil peças de prata.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '20', 'Assim a palavra do SENHOR crescia poderosamente e prevalecia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '21', 'E, cumpridas estas coisas, Paulo propôs em espírito, ir a Jerusalém, passando pela Macedónia e pela Acaia, dizendo: Depois que houver estado ali importa-me ver também Roma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '22', 'E, enviando à Macedónia dois daqueles que o serviam, Timóteo e Erasto, ficou ele por algum tempo na Ásia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '23', 'E, naquele mesmo tempo, houve um não pequeno alvoroço acerca do Caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '24', 'Porque um certo ourives da prata, por nome Demétrio, que fazia de prata nichos de Diana, dava não pouco lucro aos artífices,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '25', 'Aos quais, havendo-os ajuntado com os oficiais de obras semelhantes, disse: Varões, vós bem sabeis que deste ofício temos a nossa prosperidade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '26', 'E bem vedes e ouvis que não só em Éfeso, mas até quase em toda a Ásia, este Paulo tem convencido e afastado uma grande multidão, dizendo que não são deuses os que se fazem com as mãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '27', 'E não somente há o perigo de que nossa profissão caia em descrédito, mas também de que o próprio templo da grande deusa Diana seja estimado em nada, vindo a majestade daquela que toda a Ásia e o mundo veneram a ser destruída.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '28', 'E, ouvindo-o, encheram-se de ira, e clamaram, dizendo: Grande é a Diana dos efésios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '29', 'E encheu-se de confusão toda a cidade; e unânimes correram ao teatro, arrebatando a Gaio e a Aristarco, macedónios, companheiros de Paulo na viagem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '30', 'E, querendo Paulo apresentar-se ao povo, não lho permitiram os discípulos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '31', 'E também alguns dos principais da Ásia, que eram seus amigos, lhe rogaram que não se apresentasse no teatro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '32', 'Uns pois clamavam de uma maneira, outros doutra, porque o ajuntamento era confuso; e os mais deles não sabiam por que causa se tinham ajuntado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '33', 'Então tiraram Alexandre de entre a multidão, impelindo-o os judeus para diante; e Alexandre, acenando com a mão, queria dar razão disto ao povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '34', 'Mas quando conheceram que era judeu, todos unanimemente levantaram a voz, clamando por espaço de quase duas horas: Grande é a Diana dos efésios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '35', 'Então o escrivão da cidade, tendo apaziguado a multidão, disse: Varões efésios, qual é o homem que não sabe que a cidade dos efésios é a guardadora do templo da grande deusa Diana, e da imagem que desceu de Júpiter ?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '36', 'Ora, não podendo isto ser contraditado, convém que vos aplaqueis, nada façais temerariamente;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '37', 'Porque estes homens que aqui trouxestes nem são sacrílegos nem blasfemam da vossa deusa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '38', 'Mas, se Demétrio e os artífices que estão com ele têm alguma coisa contra alguém, há audiências e há procônsules: que se acusem uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '39', 'E, se alguma outra coisa demandais, averiguar-se-á em legítimo ajuntamento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '40', 'Na verdade até corremos perigo de que, por hoje, sejamos acusados de sedição, não havendo coisa alguma com que possamos justificar este concurso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '19', '41', 'E, tendo dito isto, despediu o ajuntamento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '1', 'E DEPOIS que cessou o alvoroço, Paulo chamou a si os discípulos e, abraçando-os, saiu para a Macedónia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '2', 'E, havendo andado por aquelas terras, e exortando-os com muitas palavras, veio à Grécia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '3', 'E, passando ali três meses, e sendo-lhe pelos judeus postas ciladas, como tivesse de navegar para a Síria, determinou voltar pela Macedónia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '4', 'E acompanhou-o, até à Ásia, Sopater, de Bereia, e, dos de Tessalónica, Aristarco, e Segundo, e Gaio de Derbe, e Timóteo, e, dos da Ásia, Tíquico e Trófimo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '5', 'Estes, indo adiante, nos esperaram em Tróade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '6', 'E, depois dos dias dos pães asmos, navegámos de Filipos, e em cinco dias fomos ter com eles a Tróade, onde estivemos sete dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '7', 'E no primeiro dia da semana, ajuntando-se os discípulos para partir no dia seguinte, falava com eles; e alargou a prática até à meia noite.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '8', 'E havia muitas luzes no cenáculo onde estavam juntos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '9', 'E, estando um certo mancebo, por nome Eutico, assentado numa janela, caiu do terceiro andar, tomado de um sono profundo que lhe sobreveio durante o extenso discurso de Paulo, e foi levantado morto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '10', 'Paulo, porém, descendo, inclinou-se sobre ele e, abraçando-o, disse: Não vos perturbeis, que a sua alma nele está.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '11', 'E subindo, e partindo o pão, e comendo, ainda lhes falou largamente até à alvorada; e assim partiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '12', 'E levaram vivo o mancebo, e ficaram não pouco consolados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '13', 'Nós, porém, subindo ao navio, navegámos até Assos, onde devíamos receber a Paulo, porque assim o ordenara, indo ele por terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '14', 'E, logo que se ajuntou connosco em Assos, o recebemos, e fomos a Mitilene.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '15', 'E, navegando dali, chegámos no dia seguinte defronte de Quio, e no outro aportámos a Samos, e, ficando em Trogílio, chegámos no dia seguinte a Mileto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '16', 'Porque já Paulo tinha determinado passar adiante de Éfeso, para não gastar tempo na Ásia. Apressava-se pois para estar, se lhe fosse possível, em Jerusalém no dia de Pentecostes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '17', 'E de Mileto mandou a Éfeso, a chamar os anciãos da igreja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '18', 'E, logo que chegaram junto dele, disse-lhes: Vós bem sabeis, desde o primeiro dia em que entrei na Ásia, como em todo esse tempo me portei no meio de vós,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '19', 'Servindo ao SENHOR com toda a humildade, e com muitas lágrimas e tentações, que pelas ciladas dos judeus me sobrevieram;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '20', 'Como nada, que útil seja, deixei de vos anunciar, e ensinar publicamente e pelas casas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '21', 'Testificando, tanto aos judeus como aos gregos, a conversão a Deus e a fé em nosso SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '22', 'E agora, eis que, ligado eu pelo espírito, vou para Jerusalém, não sabendo o que lá me há-de acontecer;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '23', 'Senão o que o Espírito Santo de cidade em cidade me revela, dizendo que me esperam prisões e tribulações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '24', 'Mas em nada tenho a minha vida por preciosa, contanto que cumpra com alegria a minha carreira, e o ministério que recebi do SENHOR Jesus, para dar testemunho do evangelho da graça de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '25', 'E agora, na verdade, sei que todos vós, por quem passei pregando o reino de Deus, não vereis mais o meu rosto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '26', 'Portanto, no dia de hoje, vos protesto que estou limpo do sangue de todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '27', 'Porque nunca deixei de vos anunciar todo o conselho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '28', 'Olhai pois por vós, e por todo o rebanho sobre que o Espírito Santo vos constituiu bispos, para apascentardes a igreja de Deus, que ele resgatou com seu próprio sangue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '29', 'Porque eu sei isto, que, depois da minha partida, entrarão no meio de vós lobos cruéis, que não perdoarão ao rebanho;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '30', 'E que de entre vós mesmos se levantarão homens que falarão coisas perversas, para atraírem os discípulos após si.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '31', 'Portanto, vigiai, lembrando-vos de que durante três anos, não cessei, noite e dia, de admoestar com lágrimas a cada um de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '32', 'Agora pois, irmãos, encomendo-vos a Deus e à palavra da sua graça; a ele que é poderoso para vos edificar e dar herança entre todos os santificados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '33', 'De ninguém cobicei a prata, nem o ouro, nem o vestido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '34', 'Vós mesmos sabeis que para o que me era necessário a mim, e aos que estão comigo, estas mãos me serviram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '35', 'Tenho-vos mostrado em tudo que, trabalhando assim, é necessário auxiliar os enfermos, e recordar as palavras do SENHOR Jesus, que disse: Mais bem-aventurada coisa é dar do que receber.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '36', 'E, havendo dito isto pôs-se de joelhos, e orou com todos eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '37', 'E levantou-se um grande pranto entre todos, e, lançando-se ao pescoço de Paulo, o beijavam,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '20', '38', 'Entristecendo-se muito, principalmente pela palavra que dissera, que não veriam mais o seu rosto. E acompanharam-no até ao navio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '1', 'E ACONTECEU que, separando-nos deles, navegámos e fomos correndo caminho direito, e chegámos a Cós, e no dia seguinte a Rodes, de onde passámos a Pátara.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '2', 'E, achando um navio, que ia para a Fenícia, embarcámos nele, e partimos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '3', 'E, indo já à vista de Chipre, deixando-a à esquerda, navegámos para a Síria e chegámos a Tiro; porque o navio havia de ser descarregado ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '4', 'E, achando discípulos, ficámos ali sete dias; os quais pelo Espírito diziam a Paulo que não subisse a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '5', 'E, havendo passado ali aqueles dias, saímos, e seguimos nosso caminho, acompanhando-nos todos, com suas mulheres e filhos, até fora da cidade; e, postos de joelhos na praia, orámos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '6', 'E, saudando-nos uns aos outros, subimos ao navio; eles voltaram para suas casas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '7', 'E nós, concluída a navegação de Tiro, viemos a Ptolemaida; e havendo saudado os irmãos, ficámos com eles um dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '8', 'E no dia seguinte, partindo dali Paulo, e nós que com ele estávamos, chegámos a Cesareia; e, entrando em casa de Filipe, o evangelista, que era um dos sete, ficámos com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '9', 'E tinha este quatro filhas donzelas, que profetizavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '10', 'E, demorando-nos ali por muitos dias, chegou da Judeia um profeta, por nome Ágabo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '11', 'E, vindo ter connosco, tomou a cinta de Paulo, e ligando-se os seus próprios pés e mãos, disse: Isto diz o Espírito Santo: Assim ligarão os judeus em Jerusalém o varão de quem é esta cinta, e o entregarão nas mãos dos gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '12', 'E, ouvindo nós isto, rogámos-lhe, tanto nós como os que eram daquele lugar, que não subisse a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '13', 'Mas Paulo respondeu: Que fazeis vós, chorando e magoando-me o coração? Porque eu estou pronto, não só a ser ligado, mas ainda a morrer em Jerusalém pelo nome do SENHOR Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '14', 'E, como não podíamos convencê-lo, nos aquietámos, dizendo: Faça-se a vontade do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '15', 'E depois daqueles dias, havendo feito os nossos preparativos, subimos a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '16', 'E foram também connosco alguns discípulos de Cesareia, levando consigo um certo Mnasom, chíprio, discípulo antigo, com quem havíamos de hospedar-nos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '17', 'E, logo que chegámos a Jerusalém, os irmãos nos receberam de muito boa vontade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '18', 'E no dia seguinte Paulo entrou connosco em casa de Tiago, e todos os anciãos vieram ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '19', 'E, havendo-os saudado, contou-lhes por miúdo o que por seu ministério Deus fizera entre os gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '20', 'E, ouvindo-o eles, glorificaram ao SENHOR, e disseram-lhe: Bem vês, irmão, quantos milhares de judeus há que crêem, e todos são zeladores da lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '21', 'E já acerca de ti foram informados de que ensinas todos os judeus que estão entre os gentios a apartarem-se de Moisés, dizendo que não devem circuncidar seus filhos, nem andar segundo o costume da lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '22', 'Que faremos pois? Em todo o caso é necessário que a multidão se ajunte; porque terão ouvido que já és vindo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '23', 'Faz pois isto que te dizemos: Temos quatro varões que fizeram voto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '24', 'Toma estes contigo, e santifica-te com eles, e faz por eles os gastos para que rapem a cabeça, e todos ficarão sabendo que nada há daquilo de que foram informados acerca de ti, mas que também tu mesmo andas guardando a lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '25', 'Todavia, quanto aos que crêem dos gentios, já nós havemos escrito, e achado por bem, que nada disto observem; mas que só se guardem do que se sacrifica aos ídolos, e do sangue, e do sufocado e da prostituição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '26', 'Então Paulo, tomando consigo aqueles varões, entrou no dia seguinte no templo, já santificado com eles, anunciando serem já cumpridos os dias da purificação; e ficou ali até se oferecer por cada um deles a oferta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '27', 'E quando os sete dias estavam quase a terminar, os judeus da Ásia, vendo-o no templo, alvoroçaram todo o povo e lançaram mão dele,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '28', 'Clamando: Varões israelitas, acudi: este é o homem que por todas as partes ensina a todos, contra o povo e contra a lei, e contra este lugar; e, demais disto, introduziu também no templo os gregos, e profanou este santo lugar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '29', 'Porque tinham visto com ele na cidade a Trófimo, de Éfeso, o qual pensavam que Paulo introduzira no templo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '30', 'E alvoroçou-se toda a cidade, e houve grande concurso de povo; e, pegando de Paulo, o arrastaram para fora do templo, e logo as portas se fecharam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '31', 'E, procurando eles matá-lo, chegou ao tribuno da coorte o aviso de que Jerusalém estava toda em confusão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '32', 'O qual, tomando logo consigo soldados e centuriões, correu para eles. E, quando viram o tribuno e os soldados, cessaram de ferir a Paulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '33', 'Então, aproximando-se o tribuno, o prendeu e mandou atar com duas cadeias, e lhe perguntou quem era e o que tinha feito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '34', 'E na multidão uns clamavam duma maneira, outros doutra; mas, como nada podia saber ao certo, por causa do alvoroço, mandou conduzi-lo para a fortaleza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '35', 'E sucedeu que, chegando às escadas, os soldados tiveram de lhe pegar por causa da violência da multidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '36', 'Porque a multidão do povo o seguia, clamando: Mata-o!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '37', 'E, quando iam a introduzir Paulo na fortaleza, disse Paulo ao tribuno: É-me permitido dizer-te alguma coisa? E ele disse: Sabes o grego?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '38', 'Não és tu porventura aquele egípcio que antes destes dias fez uma sedição e levou ao deserto quatro mil salteadores?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '39', 'Mas Paulo lhe disse: Na verdade que sou um homem judeu, cidadão de Tarso, cidade não pouco célebre na Cilícia; rogo-te, porém, que me permitas falar ao povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '21', '40', 'E, havendo-lhe permitido, Paulo, pondo-se em pé nas escadas, fez sinal com a mão ao povo; e, feito grande silêncio, falou-lhe em língua hebraica, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '1', 'VARÕES irmãos e pais, ouvi agora a minha defesa perante vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '2', '(E, quando ouviram falar-lhe em língua hebraica, maior silêncio guardaram). E disse');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '3', 'Quanto a mim, sou varão judeu, nascido em Tarso da Cilícia, e nesta cidade criado aos pés de Gamaliel, instruído conforme a verdade da lei de nossos pais, zelador de Deus, como todos vós hoje sois.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '4', 'E persegui este caminho até à morte, prendendo, e metendo em prisões, tanto varões como mulheres,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '5', 'Como também o sumo sacerdote me é testemunha, e todo o conselho dos anciãos: e, recebendo destes cartas para os irmãos, fui a Damasco para trazer maniatados para Jerusalém aqueles que ali estivessem, a fim de que fossem castigados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '6', 'Ora aconteceu que, indo eu já de caminho, e chegando perto de Damasco, quase ao meio dia, de repente me rodeou uma grande luz do céu,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '7', 'E caí por terra, e ouvi uma voz que me dizia: Saulo, Saulo, porque me persegues?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '8', 'E eu respondi: Quem és, Senhor? E disse-me: Eu sou Jesus nazareno, a quem tu persegues.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '9', 'E os que estavam comigo viram, em verdade, a luz, e se atemorizaram muito; mas não ouviram a voz daquele que falava comigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '10', 'Então disse eu: Senhor, que farei? E o SENHOR disse-me: Levanta-te, e vai a Damasco, e ali se te dirá tudo o que te é ordenado fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '11', 'E, como eu não via, por causa do esplendor daquela luz, fui levado pela mão dos que estavam comigo, e cheguei a Damasco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '12', 'E um certo Ananias, varão piedoso conforme a lei, que tinha bom testemunho de todos os judeus que ali moravam,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '13', 'Vindo ter comigo, e apresentando-se, disse-me: Saulo, irmão, recobra a vista. E naquela mesma hora o vi.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '14', 'E ele disse: O Deus de nossos pais de antemão te designou para que conheças a sua vontade, e vejas aquele Justo, e ouças a voz da sua boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '15', 'Porque hás-de ser sua testemunha para com todos os homens do que tens visto e ouvido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '16', 'E agora porque te detens? Levanta-te, e baptiza-te, e lava os teus pecados, invocando o nome do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '17', 'E aconteceu que, tornando eu para Jerusalém, quando orava no templo, fui arrebatado para fora de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '18', 'E vi aquele que me dizia: Dá-te pressa, e sai apressadamente de Jerusalém; porque não receberão o teu testemunho acerca de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '19', 'E eu disse: Senhor, eles bem sabem que eu lançava na prisão e açoitava nas sinagogas os que criam em ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '20', 'E quando o sangue de Estêvão, tua testemunha, se derramava, também eu estava presente, e consentia na sua morte, e guardava os vestidos dos que o matavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '21', 'E disse-me: Vai, porque hei-de enviar-te aos gentios de longe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '22', 'E ouviram-no até esta palavra, e levantaram a voz, dizendo: Tira da terra um tal homem, porque não convém que viva.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '23', 'E, clamando eles, e arrojando de si os vestidos, e lançando pó para o ar,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '24', 'O tribuno mandou que o levassem para a fortaleza, dizendo que o examinassem com açoites, para saber por que causa assim clamavam contra ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '25', 'E, quando o estavam atando com correias, disse Paulo ao centurião que ali estava: É-vos lícito açoitar um romano, sem ser condenado?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '26', 'E, ouvindo isto, o centurião foi, e anunciou ao tribuno, dizendo: Vê o que vais fazer, porque este homem é romano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '27', 'E, vindo o tribuno, disse-lhe: Diz-me, és tu romano? E ele disse: Sim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '28', 'E respondeu o tribuno: Eu com grande soma de dinheiro alcancei este direito de cidadão. Paulo disse: Mas eu sou-o de nascimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '29', 'E logo dele se apartaram os que o haviam de examinar; e até o tribuno teve temor, quando soube que era romano, visto que o tinha ligado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '22', '30', 'E no dia seguinte, querendo saber ao certo a causa por que era acusado pelos judeus, soltou-o das prisões, e mandou vir os principais dos sacerdotes, e todo o seu conselho; e, trazendo Paulo, o apresentou diante deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '1', 'E, PONDO Paulo os olhos no conselho, disse: Varões irmãos, até ao dia de hoje tenho andado diante de Deus com toda a boa consciência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '2', 'Mas o sumo sacerdote, Ananias, mandou aos que estavam junto dele que o ferissem na boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '3', 'Então Paulo lhe disse: Deus te ferirá, parede branqueada: tu estás aqui assentado para julgar-me conforme a lei, e contra a lei me mandas ferir?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '4', 'E os que ali estavam disseram: Injurias o sumo sacerdote de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '5', 'E Paulo disse: Não sabia, irmãos, que era o sumo sacerdote; porque está escrito: Não dirás mal do príncipe do teu povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '6', 'E Paulo, sabendo que uma parte era de saduceus e outra de fariseus, clamou no conselho: Varões irmãos, eu sou fariseu, filho de fariseu, no tocante à esperança e ressurreição dos mortos sou julgado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '7', 'E, havendo dito isto houve dissensão entre os fariseus e saduceus; e a multidão se dividiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '8', 'Porque os saduceus dizem que não há ressurreição, nem anjo, nem espírito; mas os fariseus reconhecem uma e outra coisa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '9', 'E originou-se um grande clamor; e, levantando-se os escribas da parte dos fariseus, contendiam, dizendo: Nenhum mal achamos neste homem, e, se algum espírito ou anjo lhe falou, não resistamos a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '10', 'E, havendo grande dissensão, o tribuno, temendo que Paulo fosse despedaçado por eles, mandou descer a soldadesca, para que o tirassem do meio deles, e o levassem para a fortaleza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '11', 'E na noite seguinte, apresentando-se-lhe o SENHOR, disse: Paulo tem ânimo; porque, como de mim testificaste em Jerusalém, assim importa que testifiques também em Roma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '12', 'E, quando já era dia, alguns dos judeus fizeram uma conspiração, e juraram, dizendo que não comeriam nem beberiam enquanto não matassem a Paulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '13', 'E eram mais de quarenta os que fizeram esta conjuração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '14', 'E estes foram ter com os principais dos sacerdotes e anciãos, e disseram: Conjurámo-nos, sob pena de maldição, a nada provarmos até que matemos a Paulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '15', 'Agora, pois, com o conselho, rogai ao tribuno que vo-lo traga amanhã como que querendo saber mais alguma coisa de seus negócios, e, antes que chegue, estaremos prontos para o matar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '16', 'E o filho da irmã de Paulo, tendo ouvido acerca desta cilada, foi, e entrou na fortaleza, e o anunciou a Paulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '17', 'E Paulo, chamando a si um dos centuriões, disse: Leva este mancebo ao tribuno, porque tem alguma coisa que lhe comunicar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '18', 'Tomando-o ele, pois, o levou ao tribuno, e disse: O preso Paulo, chamando-me a si, me rogou que te trouxesse este mancebo, que tem alguma coisa que dizer-te.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '19', 'E o tribuno, tomando-o pela mão e pondo-se à parte, perguntou-lhe em particular: Que tens que me contar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '20', 'E disse ele: Os judeus se concertaram rogar-te que amanhã leves Paulo ao conselho, como que tendo de inquirir dele mais alguma coisa ao certo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '21', 'Mas tu não o creias; porque mais de quarenta homens de entre eles lhe andam armando ciladas: os quais se obrigaram, sob pena de maldição, a não comerem nem beberem até que o tenham morto; e já estão apercebidos, esperando de ti promessa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '22', 'Então o tribuno despediu o mancebo, mandando-lhe que a ninguém dissesse que lhe havia contado aquilo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '23', 'E, chamando dois centuriões, lhes disse: Aprontai para as três horas da noite duzentos soldados, e setenta de cavalo, e duzentos archeiros para irem até Cesareia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '24', 'E aparelhai cavalgaduras, para que, pondo nelas a Paulo, o levem salvo ao presidente Félix.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '25', 'E escreveu uma carta, que continha isto:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '26', 'Cláudio Lísias, a Félix, potentíssimo presidente, saúde.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '27', 'Esse homem foi preso pelos judeus; e, estando já a ponto de ser morto por eles, sobrevim eu com a soldadesca, e o livrei, informado de que era romano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '28', 'E, querendo saber a causa por que o acusavam, o levei ao seu conselho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '29', 'E achei que o acusavam de algumas questões da sua lei; mas que nenhum crime havia nele digno de morte ou de prisão,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '30', 'E, sendo-me notificado que os judeus haviam de armar ciladas a esse homem, logo to enviei, mandando também aos acusadores que perante ti digam o que tiverem contra ele. Passa bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '31', 'Tomando pois os soldados a Paulo, como lhe fora mandado, o trouxeram de noite a Antipátride.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '32', 'E no dia seguinte, deixando aos de cavalo irem com ele, tornaram à fortaleza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '33', 'Os quais, logo que chegaram a Cesareia, e entregaram a carta ao presidente, lhe apresentaram Paulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '34', 'E o presidente, lida a carta, perguntou de que província era; e, sabendo que da Cilícia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '23', '35', 'Ouvir-te-ei, disse, quando também aqui vierem os teus acusadores. E mandou que o guardassem no pretório de Herodes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '1', 'E CINCO dias depois o sumo sacerdote Ananias desceu com os anciãos, e um certo Tértulo, orador, os quais compareceram perante o presidente contra Paulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '2', 'E, sendo chamado, Tértulo começou a acusá-lo, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '3', 'Visto como por ti temos tanta paz e por tua prudência se fazem a este povo muitos e louváveis serviços, sempre e em todo o lugar, ó potentíssimo Félix, com todo o agradecimento o queremos reconhecer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '4', 'Mas, para que te não detenha muito, rogo-te que, conforme a tua equidade, nos ouças por pouco tempo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '5', 'Temos achado que este homem é uma peste, e promotor de sedições entre todos os judeus, por todo o mundo; e o principal defensor da seita dos nazarenos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '6', 'O qual intentou também profanar o templo: e por isso o prendemos, e conforme a nossa lei o quisemos julgar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '7', 'Mas, sobrevindo o tribuno Lísias, no-lo tirou de entre as mãos com grande violência,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '8', 'Mandando aos seus acusadores que viessem a ti: e dele tu mesmo, examinando-o, poderás entender tudo o de que o acusamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '9', 'E também os judeus o acusavam, dizendo serem estas coisas assim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '10', 'Paulo, porém, fazendo-lhe o presidente sinal que falasse, respondeu: Porque sei que já vai para muitos anos que desta nação és juiz, com tanto melhor ânimo respondo por mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '11', 'Pois bem podes saber que não há mais de doze dias que subi a Jerusalém a adorar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '12', 'E não me acharam no templo falando com alguém, nem amotinando o povo nas sinagogas, nem na cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '13', 'Nem tampouco podem provar as coisas de que agora me acusam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '14', 'Mas confesso-te isto: que, conforme aquele caminho que chamam seita, assim sirvo a Deus de nossos pais, crendo tudo quanto está escrito na lei e nos profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '15', 'Tendo esperança em Deus, como estes mesmos também esperam, de que há-de haver ressurreição de mortos, assim dos justos como dos injustos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '16', 'E por isso procuro sempre ter uma consciência sem ofensa, tanto para com Deus como para com os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '17', 'Ora, muitos anos depois, vim trazer à minha nação esmolas e ofertas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '18', 'Nisto me acharam já santificado no templo, não em ajuntamentos, nem com alvoroços, uns certos judeus da Ásia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '19', 'Os quais convinha que estivessem presentes perante ti, e me acusassem, se alguma coisa contra mim tivessem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '20', 'Ou digam estes mesmos, se acharam em mim alguma iniquidade, quando compareci perante o conselho,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '21', 'A não ser estas palavras, que, estando entre eles, clamei: Hoje sou julgado por vós acerca da ressurreição dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '22', 'Então Félix, havendo ouvido estas coisas, lhes pôs dilação, dizendo: Havendo-me informado melhor deste caminho, quando o tribuno Lísias tiver descido, então tomarei inteiro conhecimento dos vossos negócios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '23', 'E mandou ao centurião que o guardassem em prisão, tratando-o com brandura, e que a ninguém dos seus proibisse servi-lo ou vir ter com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '24', 'E alguns dias depois, vindo Félix com sua mulher Drusila, que era judia, mandou chamar a Paulo, e ouviu-o acerca da fé em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '25', 'E, tratando ele da justiça, e da temperança, e do juízo vindouro, Félix, espavorido, respondeu: Por agora vai-te, e em tendo oportunidade te chamarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '26', 'Esperando ao mesmo tempo que Paulo lhe desse dinheiro, para que o soltasse; pelo que também muitas vezes o mandava chamar, e falava com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '24', '27', 'Mas, passados dois anos, Félix teve por sucessor a Pórcio Festo; e, querendo Félix comprazer aos judeus, deixou a Paulo preso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '1', 'ENTRANDO pois Festo na província, subiu dali a três dias de Cesareia a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '2', 'E o sumo sacerdote e os principais dos judeus compareceram perante ele contra Paulo, e lhe rogaram,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '3', 'Pedindo como favor contra ele que o fizesse vir a Jerusalém, armando ciladas para o matarem no caminho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '4', 'Mas Festo respondeu que Paulo estava guardado em Cesareia, e que ele brevemente partiria para lá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '5', 'Os que pois, disse, de entre vós têm poder, desçam comigo e, se neste varão houver algum crime, acusem-no.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '6', 'E, não se demorando entre eles mais de dez dias, desceu a Cesareia; e no dia seguinte, assentando-se no tribunal, mandou que trouxessem Paulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '7', 'E, chegando ele, o rodearam os judeus que haviam descido de Jerusalém, trazendo contra Paulo muitas e graves acusações, que não podiam provar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '8', 'Mas ele, em sua defesa disse: Eu não pequei em coisa alguma contra a lei dos judeus, nem contra o templo, nem contra César.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '9', 'Todavia Festo, querendo comprazer aos judeus, respondendo a Paulo, disse: Queres tu subir a Jerusalém, e ser lá perante mim julgado acerca destas coisas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '10', 'Mas Paulo disse: Estou perante o tribunal de César, onde convém que seja julgado; não fiz agravo algum aos judeus, como tu muito bem sabes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '11', 'Se fiz algum agravo, ou cometi alguma coisa digna de morte, não recuso morrer; mas, se nada há das coisas de que estes me acusam, ninguém me pode entregar a eles; apelo para César.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '12', 'Então Festo, tendo falado com o conselho, respondeu: Apelaste para César? Para César irás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '13', 'E, passados alguns dias, o rei Agripa e Berenice vieram a Cesareia, a saudar Festo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '14', 'E, como ali ficassem muitos dias, Festo contou ao rei os negócios de Paulo, dizendo: Um certo varão foi deixado por Félix aqui preso,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '15', 'Por cujo respeito os principais dos sacerdotes e os anciãos dos judeus, estando eu em Jerusalém, compareceram perante mim, pedindo sentença contra ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '16', 'Aos quais respondi não ser costume dos romanos entregar algum homem à morte, sem que o acusado tenha presente os seus acusadores, e possa defender-se da acusação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '17', 'De sorte que, chegando eles aqui juntos, no dia seguinte, sem fazer dilação alguma, assentado no tribunal, mandei que trouxessem o homem,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '18', 'Acerca do qual, estando presentes os acusadores, nenhuma coisa apontaram daquelas que eu suspeitava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '19', 'Tinham, porém, contra ele algumas questões acerca da sua superstição, e de um tal Jesus, defunto, que Paulo afirmava viver.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '20', 'E, estando eu perplexo acerca da inquirição desta causa, disse se queria ir a Jerusalém, e lá ser julgado acerca destas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '21', 'E, apelando Paulo para que fosse reservado ao conhecimento de Augusto, mandei que o guardassem até que o envie a César.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '22', 'Então Agripa disse a Festo: Bem quisera eu também ouvir esse homem. E ele disse: Amanhã o ouvirás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '23', 'E, no dia seguinte, vindo Agripa e Berenice, com muito aparato, entraram no auditório com os tribunos e varões principais da cidade, sendo trazido Paulo por mandado de Festo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '24', 'E Festo disse: Rei Agripa, e todos os varões que estais presentes connosco: aqui vedes um homem de quem toda a multidão dos judeus me tem falado, tanto em Jerusalém como aqui, clamando que não convém que viva mais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '25', 'Mas, achando eu que nenhuma coisa digna de morte fizera, e apelando ele mesmo também para Augusto, tenho determinado enviar-lho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '26', 'Do qual não tenho coisa alguma certa que escreva ao meu senhor, e por isso perante vós o trouxe, principalmente perante ti, ó rei Agripa, para que, depois de interrogado, tenha alguma coisa que escrever.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '25', '27', 'Porque me parece contra a razão enviar um preso, e não notificar contra ele as acusações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '1', 'DEPOIS Agripa disse a Paulo: Permite-se-te que te defendas. Então Paulo, estendendo a mão em sua defesa, respondeu:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '2', 'Tenho-me por venturoso, ó rei Agripa, de que perante ti me haja hoje de defender de todas as coisas de que sou acusado pelos judeus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '3', 'Mormente sabendo eu que tens conhecimento de todos os costumes e questões que há entre os judeus; pelo que te rogo que me ouças com paciência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '4', 'A minha vida, pois, desde a mocidade, qual haja sido, desde o princípio, em Jerusalém, entre os da minha nação, todos os judeus a sabem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '5', 'Sabendo de mim desde o principio (se o quiserem testificar), que, conforme a mais severa seita da nossa religião, vivi fariseu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '6', 'E agora pela esperança da promessa que por Deus foi feita a nossos pais, estou aqui e sou julgado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '7', 'À qual as nossas doze tribos esperam chegar, servindo a Deus continuamente, noite e dia. Por esta esperança, ó rei Agripa, eu sou acusado pelos judeus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '8', 'Pois quê? Julga-se coisa incrível entre vós que Deus ressuscite os mortos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '9', 'Bem tinha eu imaginado que contra o nome de Jesus nazareno devia eu praticar muitos actos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '10', 'O que também fiz em Jerusalém. E, havendo recebido poder dos principais dos sacerdotes, encerrei muitos dos santos nas prisões; e quando os matavam eu dava o meu voto contra eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '11', 'E, castigando-os muitas vezes por todas as sinagogas, os obriguei a blasfemar. E, enfurecido demasiadamente contra eles, até nas cidades estranhas os persegui.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '12', 'Sobre o que, indo então a Damasco, com poder e comissão dos principais dos sacerdotes,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '13', 'Ao meio dia, ó rei, vi no caminho uma luz do céu, que excedia o esplendor do sol, cuja claridade me envolveu a mim e aos que iam comigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '14', 'E, caindo nós todos por terra, ouvi uma voz que me falava, e em língua hebraica dizia: Saulo, Saulo, porque me persegues? Dura coisa te é recalcitrar contra os aguilhões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '15', 'E disse eu: Quem és, Senhor? E ele respondeu: Eu sou Jesus, a quem tu persegues;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '16', 'Mas levanta-te e põe-te sobre teus pés, porque te apareci por isto, para te pôr por ministro e testemunha tanto das coisas que tens visto como daquelas pelas quais te aparecerei ainda;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '17', 'Livrando-te deste povo, e dos gentios, a quem agora te envio,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '18', 'Para lhes abrires os olhos, e das trevas os converteres à luz, e do poder de Satanás a Deus; a fim de que recebam a remissão dos pecados pela fé em mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '19', 'Pelo que, ó rei Agripa, não fui desobediente à visão celestial.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '20', 'Antes anunciei primeiramente aos que estão em Damasco e em Jerusalém, e por toda a terra da Judeia, e aos gentios, que se emendassem e se convertessem a Deus, fazendo obras dignas de arrependimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '21', 'Por causa disto os judeus lançaram mão de mim no templo, e procuraram matar-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '22', 'Mas, alcançando socorro de Deus, ainda até ao dia de hoje permaneço dando testemunho tanto a pequenos como a grandes, não dizendo nada mais do que o que os profetas e Moisés disseram que devia acontecer,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '23', 'Isto é, que o Cristo devia padecer, e, sendo o primeiro da ressurreição dos mortos, devia anunciar a luz a este povo e aos gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '24', 'E, dizendo ele isto em sua defesa, disse Festo em alta voz: Estás louco, Paulo: as muitas letras te fazem delirar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '25', 'Mas ele disse: Não deliro ó potentíssimo Festo; antes digo palavras de verdade e de um são juízo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '26', 'Porque o rei, diante de quem falo com ousadia, sabe estas coisas, pois não creio que nada disto lhe é oculto; porque isto não se fez em qualquer canto,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '27', 'Crês tu nos profetas, ó rei Agripa? Bem sei que crês.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '28', 'E disse Agripa a Paulo: Por pouco me queres persuadir a que me faça cristão!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '29', 'E disse Paulo: Prouvera a Deus que, ou por pouco ou por muito, não somente tu, mas também todos quantos hoje me estão ouvindo, se tornassem tais qual eu sou, excepto estas cadeias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '30', 'E, dizendo ele isto, se levantou o rei, e o presidente, e Berenice, e os que com eles estavam assentados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '31', 'E, apartando-se dali, falavam uns com os outros, dizendo: Este homem nada fez digno de morte ou de prisões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '26', '32', 'E Agripa disse a Festo: Bem podia soltar-se este homem, se não houvera apelado para César.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '1', 'E, COMO se determinou que havíamos de navegar para a Itália entregaram Paulo, e alguns outros presos, a um centurião por nome Júlio, da coorte augusta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '2', 'E, embarcando nós em um navio adramitino partimos navegando pelos lugares da costa da Ásia, estando connosco Aristarco, macedónio, de Tessalónica.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '3', 'E chegámos no dia seguinte a Sídon, e Júlio, tratando Paulo humanamente, lhe permitiu ir ver os amigos, para que cuidassem dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '4', 'E, partindo dali, fomos navegando abaixo de Chipre, porque os ventos eram contrários.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '5', 'E, tendo atravessado o mar, ao longo da Cilícia e Panfília, chegámos a Mira, na Lícia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '6', 'E, achando ali o centurião um navio de Alexandria, que navegava para a Itália, nos fez embarcar nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '7', 'E, como por muitos dias navegássemos vagarosamente, havendo chegado apenas defronte de Cnido, não nos permitindo o vento ir mais adiante, navegámos abaixo de Creta, junto de Salmona.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '8', 'E, costeando-a dificilmente, chegámos a um lugar chamado Bons Portos, perto do qual estava a cidade de Laseia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '9', 'E, passado muito tempo, e sendo já perigosa a navegação, pois também o jejum já tinha passado, Paulo os admoestava,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '10', 'Dizendo-lhes: Varões, vejo que a navegação há-de ser incómoda, e com muito dano, não só para o navio e carga, mas também para as nossas vidas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '11', 'Mas o centurião cria mais no piloto e no mestre, do que no que dizia Paulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '12', 'E, como aquele porto não era cómodo para invernar, os mais deles foram de parecer que se partisse dali para ver se podiam chegar a Fénix, que é um porto de Creta que olha para a banda do vento da África e do Coro, e invernar ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '13', 'E, soprando o sul brandamente, lhes pareceu terem já o que desejavam, e, fazendo-se de vela, foram de muito perto costeando Creta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '14', 'Mas não muito depois deu nela um pé de vento, chamado Euro-aquilão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '15', 'E, sendo o navio arrebatado, e não podendo navegar contra o vento, dando de mão a tudo, nos deixámos ir à toa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '16', 'E, correndo abaixo de uma pequena ilha chamada Clauda, apenas pudémos ganhar o batel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '17', 'E, levado este para cima usaram de todos os meios, cingindo o navio; e, temendo darem à costa na Sirte, amainadas as velas, assim foram à toa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '18', 'E, andando nós agitados por uma veemente tempestade, no dia seguinte aliviaram o navio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '19', 'E ao terceiro dia nós mesmos, com as nossas próprias mãos, lançámos ao mar a armação do navio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '20', 'E, não aparecendo, havia já muitos dias, nem sol nem estrelas, e caindo sobre nós uma não pequena tempestade, fugiu-nos toda a esperança de nos salvarmos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '21', 'E, havendo já muito que se não comia, então Paulo, pondo-se em pé no meio deles, disse: Fora, na verdade; razoável, ó varões, ter-me ouvido a mim e não partir de Greta, e assim evitariam este incómodo e esta perdição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '22', 'Mas agora vos admoesto a que tenhais bom ânimo, porque não se perderá a vida de nenhum de vós, mas somente o navio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '23', 'Porque esta mesma noite o anjo de Deus, de quem eu sou, e a quem sirvo, esteve comigo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '24', 'Dizendo: Paulo, não temas; importa que sejas apresentado a César, e eis que Deus te deu todos quantos navegam contigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '25', 'Portanto, ó varões, tende bom ânimo; porque creio em Deus, que há-de acontecer assim como a mim me foi dito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '26', 'É contudo necessário irmos dar numa ilha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '27', 'E, quando chegou a décima quarta noite, sendo impelidos de uma e outra banda no mar Adriático, lá pela meia noite suspeitaram os marinheiros que estavam próximos de alguma terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '28', 'E, lançando o prumo, acharam vinte braças; e, passando um pouco mais adiante, tornando a lançar o prumo, acharam quinze braças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '29', 'E, temendo ir dar em alguns rochedos, lançaram da popa quatro âncoras, desejando que viesse o dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '30', 'Procurando, porém, os marinheiros fugir do navio, e tendo já deitado o batel ao mar, como que querendo lançar as âncoras pela proa,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '31', 'Disse Paulo ao centurião e aos soldados: Se estes não ficarem no navio, não podereis salvar-vos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '32', 'Então o soldados cortaram os cabos do batel, e o deixaram cair.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '33', 'E, entretanto que o dia vinha, Paulo exortava a todos a que comessem alguma coisa, dizendo: É já hoje o décimo dia que esperais, e permaneceis sem comer, não havendo provado nada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '34', 'Portanto, exorto-vos a que comais alguma coisa, pois é para a vossa saúde; porque nem um cabelo cairá da cabeça de qualquer de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '35', 'E, havendo dito isto, tomando o pão, deu graças a Deus na presença de todos; e, partindo-o, começou a comer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '36', 'E, tendo já todos bom ânimo, puseram-se também a comer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '37', 'E éramos por todos no navio duzentas e setenta e seis almas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '38', 'E, refeitos com a comida, aliviaram o navio, lançando o trigo ao mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '39', 'E, sendo já dia, não conheceram a terra; enxergaram porém uma enseada que tinha praia; e consultaram-se sobre se deveriam encalhar nela o navio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '40', 'E, levantando as âncoras, deixaram-no ir ao mar, largando também as amarras do leme; e, alçando a vela maior ao vento, dirigiram-se para a praia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '41', 'Dando, porém, num lugar de dois mares, encalharam ali o navio; e, fixa a proa, ficou imóvel, mas a popa abria-se com a força das ondas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '42', 'Então a ideia dos soldados foi que matassem os presos para que nenhum fugisse, escapando a nado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '43', 'Mas o centurião, querendo salvar a Paulo, lhes estorvou este intento; e mandou que os que pudessem nadar se lançassem primeiro ao mar, e se salvassem em terra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '27', '44', 'E os demais, uns em tábuas e outros em coisas do navio. E assim aconteceu que todos chegaram à terra a salvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '1', 'E HAVENDO escapado, então souberam que a ilha se chamava Malta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '2', 'E os bárbaros usaram connosco de não pouca humanidade; porque, acendendo uma grande fogueira, nos recolheram a todos por causa da chuva que caía, e por causa do frio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '3', 'E, havendo Paulo ajuntado uma quantidade de vides, e pondo-as no fogo, uma víbora, fugindo do calor, lhe acometeu a mão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '4', 'E os bárbaros, vendo-lhe a bicha pendurada na mão, diziam uns aos outros: Certamente este homem é homicida, visto como, escapando do mar, a Justiça não o deixa viver.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '5', 'Mas, sacudindo ele a bicha no fogo, não padeceu nenhum mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '6', 'E eles esperavam que viesse a inchar ou a cair morto de repente; mas tendo esperado já muito, e vendo que nenhum incómodo lhe sobrevinha, mudando de parecer, diziam que era um deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '7', 'E ali, próximo daquele lugar, havia umas herdades que pertenciam ao principal da ilha, por nome Públio, o qual nos recebeu e hospedou benignamente por três dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '8', 'E aconteceu estar de cama enfermo de febres e disenteria o pai de Públio, que Paulo foi ver, e, havendo orado, pôs as mãos sobre ele, e o curou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '9', 'Feito pois isto, vieram também ter com ele os demais que na ilha tinham enfermidades, e sararam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '10', 'Os quais nos distinguiram também com muitas honras; e, havendo de navegar, nos proveram das coisas necessárias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '11', 'E três meses depois partimos num navio de Alexandria que invernava na ilha, o qual tinha por insígnia Castor e Pólux.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '12', 'E, chegando a Siracusa, ficámos ali três dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '13', 'Donde, indo costeando, viemos a Régio; e soprando, um dia depois, um vento do sul, chegámos no segundo dia a Putéolos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '14', 'Onde, achando alguns irmãos, nos rogaram que por sete dias ficássemos com eles; e depois nos dirigimos a Roma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '15', 'E de lá ouvindo os irmãos novas de nós, nos saíram ao encontro à praça de Ápio e às três Vendas, e Paulo, vendo-os, deu graças a Deus, e tomou ânimo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '16', 'E, logo que chegámos a Roma, o centurião entregou os presos ao general dos exércitos; mas a Paulo se lhe permitiu morar sobre si à parte, com o soldado que o guardava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '17', 'E aconteceu que, três dias depois, Paulo convocou os principais dos judeus, e, juntos eles, lhes disse: Varões irmãos, não havendo eu feito nada contra o povo, ou contra os ritos paternos, vim contudo preso desde Jerusalém, entregue nas mãos dos romanos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '18', 'Os quais, havendo-me examinado, queriam soltar-me, por não haver em mim crime algum de morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '19', 'Mas, opondo-se os judeus, foi-me forçoso apelar para César, não tendo, contudo, de que acusar a minha nação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '20', 'Por esta causa vos chamei, para vos ver e falar; porque pela esperança de Israel estou com esta cadeia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '21', 'Então eles lhe disseram: Nós não recebemos acerca de ti cartas algumas da Judeia, nem veio aqui algum dos irmãos, que nos anunciasse ou dissesse de ti mal algum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '22', 'No entanto bem quiséramos ouvir de ti o que sentes; porque, quanto a esta seita, notório nos é que em toda a parte se fala contra ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '23', 'E, havendo-lhe eles assinalado um dia, muitos foram ter com ele à pousada, aos quais declarava com bom testemunho o reino de Deus, e procurava persuadi-los à fé de Jesus, tanto pela lei de Moisés como pelos profetas, desde pela manhã até à tarde.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '24', 'E alguns criam no que se dizia; mas outros não criam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '25', 'E, como ficaram entre si discordes, se despediram, dizendo Paulo esta palavra: Bem falou o Espírito Santo a nossos pais pelo profeta Isaías,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '26', 'Dizendo: Vai a este povo, e diz: De ouvido ouvireis, e de maneira nenhuma entendereis; e, vendo, vereis, e de maneira nenhuma percebereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '27', 'Porquanto o coração deste povo está endurecido, e com os ouvidos ouviram pesadamente, e fecharam os olhos, para que nunca com os olhos ouçam, nem do coração entendam, e se convertam e eu os cure.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '28', 'Seja-vos pois notório que esta salvação de Deus é enviada aos gentios, e eles a ouvirão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '29', 'E, havendo ele dito isto, partiram os judeus, tendo entre si grande contenda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '30', 'E Paulo ficou dois anos inteiros na sua própria habitação que alugara, e recebia todos quantos vinham vê-lo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('5', '28', '31', 'Pregando o reino de Deus, e ensinando com toda a liberdade as coisas pertencentes ao SENHOR Jesus Cristo, sem impedimento algum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '1', '¶ PAULO, servo de Jesus Cristo, chamado para apóstolo, separado para o evangelho de Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '2', 'O qual antes havia prometido pelos seus profetas nas santas escrituras,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '3', 'Acerca de seu Filho, que nasceu da descendência de David segundo a carne,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '4', 'Declarado Filho de Deus em poder, segundo o Espírito de santificação, pela ressurreição dos mortos - Jesus Cristo, Nosso SENHOR,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '5', 'Pelo qual recebemos a graça e o apostolado, para a obediência da fé entre todas as gentes pelo seu nome,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '6', 'Entre as quais sois também vós chamados para serdes de Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '7', 'A todos os que estais em Roma, amados de Deus, chamados santos: Graça e paz de Deus nosso pai, e do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '8', 'Primeiramente dou graças ao meu Deus por Jesus Cristo, acerca de vós todos, porque em todo o mundo é anunciada a vossa fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '9', 'Porque Deus, a quem sirvo em meu espírito, no evangelho de seu Filho, me é testemunha de como incessantemente faço menção de vós,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '10', 'Pedindo sempre em minhas orações que nalgum tempo, pela vontade de Deus, se me ofereça boa ocasião de ir ter convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '11', 'Porque desejo ver-vos, para vos comunicar algum dom espiritual, a fim de que sejais confortados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '12', 'Isto é: para que juntamente convosco eu seja consolado pela fé mutua, assim vossa como minha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '13', 'Não quero porém, irmãos, que ignoreis que muitas vezes propus ir ter convosco (mas até agora tenho sido impedido) para também ter entre vós algum fruto, como também entre os demais gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '14', 'Eu, sou devedor, tanto a gregos como a bárbaros, tanto a sábios como a ignorantes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '15', 'E assim, quanto está em mim, estou pronto para também vos anunciar o evangelho, a vós que estais em Roma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '16', 'Porque não me envergonho do evangelho de Cristo, pois é o poder de Deus para salvação de todo aquele que crê; primeiro do judeu, e também do grego.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '17', 'Porque nele se descobre a justiça de Deus de fé em fé, como está escrito: Mas o justo viverá da fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '18', 'Porque do céu se manifesta a ira de Deus sobre toda a impiedade e injustiça dos homens, que detêm a verdade em injustiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '19', 'Porquanto o que de Deus se pode conhecer neles se manifesta, porque Deus lho manifestou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '20', 'Porque as suas coisas invisíveis, desde a criação do mundo, tanto o seu eterno poder, como a sua divindade, se entendem, e claramente se vêem pelas coisas que estão criadas, para que eles fiquem inescusáveis;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '21', 'Porquanto, tendo conhecido a Deus, não o glorificaram como Deus, nem lhe deram graças, antes em seus discursos se desvaneceram, e o seu coração insensato se obscureceu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '22', 'Dizendo-se sábios, tornaram-se loucos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '23', 'E mudaram a glória do Deus incorruptível em semelhança da imagem de homem corruptível, e de aves, e de quadrúpedes, e de répteis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '24', 'Pelo que também Deus os entregou às concupiscências, de seus corações, à imundície, para desonrarem seus corpos entre si;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '25', 'Pois mudaram a verdade de Deus em mentira, e honraram e serviram mais a criatura do que o Criador, que é bendito eternamente. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '26', 'Pelo que Deus os abandonou às paixões infames. Porque até as suas mulheres mudaram o uso natural, no contrário à natureza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '27', 'E, semelhantemente, também os varões, deixando o uso natural da mulher, se inflamaram em sua sensualidade uns para com os outros, varão com varão, cometendo torpeza e recebendo em si mesmos a recompensa que convinha ao seu erro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '28', 'E, como eles se não importaram de ter conhecimento de Deus, assim Deus os entregou a um sentimento perverso, para fazerem coisas que não convêm;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '29', 'Estando cheios de toda a iniquidade, prostituição, malícia, avareza, maldade, cheios de inveja, homicídio, contenda, engano, malignidade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '30', 'Sendo murmuradores, detractores, aborrecedores de Deus, injuriadores, soberbos, presunçosos, inventores de males, desobedientes aos pais e às mães;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '31', 'Néscios, infiéis nos contratos, sem afeição natural, irreconciliáveis, sem misericórdia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '1', '32', 'Os quais, conhecendo a justiça de Deus (que são dignos de morte os que tais coisas praticam), não somente as fazem, mas também consentem aos que as fazem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '1', 'PORTANTO, és inescusável quando julgas, ó homem quem quer que sejas, porque te condenas a ti mesmo naquilo em que julgas a outro; pois tu, que julgas, fazes o mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '2', 'E bem sabemos que o juízo de Deus é segundo a verdade sobre os que tais coisas fazem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '3', 'E tu, ó homem, que julgas os que fazem tais coisas, cuidas que, fazendo-as tu, escaparás ao juízo de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '4', 'Ou desprezas tu as riquezas da sua benignidade, e paciência e longanimidade, ignorando que a benignidade de Deus te leva ao arrependimento?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '5', 'Mas, segundo a tua dureza e teu coração impenitente, entesouras ira para ti no dia da ira e da manifestação do juízo de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '6', 'O qual recompensará cada um segundo as suas obras;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '7', 'A saber: a vida eterna aos que, com perseverança em fazer bem, procuram glória, e honra e incorrupção;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '8', 'Mas a indignação e a ira aos que são contenciosos, e desobedientes à verdade e obedientes à iniquidade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '9', 'Tribulação e angústia sobre toda a alma do homem que obra o mal; primeiramente do judeu e também do grego;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '10', 'Glória, porém, e honra e paz a qualquer que obra o bem; primeiramente ao judeu e também ao grego;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '11', 'Porque, para com Deus, não há acepção de pessoas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '12', 'Porque todos os que sem lei pecaram sem lei também perecerão; e todos os que sob a lei pecaram pela lei serão julgados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '13', 'Porque os que ouvem a lei não são justos diante de Deus; mas os que praticam a lei hão-de ser justificados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '14', 'Porque, quando os gentios, que não têm lei, fazem naturalmente as coisas que são da lei, não tendo eles lei, para si mesmos são lei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '15', 'Os quais mostram a obra da lei escrita em seus corações, testificando juntamente a sua consciência, e os seus pensamentos, quer acusando-os, quer defendendo-os;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '16', 'No dia em que Deus há-de julgar os segredos dos homens, por Jesus Cristo, segundo o meu evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '17', 'Eis que tu que tens por sobrenome judeu, e repousas na lei, e te glorias em Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '18', 'E sabes a sua vontade e aprovas as coisas excelentes, sendo instruído por lei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '19', 'E confias que és guia dos cegos, luz dos que estão em trevas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '20', 'Instruidor dos néscios, mestre de crianças, que tens a forma da ciência, e da verdade na lei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '21', 'Tu, pois, que ensinas a outro, não te ensinas a ti mesmo? Tu, que pregas que não se deve furtar, furtas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '22', 'Tu, que dizes que não se deve adulterar, adulteras? Tu, que abominas os ídolos, cometes sacrilégio?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '23', 'Tu, que te glorias na lei, desonras a Deus pela transgressão da lei?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '24', 'Porque, como está escrito, o nome de Deus é blasfemado entre os gentios por causa de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '25', 'Porque a circuncisão é, na verdade, proveitosa, se tu guardares a lei; mas, se tu és transgressor da lei, a tua circuncisão se torna em incircuncisão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '26', 'Se, pois, a incircuncisão guardar os preceitos da lei, porventura a incircuncisão não será reputada como circuncisão?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '27', 'E a incircuncisão que por natureza o é, se cumpre a lei, não te julgará porventura a ti, que pela letra e circuncisão és transgressor da lei?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '28', 'Porque não é judeu o que o é exteriormente, nem é circuncisão a que o é exteriormente na carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '2', '29', 'Mas é judeu o que o é no interior, e circuncisão a que é do coração, no espírito, não na letra: cujo louvor não provém dos homens, mas de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '1', 'QUAL é logo a vantagem do judeu? Ou qual a utilidade da circuncisão?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '2', 'Muita, em toda a maneira, porque, primeiramente, as palavras de Deus lhe foram confiadas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '3', 'Pois quê? Se alguns foram incrédulos, a sua incredulidade aniquilará a fidelidade de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '4', 'De maneira nenhuma; sempre seja Deus verdadeiro, e todo o homem mentiroso; como está escrito: Para que sejas justificado em tuas palavras, e venças quando fores julgado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '5', 'E, se a nossa injustiça for causa da justiça de Deus, que diremos? Porventura será Deus injusto, trazendo ira sobre nós? (Falo como homem),');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '6', 'De maneira nenhuma; doutro modo, como julgará Deus o mundo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '7', 'Mas, se pela minha mentira abundou mais a verdade de Deus para glória sua, porque sou eu ainda julgado também como pecador?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '8', 'E porque não dizemos (como somos blasfemados, e como alguns dizem que dizemos): Façamos males, para que venham bens? A condenação desses é justa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '9', 'Pois quê? Somos nós mais excelentes? De maneira nenhuma, pois já dantes demonstrámos que, tanto judeus como gregos, todos estão debaixo do pecado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '10', 'Como está escrito: Não há um justo, nem um sequer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '11', 'Não há ninguém que entenda; não há ninguém que busque a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '12', 'Todos se extraviaram, e juntamente se fizeram inúteis. Não há quem faça o bem, não há nem um só.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '13', 'A sua garganta é um sepulcro aberto: com as suas línguas tratam enganosamente, peçonha de áspides está debaixo de seus lábios;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '14', 'Cuja boca está cheia de maldição e amargura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '15', 'Os seus pés são ligeiros para derramar sangue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '16', 'Em seus caminhos há destruição e miséria;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '17', 'E não conheceram o caminho da paz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '18', 'Não há temor de Deus diante de seus olhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '19', 'Ora, nós sabemos que tudo o que a lei diz aos que estão debaixo da lei, o diz, para que toda a boca esteja fechada e todo o mundo seja condenável diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '20', 'Por isso nenhuma carne será justificada diante dele pelas obras da lei, porque pela lei vem o conhecimento do pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '21', 'Mas agora se manifestou sem lei a justiça de Deus, tendo o testemunho da lei e dos profetas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '22', 'Isto é, a justiça de Deus pela fé em Jesus Cristo para todos e sobre todos os que crêem; porque não há diferença.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '23', 'Porque todos pecaram e destituídos estão da glória de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '24', 'Sendo justificados gratuitamente pela sua graça, pela redenção que há em Cristo Jesus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '25', 'Ao qual Deus propôs para propiciação pela fé no seu sangue, para demonstrar a sua justiça pela remissão dos pecados dantes cometidos, sob a paciência de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '26', 'Para demonstração da sua justiça neste tempo presente, para que ele seja justo e justificador daquele que tem fé em Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '27', 'Onde está logo a jactância? É excluída. Por qual lei? Das obras? Não; mas pela lei da fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '28', 'Concluímos pois que o homem é justificado pela fé sem as obras da lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '29', 'É porventura Deus somente dos judeus? E não o é também dos gentios? Também dos gentios, certamente,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '30', 'Se Deus é um só, que justifica pela fé a circuncisão, e por meio da fé a incircuncisão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '3', '31', 'Anulamos, pois, a lei pela fé? De maneira nenhuma, antes estabelecemos a lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '1', 'QUE diremos pois ter alcançado Abraão, nosso pai segundo a carne?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '2', 'Porque, se Abraão foi justificado pelas obras, tem de que se gloriar, mas não diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '3', 'Pois, que diz a Escritura? Creu Abraão a Deus, e isso lhe foi imputado como justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '4', 'Ora àquele que faz qualquer obra não lhe é imputado o galardão segundo a graça, mas segundo a dívida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '5', 'Mas àquele que não pratica, mas crê naquele que justifica o ímpio, a sua fé lhe é imputada como justiça,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '6', 'Assim também David declara bem-aventurado o homem a quem Deus imputa a justiça sem as obras, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '7', 'Bem-aventurados aqueles cujas maldades são perdoadas, e cujos pecados são cobertos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '8', 'Bem-aventurado o homem a quem o SENHOR não imputa o pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '9', 'Vem pois esta bem-aventurança sobre a circuncisão somente, ou também sobre a incircuncisão? Porque dizemos que a fé foi imputada como justiça a Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '10', 'Como lhe foi pois imputada? Estando na circuncisão ou na incircuncisão? Não na circuncisão, mas na incircuncisão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '11', 'E recebeu o sinal da circuncisão, selo da justiça da fé quando estava na incircuncisão, para que fosse pai de todos os que crêem, estando eles também na incircuncisão; a fim de que também a justiça lhes seja imputada;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '12', 'E fosse pai da circuncisão, daqueles que não somente são da circuncisão, mas que também andam nas pisadas daquela fé de nosso pai Abraão, que tivera na incircuncisão:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '13', 'Porque a promessa de que havia de ser herdeiro do mundo não foi feita pela lei a Abraão, ou à sua posteridade, mas pela justiça da fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '14', 'Porque, se os que são da lei são herdeiros, logo a fé é vã e a promessa é aniquilada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '15', 'Porque a lei opera a ira. Porque onde não há lei também não há transgressão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '16', 'Porque é pela fé, para que seja segundo a graça, a fim de que a promessa seja firme a toda a posteridade, não somente à que é da fé de Abraão, o qual é pai de todos nós,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '17', '(Como está escrito: Por pai de muitas nações te constituí) perante aquele no qual creu, a saber, Deus, o qual vivifica os mortos, e chama as coisas que não são como se já fossem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '18', 'O qual, em esperança, creu contra a esperança que seria feito pai de muitas nações, conforme o que lhe fora dito: Assim será a tua descendência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '19', 'E não enfraqueceu na fé, nem atentou para o seu próprio corpo já amortecido, pois era já de quase cem anos, nem tampouco para o amortecimento do ventre de Sara.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '20', 'E não duvidou da promessa de Deus por incredulidade, mas foi fortificado na fé, dando glória a Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '21', 'E estando certíssimo de que o que ele tinha prometido também era poderoso para o fazer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '22', 'Pelo que isso lhe foi também imputado como justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '23', 'Ora não só por causa dele está escrito, que lhe fosse tomado em conta,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '24', 'Mas também por nós, a quem será tomado em conta; os que cremos naquele que dos mortos ressuscitou a Jesus nosso SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '4', '25', 'O qual por nossos pecados foi entregue, e ressuscitou para nossa justificação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '1', 'SENDO pois justificados pela fé, temos paz com Deus, por nosso SENHOR Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '2', 'Pelo qual também temos entrada pela fé a esta graça, na qual estamos firmes, e nos gloriamos na esperança da glória de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '3', 'E não somente isto, mas também nos gloriamos nas tribulações; sabendo que a tribulação produz a paciência,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '4', 'E a paciência a experiência, e a experiência a esperança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '5', 'E a esperança não traz confusão, porquanto o amor de Deus está derramado em nossos corações pelo Espírito Santo que nos foi dado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '6', 'Porque Cristo, estando nós ainda fracos, morreu a seu tempo pelos ímpios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '7', 'Porque apenas alguém morrerá por um justo; pois poderá ser que pelo bom alguém ouse morrer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '8', 'Mas Deus prova o seu amor para connosco, em que Cristo morreu por nós, sendo nós ainda pecadores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '9', 'Logo muito mais agora, sendo justificados pelo seu sangue, seremos por ele salvos da ira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '10', 'Porque se nós, sendo inimigos, fomos reconciliados com Deus pela morte de seu Filho, muito mais, estando já reconciliados, seremos salvos pela sua vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '11', 'E não somente isto, mas também nos gloriamos em Deus por nosso SENHOR Jesus Cristo, pelo qual agora alcançamos a reconciliação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '12', 'Pelo que, como por um homem entrou o pecado no mundo, e pelo pecado a morte, assim também a morte passou a todos os homens por isso que todos pecaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '13', 'Porque até à lei estava o pecado no mundo, mas o pecado não é imputado não havendo lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '14', 'No entanto a morte reinou desde Adão até Moisés, até sobre aqueles que não pecaram à semelhança da transgressão de Adão, o qual é a figura daquele que havia de vir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '15', 'Mas não é assim o dom gratuito como a ofensa. Porque, se pela ofensa de um morreram muitos, muito mais a graça de Deus, e o dom pela graça, que é dum só homem, Jesus Cristo, abundou sobre muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '16', 'E não foi assim o dom como a ofensa, por um só que pecou. Porque o juízo veio de uma só ofensa, na verdade, para condenação, mas o dom gratuito veio de muitas ofensas para justificação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '17', 'Porque, se pela ofensa de um só, a morte reinou por esse, muito mais os que recebem a abundância da graça, e do dom da justiça, reinarão em vida por um só - Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '18', 'Pois assim como por uma só ofensa veio o juízo sobre todos os homens para condenação, assim também por um só acto de justiça veio a graça sobre todos os homens para justificação de vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '19', 'Porque, como pela desobediência de um só homem, muitos foram feitos pecadores, assim pela obediência de um muitos serão feitos justos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '20', 'Veio, porém, a lei para que a ofensa abundasse; mas, onde o pecado abundou, superabundou a graça;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '5', '21', 'Para que, assim como o pecado reinou na morte, também a graça reinasse pela justiça para a vida eterna, por Jesus Cristo nosso SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '1', 'QUE diremos pois? Permaneceremos no pecado, para que a graça abunde?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '2', 'De modo nenhum. Nós, que estamos mortos para o pecado, como viveremos ainda nele?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '3', 'Ou não sabeis que todos quantos fomos baptizados em Jesus Cristo fomos baptizados na sua morte?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '4', 'De sorte que fomos sepultados com ele pelo baptismo na morte; para que como Cristo ressuscitou dos mortos, pela glória do Pai, assim andemos nós também em novidade de vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '5', 'Porque, se fomos plantados juntamente com ele na semelhança da sua morte, também o seremos na da sua ressurreição;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '6', 'Sabemos isto, que o nosso homem velho foi com ele crucificado, para que o corpo do pecado seja desfeito, para que não sirvamos mais ao pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '7', 'Porque aquele que está morto está justificado do pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '8', 'Ora, se já morremos com Cristo, cremos que também com ele viveremos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '9', 'Sabemos que, havendo Cristo ressuscitado dos mortos, já não morre; a morte não mais terá domínio sobre ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '10', 'Pois, quanto a ter morrido, de uma vez morreu para o pecado, mas, quanto a viver, vive para Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '11', 'Assim também vós considerai-vos como mortos para o pecado, mas vivos para Deus em Cristo Jesus nosso SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '12', 'Não reine portanto o pecado em vosso corpo mortal, para lhe obedecerdes em suas concupiscências;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '13', 'Nem tampouco, apresenteis os vossos membros ao pecado por instrumentos de iniquidade; mas apresentai-vos a Deus, como vivos de entre mortos, e os vossos membros a Deus, como instrumentos de justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '14', 'Porque o pecado não terá domínio sobre vós, pois não estais debaixo da lei, mas debaixo da graça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '15', 'Pois quê? Pecaremos porque não estamos debaixo da lei, mas debaixo da graça? De modo nenhum.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '16', 'Não sabeis vós que a quem vos apresentardes por servos para lhe obedecer, sois servos daquele a quem obedeceis, ou do pecado para a morte, ou da obediência para a justiça?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '17', 'Mas graças a Deus que, tendo sido servos do pecado, obedecestes de coração à forma de doutrina a que fostes entregues.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '18', 'E, libertados do pecado, fostes feitos servos da justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '19', 'Falo como homem, pela fraqueza da vossa carne: pois que, assim como apresentastes os vossos membros para servirem à imundície, e à maldade para maldade, assim apresentai agora os vossos membros para servirem à justiça para santificação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '20', 'Porque, quando éreis servos do pecado, estáveis livres da justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '21', 'E que fruto tínheis então das coisas de que agora vos envergonhais? Porque o fim delas é a morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '22', 'Mas agora, libertados do pecado, e feitos servos de Deus, tendes o vosso fruto para santificação, e por fim a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '6', '23', 'Porque o salário do pecado é a morte, mas o dom gratuito de Deus é a vida eterna, por Cristo Jesus nosso SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '1', 'NÃO sabeis vós, irmãos (pois que falo aos que sabem a lei), que a lei tem domínio sobre o homem por todo o tempo que vive?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '2', 'Porque a mulher que está sujeita ao marido, enquanto ele viver, está-lhe ligada pela lei; mas, morto o marido, está livre da lei do marido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '3', 'De sorte que, vivendo o marido, será chamada adúltera, se for doutro marido; mas, morto o marido, livre está da lei, e assim não será adúltera, se for doutro marido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '4', 'Assim, meus irmãos, também vós estais mortos para a lei pelo corpo de Cristo, para que sejais doutro, daquele que ressuscitou de entre os mortos, a fim de que dêmos fruto para Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '5', 'Porque, quando estávamos na carne, as paixões dos pecados, que, são pela lei, obravam em nossos membros para darem fruto, para a morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '6', 'Mas agora estamos livres da lei, pois morremos para aquilo em que estávamos retidos; para que sirvamos em novidade de espírito, e não na velhice da letra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '7', 'Que diremos pois? É a lei pecado? De modo nenhum. Mas eu não conheci o pecado senão pela lei; porque eu não conheceria a concupiscência, se a lei não dissesse: Não cobiçarás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '8', 'Mas o pecado, tomando ocasião pelo mandamento, obrou em mim toda a concupiscência: porquanto sem a lei estava morto o pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '9', 'E eu, nalgum tempo, vivia sem lei, mas, vindo o mandamento, reviveu o pecado, e eu morri;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '10', 'E o mandamento que era para vida achei eu que me era para morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '11', 'Porque o pecado, tomando ocasião pelo mandamento, me enganou, e por ele me matou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '12', 'E assim a lei é santa, e o mandamento santo, justo e bom.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '13', 'Logo tornou-se-me o bom em morte? De modo nenhum; mas o pecado, para que se mostrasse pecado; operou em mim a morte pelo bem; a fim de que pelo mandamento o pecado se fizesse excessivamente maligno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '14', 'Porque bem sabemos que a lei é espiritual: mas eu sou carnal, vendido sob o pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '15', 'Porque o que faço não o aprovo; pois o que quero isso não faço, mas o que aborreço isso faço.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '16', 'E, se faço o que não quero, consinto com a lei, que é boa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '17', 'De maneira que agora já não sou eu que faço isto, mas o pecado que habita em mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '18', 'Porque eu sei que em mim, isto é, na minha carne, não habita bem algum: e com efeito o querer está em mim, mas não consigo realizar o bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '19', 'Porque não faço o bem que quero, mas o mal que não quero esse faço.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '20', 'Ora, se eu faço o que não quero, já o não faço eu, mas o pecado que habita em mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '21', 'Acho então esta lei em mim: que, quando quero fazer o bem, o mal está comigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '22', 'Porque, segundo o homem interior, tenho prazer na lei de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '23', 'Mas vejo nos meus membros outra lei, que batalha contra a lei do meu entendimento, e me prende debaixo da lei do pecado que está nos meus membros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '24', 'Miserável homem que eu sou! Quem me livrará do corpo desta morte?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '7', '25', 'Dou graças a Deus por Jesus Cristo nosso SENHOR. Assim que eu mesmo com o entendimento sirvo à lei de Deus, mas com a carne à lei do pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '1', 'PORTANTO agora nenhuma condenação há para os que estão em Cristo Jesus, que não andam segundo a carne, mas segundo o espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '2', 'Porque a lei do espírito de vida, em Cristo Jesus, me livrou da lei do pecado e da morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '3', 'Porquanto o que era impossível à lei, visto como estava enferma pela carne, Deus, enviando o seu Filho em semelhança da carne do pecado, pelo pecado condenou o pecado na carne;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '4', 'Para que a justiça da lei se cumprisse em nós, que não andamos segundo a carne, mas segundo o espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '5', 'Porque os que são segundo a carne inclinam-se para as coisas da carne; mas os que são segundo o espírito para as coisas do espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '6', 'Porque a inclinação da carne é morte; mas a inclinação do espírito é vida e paz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '7', 'Porquanto a inclinação da carne é inimizade contra Deus, pois não é sujeita à lei de Deus, nem, em verdade, o pode ser.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '8', 'Portanto os que estão na carne não podem agradar a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '9', 'Vós, porém, não estais na carne, mas no Espírito, se é que o Espírito de Deus habita em vós. Mas; se alguém não tem o Espírito de Cristo, esse tal não é dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '10', 'E, se Cristo está em vós, o corpo, na verdade, está morto por causa do pecado, mas o espírito vive por causa da justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '11', 'E, se o Espírito daquele que dos mortos ressuscitou a Jesus habita em vós, aquele que dos mortos ressuscitou a Cristo também vivificará os vossos corpos mortais, pelo seu Espírito que em vós habita.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '12', 'De maneira que, irmãos, somos devedores, não à carne para viver segundo a carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '13', 'Porque, se viverdes segundo a carne, morrereis; mas, se pelo espírito mortificardes as obras do corpo vivereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '14', 'Porque todos os que são guiados pelo Espírito de Deus esses são filhos de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '15', 'Porque não recebestes o espírito de escravidão, para outra vez estardes em temor, mas recebestes o espírito de adopção de filhos, pelo qual clamamos: Aba, Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '16', 'O mesmo Espírito testifica com o nosso espírito que somos filhos de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '17', 'E, se nós somos filhos, somos logo herdeiros também, herdeiros de Deus e co-herdeiros de Cristo: se é certo que com ele padecemos, para que também com ele sejamos glorificados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '18', 'Porque para mim tenho por certo que as aflições deste tempo presente não são para comparar com a glória que em nós há-de ser revelada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '19', 'Porque a ardente expectação da criatura espera a manifestação dos filhos de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '20', 'Porque a criação ficou sujeita à vaidade, não por sua vontade, mas por causa do que a sujeitou,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '21', 'Na esperança de que também a mesma criatura será libertada da servidão da corrupção, para a liberdade da glória dos filhos de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '22', 'Porque sabemos que toda a criatura geme e está juntamente com dores de parto até agora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '23', 'E não só ela, mas nós mesmos, que temos as primícias do Espírito, também gememos em nós mesmos, esperando a adopção, a saber, a redenção do nosso corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '24', 'Porque em esperança somos salvos. Ora a esperança que se vê não é esperança; porque o que alguém vê como o esperará?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '25', 'Mas, se esperamos o que não vemos, com paciência o esperamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '26', 'E da mesma maneira também o Espírito ajuda as nossas fraquezas; porque não sabemos o que havemos de pedir como convém, mas o mesmo Espírito intercede por nós com gemidos inexprimíveis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '27', 'E aquele que examina os corações sabe qual é a intenção do Espírito; e é ele que segundo Deus intercede pelos santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '28', 'E sabemos que todas as coisas contribuem juntamente para o bem daqueles que são chamados por seu decreto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '29', 'Porque os que dantes conheceu também os predestinou para serem conformes à imagem de seu Filho; a fim de que ele seja o primogénito entre muitos irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '30', 'E aos que predestinou a estes também chamou; e aos que chamou a estes também justificou; e aos que justificou a estes também glorificou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '31', 'Que diremos pois a estas coisas? Se Deus é por nós, quem será contra nós?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '32', 'Aquele que nem mesmo a seu próprio Filho poupou, antes o entregou por todos nós, como nos não dará também com ele todas as coisas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '33', 'Quem intentará acusação contra os escolhidos de Deus? É Deus quem os justifica.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '34', 'Quem os condenará? Pois é Cristo quem morreu, ou antes quem ressuscitou de entre os mortos, o qual está à direita de Deus, e também intercede por nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '35', 'Quem nos separará do amor de Cristo? A tribulação, ou a angústia, ou a perseguição, ou a fome, ou a nudez, ou o perigo, ou a espada?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '36', 'Como está escrito: Por amor de ti somos entregues à morte todo o dia: fomos reputados como ovelhas para o matadouro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '37', 'Mas em todas estas coisas somos mais do que vencedores, por aquele que nos amou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '38', 'Porque estou certo de que, nem a morte, nem a vida, nem os anjos, nem os principados, nem as potestades, nem o presente, nem o porvir,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '8', '39', 'Nem a altura, nem a profundidade, nem alguma outra criatura nos poderá separar do amor de Deus, que está em Cristo Jesus nosso SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '1', 'EM Cristo digo a verdade, não minto (dando-me testemunho a minha consciência no Espírito Santo);');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '2', 'Que tenho grande tristeza e contínua dor no meu coração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '3', 'Porque eu mesmo poderia desejar ser separado de Cristo, por amor de meus irmãos, que são meus parentes segundo a carne;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '4', 'Que são israelitas, dos quais é a adopção de filhos, e a glória, e os concertos, e a lei, e o culto, e as promessas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '5', 'Dos quais são os pais, e dos quais é Cristo segundo a carne, o qual é sobre todos; Deus bendito eternamente: Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '6', 'Não que a palavra de Deus haja faltado, porque nem todos os que são de Israel são israelitas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '7', 'Nem por serem descendência de Abraão são todos filhos; mas: Em Isaque será chamada a tua descendência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '8', 'Isto é: não são os filhos da carne que são filhos de Deus, mas os filhos da promessa são contados como descendência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '9', 'Porque a palavra da promessa é esta: Por este tempo virei, e Sara terá um filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '10', 'E não somente esta, mas também Rebeca, quando concebeu de um, de Isaque, nosso pai;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '11', 'Porque, não tendo eles ainda nascido, nem tendo feito bem ou mal (para que o propósito de Deus, segundo a eleição, ficasse firme, não por causa das obras, mas por aquele que chama),');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '12', 'Foi-lhe dito a ela: O maior servirá o menor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '13', 'Como está escrito: Amei Jacob e aborreci a Esaú.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '14', 'Que diremos pois? Que há injustiça da parte de Deus? De maneira nenhuma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '15', 'Pois dizia Moisés: Compadecer-me-ei de quem me compadecer, e terei misericórdia de quem eu tiver misericórdia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '16', 'Assim, pois, isto não depende do que quer, nem de que corre, mas de Deus, que se compadece.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '17', 'Porque diz a Escritura a Faraó: Para isto mesmo te levantei: Para em ti mostrar o meu poder, e para que o meu nome seja anunciado em toda a terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '18', 'Logo pois compadece-se de quem quer, e endurece a quem quer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '19', 'Dir-me-ás então: Porque se queixa ele ainda? Porquanto, quem resiste à sua vontade?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '20', 'Mas, ó homem, quem és tu, que a Deus replicas? Porventura a coisa formada dirá ao que a formou: Porque me, fizeste assim?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '21', 'Não tem o oleiro poder sobre o barro, para da mesma massa fazer um vaso para a honra e outro para desonra?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '22', 'E que direis se Deus querendo mostrar a sua ira, e dar a conhecer o seu poder, suportou com muita paciência os vasos da ira, preparados para a perdição;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '23', 'Para que também desse a conhecer as riquezas da sua glória nos vasos de misericórdia, que para glória já dantes preparou,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '24', 'Os quais somos nós, a quem também chamou, não só dentre os judeus, mas também dentre os gentios?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '25', 'Como também diz em Oseias: Chamarei meu povo ao que não era meu povo; e amada à que não era amada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '26', 'E sucederá que no lugar em que lhes foi dito: Vós não sois meu povo; aí serão chamados filhos do Deus vivo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '27', 'Também Isaías clamava acerca de Israel: Ainda que o número dos filhos de Israel seja como a areia do mar, o remanescente é que será salvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '28', 'Porque o SENHOR executará a sua palavra sobre a terra, completando-a e abreviando-a.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '29', 'E como antes disse Isaías: Se o SENHOR dos Exércitos nos não deixara descendência, teríamos sido feitos como Sodoma e seríamos semelhantes a Gomorra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '30', 'Que diremos pois? Que os gentios, que não buscavam a justiça, alcançaram a justiça? Sim, mas a justiça que é pela fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '31', 'Mas Israel, que buscava a lei da justiça, não chegou à lei da justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '32', 'Porquê? Porque não foi pela fé, mas como que pelas obras da lei; tropeçaram na pedra de tropeço;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '9', '33', 'Como está escrito: Eis que eu ponho em Sião uma pedra de tropeço, e uma rocha de escândalo; e todo aquele que crer nela não será confundido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '1', 'IRMÃOS, o bom desejo do meu coração e a oração a Deus por Israel é para sua salvação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '2', 'Porque lhes dou testemunho de que têm zelo de Deus, mas não com entendimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '3', 'Porquanto, não conhecendo a justiça de Deus, e procurando estabelecer a sua própria justiça, não se sujeitaram à justiça de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '4', 'Porque o fim da lei é Cristo para justiça de todo aquele que crê.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '5', 'Ora Moisés descreve a justiça que é pela lei, dizendo: O homem que fizer estas coisas viverá por elas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '6', 'Mas a justiça que é pela fé diz assim: Não digas em teu coração: Quem subirá ao céu? (isto é, a trazer do alto a Cristo).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '7', 'Ou: Quem descerá ao abismo? (isto é, a tornar a trazer de entre os mortos a Cristo).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '8', 'Mas que diz? A palavra está junto de ti, na tua boca e no teu coração; esta é a palavra da fé, que pregamos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '9', 'A saber: Se com a tua boca confessares ao SENHOR Jesus, e, em teu coração creres que Deus o ressuscitou dos mortos, serás salvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '10', 'Visto que com o coração se crê para a justiça, e com a boca se faz confissão para a salvação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '11', 'Porque a Escritura diz: Todo aquele que nele crer não será confundido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '12', 'Porquanto não há diferença entre judeu e grego; porque um mesmo é o SENHOR de todos os que o invocam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '13', 'Porque todo aquele que invocar o nome do SENHOR será salvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '14', 'Como pois invocarão aquele em quem não creram? E como crerão naquele de quem não ouviram? E como ouvirão, se não há quem pregue?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '15', 'E como pregarão, se não forem enviados? Como está escrito: Quão formosos os pés dos que anunciam a paz, dos que anunciam coisas boas!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '16', 'Mas nem todos obedecem ao evangelho; pois Isaías diz: Senhor, quem creu na nossa pregação?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '17', 'De sorte que a fé é pelo ouvir, e o ouvir pela palavra de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '18', 'Mas digo: Porventura não ouviram? Sim, por certo, pois por toda a terra saiu a voz deles, e as suas palavras até aos confins do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '19', 'Mas digo: Porventura Israel não o soube? Primeiramente diz Moisés: Eu vos meterei em ciúmes com aqueles que não são povo, com gente insensata vos provocarei à ira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '20', 'E Isaías ousadamente diz: Fui achado pelos que me não buscavam, fui manifestado aos que por mim não perguntavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '10', '21', 'Mas contra Israel diz. Todo o dia estendi as minhas mãos a um povo rebelde e contradizente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '1', 'DIGO pois: porventura rejeitou Deus o seu povo? De modo nenhum, porque também eu sou israelita, da descendência de Abraão, da tribo de Benjamim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '2', 'Deus não rejeitou o seu povo, que antes conheceu. Ou não sabeis o que a Escritura diz de Elias, como fala a Deus contra Israel, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '3', 'Senhor, mataram os teus profetas, e derribaram os teus altares; e só eu fiquei, e buscam a minha alma?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '4', 'Mas que lhe diz a resposta divina? Reservei para mim sete mil varões, que não dobraram os joelhos diante de Baal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '5', 'Assim pois também agora neste tempo ficou um resto, segundo a eleição da graça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '6', 'Mas se é por graça, já não é pelas obras, de outra maneira, a graça já não é graça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '7', 'Pois quê? O que Israel buscava não o alcançou; mas os eleitos o alcançaram, e os outros foram endurecidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '8', 'Como está escrito: Deus lhes deu espirito de profundo sono, olhos para não verem , e ouvidos para não ouvirem, até ao dia de hoje.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '9', 'E David diz: Torne-se-lhes a sua mesa em laço, e em armadilha, e em tropeço, por sua retribuição;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '10', 'Escureçam-se-lhes os olhos para não verem, e encurvem-se-lhes continuamente as costas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '11', 'Digo pois: Porventura tropeçaram para que caíssem? De modo nenhum, mas pela sua queda veio a salvação aos gentios, para os incitar à emulação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '12', 'E se a sua queda é a riqueza do mundo, e a sua diminuição a riqueza dos gentios, quanto mais a sua plenitude!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '13', 'Porque convosco falo, gentios, que, enquanto for apóstolo dos gentios, glorificarei o meu ministério;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '14', 'Para ver se de alguma maneira posso incitar à emulação os da minha carne e salvar alguns deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '15', 'Porque, se a sua rejeição é a reconciliação do mundo, qual será a sua admissão, senão a vida entre os mortos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '16', 'E, se as primícias são santas, também a massa o é; se a raiz é santa, também os ramos o são.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '17', 'E se alguns dos ramos foram quebrados, e tu, sendo zambujeiro, foste enxertado em lugar deles, e feito participante da raiz e da seiva da oliveira,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '18', 'Não te glorieis contra os ramos; e, se contra eles te gloriares, não és tu que sustentas a raiz, mas a raiz a ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '19', 'Dirás pois: Os ramos foram quebrados, para que eu fosse enxertado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '20', 'Está bem: pela sua incredulidade foram quebrados, e tu estás em pé pela fé. Então não te ensoberbeças, mas teme.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '21', 'Porque, se Deus não poupou os ramos naturais, teme que te não poupe a ti também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '22', 'Considera pois a bondade e a severidade de Deus: para com os que caíram, severidade; mas para contigo, a benignidade de Deus, se permaneceres na sua benignidade; de outra maneira também tu serás cortado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '23', 'E também eles, se não permanecerem na incredulidade, serão enxertados; porque poderoso é Deus para os tornar a enxertar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '24', 'Porque, se tu foste cortado do natural zambujeiro, e, contra a natureza enxertado na boa oliveira, quanto mais esses, que são naturais, serão enxertados na sua própria oliveira!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '25', 'Porque não quero, irmãos, que ignoreis este segredo (para que não presumais de vós mesmos): que o endurecimento veio em parte sobre Israel, até que a plenitude dos gentios haja entrado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '26', 'E assim todo o Israel será salvo, como está escrito: de Sião virá o Libertador, e desviará de Jacob as impiedades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '27', 'E este será o meu concerto com eles, quando eu tirar os seus pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '28', 'Assim que, quanto ao evangelho, são inimigos por causa de vós; mas, quanto à eleição, amados por causa dos pais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '29', 'Porque os dons e a vocação de Deus são sem arrependimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '30', 'Porque assim como vós também antigamente fostes desobedientes a Deus, mas agora alcançastes misericórdia pela desobediência deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '31', 'Assim também estes agora foram desobedientes, para também alcançarem misericórdia pela misericórdia a vós demonstrada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '32', 'Porque Deus encerrou a todos debaixo da desobediência, para com todos usar de misericórdia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '33', 'Ó profundidade das riquezas, tanto da sabedoria, como da ciência de Deus! Quão insondáveis são os seus juízos, e quão inescrutáveis os seus caminhos!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '34', 'Porque quem compreendeu o intento do SENHOR? Ou quem foi o seu conselheiro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '35', 'Ou quem lhe deu primeiro a ele, para que lhe seja recompensado?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '11', '36', 'Porque dele e por ele, e para ele, são todas as coisas; glória pois a ele eternamente. Amém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '1', 'ROGO-VOS pois irmãos, pela compaixão de Deus, que apresenteis os vossos corpos em sacrifício vivo, santo e agradável a Deus, que é o vosso culto racional.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '2', 'E não vos conformeis com este mundo, mas transformai-vos pela renovação do vosso entendimento, para que experimenteis qual seja a boa, agradável, e perfeita vontade de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '3', 'Porque pela graça, que me é dada, digo a cada um de entre vós que não saiba mais do que convém saber, mas que saiba com temperança, conforme a medida da fé que Deus repartiu a cada um.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '4', 'Porque assim como em um corpo temos muitos membros, e nem todos os membros têm a mesma operação,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '5', 'Assim nós, que somos muitos, somos um só corpo em Cristo; mas individualmente somos membros uns dos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '6', 'De modo que, tendo diferentes dons, segundo a graça que nos é dada, se é profecia, seja ela segundo a medida da fé;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '7', 'Se é ministério, seja em ministrar; se é ensinar haja dedicação ao ensino,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '8', 'Ou o que exorta, use esse dom em exortar; o que reparte, faça-o com liberalidade; o que preside, com cuidado; o que exercita misericórdia, com alegria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '9', 'O amor seja não fingido. Aborrecei o mal e apegai-vos ao bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '10', 'Amai-vos cordialmente uns aos outros com amor fraternal, preferindo-vos em honra uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '11', 'Não sejais vagarosos no cuidado: sede fervorosos no espírito, servindo ao SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '12', 'Alegrai-vos na esperança, sede pacientes na tribulação, perseverai na oração;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '13', 'Comunicai com os santos nas suas necessidades, segui a hospitalidade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '14', 'Abençoai aos que vos perseguem, abençoai, e não amaldiçoeis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '15', 'Alegrai-vos com os que se alegram; e chorai com os que choram;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '16', 'Sede unânimes entre vós; não ambicioneis coisas altas, mas acomodai-vos às humildes; não sejais sábios em vós mesmos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '17', 'A ninguém torneis mal por mal; procurai as coisas honestas, perante todos os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '18', 'Se for possível, quanto estiver em vós, tende paz com todos os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '19', 'Não vos vingueis a vós mesmos, amados, mas dai lugar à ira, porque está escrito: Minha é a vingança; eu recompensarei, diz o SENHOR,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '20', 'Portanto, se o teu inimigo tiver fome, dá-lhe de comer; se tiver sede, dá-lhe de beber; porque, fazendo isto, amontoarás brasas de fogo sobre a sua cabeça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '12', '21', 'Não te deixes vencer do mal, mas vence o mal com o bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '1', 'TODA a alma esteja sujeita às potestades superiores; porque não há potestade que não venha de Deus; e as potestades que há foram ordenadas por Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '2', 'Por isso quem resiste à potestade resiste à ordenação de Deus; e os que resistem trarão sobre si mesmos a condenação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '3', 'Porque os magistrados não são terror para as boas obras, mas para as más. Queres tu, pois, não temer a potestade? Faz o bem, e terás louvor dela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '4', 'Porque ela é ministro de Deus para teu bem. Mas, se fizeres o mal, teme, pois não traz debalde a espada; porque é ministro de Deus, e vingador para castigar o que faz o mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '5', 'Portanto é necessário que lhe estejais sujeitos, não somente pelo castigo, mas também pela consciência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '6', 'Por esta razão também pagais tributos: porque são ministros de Deus, atendendo sempre a isto mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '7', 'Portanto dai a cada um o que deveis: a quem tributo, tributo; a quem imposto, imposto; a quem temor, temor; a quem honra, honra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '8', 'A ninguém devais coisa alguma, a não ser o amor com que vos ameis uns aos outros: porque quem ama aos outros cumpriu a lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '9', 'Com efeito: Não adulterarás, não matarás, não furtarás, não darás falso testemunho, não cobiçarás; e se há algum outro mandamento, tudo nesta palavra se resume: Amarás ao teu próximo como a ti mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '10', 'O amor não faz mal ao próximo. De sorte que o cumprimento da lei é o amor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '11', 'E isto digo, conhecendo o tempo, que é já hora de despertarmos do sono; porque a nossa salvação está agora mais perto de nós do que quando aceitámos a fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '12', 'A noite é passada, e o dia é chegado. Rejeitemos pois as obras das trevas, e vistamo-nos das armas da luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '13', 'Andemos honestamente, como de dia: não em glutonarias, nem em bebedeiras, nem em desonestidade, nem em dissoluções, nem em contendas e inveja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '13', '14', 'Mas revesti-vos do SENHOR Jesus Cristo, e não tenhais cuidado da carne em suas concupiscências.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '1', 'ORA, quanto ao que está enfermo na fé, recebei-o, não em contendas sobre dúvidas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '2', 'Porque um crê que de tudo se pode comer, e outro, que é fraco, come legumes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '3', 'O que come não despreze o que não come; e o que não come não julgue o que come; porque Deus o recebeu por seu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '4', 'Quem és tu, que julgas o servo alheio? Para seu próprio SENHOR ele está em pé ou cai; mas estará firme; porque poderoso é Deus para o firmar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '5', 'Um faz diferença entre dia e dia, mas outro julga iguais todos os dias. Cada um esteja inteiramente seguro em seu próprio ânimo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '6', 'Aquele que faz caso do dia, para o SENHOR o faz. O que come, para o SENHOR come, porque dá graças a Deus; e o que não come, para o SENHOR não come, e dá graças a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '7', 'Porque nenhum de nós vive para si, e nenhum morre para si.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '8', 'Porque, se vivemos, para o SENHOR vivemos: se morremos, para o SENHOR morremos. De sorte que, ou vivamos ou morramos, somos do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '9', 'Foi para isto que morreu Cristo, e tornou a viver; para ser Senhor, tanto dos mortos, como dos vivos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '10', 'Mas tu, porque julgas teu irmão? Ou tu, também, porque desprezas teu irmão? Pois todos havemos de comparecer ante o tribunal de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '11', 'Porque está escrito: Pela minha vida, diz o SENHOR: que todo o joelho se dobrará diante de mim, e toda a língua confessará a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '12', 'De maneira que cada um de nós dará conta de si mesmo a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '13', 'Assim que não nos julguemos mais uns aos outros; antes seja o vosso propósito não pôr tropeço ou escândalo ao irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '14', 'Eu sei, e estou certo no SENHOR Jesus, que nenhuma coisa é de si mesmo imunda a não ser para aquele que a tem por imunda; para esse é imunda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '15', 'Mas, se por causa da comida se contrista teu irmão, já não andas conforme o amor. Não destruas por causa da tua comida aquele por quem Cristo morreu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '16', 'Não seja pois blasfemado o vosso bem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '17', 'Porque o reino de Deus não é comida nem bebida, mas justiça, e paz, e alegria no Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '18', 'Porque quem nisto serve a Cristo agradável é a Deus e aceito aos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '19', 'Sigamos pois as coisas que servem para a paz e para a edificação de uns para com os outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '20', 'Não destruas por causa da comida a obra de Deus. É verdade que tudo é limpo, mas mal vai para o homem que come com escândalo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '21', 'Bom é não comer carne, nem beber vinho, nem fazer outras coisas em que teu irmão tropece, ou se escandalize, ou se enfraqueça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '22', 'Tens tu fé? Tem-na em ti mesmo diante de Deus. Bem-aventurado aquele que não se condena a si mesmo naquilo que aprova.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '14', '23', 'Mas aquele que tem dúvidas, se come está condenado, porque não come por fé; e tudo o que não é de fé é pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '1', 'MAS nós, que somos fortes, devemos suportar as fraquezas dos fracos, e não agradar a nós mesmos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '2', 'Portanto cada um de nós agrade ao seu próximo no que é bom para edificação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '3', 'Porque também Cristo não agradou a si mesmo, mas, como está escrito: sobre mim caíram as injúrias dos que te injuriavam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '4', 'Porque tudo que dantes foi escrito, para nosso ensino foi escrito, para que pela paciência e consolação das Escrituras tenhamos esperança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '5', 'Ora o Deus de paciência e consolação vos conceda o mesmo sentimento uns para com os outros, segundo Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '6', 'Para que concordes, a uma boca, glorifiqueis ao Deus e Pai de nosso SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '7', 'Portanto recebei-vos uns aos outros, como também Cristo nos recebeu para glória de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '8', 'Digo pois que Jesus Cristo foi ministro da circuncisão, por causa da verdade de Deus, para que confirmasse as promessas feitas aos pais;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '9', 'E para que os gentios glorifiquem a Deus pela sua misericórdia, como está escrito: Portanto eu te louvarei entre os gentios, e cantarei ao teu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '10', 'E outra vez diz: Alegrai-vos, gentios, com o seu povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '11', 'E outra vez: Louvai ao SENHOR, todos os gentios, e celebrai-o, todos os povos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '12', 'E outra vez diz Isaías: Uma raiz em Jessé haverá, e naquele que se levantar para reger os gentios, os gentios esperarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '13', 'Ora o Deus de esperança vos encha de todo o gozo e paz em crença, para que abundeis em esperança pela virtude do Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '14', 'Eu próprio, meus irmãos, certo estou, a respeito de vós, que vós mesmos estais cheios de bondade, cheios de todo o conhecimento, podendo admoestar-vos uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '15', 'Mas, irmãos, em parte vos escrevi mais ousadamente, como para vos trazer outra vez isto à memória, pela graça que por Deus me foi dada;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '16', 'Que seja ministro de Jesus Cristo entre os gentios, ministrando o evangelho de Deus, para que seja agradável a oferta dos gentios, santificada pelo Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '17', 'De sorte que tenho glória em Jesus Cristo nas coisas que pertencem a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '18', 'Porque não ousaria dizer coisa alguma, que Cristo por mim não tenha feito, a obediência dos gentios, por palavra e por obras;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '19', 'Pelo poder dos sinais e prodígios, na virtude do Espírito de Deus: de maneira que desde Jerusalém, e arredores, até ao Ilírico, tenho pregado o evangelho de Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '20', 'E desta maneira me esforcei por anunciar o evangelho, não onde Cristo houvera sido nomeado, para não edificar sobre fundamento alheio;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '21', 'Antes, como está escrito: Aqueles a quem não foi anunciado, o verão, e os que não ouviram o entenderão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '22', 'Pelo que também muitas vezes tenho sido impedido de ir ter convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '23', 'Mas agora, que não tenho mais demora nestes sítios, e tendo já há muitos anos grande desejo de ir ter convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '24', 'Quando partir para Espanha irei ter convosco; pois espero que de passagem vos verei, e que para lá seja encaminhado por vós, depois de ter gozado um pouco da vossa companhia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '25', 'Mas agora vou a Jerusalém para ministrar aos santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '26', 'Porque pareceu bem à Macedónia e à Acaia fazerem uma colecta para os pobres de entre os santos que estão em Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '27', 'Isto lhes pareceu bem, como devedores que são para com eles. Porque, se os gentios foram participantes dos seus bens espirituais, devem também ministrar-lhes os temporais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '28', 'Assim que, concluído isto, e havendo-lhes consignado este fruto, de lá, passando por vós, irei à Espanha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '29', 'E bem sei que, indo ter convosco, chegarei com a plenitude da bênção do evangelho de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '30', 'E rogo-vos irmãos, por nosso SENHOR Jesus Cristo e, pelo amor do Espírito, que combatais comigo nas vossas orações por mim a Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '31', 'Para que seja livre dos rebeldes que estão na Judeia, e que esta minha administração, que em Jerusalém faço, seja bem aceita pelos santos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '32', 'A fim de que, pela vontade de Deus, chegue a vós com alegria, e possa recrear-me convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '15', '33', 'E o Deus de paz seja com todos vós. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '1', 'RECOMENDO-VOS pois Febe, nossa irmã, a qual serve na igreja que está em Cencreia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '2', 'Para que a recebais no SENHOR, como convém aos santos, e a ajudeis em qualquer coisa que de vós necessitar; porque tem hospedado a muitos, como também a mim mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '3', 'Saudai a Priscila e a Áquila, meus cooperadores em Cristo Jesus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '4', 'Os quais pela minha vida expuseram as suas cabeças; o que não só eu lhes agradeço, mas também todas as igrejas dos gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '5', 'Saudai também a igreja que está em sua casa. Saudai a Epéneto, meu amado, que é as primícias da Ásia em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '6', 'Saudai a Maria que trabalhou muito por nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '7', 'Saudai a Andrónico e a Júnia, meus parentes e meus companheiros na prisão, os quais se distinguiram entre os apóstolos e que foram antes de mim em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '8', 'Saudai a Amplias, meu amado no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '9', 'Saudai a Urbano, nosso cooperador em Cristo, e a Estáquis, meu amado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '10', 'Saudai a Apeles aprovado em Cristo. Saudai aos da família de Aristóbulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '11', 'Saudai a Herodião, meu parente. Saudai aos da família de Narciso, os que estão no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '12', 'Saudai a Trifena e a Trifosa, as quais trabalham no SENHOR. Saudai a amada Pérside, a qual muito trabalhou no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '13', 'Saudai a Rufo, eleito no SENHOR, e a sua mãe e minha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '14', 'Saudai a Asíncrito, a Flegonte, a Hermas, a Pátrobas, a Hermes, e aos irmãos que estão com eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '15', 'Saudai a Filólogo e a Júlia, a Nereu e a sua irmã, e a Olímpia; e a todos os santos que com eles estão. l');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '16', 'Saudai-vos uns aos outros com santo ósculo. As igrejas de Cristo vos saúdam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '17', 'E rogo-vos, irmãos, que noteis os que promovem dissensões e escândalos contra a doutrina que aprendestes; desviai-vos deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '18', 'Porque os tais não servem a nosso SENHOR Jesus Cristo, mas ao seu ventre: e com suaves palavras e lisonjas enganam os corações dos símplices.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '19', 'Quanto à vossa obediência é ela conhecida de todos. Comprazo-me pois em vós; e quero que sejais sábios no bem, mas símplices no mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '20', 'E o Deus de paz esmagará em breve Satanás debaixo dos vossos pés. A graça de Nosso SENHOR Jesus Cristo seja convosco. Amém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '21', 'Saúdam-vos Timóteo, meu cooperador, e Lúcio, e Jason, e Sosípatro, meus parentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '22', 'Eu, Tércio, que esta carta escrevi, vos saúdo no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '23', 'Saúda-vos Gaio, meu hospedeiro, e de toda a igreja. Saúda-vos Erasto, procurador da cidade, e também o irmão Quarto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '24', 'A graça de nosso SENHOR Jesus Cristo seja com todos vós. Amém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '25', 'Ora, aquele que é poderoso para vos confirmar segundo o meu evangelho e a pregação de Jesus Cristo, conforme a revelação do mistério que desde tempos eternos esteve oculto,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '26', 'Mas que se manifestou agora, e se notificou pelas Escrituras dos profetas, segundo o mandamento do Deus eterno, a todas as nações para obediência da fé;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('6', '16', '27', 'Ao único Deus, sábio, seja dada glória por Jesus Cristo para todo o sempre. Amém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '1', '¶ PAULO (chamado apóstolo de Jesus Cristo, pela vontade de Deus), e o irmão Sóstenes,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '2', 'À igreja de Deus que está em Corinto, aos santificados em Cristo Jesus, chamados santos, com todos os que em todo o lugar invocam o nome de nosso SENHOR Jesus Cristo, Senhor deles e nosso:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '3', 'Graça e paz da parte de Deus nosso Pai, e do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '4', 'Sempre dou graças ao meu Deus por vós pela graça de Deus que vos foi dada em Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '5', 'Porque em tudo fostes enriquecido n\'Ele, em toda a palavra e em todo o conhecimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '6', '(Como foi mesmo o testemunho de Cristo confirmado entre vós).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '7', 'De maneira que nenhum dom vos falta, esperando a manifestação de nosso SENHOR Jesus Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '8', 'O qual vos confirmará também até ao fim, para serdes irrepreensíveis no dia de nosso SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '9', 'Fiel é Deus, pelo qual fostes chamados para a comunhão de seu Filho Jesus Cristo nosso SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '10', 'Rogo-vos, porém, irmãos, pelo nome de nosso SENHOR Jesus Cristo, que digais todos uma mesma coisa, e que não haja entre vós dissensões; antes sejais unidos em um mesmo sentido e em um mesmo parecer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '11', 'Porque a respeito de vós, irmãos meus, me foi comunicado pelos da família de Cloé que há contendas entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '12', 'Quero dizer com isto, que cada um de vós diz: Eu sou de Paulo, e eu de Apolos, e eu de Cefas, e eu de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '13', 'Está Cristo dividido? Foi Paulo crucificado por vós? Ou fostes vós baptizados em nome de Paulo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '14', 'Dou graças a Deus, porque a nenhum de vós baptizei, senão a Crispo e a Gaio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '15', 'Para que ninguém diga que fostes baptizados em meu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '16', 'E baptizei também a família de Estéfanas; além destes, não sei se baptizei algum outro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '17', 'Porque Cristo enviou-me, não para baptizar, mas para evangelizar; não em sabedoria de palavras, para que a cruz de Cristo se não faça vã.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '18', 'Porque a palavra da cruz é loucura para os que perecem; mas para nós, que somos salvos, é o poder de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '19', 'Porque está escrito: Destruirei a sabedoria dos sábios, e aniquilarei a inteligência dos inteligentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '20', 'Onde está o sábio? Onde está o escriba? Onde está o inquiridor deste século? Porventura não tornou Deus louca a sabedoria deste mundo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '21', 'Visto como na sabedoria de Deus o mundo não conheceu a Deus pela sua sabedoria, aprouve a Deus salvar os crentes pela loucura da pregação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '22', 'Porque os judeus pedem sinal, e os gregos buscam sabedoria;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '23', 'Mas nós pregamos a Cristo crucificado, que é escândalo para os judeus, e loucura para os gregos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '24', 'Mas para os que são chamados, tanto judeus como gregos, lhes pregamos a Cristo, poder de Deus, e sabedoria de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '25', 'Porque a loucura de Deus é mais sábia do que os homens; e a fraqueza de Deus é mais forte do que os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '26', 'Porque, vede, irmãos, a vossa vocação, que não são muitos os sábios segundo a carne, nem muitos os poderosos, nem muitos os nobres que são chamados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '27', 'Mas Deus escolheu as coisas loucas deste mundo para confundir as sábias; e Deus escolheu as coisas fracas deste mundo para confundir as fortes;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '28', 'E Deus escolheu as coisas vis deste mundo, e as desprezíveis, e as que não são, para aniquilar as que são;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '29', 'Para que nenhuma carne se glorie perante Ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '30', 'Mas vós sois de Ele, em Jesus Cristo, o qual para nós foi feito por Deus sabedoria, e justiça, e santificação, e redenção;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '1', '31', 'Para que, como está escrito: Aquele que se gloria, glorie-se no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '1', 'E EU, irmãos, quando fui ter convosco, anunciando-vos o testemunho de Deus, não fui com sublimidade de palavras ou de sabedoria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '2', 'Porque nada me propus saber entre vós, senão a Jesus Cristo, e este crucificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '3', 'E eu estive convosco em fraqueza, e em temor, e em grande tremor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '4', 'A minha palavra, e a minha pregação, não consistiu em palavras persuasivas de sabedoria humana, mas em demonstração de Espírito e de poder;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '5', 'Para que a vossa fé não se apoiasse em sabedoria dos homens, mas no poder de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '6', 'Todavia falamos sabedoria entre os perfeitos; não porém a sabedoria deste mundo, nem dos príncipes deste mundo, que se aniquilam;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '7', 'Mas falamos a sabedoria de Deus, oculta em mistério, a qual Deus ordenou antes dos séculos para nossa glória;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '8', 'A qual nenhum dos príncipes deste mundo conheceu; porque, se a conhecessem, nunca crucificariam ao SENHOR da glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '9', 'Mas, como está escrito: As coisas que o olho não viu, e o ouvido não ouviu, e não subiram ao coração do homem, são as que Deus preparou para os que o amam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '10', 'Mas Deus no-las revelou pelo seu Espírito; porque o Espírito penetra todas as coisas, ainda as profundezas de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '11', 'Porque, qual dos homens sabe as coisas do homem, senão o espírito do homem, que nele está? Assim também ninguém sabe as coisas de Deus, senão o Espírito de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '12', 'Mas nós não recebemos o espírito do mundo, mas o Espírito que provém de Deus, para que pudéssemos conhecer o que nos é dado gratuitamente por Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '13', 'As quais também falamos, não com palavras de sabedoria humana, mas com as que do Espírito Santo ensina, comparando as coisas espirituais com as espirituais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '14', 'Ora o homem natural não compreende as coisas do Espírito de Deus, porque lhe parecem loucura; e não pode entendê-las, porque elas se discernem espiritualmente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '15', 'Mas o que é espiritual discerne bem tudo, e ele de ninguém é discernido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '2', '16', 'Porque, quem conheceu a mente do SENHOR, para que possa instruí-lo? Mas nós temos a mente de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '1', 'E EU, irmãos, não vos pude falar como a espirituais, mas como a carnais, como a meninos em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '2', 'Com leite vos criei, e não com manjar, porque ainda não podíeis, nem tampouco ainda agora podeis;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '3', 'Porque ainda sois carnais. Pois, havendo entre vós inveja, contendas e dissensões, não sois porventura carnais, e não andais segundo os homens?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '4', 'Porque, dizendo um: Eu sou de Paulo; e outro: Eu de Apolos; porventura não sois carnais?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '5', 'Pois quem é Paulo, e quem é Apolos, senão ministros pelos quais crestes, e conforme o que o SENHOR deu a cada um?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '6', 'Eu plantei; Apolos regou; mas Deus deu o crescimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '7', 'Pelo que, nem o que planta é alguma coisa, nem o que rega, mas Deus, que dá o crescimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '8', 'Ora o que planta e o que rega são um; mas cada um receberá o seu galardão segundo o seu trabalho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '9', 'Porque nós somos cooperadores de Deus; vós sois lavoura de Deus e edifício de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '10', 'Segundo a graça de Deus que me foi dada, pus eu, como sábio arquitecto, o fundamento, e outro edifica sobre ele; mas veja cada um como edifica sobre ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '11', 'Porque ninguém pode pôr outro fundamento, além do que já está posto, o qual é Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '12', 'E, se alguém sobre este fundamento formar um edifício de ouro, prata, pedras preciosas, madeira, feno, palha,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '13', 'A obra de cada um se manifestará; na verdade o dia a declarará, porque pelo fogo será descoberta; e o fogo provará qual seja a obra de cada um.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '14', 'Se a obra que alguém edificou nessa parte permanecer, esse receberá galardão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '15', 'Se a obra de alguém se queimar, sofrerá detrimento; mas o tal será salvo, todavia como pelo fogo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '16', 'Não sabeis vós que sois o templo de Deus, e que o Espírito de Deus habita em vós?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '17', 'Se alguém destruir o templo de Deus, Deus o destruirá; porque o templo de Deus, que sois vós, é santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '18', 'Ninguém se engane a si mesmo; se alguém de entre vós se tem por sábio neste mundo, faça-se louco para ser sábio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '19', 'Parque a sabedoria deste mundo é loucura diante de Deus; pois está escrito: Ele apanha os sábios na sua própria astúcia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '20', 'E outra vez: O SENHOR conhece os pensamentos dos sábios, que são vãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '21', 'Portanto ninguém se glorie nos homens; porque tudo é vosso;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '22', 'Seja Paulo, seja Apolos, seja Cefas, seja o mundo, seja a vida, seja a morte, seja o presente, seja o futuro, tudo é vosso,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '3', '23', 'E vós de Cristo, e Cristo de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '1', 'QUE os homens nos considerem como ministros de Cristo, e despenseiros dos mistérios de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '2', 'Além disso requer-se nos despenseiros que cada um se ache fiel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '3', 'Todavia, a mim mui pouco se me dá de ser julgado por vós, ou por algum juízo humano; nem eu tampouco a mim mesmo me julgo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '4', 'Porque em nada me sinto culpado; mas nem por isso me considero justificado; pois quem me julga é o SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '5', 'Portanto nada julgueis antes de tempo, até que o SENHOR venha, o qual também trará à luz as coisas ocultas das trevas, e manifestará os desígnios dos corações; e então cada um receberá de Deus o louvor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '6', 'E eu, irmãos, apliquei estas coisas, por semelhança, a mim e a Apolos, por amor de vós; para que em nós aprendais a não ir além do que está escrito, não vos ensoberbecendo a favor de um contra outro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '7', 'Porque, quem te diferença? E que tens tu que não tenhas recebido? E, se o recebeste, porque te glorias como se não o houveras recebido?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '8', 'Já estais fartos! Já estais ricos! Sem nós reinais! E oxalá reinásseis para que também nós viéssemos a reinar convosco!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '9', 'Porque tenho para mim, que Deus a nós, apóstolos, nos pôs por últimos, como condenados à morte; pois somos feitos espectáculo ao mundo, aos anjos, e aos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '10', 'Nós somos loucos por amor de Cristo, e vós sábios em Cristo; nós fracos; e vós fortes; vós ilustres, e nós vis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '11', 'Até esta presente hora sofremos fome, e sede, e estamos nus, e recebemos bofetadas, e não temos pousada certa,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '12', 'E nos afadigamos, trabalhando com nossas próprias mãos; somos injuriados e bendizemos: somos perseguidos, e sofremos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '13', 'Somos blasfemados, e rogamos; até ao presente temos chegado a ser como o lixo deste mundo, e como a escória de todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '14', 'Não escrevo estas coisas para vos envergonhar; mas admoesto-vos como meus filhos amados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '15', 'Porque ainda que tivésseis dez mil aios em Cristo não teríeis contudo muitos pais; porque eu pelo evangelho vos gerei em Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '16', 'Admoesto-vos portanto a que sejais meus imitadores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '17', 'Por esta causa vos mandei Timóteo, que é filho amado e fiel no SENHOR: o qual vos lembrará os meus caminhos em Cristo, como por toda a parte ensino em cada igreja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '18', 'Mas alguns andam inchados, como se eu não houvesse de ir ter convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '19', 'Mas em breve irei ter convosco, se o SENHOR quiser, e então conhecerei, não as palavras dos que andam inchados, mas a virtude.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '20', 'Porque o reino de Deus não consiste em palavras, mas em virtude.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '4', '21', 'Que quereis? Irei ter convosco com vara ou com amor e espírito de mansidão?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '1', 'GERALMENTE se ouve que há entre vós prostituição tal, qual nem ainda entre os gentios, como é haver quem abuse da mulher de seu pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '2', 'Estais inchados, e nem ao menos vos entristecestes por não ter sido de entre vós tirado quem cometeu tal acção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '3', 'Eu na verdade, ainda que ausente no corpo, mas presente no espírito, já determinei, como se estivesse presente que o que tal acto praticou,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '4', 'Em nome de nosso SENHOR Jesus Cristo, juntos vós e o meu espirito pelo poder de nosso SENHOR Jesus Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '5', 'Seja entregue a Satanás para destruição da carne, para que o espírito seja salvo no dia do SENHOR Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '6', 'Não é boa a vossa jactância. Não sabeis que um pouco de fermento faz levedar toda a massa?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '7', 'Alimpai-vos pois do fermento velho, para que sejais uma nova massa, assim como estais sem fermento. Porque Cristo, nossa páscoa, foi sacrificado por nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '8', 'Pelo que façamos festa, não com o fermento da maldade e da malícia, mas com os asmos da sinceridade e da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '9', 'Já por carta vos tenho escrito, que não vos associeis com os que se prostituem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '10', 'Isto não quer dizer absolutamente com os devassos deste mundo, ou com os avarentos, ou com os roubadores, ou com os idólatras; porque então vos seria necessário sair do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '11', 'Mas agora vos escrevi que não vos associeis com aquele que, dizendo-se irmão, for devasso, ou avarento, ou idólatra, ou maldizente, ou beberrão, ou roubador; com o tal nem ainda comais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '12', 'Porque, que tenho eu em julgar também os que estão de fora? Não julgais vós os que estão dentro?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '5', '13', 'Mas Deus julga os que estão de fora. Tirai pois de entre vós a esse iníquo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '1', 'OUSA algum de vós, tendo algum negócio contra outro, ir a juízo perante os injustos, e não perante os santos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '2', 'Não sabeis vós que os santos hão-de julgar o mundo? Ora, se o mundo deve ser julgado por vós, sois porventura indignos de julgar as coisas mínimas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '3', 'Não sabeis vós que havemos de julgar os anjos? Quanto mais as coisas pertencentes a esta vida?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '4', 'Então, se tiverdes negócios em juízo, pertencentes a esta vida, pondes na cadeira aos que são de menos estima na igreja?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '5', 'Para vos envergonhar o digo: Não há pois entre vós sábios, nem mesmo um, que possa julgar entre seus irmãos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '6', 'Mas o irmão vai a juízo com o irmão, e isto perante infiéis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '7', 'Na verdade é já realmente uma falta entre vós; terdes demandas uns contra os outros. Porque não sofreis antes a injustiça? Porque não sofreis antes o dano?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '8', 'Mas vós mesmos fazeis a injustiça e fazeis o dano: e isto aos irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '9', 'Não sabeis que os injustos não hão-de herdar o reino de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '10', 'Não erreis: nem os devassos, nem os idólatras, nem os adúlteros, nem os efeminados, nem os sodomitas, nem os ladrões; nem os avarentos, nem os bêbedos, nem os maldizentes, nem os roubadores herdarão o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '11', 'E é o que alguns têm sido, mas haveis sido lavados; mas haveis sido santificados, mas haveis sido justificados em nome do SENHOR Jesus, e pelo Espírito do nosso Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '12', 'Todas as coisas me são lícitas; mas nem todas as coisas convêm; todas as coisas me são lícitas; mas eu não me deixarei dominar por nenhuma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '13', 'Os manjares são para o ventre e o ventre para os manjares; Deus, porém, aniquilará, tanto um como os outros. Mas o corpo não é para a prostituição; senão para o SENHOR, e o SENHOR para o corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '14', 'Ora Deus, que também ressuscitou o SENHOR, nos ressuscitará a nós pelo seu poder.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '15', 'Não sabeis vós que os vossos corpos são membros de Cristo? Tomarei pois os membros de Cristo, e fá-los-ei membros de uma meretriz? Não, por certo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '16', 'Ou não sabeis que o que se ajunta com a meretriz, faz-se um corpo com ela? Porque serão, disse, dois numa só carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '17', 'Mas o que se ajunta com o SENHOR é um mesmo espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '18', 'Fugi da prostituição. Todo o pecado que o homem comete é fora do corpo; mas o que se prostitui peca contra o seu próprio corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '19', 'Ou não sabeis que o nosso corpo é o templo do Espírito Santo, que habita em vós, proveniente de Deus, e que não sois de vós mesmos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '6', '20', 'Porque fostes comprados por bom preço; glorificai pois a Deus no vosso corpo, e no vosso espírito, os quais pertencem a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '1', 'ORA, quanto às coisas, que me escrevestes, bom seria que o homem não tocasse em mulher;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '2', 'Mas, por causa da prostituição, cada um tenha a sua própria mulher, e cada uma tenha o seu próprio marido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '3', 'O marido pague à mulher a devida benevolência, e da mesma sorte a mulher ao marido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '4', 'A mulher não tem poder sobre o seu próprio corpo, mas tem-no o marido; e também da mesma maneira o marido não tem poder sobre o seu próprio corpo, mas tem-no a mulher.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '5', 'Não vos defraudeis um ao outro, senão por consentimento mútuo por algum tempo, para vos aplicardes à oração; e depois ajuntai-vos outra vez, para que Satanás vos não tente pela vossa incontinência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '6', 'Digo, porém, isto como que por permissão e não por mandamento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '7', 'Porque quereria que todos os homens fossem como eu mesmo; mas cada um tem de Deus o seu próprio dom, um duma maneira e outro doutra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '8', 'Digo, porém, aos solteiros e às viúvas, que lhes é bom se ficarem como eu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '9', 'Mas, se não podem conter-se, casem-se. Porque é melhor casar do que abrasar-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '10', 'Todavia, aos casados, mando, não eu mas o SENHOR, que a mulher se não aparte do marido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '11', 'Se, porém, se apartar, que fique sem casar, ou que se reconcilie com o marido; e que o marido não deixe a mulher.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '12', 'Mas aos outros digo eu, não o SENHOR: Se algum irmão tem mulher descrente, e ela consente em habitar com ele, não a deixe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '13', 'E se alguma mulher tem marido descrente, e ele consente em habitar com ela, não o deixe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '14', 'Porque o marido descrente é santificado pela mulher; e a mulher descrente é santificada pelo marido; doutra sorte os vossos filhos seriam imundos; mas agora são santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '15', 'Mas, se o descrente se apartar aparte-se; porque neste caso o irmão, ou irmã, não está sujeito à servidão mas Deus chamou-nos para a paz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '16', 'Porque, donde sabes, ó mulher, se salvarás teu marido? ou, donde sabes, ó marido, se salvarás tua mulher?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '17', 'E assim cada um ande como Deus lhe repartiu, cada um como o SENHOR o chamou. É o que ordeno em todas as igrejas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '18', 'É alguém chamado, estando circuncidado? Fique circuncidado. É alguém chamado estando incircuncidado? Não se circuncide.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '19', 'A circuncisão é nada e a incircuncisão nada é, mas sim a observância dos mandamentos de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '20', 'Cada um fique na vocação em que foi chamado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '21', 'Foste chamado sendo servo? Não te dê cuidado; e, se ainda podes ser livre, aproveita a ocasião.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '22', 'Porque o que é chamado pelo SENHOR, sendo servo, é liberto do SENHOR; e da mesma maneira também o que é chamado sendo livre, servo é de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '23', 'Fostes comprados por bom preço; não vos façais servos dos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '24', 'Irmãos, cada um fique diante de Deus no estado em que foi chamado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '25', 'Ora, quanto às virgens, não tenho mandamento do SENHOR; dou, porém, o meu parecer, como quem tem alcançado misericórdia do SENHOR para ser fiel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '26', 'Tenho pois por bom, por causa da instante necessidade, que é bom para o homem o estar assim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '27', 'Estás ligado à mulher? Não busques separar-te. Estás livre de mulher? Não busques mulher.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '28', 'Mas, se te casares, não pecas; e, se a virgem se casar, não peca. Todavia os tais terão tribulações na carne, e eu quereria poupar-vos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '29', 'Isto, porém, vos digo, irmãos, que o tempo se abrevia; o que resta é que também os que têm mulheres sejam como se as não tivessem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '30', 'E os que choram, como se não chorassem; e os que folgam, como se não folgassem; e os que compram, como se não possuíssem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '31', 'E os que usam deste mundo, como se dele não abusassem, porque a aparência deste mundo passa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '32', 'E bem quisera eu que estivésseis sem cuidado. O solteiro cuida nas coisas do SENHOR, em como há-de agradar ao SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '33', 'Mas o que é casado cuida nas coisas do mundo, em como há-de agradar à mulher.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '34', 'Há diferença entre a mulher casada e a virgem; a solteira cuida nas coisas do SENHOR para ser santa, tanto no corpo como no espírito; porém a casada cuida nas coisas do mundo, em como há-de agradar ao marido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '35', 'E digo isto para proveito vosso; não para vos enlaçar, mas para o que é decente e conveniente, para vos unirdes ao SENHOR sem distracção alguma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '36', 'Mas, se alguém julga que trata dignamente a sua virgem, se tiver passado a flor da idade, e se for necessário, que faça o tal o que quiser; não peca; casem-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '37', 'Todavia o que está firme em seu coração, não tendo necessidade, mas com poder sobre a sua própria vontade, se resolveu no seu coração guardar a sua virgem, faz bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '38', 'De modo que, o que a dá em casamento, faz bem; mas o que a não dá em casamento faz melhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '39', 'A mulher casada está ligada pela lei todo o tempo que o seu marido vive; mas se falecer o seu marido fica livre para casar com quem quiser, contanto que seja no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '7', '40', 'Será, porém, mais bem-aventurada se ficar assim, segundo o meu parecer, e também eu cuido que tenho o Espírito de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '1', 'ORA, no tocante às coisas sacrificadas aos ídolos, sabemos que todos temos ciência. A ciência incha, mas o amor edifica.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '2', 'E, se alguém cuida saber alguma coisa, ainda não sabe como convém saber.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '3', 'Mas, se alguém ama a Deus, esse é conhecido dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '4', 'Assim que, quanto ao comer das coisas sacrificadas aos ídolos nada é no mundo, e que não há outro Deus, senão um só.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '5', 'Porque, ainda que haja também alguns que se chamem deuses, quer no céu quer na terra (como há muitos deuses e muitos senhores);');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '6', 'Todavia para nós há um só Deus, o Pai, de quem é tudo e para quem nós vivemos; e um só Senhor, Jesus Cristo, pelo qual são todas as coisas, e nós por Ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '7', 'Mas nem em todos há conhecimento; porque alguns até agora comem, no seu costume para com o ídolo, coisas sacrificadas ao ídolo; e a sua consciência, sendo fraca, fica contaminada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '8', 'Ora o manjar não nos faz agradáveis a Deus, porque, se comemos, nada temos de mais, e, se não comemos, nada nos falta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '9', 'Mas vede que essa liberdade não seja dalguma maneira escândalo para os fracos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '10', 'Porque, se alguém te vir a ti, que tens ciência, sentado à mesa no templo dos ídolos, não será a consciência do que é fraco induzida a comer das coisas sacrificadas aos ídolos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '11', 'E pela tua ciência perecerá o irmão fraco, pelo qual Cristo morreu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '12', 'Ora, pecando assim contra os irmãos, e ferindo a sua fraca consciência, pecais contra Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '8', '13', 'Pelo que, se o manjar escandalizar a meu irmão, nunca mais comerei carne, para que meu irmão se não escandalize');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '1', 'NÃO sou eu apóstolo? Não sou livre? Não vi eu a Jesus Cristo SENHOR nosso? Não sois vós a minha obra no SENHOR?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '2', 'Se eu não sou apóstolo para os outros, ao menos o sou para vós; porque vós sois o selo do meu apostolado no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '3', 'Esta é a minha defesa para com os que me condenam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '4', 'Não temos nós direito de comer e beber?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '5', 'Não temos nós direito de levar connosco uma mulher irmã, como também os demais apóstolos, e os irmãos do SENHOR, e Cefas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '6', 'Ou só eu e Barnabé não temos direito de deixar de trabalhar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '7', 'Quem jamais milita à sua própria custa? Quem planta a vinha e não come do seu fruto? Ou quem apascento o gado e não come do leite do gado?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '8', 'Digo eu isto segundo os homens? Ou não diz a lei também o mesmo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '9', 'Porque na lei de Moisés está escrito: Não atarás a boca ao boi que trilha o grão. Porventura tem Deus cuidado dos bois?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '10', 'Ou não o diz certamente por nós? Certamente que por nós está escrito porque o que lavra deve lavrar com esperança, e o que debulha deve debulhar com esperança de ser participante.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '11', 'Se nós vos semeamos as coisas espirituais, será muito que de vós recolhamos as carnais?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '12', 'Se outros participam deste poder sobre vós, porque não, mais justamente, nós? Mas nós não usamos deste direito; antes suportamos tudo, para não pormos impedimento algum ao evangelho de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '13', 'Não sabeis vós que os que administram o que é sagrado comem do que é do templo? E que os que de contínuo estão junto ao altar; participam do altar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '14', 'Assim ordenou também o SENHOR aos que anunciam o evangelho, que vivam do evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '15', 'Mas eu de nenhuma destas coisas usei, e não escrevi isto para que assim se faça comigo; porque melhor me fora morrer, do que alguém fazer vã esta minha glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '16', 'Porque, se anuncio o evangelho, não tenho de que me gloriar, pois me é imposta essa obrigação; e ai de mim se não anunciar o evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '17', 'E por isso, se o faço de boamente, terei prémio; mas, se de má vontade, apenas uma dispensação me é confiada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '18', 'Logo, que prémio tenho? Que, evangelizando, proponha de graça o evangelho de Cristo para não abusar do meu poder no evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '19', 'Porque, sendo livre para com todos, fiz-me servo de todos para ganhar ainda mais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '20', 'E fiz-me como judeu para os judeus, para ganhar os judeus; para os que estão debaixo da lei, como se estivera debaixo da lei, para ganhar os que estão debaixo da lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '21', 'Para os que estão sem lei, como se estivera sem lei (não estando sem lei para com Deus, mas debaixo da lei de Cristo), para ganhar os que estão sem lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '22', 'Fiz-me como fraco para os fracos, para ganhar os fracos. Fiz-me tudo para todos, para por todos os meios chegar a salvar alguns.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '23', 'E eu faço isto por causa do evangelho, para ser também participante dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '24', 'Não sabeis vós que os que correm no estádio, todos, na verdade, correm, mas um só leva o prémio? Correi de tal maneira que o alcanceis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '25', 'E todo aquele que luta de tudo se abstém; eles o fazem para alcançar uma coroa corruptível, nós, porém, uma incorruptível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '26', 'Pois eu assim corro, não como a coisa incerta: assim combato, não como batendo no ar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '9', '27', 'Antes subjugo o meu corpo, e o reduzo à servidão, para que, pregando aos outros, eu mesmo não venha dalguma maneira a ficar reprovado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '1', 'ORA, irmãos não quero que ignoreis que nossos pais estiveram todos debaixo da nuvem, e todos passaram pelo mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '2', 'E todos foram baptizados em Moisés, na nuvem e no mar,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '3', 'E todos comeram dum mesmo manjar espiritual,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '4', 'E beberam todos duma mesma bebida espiritual, porque bebiam da pedra espiritual que os seguia; e a pedra era Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '5', 'Mas Deus não se agradou da maior parte deles, pelo que foram prostrados no deserto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '6', 'E estas coisas foram-nos feitas em figura, para que não cobicemos as coisas más, como eles cobiçaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '7', 'Não vos façais pois idólatras, como alguns deles, conforme está escrito: O povo assentou-se a comer e a beber, e levantou-se para folgar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '8', 'E não nos prostituamos, como alguns deles fizeram; e caíram num dia vinte e três mil.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '9', 'E não tentemos a Cristo, como alguns deles também tentaram, e pereceram pelas serpentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '10', 'E não murmureis, como também alguns deles murmuraram, e pereceram pelo destruidor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '11', 'Ora tudo isto lhes sobreveio como figuras, e estão escritas para aviso nosso para quem já são chegados os fins dos séculos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '12', 'Aquele pois que cuida estar em pé, olhe não caia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '13', 'Não veio sobre vós tentação, senão humana; mas fiel é Deus, que vos não deixará tentar acima do que podeis, antes com a tentação dará também o escape, para que a possais suportar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '14', 'Portanto, meus amados, fugi da idolatria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '15', 'Falo como a entendidos, julgai vós mesmos o que digo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '16', 'Porventura o cálix de bênção, que abençoamos, não é a comunhão do sangue de Cristo? O pão que partimos não é porventura a comunhão do corpo de Cristo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '17', 'Porque nós, sendo muitos, somos um só pão e um só corpo por que todos participamos do mesmo pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '18', 'Vede a Israel segundo a carne os que comem os sacrifícios não são porventura participantes do altar?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '19', 'Mas que digo? Que o ídolo é alguma coisa? Ou que o sacrificado ao ídolo é alguma coisa?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '20', 'Antes digo que as coisas que os gentios sacrificam, as sacrificam aos demónios, e não a Deus. E não quero que sejais participantes com os demónios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '21', 'Não podeis beber o cálix do SENHOR e o cálix dos demónios; não podeis ser participantes da mesa do SENHOR e da mesa dos demónios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '22', 'Ou irritaremos o SENHOR? Somos nós mais fortes do que ele?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '23', 'Todas as coisas me são lícitas, mas nem todas as coisas convêm; todas as coisas me são lícitas, mas nem todas as coisas edificam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '24', 'Ninguém busque o proveito próprio, antes cada um o que é de outrem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '25', 'Comei de tudo quanto se vende no açougue, sem perguntar nada, por causa da consciência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '26', 'Porque a terra é do SENHOR, e toda a sua plenitude.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '27', 'E, se algum dos infiéis vos convidar, e quiserdes ir, comei de tudo o que se puser diante de vós, sem nada perguntar, por causa da consciência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '28', 'Mas, se alguém vos disser: Isto foi sacrificado aos ídolos, não comais, por causa daquele que vos advertiu e por causa da consciência; porque a terra é do SENHOR, e toda a sua plenitude.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '29', 'Digo, porém, a consciência, não a tua, mas a do outro. Pois porque há-de a minha liberdade ser julgada pela consciência de outrem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '30', 'E, se eu com graça participo, porque sou blasfemado naquilo por que dou graças?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '31', 'Portanto, quer comais quer bebais, ou façais outra qualquer coisa, fazei tudo para glória de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '32', 'Portai-vos de modo que não deis escândalo nem aos judeus, nem aos gregos, nem à igreja de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '10', '33', 'Como também eu em tudo agrado a todos, não buscando o meu próprio proveito, mas o de muitos, para que assim se possam salvar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '1', 'SEDE meus imitadores, como também eu de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '2', 'E louvo-vos irmãos, porque em tudo vos lembrais de mim, e retendes os preceitos como vo-los entreguei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '3', 'Mas quero que saibais que Cristo é a cabeça de todo o varão, e o varão a cabeça da mulher; e Deus a cabeça de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '4', 'Todo o homem que ora ou profetiza, tendo a cabeça coberta, desonra a sua própria cabeça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '5', 'Mas toda a mulher que ora ou profetiza com a cabeça descoberta, desonra a sua própria cabeça, porque é como se estivesse rapada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '6', 'Portanto, se a mulher não se cobre com véu, tosquie-se também. Mas, se para a mulher é coisa indecente tosquiar-se ou rapar-se, que ponha o véu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '7', 'O varão pois não deve cobrir a cabeça, porque é a imagem e glória de Deus, mas a mulher é a glória do varão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '8', 'Porque o varão não provém da mulher, mas a mulher do varão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '9', 'Porque também o varão não foi criado por causa da mulher, mas a mulher por causa do varão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '10', 'Portanto, a mulher deve ter sobre a cabeça sinal de poderio, por causa dos anjos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '11', 'Todavia, nem o varão é sem a mulher, nem a mulher sem o varão, no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '12', 'Porque, como a mulher provém do varão, assim também o varão provém da mulher, mas tudo vem de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '13', 'Julgai entre vós mesmos: é decente que a mulher ore a Deus descoberta?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '14', 'Ou não vos ensina a mesma natureza que é desonra para o varão ter cabelo crescido?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '15', 'Mas ter a mulher cabelo crescido lhe é honroso, porque o cabelo lhe foi dado em lugar de véu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '16', 'Mas, se alguém quiser ser contencioso, nós não temos tal costume, nem as igrejas de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '17', 'Nisto, porém, que vou dizer-vos não vos louvo; porquanto vos ajuntais, não para melhor, senão para pior.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '18', 'Porque antes de tudo ouço que, quando vos ajuntais na igreja, há entre vós dissensões; e em parte o creio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '19', 'E até importa que haja entre vós heresias, para que os que são sinceros se manifestem entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '20', 'De sorte que, quando vos ajuntais num lugar, não é para comer a ceia do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '21', 'Porque, comendo, cada um toma antecipadamente a sua própria ceia; e assim um tem fome e outro embriaga-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '22', 'Não tendes porventura casas para comer e para beber? Ou desprezais a igreja de Deus, e envergonhais os que nada têm? Que vos direi? Louvar-vos-ei? Nisto, não vos louvo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '23', 'Porque eu recebi do SENHOR o que também vos ensinei: que o SENHOR Jesus, na noite em que foi traído tomou o pão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '24', 'E, tendo dado graças, o partiu e disse: Tomai, comei: isto é o meu corpo que é partido por vós; fazei isto em memória de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '25', 'Semelhantemente também, depois de cear, tomou o cálix, dizendo: Este cálix é o Novo Testamento no meu sangue: fazei isto, todas as vezes que beberdes, em memória de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '26', 'Porque todas as vezes que comerdes este pão e beberdes este cálix anunciais a morte do SENHOR, até que venha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '27', 'Portanto, qualquer que comer este pão, ou beber o cálix do SENHOR indignamente, será culpado do corpo e do sangue do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '28', 'Examine-se pois o homem a si mesmo, e assim coma deste pão e beba deste cálix.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '29', 'Porque o que come e bebe indignamente, come e bebe para sua própria condenação, não discernindo o corpo do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '30', 'Por causa disto há entre vós muitos fracos e doentes, e muitos que dormem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '31', 'Porque, se nós nos julgássemos a nós mesmos, não seríamos julgados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '32', 'Mas, quando somos julgados, somos repreendidos pelo SENHOR, para não sermos condenados com o mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '33', 'Portanto, meus irmãos, quando vos ajuntais para comer, esperai uns pelos outros,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '11', '34', 'Mas, se algum tiver fome, coma em casa, para que vos não ajunteis para condenação. Quanto às demais coisas, ordená-las-ei quando for.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '1', 'ACERCA dos dons espirituais, não quero, irmãos, que sejais ignorantes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '2', 'Vós bem sabeis que éreis gentios; levados aos ídolos mudos, conforme éreis guiados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '3', 'Portanto vos quero fazer compreender que ninguém que fala pelo Espírito de Deus diz: Jesus é anátema, e ninguém pode dizer que Jesus é o SENHOR, senão pelo Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '4', 'Ora há diversidade de dons, mas o Espírito é o mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '5', 'E há diversidade de ministérios, mas o SENHOR é o mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '6', 'E há diversidade de operações, mas é o mesmo Deus que opera tudo em todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '7', 'Mas a manifestação do Espírito é dada a cada um, para o que for útil.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '8', 'Porque a um pelo Espírito é dada a palavra da sabedoria; e a outro, pelo mesmo Espírito, a palavra da ciência;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '9', 'E a outro, pelo mesmo Espírito, a fé; e a outro, pelo mesmo Espírito, os dons de curar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '10', 'E a outro a operação de maravilhas; e a outro a profecia; e a outro o dom de discernir os espíritos; e a outro a variedade de línguas; e a outro a interpretação das línguas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '11', 'Mas um só e o mesmo Espírito opera todas estas coisas, repartindo particularmente a cada um como quer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '12', 'Porque, assim como o corpo é um, e tem muitos membros, e todos os membros, sendo muitos, são um só corpo, assim é Cristo também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '13', 'Pois todos nós fomos baptizados em um Espírito, formando um corpo, quer judeus, quer gregos, quer servos, quer livres, e todos temos bebido de um Espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '14', 'Porque também o corpo não é um só membro, mas muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '15', 'Se o pé disser: Porque não sou mão, não sou do corpo; não será por isso do corpo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '16', 'E se a orelha disser: Porque não sou olho não sou do corpo; não será por isso do corpo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '17', 'Se todo o corpo fosse olho, onde estaria o ouvido? Se todo fosse ouvido, onde estaria o olfacto?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '18', 'Mas agora Deus colocou os membros no corpo, cada um deles como quis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '19', 'E, se todos fossem um só membro, onde estaria o corpo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '20', 'Agora pois há muitos membros, mas um corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '21', 'E o olho não pode dizer à mão: Não tenho necessidade de ti; nem ainda a cabeça aos pés: Não tenho necessidade de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '22', 'Antes, os membros do corpo que parecem ser os mais fracos são necessários;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '23', 'E os que reputamos serem menos honrosos no corpo, a esses honramos muito mais; e aos que em nós são menos decorosos damos muito mais honra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '24', 'Porque os que em nós são mais honestos não têm necessidade disso; mas Deus assim formou o corpo, dando muito mais honra ao que tinha falta dela;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '25', 'Para que não haja divisão no corpo, mas antes tenham os membros igual cuidado uns dos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '26', 'De maneira que, se um membro padece, todos os membros padecem com ele; e, se um membro é honrado, todos os membros se regozijam com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '27', 'Ora vós sois o corpo de Cristo, e seus membros em particular.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '28', 'E a uns pôs Deus na igreja, primeiramente apóstolos, em segundo lugar profetas, em terceiro doutores, depois milagres, depois dons de curar, socorros, governos, variedades de línguas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '29', 'Porventura são todos apóstolos? São todos profetas? São todos doutores? São todos operadores de milagres?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '30', 'Têm todos o dom de curar? Falam todos diversas línguas? Interpretam todos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '12', '31', 'Portanto, procurai com zelo os melhores dons; e eu vos mostrarei um caminho ainda mais excelente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '1', 'AINDA que eu falasse as línguas dos homens e dos anjos, e não tivesse caridade, seria como o metal que soa ou como o sino que tine.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '2', 'E ainda que tivesse o dom de profecia, e conhecesse todos os mistérios e toda a ciência, e ainda que tivesse toda a fé, de maneira tal que transportasse os montes, e não tivesse caridade, nada seria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '3', 'E ainda que distribuísse toda a minha fortuna para sustento dos pobres, e ainda, que entregasse o meu corpo para ser queimado, e não tivesse caridade, nada disso me aproveitaria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '4', 'A caridade é sofredora, é benigna; a caridade não é invejosa; a caridade não trata com leviandade, não se ensoberbece,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '5', 'Não se porta com indecência, não busca os seus interesses, não se irrita, não suspeita mal;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '6', 'Não folga com a injustiça, mas folga com a verdade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '7', 'Tudo sofre, tudo crê, tudo espera, tudo suporta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '8', 'a caridade nunca falha: mas havendo profecias, serão aniquiladas: havendo línguas, cessarão; havendo ciência, desaparecerá;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '9', 'Porque, em parte, conhecemos, e em parte profetizamos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '10', 'Mas, quando vier o que é perfeito, então o que é em parte será aniquilado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '11', 'Quando eu era menino, falava como menino, sentia como menino, descorria como menino, mas, logo que cheguei a ser homem, acabei com as coisas de menino.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '12', 'Porque agora vemos por espelho em enigma, mas então veremos face a face; agora conheço em parte, mas então conhecerei como também sou conhecido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '13', '13', 'Agora, pois, permanecem a fé, a esperança e a caridade, estas três, mas a maior destas é a caridade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '1', 'SEGUI a caridade, e procurai com zelo os dons espirituais, mas principalmente o de profetizar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '2', 'Porque o que fala língua estranha não fala aos homens, senão a Deus; porque ninguém o entende, e em espírito fala de mistérios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '3', 'Mas o que profetiza fala aos homens para edificação, exortação e consolação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '4', 'O que fala língua estranha edifica-se a si mesmo, mas o que profetiza edifica a igreja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '5', 'E eu quero que todos vós faleis línguas estranhas, mas muito mais que profetizeis, porque o que profetiza é maior do que o que fala línguas estranhas, a não ser que também interprete para que a igreja receba edificação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '6', 'E agora, irmãos, se eu for ter convosco falando línguas estranhas, que vos aproveitaria, se vos não falasse ou por meio da revelação, ou da ciência, ou da profecia, ou da doutrina?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '7', 'Da mesma sorte, se as coisas inanimadas, que fazem som, seja flauta, seja cítara, não formarem sons distintos, como se conhecerá o que se toca com a flauta ou com a cítara?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '8', 'Porque, se a trombeta der sonido incerto, quem se preparará para a batalha?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '9', 'Assim também vós; se com a língua não pronunciardes palavras bem inteligíveis, como se entenderá o que se diz? Porque estareis como que falando ao ar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '10', 'Há, por exemplo, tanta espécie de vozes no mundo, e nenhuma delas é sem significação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '11', 'Mas, se eu ignorar o sentido da voz, serei bárbaro para aquele a quem falo, e o que fala será bárbaro para mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '12', 'Assim também vós, como desejais dons espirituais, procurai abundar neles, para edificação da igreja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '13', 'Pelo que, o que fala língua estranha, ore para que a possa interpretar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '14', 'Porque, se eu orar em língua estranha, o meu espírito ora bem, mas o meu entendimento fica sem fruto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '15', 'Que farei pois? Orarei com o espírito, mas também orarei com o entendimento; cantarei com o espirito, mas também cantarei com o entendimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '16', 'Doutra maneira, se tu bendisseres com o espírito, como dirá o que ocupa o lugar de indouto, o Ámen, sobre a tua acção de graças, visto que não sabe o que dizes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '17', 'Porque realmente tu dás bem as graças, mas o outro não é edificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '18', 'Dou graças ao meu Deus, porque falo mais línguas do que vós todos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '19', 'Todavia eu antes quero falar na igreja cinco palavras na minha própria inteligência, para que possa também instruir os outros, do que dez mil palavras em língua desconhecida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '20', 'Irmãos, não sejais meninos no entendimento, mas sede meninos na malícia, e adultos no entendimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '21', 'Está escrito na lei: Por gente doutras línguas, e por outros lábios, falarei a este povo; e ainda assim me não ouvirão, diz o SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '22', 'De sorte que as línguas são um sinal, não para os fiéis, mas para os infiéis; e a profecia não é sinal para os infiéis, mas para os fiéis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '23', 'Se pois toda a igreja se congregar num lugar, e todos falarem línguas estranhas, e entrarem indoutos ou infiéis não dirão porventura que estais loucos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '24', 'Mas, se todos profetizarem, e algum indouto ou infiel entrar, de todos é convencido, de todos é julgado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '25', 'Os segredos do seu coração ficarão manifestos, e assim, lançando-se sobre o seu rosto, adorará a Deus, publicando que Deus está verdadeiramente entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '26', 'Que fareis pois, irmãos? Quando vos juntais, cada um de vós tem salmo, tem doutrina, tem revelação, tem língua, tem interpretação. Faça-se tudo para edificação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '27', 'E, se alguém falar língua estranha, faça-se isso por dois, ou quando muito três, e por sua vez, e haja intérprete.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '28', 'Mas, se não houver intérprete, esteja calado na igreja, e fale consigo mesmo, e com Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '29', 'E falem dois ou três profetas, e os outros julguem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '30', 'Mas se a outro, que estiver assentado, for revelada alguma coisa, cale-se o primeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '31', 'Porque todos podereis profetizar, uns depois dos outros; para que todos aprendam, e todos sejam consolados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '32', 'E os espíritos dos profetas estão sujeitos aos profetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '33', 'Porque Deus não é Deus de confusão, senão de paz, como em todas as igrejas dos santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '34', 'As mulheres estejam caladas nas igrejas; porque lhes não é permitido falar; mas estejam sujeitas, como também ordena a lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '35', 'E, se querem aprender alguma coisa, interroguem em casa a seus próprios maridos; porque é indecente que as mulheres falem na igreja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '36', 'Porventura saiu de entre vós a palavra de Deus? Ou veio ela somente para vós?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '37', 'Se alguém cuida ser profeta, ou espiritual, reconheça que as coisas que vos escrevo são mandamentos do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '38', 'Mas, se alguém ignora isto, que ignore.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '39', 'Portanto, irmãos, procurai, com zelo, profetizar, e não proibais falar línguas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '14', '40', 'Mas faça-se tudo decentemente e com ordem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '1', 'TAMBÉM vos notifico, irmãos, o evangelho que já vos tenho anunciado; o qual também recebestes, e no qual também permaneceis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '2', 'Pelo qual também sois salvos se o retiverdes tal como vo-lo tenho anunciado; se não é que crestes em vão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '3', 'Porque primeiramente vos entreguei o que também recebi: que Cristo morreu por nossos pecados, segundo as Escrituras,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '4', 'E que foi sepultado, e que ressuscitou ao terceiro dia, segundo as Escrituras,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '5', 'E que foi visto por Cefas, e depois pelos doze.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '6', 'Depois foi visto, uma vez, por mais de quinhentos irmãos, dos quais vive ainda a maior parte, mas alguns já dormem também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '7', 'Depois foi visto por Tiago, depois por todos os apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '8', 'E por derradeiro de todos me apareceu também a mim, como a um abortivo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '9', 'Porque eu sou o menor dos apóstolos, que não sou digno de ser chamado apóstolo, pois que persegui a igreja de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '10', 'Mas pela graça de Deus sou o que sou; e a sua graça para comigo não foi vã, antes trabalhei muito mais do que todos eles; todavia não eu, mas a graça de Deus, que está comigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '11', 'Então, ou seja eu ou sejam eles, assim pregamos e assim haveis crido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '12', 'Ora, se se prega que Cristo ressuscitou dos mortos, como dizem alguns de entre vós que não há ressurreição de mortos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '13', 'E, se não há ressurreição de mortos, também Cristo não ressuscitou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '14', 'E se Cristo não ressuscitou, logo é vã a nossa pregação, e também é vã a vossa fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '15', 'E assim somos também considerados como falsas testemunhas de Deus, pois testificamos de Deus, que ressuscitou a Cristo, ao qual, porém, não ressuscitou, se na verdade, os mortos não ressuscitam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '16', 'Porque, se os mortos não ressuscitam, também Cristo não ressuscitou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '17', 'E, se Cristo não ressuscitou, é vã a vossa fé, e ainda permaneceis nos vossos pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '18', 'E também os que dormiram em Cristo estão perdidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '19', 'Se esperamos em Cristo só nesta vida, somos os mais miseráveis de todos os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '20', 'Mas agora Cristo ressuscitou dos mortos, e foi feito as primícias dos que dormem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '21', 'Porque assim como a morte veio por um homem, também a ressurreição dos mortos veio por um homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '22', 'Porque, assim como todos morrem em Adão, assim também todos serão vivificados em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '23', 'Mas cada um por sua ordem: Cristo as primícias, depois os que são de Cristo, na sua vinda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '24', 'Depois virá o fim, quando tiver entregado o reino a Deus, ao Pai, e quando houver aniquilado todo o império, e toda a potestade e força.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '25', 'Porque convém que reine até que haja posto a todos os inimigos debaixo de seus pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '26', 'Ora o último inimigo que há-de ser aniquilado é a morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '27', 'Porque todas as coisas sujeitou debaixo de seus pés. Mas, quando diz que todas as coisas lhe estão sujeitas, claro está que se exceptua aquele que lhe sujeitou todas as coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '28', 'E, quando todas as coisas lhe estiverem sujeitas, então também o mesmo Filho se sujeitará àquele que todas as coisas lhe sujeitou, para que Deus seja tudo em todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '29', 'Doutra maneira, que farão os que se baptizam pelos mortos, se absolutamente os mortos não ressuscitam? Porque se baptizam eles então pelos mortos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '30', 'Porque estamos nós também a toda a hora em perigo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '31', 'Eu protesto que cada dia morro gloriando-me em vós, irmãos, por Cristo Jesus, nosso SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '32', 'Se, como homem, combati em Êfeso contra as bestas, que me aproveita isso, se os mortos não ressuscitam? Comamos e bebamos, que amanhã morreremos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '33', 'Não vos enganeis: as más conversações corrompem os bons costumes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '34', 'Vigiai justamente e não pequeis; porque alguns ainda não têm o conhecimento de Deus: digo-o para vergonha vossa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '35', 'Mas alguém dirá: Como ressuscitarão os mortos? E com que corpo virão?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '36', 'Insensato o que tu semeias não é vivificado, se primeiro não morrer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '37', 'E, quando semeias, não semeias o corpo que há-de nascer, mas o simples grão, como de trigo, ou doutra qualquer semente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '38', 'Mas Deus dá-lhe o corpo como quer, e a cada semente o seu próprio corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '39', 'Nem toda a carne é uma mesma carne, mas uma é a carne dos homens, e outra a carne dos animais, e outra a dos peixes, e outra a das aves.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '40', 'E há corpos celestes e corpos terrestres, mas uma é a glória dos celestes e outra a dos terrestres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '41', 'Uma é a glória do sol, e outra a glória da lua, e outra a glória das estrelas; porque uma estrela difere em glória de outra estrela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '42', 'Assim também a ressurreição dos mortos. Semeia-se o corpo em corrupção; ressuscitará em incorrupção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '43', 'Semeia-se em ignomínia, ressuscitará em glória. Semeia-se em fraqueza ressuscitará com vigor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '44', 'Semeia-se corpo animal, ressuscitará corpo espiritual. Se há corpo animal, há também corpo espiritual.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '45', 'Assim está também escrito: O primeiro homem, Adão, foi feito em alma vivente: o último Adão em espírito vivificante.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '46', 'Mas não é o primeiro o espiritual, senão o animal; depois o espiritual.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '47', 'O primeiro homem, da terra, é terreno; o segundo homem, o SENHOR, é do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '48', 'Qual o terreno, tais são também os terrestres; e, qual o celestial, tais também os celestiais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '49', 'E, assim como trouxemos a imagem do terreno, assim traremos também a imagem do celestial.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '50', 'E agora digo isto, irmãos: que a carne e o sangue pão podem herdar o reino de Deus, nem a corrupção herda a incorrupção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '51', 'Eis aqui vos digo um mistério: Na verdade, nem todos dormiremos, mas todos seremos transformados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '52', 'Num momento, num abrir e fechar de olhos, ante a última trombeta; porque a trombeta soará, e os mortos ressuscitarão incorruptíveis, e nós seremos transformados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '53', 'Porque convém que isto que é corruptível se revista da incorruptibilidade, e que isto que é mortal se revista da imortalidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '54', 'E, quando isto que é corruptível se revestir da incorruptibilidade, e isto que é mortal se revestir da imortalidade, então cumprir-se-á a palavra que está escrita: Tragada foi a morte na vitória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '55', 'Onde está, ó morte, o teu aguilhão? Onde está, ó inferno, a tua vitória?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '56', 'Ora o aguilhão da morte é o pecado, e a força do pecado é a lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '57', 'Mas graças a Deus que nos dá a vitória por nosso SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '15', '58', 'Portanto, meus amados irmãos, sede firmes e constantes, sempre abundantes na obra do SENHOR, sabendo que o vosso trabalho não é vão no SENHOR');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '1', 'ORA, quanto à colecta que se faz para os santos, fazei vós também o mesmo que ordenei às igrejas da Galácia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '2', 'No primeiro dia da semana cada um de vós ponha de parte o que puder ajuntar, conforme a sua prosperidade, para que se não façam as colectas quando chegar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '3', 'E, quando tiver chegado, mandarei os que por cartas aprovardes, para levar a vossa dádiva a Jerusalém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '4', 'E, se valer a pena que eu também vá, irão comigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '5', 'Irei, porém, ter convosco depois de ter passado pela Macedónia (porque tenho de passar pela Macedónia).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '6', 'E bem pode ser que fique convosco, e passe também o inverno, para que me acompanheis aonde quer que eu for.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '7', 'Porque não vos quero agora ver de passagem, mas espero ficar convosco algum tempo, se o SENHOR o permitir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '8', 'Ficarei, porém, em Éfeso até ao Pentecostes;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '9', 'Porque uma porta grande e eficaz se me abriu; e há muitos adversários.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '10', 'E, se Timóteo for, vede que esteja sem temor convosco; porque trabalha na obra do SENHOR, como eu também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '11', 'Portanto ninguém o despreze, mas acompanhai-o em paz, para que venha ter comigo: pois o espero com os irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '12', 'E, acerca do irmão Apolo, roguei-lhe muito que fosse com os irmãos ter convosco, mas, na verdade, não teve vontade de ir agora; irá, porém, quando se lhe ofereça boa ocasião.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '13', 'Vigiai, estai firmes na fé: portai-vos varonilmente e fortalecei-vos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '14', 'Todas as vossas coisas sejam feitas com caridade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '15', 'Agora vos rogo, irmãos, (sabeis que a família de Estéfanas é as primícias da Acaia, e que se tem dedicado ao ministério dos santos)');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '16', 'Que também vos sujeites aos tais, e a todo aquele que auxilia na obra e trabalha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '17', 'Folgo, porém, com a vinda de Estéfanes, e de Fortunato e de Acaio; porque estes supriram o que da vossa parte me faltava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '18', 'Porque recrearam o meu espírito e o vosso. Reconhecei pois aos tais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '19', 'As igrejas da Ásia vos saúdam. Saúdam-vos afectuosamente no SENHOR Áquila e Priscila, com a igreja, que está em sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '20', 'Todos os irmãos vos saúdam. Saudai-vos uns aos outros com ósculo santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '21', 'Saudação da minha própria mão, de Paulo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '22', 'Se alguém não ama ao SENHOR Jesus Cristo, seja anátema; maranata.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '23', 'A graça do SENHOR Jesus Cristo seja convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('7', '16', '24', 'O meu amor seja com todos vós em Cristo Jesus. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '1', '¶ PAULO, apóstolo de Jesus Cristo, pela vontade de Deus, e o irmão Timóteo, à igreja de Deus, que está em Corinto, com todos os santos que estão em toda a Acaia:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '2', 'Graça a vós e paz da parte de Deus nosso Pai e da do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '3', 'Bendito seja o Deus e Pai de nosso SENHOR Jesus Cristo, o Pai das misericórdias e o Deus de toda a consolação;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '4', 'Que nos consola em toda a nossa tribulação, para que também possamos consolar os que estiverem em alguma tribulação, com a consolação com que nós mesmos somos consolados de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '5', 'Porque, como as aflições de Cristo abundam em nós, assim também a nossa consolação abunda por meio de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '6', 'Mas, se somos atribulados, é para vossa consolação e salvação; ou, se somos consolados, para vossa consolação é, a qual se opera suportando com paciência as mesmas aflições que nós também padecemos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '7', 'E a nossa esperança acerca de vós é firme, sabendo que, como sois participantes das aflições, assim o sereis também da consolação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '8', 'Porque não queremos, irmãos, que ignoreis a tribulação que nos sobreveio na Ásia, pois que fomos sobremaneira agravados mais do que podíamos suportar, de modo tal que até da vida desesperámos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '9', 'Mas já em nós mesmos tínhamos a sentença de morte, para que não, confiássemos em nós, mas em Deus, que ressuscita os mortos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '10', 'O qual nos livrou de tão grande morte, e livrará; em quem esperamos que também nos livrará ainda,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '11', 'Ajudando-nos também vós com orações por nós, para que pela mercê, que por muitas pessoas nos foi feita, por muitas também sejam dadas graças a nosso respeito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '12', 'Porque a nossa glória é esta: O testemunho da nossa consciência, de que com simplicidade e sinceridade de Deus, não com sabedoria carnal, mas na graça de Deus, temos vivido no mundo, e maiormente convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '13', 'Porque nenhumas outras coisas vos escrevemos, senão as que já sabeis ou também reconheceis; e espero que também até ao fim as reconhecereis:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '14', 'Como também já em parte reconhecestes em nós, que somos a vossa glória, como também vós sereis a nossa no dia do SENHOR Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '15', 'E com esta confiança quis primeiro ir ter convosco; para que tivésseis uma segunda graça;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '16', 'E por vós passar à Macedónia, e da Macedónia ir outra vez ter convosco, e ser guiado por vós à Judeia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '17', 'E, deliberando isto, usei porventura de leviandade? Ou o que delibero, o delibero segundo a carne, para que haja em mim sim, sim, e não, não?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '18', 'Antes, como Deus é fiel, a nossa palavra para convosco não foi sim e não.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '19', 'Porque o Filho de Deus, Jesus Cristo, que entre vós foi pregado por nós, isto é, por mim, e Silvano, e Timóteo, não foi sim e não; mas nele houve sim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '20', 'Porque todas quantas promessas há de Deus, são nele sim, e por ele o Ámen, para glória de Deus por nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '21', 'Mas o que nos confirma convosco em Cristo, e o que nos ungiu, é Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '22', 'O qual também nos selou e deu o penhor do Espírito em nossos corações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '23', 'Invoco, porém, a Deus por testemunha sobre a minha alma, que para vos poupar não tenho até agora ido a Corinto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '1', '24', 'Não que tenhamos domínio sobre a vossa fé, mas porque somos cooperadores do vosso gozo; porque pela fé estais em pé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '1', 'MAS deliberei isto comigo não ir mais ter convosco em tristeza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '2', 'Porque, se eu vos entristeço, quem é que me alegrará, senão aquele que por mim foi contristado?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '3', 'E escrevi-vos isto mesmo, para que, quando lá for, não tenha tristeza da parte dos que deveriam alegrar-me; confiando em vós todos, que a minha alegria é a de todos vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '4', 'Porque em muita tribulação e angústia do coração vos escrevi, com muitas lágrimas, não para que vos entristecêsseis, mas para que conhecêsseis o amor que abundantemente vos tenho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '5', 'Porque, se alguém me contristou, não me contristou a mim senão em parte, para vos não sobrecarregar a vós todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '6', 'Basta-lhe ao tal esta repreensão feita por muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '7', 'De maneira que pelo contrário deveis antes perdoar-lhe e consolá-lo, para que o tal não seja de modo algum devorado de demasiada tristeza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '8', 'Pelo que vos rogo que confirmeis para com ele o vosso amor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '9', 'E para isso vos escrevi também, para por esta prova saber se sois obedientes em tudo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '10', 'E a quem perdoardes alguma coisa também; porque, o que eu também perdoei, se é que tenho perdoado, por amor de vós o fiz na presença de Cristo; para que não sejamos vencidos por Satanás;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '11', 'Porque não ignoramos os seus ardis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '12', 'Ora, quando cheguei a Troas para pregar o evangelho de Cristo, e abrindo-se-me uma porta no SENHOR,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '13', 'Não tive descanso no meu espírito, porque não achei ali meu irmão Tito; mas, despedindo-me deles, parti para a Macedónia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '14', 'E graças a Deus, que sempre nos faz triunfar em Cristo, e por meio de nós manifesta em todo o lugar o cheiro do seu conhecimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '15', 'Porque para Deus somos o bom cheiro de Cristo, nos que se salvam e nos que se perdem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '16', 'Para estes certamente cheiro de morte; mas para aqueles cheiro de vida. E para estas coisas quem é idóneo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '2', '17', 'Porque nós não somos, como muitos, falsificadores da palavra de Deus, antes falamos de Cristo com sinceridade, como de Deus na presença de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '1', 'PORVENTURA começamos outra vez a louvar-nos a nós mesmos? Ou necessitamos, como alguns, de cartas de recomendação para vós, ou de recomendações de vós?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '2', 'Vós sois a nossa carta, escrita em nossos corações, conhecida e lida por todos os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '3', 'Porque já é manifesto que vós sois a carta de Cristo, ministrada por nós, e escrita, não com tinta, mas com o Espírito de Deus vivo, não em tábuas de pedra, mas nas tábuas de carne do coração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '4', 'E é por Cristo que temos tal confiança em Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '5', 'Não que sejamos capazes, por nós, de pensar alguma coisa, como de nós mesmos; mas a nossa capacidade vem de Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '6', 'O qual nos fez também capazes de ser ministros dum novo testamento, não da letra, mas do espírito; porque a letra mata, e o espírito vivifica.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '7', 'E, se o ministério da morte, gravado com letras em pedras, veio em glória, de maneira que os filhos de Israel não podiam fitar os olhos na face de Moisés, por causa da glória do seu rosto, a qual era transitória,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '8', 'Como não será de maior glória o ministério do espírito?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '9', 'Porque, se o ministério da condenação foi glorioso, muito mais excederá em glória o ministério da justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '10', 'Porque também o que foi glorificado nesta parte não foi glorificado, por causa desta excelente glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '11', 'Porque, se o que era transitório foi para glória, muito mais é em glória o que permanece.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '12', 'Tendo, pois, tal esperança, usamos de muita ousadia no falar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '13', 'E não somos como Moisés, que punha um véu sobre a sua face, para que os filhos de Israel não olhassem firmemente para o fim daquilo que era transitório.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '14', 'Mas os seus sentidos foram endurecidos: porque até hoje o mesmo véu está por levantar na lição do velho testamento, o qual foi por Cristo abolido;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '15', 'E até hoje, quando é lido Moisés, o véu está posto sobre o coração deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '16', 'Mas, quando se converterem ao SENHOR, então o véu se tirará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '17', 'Ora o SENHOR é Espírito; e onde está o Espírito do SENHOR aí há liberdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '3', '18', 'Mas todos nós, com cara descoberta, reflectindo como um espelho a glória do SENHOR, somos transformados de glória em glória na mesma imagem, como pelo Espírito do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '1', 'PELO que, tendo este ministério segundo a misericórdia que nos foi feita, não desfalecemos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '2', 'Antes, rejeitámos as coisas que por vergonha se ocultam, não andando com astúcia nem falsificando a palavra de Deus; e assim nos recomendamos à consciência de todo o homem, na presença de Deus, pela manifestação da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '3', 'Mas, se ainda o nosso evangelho está encoberto, para os que se perdem está encoberto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '4', 'Nos quais o deus deste século cegou os entendimentos dos incrédulos, para que lhes não resplandeça a luz do evangelho da glória de Cristo, que é a imagem de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '5', 'Porque não nos pregamos a nós mesmos, mas a Cristo Jesus, o SENHOR; e nós mesmo somos vossos servos por amor de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '6', 'Porque Deus, que disse que das trevas resplandecesse a luz, é quem resplandeceu em nossos corações, para iluminação do conhecimento da glória de Deus, na face de Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '7', 'Temos, porém, este tesouro em vasos de barro, para que a excelência do poder seja de Deus, e não de nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '8', 'Em tudo somos atribulados, mas não angustiados; perplexos, mas não desanimados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '9', 'Perseguidos, mas não desamparados; abatidos, mas não destruídos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '10', 'Trazendo sempre por toda a parte a mortificação do SENHOR Jesus no nosso corpo, para que a vida de Jesus se manifeste também em nossos corpos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '11', 'E assim nós, que vivemos, estamos sempre entregues à morte por amor de Jesus, para que a vida de Jesus se manifeste também na nossa carne mortal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '12', 'De maneira que em nós opera a morte, mas em vós a vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '13', 'E temos portanto o mesmo espírito de fé, como está escrito: Cri, por isso falei. Nós cremos também, por isso também falamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '14', 'Sabendo que o que ressuscitou o SENHOR Jesus nos ressuscitará também por Jesus, e nos apresentará convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '15', 'Porque tudo isto é por amor de vós, para que a graça, multiplicada por meio de muitos, faça abundar a acção de graças para glória de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '16', 'Por isso não desfalecemos; mas, ainda que o nosso homem exterior se corrompa, o interior, contudo, se renova de dia em dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '17', 'Porque a nossa leve e momentânea tribulação produz para nós um peso eterno de glória mui excelente;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '4', '18', 'Não atentando nós nas coisas que se vêem, mas nas que se não vêem; porque as que se vêem são temporais, e as que se não vêem são eternas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '1', 'PORQUE sabemos que, se a nossa casa terrestre deste tabernáculo se desfizer, temos de Deus um edifício, uma casa não feita por mãos, eterna, nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '2', 'E por isso também gememos, desejando ser revestidos da nossa habitação, que é do céu;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '3', 'Se, todavia, estando vestidos, não formos achados nus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '4', 'Porque também nós, os que estamos neste tabernáculo, gememos carregados: não porque queremos ser despidos, mas revestidos para que o mortal seja absorvido pela vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '5', 'Ora, quem para isto mesmo nos preparou foi Deus, o qual nos deu também o penhor do Espirito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '6', 'Pelo que estamos sempre de bom ânimo, sabendo que, enquanto estamos no corpo, vivemos ausentes do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '7', '(Porque andamos por fé, e não por vista).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '8', 'Mas temos confiança e desejamos antes deixar este corpo, para habitar com o SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '9', 'Pelo que muito desejamos também ser-lhe agradáveis, quer presentes, quer ausentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '10', 'Porque todos devemos comparecer ante o tribunal de Cristo, para que cada um receba segundo o que tiver feito por meio do corpo, ou bem, ou mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '11', 'Assim que, sabendo o temor que se deve ao SENHOR, persuadimos os homens à fé, mas somos manifestos a Deus; e espero que nas vossas consciências sejamos também manifestos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '12', 'Porque não nos recomendamos outra vez a vós; mas damo-vos ocasião de vos gloriardes de nós, para que tenhais que responder aos que se gloriam na aparência, e não no coração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '13', 'Porque, se enlouquecemos, é para Deus; e, se conservamos o juízo, é para vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '14', 'Porque o amor de Cristo nos constrange, julgando nós assim: que, se um morreu por todos, logo todos morreram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '15', 'E ele morreu por todos, para que os que vivem não vivam mais, para si, mas para aquele que por eles morreu e ressuscitou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '16', 'Assim que daqui por diante a ninguém conhecemos segundo a carne, e, ainda que também tenhamos conhecido Cristo segundo a carne, contudo agora já o não conhecemos deste modo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '17', 'Assim que, se alguém está em Cristo, nova criatura é: as coisas velhas já passaram; eis que tudo se fez novo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '18', 'E tudo isto provém de Deus, que nos reconciliou consigo mesmo por Jesus Cristo, e nos deu o ministério da reconciliação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '19', 'Isto é, Deus estava em Cristo reconciliando consigo o mundo, não lhes imputando os seus pecados; e pôs em nós a palavra da reconciliação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '20', 'De sorte que somos embaixadores da parte de Cristo, como se Deus por nós rogasse. Rogamo-vos pois da parte de Cristo que vos reconcilieis com Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '5', '21', 'Aquele que não conheceu pecado, o fez pecado por nós; para que nele fôssemos feitos justiça de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '1', 'E NÓS, cooperando também com ele, vos exortamos a que não recebais a graça de Deus em vão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '2', '(Porque diz: Ouvi-te em tempo aceitável e socorri-te no dia da salvação: eis aqui agora o tempo aceitável, eis aqui agora o dia da salvação).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '3', 'Não dando nós escândalo em coisa alguma, para que o nosso ministério não seja censurado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '4', 'Antes, como ministro de Deus, tornando-nos recomendáveis em tudo na muita paciência, nas aflições, nas necessidades, nas angústias,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '5', 'Nos açoites, nas prisões, nos tumultos, nos trabalhos, nas vigílias, nos jejuns,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '6', 'Na pureza, na ciência, na longanimidade, na benignidade, no Espírito Santo, no amor não fingido,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '7', 'Na palavra da verdade, no poder de Deus, pelas armas da justiça, à direita e à esquerda,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '8', 'Por honra e por desonra, por infâmia e por boa fama; como enganadores, e sendo verdadeiros;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '9', 'Como desconhecidos, mas sendo bem conhecidos; como morrendo, e eis que vivemos; como castigados, e não mortos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '10', 'Como contristados, mas sempre alegres; como pobres, mas enriquecendo a muitos; como nada tendo, e possuindo tudo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '11', 'Ó coríntios, a nossa boca está aberta para vós, o nosso coração está dilatado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '12', 'Não estais estreitados em nós; mas estais estreitados nos vossos próprios afectos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '13', 'Ora, em recompensa disto, (falo como a filhos) dilatai-vos também vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '14', 'Não vos prendais a um jugo desigual com os infiéis; Porque, que sociedade tem a justiça com a injustiça? E que comunhão tem a luz com as trevas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '15', 'E que concórdia há entre Cristo e Belial? Ou que parte tem o fiel com o infiel?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '16', 'E que consenso tem o templo de Deus com os ídolos? Porque vós sois o templo do Deus vivente, como Deus disse: Neles habitarei, e entre eles andarei; e eu serei o seu Deus e eles serão o meu povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '17', 'Pelo que saí do meio deles, e apartai-vos, diz o SENHOR; e não toqueis nada imundo, e eu vos receberei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '6', '18', 'E eu serei para vós Pai e vós sereis para mim filhos e filhas, diz o SENHOR todo poderoso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '1', 'ORA, amados, pois que temos tais promessas, purifiquemo-nos de toda a imundície da carne e do espírito, aperfeiçoando a santificação no temor de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '2', 'Recebei-nos em vossos corações; a ninguém agradámos, a ninguém corrompemos, de ninguém buscámos o nosso proveito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '3', 'Não digo isto para vossa condenação; pois já antes tinha dito que estais em nossos corações para juntamente morrer e viver.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '4', 'Grande é a ousadia da minha fala para convosco, e grande a minha jactância a respeito de vós; estou cheio de consolação: superabundo de gozo em todas as nossas tribulações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '5', 'Porque, mesmo quando chegámos à Macedónia, a nossa carne não teve repouso algum; antes em tudo fomos atribulados; por fora combates, temores por dentro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '6', 'Mas Deus, que consola os abatidos, nos consolou com a vinda de Tito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '7', 'E não somente com a sua vinda, mas também pela consolação com que foi consolado de vós, constando-nos as vossas saudades, o vosso choro, o vosso zelo por mim, de maneira que muito me regozijei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '8', 'Porquanto, ainda que vos contristei com a minha carta, não me arrependo, embora já me tivesse arrependido por ver que aquela carta vos contristou, ainda que por pouco tempo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '9', 'Agora folgo, não porque fostes contristados para o arrependimento; pois fostes contristados segundo Deus; de maneira que por nós não padecestes dano em coisa alguma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '10', 'Porque a tristeza segundo Deus opera arrependimento para a salvação, da qual ninguém se arrepende; mas a tristeza do mundo opera a morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '11', 'Porque, quanto cuidado não produziu isto mesmo em vós, que segundo Deus fostes contristados! Que apologia, que indignação, que temor, que saudades, que zelo, que vingança! Em tudo mostrastes estar puros neste negócio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '12', 'Portanto, ainda que vos escrevi, não foi por causa do que fez o agravo, nem por causa do que sofreu o agravo, mas para que o vosso grande cuidado por nós fosse manifesto diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '13', 'Por isso fomos consolados pela vossa consolação, e muito mais nos alegramos pela alegria de Tito, porque seu espírito foi recreado por vós todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '14', 'Porque, se nalguma coisa me gloriei de vós para com ele, não fiquei envergonhado; mas, como vos dissemos tudo com verdade, também a nossa glória para com Tito se achou verdadeira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '15', 'E o seu entranhável afecto para convosco é mais abundante, lembrando-se da obediência de vós todos, e de como o recebestes com temor e tremor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '7', '16', 'Regozijo-me de em tudo poder confiar em vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '1', 'TAMBÉM, irmãos, vos fazemos conhecer a graça de Deus dada às igrejas da Macedónia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '2', 'Como em muita prova de tribulação houve abundância do seu gozo e como a sua profunda pobreza abundou em riquezas da sua generosidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '3', 'Porque, segundo o seu poder (o que eu mesmo testifico), e ainda acima do seu poder, deram voluntariamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '4', 'Pedindo-nos com muitos rogos a graça e a comunicação deste serviço, que se fazia para com os santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '5', 'E não somente fizeram como nós esperávamos, mas a si mesmos se deram primeiramente ao SENHOR, e depois a nós, pela vontade de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '6', 'De maneira que exortámos a Tito que, assim como antes tinha começado, assim também acabe esta graça entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '7', 'Portanto, assim como em tudo abundais em fé, e em palavra, e em ciência, e em toda a diligência, e em vossa caridade para connosco, assim também abundeis nesta graça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '8', 'Não digo isto como quem manda, mas para provar, pela diligência dos outros, a sinceridade da vossa caridade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '9', 'Porque já sabeis a graça de nosso SENHOR Jesus Cristo, que, sendo rico, por amor de vós se fez pobre; para que pela sua pobreza enriquecêsseis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '10', 'E nisto dou o meu parecer; pois isto vos convém a vós, que desde o ano passado começastes; e não foi só praticar, mas também querer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '11', 'Agora, porém, completai também o já começado, para que, assim como houve a prontidão de vontade, haja também o cumprimento, segundo o que tendes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '12', 'Porque, se há prontidão de vontade, será aceita segundo o que qualquer tem, e não segundo o que não tem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '13', 'Mas, não digo isto para que os outros tenham alivio, e vós opressão,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '14', 'Mas para igualdade: neste tempo presente, a vossa abundância supra a falta dos outros, para que também a sua abundância supra a vossa falta, e haja igualdade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '15', 'Como está escrito: O que muito colheu não teve de mais; e o que pouco, não teve de menos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '16', 'Mas, graças a Deus, que pôs a mesma solicitude por vós no coração de Tito;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '17', 'Pois aceitou a exortação, e muito diligente partiu voluntariamente para vós,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '18', 'E com ele enviámos aquele irmão cujo louvor no evangelho está espalhado em todas as igrejas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '19', 'E não só isto, mas foi também escolhido pelas igrejas para companheiro da nossa viagem, nesta graça que por nós é ministrada para glória do mesmo SENHOR, e prontidão do vosso ânimo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '20', 'Evitando isto, que alguém nos vitupere por esta abundância, que por nós é ministrada;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '21', 'Pois zelamos do que é honesto, não só diante do SENHOR, mas também diante dos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '22', 'Com eles enviámos também outro nosso irmão, o qual muitas vezes, e em muitas coisas, já experimentámos ser diligente, e agora muito mais diligente ainda pela muita confiança que em vós tem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '23', 'Quanto a Tito, é meu companheiro, e cooperador para convosco; quanto a nossos irmãos, são embaixadores das igrejas e glória de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '8', '24', 'Portanto mostrai para com eles, perante a face das igrejas, a prova da vossa caridade e da nossa glória acerca de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '1', 'QUANTO à administração que se faz a favor dos santos, não necessito escrever-vos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '2', 'Porque bem sei a prontidão do vosso ânimo, da qual me glorio de vós para com os macedónios; que a Acaia está pronta desde o ano passado, e o vosso zelo tem estimulado muitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '3', 'Mas enviei estes irmãos, para que a nossa glória, acerca de vós, não seja vã nesta parte; para que (como já disse) possais estar prontos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '4', 'A fim de, se acaso os macedónios vierem comigo, e vos acharem desapercebidos, não nos envergonharmos nós (para não dizermos vós) deste firme fundamento de glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '5', 'Portanto, tive por coisa necessária exortar estes irmãos, para que primeiro fossem ter convosco, e preparassem de antemão a vossa bênção, já antes anunciada, para que esteja pronta como bênção, e não como avareza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '6', 'E digo isto: Que o que semeia pouco, pouco também ceifará; e o que semeia em abundância, em abundância ceifará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '7', 'Cada um contribua segundo propôs no seu coração; não com tristeza, ou por necessidade; porque Deus ama ao que dá com alegria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '8', 'E Deus é poderoso para fazer abundar em vós toda a graça, a fim de que tendo sempre, em tudo, toda a suficiência, abundeis em toda a boa obra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '9', 'Conforme está escrito: Espalhou, deu aos pobres; a sua justiça permanece para sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '10', 'Ora, aquele que dá a semente ao que semeia, e pão para comer, também multiplicará a vossa sementeira, e aumentará os frutos da vossa justiça;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '11', 'Para que em tudo enriqueçais para toda a beneficência, a qual faz que por nós se dêem graças a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '12', 'Porque a administração deste serviço, não só supre as necessidades dos santos, mas também abunda em muitas graças, que se dão a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '13', 'Visto como, na prova desta administração, glorificam a Deus pela submissão que confessais, quanto ao evangelho de Cristo, e pela liberalidade de vossos dons para com eles, e para com todos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '14', 'E pela sua oração por vós, tendo de vós saudades, por causa da excelente graça de Deus que em vós há.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '9', '15', 'Graças a Deus pois pelo seu dom inefável.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '1', 'ALÉM disto, eu, Paulo, vos rogo, pela mansidão e benignidade de Cristo, eu que, na verdade, quando presente entre vós, sou humilde, mas ausente, ousado para convosco;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '2', 'Rogo-vos pois que, quando estiver presente, não me veja obrigado a usar com confiança da ousadia que espero ter com alguns que nos julgam, como se andássemos segundo a carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '3', 'Porque, andando na carne, não militamos segundo a carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '4', 'Porque as armas da nossa milícia não são carnais, mas sim poderosas em Deus, para destruição das fortalezas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '5', 'Destruindo os conselhos, e toda a altivez que se levanta contra o conhecimento de Deus, e levando cativo todo o entendimento à obediência de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '6', 'E estando prontos para vingar toda a desobediência, quando for cumprida a vossa obediência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '7', 'Olhais para as coisas segundo a aparência? Se alguém confia de si mesmo que é de Cristo, pense outra vez isto consigo, que, assim como ele é de Cristo, também nós de Cristo somos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '8', 'Porque, ainda que eu me glorie mais alguma coisa do nosso poder, o qual o SENHOR nos deu para edificação, e não para vossa destruição, não me envergonharei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '9', 'Para que não pareça como se quisera intimidar-vos por cartas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '10', 'Porque as suas cartas, dizem, são graves e fortes, mas a presença do corpo é fraca, e a palavra desprezível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '11', 'Pense o tal isto, que quais somos na palavra por cartas, estando ausentes, tais seremos também por obra, estando presentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '12', 'Porque não ousamos classificar-nos, ou comparar-nos com alguns, que se louvam a si mesmos; mas estes que se medem a si mesmos, e se comparam consigo mesmos, estão sem entendimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '13', 'Porém não nos gloriaremos fora de medida, mas conforme a recta medida que Deus nos deu, para chegarmos até vós;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '14', 'Porque não nos estendemos além do que convém, como se não houvéssemos de chegar até vós, pois já chegámos também até vós no evangelho de Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '15', 'Não nos gloriando fora de medida nos trabalhos alheios; antes tendo esperança de que, crescendo a vossa fé, seremos abundantemente engrandecidos entre vós, conforme a nossa regra,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '16', 'Para anunciar o evangelho nos lugares que estão além de vós, e não em campo de outrem, para nos não gloriarmos no que estava já preparado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '17', 'Aquele, porém, que se gloria, glorie-se no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '10', '18', 'Porque não é aprovado quem a si mesmo se louva, mas sim aquele a quem o SENHOR louva.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '1', 'OXALÁ me suportásseis um pouco na minha loucura! Suportai-me, porém, ainda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '2', 'Porque estou zeloso de vós com zelo de Deus; porque vos tenho preparado para vos apresentar como uma virgem pura a um marido, a saber, a Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '3', 'Mas temo que, assim como a serpente enganou Eva com a sua astúcia, assim também sejam de alguma sorte corrompidos os vossos sentidos, e se apartem da simplicidade que há em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '4', 'Porque, se alguém for pregar-vos outro Jesus que nós não temos pregado, ou se recebeis outro espirito que não recebestes, ou outro evangelho que não abraçastes, com razão o sofrereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '5', 'Porque penso que em nada fui inferior aos mais excelentes apóstolos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '6', 'E, se sou rude na palavra, não o sou contudo na ciência; mas já em tudo nos temos feito conhecer totalmente entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '7', 'Pequei porventura, humilhando-me a mim mesmo, para que vós fôsseis exaltados, porque de graça vos anunciei o evangelho de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '8', 'Outras igrejas despojei eu para vos servir, recebendo delas salário; e quando estava presente convosco, e tinha necessidade, a ninguém fui pesado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '9', 'Porque os irmãos que vieram da Macedónia supriram a minha necessidade; e em tudo me guardei de vos ser pesado, e ainda me guardarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '10', 'Como a verdade de Cristo está em mim, esta glória não me será impedida nas regiões da Acaia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '11', 'Porquê? Porque vos não amo? Deus o sabe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '12', 'Mas, o que eu faço, o farei, para cortar ocasião aos que buscam ocasião, a fim de que, naquilo em que se gloriam, sejam achados assim como nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '13', 'Porque tais falsos apóstolos são obreiros fraudulentos, transfigurando-se em apóstolos de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '14', 'E não é maravilha, porque o próprio Satanás se transfigura em anjo de luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '15', 'Não é muito pois que os seus ministros se transfigurem em ministros da justiça: o fim dos quais será conforme as suas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '16', 'Outra vez digo: ninguém me julgue insensato, ou então recebei-me como insensato, para que também me glorie um pouco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '17', 'O que digo, não o digo segundo o SENHOR, mas como por loucura, nesta confiança de gloriar-me.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '18', 'Pois que muitos se gloriam segundo a carne, eu também me gloriarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '19', 'Porque, sendo vós sensatos, de boamente tolerais os insensatos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '20', 'Pois sois sofredores, se alguém vos põe em servidão, se alguém vos devora, se alguém vos apanha, se alguém se exalta, se alguém vos fere no rosto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '21', 'Envergonhado o digo, como se nós fôssemos fracos, mas no que qualquer tem ousadia (com insensatez falo) também eu tenho ousadia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '22', 'São hebreus? Também eu; são israelitas? Também eu; são descendência de Abraão? Também eu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '23', 'São ministros de Cristo? (falo como fora de mim) eu ainda mais: em trabalhos, muito mais; em açoites, mais do que eles; em prisões, muito mais; em perigo de morte, muitas vezes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '24', 'Recebi dos judeus cinco quarentenas de açoites menos um.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '25', 'Três vezes fui açoitado com varas, uma vez fui apedrejado, três vezes sofri naufrágio, uma noite e um dia passei no abismo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '26', 'Em viagens muitas vezes, em perigos de rios, em perigos de salteadores, em perigos dos da minha nação, em perigos dos gentios, em perigos na cidade, em perigos no deserto, em perigos no mar, em perigos entre os falsos irmãos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '27', 'Em trabalhos e fadiga, em vigílias, em fome e sede, em jejum muitas vezes, em frio e nudez.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '28', 'Além das coisas exteriores, me oprime cada dia o cuidado de todas as igrejas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '29', 'Quem enfraquece, que eu também não enfraqueça? Quem se escandaliza, que eu me não abrase?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '30', 'Se convém gloriar-me, gloriar-me-ei no que diz respeito à minha fraqueza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '31', 'O Deus e Pai de Nosso SENHOR Jesus Cristo, que é eternamente bendito, sabe que não minto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '32', 'Em Damasco, o que governava sob o rei Aretas pôs guardas às portas da cidade dos damascenos, para me prenderem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '11', '33', 'E fui descido num cesto por uma janela da muralha; e assim escapei das suas mãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '1', 'EM verdade que não convém gloriar-me; mas passarei às visões e revelações do SENHOR:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '2', 'Conheço um homem em Cristo que há catorze anos (se no corpo não sei, se fora do corpo, não sei; Deus o sabe) foi arrebatado até ao terceiro céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '3', 'E sei que o tal homem (se no corpo, se fora do corpo, não sei; Deus o sabe)');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '4', 'Foi arrebatado ao paraíso; e ouviu palavras inefáveis, de que ao homem não é lícito falar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '5', 'De um assim me gloriarei eu, mas de mim mesmo não me gloriarei, senão nas minhas fraquezas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '6', 'Porque, se quiser gloriar-me, não serei néscio, porque direi a verdade; mas deixo isto, para que ninguém cuide de mim mais do que em mim vê ou de mim ouve.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '7', 'E, para que me não exaltasse pelas excelências das revelações, foi-me dado um espinho na carne, a saber, um mensageiro de Satanás para me esbofetear, a fim de me não exaltar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '8', 'Acerca do qual três vezes orei ao SENHOR para que se desviasse de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '9', 'E disse-me: A minha graça te basta, porque o meu poder se aperfeiçoa na fraqueza. De boa vontade pois me gloriarei nas minhas fraquezas, para que em mim habite o poder de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '10', 'Pelo que sinto prazer nas fraquezas, nas injúrias, nas necessidades, nas perseguições, nas angústias por amor de Cristo. Porque quando estou fraco então sou forte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '11', 'Fui néscio em gloriar-me: vós me constrangestes; porque eu devia ser louvado por vós, visto que em nada fui inferior aos mais excelentes apóstolos; ainda que nada sou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '12', 'Os sinais do meu apostolado foram manifestos entre vós com toda a paciência, por sinais, prodígios e maravilhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '13', 'Porque, em que tendes vós sido inferiores às outras igrejas, a não ser que eu mesmo vos não fui pesado? Perdoai-me este agravo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '14', 'Eis aqui estou pronto para pela terceira vez ir ter convosco, e não vos serei pesado, pois que não busco o que é vosso, mas sim a vós; porque não devem os filhos entesourar para os pais, mas os pais para os filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '15', 'Eu de muito boa vontade gastarei, e me deixarei gastar pelas vossas almas, ainda que, amando-vos cada vez mais, seja menos amado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '16', 'Mas seja assim: eu não vos fui pesado, mas, sendo astuto, vos tomei com dolo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '17', 'Porventura aproveitei-me de vós por algum daqueles que vos enviei?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '18', 'Roguei a Tito, e enviei com ele um irmão. Porventura Tito se aproveitou de vós? Não andámos porventura no mesmo espírito, sobre as mesmas pisadas?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '19', 'Cuidais que ainda nos desculpamos convosco? Falamos em Cristo perante Deus, e tudo isto, ó amados, para vossa edificação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '20', 'Porque receio que, quando chegar, vos não ache como eu quereria, e eu seja achado de vós como não quereríeis: que de alguma maneira haja pendência, invejas, iras, porfias, detracções, mexericos, orgulhos, tumultos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '12', '21', 'Que, quando for outra vez, o meu Deus me humilhe para convosco, e chore por muitos daqueles que dantes pecaram, e não se arrependeram da imundície, e prostituição, e desonestidade que cometeram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '1', 'É ESTA a terceira vez que vou ter convosco. Por boca de duas ou três testemunhas será confirmada toda a palavra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '2', 'Já anteriormente o disse, e segunda vez o digo como quando estava presente; mas agora, estando ausente, o digo aos que antes pecaram e a todos os mais, que, se outra vez for, não lhes perdoarei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '3', 'Visto que buscais uma prova de Cristo que fala em mim, o qual não é fraco para convosco, antes é poderoso entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '4', 'Porque, ainda que foi crucificado por fraqueza, vive contudo pelo poder de Deus. Porque nós também somos fracos nele, mas viveremos em vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '5', 'Examinai-vos a vós mesmos, se permaneceis na fé; provai-vos a vós mesmos. Ou não sabeis quanto a vós mesmos, que Jesus Cristo está em vós? Se não é que já estais reprovados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '6', 'Mas espero que entendereis que nós não somos reprovados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '7', 'Ora eu rogo a Deus que não façais mal algum, não para que sejamos achados aprovados, mas para que vós façais o bem, embora nós sejamos como reprovados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '8', 'Porque nada podemos contra a verdade, senão pela verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '9', 'Porque nos regozijamos de estar fracos, quando vós estais fortes; e o que desejamos é a vossa perfeição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '10', 'Portanto, escrevo estas coisas estando ausente, para que, estando presente, não use de rigor, segundo o poder que o SENHOR me deu para edificação, e não para destruição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '11', 'Quanto ao mais, irmãos, regozijai-vos, sede perfeitos, sede consolados, sede de um mesmo parecer, vivei em paz; e o Deus de amor e de paz será convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '12', 'Saudai-vos uns aos outros com ósculo santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '13', 'Todos os santos vos saúdam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('8', '13', '14', 'A graça do SENHOR Jesus Cristo, e o amor de Deus, e a comunhão do Espírito Santo seja com todos vós. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '1', '¶ PAULO apóstolo (não da parte dos homens, nem por homem algum, mas por Jesus Cristo, e por Deus Pai, que o ressuscitou dos mortos),');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '2', 'E todos os irmãos que estão comigo, às igrejas da Galácia:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '3', 'Graça e paz da parte de Deus Pai e da de nosso SENHOR Jesus Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '4', 'O qual se deu a si mesmo por nossos pecados, para nos livrar do presente século mau, segundo a vontade de Deus nosso Pai,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '5', 'Ao qual glória para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '6', 'Maravilho-me de que tão depressa passásseis daquele que vos chamou à graça de Cristo para outro evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '7', 'O qual não é outro, mas há alguns que vos inquietam e querem transtornar o evangelho de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '8', 'Mas, ainda que nós mesmos ou um anjo do céu vos anuncie outro evangelho além do que já vos tenho anunciado, seja anátema.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '9', 'Assim como já vo-lo dissemos, agora de novo também vo-lo digo. Se alguém vos anunciar outro evangelho além do que já recebestes, seja anátema.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '10', 'Porque, persuado eu agora a homens ou a Deus? Ou procuro agradar a homens? Se estivesse ainda agradando aos homens, não seria servo de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '11', 'Mas faço-vos saber, irmãos, que o evangelho que por mim foi anunciado não é segundo os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '12', 'Porque não o recebi, nem aprendi de homem algum, mas pela revelação de Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '13', 'Porque já ouvistes qual foi antigamente a minha conduta no judaísmo como sobremaneira perseguia a igreja de Deus e a assolava.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '14', 'E na minha nação excedia em judaísmo a muitos da minha idade, sendo extremamente zeloso das tradições dos meus pais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '15', 'Mas, quando aprouve a Deus, que desde o ventre de minha mãe me separou, e me chamou pela sua graça,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '16', 'Revelar seu filho em mim, para que o pregasse entre os gentios, não consultei a carne nem o sangue,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '17', 'Nem tornei a Jerusalém, a ter com os que já antes de mim eram apóstolos, mas parti para a Arábia, e voltei outra vez a Damasco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '18', 'Depois, passados três anos, fui a Jerusalém para ver a Pedro, e fiquei com ele quinze dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '19', 'E não vi a nenhum outro dos apóstolos, senão a Tiago, irmão do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '20', 'Ora, acerca do que vos escrevo, eis que diante de Deus testifico que não minto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '21', 'Depois fui para as partes da Síria e da Cilícia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '22', 'E não era conhecido de vista das igrejas da Judeia, que estavam em Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '23', 'Mas somente tinham ouvido dizer: Aquele que já nos perseguiu anuncia agora a fé que antes destruía.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '1', '24', 'E glorificavam a Deus a respeito de mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '1', 'DEPOIS, passados catorze anos, subi outra vez a Jerusalém com Barnabé, levando também comigo Tito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '2', 'E subi por uma revelação, e lhes expus o Evangelho, que prego entre os gentios, e particularmente aos que estavam em estima: para que de maneira alguma não corresse ou não tivesse corrido em vão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '3', 'Mas nem ainda Tito, que estava comigo, sendo grego, foi constrangido a circuncidar-se;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '4', 'E isto por causa dos falsos irmãos que se tinham entremetido, e secretamente entraram a espiar a nossa liberdade, que temos em Cristo Jesus, para nos porem em servidão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '5', 'Aos quais nem ainda por uma hora cedemos com sujeição, para que a verdade do evangelho permanecesse entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '6', 'E, quanto àqueles que pareciam ser alguma coisa (quais tenham sido noutro tempo, não se me dá; Deus não aceita a aparência, do homem), esses, digo, que pareciam ser alguma coisa, nada me comunicaram,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '7', 'Antes, pelo contrário, quando viram que o evangelho da incircuncisão me estava confiado, como a Pedro o da circuncisão');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '8', '(Porque aquele que operou eficazmente em Pedro para o apostolado da circuncisão esse operou também em mim com eficácia para com os gentios),');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '9', 'E conhecendo Tiago, Cefas e João, que eram considerados como as colunas, a graça que se me havia dado, deram-nos as destras, em comunhão comigo e com Barnabé, para que, nós fôssemos aos gentios, e eles à circuncisão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '10', 'Recomendando-nos somente que nos lembrássemos dos pobres: o que também procurei fazer com diligência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '11', 'E, chegando Pedro a Antioquia, lhe resisti na cara, porque era repreensível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '12', 'Porque, antes que alguns tivessem chegado da parte de Tiago, comia com os gentios; mas, depois que chegaram, se foi retirando, e se apartou deles, temendo os que eram da circuncisão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '13', 'E os outros judeus também dissimulavam com ele, de maneira que até Barnabé se deixou levar pela sua dissimulação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '14', 'Mas, quando vi que não andavam bem e directamente conforme a verdade do evangelho, disse a Pedro na presença de todos: Se tu sendo judeu, vives como os gentios, e não como judeu, porque obrigas os gentios a viverem como judeus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '15', 'Nós somos judeus por natureza e não pecadores de entre os gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '16', 'Sabendo que o homem não é justificado pelas obras da lei, mas pela fé em Jesus Cristo, temos também crido em Jesus Cristo, para sermos justificados pela fé de Cristo, e não pelas obras da lei; porquanto pelas obras da lei nenhuma carne será justificada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '17', 'Pois, se nós, que procuramos ser justificados em Cristo, nós mesmos também somos achados pecadores, é porventura Cristo ministro do pecado? De maneira nenhuma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '18', 'Porque se torno a edificar aquilo que destruí, constituo-me a mim mesmo transgressor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '19', 'Porque eu pela lei estou morto para a lei, para viver para Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '20', 'Já estou crucificado com Cristo; e vivo, não mais eu, mas Cristo vive em mim; e a vida que eu agora vivo na carne vivo-a na fé do Filho de Deus, o qual me amou, e se entregou a si mesmo por mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '2', '21', 'Não aniquilo a graça de Deus; porque, se a justiça provém da lei, segue-se que Cristo morreu debalde.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '1', 'INSENSATOS gálatas! Quem vos fascinou para não obedecerdes à verdade, a vós, perante os olhos de quem Jesus Cristo foi já representado como crucificado?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '2', 'Só quisera saber isto de vós: recebestes o Espírito pelas abras da lei ou pela pregação da fé?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '3', 'Sois vós tão insensatos que, tendo começado pelo Espírito, acabeis agora pela carne?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '4', 'Será em vão que tenhais padecido tanto? Se é que isso também foi em vão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '5', 'Aquele pois que vos dá o Espírito e que obra maravilhas entre vós, fá-lo pelas obras da lei, ou pela pregação da fé?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '6', 'Assim como Abraão creu em Deus, e isso lhe foi imputado como justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '7', 'Sabei pois que os que são da fé são filhos de Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '8', 'Ora, tendo a Escritura previsto que Deus havia de justificar pela fé os gentios, anunciou primeiro o evangelho a Abraão, dizendo: Todas as nações serão benditas em ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '9', 'De sorte que os que são da fé são benditos com o crente Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '10', 'Todos aqueles pois que são das obras da lei estão debaixo da maldição; porque escrito está: Maldito todo aquele que não permanecer em todas as coisas que estão escritas no livro da lei, para fazê-las.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '11', 'E é evidente que pela lei ninguém será justificado diante de Deus, porque o justo viverá da fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '12', 'Ora a lei não é da fé; mas o homem, que fizer estas coisas, por elas viverá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '13', 'Cristo nos resgatou da maldição da lei, fazendo-se maldição por nós; porque está escrito: Maldito todo aquele que for pendurado no madeiro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '14', 'Para que a bênção de Abraão chegasse aos gentios por Jesus Cristo, e para que pela fé nós recebamos a promessa do Espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '15', 'Irmãos, como homem falo; se o testamento de um homem for confirmado, ninguém o anula nem o acrescenta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '16', 'Ora as promessas foram feitas a Abraão e à sua posteridade. Não diz: E às posteridades como falando de muitas, mas como de uma só: E à tua posteridade, que é Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '17', 'Mas digo isto: Que tendo sido o testamento anteriormente confirmado por Deus, a lei, que veio quatrocentos e trinta anos depois, não o invalida, de forma a abolir a promessa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '18', 'Porque, se a herança provém da lei, já não provém da promessa; mas Deus pela promessa a deu gratuitamente a Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '19', 'Logo, para que é a lei? Foi ordenada por causa das transgressões, até que viesse a posteridade a quem a promessa tinha sido feita; e foi posta pelos anjos na mão de um medianeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '20', 'Ora o medianeiro não o é de um só, mas Deus é um.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '21', 'Logo, a lei é contra as promessas de Deus? De nenhuma sorte; porque, se dada fosse uma lei que pudesse vivificar, a justiça, na verdade, teria sido pela lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '22', 'Mas a Escritura encerrou tudo debaixo do pecado, para que a promessa pela fé em Jesus Cristo fosse dada aos crentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '23', 'Mas, antes que a fé viesse, estávamos guardados debaixo da lei, e encerrados para aquela fé que se havia de manifestar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '24', 'De maneira que a lei nos serviu de aio, para nos conduzir a Cristo, para que pela fé fôssemos justificados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '25', 'Mas, depois que a fé veio, já não estamos debaixo de aio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '26', 'Porque todos sois filhos de Deus pela fé em Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '27', 'Porque todos quantos fostes baptizados em Cristo já vos revestistes de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '28', 'Nisto não há judeu nem grego; não há servo nem livre; não há macho nem fêmea; porque todos vós sois um em Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '3', '29', 'E, se sois de Cristo, então sois descendência de Abraão, e herdeiros conforme a promessa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '1', 'DIGO, pois, que todo o tempo que o herdeiro é menino em nada difere do servo, ainda que seja Senhor de tudo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '2', 'Mas está debaixo de tutores e curadores até ao tempo determinado pelo pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '3', 'Assim também nós, quando éramos meninos, estávamos reduzidos à servidão debaixo dos primeiros rudimentos do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '4', 'Mas, vindo a plenitude dos tempos, Deus enviou seu Filho, nascido de mulher, nascido sob a lei,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '5', 'Para remir os que estavam debaixo da lei, a fim de recebermos a adopção de filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '6', 'E, porque sois filhos, Deus enviou aos nossos corações o Espírito de seu filho, que clama: Aba, Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '7', 'Assim que já não és mais servo, mas filho; e, se és filho, és também herdeiro de Deus por Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '8', 'Mas, quando não conhecíeis a Deus, servíeis aos que por natureza não são deuses.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '9', 'Mas agora, conhecendo a Deus, ou, antes, sendo conhecidos de Deus, como tornais outra vez a esses rudimentos fracos e pobres, aos quais de novo quereis servir?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '10', 'Guardais dias, e meses, e tempos, e anos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '11', 'Receio de vós, que não haja trabalhado em vão para convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '12', 'Irmãos, rogo-vos que sejais como eu, porque também eu sou como vós; nenhum mal me fizestes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '13', 'E vós sabeis que primeiro vos anunciei o Evangelho estando em fraqueza da carne;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '14', 'E não rejeitastes, nem desprezastes isso que era uma tentação na minha carne, antes me recebestes como um anjo de Deus, como Jesus Cristo mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '15', 'Qual é, logo, a vossa bem-aventurança? Porque vos dou testemunho de que, se possível fora, arrancaríeis os vossos olhos, e mos daríeis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '16', 'Fiz-me acaso vosso inimigo, dizendo a verdade?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '17', 'Eles têm bom zelo por vós, não como convém; mas querem excluir-vos, para que vós tenhais zelo por eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '18', 'É bom ser zeloso, mas sempre do bem, e não somente quando estou presente convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '19', 'Meus filhinhos, por quem de novo sinto as dores de parto, até que Cristo seja formado em vós;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '20', 'Eu bem quisera agora estar presente convosco, e mudar a minha voz; porque estou perplexo a vosso respeito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '21', 'Dizei-me os que quereis estar debaixo da lei, não ouvis vós a lei?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '22', 'Porque está escrito que Abraão teve dois filhos, um da escrava, e outro da livre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '23', 'Todavia o que era da escrava nasceu segundo a carne, mas, o que era da livre, por promessa,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '24', 'O que se entende por alegoria; porque estes são os dois concertos; um, do monte Sinai, gerando filhos para a servidão, que é Agar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '25', 'Ora esta Agar é Sinai, um monte da Arábia, que corresponde à Jerusalém que agora existe, pois é escrava com seus filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '26', 'Mas a Jerusalém que é de cima é livre; a qual é mãe de todos nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '27', 'Porque está escrito: Alegra-te, estéril, que não dás à luz: esforça-te e clama, tu que não estás de parto; porque os filhos da solitária são mais do que os da que tem marido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '28', 'Mas nós, irmãos, somos filhos da promessa como Isaque.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '29', 'Mas, como então aquele que era gerado segundo a carne perseguia o que era segundo o Espírito, assim é também agora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '30', 'Mas que diz a Escritura? Lança fora a escrava e seu filho, porque de modo algum o filho da escrava herdará com o filho da livre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '4', '31', 'De maneira que, irmãos, somos filhos, não da escrava, mas da livre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '1', 'ESTAI pois firmes na liberdade com que Cristo nos libertou, e não torneis a meter-vos debaixo do jugo da servidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '2', 'Eis que eu, Paulo, vos digo que, se vos deixardes circuncidar, Cristo de nada vos aproveitará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '3', 'E de novo protesto a todo o homem, que se deixa circuncidar, que está obrigado a guardar toda a lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '4', 'Separados estais de Cristo, vós os que vos justificais pela lei: da graça tendes caído.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '5', 'Porque nós pelo espírito da fé aguardamos a esperança da justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '6', 'Porque em Jesus Cristo nem a circuncisão nem a incircuncisão tem virtude alguma; mas sim a fé que opera por caridade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '7', 'Corríeis bem; quem vos impediu, para que não obedeçais à verdade?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '8', 'Esta persuasão não vem daquele que vos chamou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '9', 'Um pouco de fermento leveda toda a massa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '10', 'Confio de vós, no SENHOR, que nenhuma outra coisa sentireis; mas aquele que vos inquieta, seja ele quem for, sofrerá a condenação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '11', 'Eu porém, irmãos, se prego ainda a circuncisão, porque sou pois perseguido? Logo o escândalo da cruz está aniquilado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '12', 'Eu quereria que fossem cortados aqueles que vos andam inquietando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '13', 'Porque vós, irmãos, fostes chamados à liberdade. Não useis então da liberdade para dar ocasião à carne, mas servi-vos uns aos outros pela caridade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '14', 'Porque toda a lei se cumpre numa só palavra, nesta: Amarás ao teu próximo como a ti mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '15', 'Se vós, porém, vos mordeis e devorais uns aos outros, vede não vos consumais também uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '16', 'Digo, porém: Andai em Espírito, e não cumprireis a concupiscência da carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '17', 'Porque a carne cobiça contra o Espírito, e o Espírito contra a carne; e estes opõem-se um ao outro; para que não façais o que quereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '18', 'Mas, se sois guiados pelo Espírito, não estais debaixo da lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '19', 'Porque as obras da carne são manifestas, as quais são: prostituição, impureza, lascívia,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '20', 'Idolatria, feitiçarias, inimizades, porfias, emulações, iras, pelejas, dissensões, heresias,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '21', 'Invejas, homicídios, bebedices, glutonarias, e coisas semelhantes a estas, acerca das quais declaro, como já antes vos disse, que os que cometem tais coisas não herdarão o reino de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '22', 'Mas o fruto do Espírito é: caridade, gozo, paz, longanimidade, benignidade, bondade, fé, mansidão, temperança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '23', 'Contra estas coisas não há lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '24', 'E os que são de Cristo crucificaram a carne com as suas paixões e concupiscências.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '25', 'Se vivemos em Espírito, andemos também em Espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '5', '26', 'Não sejamos cobiçosos de vanglórias, irritando-nos uns aos outros, invejando-nos uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '1', 'IRMÃOS, se algum homem chegar a ser surpreendido nalguma ofensa, vós, que sois espirituais, encaminhai o tal com espírito de mansidão; olhando por ti mesmo, para que não sejas também tentado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '2', 'Levai as cargas uns dos outros, e assim cumprireis a lei de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '3', 'Porque, se alguém cuida ser alguma coisa, não senda nada, engana-se a si mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '4', 'Mas prove cada um a sua própria obra, e terá glória só em si mesmo, e não noutro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '5', 'Porque cada qual levará a sua própria carga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '6', 'E o que é instruído na palavra reparta de todos os seus bens com aquele que o instrui.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '7', 'Não erreis: Deus não se deixa escarnecer; porque tudo o que o homem semear, isso também ceifará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '8', 'Porque o que semeia na sua carne, da carne ceifará a corrupção; mas o que semeia no Espírito, do Espírito ceifará a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '9', 'E não nos cansemos de fazer bem, porque a seu tempo ceifaremos, se não houvermos desfalecido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '10', 'Então, enquanto temos tempo, façamos bem a todos, mas principalmente aos domésticos da fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '11', 'Vede com que grandes letras vos escrevi por minha mão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '12', 'Todos os que querem mostrar boa aparência na carne, esses vos obrigam a circuncidar-vos, somente para não serem perseguidos por causa da cruz de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '13', 'Porque nem ainda esses mesmos que se circuncidam guardam a lei, mas querem que vos circuncideis, para se gloriarem na vossa carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '14', 'Mas longe esteja de mim gloriar-me, a não ser na cruz de nosso SENHOR Jesus Cristo, pela qual o mundo está crucificado para mim e eu para o mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '15', 'Porque em Cristo Jesus nem a circuncisão nem a incircuncisão tem virtude alguma, mas sim o ser uma nova criatura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '16', 'E a todos quantos andarem conforme esta regra, paz e misericórdia sobre eles e sobre o Israel de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '17', 'Desde agora ninguém me inquiete; porque trago no meu corpo as marcas do SENHOR Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('9', '6', '18', 'A graça de nosso SENHOR Jesus Cristo seja, irmãos, com o vosso espírito. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '1', '¶ PAULO, apóstolo de Jesus Cristo, pela vontade de Deus, aos santos que estão em Éfeso, e fiéis em Cristo Jesus:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '2', 'A vós graça, e paz da parte de Deus nosso Pai e da do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '3', 'Bendito o Deus e Pai de nosso SENHOR Jesus Cristo, o qual nos abençoou com todas as bênçãos espirituais nos lugares celestiais em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '4', 'Como também nos elegeu nele antes da fundação do mundo, para que fôssemos santos e irrepreensíveis diante dele em caridade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '5', 'E nos predestinou para filhos de adopção por Jesus Cristo, para si mesmo, segundo o benefício da sua vontade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '6', 'Para louvor e glória da sua graça, pela qual nos fez agradáveis a si no Amado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '7', 'Em quem temos a redenção pelo seu sangue, a remissão das ofensas, segundo as riquezas da sua graça,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '8', 'Que Ele fez abundar para connosco em toda a sabedoria e prudência;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '9', 'Descobrindo-nos o mistério da sua vontade, segundo o seu beneplácito, que propusera em si mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '10', 'De tornar a congregar em Cristo todas as coisas, na dispensação da plenitude dos tempos, tanto as que estão nos céus como as que estão na terra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '11', 'Nele, digo, em quem também fomos feitos herança, havendo sido predestinados, conforme o propósito daquele que faz todas as coisas, segundo o conselho da sua vontade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '12', 'Com o fim de sermos para louvor da sua glória, nós, os que primeiro esperámos em Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '13', 'Em quem também vós estais, depois que ouvistes a palavra da verdade, o evangelho da vossa salvação; e, tendo nele também crido, fostes selados com o Espírito Santo da promessa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '14', 'O qual é o penhor da nossa herança, para redenção da possessão de Deus, para louvor da sua glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '15', 'Pelo que, ouvindo eu também a fé que entre vós há no SENHOR Jesus, e a vossa caridade para com todos os santos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '16', 'Não cesso de dar graças a Deus por vós, lembrando-me de vós nas minhas orações;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '17', 'Para que o Deus de nosso SENHOR Jesus Cristo, o Pai da glória, vos dê em seu conhecimento o espírito de sabedoria e de revelação;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '18', 'Tendo iluminado os olhos do vosso entendimento, para que saibais qual seja a esperança da sua vocação, e quais as riquezas da glória da sua herança nos santos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '19', 'E qual a sobre excelente grandeza do seu poder sobre nós, os que cremos, segundo a operação da força do seu poder,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '20', 'Que manifestou em Cristo, ressuscitando-o dos mortos, e pondo-o à sua direita nos céus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '21', 'Acima de todo o principado, e poder, e potestade, e domínio, e de todo o nome que se nomeia, não só neste século, mas também no vindouro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '22', 'E sujeitou todas as coisas a seus pés, e sobre todas as coisas o constituiu como cabeça da igreja,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '1', '23', 'Que é o seu corpo, a plenitude daquele que cumpre tudo em todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '1', 'E VOS vivificou estando vós mortos em ofensas e pecados,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '2', 'Em que noutro tempo andastes segundo o curso deste mundo, segundo o príncipe das potestades do ar, do espírito que agora opera nos filhos da desobediência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '3', 'Entre os quais todos nós também antes andávamos nos desejos da nossa carne, fazendo a vontade da carne e dos pensamentos; e éramos por natureza filhos da ira, como os outros também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '4', 'Mas Deus, que é riquíssimo em misericórdia, pelo seu muito amor com que nos amou,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '5', 'Estando nós ainda mortos em nossas ofensas, nos vivificou juntamente com Cristo (pela graça sois salvos).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '6', 'E nos ressuscitou juntamente com ele, e nos fez assentar nos lugares celestiais, em Cristo Jesus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '7', 'Para prostrar nos séculos vindouros as abundantes riquezas da sua graça, pela sua benignidade para connosco em Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '8', 'Porque pela graça sois salvos, por meio da fé; e isto não vem de vós; é dom de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '9', 'Não vem das obras, para que ninguém se glorie.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '10', 'Porque somos feitura sua, criados em Cristo Jesus para as boas obras, as quais Deus preparou para que andássemos nelas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '11', 'Portanto, lembrai-vos de que vós noutro tempo éreis gentios na carne e chamados incircuncisão pelos que na carne se chamam circuncisão feita pela mão dos homens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '12', 'Que naquele tempo estáveis sem Cristo, separados da comunidade de Israel, e estranhos aos concertos da promessa, não tendo esperança, e sem Deus no mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '13', 'Mas agora em Cristo Jesus, vós, que antes estáveis longe, já, pelo sangue de Cristo chegastes perto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '14', 'Porque ele é a nossa paz, o qual de ambos os povos fez um; e, derribando a parede de separação que estava no meio,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '15', 'Na sua carne desfez a inimizade, isto é, a lei dos mandamentos, que consistia em ordenanças, para dos dois criar em si mesmo um novo homem, fazendo a paz,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '16', 'E pela cruz reconciliar ambos com Deus em um corpo, matando com ela as inimizades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '17', 'E, vindo, ele evangelizou a paz, a vós que estáveis longe, e aos que estavam perto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '18', 'Porque por ele ambos temos acesso ao Pai em um mesmo Espírito,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '19', 'Assim que já não sois estrangeiros, nem forasteiros, mas concidadãos dos santos, e da família de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '20', 'Edificados sobre o fundamento dos apóstolos e dos profetas, de que Jesus Cristo é a principal pedra da esquina;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '21', 'No qual todo o edifício, bem ajustado, cresce para templo santo no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '2', '22', 'No qual também vós juntamente sois edificados para morada de Deus em Espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '1', 'POR esta causa eu, Paulo, sou o prisioneiro de Jesus Cristo por vós, os gentios;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '2', 'Se é que tendes ouvido a dispensação da graça de Deus, que para convosco me foi dada;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '3', 'Como me foi este mistério manifestado pela revelação como acima em pouco vos escrevi;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '4', 'Pelo que, quando lerdes, podeis perceber a minha compreensão do mistério de Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '5', 'O qual noutros séculos não foi manifestado aos filhos dos homens, como agora tem sido revelado pelo Espírito aos seus santos apóstolos e profetas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '6', 'A saber, que os gentios são co-herdeiros, e de um mesmo corpo, e participantes da promessa em Cristo pelo evangelho;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '7', 'Do qual fui feito ministro, pelo dom da graça de Deus, que me foi dado segundo a operação do seu poder.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '8', 'A mim, o mínimo de todos os santos, me foi dada esta graça de anunciar entre os gentios, por meio do evangelho, as riquezas incompreensíveis de Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '9', 'E demonstrar a todos qual seja a dispensação do mistério, que desde os séculos esteve oculto em Deus, que tudo criou;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '10', 'Para que agora, pela igreja, a multiforme sabedoria de Deus seja conhecida dos principados e potestades nos céus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '11', 'Segundo o eterno propósito que fez em Cristo Jesus nosso SENHOR,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '12', 'No qual temos ousadia e acesso com confiança, pela nossa fé nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '13', 'Portanto vos peço que não desfaleçais nas minhas tribulações por vós que são a vossa glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '14', 'Por causa disto me ponho de joelhos perante o Pai de nosso SENHOR Jesus Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '15', 'Do qual toda a família nos céus e na terra toma o nome,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '16', 'Para que, segundo as riquezas da sua glória, vos conceda que sejais corroborados com poder pelo seu Espírito no homem interior;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '17', 'Para que Cristo habite pela fé nos vossos corações; a fim de, estando arraigados e fundados em amor,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '18', 'Poderdes perfeitamente compreender, com todos os santos, qual seja a largura, e o comprimento, e a altura, e a profundidade,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '19', 'E conhecer o amor de Cristo, que excede todo o entendimento, para que sejais cheios de toda a plenitude de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '20', 'Ora, àquele que é poderoso para fazer tudo muito mais abundantemente além daquilo que pedimos ou pensamos, segundo o poder que em nós opera;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '3', '21', 'A esse glória na igreja, por Jesus Cristo, em todas as gerações, para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '1', 'ROGO-VOS, pois, eu, o preso do SENHOR, que andeis como é digno da vocação com que fostes chamados,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '2', 'Com toda a humildade e mansidão, com longanimidade, suportando-vos uns aos outros em amor,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '3', 'Procurando guardar a unidade do Espírito pelo vinculo da paz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '4', 'Há um só corpo e um só Espirito, como também fostes chamados em uma só esperança da vossa vocação;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '5', 'Um só Senhor, uma só fé, um só baptismo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '6', 'Um só Deus e Pai de todos, o qual é sobre todos, e por todos e em todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '7', 'Mas a graça foi dada a cada um de nós segundo a medida do dom de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '8', 'Pelo que diz: Subindo ao alto, levou cativo o cativeiro, e deu dons aos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '9', 'Ora, isto - ele subiu - que é, senão que também antes tinha descido às partes mais baixas da terra?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '10', 'Aquele que desce é também o mesmo que subiu acima de todos os céus, para cumprir todas as coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '11', 'E ele mesmo deu uns para apóstolos, e outros para profetas, e outros para evangelistas, e outros para pastores e doutores,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '12', 'Querendo o aperfeiçoamento dos santos, para a obra do ministério, para edificação do corpo de Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '13', 'Até que todos cheguemos à unidade da fé, e ao conhecimento do Filho de Deus, a varão perfeito, à medida da estatura completa de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '14', 'Para que não sejamos mais meninos inconstantes, levados em roda por todo o vento de doutrina, pelo engano dos homens que com astúcia enganam fraudulosamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '15', 'Antes, seguindo a verdade em caridade, cresçamos em tudo naquele que é a cabeça, Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '16', 'Do qual todo o corpo, bem ajustado, e ligado pelo auxílio de todas as juntas, segundo a justa operação de cada parte, faz o aumento do corpo, para sua edificação em amor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '17', 'E digo isto, e testifico no SENHOR, para que não andeis mais como andam também os outros gentios, na vaidade do seu sentido,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '18', 'Entenebrecidos no entendimento separados da vida de Deus pela ignorância que há neles, pela dureza do seu coração;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '19', 'Os quais, havendo perdido todo o sentimento, se entregaram à dissolução, para com avidez cometerem toda a impureza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '20', 'Mas vós não aprendestes assim a Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '21', 'Se é que o tendes ouvido, e nele fostes ensinados, como está a verdade em Jesus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '22', 'Que, quanto ao trato passado, vos despojeis do velho homem, que se corrompe pelas concupiscências do engano;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '23', 'E vos renoveis no espírito do vosso sentido;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '24', 'E vos revistais do novo homem, que segundo Deus é criado em verdadeira justiça e santidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '25', 'Pelo que deixai a mentira, e falai a verdade, cada um com o seu próximo; porque somos membros uns dos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '26', 'Irai-vos, e não pequeis; não se ponha o sol sobre a vossa ira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '27', 'Não deis lugar ao diabo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '28', 'Aquele que furtava, não furte mais; antes trabalhe, fazendo com as mãos o que é bom, para que tenha que repartir com o que tiver necessidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '29', 'Não saia da vossa boca nenhuma palavra torpe, mas só a que for boa para promover a edificação, para que dê graça aos que a ouvem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '30', 'E não entristeçais o Espírito Santo de Deus, no qual estais selados para o dia da redenção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '31', 'Toda a amargura, e ira, e cólera, e gritaria, e blasfémia e toda a malícia seja tirada de entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '4', '32', 'Antes sede uns para com os outros benignos, misericordiosos, perdoando-vos uns aos outros, como também Deus vos perdoou em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '1', 'SEDE pois imitadores de Deus, como filhos amados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '2', 'E andai em amor, como também Cristo vos amou, e se entregou a si mesmo por nós, em oferta e sacrifício a Deus, em cheiro suave.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '3', 'Mas a prostituição, e toda a impureza ou avareza, nem ainda se nomeie entre vós, como convém a santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '4', 'Nem torpezas, nem parvoíces, nem chocarrices, que não convêm; mas antes acções de graças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '5', 'Porque bem sabeis isto: que nenhum devasso, ou impuro, ou avarento, o qual é idólatra, tem herança no reino de Cristo e de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '6', 'Ninguém vos engane com palavras vãs; porque por estas coisas vem a ira de Deus sobre os filhos da desobediência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '7', 'Portanto não sejais seus companheiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '8', 'Porque noutro tempo éreis trevas, mas agora sois luz no SENHOR: andai como filhos da luz');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '9', '(Porque o fruto do Espírito está em toda a bondade, e justiça e verdade);');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '10', 'Aprovando o que é agradável ao SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '11', 'E não comuniqueis com as obras infrutuosas das trevas, mas antes condenai-as.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '12', 'Porque o que eles fazem em oculto até dizê-lo é torpe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '13', 'Mas todas estas coisas se manifestam, sendo condenadas, pela luz, porque a luz tudo manifesta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '14', 'Pelo que diz: Desperta, tu que dormes, e levanta-te de entre os mortos, e Cristo te esclarecerá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '15', 'Portanto, vede prudentemente como andais, não como néscios, mas como sábios,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '16', 'Remindo o tempo; porquanto os dias são maus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '17', 'Pelo que não sejais insensatos, mas entendei qual seja a vontade do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '18', 'E não vos embriagueis com vinho, em que há contenda, mas enchei-vos do Espirito;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '19', 'Falando entre vós em salmos, e hinos, e cânticos espirituais: cantando e salmodiando ao SENHOR no vosso coração;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '20', 'Dando sempre graças por tudo ao nosso Deus e Pai, em nome de nosso SENHOR Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '21', 'Sujeitando-vos uns aos outros no temor de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '22', 'Vós, mulheres, sujeitai-vos a vossos maridos, como ao SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '23', 'Porque o marido é a cabeça da mulher, como também Cristo é a cabeça da igreja, sendo ele próprio o salvador do corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '24', 'De sorte que, assim como a igreja está sujeita a Cristo, assim também as mulheres sejam em tudo sujeitas a seus maridos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '25', 'Vós, maridos, amai vossas mulheres, como também Cristo amou a igreja, e a si mesmo se entregou por ela,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '26', 'Para a santificar, purificando-a com a lavagem da água, pela palavra,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '27', 'Para a apresentar a si mesmo igreja gloriosa, sem mácula, nem ruga, nem coisa semelhante, mas santa e irrepreensível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '28', 'Assim devem os maridos amar a suas próprias mulheres, como a seus próprios corpos. Quem ama a sua mulher, ama-se a si mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '29', 'Porque nunca ninguém aborreceu a sua própria carne; antes a alimenta e sustenta, como também o SENHOR à igreja;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '30', 'Porque somos membros do seu corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '31', 'Por isso deixará o homem seu pai e sua mãe, e se unirá a sua mulher; e serão dois numa carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '32', 'Grande é este mistério: digo-o porém a respeito de Cristo e da igreja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '5', '33', 'Assim também vós cada um em particular ame a sua própria mulher como a si mesmo, e a mulher reverencie o marido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '1', 'VÓS, filhos, sede obedientes a vossos pais no SENHOR, porque isto é justo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '2', 'Honra a teu pai e a tua mãe, que é o primeiro mandamento com promessa;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '3', 'Para que te vá bem, e vivas muito tempo sobre a terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '4', 'E vós, pais, não provoqueis a ira a vossos filhos, mas criai-os na doutrina e admoestação do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '5', 'Vós, servos, obedecei a vossos senhores segundo a carne, com temor e tremor, na sinceridade de vosso coração, como a Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '6', 'Não servindo à vista, como para agradar aos homens, mas como servos de Cristo, fazendo de coração a vontade de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '7', 'Servindo de boa vontade como ao SENHOR, e não como aos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '8', 'Sabendo que cada um receberá do SENHOR todo o bem que fizer, seja servo, seja livre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '9', 'E vós senhores, fazei o mesmo para com eles, deixando as ameaças, sabendo também que o SENHOR deles e vosso está no céu, e que para com ele não há acepção de pessoas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '10', 'No demais, irmãos meus, fortalecei-vos no SENHOR e na força do seu poder.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '11', 'Revesti-vos de toda a armadura de Deus, para que possais estar firmes contra as astutas ciladas do diabo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '12', 'Porque não temos que lutar contra a carne e o sangue, mas sim contra os principados, contra as potestades, contra os príncipes das trevas deste século, contra as hostes espirituais da maldade, nos lugares celestiais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '13', 'Portanto tomai toda a armadura de Deus, para que possais resistir no dia mau, e, havendo feito tudo, ficar firmes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '14', 'Estai pois firmes, tendo cingidos os vossos lombos com a verdade, e vestida a couraça da justiça;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '15', 'E calçados os pés na preparação do evangelho da paz,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '16', 'Tomando sobretudo o escudo da fé, com o qual podereis apagar todos os dardos inflamados do maligno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '17', 'Tomai também o capacete da salvação, e espada do Espírito, que é a palavra de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '18', 'Orando em todo o tempo com toda a oração e súplica no Espírito, e vigiando nisto com toda a perseverança e súplica por todos os santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '19', 'E por mim; para que me seja dada, no abrir da minha boca, a palavra com confiança, para fazer notório o mistério do evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '20', 'Pelo qual sou embaixador em cadeias; para que possa falar dele livremente, como me convém falar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '21', 'Ora; para que vós também possais saber dos meus negócios, e o que eu faço, Tíquico, irmão amado, e fiel ministro do SENHOR, vos informará de tudo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '22', 'O qual vos enviei para o mesmo fim, para que saibais do nosso estado, e ele console os vossos corações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '23', 'Paz seja com os irmãos, e caridade com fé da parte de Deus Pai e da do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('10', '6', '24', 'A graça seja com todos os que amam a nosso SENHOR Jesus Cristo em sinceridade. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '1', '¶ PAULO e Timóteo, servos de Jesus Cristo, a todos os santos em Cristo Jesus, que estão em Filipos, com os bispos e diáconos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '2', 'Graça a vós, e paz da parte de Deus nosso Pai e da do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '3', 'Dou graças ao meu Deus todas as vezes que me lembro de vós,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '4', 'Fazendo sempre com alegria oração por vós em todas as minhas súplicas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '5', 'Pela vossa cooperação no evangelho desde o primeiro dia até agora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '6', 'Tendo por certo isto mesmo, que aquele que em vós começou a boa obra a aperfeiçoará até ao dia de Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '7', 'Como tenho por justo sentir isto de vós todos, porque vos retenho em meu coração, pois todos vós fostes participantes da minha graça, tanto nas minhas prisões como na minha defesa e confirmação do evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '8', 'Porque Deus me é testemunha das saudades que de todos vós tenho, em entranhável afeição de Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '9', 'E peço isto: que a vossa caridade abunde mais e mais em ciência e em todo o conhecimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '10', 'Para que aproveis as coisas excelentes, para que sejais sinceros, e sem escândalo algum até ao fim de Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '11', 'Cheios de frutos de justiça, que são por Jesus Cristo, para glória e louvor de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '12', 'E quero, irmãos, que saibais que as coisas que me aconteceram contribuíram para maior proveito do evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '13', 'De maneira que as minhas prisões em Cristo foram manifestadas por toda a guarda pretoriana, e por todos os demais lugares;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '14', 'E muitos dos irmãos no SENHOR, tomando ânimo com as minhas prisões, ousam falar a palavra mais confiadamente, sem temor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '15', 'Verdade é que também alguns pregam a Cristo por inveja e porfia, mas outros de boamente;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '16', 'Uns por amor, sabendo que fui posto para defesa do evangelho,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '17', 'Mas outros, na verdade, anunciam a Cristo por contenção, não puramente, julgando acrescentar aflição às minhas prisões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '18', 'Mas que importa? Contanto que Cristo seja anunciado de toda a maneira, ou com fingimento ou em verdade, nisto me regozijo, e me regozijarei ainda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '19', 'Porque sei que disto me resultará salvação, pela vossa oração e pelo socorro do Espírito de Jesus Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '20', 'Segundo a minha intensa expectação e esperança, de que em nada serei confundido; antes, com toda a confiança, Cristo será, tanto agora como sempre, engrandecido no meu corpo, seja pela vida, seja pela morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '21', 'Porque para mim o viver é Cristo, e o morrer é ganho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '22', 'Mas, se o viver na carne me der fruto da minha obra, não sei então o que deva escolher.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '23', 'Mas de ambos os lados estou em aperto, tendo desejo de partir, e estar com Cristo, porque isto é ainda muito melhor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '24', 'Mas julgo mais necessário, por amor de vós, ficar na carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '25', 'E, tendo esta confiança, sei que ficarei, e permanecerei com todos vós para proveito vosso e gozo da fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '26', 'Para que a vossa glória abunde por mim em Cristo Jesus, pela minha nova ida a vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '27', 'Somente deveis portar-vos dignamente conforme o evangelho de Cristo, para que, quer vá e vos veja, quer esteja ausente, ouça acerca de vós que estais num mesmo espírito, combatendo juntamente com o mesmo ânimo pela fé do evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '28', 'E em nada vos espanteis, dos que resistem, o que para eles, na verdade, é indício de perdição, mas para vós de salvação, e isto, de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '29', 'Porque a vós vos foi concedido, em relação a Cristo, não somente crer nele, como também padecer por ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '1', '30', 'Tendo o mesmo combate que já em mim tendes visto e agora ouvis estar em mim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '1', 'PORTANTO, se há algum conforto em Cristo, se alguma consolação de amor, se alguma comunhão no Espírito, se alguns entranháveis afectos e compaixões,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '2', 'Completai o meu gozo, para que sintais o mesmo, tendo o mesmo amor, o mesmo ânimo, sentindo uma mesma coisa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '3', 'Nada façais por contenda ou por vanglória, mas por humildade; cada um considere os outros superiores a si mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '4', 'Não atente cada um para o que é propriamente seu, mas cada qual também para o que é dos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '5', 'De sorte que haja em vós o mesmo sentimento que houve também em Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '6', 'Que, sendo em forma de Deus, não teve por usurpação ser igual a Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '7', 'Mas aniquilou-se a si mesmo, tomando a forma de servo, fazendo-se semelhante aos homens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '8', 'E, achado na forma de homem, humilhou-se a si mesmo, sendo obediente até à morte, e morte de cruz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '9', 'Pelo que também Deus o exaltou soberanamente, e lhe deu um nome que é sobre todo o nome;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '10', 'Para que ao nome de Jesus se dobre todo o joelho dos que estão nos céus, e na terra, e debaixo da terra,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '11', 'E toda a língua confesse que Jesus Cristo é o SENHOR, para glória de Deus Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '12', 'De sorte que, meus amados, assim como sempre obedecestes, não só na minha presença, mas muito mais agora na minha ausência, assim também operai a vossa salvação com temor e tremor;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '13', 'Porque Deus é o que opera em vós tanto o querer como o efectuar, segundo a sua boa vontade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '14', 'Fazei todas as coisas sem murmurações nem contendas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '15', 'Para que sejais irrepreensíveis e sinceros, filhos de Deus, inculpáveis no meio de uma geração corrompida e perversa, entre a qual resplandeceis como astros no mundo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '16', 'Retendo a palavra da vida, para que no dia de Cristo possa gloriar-me de não ter corrido nem trabalhado em vão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '17', 'E, ainda que seja oferecido por libação sobre o sacrifício e serviço da vossa fé, folgo e me regozijo com todos vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '18', 'E vós também regozijai-vos e alegrai-vos comigo por isto mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '19', 'E espero no SENHOR Jesus que em breve vos mandarei Timóteo, para que também eu esteja de bom animo sabendo dos vossos negócios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '20', 'Porque a ninguém tenho de igual sentimento, que sinceramente cuide do vosso estado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '21', 'Porque todos buscam o que é seu, e não o que é de Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '22', 'Mas bem sabeis qual a sua experiência, e que serviu comigo no evangelho, como filho ao pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '23', 'De sorte que espero enviar-vo-lo logo que tenha provido a meus negócios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '24', 'Mas confio no SENHOR, que também eu mesmo em breve irei ter convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '25', 'Julguei, contudo, necessário mandar-vos Epafrodito, meu irmão, e cooperador, e companheiro nos combates e vosso enviado para prover às minhas necessidades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '26', 'Porquanto tinha muitas saudades de vós todos, e estava muito angustiado de que tivésseis ouvido que ele estivera doente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '27', 'E de facto esteve doente, e quase à morte; mas Deus se apiedou dele e não somente dele, mas também de mim, para que eu não tivesse tristeza sobre tristeza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '28', 'Por isso vo-lo enviei mais depressa, para que vendo-o outra vez, vos regozijeis, e eu tenha menos tristeza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '29', 'Recebei-o pois no SENHOR com todo o gozo, e tende-o em honra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '2', '30', 'Porque pela obra de Cristo chegou até bem próximo da morte, não fazendo caso da vida para suprir para comigo a falta do vosso serviço.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '1', 'RESTA, irmãos meus, que vos regozijeis no SENHOR. Não me aborreço de escrever-vos as mesmas coisas, e é segurança para vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '2', 'Guardai-vos dos cães, guardai-vos dos maus obreiros, guardai-vos da circuncisão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '3', 'Porque a circuncisão somos nós, que servimos a Deus em espírito, e nos gloriamos em Jesus Cristo, e não confiamos na carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '4', 'Ainda que também podia confiar na carne: se algum outro cuida que pode confiar na carne, ainda mais eu:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '5', 'Circuncidado ao oitavo dia, da linhagem de Israel, da tribo de Benjamim, hebreu de hebreus; segundo a lei, fui fariseu;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '6', 'Segundo o zelo, perseguidor da igreja, segundo a justiça que há na lei, irrepreensível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '7', 'Mas o que para mim era ganho reputei-o perda por Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '8', 'E, na verdade, tenho também por perda todas as coisas, pela excelência do conhecimento de Cristo Jesus, meu Senhor; pelo qual sofri a perda de todas estas coisas, e as considero como esterco, para que possa ganhar a Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '9', 'E seja achado nele, não tendo a minha justiça que vem da lei, mas a que vem pela fé em Cristo, a saber, a justiça que vem de Deus pela fé;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '10', 'Para conhecê-lo, e à virtude da sua ressurreição, e à comunicação de suas aflições, sendo feito conforme à sua morte;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '11', 'Para ver se de alguma maneira posso chegar à ressurreição dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '12', 'Não que já a tenha alcançado, ou que seja perfeito; mas prossigo para alcançar aquilo para o que fui também preso por Cristo Jesus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '13', 'Irmãos, quanto a mim, não julgo que o haja alcançado; mas uma coisa faço e é que, esquecendo-me das coisas que atrás ficam, e avançando para as que estão adiante de mim,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '14', 'Prossigo para o alvo, pelo prémio da soberana vocação de Deus em Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '15', 'Pelo que todos quantos já somos perfeitos sintamos isto mesmo; e, se sentis alguma coisa de outra maneira, também Deus vo-lo revelará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '16', 'Mas, naquilo a que já chegámos, andemos segundo a mesma regra, e sintamos o mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '17', 'Sede também meus imitadores, irmãos, e tende cuidado, segundo o exemplo que tendes em nós, pelos que assim andam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '18', 'Porque muitos há, dos quais muitas vezes vos disse, e agora também digo, chorando, que são inimigos da cruz de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '19', 'Cujo fim é a perdição; cujo Deus é o ventre; e cuja glória é para confusão deles, que só pensam nas coisas terrenas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '20', 'Mas a nossa cidade está nos céus, donde também esperamos o Salvador, o SENHOR Jesus Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '3', '21', 'Que transformará o nosso corpo abatido, para ser conforme o seu corpo glorioso, segundo o seu eficaz poder de sujeitar também a si todas as coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '1', 'PORTANTO, meus amados e mui queridos irmãos, minha alegria e coroa, estai assim firmes no SENHOR, amados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '2', 'Rogo a Evódia, e rogo a Síntique, que sintam o mesmo no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '3', 'E peço-te também a ti, meu verdadeiro companheiro, que ajudes essas mulheres que trabalharam comigo no evangelho, e com Clemente, e com os outros cooperadores, cujos nomes estão no livro da vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '4', 'Regozijai-vos sempre no SENHOR; outra vez digo, regozijai-vos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '5', 'Seja a vossa equidade notória a todos os homens. Perto está o SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '6', 'Não estejais inquietos por coisa alguma: antes as vossas petições sejam em tudo conhecidas diante de Deus pela oração e súplicas, com acção de graças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '7', 'E a paz de Deus, que excede todo o entendimento, guardará os vossos corações e os vossos sentimentos em Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '8', 'Quanto ao mais, irmãos, tudo o que é verdadeiro, tudo o que é honesto, tudo o que é justo, tudo o que é puro, tudo o que é amável, tudo o que é de boa fama, se há alguma virtude, e se há algum louvor, nisso pensai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '9', 'O que também aprendestes, e recebestes, e ouvistes, e vistes em mim, isso fazei; e o Deus de paz será convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '10', 'Ora muito me regozijei no SENHOR por finalmente reviver a vossa lembrança de mim, pois já vos tínheis lembrado; mas não tínheis tido oportunidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '11', 'Não digo isto como por necessidade, porque já aprendi a contentar-me com o que tenho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '12', 'Sei estar abatido, e sei também ter abundância; em toda a maneira, e em todas as coisas estou instruído, tanto a ter fartura, como a ter fome; tanto a ter abundância, como a padecer necessidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '13', 'Posso todas as coisas naquele que me fortalece.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '14', 'Todavia fizestes bem em tomar parte na minha aflição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '15', 'E bem sabeis também vós, ó filipenses, que, no princípio do evangelho, quando parti da Macedónia, nenhuma igreja comunicou comigo com respeito a dar e a receber, senão vós somente;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '16', 'Porque também uma e outra vez me mandastes o necessário a Tessalónica.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '17', 'Não que procure dádivas, mas procuro o fruto que abunde para a vossa conta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '18', 'Mas bastante tenho recebido, e tenho abundância: cheio estou, depois que recebi de Epafrodito o que da vossa parte me foi enviado, como cheiro de suavidade e sacrifício agradável e aprazível a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '19', 'O meu Deus, segundo as suas riquezas, suprirá todas as vossas necessidades em glória, por Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '20', 'Ora a nosso Deus e Pai seja dada glória para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '21', 'Saudai a todos os santos em Cristo Jesus. Os irmãos que estão comigo vos saúdam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '22', 'Todos os santos vos saúdam, mas principalmente os que são da casa de César.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('11', '4', '23', 'A graça de nosso SENHOR Jesus Cristo seja com vós todos. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '1', '¶ PAULO, apóstolo de Jesus Cristo; pela vontade de Deus, e o irmão Timóteo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '2', 'Aos santos e irmãos fiéis em Cristo, que estão em Colossos. Graça a vós, e paz da parte de Deus nosso Pai e da do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '3', 'Graças damos a Deus, Pai de nosso SENHOR Jesus Cristo, orando sempre por vós,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '4', 'Porquanto ouvimos da vossa fé em Cristo Jesus, e da caridade que tendes para com todos os santos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '5', 'Por causa da esperança que vos está reservada nos céus, da qual já antes ouvistes pela palavra da verdade do evangelho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '6', 'Que já chegou a vós, como também está em todo o mundo; e já vai frutificando, como também entre vós, desde o dia em que ouvistes e conhecestes a graça de Deus em verdade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '7', 'Como aprendestes de Epafras, nosso amado conservo, que para vós é um fiel ministro de Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '8', 'O qual nos declarou também a vossa caridade no Espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '9', 'Por esta razão, nós também, desde o dia em que o ouvimos, não cessámos de orar por vós, e de pedir que sejais cheios do conhecimento da sua vontade, em toda a sabedoria e inteligência espiritual;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '10', 'Para que possais andar dignamente diante do SENHOR, agradando-lhe em tudo, frutificando em toda a boa obra, e crescendo no conhecimento de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '11', 'Corroborando em toda a fortaleza, segundo a força da sua glória, em toda a paciência, e longanimidade com gozo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '12', 'Dando graças ao Pai que nos fez idóneos para participar da herança dos santos na luz;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '13', 'O qual nos tirou da potestade das trevas, e nos transportou para o reino do Filho do seu amor;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '14', 'Em que temos a redenção pelo seu sangue, a saber, a remissão dos pecados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '15', 'O qual é a imagem do Deus invisível, o primogénito de toda a criação;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '16', 'Porque nele foram criadas todas as coisas que há nos céus e na terra, visíveis e invisíveis, sejam tronos, sejam dominações, sejam principados, sejam potestades: tudo foi criado por ele e para ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '17', 'E ele é antes de todas as coisas e todas as coisas subsistem por ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '18', 'E ele é a cabeça do corpo da igreja: é o princípio e o primogénito de entre os mortos, para que em tudo tenha a preeminência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '19', 'Porque foi do agrado do Pai que toda a plenitude nele habitasse,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '20', 'E que, havendo por ele feito a paz pelo sangue da sua cruz, por meio dele reconciliasse consigo mesmo todas as coisas, tanto as que estão na terra, como as que estão nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '21', 'A vós também, que noutro tempo éreis estranhos e inimigos no entendimento pelas vossas obras más, agora contudo vos reconciliou');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '22', 'No corpo da sua carne, pela morte, para perante ele vos apresentar santos, e irrepreensíveis, e inculpáveis,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '23', 'Se, na verdade, permanecerdes fundados e firmes na fé, e não vos moverdes da esperança do evangelho que tendes ouvido, o qual foi pregado a toda a criatura que há debaixo do céu, e do qual eu, Paulo, estou feito ministro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '24', 'Regozijo-me agora no que padeço por vós, e na minha carne cumpro o resto das aflições de Cristo, pelo seu corpo, que é a igreja;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '25', 'Da qual eu estou feito ministro segundo a dispensação de Deus, que me foi concedida para convosco, para cumprir a palavra de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '26', 'O mistério que esteve oculto desde todos os séculos e em todas as gerações, e que agora foi manifestado aos seus santos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '27', 'Aos quais Deus quis fazer conhecer quais são as riquezas da glória deste mistério entre os gentios, que é Cristo em vós, esperança da glória;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '28', 'A quem anunciamos, admoestando a todo o homem, e ensinando a todo o homem em toda a sabedoria; para que apresentemos todo o homem perfeito em Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '1', '29', 'E para isto também trabalho, combatendo segundo a sua eficácia, que obra em mim poderosamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '1', 'PORQUE quero que saibais quão grande combate tenho por vós, e pelos que estão em Laodiceia, e por quantos não viram o meu rosto em carne;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '2', 'Para que os seus corações sejam consolados, e estejam unidos em caridade, e enriquecidos da plenitude da inteligência, para conhecimento do mistério de Deus, Cristo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '3', 'Em quem estão escondidos todos os tesouros da sabedoria e da ciência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '4', 'E digo isto, para que ninguém vos engane com palavras persuasivas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '5', 'Porque ainda que esteja ausente quanto ao corpo, contudo em espírito estou convosco, regozijando-me, e vendo a vossa ordem, e a firmeza da vossa fé em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '6', 'Como, pois, recebestes o SENHOR, Jesus Cristo, assim também andai nele,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '7', 'Arraigados e sobreedificados nele, e confirmados na fé, assim como fostes ensinados, abundando em acção de graças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '8', 'Tende cuidado, para que ninguém vos faça presa sua, por meio de filosofia e vãs subtilezas, segundo a tradição dos homens, segundo os rudimentos do mundo, e não segundo Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '9', 'Porque nele habita corporalmente toda a plenitude da divindade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '10', 'E estais perfeitos nele, que é a cabeça de todo o principado e potestade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '11', 'No qual também estais circuncidados com a circuncisão não feita por mão no despojo do corpo da carne: a circuncisão de Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '12', 'Sepultados com ele no baptismo, nele também ressuscitastes pela fé no poder de Deus, que o ressuscitou dos mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '13', 'E, quando vós estáveis mortos nos pecados, e na incircuncisão da vossa carne, vos vivificou juntamente com ele, perdoando-vos todas as ofensas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '14', 'Havendo riscado a cédula que era contra nós nas suas ordenanças, a qual de alguma maneira nos era contrária, e a tirou do meio de nós, cravando-a na cruz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '15', 'E, despojando os principados e potestades, os expôs publicamente e deles triunfou em si mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '16', 'Portanto ninguém vos julgue pelo comer, ou pelo beber, ou por causa dos dias de festa, ou da lua nova, ou dos sábados,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '17', 'Que são sombras das coisas futuras, mas o corpo é de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '18', 'Ninguém vos domine a seu belprazer com pretexto de humilhação e culto dos anjos, metendo-se em coisas que não viu; estando debalde inchado na sua carnal compreensão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '19', 'E não ligado à cabeça, da qual todo o corpo, provido e organizado pelas juntas e ligaduras, vai crescendo em aumento de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '20', 'Se, pois, estais mortos com Cristo quanto aos rudimentos do mundo, porque vos carregam ainda as ordenaças como se vivêsseis no mundo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '21', 'Tais como: Não toques, não proves, não manuseies?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '22', 'As quais coisas todas perecem pelo uso, segundo os preceitos e doutrinas dos homens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '2', '23', 'As quais têm, na verdade, alguma aparência de sabedoria, em devoção voluntária, humildade, e em disciplina do corpo, mas não são de valor algum senão para a satisfação da carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '1', 'PORTANTO, se já ressuscitastes com Cristo, buscai as coisas que são de cima, onde Cristo está assentado a dextra de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '2', 'Pensai nas coisas que são de cima, e não nas que são da terra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '3', 'Porque já estais mortos, e a vossa vida está escondida com Cristo em Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '4', 'Quando Cristo, que é a nossa vida, se manifestar, então também vós vos manifestareis com ele em glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '5', 'Mortificai pois os vossos membros, que estão sobre a terra: a prostituição, a impureza, o apetite desordenado, a vil concupiscência e a avareza, que é idolatria;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '6', 'Pelas quais coisas vem a ira de Deus sobre os filhos da desobediência;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '7', 'Nas quais também em outro tempo andastes, quando vivíeis nelas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '8', 'Mas agora despojai-vos também de tudo: da ira, da cólera, da malícia, da maledicência, das palavras torpes da vossa boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '9', 'Não mintais uns aos outros, pois que já vos despistes do velho homem com os seus feitos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '10', 'E vos vestistes do novo, que se renova para o conhecimento, segundo a imagem daquele que o criou;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '11', 'Onde não há grego nem judeu, circuncisão nem incincuncisão, bárbaro, cita, servo ou livre; mas Cristo é tudo em todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '12', 'Revesti-vos pois, como eleitos de Deus, santos, e amados, de entranhas de misericórdia, de benignidade, humildade, mansidão, longanimidade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '13', 'Suportando-vos uns aos outros, e perdoando-vos uns aos outros, se algum tiver queixa contra outro: assim como Cristo vos perdoou, assim fazei vós também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '14', 'E, sobre tudo isto, revesti-vos de caridade, que é o vínculo da perfeição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '15', 'E a paz de Deus, para a qual também fostes chamados em um corpo, domine em vossos corações; e sede agradecidos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '16', 'A palavra de Cristo habite em vós abundantemente, em toda a sabedoria, ensinando-vos e admoestando-vos uns aos outros, com salmos, hinos e cânticos espirituais; cantando ao SENHOR com graça em vosso coração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '17', 'E, quanto fizerdes por palavra ou por obras, fazei tudo em nome do SENHOR Jesus, dando por ele graça a Deus Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '18', 'Vós, mulheres, estai sujeitas a vossos próprios maridos, como convém no SENHOR,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '19', 'Vós, maridos, amai a vossas mulheres, e não vos irriteis contra elas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '20', 'Vós, filhos, obedecei em tudo a vossos pais; porque isto é agradável ao SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '21', 'Vós, pais, não irriteis a vossos filhos, para que não percam o ânimo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '22', 'Vós, servos, obedecei em tudo a vossos senhores segundo a carne, não servindo só na aparência, como para agradar aos homens, mas em simplicidade de coração, temendo a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '23', 'E, tudo quanto fizerdes, fazei-o de todo o coração, como ao SENHOR, e não aos homens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '24', 'Sabendo que recebereis do SENHOR o galardão da herança, porque a Cristo, o SENHOR, servis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '3', '25', 'Mas quem fizer agravo receberá o agravo que fizer; pois não há acepção de pessoas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '1', 'VÓS, senhores, fazei o que for de justiça e equidade a vossos servos, sabendo que também tendes um Senhor nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '2', 'Perseverai em oração, velando nela com acção de graças;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '3', 'Orando também juntamente por nós, para que Deus nos abra a porta da palavra, a fim de falarmos do mistério de Cristo, pelo qual estou também preso;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '4', 'Para que o manifeste, como me convém falar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '5', 'Andai com sabedoria para com os que estão de fora, remindo o tempo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '6', 'A vossa palavra seja sempre agradável, temperada com sal, para que saibais como vos convém responder, a cada um.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '7', 'Tíquico, irmão amado e fiel ministro, e conservo no SENHOR, vos fará saber o meu estado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '8', 'O qual vos enviei para o mesmo fim, para que saiba do vosso estado e console os vossos corações;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '9', 'Juntamente com Onésimo, amado, e fiel irmão, que é dos vossos; eles vos farão saber tudo o que por aqui se passa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '10', 'Aristarco, que está preso comigo, vos saúda, e Marcos, o sobrinho de Barnabé, acerca do qual já recebestes mandamentos; se ele for ter convosco, recebei-o;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '11', 'E Jesus, chamado Justo: os quais são da circuncisão; são estes unicamente os meus cooperadores no reino de Deus; e para mim têm sido consolação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '12', 'Saúda-vos Epafras, que é dos vossos, servo de Cristo, combatendo sempre por vós em orações, para que vos conserveis firmes, perfeitos e consumados em toda a vontade de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '13', 'Pois eu lhe dou testemunho de que tem grande zelo por vós, e pelos que estão em Laodiceia, e pelos que estão em Hierápolis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '14', 'Saúda-vos Lucas, o médico amado, e Demas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '15', 'Saudai aos irmãos que estão em Laodiceia, e a Ninfa e à igreja que está em sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '16', 'E, quando esta epístola tiver sido lida entre vós, fazei que também o seja na igreja dos laodicenses, e a que veio de Laodiceia lêde-a vós também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '17', 'E dizei a Arquipo: Atenta para o ministério que recebeste no SENHOR, para que o cumpras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('12', '4', '18', 'Saudação de minha mão, de Paulo. Lembrai-vos das minhas prisões: A graça seja convosco. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '1', '1', '¶ PAULO, e Silvano, e Timóteo, à igreja dos tessalonicenses, em Deus, o Pai, e no SENHOR Jesus Cristo: Graça e paz tenhais de Deus nosso Pai e do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '1', '2', 'Sempre damos graças a Deus por vós todos, fazendo menção de vós em nossas orações;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '1', '3', 'Lembrando-nos sem cessar da obra da vossa fé, do trabalho da caridade, e da paciência da esperança em Nosso SENHOR Jesus Cristo, diante de nosso Deus e Pai;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '1', '4', 'Sabendo, amados irmãos, que a vossa eleição é de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '1', '5', 'Porque o nosso evangelho não foi a vós somente em palavras, mas também em poder, e no Espirito Santo, e em muita certeza; como bem sabeis quais fomos entre vós, por amor de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '1', '6', 'E vós fostes feitos nossos imitadores, e do SENHOR, recebendo a palavra em muita tribulação, com gozo do Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '1', '7', 'De maneira que fostes exemplo para todos os fiéis na Macedónia e Acaia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '1', '8', 'Porque por vós soou a palavra do SENHOR, não somente na Macedónia e Acaia, mas também em todos os lugares a vossa fé para com Deus se espalhou, de tal maneira que já dela não temos necessidade de falar coisa alguma;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '1', '9', 'Porque eles mesmos anunciam de nós qual a entrada que tivemos para convosco, e como dos ídolos vos convertestes a Deus, para servir o Deus vivo e verdadeiro,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '1', '10', 'E esperar dos céus a seu Filho, a quem ressuscitou dos mortos, a saber, Jesus, que nos livra da ira futura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '1', 'PORQUE vós mesmos, irmãos, bem sabeis que a nossa entrada para convosco não foi vã;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '2', 'Mas, havendo primeiro padecido, e sido agravados em Filipos, como sabeis, tornámo-nos ousados em nosso Deus, para vos falar o evangelho de Deus com grande combate.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '3', 'Porque a nossa exortação não foi com engano, nem com imundície, nem com fraudulência;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '4', 'Mas, como fomos aprovados de Deus para que o evangelho nos fosse confiado, assim falámos, não como para agradar aos homens, mas a Deus, que prova os nossos corações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '5', 'Porque, como bem sabeis, nunca usámos de palavras lisonjeiras, nem houve um pretexto de avareza; Deus é testemunha;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '6', 'E não buscámos glória dos homens, nem de vós, nem de outros, ainda que podíamos, como apóstolos de Cristo, ser-vos pesados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '7', 'Antes fomos brandos entre vós, como a ama que cria seus filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '8', 'Assim nós, sendo-vos tão afeiçoados, de boa vontade quiséramos comunicar-vos, não somente o evangelho de Deus, mas ainda as nossas próprias almas; porquanto nos éreis muito queridos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '9', 'Porque bem vos lembrais, irmãos, do nosso trabalho e fadiga; pois, trabalhando, noite e dia, para não sermos pesados a nenhum de vós, vos pregámos o evangelho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '10', 'Vós e Deus sois testemunhas de quão santa, e justa, e irrepreensivelmente nos houvemos para convosco, os que crestes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '11', 'Assim como bem sabeis de que modo vos exortávamos e consolávamos, a cada um de vós, como o pai a seus filhos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '12', 'Para que vos conduzísseis dignamente para com Deus, que vos chama para o seu reino e glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '13', 'Pelo que também damos sem cessar graças a Deus, pois, havendo recebido de nós a palavra da pregação de Deus, a recebestes, não como palavra de homens, mas (segundo é, na verdade), como palavra de Deus, a qual também opera em vós, os que crestes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '14', 'Porque vós, irmãos, haveis sido feitos imitadores das igrejas de Deus que na Judeia estão em Jesus Cristo; porquanto também padecestes de vossos próprios concidadãos o mesmo que os judeus lhes fizeram a eles,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '15', 'Os quais também mataram o SENHOR Jesus e os seus próprios profetas, e nos têm perseguido; e não agradam a Deus, e são contrários a todos os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '16', 'E nos impedem de pregar aos gentios as palavras da salvação, a fim de encherem sempre a medida de seus pecados; mas a ira de Deus caiu sobre eles até ao fim;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '17', 'Nós, porém, irmãos, sendo privados de vós por um momento de tempo, de vista, mas não do coração, tanto mais procurámos com grande desejo ver o vosso rosto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '18', 'Pelo que bem quisemos uma e outra vez ir ter convosco, pelo menos eu, Paulo, mas Satanás no-lo impediu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '19', 'Porque, qual é a nossa esperança, ou gozo, ou coroa de glória? Porventura não o sois vós também diante de nosso SENHOR Jesus Cristo em sua vinda?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '2', '20', 'Na verdade vós sois a nossa glória e gozo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '1', 'PELO que, não podendo esperar mais, de boamente quisemos deixar-nos ficar sós em Atenas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '2', 'E enviámos Timóteo, nosso irmão, e ministro de Deus, e nosso cooperador no evangelho de Cristo, para vos confortar e vos exortar acerca da vossa fé;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '3', 'Para que ninguém se comova por estas tribulações; porque vós mesmos sabeis que para isto fomos ordenados,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '4', 'Pois, estando ainda convosco, vos predizíamos que havíamos de ser afligidos, como sucedeu, e vós o sabeis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '5', 'Portanto, não podendo eu também esperar mais, mandei-o saber da vossa fé, temendo que o tentador vos tentasse, e o nosso trabalho viesse a ser inútil.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '6', 'Vindo, porém, agora Timóteo de vós para nós, e trazendo-nos boas novas da vossa fé e caridade, e de como sempre tendes boa lembrança de nós, desejando muito ver-nos, como nós também a vós;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '7', 'Por esta razão, irmãos, ficámos consolados acerca de vós, em toda a nossa aflição e necessidade, pela vossa fé,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '8', 'Porque agora vivemos, se estais firmes no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '9', 'Porque, que acção de graças poderemos dar a Deus por vós, por todo o gozo com que nos regozijamos por vossa causa diante do nosso Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '10', 'Orando abundantemente dia e noite, para que possamos ver o vosso rosto, e supramos o que falta à vossa fé?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '11', 'Ora o mesmo nosso Deus e Pai, e nosso SENHOR Jesus Cristo, encaminhe a nossa viagem para vós,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '12', 'E o SENHOR vos aumente, e faça abundar em caridade uns para com os outros, e para com todos, como também abundamos para convosco;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '3', '13', 'Para confortar os vossos corações, para que sejais irrepreensíveis em santidade diante de nosso Deus e Pai, na vinda de nosso SENHOR Jesus Cristo com todos os seus santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '1', 'FINALMENTE, irmãos, vos rogamos e exortamos no SENHOR Jesus, que, assim como recebestes de nós, de que maneira convém andar e agradar a Deus, assim andai, para que possais progredir cada vez mais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '2', 'Porque vós bem sabeis que mandamentos vos temos dado pelo SENHOR Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '3', 'Porque esta é a vontade de Deus, a vossa santificação: que vos abstenhais da prostituição;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '4', 'Que cada um de vós saiba possuir o seu vaso em santificação e honra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '5', 'Não na paixão de concupiscência, como os gentios, que não conhecem a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '6', 'Ninguém oprima ou engane a seu irmão em negócio algum, porque o SENHOR é vingador de todas estas coisas, como também antes vo-lo dissemos e testificámos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '7', 'Porque não nos chamou Deus para a imundície, mas para a santificação:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '8', 'Portanto, quem despreza isto não despreza ao homem, mas sim a Deus, que nos deu também o seu Espírito Santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '9', 'Quanto, porém, ao amor fraternal, não necessitais de que vos escreva, visto que vós mesmos estais instruídos por Deus que vos ameis uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '10', 'Porque também já assim o fazeis para com todos os irmãos que estão por toda a Macedónia. Exortamo-vos, porém, a que ainda nisto abundeis cada vez mais,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '11', 'E procureis viver quietos, e tratar dos vossos próprios negócios, e trabalhar com vossas próprias mãos, como já vo-lo temos mandado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '12', 'Para que andeis honestamente para com os que estão de fora, e não necessiteis de coisa alguma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '13', 'Não quero, porém, irmãos, que sejais ignorantes acerca dos que já dormem, para que não vos entristeçais, como os demais, que não têm esperança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '14', 'Porque, se cremos que Jesus morreu e ressuscitou, assim também, aos que em Jesus dormem, Deus os tornará a trazer com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '15', 'Dizemo-vos, pois, isto pela palavra do SENHOR: que nós, os que ficarmos vivos para a vinda do SENHOR, não precederemos os que dormem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '16', 'Porque o mesmo SENHOR descerá do céu com alarido, e com voz de arcanjo, e com a trombeta de Deus; e os que morreram em Cristo ressuscitarão primeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '17', 'Depois nós, os que ficarmos vivos, seremos arrebatados juntamente com eles nas nuvens, a encontrar o SENHOR nos ares, e assim estaremos sempre com o SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '4', '18', 'Portanto, consolai-vos uns aos outros com estas palavras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '1', 'MAS, irmãos, acerca dos tempos e das estações, não necessitais de que se vos escreva;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '2', 'Porque vós mesmos sabeis muito bem que o dia do SENHOR virá como o ladrão de noite;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '3', 'Pois que, quando disserem: Há paz e segurança; então lhes sobrevirá repentina destruição, como as dores de parto àquela que está grávida; e de modo nenhum escaparão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '4', 'Mas vós, irmãos, já não estais em trevas, para que aquele dia vos surpreenda como um ladrão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '5', 'Porque todos vós sois filhos da luz e filhos do dia; nós não somos da noite nem das trevas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '6', 'Não durmamos pois, como os demais, mas vigiemos, e sejamos sóbrios,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '7', 'Porque os que dormem dormem de noite, e os que se embebedam, embebedam-se de noite.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '8', 'Mas nós, que somos do dia, sejamos sóbrios, vestindo-nos da couraça da fé e da caridade, e tendo por capacete a esperança da salvação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '9', 'Porque Deus não nos destinou para a ira, mas para a aquisição da salvação, por nosso SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '10', 'Que morreu por nós, para que, quer vigiemos, quer durmamos, vivamos juntamente com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '11', 'Pelo que exortai-vos uns aos outros e edificai-vos uns aos outros, como também o fazeis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '12', 'E rogamo-vos, irmãos, que reconheçais os que trabalham entre vós e que presidem sobre vós no SENHOR, e vos admoestam;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '13', 'E que os tenhais em grande estima e amor, por causa da sua obra. Tende paz entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '14', 'Rogamo-vos também, irmãos, que admoesteis os desordeiros, consoleis os de pouco ânimo, sustenteis os fracos, e sejais pacientes para com todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '15', 'Vede que ninguém dê a outrem mal por mal, mas segui sempre o bem, tanto uns para com os outros, como para com todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '16', 'Regozijai-vos sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '17', 'Orai sem cessar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '18', 'Em tudo dai graças; porque esta é a vontade de Deus em Cristo Jesus para convosco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '19', 'Não extingais o Espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '20', 'Não desprezeis as profecias;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '21', 'Examinai tudo. Retende o bem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '22', 'Abstende-vos de toda a aparência do mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '23', 'E o mesmo Deus de paz vos santifique em tudo; e todo o vosso espírito e alma, e corpo, sejam plenamente conservados irrepreensíveis para a vinda de nosso SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '24', 'Fiel é o que vos chama, o qual também o fará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '25', 'Irmãos, orai por nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '26', 'Saudai a todos os irmãos em ósculo santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '27', 'Pelo SENHOR vos conjuro que esta epístola seja lida a todos os santos irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('13', '5', '28', 'A graça de nosso SENHOR Jesus Cristo seja convosco. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '1', '¶ PAULO, e Silvano, e Timóteo, à igreja dos tessalonicenses, em Deus nosso Pai e no SENHOR Jesus Cristo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '2', 'Graça e paz a vós da parte de Deus nosso Pai, e da do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '3', 'Sempre devemos, irmãos, dar graças a Deus por vós, como é de razão, porque a vossa fé cresce muitíssimo e a caridade de cada um de vós abunda de uns para com os outros,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '4', 'De maneira que nós mesmos nos gloriamos de vós nas igrejas de Deus por causa da vossa paciência e fé, e em todas as vossas perseguições e aflições que suportais;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '5', 'Prova clara do justo juízo de Deus, para que sejais havidos por dignos do reino de Deus, pelo qual também padeceis;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '6', 'Se de facto é justo diante de Deus que dê em paga tribulação aos que vos atribulam,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '7', 'E a vós, que sois atribulados, descanso connosco quando se manifestar o SENHOR Jesus desde o céu com os anjos do seu poder,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '8', 'Como labareda de fogo, tomando vingança dos que não conhecem a Deus e dos que não obedecem ao evangelho de nosso SENHOR Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '9', 'Os quais por castigo padecerão eterna perdição, ante a face do SENHOR e a glória do seu poder,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '10', 'Quando vier para ser glorificado nos seus santos, e para se fazer admirável naquele dia em todos os que crêem (porquanto o nosso testemunho foi crido entre vós).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '11', 'Pelo que também rogamos sempre por vós, para que o nosso Deus vos faça dignos da sua vocação, e cumpra todo o desejo da sua bondade, e a obra da fé com poder;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '1', '12', 'Para que o nome de nosso SENHOR Jesus Cristo seja em vós glorificado, e vós nele, segundo a graça de nosso Deus e do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '1', 'ORA, irmãos, rogamo-vos, pela vinda de nosso SENHOR Jesus Cristo, e pela nossa reunião com ele,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '2', 'Que não vos movais facilmente do vosso entendimento, nem vos perturbeis, quer por espírito, quer por palavra, quer por epístola, como de nós, como se o dia de Cristo estivesse já perto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '3', 'Ninguém de maneira alguma vos engane; porque não será assim sem que antes venha a apostasia, e se manifeste o homem do pecado, o filho da perdição;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '4', 'O qual se opõe, e se levanta contra tudo o que se chama Deus, ou se adora; de sorte que se assentará, como Deus, no templo de Deus, querendo parecer Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '5', 'Não vos lembrais de que estas coisas vos dizia quando ainda estava convosco?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '6', 'E agora vós sabeis o que o detém, para que a seu próprio tempo seja manifestado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '7', 'Porque já o mistério da injustiça opera; somente há um que agora resiste até que do meio seja tirado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '8', 'E então será revelado o iníquo, a quem o SENHOR desfará pelo assopro da sua boca, e aniquilará pelo esplendor da sua vinda;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '9', 'A esse cuja vinda é segundo a eficácia de Satanás, com todo o poder e sinais e prodígios de mentira,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '10', 'E com todo o engano da injustiça para os que perecem, porque não receberam o amor da verdade para se salvarem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '11', 'E por isso Deus lhes enviará a operação do erro, para que creiam a mentira;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '12', 'Para que sejam julgados todos os que não creram a verdade, antes tiveram prazer na iniquidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '13', 'Mas devemos sempre dar graças a Deus por vós, irmãos amados do SENHOR, por vos ter Deus elegido desde o princípio para a salvação, em santificação do Espírito, e fé da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '14', 'Para o que pelo nosso evangelho vos chamou, para alcançardes a glória de nosso SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '15', 'Então, irmãos, estai firmes e retende as tradições que vos foram ensinadas, seja por palavra, seja por epístola nossa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '16', 'E o próprio nosso SENHOR Jesus Cristo nosso Deus e Pai, que nos amou, e em graça nos deu uma eterna consolação e boa esperança,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '2', '17', 'Console os vossos corações, e vos conforte em toda a boa palavra e obra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '1', 'NO demais, irmãos, rogai por nós, para que a palavra do SENHOR tenha livre curso e seja glorificada, como também o é entre vós;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '2', 'E para que sejamos livres de homens dissolutos e maus; porque a fé não é de todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '3', 'Mas fiel é o SENHOR, que vos confortará, e guardará do maligno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '4', 'E confiamos de vós no SENHOR que não só fazeis, como fareis o que vos mandamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '5', 'Ora o SENHOR encaminhe os vossos corações na caridade de Deus, e na paciência de Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '6', 'Mandamo-vos, porém, irmãos, em nome de nosso SENHOR Jesus Cristo, que vos aparteis de todo o irmão que andar desordenadamente, e não segundo a tradição que de nós recebeu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '7', 'Porque vós mesmos sabeis como convém imitar-nos, pois que não nos houvemos desordenadamente entre vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '8', 'Nem de graça comemos o pão de homem algum, mas com trabalho e fadiga, trabalhando noite e dia, para não sermos pesados a nenhum de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '9', 'Não porque não tivéssemos autoridade, mas para vos dar em nós mesmos exemplo, para nos imitardes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '10', 'Porque, quando ainda estávamos convosco, vos mandámos isto, que, se alguém não quiser trabalhar, não coma também.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '11', 'Porquanto ouvimos que alguns entre vós andam desordenadamente, não trabalhando, antes fazendo coisas vãs.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '12', 'A esses tais, porém, mandamos, e exortamos por nosso SENHOR Jesus Cristo, que, trabalhando com sossego, comam o seu próprio pão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '13', 'E vós, irmãos, não vos canseis de fazer bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '14', 'Mas, se alguém não obedecer à nossa palavra por esta carta, notai o tal, e não vos mistureis com ele, para que se envergonhe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '15', 'Todavia não o tenhais como inimigo, mas admoestai-o como irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '16', 'Ora o mesmo SENHOR da paz vos dê sempre paz de toda a maneira. O SENHOR seja com todos vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '17', 'Saudação da minha própria mão, de mim, Paulo, que é o sinal em todas as epístolas: assim escrevo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('14', '3', '18', 'A graça de nosso SENHOR Jesus Cristo seja com todos vós. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '1', '¶ PAULO, apóstolo de Jesus Cristo segundo o mandado de Deus, nosso Salvador, e do SENHOR Jesus Cristo, esperança nossa,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '2', 'A Timóteo meu verdadeiro filho na fé: graça, misericórdia e paz da parte de Deus nosso Pai e de Cristo Jesus, nosso SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '3', 'Como te roguei, quando parti para a Macedónia, que ficasses em Éfeso, para advertires a alguns, que não ensinem outra doutrina,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '4', 'Nem se dêem a fábulas ou a genealogias intermináveis, que mais produzem questões do que edificação de Deus, que consiste na fé; assim, o faço agora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '5', 'Ora o fim do mandamento é a caridade de um coração puro, e de uma boa consciência, e de uma fé não fingida:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '6', 'Do que desviando-se alguns, se entregaram a vãs contendas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '7', 'Querendo ser doutores da lei, e não entendendo nem o que dizem, nem o que afirmam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '8', 'Sabemos, porém, que a lei é boa, se alguém dela usa legitimamente;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '9', 'Sabendo isto, que a lei não é feita para o justo, mas para os injustos e obstinados, para os ímpios e pecadores, para os profanos e irreligiosos, para os parricidas e matricidas, para os homicidas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '10', 'Para os devassos, para os sodomitas, para os roubadores de homens, para os mentirosos, para as perjuros, e para o que for contrário à sã doutrina,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '11', 'Conforme o evangelho da glória de Deus bem-aventurado, que me foi confiado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '12', 'E dou graças ao que me tem confortado, a Cristo Jesus Senhor nosso, porque me teve por fiel, pondo-me no ministério;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '13', 'A mim, que dantes fui blasfemo, e perseguidor, e opressor; mas alcancei misericórdia, porque o fiz ignorantemente, na incredulidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '14', 'E a graça de nosso SENHOR superabundou com a fé e amor que há em Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '15', 'Esta é uma palavra fiel, e digna de toda a aceitação, que Cristo Jesus veio ao mundo, para salvar os pecadores, dos quais eu sou o principal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '16', 'Mas por isso alcancei misericórdia, para que em mim, que sou o principal, Jesus Cristo mostrasse toda a sua longanimidade, para exemplo dos que haviam de crer nele para a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '17', 'Ora ao Rei dos séculos, imortal, invisível, ao único Deus seja honra e glória para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '18', 'Este mandamento te dou, meu filho Timóteo, que, segundo as profecias que houve acerca de ti, milites por elas boa milícia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '19', 'Conservando a fé, e a boa consciência, rejeitando a qual alguns fizeram naufrágio na fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '1', '20', 'E entre esses foram Himeneu e Alexandre, os quais entreguei a Satanás, para que aprendam a não blasfemar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '1', 'ADMOESTO-TE pois, antes de tudo, que se façam deprecações, orações, intercessões, e acções de graças por todos os homens;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '2', 'Pelos reis, e por todos os que estão em eminência, para que tenhamos uma vida quieta e sossegada, em toda a piedade e honestidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '3', 'Porque isto é bom e agradável diante de Deus nosso Salvador,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '4', 'Que quer que todos os homens se salvem, e venham ao conhecimento da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '5', 'Porque há um só Deus, e um só Mediador entre Deus e os homens, Jesus Cristo homem,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '6', 'O qual se deu a si mesmo em preço de redenção por todos, para servir de testemunho a seu tempo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '7', 'Para o que (digo a verdade em Cristo, não minto) fui constituído pregador, e apóstolo, e doutor dos gentios na fé e na verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '8', 'Quero pois que os homens orem em todo o lugar, levantando mãos santas, sem ira nem contenda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '9', 'Que do mesmo modo as mulheres se ataviem em traje honesto, com pudor e modéstia, não com tranças, ou com ouro, ou pérolas, ou vestidos preciosos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '10', 'Mas (como convém a mulheres que fazem profissão de servir a Deus) com boas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '11', 'A mulher aprenda em silêncio, com toda a sujeição;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '12', 'Não permito, porém, que a mulher ensine, nem use de autoridade sobre o marido, mas que esteja em silêncio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '13', 'Porque primeiro foi formado Adão, depois Eva.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '14', 'E Adão não foi enganado, mas a mulher, sendo enganada, caiu em transgressão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '2', '15', 'Salvar-se-á, porém, dando à luz na fé, na caridade e na santificação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '1', 'ESTA é uma palavra fiel: se alguém deseja o episcopado, excelente obra deseja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '2', 'Convém pois que o bispo seja irrepreensível, marido de uma mulher, vigilante, sério, honesto, hospitaleiro, apto para ensinar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '3', 'Não dado ao vinho, não espancador, não cobiçoso de torpe ganância, mas moderado, não contencioso, não avarento;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '4', 'Que governe bem a sua própria casa, tendo seus filhos em sujeição, com toda a modéstia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '5', '(Porque, se alguém não sabe governar a sua própria casa, terá cuidado da igreja de Deus?)');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '6', 'Não neófito, para que, ensoberbecendo-se, não caia na condenação do diabo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '7', 'Convém também que tenha bom testemunho dos que estão de fora, para que não caia em afronta, e no laço do diabo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '8', 'Da mesma sorte os diáconos sejam honestos, não de língua dobre, não dados a muito vinho, não cobiçosos de torpe ganância;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '9', 'Guardando o mistério da fé em uma pura consciência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '10', 'E também estes sejam primeiro provados, depois sirvam, se forem irrepreensíveis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '11', 'Da mesma sorte as mulheres sejam honestas, não maldizentes, sóbrias e fieis em tudo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '12', 'Os diáconos sejam maridos de uma mulher, e governem bem a seus filhos e suas próprias casas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '13', 'Porque os que servirem bem como diáconos, adquirirão para si uma boa posição, e muita confiança na fé que há em Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '14', 'Escrevo-te estas coisas, esperando ir ver-te bem depressa;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '15', 'Mas, se tardar, para que saibas como convém andar na casa de Deus, que é a igreja do Deus vivo, a coluna e firmeza da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '3', '16', 'E sem dúvida alguma grande é o mistério da piedade: Aquele que se manifestou em carne, foi justificado em espírito, visto dos anjos, pregado aos gentios, crido no mundo, e recebido acima na glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '1', 'MAS o Espírito expressamente diz que nos últimos tempos apostatarão alguns da fé, dando ouvidos a espíritos enganadores, e a doutrinas de demónios;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '2', 'Pela hipocrisia de homens que falam mentiras, tendo cauterizada a sua própria consciência;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '3', 'Proibindo o casamento, e ordenando a abstinência dos manjares que Deus criou para os fiéis, e para os que conhecem a verdade, a fim de usarem deles com acções de graças;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '4', 'Porque toda a criatura de Deus é boa, e não há nada que rejeitar, sendo recebido com acções de graças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '5', 'Porque pela palavra de Deus e pela oração é santificada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '6', 'Propondo estas coisas aos irmãos, serás bom ministro de Jesus Cristo, criado com as palavras da fé e da boa doutrina que tens seguido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '7', 'Mas rejeita as fábulas profanas e de velhas, e exercita-te a ti mesmo em piedade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '8', 'Porque o exercício corporal para pouco aproveita, mas a piedade para tudo é proveitosa, tendo a promessa da vida presente e da que há de vir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '9', 'Esta palavra é fiel e digna de toda a aceitação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '10', 'Porque para isto trabalhamos e lutamos, pois esperamos no Deus vivo, que é o salvador de todos os homens, principalmente dos fiéis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '11', 'Manda estas coisas e ensina-as.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '12', 'Ninguém despreze a tua mocidade: mas sê o exemplo dos fiéis, na palavra, no trato, na caridade, no espírito, na fé, na pureza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '13', 'Persiste em ler, exortar e ensinar, até que eu vá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '14', 'Não desprezes o dom que há em ti, o qual te foi dado por profecia, com a imposição das mãos do presbitério.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '15', 'Medita estas coisas; ocupa-te nelas, para que o teu aproveitamento seja manifesto a todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '4', '16', 'Tem cuidado de ti mesmo e da doutrina: persevera nestas coisas; porque, fazendo isto, te salvarás, tanto a ti mesmo como aos que te ouvem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '1', 'NÃO repreendas asperamente os anciãos, mas admoesta-os como a pais, aos mancebos como a irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '2', 'Às mulheres idosas, como a mães, às moças, como a irmãs, em toda a pureza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '3', 'Honra as viúvas que verdadeiramente são viúvas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '4', 'Mas, se alguma viúva tiver filhos, ou netos, aprendam primeiro a exercer piedade para com a sua própria família, e a recompensar seus pais; porque isto é bom e agradável diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '5', 'Ora a que é verdadeiramente viúva e desamparada espera em Deus, e persevera de noite e de dia em rogos e orações;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '6', 'Mas a que vive em deleites, vivendo está morta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '7', 'Manda, pois, estas coisas, para que elas sejam irrepreensíveis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '8', 'Mas, se alguém não tem cuidado dos seus, e principalmente dos da sua família, negou a fé, e é pior do que o infiel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '9', 'Nunca seja inscrita viúva com menos de sessenta anos, e só a que tenha sido mulher de um só marido;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '10', 'Tendo testemunho de boas obras se criou os filhos, se exercitou hospitalidade, se lavou os pés aos santos, se socorreu os aflitos, se praticou toda a boa obra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '11', 'Mas não admitas as viúvas mais novas, porque quando se tornam levianas contra Cristo, querem casar-se;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '12', 'Tendo já a sua condenação por haverem aniquilado a primeira fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '13', 'E, além disto, aprendem também a andar ociosas de casa em casa; e não só ociosas, mas também paroleiras e curiosas, falando o que não convém.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '14', 'Quero pois que as que são moças se casem, gerem filhos, governem a casa, e não dêem ocasião ao adversário de maldizer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '15', 'Porque já algumas se desviaram, indo após Satanás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '16', 'Se algum crente ou alguma crente tem viúvas, socorra-as, e não se sobrecarregue a igreja, para que se possam sustentar as que deveras são viúvas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '17', 'Os presbíteros que governam bem sejam estimados por dignos de duplicada honra, principalmente os que trabalham na palavra e na doutrina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '18', 'Porque diz a Escritura: Não ligarás a boca ao boi que debulha. E: Digno é o obreiro do seu salário.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '19', 'Não aceites acusação contra o presbítero senão com duas ou três testemunhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '20', 'Aos que pecarem, repreende-os na presença de todos, para que também os outros tenham temor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '21', 'Conjuro-te diante de Deus, e do SENHOR Jesus Cristo, e dos anjos eleitos, que sem prevenção guardes estas coisas, nada fazendo por parcialidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '22', 'A ninguém imponhas precipitadamente as mãos, nem participes dos pecados alheios: conserva-te a ti mesmo puro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '23', 'Não bebas mais água só, mas usa de um pouco de vinho, por causa do teu estômago e das tuas frequentes enfermidades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '24', 'Os pecados de alguns homens são manifestos precedendo o juízo; e em alguns manifestam-se depois.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '5', '25', 'Assim mesmo também as boas obras são manifestas, e as que são doutra maneira não podem ocultar-se.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '1', 'TODOS os servos que estão debaixo do jugo estimem a seus senhores por dignos de toda a honra, para que o nome de Deus e a doutrina não sejam blasfemados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '2', 'E os que têm senhores crentes não os desprezem, por serem irmãos; antes os sirvam melhor, porque eles, que participam do benefício, são crentes e amados. Isto ensina e exorta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '3', 'Se alguém ensina alguma outra doutrina, e se não conforma com as sãs palavras de nosso SENHOR Jesus Cristo, e com a doutrina que é segundo a piedade,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '4', 'É soberbo, e nada sabe, mas delira acerca de questões e contendas de palavras, das quais nascem invejas, porfias, blasfémias, ruins suspeitas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '5', 'Contendas de homens corruptos de entendimento, e privados da verdade, cuidando que a piedade seja causa de ganho: aparta-te dos tais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '6', 'Mas é grande ganho a piedade com contentamento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '7', 'Porque nada trouxemos para este mundo, e manifesto é que nada podemos levar dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '8', 'Tendo, porém, sustento, e com que nos cobrirmos, estejamos com isso contentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '9', 'Mas os que querem ser ricos caem em tentação e em laços, e em muitas concupiscências loucas e nocivas, que submergem os homens na perdição e ruína.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '10', 'Porque o amor do dinheiro é a raiz de toda a espécie de males; e nessa cobiça alguns se desviaram da fé, e se traspassaram a si mesmo com muitas dores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '11', 'Mas tu, ó homem de Deus, foge destas coisas, e segue a justiça, a piedade, a fé, a caridade, a paciência, a mansidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '12', 'Milita a boa milícia da fé, toma posse da vida eterna, para a qual também foste chamado, tendo já feito boa confissão diante de muitas testemunhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '13', 'Mando-te diante de Deus, que todas as coisas vivifica; e de Cristo Jesus, que diante de Pôncio Pilatos deu o testemunho de boa confissão,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '14', 'Que guardes este mandamento sem mácula e repreensão, até à aparição de nosso SENHOR Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '15', 'A qual a seu tempo mostrará o bem-aventurado, e único poderoso SENHOR, Rei dos reis e Senhor dos senhores;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '16', 'Aquele que tem, ele só, a imortalidade, e habita na luz inacessível; a quem nenhum dos homens viu nem pode ver, ao qual seja honra e poder sempiterno. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '17', 'Manda aos ricos deste mundo que não sejam altivos, nem ponham a esperança na incerteza das riquezas, mas em Deus, que abundantemente nos dá todas as coisas para delas gozarmos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '18', 'Que façam bem, enriqueçam em boas obras, repartam de boamente, e sejam comunicáveis;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '19', 'Que entesourem para si mesmos um bom fundamento para o futuro, para que possam alcançar a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '20', 'Ó Timóteo, guarda o depósito que te foi confiado, tendo horror aos clamores vãos e profanos e às oposições da falsamente chamada ciência,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('15', '6', '21', 'A qual professando-a alguns, se desviaram da fé. A graça seja contigo. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '1', '¶ PAULO, apóstolo de Jesus Cristo, pela vontade de Deus, segundo a promessa da vida que está em Cristo Jesus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '2', 'A Timóteo, meu amado filho: graça, misericórdia, e paz da parte de Deus Pai, e da de Cristo Jesus, Senhor nosso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '3', 'Dou graças a Deus, a quem desde os meus antepassados sirvo com uma consciência pura, de que sem cessar faço memória de ti nas minhas orações noite e dia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '4', 'Desejando muito ver-te, lembrando-me das tuas lágrimas, para me encher de gozo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '5', 'Trazendo à memória a fé não fingida que em ti há, a qual habitou primeiro em tua avó Lóide, em tua mãe Eunice, e estou certo de que também habita em ti.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '6', 'Por cujo motivo te lembro que despertes o dom de Deus que existe em ti pela imposição das minhas mãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '7', 'Porque Deus não nos deus o espírito de temor, mas de fortaleza, e de amor, e de moderação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '8', 'Portanto não te envergonhes do testemunho de nosso SENHOR, nem de mim, que sou prisioneiro seu; antes participa das aflições do evangelho segundo o poder de Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '9', 'Que nos salvou, e chamou com uma santa vocação; não segundo as nossas obras, mas segundo o seu próprio propósito e graça, que nos foi dada em Cristo Jesus antes dos tempos dos séculos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '10', 'E que é manifesta agora pela aparição de nosso Salvador Jesus Cristo, o qual aboliu a morte, e trouxe à luz a vida e a incorrupção pelo evangelho;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '11', 'Para o que fui constituído pregador, e apóstolo, e doutor, dos gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '12', 'Por cuja causa padeço também isto, mas não me envergonho; porque eu sei em quem tenho crido, e estou certo de que é poderoso para guardar o meu depósito até àquele dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '13', 'Conserva o modelo das sãs palavras que de mim tens ouvido, na fé e na caridade que há em Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '14', 'Guarda o bom depósito pelo Espírito Santo que habita em nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '15', 'Bem sabes isto, que os que estão na Ásia todos se apartaram de mim; entre os quais foram Figelo e Hermógenes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '16', 'O SENHOR conceda misericórdia à casa de Onesíforo, porque muitas vezes me recriou e não se envergonhou das minhas cadeias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '17', 'Antes, vindo ele a Roma, com muito cuidado me procurou e me achou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '1', '18', 'O SENHOR lhe conceda que naquele dia ache misericórdia diante do SENHOR. E, quanto me ajudou em Éfeso, melhor o sabes tu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '1', 'TU, pois, meu filho, fortifica-te na graça que há em Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '2', 'E o que de mim, entre muitas testemunhas ouvistes, confia-o a homens fiéis, que sejam idóneos para também ensinarem os outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '3', 'Sofre pois, comigo, as aflições como bom soldado de Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '4', 'Ninguém que milita se embaraça com negócios desta vida, a fim de agradar àquele que o alistou para a guerra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '5', 'E, se alguém também milita, não é coroado se não militar legitimamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '6', 'O lavrador que trabalha deve ser o primeiro a gozar dos frutos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '7', 'Considera o que digo, porque o SENHOR te dará entendimento em tudo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '8', 'Lembra-te de que Jesus Cristo, que é da descendência de David, ressuscitou dos mortos, segundo o meu evangelho;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '9', 'Pelo que sofro trabalhos e até prisões, como um malfeitor; mas a palavra de Deus não está presa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '10', 'Portanto tudo sofro por amor dos escolhidos, para que também eles alcancem a salvação que está em Cristo Jesus com glória eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '11', 'Palavra fiel é esta: que, se morrermos com ele, também com ele viveremos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '12', 'Se sofrermos, também com ele reinaremos; se o negarmos, também ele nos negará;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '13', 'Se formos infiéis, ele permanece fiel: não pode negar-se a si mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '14', 'Traz estas coisas à memória ordenando-lhes diante do SENHOR que não tenham contendas de palavras, que para nada aproveitam e são para perversão dos ouvintes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '15', 'Procura apresentar-te a Deus aprovado, como obreiro que não tem de que se envergonhar, que maneja bem a palavra da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '16', 'Mas evita os falatórios profanos, porque produzirão maior impiedade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '17', 'E a palavra desses roerá como gangrena; entre os quais são Himeneu e Fileto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '18', 'Os quais se desviaram da verdade, dizendo que a ressurreição era já feita, e perverteram a fé de alguns.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '19', 'Todavia o fundamento de Deus fica firme, tendo este selo: O SENHOR conhece os que são seus, e qualquer que profere o nome de Cristo aparte-se da iniquidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '20', 'Ora numa grande casa não somente há vasos de ouro e de prata, mas também de pau e de barro; uns para honra, outros, porém, para desonra,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '21', 'De sorte que, se alguém se purificar destas coisas, será vaso para honra, santificado e idóneo para uso do SENHOR, e preparado para toda a boa obra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '22', 'Foge também dos desejos da mocidade; e segue a justiça, a fé, a caridade, e a paz com os que, com um coração puro, invocam o SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '23', 'E rejeita as questões loucas, e sem instrução, sabendo que produzem contendas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '24', 'E ao servo do SENHOR não convém contender, mas sim ser manso para com todos, apto para ensinar, sofredor;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '25', 'Instruindo com mansidão os que resistem, a ver se porventura Deus lhes dará arrependimento para conhecerem a verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '2', '26', 'E tornarem a despertar, desprendendo-se dos laços do diabo, em que à vontade dele estão presos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '1', 'SABE, porém, isto: que nos últimos dias sobrevirão tempos trabalhosos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '2', 'Porque haverá homens amantes de si mesmos, avarentos, presunçosos, soberbos, blasfemos, desobedientes a pais e mães, ingratos, profanos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '3', 'Sem afecto natural, irreconciliáveis, caluniadores, incontinentes, cruéis, sem amor para com os bons.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '4', 'Traidores, obstinados, orgulhosos, mais amigos dos deleites do que amigos de Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '5', 'Tendo aparência de piedade, mas negando a eficácia dela. Destes afasta-te.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '6', 'Porque deste número são os que se introduzem pelas casas, e levam cativas mulheres néscias carregadas de pecados, levadas de várias concupiscências;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '7', 'Que aprendem sempre, e nunca podem chegar ao conhecimento da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '8', 'E, como Janes e Jambres resistiram a Moisés, assim também estes resistem à verdade, sendo homens corruptos de entendimento e réprobos quanto à fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '9', 'Não irão, porém, avante; porque a todos será manifesto o seu desvario, como também o foi o daqueles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '10', 'Tu, porém, tens seguido a minha doutrina, modo de viver, intenção, fé, longanimidade, caridade, paciência,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '11', 'Perseguições, e aflições tais quais me aconteceram em Antioquia, em Icónio, e em Listra: quantas perseguições sofri, e o SENHOR de todas me livrou;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '12', 'E também todos os que piamente querem viver em Cristo Jesus padecerão perseguições.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '13', 'Mas os homens maus e enganadores irão de mal para pior, enganando e sendo enganados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '14', 'Tu, porém, permanece naquilo que aprendeste, e de que foste inteirado, sabendo de quem o tens aprendido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '15', 'E que desde a tua meninice sabes as sagradas letras, que podem fazer-te sábio para a salvação, pela fé que há em Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '16', 'Toda a Escritura divinamente inspirada é proveitosa para ensinar, para redarguir, para corrigir, para instruir em justiça;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '3', '17', 'Para que o homem de Deus seja perfeito, e perfeitamente instruído para toda a boa obra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '1', 'CONJURO-TE pois diante de Deus, e do SENHOR Jesus Cristo, que há de julgar os vivos e os mortos, na sua vinda e no seu reino,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '2', 'Que pregues a palavra, instes a tempo e fora de tempo, redarguas, repreendas, exortes, com toda a longanimidade e doutrina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '3', 'Porque virá tempo em que não sofrerão a sã doutrina; mas, tendo comichão nos ouvidos, amontoarão para si doutores conforme as suas próprias concupiscências;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '4', 'E desviarão os ouvidos da verdade, voltando às fábulas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '5', 'Mas tu sê sóbrio em tudo, sofre as aflições, faz a obra dum evangelista, cumpre o teu ministério.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '6', 'Porque eu já estou sendo oferecido o por aspersão de sacrifício, e o tempo da minha partida está próximo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '7', 'Combati o bom combate, acabei a carreira, guardei a fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '8', 'Desde agora, a coroa da justiça me está guardada, a qual o SENHOR, justo juiz, me dará naquele dia; e não somente a mim, mas também a todos os que amarem a sua vinda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '9', 'Procura vir ter comigo depressa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '10', 'Porque Demas me desamparou, amando o presente século, e foi para Tessalónica, Crescente para Galácia, Tito para Dalmácia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '11', 'Só Lucas está comigo. Toma Marcos, e trá-lo contigo, porque me é muito útil para o ministério.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '12', 'Também enviei Tíquico a Éfeso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '13', 'Quando vieres traz a capa que deixei em Tróade, em casa de Carpo, e os livros, principalmente os pergaminhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '14', 'Alexandre, o latoeiro, causou-me muitos males; o SENHOR lhe pague segundo as suas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '15', 'Tu guarda-te também dele; porque resistiu muito às nossas palavras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '16', 'Ninguém me assistiu na minha primeira defesa, antes todos me desampararam: Que isto lhes não seja imputado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '17', 'Mas o SENHOR assistiu-me e fortaleceu-me, para que por mim fosse cumprida a pregação, e todos os gentios a ouvissem: e fiquei livre da boca do leão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '18', 'E o SENHOR me livrará de toda a má obra, e guardar-me-á para o seu reino celestial; a quem seja dada glória para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '19', 'Saúda a Priscila e a Áquila, e à casa de Onesíforo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '20', 'Erasto ficou em Corinto, e deixei Trófimo doente em Mileto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '21', 'Procura vir antes do inverno. Eubulo, e Prudente, e Lino, e Cláudia, e todos os irmãos te saúdam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('16', '4', '22', 'O SENHOR Jesus Cristo seja com o teu espírito. A graça seja convosco. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '1', '¶ PAULO, servo de Deus, e apóstolo de Jesus Cristo, segundo a fé dos eleitos de Deus, e o conhecimento da verdade, que é segundo a piedade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '2', 'Em esperança da vida eterna, a qual Deus, que não pode mentir, prometeu antes dos tempos dos séculos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '3', 'Mas a seu tempo manifestou a sua palavra pela pregação que me foi confiada segundo o mandamento de Deus; nosso salvador;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '4', 'A Tito, meu verdadeiro filho, segundo a fé comum, graça, misericórdia, e paz da parte de Deus Pai, e da do SENHOR Jesus Cristo, nosso Salvador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '5', 'Por esta causa te deixei em Creta, para que pusesses em boa ordem as coisas que ainda restam, e de cidade em cidade estabelecesses presbíteros, como já te mandei;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '6', 'Aquele que for irrepreensível, marido de uma mulher, que tenha filhos fiéis, que não possam ser acusados de dissolução, nem sejam desobedientes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '7', 'Porque convém que o bispo seja irrepreensível, como dispenseiro de casa de Deus, não soberbo, nem iracundo, nem dado ao vinho, nem espancador, nem cobiçoso de torpe ganância;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '8', 'Mas dado à hospitalidade, amigo do bem, moderado, justo, santo, temperante;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '9', 'Retendo firme a fiel palavra, que é conforme a doutrina, para que seja poderoso, tanto para admoestar com a sã doutrina, como para convencer os contradizentes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '10', 'Porque há muitos desordenados, faladores, vãos e enganadores, principalmente os da circuncisão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '11', 'Aos quais convém tapar a boca; homens que transtornam casas inteiras ensinando o que não convém, por torpe ganância.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '12', 'Um deles, seu próprio profeta, disse: Os cretenses são sempre mentirosos, bestas ruins, ventres preguiçosos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '13', 'Este testemunho é verdadeiro. Portanto repreende-os severamente, para que sejam sãos na fé,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '14', 'Não dando ouvidos às fábulas judaicas, nem aos mandamentos de homens que se desviam da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '15', 'Todas as coisas são puras para os puros, mas nada é puro para os contaminados e infiéis: antes o seu entendimento e consciência estão contaminados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '1', '16', 'Confessam que conhecem a Deus, mas negam-no com as obras, sendo abomináveis, e desobedientes, e reprovados para toda a boa obra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '1', 'TU, porém, fala o que convém à sã doutrina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '2', 'Os velhos que sejam sóbrios, graves, prudentes, sãos na fé, na caridade, e na paciência;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '3', 'As mulheres idosas, semelhantemente, que sejam sérias no seu viver, como convém a santas, não caluniadoras, não dadas a muito vinho, mestras no bem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '4', 'Para que ensinem as mulheres novas a serem prudentes, a amarem seus maridos, a amarem seus filhos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '5', 'A serem moderadas, castas, boas donas de casa, sujeitas a seus maridos, a fim de que a palavra de Deus não seja blasfemada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '6', 'Exorta semelhantemente os mancebos a que sejam moderados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '7', 'Em tudo te dá por exemplo de boas obras; na doutrina mostra incorrupção, gravidade, sinceridade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '8', 'Linguagem sã e irrepreensível, para que o adversário se envergonhe, não tendo nenhum mal que dizer de nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '9', 'Exorta os servos a que se sujeitem a seus senhores, e em tudo agradem, não contradizendo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '10', 'Não defraudando, antes mostrando toda a boa lealdade, para que em tudo sejam ornamento da doutrina de Deus, nosso Salvador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '11', 'Porque a graça de Deus se há manifestado, trazendo salvação a todos os homens,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '12', 'Ensinando-nos que, renunciando à impiedade e às concupiscências mundanas, vivamos neste presente século sóbria, e justa e piamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '13', 'Aguardando a bem-aventurada esperança e o aparecimento da glória do grande Deus e nosso SENHOR, Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '14', 'O qual se deu a si mesmo por nós para nos remir de toda a iniquidade, e purificar para si um povo seu especial, zeloso de boas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '2', '15', 'Fala disto, e exorta e repreende com toda a autoridade. Ninguém te despreze.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '1', 'ADMOESTA-OS a que se sujeitem aos principados e potestades, que lhes obedeçam, e estejam preparados para toda a boa obra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '2', 'Que a ninguém infamem, nem sejam contenciosos, mas modestos, mostrando toda a mansidão para com todos os homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '3', 'Porque também nós éramos noutro tempo insensatos, desobedientes, extraviados, servindo a várias concupiscências e deleites, vivendo em malícia e inveja, odiosos, odiando-nos uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '4', 'Mas quando apareceu a benignidade e caridade de Deus, nosso Salvador, para com os homens,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '5', 'Não pelas obras de justiça que houvéssemos feito, mas segundo a sua misericórdia, nos salvou pela lavagem da regeneração e da renovação do Espírito Santo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '6', 'Que abundantemente ele derramou sobre nós por Jesus Cristo nosso Salvador;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '7', 'Para que, sendo justificados pela sua graça, sejamos feitos herdeiros segundo a esperança da vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '8', 'Fiel é a palavra, e isto quero que deveras afirmes, para que os que crêem em Deus procurem aplicar-se às boas obras; estas coisas são boas e proveitosas aos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '9', 'Mas não entres em questões loucas, genealogias e contendas, e nos debates acerca da lei; porque são coisas inúteis e vãs.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '10', 'Ao homem herege, depois de uma e outra admoestação, evita-o.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '11', 'Sabendo que esse tal está pervertido, e peca, estando já em si mesmo condenado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '12', 'Quando te enviar Ártemas, ou Tíquico, procura vir ter comigo a Nicópolis; porque deliberei invernar ali.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '13', 'Acompanha com muito cuidado Zenas, doutor da lei, e Apolo, para que nada lhes falte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '14', 'E os nossos aprendam também a aplicar-se às boas obras, nas coisas necessárias, para que não sejam infrutuosos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('17', '3', '15', 'Saúdam-te todos os que estão comigo. Saúda tu os que nos amam na fé. A graça seja com vós todos. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '1', '¶ PAULO, prisioneiro de Jesus Cristo, e o irmão Timóteo, ao amado Filémon, nosso cooperador,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '2', 'E à nossa irmã Afia, e a Arquipo, nosso camarada, e à igreja que está em tua casa:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '3', 'Graça a vós e paz da parte de Deus nosso Pai, e da do SENHOR Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '4', 'Graças dou ao meu Deus, lembrando-me sempre de ti nas minhas orações;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '5', 'Ouvindo a tua caridade e a fé que tens para com o SENHOR Jesus Cristo, e para com todos os santos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '6', 'Para que a comunicação da tua fé seja eficaz no conhecimento de todo o bem que em vós há por Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '7', 'Tive grande gozo e consolação da tua caridade, porque por ti, ó irmão, as entranhas dos santos foram recriadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '8', 'Pelo que, ainda que tenha em Cristo grande confiança para te mandar o que te convém,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '9', 'Todavia peço-te antes por caridade, sendo eu tal como sou, Paulo o velho, e também agora prisioneiro de Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '10', 'Peço-te por meu filho Onésimo, que gerei nas minhas prisões;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '11', 'O qual noutro tempo te foi inútil, mas agora a ti e a mim muito útil; eu to tornei a enviar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '12', 'E tu torna a recebê-lo como às minhas entranhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '13', 'Eu bem o quisera conservar comigo, para que por ti me servisse nas prisões do evangelho;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '14', 'Mas nada quis fazer sem o teu parecer, para que o teu benefício não fosse como por força, mas voluntário.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '15', 'Porque bem pode ser que ele se tenha separado de ti por algum tempo, para que o retivesses para sempre,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '16', 'Não já como servo, antes, mais do que servo, como irmão amado, particularmente de mim: e quanto mais de ti, assim na carne como no SENHOR?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '17', 'Assim pois, se me tens por companheiro, recebe-o como a mim mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '18', 'E, se te fez algum dano, ou te deve alguma coisa, põe isso à minha conta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '19', 'Eu, Paulo, de minha própria mão o escrevi; eu o pagarei, para te não dizer que ainda mesmo a ti próprio a mim te deves.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '20', 'Sim, irmão, eu me regozijarei de ti no SENHOR: recreia as minhas entranhas no SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '21', 'Escrevi-te confiado na tua obediência, sabendo que ainda farás mais do que digo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '22', 'E juntamente prepara-me também pousada, porque espero que pelas vossas orações vos hei de ser concedido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '23', 'Saúdam-te Epafras, meu companheiro de prisão por Cristo Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '24', 'Marcos, Aristarco, Demas e Lucas, meus cooperadores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('18', '1', '25', 'A graça de nosso SENHOR Jesus Cristo seja com o vosso espírito. Ámen');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '1', '¶ HAVENDO Deus antigamente falado muitas vezes, e de muitas maneiras, aos pais pelos profetas, a nós falou-nos nestes últimos dias pelo Filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '2', 'A quem constituiu herdeiro de tudo, por quem fez também o mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '3', 'O qual, sendo o resplendor da sua glória, e a expressa imagem da sua pessoa, e sustentando todas as coisas pela palavra do seu poder, havendo feito por si mesmo a purificação das nossos pecados, assentou-se à dextra da majestade nas altura;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '4', 'Feito tanto mais excelente do que os anjos, quanto herdou mais excelente nome do que eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '5', 'Porque, a qual dos anjos disse jamais: Tu és meu Filho, hoje te gerei? E outra vez: Eu lhe serei por Pai, e ele me será por Filho?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '6', 'E quando outra vez introduz no mundo o primogénito, diz: E todos os anjos de Deus o adorem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '7', 'E, quanto aos anjos, diz: O que de seus anjos faz ventos, e de seus ministros labareda de fogo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '8', 'Mas, do Filho, diz: Ó Deus, o teu trono subsiste pelos séculos dos séculos, cetro de equidade é o cetro do teu reino.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '9', 'Amaste a justiça e aborreceste a iniquidade; por isso Deus, o teu Deus te ungiu com óleo de alegria mais do que a teus companheiros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '10', 'E Tu, Senhor, no princípio fundaste a terra, e os céus são obra de tuas mãos:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '11', 'Eles perecerão, mas tu permanecerás; e todos eles, como roupa, envelhecerão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '12', 'E como um manto os enrolarás, e como um vestido se mudarão, mas tu és o mesmo, e os teus anos não acabarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '13', 'E a qual dos anjos disse jamais: Assenta-te à minha dextra até que ponha a teus inimigos por escabelo de teus pés?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '1', '14', 'Não são porventura todos eles espíritos ministradores, enviados para servir a favor daqueles que hão de herdar a salvação?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '1', 'PORTANTO convém-nos atentar com mais diligência para as coisas que já temos ouvido, para que em tempo algum nos desviemos delas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '2', 'Porque, se a palavra falada pelos anjos permaneceu firme, e toda a transgressão e desobediência recebeu a justa retribuição,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '3', 'Como escaparemos nós, se não atentarmos para uma tão grande salvação, a qual, começando a ser anunciada pelo SENHOR, foi-nos depois confirmada pelos que a ouviram;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '4', 'Testificando também Deus com eles, por sinais, e milagres, e várias maravilhas e dons do Espírito Santo, distribuídos por sua vontade?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '5', 'Porque não foi aos anjos que sujeitou o mundo futuro, de que falamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '6', 'Mas em certo lugar testificou alguém, dizendo: Que é o homem, para que dele te lembres? Ou o filho do homem, para que o visites?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '7', 'Tu o fizeste um pouco menor do que os anjos, de glória e de honra o coroaste, e o constituíste sobre as obras de tuas mãos:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '8', 'Todas as coisas lhe sujeitaste debaixo dos pés. Ora, visto que lhe sujeitou todas as coisas, nada deixou que lhe não esteja sujeito. Mas agora ainda não vemos que todas as coisas lhe estejam sujeitas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '9', 'Vemos, porém, coroado de glória e de honra aquele Jesus que fora feito um pouco menor do que os anjos, por causa da paixão da morte, para que, pela graça de Deus, provasse a morte por todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '10', 'Porque convinha que aquele para quem são todas as coisas, e mediante quem tudo existe, trazendo muitos filhos à glória, consagrasse pelas aflições o príncipe da salvação deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '11', 'Porque, assim o que santifica, como os que são santificados, são todos de um; por cuja causa não se envergonha de lhes chamar irmãos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '12', 'Dizendo: Anunciarei o teu nome a meus irmãos, cantar-te-ei louvores no meio da congregação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '13', 'E outra vez: Porei nele a minha confiança. E outra vez: Eis-me aqui a mim, e aos filhos que Deus me deu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '14', 'E, visto como os filhos participam da carne e do sangue, também ele participou das mesmas coisas, para que pela morte aniquilasse o que tinha o império da morte, isto é, o diabo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '15', 'E livrasse todos os que, com medo da morte, estavam por toda a vida sujeitos à servidão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '16', 'Porque, na verdade, ele não tomou os anjos, mas tomou a descendência de Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '17', 'Pelo que convinha que em tudo fosse semelhante aos irmãos, para ser misericordioso e fiel sumo sacerdote naquilo que é de Deus, para expiar os pecados do povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '2', '18', 'Porque naquilo que ele mesmo, sendo tentado, padeceu, pode socorrer aos que são tentados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '1', 'PELO que, irmãos santos, participantes da vocação celestial, considerai a Jesus Cristo, apóstolo e sumo sacerdote da nossa confissão,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '2', 'Sendo fiel ao que o constituiu, como também o foi Moisés em toda a sua casa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '3', 'Porque ele é tido por digno de tanto maior glória do que Moisés, quanto maior honra do que a casa tem aquele que a edificou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '4', 'Porque toda a casa é edificada por alguém, mas o que edificou todas as coisas é Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '5', 'E, na verdade, Moisés foi fiel em toda a sua casa, como servo, para testemunho das coisas que se haviam de anunciar;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '6', 'Mas Cristo, como Filho sobre a sua própria casa; a qual casa somos nós, se tão somente conservarmos firme a confiança e a glória da esperança até ao fim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '7', 'Portanto como diz o Espírito Santo, se ouvirdes hoje a sua voz,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '8', 'Não endureçais os vossos corações, como na provocação, no dia da tentação no deserto,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '9', 'Onde vossos pais me tentaram, me provocaram, e viram por quarenta anos as minhas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '10', 'Por isso me indignei contra esta geração, e disse: Estes sempre erram em seu coração, e não conheceram os meus caminhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '11', 'Assim jurei na minha ira que não entrarão no meu repouso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '12', 'Vede, irmãos, que nunca haja em qualquer de vós um coração mau e infiel, para se apartar do Deus vivo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '13', 'Antes exortai-vos uns aos outros todos os dias, durante o tempo que se chama Hoje, para que nenhum de vós se endureça pelo engano do pecado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '14', 'Porque nos tornamos participantes de Cristo, se retivermos firmemente o princípio da nossa confiança até ao fim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '15', 'Enquanto se diz: Hoje, se ouvirdes a sua voz, não endureçais os vossos corações, como na provocação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '16', 'Porque, havendo-a alguns ouvido, o provocaram; mas não todos os que saíram do Egipto por meio de Moisés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '17', 'Mas com quem se indignou por quarenta anos? Não foi porventura com os que pecaram, cujos corpos caíram no deserto?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '18', 'E a quem jurou que não entrariam no seu repouso senão aos que foram desobedientes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '3', '19', 'E vemos que não puderam entrar por causa da sua incredulidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '1', 'TEMAMOS pois que, porventura, deixada a promessa de entrar no seu repouso, pareça que algum de vós fica para trás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '2', 'Porque também a nós foram pregadas as boas novas, como a eles, mas a palavra da pregação nada lhes aproveitou, porquanto não estava misturada com a fé naqueles que a ouviam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '3', 'Porque nós, os que temos crido, entramos no repouso, tal como disse: Assim jurei na minha ira que não entrarão no meu repouso; embora as suas obras estivessem acabadas desde a fundação do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '4', 'Porque em certo lugar disse assim do dia sétimo: E repousou Deus de todas as suas obras no sétimo dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '5', 'E outra vez neste lugar: Não entrarão no meu repouso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '6', 'Visto, pois, que resta que alguns entrem nele, e que aqueles a quem primeiro foram pregadas as boas novas, não entraram por causa da desobediência,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '7', 'Determina outra vez um certo dia, Hoje, dizendo por David, muito tempo depois, como está dito: Hoje, se ouvirdes a sua voz, não endureçais os vossos corações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '8', 'Porque, se Josué lhes houvesse dado repouso, não falaria depois de outro dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '9', 'Portanto resta ainda um repouso para o povo de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '10', 'Porque aquele que entrou no seu repouso, ele próprio repousou de suas obras, como Deus das suas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '11', 'Procuremos pois entrar naquele repouso, para que ninguém caia no mesmo exemplo de desobediência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '12', 'Porque a palavra de Deus é viva e eficaz e mais penetrante do que espada alguma de dois gumes, e penetra até a divisão da alma e do espírito, e das juntas e medulas, e é apta para discernir os pensamentos e intenções do coração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '13', 'E não há criatura alguma encoberta diante dele; antes todas as coisas estão nuas e patentes aos olhos daquele com quem temos de tratar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '14', 'Visto que temos um grande sumo sacerdote, Jesus, Filho de Deus, que penetrou nos céus, retenhamos firmemente a nossa confissão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '15', 'Porque não temos um sumo sacerdote que não possa compadecer-se das nossas fraquezas; porém um que, como nós, em tudo foi tentado, mas sem pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '4', '16', 'Cheguemos pois como confiança ao trono da graça, para que possamos alcançar misericórdia e achar graça, a fim de sermos ajudados em tempo oportuno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '1', 'PORQUE todo o sumo sacerdote, tomado de entre os homens, é constituído a favor dos homens nas coisas concernentes a Deus, para que ofereça dons e sacrifícios pelos pecados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '2', 'E possa compadecer-se ternamente dos ignorantes e errados; pois também ele mesmo está rodeado de fraqueza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '3', 'E por esta causa deve ele, tanto pelo povo, como também por si mesmo, fazer oferta pelos pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '4', 'E ninguém toma para si esta honra, senão o que é chamado por Deus, como Aarão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '5', 'Assim também Cristo se não glorificou a si mesmo, para se fazer sumo sacerdote, mas aquele que lhe disse: Tu és meu Filho, hoje te gerei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '6', 'Como também diz noutro lugar: Tu és Sacerdote eternamente, segundo a ordem de Melquisedeque.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '7', 'O qual, nos dias da sua carne, oferecendo, com grande clamor e lágrimas, orações e súplicas ao que o podia livrar da morte, foi ouvido quanto ao que temia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '8', 'Ainda que era Filho, aprendeu a obediência, por aquilo que padeceu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '9', 'E, sendo ele consumado, veio a ser a causa de eterna salvação para todos os que lhe obedecem;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '10', 'Chamado por Deus sumo sacerdote, segundo a ordem de Melquisedeque.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '11', 'Do qual muito temos que dizer, de difícil interpretação; porquanto vos fizestes negligentes para ouvir,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '12', 'Porque, devendo já ser mestres pelo tempo, ainda necessitais de que se vos torne a ensinar quais sejam os primeiros rudimentos das palavras de Deus; e vos haveis feito tais que necessitais de leite, e não de sólido mantimento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '13', 'Porque qualquer que ainda se alimenta de leite não está experimentado na palavra da justiça, porque é menino.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '5', '14', 'Mas o mantimento sólido é para os perfeitos, os quais, em razão do costume, têm os sentidos exercitados para discernir tanto o bem como o mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '1', 'PELO que, deixando os rudimentos da doutrina de Cristo, prossigamos até à perfeição, não lançando de novo o fundamento do arrependimento das obras mortas e da fé em Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '2', 'E da doutrina dos baptismos, e da imposição das mãos, e da ressurreição dos mortos, e do juízo eterno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '3', 'E isto faremos, se Deus o permitir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '4', 'Porque é impossível que os que já uma vez foram iluminados, e provaram o dom celestial, e se fizeram participantes do Espírito Santo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '5', 'E provaram a boa palavra de Deus e as virtudes do século futuro,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '6', 'E recaíram, sejam outra vez renovados para arrependimento; pois assim quanto a eles, de novo crucificam o Filho de Deus, e o expõem ao vitupério.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '7', 'Porque a terra que embebe a chuva, que muitas vezes cai sobre ela, e produz erva proveitosa para aqueles por quem é lavrada, recebe a bênção de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '8', 'Mas a que produz espinhos e abrolhos, é reprovada, e perto está da maldição; o seu fim é ser queimada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '9', 'Mas de vós, ó amados, esperamos coisas melhores, e coisas que acompanham a salvação, ainda que assim falamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '10', 'Porque Deus não é injusto para se esquecer da vossa obra, e do trabalho da caridade que para com o seu nome mostrastes, enquanto servistes aos santos; e ainda servis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '11', 'Mas desejamos que cada um de vós mostre o mesmo cuidado até ao fim, para completa certeza da esperança;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '12', 'Para que vos não façais negligentes, mas sejais imitadores dos que pela fé e paciência herdam as promessas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '13', 'Porque, quando Deus fez a promessa a Abraão, como não tinha outro maior por quem jurasse, jurou por si mesmo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '14', 'Dizendo: Certamente, abençoando, te abençoarei, e multiplicando te multiplicarei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '15', 'E assim esperando com paciência, alcançou a promessa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '16', 'Porque os homens certamente juram por alguém superior a eles, e o juramento para confirmação é, para eles, o fim de toda a contenda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '17', 'Pelo que, querendo Deus mostrar mais abundantemente a imutabilidade do seu conselho aos herdeiros da promessa, se interpôs com juramento;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '18', 'Para que por duas coisas imutáveis, nas quais é impossível que Deus minta, tenhamos a firme consolação, nós, os que pomos o nosso refúgio em reter a esperança proposta;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '19', 'A qual temos como âncora da alma segura e firme, e que penetra até ao interior do véu,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '6', '20', 'Onde Jesus, nosso precursor, entrou por nós, feito eternamente sumo sacerdote, segundo a ordem de Melquisedeque.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '1', 'PORQUE este Melquisedeque, que era rei de Salém, sacerdote do Deus Altíssimo, e que saiu ao encontro de Abraão quando ele regressava da matança dos reis, e o abençoou,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '2', 'A quem também Abraão deu o dízimo de tudo, e primeiramente é, por interpretação, rei de justiça, e depois também rei de Salém, que é rei de paz;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '3', 'Sem pai, sem mãe, sem genealogia, não tendo princípio de dias nem fim de vida, mas sendo feito semelhante ao Filho de Deus, permanece sacerdote para sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '4', 'Considerai pois quão grande era este, a quem até o patriarca Abraão deu os dízimos dos despojos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '5', 'E os que de entre os filhos de Levi recebem o sacerdócio têm ordem, segundo a lei, de tomar o dízimo do povo, isto é, de seus irmãos, ainda que tenham saído dos lombos de Abraão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '6', 'Mas aquele cuja genealogia não é contada entre eles tomou dízimos de Abraão, e abençoou o que tinha as promessas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '7', 'Ora, sem contradição alguma, o menor é abençoado pelo maior.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '8', 'E aqui certamente tomam dízimos homens que morrem; ali, porém, aquele de quem se testifica que vive.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '9', 'E, para assim dizer, por meio de Abraão até Levi, que recebe dízimos, pagou dízimos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '10', 'Porque ainda ele estava nos lombos de seu pai quando Melquisedeque lhe saiu ao encontro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '11', 'De sorte que, se a perfeição fosse pelo sacerdócio levítico (porque sob ele o povo recebeu a lei), que necessidade havia logo de que outro sacerdote se levantasse, segundo a ordem de Melquisedeque, e não fosse chamado segundo a ordem de Aarão?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '12', 'Porque, mudando-se o sacerdócio, necessariamente se faz também mudança da lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '13', 'Porque aquele de quem estas coisas se dizem pertence a outra tribo, da qual ninguém serviu ao altar,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '14', 'Visto ser manifesto que nosso SENHOR procedeu de Judá, e concernente a essa tribo nunca Moisés falou de sacerdócio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '15', 'E muito mais manifesto é ainda, se à semelhança de Melquisedeque se levantar outro sacerdote,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '16', 'Que não foi feito segundo a lei do mandamento carnal, mas segundo a virtude da vida incorruptível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '17', 'Porque dele assim se testifica: Tu és sacerdote eternamente, segundo a ordem de Melquisedeque.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '18', 'Porque o precedente mandamento é abrogado por causa da sua fraqueza e inutilidade');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '19', '(Pois a lei nenhuma coisa aperfeiçoou) e desta sorte é introduzida uma melhor esperança, pela qual chegamos a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '20', 'E visto como não é sem prestar juramento (porque certamente aqueles, sem juramento, foram feitos sacerdotes,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '21', 'Mas este com juramento por aquele que lhe disse: Jurou o SENHOR, e não se arrependerá; Tu és sacerdote eternamente segundo a ordem de Melquisedeque),');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '22', 'De tanto melhor concerto Jesus foi feito fiador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '23', 'E, na verdade, aqueles foram feitos sacerdotes em grande número, porque pela morte foram impedidos de permanecer,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '24', 'Mas este, porque permanece eternamente, tem um sacerdócio perpétuo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '25', 'Portanto, pode também salvar perfeitamente os que por ele se chegam a Deus, vivendo sempre para interceder por eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '26', 'Porque nos convinha tal sumo sacerdote, santo, inocente, imaculado, separado dos pecadores, e feito mais sublime do que os céus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '27', 'Que não necessitasse, como os sumos sacerdotes, de oferecer cada dia sacrifícios, primeiramente por seus próprios pecados, e depois pelos do povo; porque isto fez ele, uma vez, oferecendo-se a si mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '7', '28', 'Porque a lei constitui sumos sacerdotes a homens fracos, mas a palavra do juramento, que veio depois da lei, constitui ao Filho, perfeito para sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '1', 'ORA a suma do que temos dito é que temos um sumo sacerdote tal, que está assentado nos céus à dextra do trono da majestade,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '2', 'Ministro do santuário, e do verdadeiro tabernáculo, o qual o SENHOR fundou, e não o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '3', 'Porque todo o sumo sacerdote é constituído para oferecer dons e sacrifícios; pelo que era necessário que este também tivesse alguma coisa que oferecer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '4', 'Ora, se ele estivesse na terra, nem tão pouco sacerdote seria, havendo ainda sacerdotes que oferecem dons segundo a lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '5', 'Os quais servem de exemplar e sombra das coisas celestiais; como Moisés divinamente foi avisado, estando já para acabar o tabernáculo; porque foi dito: Olha, faz tudo conforme o modelo que no monte se te mostrou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '6', 'Mas agora alcançou ele ministério tanto mais excelente, quanto é mediador dum melhor concerto, que está confirmado em melhores promessas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '7', 'Porque, se aquele primeiro fora irrepreensível, nunca se teria buscado lugar para o segundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '8', 'Porque, repreendendo-os, lhes diz: Eis que virão dias, diz o SENHOR, em que com a casa de Israel e com a casa de Judá estabelecerei um novo concerto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '9', 'Não segundo o concerto que fiz com seus pais no dia em que os tomei pela mão, para os tirar da terra do Egipto; como não permaneceram naquele meu concerto, eu para eles não atentei, diz o SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '10', 'Porque este é o concerto que depois daqueles dias farei com a casa de Israel, diz o SENHOR: Porei as minhas leis no seu entendimento, e em seu coração as escreverei; e eu lhes serei por Deus, e eles me serão por povo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '11', 'E não ensinará cada um a seu próximo, nem cada um ao seu irmão, dizendo: Conhece o SENHOR; porque todos me conhecerão, desde o menor deles até ao maior.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '12', 'Porque serei misericordioso para com suas iniquidades, e de seus pecados e de suas prevaricações não me lembrarei mais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '8', '13', 'Dizendo Novo Concerto, envelheceu o primeiro. Ora, o que foi tornado velho, e se envelhece, perto está de acabar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '1', 'ORA também o primeiro tinha ordenanças de culto divino, e um santuário terrestre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '2', 'Porque um tabernáculo estava preparado, o primeiro, em que havia o candeeiro, e a mesa, e os pães da proposição; ao que se chama o santuário.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '3', 'Mas depois do segundo véu estava o tabernáculo que se chama o santo dos santos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '4', 'Que tinha o incensário de ouro, e a arca do concerto, coberta de ouro toda em redor; em que estava um vaso de ouro, que continha o maná, e a vara de Aarão, que tinta florescido, e as tábuas do concerto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '5', 'E sobre a arca os querubins da glória, que faziam sombra no propiciatório, das quais coisas não falaremos agora particularmente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '6', 'Ora, estando estas coisas assim preparadas, a todo o tempo entravam os sacerdotes no primeiro tabernáculo, cumprindo os serviços;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '7', 'Mas no segundo só o sumo sacerdote, uma vez no ano, não sem sangue, que oferecia por si mesmo e pelas culpas do povo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '8', 'Dando nisto a entender o Espírito Santo que ainda o caminho do santuário não estava descoberto enquanto se conservava em pé o primeiro tabernáculo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '9', 'Que é uma alegoria para o tempo presente, em que se oferecem dons e sacrifícios que, quanto à consciência, não podem aperfeiçoar aquele que faz o serviço;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '10', 'Consistindo somente em manjares, e bebidas, e várias abluções e justificações da carne, impostas até ao tempo da correcção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '11', 'Mas, vindo Cristo, o sumo sacerdote dos bens futuros, por um maior e mais perfeito tabernáculo, não feito por mãos, isto é, não desta criação,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '12', 'Nem por sangue de bodes e bezerros, mas por seu próprio sangue, entrou uma vez no santuário, havendo efectuado uma eterna redenção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '13', 'Porque, se o sangue dos toiros e bodes, e a cinza duma novilha esparzida sobre os imundos, os santifica, quanto à purificação da carne,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '14', 'Quanto mais o sangue de Cristo, que pelo Espírito eterno se ofereceu a si mesmo imaculado a Deus, purificará as vossas consciências das obras mortas, para servirdes ao Deus vivo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '15', 'E por isso é Mediador dum novo Testamento, para que, intervindo a morte para remissão das transgressões que havia debaixo do primeiro testamento, os chamados recebam a promessa da herança eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '16', 'Porque onde há testamento necessário é que intervenha a morte do testador.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '17', 'Porque um testamento tem força onde houve morte; ou terá ele algum valor enquanto o testador vive?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '18', 'Pelo que também o primeiro não foi consagrado sem sangue;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '19', 'Porque, havendo Moisés anunciado a todo o povo todos os mandamentos segundo a lei, tomou o sangue dos bezerros e dos bodes, com água, lã purpúrea e hissope, e aspergiu tanto o mesmo livro como todo o povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '20', 'Dizendo: Este é o sangue do testamento que Deus vos tem mandado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '21', 'E semelhantemente aspergiu com sangue o tabernáculo e todos os vasos do ministério.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '22', 'E quase todas as coisas, segundo a lei, se purificam com sangue; e sem derramamento de sangue não há remissão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '23', 'De sorte que era bem necessário que as figuras das coisas que estão no céu assim se purificassem; mas as próprias coisas celestiais com sacrifícios melhores do que estes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '24', 'Porque Cristo não entrou num santuário feito por mãos, figura do verdadeiro, porém no mesmo céu, para agora comparecer por nós perante a face de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '25', 'Nem também para a si mesmo se oferecer muitas vezes, como o sumo sacerdote cada ano entra no santuário com sangue alheio;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '26', 'Doutra maneira, necessário lhe fora padecer muitas vezes desde a fundação do mundo; mas agora na consumação dos séculos uma vez se manifestou, para aniquilar o pecado pelo sacrifício de si mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '27', 'E, como aos homens está ordenado morrerem uma vez, vindo depois disso o juízo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '9', '28', 'Assim também Cristo, oferecendo-se uma vez para tirar os pecados de muitos, aparecerá segunda vez, sem pecado, aos que o esperam para salvação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '1', 'PORQUE, tendo a lei a sombra dos bens futuros, e não a imagem exacta das coisas, nunca, pelos mesmos sacrifícios que continuamente se oferecem cada ano, pode aperfeiçoar os que a eles se chegam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '2', 'Doutra maneira, teriam deixado de se oferecer, porque, purificados uma vez os ministrantes, nunca mais teriam consciência de pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '3', 'Nesses sacrifícios, porém, cada ano se faz comemoração dos pecados,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '4', 'Porque é impossível que o sangue dos toiros e dos bodes tire os pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '5', 'Pelo que, entrando no mundo, diz: Sacrifício e oferta não quiseste, mas corpo me preparaste;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '6', 'Holocaustos e oblações pelo pecado não te agradaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '7', 'Então disse: Eis aqui venho (no princípio do livro está escrito de mim), para fazer, ó Deus, a tua vontade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '8', 'Como acima diz: Sacrifício e oferta, e holocaustos e oblações pelo pecado não quiseste, nem te agradaram (os quais se oferecem segundo a lei).');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '9', 'Então disse: Eis aqui venho, para fazer, ó Deus, a tua vontade. Tira o primeiro, para estabelecer o segundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '10', 'Na qual vontade temos sido santificados pela oblação do corpo de Jesus Cristo, feita uma vez.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '11', 'E assim todo o sacerdote aparece cada dia, ministrando e oferecendo muitas vezes os mesmos sacrifícios, que nunca podem tirar os pecados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '12', 'Mas este havendo oferecido um único sacrifício pelos pecados, está assentado para sempre à dextra de Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '13', 'Daqui em diante esperando até que os seus inimigos sejam postos por escabelo de seus pés.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '14', 'Porque com uma só oblação aperfeiçoou para sempre os que são santificados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '15', 'E também o Espírito Santo no-lo testifica, porque depois de haver dito:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '16', 'Este é o concerto que farei com eles depois daqueles dias, diz o SENHOR: Porei as minhas leis em seus corações, e as escreverei em seus entendimentos; acrescenta:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '17', 'E jamais me lembrarei dos seus pecados e das suas iniquidades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '18', 'Ora, onde há remissão destes, não há mais oblação pelo pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '19', 'Tendo pois, irmãos, ousadia para entrar no santuário, pelo sangue de Jesus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '20', 'Pelo novo e vivo caminho que ele nos consagrou, pelo véu, isto é, pela sua carne,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '21', 'E tendo um grande sacerdote sobre a casa de Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '22', 'Cheguemo-nos com verdadeiro coração, em inteira certeza de fé; tendo os corações purificados da má consciência, e o corpo lavado com água limpa,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '23', 'Retenhamos firmes a confissão da nossa esperança; porque fiel é o que prometeu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '24', 'E consideremo-nos uns aos outros, para nos estimularmos à caridade e às boas obras:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '25', 'Não deixando a nossa congregação, como é costume de alguns, antes admoestando-nos uns aos outros; e tanto mais, quanto vedes que se vai aproximando aquele dia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '26', 'Porque, se pecarmos voluntariamente, depois de termos recebido o conhecimento da verdade, já não resta mais sacrifício pelos pecados,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '27', 'Mas uma certa expectação horrível do juízo, e ardor de fogo, que há de devorar os adversários.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '28', 'Quebrantando alguém a lei de Moisés, morre sem misericórdia, só pela palavra de duas ou três testemunhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '29', 'De quanto maior castigo cuidais vós será julgado merecedor aquele que pisar o Filho de Deus, e tiver por profano o sangue do testamento, com que foi santificado, e fizer agravo ao Espírito da graça?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '30', 'Porque bem conhecemos aquele que disse: Minha é a vingança, eu darei a recompensa, diz o SENHOR. E outra vez: O SENHOR julgará o seu povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '31', 'Horrenda coisa é cair nas mãos do Deus vivo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '32', 'Lembrai-vos, porém, dos dias passados, em que, depois de serdes iluminados, suportastes grande combate de aflições.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '33', 'Em parte fostes feitos espectáculo com vitupérios e tribulações, e em parte fostes participantes com os que assim foram tratados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '34', 'Porque também vos compadecestes dos que estavam nas prisões, e com gozo permitistes a espoliação dos vossos bens, sabendo que em vós mesmos tendes nos céus uma possessão melhor e permanente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '35', 'Não rejeiteis pois a vossa confiança, que tem grande e avultado galardão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '36', 'Porque necessitais de paciência, para que, depois de haverdes feito a vontade de Deus, possais alcançar a promessa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '37', 'Porque ainda um poucochinho de tempo, e o que há de vir virá, e não tardará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '38', 'Mas o justo viverá da fé; e, se ele recuar, a minha alma não tem prazer nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '10', '39', 'Nós, porém, não somos daqueles que se retiram para a perdição, mas daqueles que crêem para a conservação da alma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '1', 'ORA, a fé é o firme fundamento das coisas que se esperam, e a prova das coisas que se não vêem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '2', 'Porque por ela os antigos alcançaram testemunho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '3', 'Pela fé entendemos que os mundos pela palavra de Deus foram criados; de maneira que aquilo que se vê não foi feito do que é aparente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '4', 'Pela fé Abel ofereceu a Deus maior sacrifício do que Caim, pelo qual alcançou testemunho de que era justo, dando Deus testemunho dos seus dons, e por ela, depois de morto, ainda fala.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '5', 'Pela fé Enoque foi trasladado para não ver a morte, e não foi achado, porque Deus o trasladara; visto como antes da sua trasladação alcançou testemunho de que agradara a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '6', 'Ora, sem fé é impossível agradar-lhe porque é necessário que aquele que se aproxima de Deus creia que ele existe, e que é galardoador dos que o buscam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '7', 'Pela fé Noé, divinamente avisado das coisas que ainda se não viam, temeu, e, para salvação da sua família, preparou a arca, pela qual condenou o mundo; e foi feito herdeiro da justiça que é segundo a fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '8', 'Pela fé Abraão, sendo chamado, obedeceu, indo para um lugar que havia de receber por herança; e saiu sem saber para onde ia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '9', 'Pela fé habitou na terra da promessa, como em terra alheia, morando em cabanas com Isaque e Jacob, herdeiros com ele da mesma promessa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '10', 'Porque esperava a cidade que tem fundamento, da qual o artífice e construtor é Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '11', 'Pela fé também a mesma Sara recebeu a virtude de conceber, e deu à luz já fora da idade; porquanto teve por fiel aquele que lho tinha prometido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '12', 'Pelo que também de um, e esse já amortecido, descenderam tantos, em multidão, como as estrelas do céu e como a areia inumerável que está na praia do mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '13', 'Todos estes morreram na fé, sem terem recebido as promessas; mas vendo-as de longe, e crendo-as e abraçando-as, confessaram que eram estrangeiros e peregrinos na terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '14', 'Porque, os que isto dizem, claramente mostram que buscam uma pátria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '15', 'E se, na verdade, se lembrassem daquela donde haviam saído, teriam oportunidade de tornar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '16', 'Mas agora desejam uma melhor, isto é, a celestial. Pelo que também Deus se não envergonha deles, de se chamar seu Deus, porque já lhes preparou uma cidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '17', 'Pela fé ofereceu Abraão a Isaque, quando foi provado; sim, aquele que recebera as promessas ofereceu o seu unigénito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '18', 'Sendo-lhe dito: Em Isaque será chamada a tua descendência, considerou que Deus era poderoso para até dos mortos o ressuscitar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '19', 'E daí também em figura ele o recobrou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '20', 'Pela fé Isaque abençoou Jacob e Esaú, no tocante às coisas futuras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '21', 'Pela fé Jacob, próximo da morte abençoou cada um dos filhos de José, e adorou encostado à ponta do seu bordão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '22', 'Pela fé José, próximo da morte, fez menção da saída dos filhos de Israel, e deu ordem acerca de seus ossos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '23', 'Pela fé Moisés, já nascido, foi escondido três meses por seus pais, porque viram que era um menino formoso; e não temeram o mandamento do rei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '24', 'Pela fé Moisés, sendo já grande recusou ser chamado filho da filha de Faraó,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '25', 'Escolhendo antes ser maltratado com o povo de Deus do que por um pouco de tempo ter o gozo do pecado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '26', 'Tendo por maiores riquezas o vitupério de Cristo do que os tesouros do Egipto; porque tinha em vista a recompensa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '27', 'Pela fé deixou o Egipto, não temendo a ira do rei; porque ficou firme, como vendo o invisível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '28', 'Pela fé celebrou a páscoa e a aspersão do sangue, para que o destruidor dos primogénitos lhes não tocasse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '29', 'Pela fé passaram o Mar Vermelho, como por terra seca; o que intentando os egípcios, se afogaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '30', 'Pela fé caíram os muros de Jericó, sendo rodeados durante sete dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '31', 'Pela fé Raabe, a meretriz, não pereceu com os incrédulos, acolhendo em paz os espias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '32', 'E que mais direi? Faltar-me-ia o tempo contando de Gideão, e de Baraque, de Sansão, e de Jefté, e de David, e de Samuel e dos profetas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '33', 'Os quais pela fé venceram reinos, praticaram a justiça, alcançaram promessas, fecharam as bocas dos leões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '34', 'Apagaram a força do fogo, escaparam do fio da espada, da fraqueza tiraram forças, na batalha se esforçaram, puseram em fugida os exércitos dos estranhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '35', 'As mulheres receberam pela ressurreição os seus mortos; uns foram torturados, não aceitando o seu livramento, para alcançarem uma melhor ressurreição;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '36', 'E outros experimentaram escárnio e açoites, e até cadeias e prisões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '37', 'Foram apedrejados, serrados, tentados, mortos ao fio da espada; andaram vestidos de peles de ovelhas e de cabras, desamparados, aflitos e maltratados,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '38', '(Dos quais o mundo não era digno), errantes pelos desertos, e montes, e pelas covas e cavernas da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '39', 'E todos estes, tendo tido testemunho pela fé, não alcançaram a promessa,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '11', '40', 'Provendo Deus alguma coisa melhor a nosso respeito, para que eles sem nós não fossem aperfeiçoados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '1', 'PORTANTO nós também, pois que estamos rodeados de uma tão grande nuvem de testemunhas, deixemos todo o embaraço, e o pecado que tão de perto nos rodeia, e corramos com paciência a carreira que nos está proposta,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '2', 'Olhando para Jesus, autor e consumador da fé, o qual pelo gozo que lhe estava proposto suportou a cruz, desprezando a afronta; e assentou-se à dextra do trono de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '3', 'Considerai pois aquele que suportou tais contradições dos pecadores contra si mesmo, para que não enfraqueçais, desfalecendo em vossos ânimos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '4', 'Ainda não resististes até ao sangue, combatendo contra o pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '5', 'E já vos esquecestes da exortação que argumenta convosco como filhos: Filho meu, não desprezes a correcção do SENHOR, e não desmaies quando por ele fores repreendido;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '6', 'Porque o SENHOR corrige o que ama, e açoita a qualquer que recebe por filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '7', 'Se suportais a correcção, Deus vos trata como filhos; porque, que filho há a quem o pai não corrija?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '8', 'Mas, se estais sem disciplina, da qual todos são feitos participantes, sois então bastardos, e não filhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '9', 'Além do que tivemos nossos pais segundo a, carne, para nos corrigirem, e nós os reverenciámos: não nos sujeitaremos muito mais ao Pai dos espíritos, para vivermos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '10', 'Porque aqueles, na verdade, por um pouco de tempo, nos corrigiam como bem lhes parecia; mas este, para nosso proveito, para sermos participantes da sua santidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '11', 'E, na verdade, toda a correcção, ao presente, não parece ser de gozo, senão de tristeza, mas depois produz um fruto pacífico de justiça nos exercitados por ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '12', 'Portanto tornai a levantar as mãos cansadas, e os joelhos desconjuntados,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '13', 'E fazei veredas direitas para os vossos pés, para que o que manqueja se não desvie inteiramente, antes seja sarado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '14', 'Segui a paz com todos e a santificação, sem a qual ninguém verá o SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '15', 'Tendo cuidado de que ninguém se prive da graça de Deus, e de que nenhuma raiz de amargura, brotando vos perturbe, e por ela muitos se contaminem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '16', 'E ninguém seja devasso, ou profano, como Esaú, que por um manjar vendeu o seu direito de primogenitura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '17', 'Porque bem sabeis que, querendo ele ainda depois herdar a bênção, foi rejeitado, porque não achou lugar de arrependimento, ainda que com lágrimas o buscou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '18', 'Porque não chegastes ao monte palpável, aceso em fogo, e à escuridão, e às trevas, e à tempestade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '19', 'E ao sonido da trombeta, e à voz das palavras, a qual os que a ouviram pediram que se lhes não falasse mais;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '20', 'Porque não podiam suportar o que se lhes mandava: se até um animal tocar o monte, será apedrejado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '21', 'E tão terrível era a visão, que Moisés disse: Estou todo assombrado, e tremendo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '22', 'Mas chegastes ao monte de Sião, e à cidade do Deus vivo, à Jerusalém celestial, e aos muitos milhares de anjos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '23', 'À universal assembleia e igreja dos primogénitos, que estão inscritos nos céus, e a Deus, o juiz de todos, e aos espíritos dos justos aperfeiçoados;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '24', 'E a Jesus, o Mediador duma Nova Aliança, e ao sangue da aspersão que fala melhor do que o de Abel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '25', 'Vede que não rejeiteis ao que fala; porque, se não escaparam aqueles que rejeitaram o que na terra os advertia, muito menos nós, se nos desviarmos daquele que é dos céus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '26', 'A voz do qual moveu então a terra, mas agora anunciou, dizendo: Ainda uma vez comoverei, não só a terra, senão também o céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '27', 'E esta palavra: Ainda uma vez, mostra a mudança das coisas móveis, como coisas feitas, para que as imóveis permaneçam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '28', 'Pelo que, tendo recebido um reino que não pode ser abalado retenhamos a graça, pela qual sirvamos a Deus agradavelmente com reverência e piedade;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '12', '29', 'Porque o nosso Deus é um fogo consumidor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '1', 'PERMANEÇA a caridade fraternal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '2', 'Não vos esqueçais da hospitalidade, porque por ela alguns, não o sabendo hospedaram anjos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '3', 'Lembrai-vos dos presos, como se estivésseis presos com eles, e dos maltratados, como sendo-o vós mesmos também no corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '4', 'Venerado seja entre todos o matrimónio e o leito sem mácula; porém aos que se dão à prostituição e aos adúlteros Deus os julgará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '5', 'Sejam vossos costumes sem avareza contentando-vos com o que tendes; porque ele disse: Não te deixarei, nem te desampararei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '6', 'E assim com confiança ousemos dizer: O SENHOR é o meu ajudador, e não temerei o que me possa fazer o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '7', 'Lembrai-vos dos vossos pastores, que vos falaram a palavra de Deus, a fé dos quais imitai, atentando para a sua maneira de viver.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '8', 'Jesus Cristo é o mesmo ontem, e hoje, e eternamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '9', 'Não vos deixeis levar em redor por doutrinas várias e estranhas porque bom é que o coração se fortifique com graça, e não com manjares, que de nada aproveitaram aos que a eles se entregaram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '10', 'Temos um altar, de que não têm direito de comer os que servem ao tabernáculo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '11', 'Porque os corpos dos animais, cujo sangue é, pelo pecado, trazido pelo sumo sacerdote para o santuário, são queimados fora do arraial.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '12', 'E por isso também Jesus, para santificar o povo pelo seu próprio sangue, padeceu fora da porta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '13', 'Saiamos pois a ele fora do arraial, levando o seu vitupério.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '14', 'Porque não temos aqui cidade permanente, mas buscamos a futura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '15', 'Portanto ofereçamos sempre por ele a Deus sacrifício de louvor, isto é, o fruto dos lábios que confessam o seu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '16', 'E não vos esqueçais da beneficência e comunicação, porque com tais sacrifícios Deus se agrada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '17', 'Obedecei a vossos pastores, e sujeitai-vos a eles; porque velam por vossas almas, como aqueles que hão de dar conta delas; para que o façam com alegria e não gemendo, porque isso não vos seria útil.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '18', 'Orai por nós, porque confiamos que, temos boa consciência, como aqueles que em tudo querem portar-se honestamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '19', 'E rogo-vos com instância que assim o façais, para que eu mais depressa vos seja restituído.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '20', 'Ora o Deus de paz, que pelo sangue do concerto eterno tornou a trazer dos mortos a nosso SENHOR Jesus Cristo, Grande Pastor das ovelhas,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '21', 'Vos aperfeiçoe em toda a boa obra, para fazerdes a sua vontade, operando em vós o que perante ele é agradável por Cristo Jesus, ao qual seja glória para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '22', 'Rogo-vos, porém, irmãos, que suporteis a palavra desta exortação; porque abreviadamente vos escrevi.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '23', 'Sabei que já está solto o irmão Timóteo, com o qual (se vier depressa) vos verei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '24', 'Saudai a todos os vossos chefes e a todos os santos. Os de Itália vos saúdam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('19', '13', '25', 'A graça seja com todos vós. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '1', '¶ TIAGO, servo de Deus e do SENHOR Jesus Cristo, às doze tribos que andam dispersas, saúde.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '2', 'Meus irmãos, tende grande gozo quando cairdes em várias tentações;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '3', 'Sabendo que a prova da vossa fé obra a paciência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '4', 'Tenha, porém, a paciência a sua obra perfeita, para que sejais perfeitos e completos, sem faltar em coisa alguma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '5', 'E, se algum de vós tem falta de sabedoria, peça-a a Deus, que a todos dá liberalmente, e o não lança em rosto, e ser-lhe-á dada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '6', 'Peça-a, porém, com fé; não duvidando; porque o que duvida é semelhante à onda do mar, que é levada pelo vento, e lançada de uma para outra parte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '7', 'Não pense tal homem que receberá do SENHOR alguma coisa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '8', 'O homem de coração dobre é inconstante em todos os seus caminhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '9', 'Mas glorie-se o irmão abatido na sua exaltação,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '10', 'E o rico em seu abatimento; porque ele passará como a flor da erva.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '11', 'Porque sai o sol com ardor, e a erva seca, e a sua flor cai, e a formosa aparência do seu aspecto perece: assim se murchará também o rico em seus caminhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '12', 'Bem-aventurado o varão que sofre a tentação; porque, quando for aprovado receberá a coroa da vida, a qual o SENHOR tem prometido aos que o amam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '13', 'Ninguém, sendo tentado diga: De Deus sou tentado; porque Deus não pode ser tentado pelo mal, e a ninguém tenta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '14', 'Mas cada um é tentado, quando atraído e engodado pela sua própria concupiscência.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '15', 'Depois, havendo a concupiscência concebido, dá à luz o pecado; e o pecado, sendo consumado, gera a morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '16', 'Não erreis, meus amados irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '17', 'Toda a boa dádiva e todo o dom perfeito vem do alto, descendo do Pai das luzes, em quem não há mudança nem sombra de variação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '18', 'Segundo a sua vontade, ele nos gerou pela palavra da verdade, para que fôssemos como primícias das suas criaturas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '19', 'Portanto, meus amados irmãos; todo o homem seja pronto para ouvir, tardio para falar, tardio para se irar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '20', 'Porque a ira do homem não opera a justiça de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '21', 'Pelo que, rejeitando toda a imundície e superfluidade de malícia, recebei com mansidão a palavra em vós enxertada, a qual pode salvar as vossas almas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '22', 'E sede cumpridores da palavra, e não somente ouvintes, enganando-vos com falsos discursos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '23', 'Porque, se alguém é ouvinte da palavra, e não cumpridor, é semelhante ao varão que contempla ao espelho o seu rosto natural;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '24', 'Porque se contempla a si mesmo, e foi-se, e logo se esqueceu de que tal era.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '25', 'Aquele, porém, que atenta bem para a lei perfeita da liberdade, e nisso persevera, não sendo ouvinte esquecido, mas fazedor da obra, este tal será bem-aventurado no seu feito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '26', 'Se alguém entre vós cuida ser religioso, e não refreia a sua língua, antes engana o seu coração, a religião desse é vã.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '1', '27', 'A religião pura e imaculada para com Deus, o pai, é esta: Visitar os órfãos e as viúvas nas suas tribulações, e guardar-se da corrupção do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '1', 'MEUS irmãos, não tenhais a fé de nosso SENHOR Jesus Cristo, Senhor da glória, em acepção de pessoas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '2', 'Porque, se no vosso ajuntamento entrar algum homem com anel de ouro no dedo, com vestidos preciosos, e entrar também algum pobre com sórdido vestido,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '3', 'E atentardes para o que traz o vestido precioso, e lhe disserdes: Assenta-te tu aqui num lugar de honra, e disserdes ao pobre: Tu, fica aí em pé, ou assenta-te abaixo do meu estrado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '4', 'Porventura não fizestes distinção dentro de vós mesmos, e não vos fizestes juízes de maus pensamentos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '5', 'Ouvi, meus amados irmãos: Porventura não escolheu Deus aos pobres deste mundo para serem ricos na fé, e herdeiros do reino que prometeu aos que o amam?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '6', 'Mas vós desonrastes o pobre. Porventura não vos oprimem os ricos, e não vos arrastam aos tribunais?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '7', 'Porventura não blasfemam eles o bom nome que sobre vós foi invocado?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '8', 'Todavia, se cumprirdes, conforme a Escritura, a lei real: Amarás a teu próximo como a ti mesmo, bem fazeis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '9', 'Mas, se fazeis acepção de pessoas, cometeis pecado, e sois redarguidos pela lei como transgressores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '10', 'Porque qualquer que guardar toda a lei, e tropeçar em um só ponto, tornou-se culpado de todos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '11', 'Porque aquele que disse: Não cometerás adultério, também disse: Não matarás. Se tu pois não cometeres adultério, mas matares, estás feito transgressor da lei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '12', 'Assim falai, e assim procedei, como devendo ser julgados pela lei da liberdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '13', 'Porque o juízo será sem misericórdia sobre aquele que não fez misericórdia; e a misericórdia triunfa do juízo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '14', 'Meus irmãos, que aproveita se alguém disser que tem fé, e não tiver as obras? Porventura a fé pode salvá-lo?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '15', 'E, se o irmão ou a irmã estiverem nus, e tiverem falta de mantimento quotidiano,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '16', 'E algum de vós lhe disser: Ide em paz, aquentai-vos, e fartai-vos; e lhe não derdes as coisas necessárias para o corpo, que proveito virá daí?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '17', 'Assim também a fé, se não tiver as obras, é morta em si mesma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '18', 'Mas dirá alguém: Tu tens a fé, e eu tenho as obras: mostra-me a tua fé sem as tuas obras, e eu te mostrarei a minha fé pelas minhas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '19', 'Tu crês que há um Deus; fazes bem, também os demónios o crêem, e estremecem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '20', 'Mas, ó homem vão, queres tu saber que a fé sem as obras é morta?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '21', 'Porventura o nosso pai Abraão não foi justificado pelas obras, quando ofereceu sobre o altar o seu filho Isaque?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '22', 'Bem vês que a fé cooperou com as suas obras, e que pelas obras a fé foi aperfeiçoada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '23', 'E cumpriu-se a Escritura, que diz: E creu Abraão em Deus, e foi-lhe isso imputado como justiça, e foi chamado o amigo de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '24', 'Vedes então que o homem é justificado pelas obras, e não somente pela fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '25', 'E de igual modo Raabe, a meretriz, não foi também justificada pelas obras, quando recolheu os emissários e os despediu por outro caminho?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '2', '26', 'Porque, assim como o corpo sem o espírito está morto, assim também a fé sem obras é morta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '1', 'MEUS irmãos, muitos de vós não sejam mestres, sabendo que receberemos mais duro juízo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '2', 'Porque todos tropeçamos em muitas coisas. Se alguém não tropeça em palavra, o tal varão é perfeito, e poderoso para também refrear todo o corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '3', 'Ora nós pomos freio nas bocas dos cavalos, para que nos obedeçam; e conseguimos dirigir todo o seu corpo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '4', 'Vede também as naus que, sendo tão grandes, e levadas de impetuosos ventos, se viram com um bem pequeno leme para onde quer a vontade daquele que as governa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '5', 'Assim também a língua é um pequeno membro, e gloria-se de grandes coisas. Vede quão grande bosque um pequeno fogo incendeia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '6', 'A língua também é um fogo; como mundo de iniquidades, a língua está posta entre os nossos membros, e contamina todo o corpo, e inflama o curso da natureza, e é inflamada pelo inferno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '7', 'Porque toda a natureza, tanto de bestas feras como de aves, tanto de repteis como de animais do mar, se amansa e foi domada pela natureza humana;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '8', 'Mas nenhum homem pode domar a língua. É um mal que não se pode refrear; está cheia de peçonha mortal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '9', 'Com ela bendizemos a Deus e Pai, e com ela amaldiçoamos os homens, feitos à semelhança de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '10', 'De uma mesma boca procede bênção e maldição. Meus irmãos, não convém que isto se faça assim.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '11', 'Porventura deita alguma fonte de um mesmo manancial água doce e água amargosa?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '12', 'Meus irmãos, pode também a figueira produzir azeitonas, ou a videira figos? Assim tampouco pode uma fonte dar água salgada e doce.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '13', 'Quem de entre vós é sábio e entendido? Mostre pelo seu bom trato as suas obras em mansidão de sabedoria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '14', 'Mas, se tendes amarga inveja, e sentimento faccioso em vosso coração, não vos glorieis, nem mintais contra a verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '15', 'Essa não é a sabedoria que vem do alto, mas é terrena, animal e diabólica.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '16', 'Porque onde há inveja e espírito faccioso aí há perturbação e toda a obra perversa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '17', 'Mas a sabedoria que do alto vem é, primeiramente, pura, depois pacífica, moderada, tratável, cheia de misericórdia e de bons frutos, sem parcialidade, e sem hipocrisia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '3', '18', 'Ora o fruto da justiça semeia-se na paz, para os que exercitam a paz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '1', 'DONDE vêm as guerras e pelejas entre vós? Porventura não vêm disto, a saber, dos vossos deleites, que nos vossos membros guerreiam?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '2', 'Cobiçais, e nada tendes: sois invejosos, e cobiçosos, e não podeis alcançar: combateis e guerreais, e nada tendes, porque não pedis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '3', 'Pedis, e não recebeis, porque pedis mal, para o gastardes em vossos deleites.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '4', 'Adúlteros e adúlteras, não sabeis vós que a amizade do mundo é inimizade contra Deus? Portanto qualquer que quiser ser amigo do mundo constitui-se inimigo de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '5', 'Ou cuidais vós que em vão diz a Escritura: O espírito que em nós habita tem ciúmes?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '6', 'Antes dá maior graça. Portanto diz: Deus resiste aos soberbos, dá, porém, graça aos humildes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '7', 'Sujeitai-vos pois a Deus, resisti ao diabo, e ele fugirá de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '8', 'Chegai-vos a Deus, e ele se chegará a vós. Alimpai as mãos, pecadores; e, vós de duplo ânimo, purificai os corações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '9', 'Senti as vossas misérias, e lamentai, e chorai; converta-se o vosso riso em pranto, e o vosso gozo em tristeza.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '10', 'Humilhai-vos perante o SENHOR, e ele vos exaltará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '11', 'Irmãos, não faleis mal uns dos outros. Quem fala mal de um irmão, e julga a seu irmão, fala mal da lei, e julga a lei; e, se tu julgas a lei, já não és observador da lei, mas juiz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '12', 'Há só um legislador e um juiz que pode salvar e destruir. Tu, porém, quem és, que julgas a outrem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '13', 'Eia agora vós, que dizeis: Hoje, ou amanhã, iremos a tal cidade, e lá passaremos um ano, e contrataremos, e ganharemos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '14', 'Digo-vos que não sabeis o que acontecerá amanhã. Porque, que é a vossa vida? É um vapor que aparece por um pouco, e depois se desvanece.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '15', 'Em lugar do que devíeis dizer; Se o SENHOR quiser, e se vivermos, faremos isto ou aquilo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '16', 'Mas agora vos gloriais em vossas presunções: toda a glória tal como esta é maligna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '4', '17', 'Aquele pois que sabe fazer o bem e o não faz, comete pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '1', 'EIA pois agora vós, ricos, chorai e pranteai, por vossas misérias, que sobre vós hão de vir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '2', 'As vossas riquezas estão apodrecidas, e os vossos vestidos estão comidos da traça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '3', 'O vosso ouro e a vossa prata se enferrujaram; e a sua ferrugem dará testemunho contra vós, e comerá como fogo a vossa carne. Entesourastes para os últimos dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '4', 'Eis que o jornal dos trabalhadores que ceifaram as vossas terras, e que por vós foi diminuído, clama; e os clamores dos que ceifaram entraram nos ouvidos do SENHOR dos exércitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '5', 'Deliciosamente vivestes sobre a terra, e vos deleitastes: cevastes os vossos corações, como num dia de matança.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '6', 'Condenastes e matastes o justo; ele não vos resistiu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '7', 'Sede pois, irmãos, pacientes até à vinda do SENHOR. Eis que o lavrador espera o precioso fruto da terra, aguardando-o com paciência, até que receba a chuva temporã e serôdia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '8', 'Sede vós também pacientes, fortalecei os vossos corações; porque já a vinda do SENHOR está próxima.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '9', 'Irmãos, não vos queixeis uns contra os outros, para que não sejais condenados. Eis que o juiz está à porta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '10', 'Meus irmãos, tomai por exemplo de aflição e paciência os profetas que falaram em nome do SENHOR.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '11', 'Eis que temos por bem-aventurados os que sofreram. Ouvistes qual foi a paciência de Job, e vistes o fim que o SENHOR lhe deu; porque o SENHOR é muito misericordioso e piedoso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '12', 'Mas, sobretudo, meus irmãos, não jureis, nem pelo céu, nem pela terra, nem façais qualquer outro juramento; mas que a vossa palavra seja sim, sim, e não, não; para que não caiais em condenação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '13', 'Está alguém entre vós aflito? Ore. Está alguém contente? Cante louvores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '14', 'Está alguém entre vós doente? Chame os presbíteros da igreja, e orem sobre ele, ungindo-o com azeite em nome do SENHOR;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '15', 'E a oração da fé salvará o doente, e o SENHOR o levantará; e, se houver cometido pecados, ser-lhe-ão perdoados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '16', 'Confessai as vossas culpas uns aos outros, e orai uns pelos outros, para que sareis: a oração feita por um justo pode muito em seus efeitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '17', 'Elias era homem sujeito às mesmas paixões que nós, e, orando, pediu que não chovesse, e, por três anos e seis meses, não choveu sobre a terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '18', 'E orou outra vez, e o céu deu chuva, e a terra produziu o seu fruto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '19', 'Irmãos, se algum de entre vós se tem desviado da verdade, e alguém o converter,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('20', '5', '20', 'Saiba que aquele que fizer converter do erro do seu caminho um pecador salvará da morte uma alma e cobrirá uma multidão de pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '1', '¶ PEDRO, apóstolo de Jesus Cristo, aos estrangeiros dispersos no Ponto, Galácia, Capadócia, Ásia e Bitínia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '2', 'Eleitos segundo a presciência de Deus Pai, em santificação do Espírito, para a obediência e aspersão do sangue de Jesus Cristo: graça e paz vos sejam multiplicadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '3', 'Bendito seja o Deus e Pai de nosso SENHOR Jesus Cristo, que, segundo a sua grande misericórdia, nos gerou de novo para uma viva esperança, pela ressurreição de Jesus Cristo de entre os mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '4', 'Para uma herança incorruptível, incontaminável, e que se não pode murchar, guardada nos céus para vós,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '5', 'Que mediante a fé estais guardados na virtude de Deus para a salvação, já prestes para se revelar no último tempo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '6', 'Em que vós grandemente vos alegrais, ainda que agora importa, sendo necessário, que estejais por um pouco contristados com várias tentações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '7', 'Para que a prova da vossa fé, muito mais preciosa do que o ouro que perece e é provado pelo fogo, se ache em louvor, e honra, e glória, na revelação de Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '8', 'Ao qual, não o havendo visto, amais; no qual, não o vendo agora, mas crendo, vos alegrais com gozo inefável e glorioso;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '9', 'Alcançando o fim da vossa fé, a salvação das almas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '10', 'Da qual salvação inquiriram e trataram diligentemente os profetas que profetizaram da graça que vos foi dada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '11', 'Indagando que tempo ou que ocasião de tempo o Espírito de Cristo, que estava neles indicava, anteriormente testificando os sofrimentos que a Cristo haviam de vir, e a glória que se lhes havia de seguir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '12', 'Aos quais foi revelado que, não para si mesmos, mas para nós, eles ministravam estas coisas que agora vos foram anunciadas por aqueles que, pelo Espírito Santo enviado do céu, vos pregaram o evangelho; para as quais coisas os anjos desejam bem atentar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '13', 'Portanto, cingindo os lombos do vosso entendimento, sede sóbrios, e esperai inteiramente na graça que se vos ofereceu na revelação de Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '14', 'Como filhos obedientes, não vos conformando com as concupiscências que antes havia em vossa ignorância.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '15', 'Mas, como é santo aquele que vos chamou, sede vós também santos em toda a vossa maneira de viver;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '16', 'Porquanto escrito está: Sede santos, porque eu sou santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '17', 'E, se invocais por Pai aquele que, sem acepção de pessoas, julga segundo a obra de cada um, andai em temor, durante o tempo da vossa peregrinação;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '18', 'Sabendo que não foi com coisas corruptíveis, como prata ou ouro, que fostes resgatados da vossa vã maneira de viver que por tradição recebestes dos vossos pais,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '19', 'Mas com o precioso sangue de Cristo, como de um cordeiro imaculado e incontaminado,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '20', 'O qual, na verdade, em outro tempo foi conhecido, ainda antes da fundação do mundo, mas manifestado nestes últimos tempos por amor de vós;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '21', 'E por ele credes em Deus, que o ressuscitou dos mortos, e lhe deu glória, para que a vossa fé e esperança estivessem em Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '22', 'Purificando as vossas almas na obediência à verdade, para a caridade fraternal, não fingida; amai-vos ardentemente uns aos outros com um coração puro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '23', 'Sendo de novo gerados, não de semente corruptível, mas da incorruptível, pela palavra de Deus, viva, e que permanece para sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '24', 'Porque toda a carne é como a erva, e toda a flor da erva. Secou-se a erva, e caiu a sua flor;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '1', '25', 'Mas a palavra do SENHOR permanece para sempre; e esta é a palavra que entre vós foi evangelizada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '1', 'DEIXANDO pois toda a malícia, e todo o engano, e fingimentos, e invejas, e todas as murmurações,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '2', 'Desejai afectuosamente, como meninos novamente nascidos, o leite racional, não falsificado, para que por ele vades crescendo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '3', 'Se é que provastes que o SENHOR é benigno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '4', 'Chegando-vos para ele - pedra viva, reprovada, na verdade, pelos homens, mas para com Deus eleita e preciosa,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '5', 'Vós também, como pedras vivas, sois edificados casa espiritual e sacerdócio santo, para oferecer sacrifícios espirituais agradáveis a Deus por Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '6', 'Pelo que também na Escritura se contém: Eis que ponho em Sião a pedra principal da esquina, eleita e preciosa; e quem nela crer não será confundido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '7', 'E assim para vós, os que credes, é preciosa, mas, para os rebeldes, a pedra que os edificadores reprovaram essa foi a principal da esquina;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '8', 'E uma pedra de tropeço e rocha de escândalo, para aqueles que tropeçam na palavra, sendo desobedientes; para o que também foram destinados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '9', 'Mas vós sois a geração eleita, o sacerdócio real, a nação santa, o povo adquirido, para que anuncieis as virtudes daquele que vos chamou das trevas para a sua maravilhosa luz;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '10', 'Vós, que em outro tempo não éreis povo, mas agora sois povo de Deus; que não tínheis alcançado misericórdia, mas agora alcançastes misericórdia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '11', 'Amados, peço-vos, como a peregrinos e forasteiros, que vos abstenhais das concupiscências que combatem contra a alma;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '12', 'Tendo o vosso viver honesto entre os gentios; para que, naquilo em que falam mal de vós, como de malfeitores, glorifiquem a Deus no dia da visitação, pelas boas obras que em vós observem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '13', 'Sujeitai-vos pois a toda a ordenação humana por amor do SENHOR: quer ao rei, como superior;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '14', 'Quer aos governadores, como por ele enviados para castigo dos malfeitores, e para louvor dos que fazem o bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '15', 'Porque assim é a vontade de Deus, que, fazendo bem, tapeis a boca à ignorância dos homens loucos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '16', 'Como livres, e não tendo a liberdade por cobertura da malícia, mas como servos de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '17', 'Honrai a todos. Amai a fraternidade. Temei a Deus. Honrai o rei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '18', 'Vós, servos, sujeitai-vos com todo o temor aos senhores, não somente aos bons e humanos, mas também aos maus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '19', 'Porque é coisa agradável, que alguém, por causa da consciência para com Deus, sofra agravos, padecendo injustamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '20', 'Porque, que glória será essa, se, pecando, sois esbofeteados e sofreis? Mas se, fazendo bem, sois afligidos, e o sofreis, isso é agradável a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '21', 'Porque para isto sois chamados; pois também Cristo padeceu por nós, deixando-nos o exemplo, para que sigais as suas pisadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '22', 'O qual não cometeu pecado, nem na sua boca se achou engano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '23', 'O qual, quando o injuriavam, não injuriava, e quando padecia não ameaçava, mas entregava-se aquele que julga justamente:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '24', 'Levando ele mesmo em seu corpo os nossos pecados sobre o madeiro, para que, mortos para os pecados, pudéssemos viver para a justiça; e pelas suas feridas fostes sarados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '2', '25', 'Porque éreis como ovelhas desgarradas: mas agora tendes voltado ao Pastor e Bispo das vossas almas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '1', 'SEMELHANTEMENTE vós, mulheres, sede sujeitas aos vossos próprios maridos; para que também, se alguns não obedecem à palavra, pelo porte de suas mulheres sejam ganhos sem palavra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '2', 'Considerando a vossa vida casta, em temor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '3', 'O enfeite delas não seja o exterior, no frisado dos cabelos, no uso de jóias de ouro, na compostura dos vestidos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '4', 'Mas o homem encoberto no coração; no incorruptível trajo de um espírito manso e quieto, que é precioso diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '5', 'Porque assim se adornavam também antigamente as santas mulheres que esperavam em Deus, e estavam sujeitas aos seus próprios maridos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '6', 'Como Sara obedecia a Abraão, chamando-lhe senhor; da qual vós sois filhos, fazendo o bem, e não temendo nenhum espanto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '7', 'Igualmente vós, maridos, coabitai com elas com entendimento, dando honra à mulher, como vaso mais fraco; para que não sejam impedidas as vossas orações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '8', 'E, finalmente, sede todos de um mesmo sentimento, compassivos, amando os irmãos, entranhavelmente misericordiosos e afáveis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '9', 'Não tornando mal por mal, ou injúria; antes, pelo contrário, bendizendo: sabendo que para isto fostes chamados, para que por herança alcanceis a bênção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '10', 'Porque quem quer amar a vida, e ver os dias bons, refreie a sua língua do mal, e os seus lábios não falem engano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '11', 'Aparte-se do mal, e faça o bem; busque a paz, e siga-a.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '12', 'Porque os olhos do SENHOR estão sobre os justos, e os seus ouvidos atentos às suas orações; mas o rosto do SENHOR é contra os que fazem males.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '13', 'E qual é aquele que vos fará mal, se fordes zelosos do bem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '14', 'Mas também se padecerdes por amor da justiça, sois bem-aventurados. E não temais com medo deles, nem vos turbeis;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '15', 'Antes santificai a Cristo, como SENHOR, em vossos corações; e estai sempre preparados para responder com mansidão e temor a qualquer que vos pedir a razão da esperança que há em vós:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '16', 'Tendo uma boa consciência, para que, naquilo em que falam mal de vós, como de malfeitores, fiquem confundidos os que blasfemam do vosso bom porte em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '17', 'Porque melhor é que padeçais fazendo bem (se a vontade de Deus assim o quer), do que fazendo mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '18', 'Porque também Cristo padeceu uma vez pelos pecados, o justo pelos injustos, para levar-nos a Deus; mortificado, na verdade, na carne, mas vivificado pelo Espírito;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '19', 'No qual também foi, e pregou aos espíritos em prisão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '20', 'Os quais noutro tempo foram rebeldes, quando a longanimidade de Deus esperava nos dias de Noé, enquanto se preparava a arca; na qual poucas (isto é oito) almas se salvaram pela água;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '21', 'Que também, como uma verdadeira figura, agora vos salva, baptismo, não do despojamento da imundície da carne, mas da indagação de uma boa consciência para com Deus, pela ressurreição de Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '3', '22', 'O qual está à dextra de Deus, tendo subido ao céu, havendo-se-lhe sujeitado os anjos, e as autoridades, e as potências.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '1', 'ORA pois, já que Cristo padeceu por nós na carne, armai-vos também vós com este pensamento, que aquele que padeceu na carne já cessou do pecado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '2', 'Para que, no tempo que vos resta na carne, não vivais mais segundo as concupiscências dos homens, mas segundo a vontade de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '3', 'Porque é bastante que no tempo passado da vida fizéssemos a vontade dos gentios, andando em dissoluções, concupiscências, borrachices, glutonarias, bebedices e abomináveis idolatrias;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '4', 'E acham estranho não correrdes com eles no mesmo desenfreamento de dissolução, blasfemando de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '5', 'Os quais hão de dar conta ao que está preparado para julgar os vivos e os mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '6', 'Porque por isto foi pregado o evangelho também aos mortos, para que, na verdade, fossem julgados segundo os homens na carne, mas vivessem segundo Deus em espírito;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '7', 'E já está próximo o fim de todas as coisas; portanto sede sóbrios e vigiai em oração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '8', 'Mas, sobretudo, tende ardente caridade uns para com os outros; porque a caridade cobrirá a multidão dos pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '9', 'Sendo hospitaleiros uns para com os outros, sem murmurações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '10', 'Cada um administre aos outros o dom como o recebeu, como bons dispenseiros da multiforme graça de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '11', 'Se alguém falar, fale segundo as palavras de Deus: se alguém administrar, administre segundo o poder que Deus dá; para que em tudo Deus seja glorificado por Jesus Cristo, a quem pertence a glória e poder para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '12', 'Amados, não estranheis a ardente prova que vem sobre vós para vos tentar, como se coisa estranha vos acontecesse;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '13', 'Mas alegrai-vos no facto de serdes participantes das aflições de Cristo, para que também na revelação da sua glória vos regozijeis e alegreis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '14', 'Se pelo nome de Cristo sois vituperados, bem-aventurados sois, porque sobre vós repousa o Espírito da glória de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '15', 'Que nenhum de vós padeça como homicida, ou ladrão, ou malfeitor, ou como o que se entremete em negócios alheios;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '16', 'Mas, se padece como cristão, não se envergonhe, antes glorifique a Deus nesta parte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '17', 'Porque já é tempo que comece o julgamento pela casa de Deus; e, se primeiro começa por nós, qual será o fim daqueles que são desobedientes ao evangelho de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '18', 'E, se o justo apenas se salva, onde aparecerá o ímpio e o pecador?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '4', '19', 'Portanto também os que padecem segundo a vontade de Deus encomendem-lhe as suas almas, como ao fiel Criador, fazendo o bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '1', 'AOS presbíteros, que estão entre vós, admoesto eu, que sou também presbítero com eles; testemunha das aflições de Cristo, e participante da glória que se há de revelar:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '2', 'Apascentai o rebanho de Deus, que está entre vós, tendo cuidado dele, não por força, mas voluntariamente; nem por torpe ganância, mas de ânimo pronto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '3', 'Nem como tendo domínio sobre a herança de Deus, mas servindo de exemplo ao rebanho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '4', 'E, quando aparecer o Sumo Pastor, alcançareis a incorruptível coroa de glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '5', 'Semelhantemente vós, mancebos, sede sujeitos aos anciãos; e sede todos sujeitos uns aos outros, e revesti-vos de humildade, porque Deus resiste aos soberbos, mas dá graça aos humildes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '6', 'Humilhai-vos pois debaixo da potente mão de Deus, para que a seu tempo vos exalte;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '7', 'Lançando sobre ele toda a vossa ansiedade, porque ele tem cuidado de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '8', 'Sede sóbrios; vigiai; porque o diabo, vosso adversário, anda em derredor, bramando como leão, buscando a quem possa tragar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '9', 'Ao qual resisti firmes na fé: sabendo que as mesmas aflições se cumprem entre os vossos irmãos no mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '10', 'E o Deus de toda a graça, que em Cristo Jesus vos chamou à sua eterna glória, depois de haverdes padecido um pouco, ele mesmo vos aperfeiçoará, confirmará, fortificará e fortalecerá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '11', 'A ele seja a glória e o poderio para todo o sempre. Ámen');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '12', 'Por Silvano, vosso fiel irmão, como cuido, escrevi abreviando e testificando que esta é a verdadeira graça de Deus, na qual estais firmes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '13', 'A vossa co-eleita em Babilónia vos saúda e meu filho Marcos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('21', '5', '14', 'Saudai-vos uns aos outros com ósculo de amor. Paz seja com todos vós que estais em Cristo Jesus. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '1', '¶ PEDRO, apóstolo de Jesus Cristo, aos estrangeiros dispersos no Ponto, Galácia, Capadócia, Ásia e Bitínia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '2', 'Eleitos segundo a presciência de Deus Pai, em santificação do Espírito, para a obediência e aspersão do sangue de Jesus Cristo: graça e paz vos sejam multiplicadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '3', 'Bendito seja o Deus e Pai de nosso SENHOR Jesus Cristo, que, segundo a sua grande misericórdia, nos gerou de novo para uma viva esperança, pela ressurreição de Jesus Cristo de entre os mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '4', 'Para uma herança incorruptível, incontaminável, e que se não pode murchar, guardada nos céus para vós,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '5', 'Que mediante a fé estais guardados na virtude de Deus para a salvação, já prestes para se revelar no último tempo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '6', 'Em que vós grandemente vos alegrais, ainda que agora importa, sendo necessário, que estejais por um pouco contristados com várias tentações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '7', 'Para que a prova da vossa fé, muito mais preciosa do que o ouro que perece e é provado pelo fogo, se ache em louvor, e honra, e glória, na revelação de Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '8', 'Ao qual, não o havendo visto, amais; no qual, não o vendo agora, mas crendo, vos alegrais com gozo inefável e glorioso;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '9', 'Alcançando o fim da vossa fé, a salvação das almas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '10', 'Da qual salvação inquiriram e trataram diligentemente os profetas que profetizaram da graça que vos foi dada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '11', 'Indagando que tempo ou que ocasião de tempo o Espírito de Cristo, que estava neles indicava, anteriormente testificando os sofrimentos que a Cristo haviam de vir, e a glória que se lhes havia de seguir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '12', 'Aos quais foi revelado que, não para si mesmos, mas para nós, eles ministravam estas coisas que agora vos foram anunciadas por aqueles que, pelo Espírito Santo enviado do céu, vos pregaram o evangelho; para as quais coisas os anjos desejam bem atentar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '13', 'Portanto, cingindo os lombos do vosso entendimento, sede sóbrios, e esperai inteiramente na graça que se vos ofereceu na revelação de Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '14', 'Como filhos obedientes, não vos conformando com as concupiscências que antes havia em vossa ignorância.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '15', 'Mas, como é santo aquele que vos chamou, sede vós também santos em toda a vossa maneira de viver;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '16', 'Porquanto escrito está: Sede santos, porque eu sou santo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '17', 'E, se invocais por Pai aquele que, sem acepção de pessoas, julga segundo a obra de cada um, andai em temor, durante o tempo da vossa peregrinação;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '18', 'Sabendo que não foi com coisas corruptíveis, como prata ou ouro, que fostes resgatados da vossa vã maneira de viver que por tradição recebestes dos vossos pais,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '19', 'Mas com o precioso sangue de Cristo, como de um cordeiro imaculado e incontaminado,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '20', 'O qual, na verdade, em outro tempo foi conhecido, ainda antes da fundação do mundo, mas manifestado nestes últimos tempos por amor de vós;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '21', 'E por ele credes em Deus, que o ressuscitou dos mortos, e lhe deu glória, para que a vossa fé e esperança estivessem em Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '22', 'Purificando as vossas almas na obediência à verdade, para a caridade fraternal, não fingida; amai-vos ardentemente uns aos outros com um coração puro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '23', 'Sendo de novo gerados, não de semente corruptível, mas da incorruptível, pela palavra de Deus, viva, e que permanece para sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '24', 'Porque toda a carne é como a erva, e toda a flor da erva. Secou-se a erva, e caiu a sua flor;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '1', '25', 'Mas a palavra do SENHOR permanece para sempre; e esta é a palavra que entre vós foi evangelizada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '1', 'DEIXANDO pois toda a malícia, e todo o engano, e fingimentos, e invejas, e todas as murmurações,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '2', 'Desejai afectuosamente, como meninos novamente nascidos, o leite racional, não falsificado, para que por ele vades crescendo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '3', 'Se é que provastes que o SENHOR é benigno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '4', 'Chegando-vos para ele - pedra viva, reprovada, na verdade, pelos homens, mas para com Deus eleita e preciosa,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '5', 'Vós também, como pedras vivas, sois edificados casa espiritual e sacerdócio santo, para oferecer sacrifícios espirituais agradáveis a Deus por Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '6', 'Pelo que também na Escritura se contém: Eis que ponho em Sião a pedra principal da esquina, eleita e preciosa; e quem nela crer não será confundido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '7', 'E assim para vós, os que credes, é preciosa, mas, para os rebeldes, a pedra que os edificadores reprovaram essa foi a principal da esquina;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '8', 'E uma pedra de tropeço e rocha de escândalo, para aqueles que tropeçam na palavra, sendo desobedientes; para o que também foram destinados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '9', 'Mas vós sois a geração eleita, o sacerdócio real, a nação santa, o povo adquirido, para que anuncieis as virtudes daquele que vos chamou das trevas para a sua maravilhosa luz;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '10', 'Vós, que em outro tempo não éreis povo, mas agora sois povo de Deus; que não tínheis alcançado misericórdia, mas agora alcançastes misericórdia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '11', 'Amados, peço-vos, como a peregrinos e forasteiros, que vos abstenhais das concupiscências que combatem contra a alma;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '12', 'Tendo o vosso viver honesto entre os gentios; para que, naquilo em que falam mal de vós, como de malfeitores, glorifiquem a Deus no dia da visitação, pelas boas obras que em vós observem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '13', 'Sujeitai-vos pois a toda a ordenação humana por amor do SENHOR: quer ao rei, como superior;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '14', 'Quer aos governadores, como por ele enviados para castigo dos malfeitores, e para louvor dos que fazem o bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '15', 'Porque assim é a vontade de Deus, que, fazendo bem, tapeis a boca à ignorância dos homens loucos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '16', 'Como livres, e não tendo a liberdade por cobertura da malícia, mas como servos de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '17', 'Honrai a todos. Amai a fraternidade. Temei a Deus. Honrai o rei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '18', 'Vós, servos, sujeitai-vos com todo o temor aos senhores, não somente aos bons e humanos, mas também aos maus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '19', 'Porque é coisa agradável, que alguém, por causa da consciência para com Deus, sofra agravos, padecendo injustamente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '20', 'Porque, que glória será essa, se, pecando, sois esbofeteados e sofreis? Mas se, fazendo bem, sois afligidos, e o sofreis, isso é agradável a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '21', 'Porque para isto sois chamados; pois também Cristo padeceu por nós, deixando-nos o exemplo, para que sigais as suas pisadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '22', 'O qual não cometeu pecado, nem na sua boca se achou engano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '23', 'O qual, quando o injuriavam, não injuriava, e quando padecia não ameaçava, mas entregava-se aquele que julga justamente:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '24', 'Levando ele mesmo em seu corpo os nossos pecados sobre o madeiro, para que, mortos para os pecados, pudéssemos viver para a justiça; e pelas suas feridas fostes sarados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '2', '25', 'Porque éreis como ovelhas desgarradas: mas agora tendes voltado ao Pastor e Bispo das vossas almas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '1', 'SEMELHANTEMENTE vós, mulheres, sede sujeitas aos vossos próprios maridos; para que também, se alguns não obedecem à palavra, pelo porte de suas mulheres sejam ganhos sem palavra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '2', 'Considerando a vossa vida casta, em temor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '3', 'O enfeite delas não seja o exterior, no frisado dos cabelos, no uso de jóias de ouro, na compostura dos vestidos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '4', 'Mas o homem encoberto no coração; no incorruptível trajo de um espírito manso e quieto, que é precioso diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '5', 'Porque assim se adornavam também antigamente as santas mulheres que esperavam em Deus, e estavam sujeitas aos seus próprios maridos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '6', 'Como Sara obedecia a Abraão, chamando-lhe senhor; da qual vós sois filhos, fazendo o bem, e não temendo nenhum espanto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '7', 'Igualmente vós, maridos, coabitai com elas com entendimento, dando honra à mulher, como vaso mais fraco; para que não sejam impedidas as vossas orações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '8', 'E, finalmente, sede todos de um mesmo sentimento, compassivos, amando os irmãos, entranhavelmente misericordiosos e afáveis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '9', 'Não tornando mal por mal, ou injúria; antes, pelo contrário, bendizendo: sabendo que para isto fostes chamados, para que por herança alcanceis a bênção.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '10', 'Porque quem quer amar a vida, e ver os dias bons, refreie a sua língua do mal, e os seus lábios não falem engano.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '11', 'Aparte-se do mal, e faça o bem; busque a paz, e siga-a.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '12', 'Porque os olhos do SENHOR estão sobre os justos, e os seus ouvidos atentos às suas orações; mas o rosto do SENHOR é contra os que fazem males.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '13', 'E qual é aquele que vos fará mal, se fordes zelosos do bem?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '14', 'Mas também se padecerdes por amor da justiça, sois bem-aventurados. E não temais com medo deles, nem vos turbeis;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '15', 'Antes santificai a Cristo, como SENHOR, em vossos corações; e estai sempre preparados para responder com mansidão e temor a qualquer que vos pedir a razão da esperança que há em vós:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '16', 'Tendo uma boa consciência, para que, naquilo em que falam mal de vós, como de malfeitores, fiquem confundidos os que blasfemam do vosso bom porte em Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '17', 'Porque melhor é que padeçais fazendo bem (se a vontade de Deus assim o quer), do que fazendo mal.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '18', 'Porque também Cristo padeceu uma vez pelos pecados, o justo pelos injustos, para levar-nos a Deus; mortificado, na verdade, na carne, mas vivificado pelo Espírito;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '19', 'No qual também foi, e pregou aos espíritos em prisão;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '20', 'Os quais noutro tempo foram rebeldes, quando a longanimidade de Deus esperava nos dias de Noé, enquanto se preparava a arca; na qual poucas (isto é oito) almas se salvaram pela água;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '21', 'Que também, como uma verdadeira figura, agora vos salva, baptismo, não do despojamento da imundície da carne, mas da indagação de uma boa consciência para com Deus, pela ressurreição de Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '3', '22', 'O qual está à dextra de Deus, tendo subido ao céu, havendo-se-lhe sujeitado os anjos, e as autoridades, e as potências.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '1', 'ORA pois, já que Cristo padeceu por nós na carne, armai-vos também vós com este pensamento, que aquele que padeceu na carne já cessou do pecado;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '2', 'Para que, no tempo que vos resta na carne, não vivais mais segundo as concupiscências dos homens, mas segundo a vontade de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '3', 'Porque é bastante que no tempo passado da vida fizéssemos a vontade dos gentios, andando em dissoluções, concupiscências, borrachices, glutonarias, bebedices e abomináveis idolatrias;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '4', 'E acham estranho não correrdes com eles no mesmo desenfreamento de dissolução, blasfemando de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '5', 'Os quais hão de dar conta ao que está preparado para julgar os vivos e os mortos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '6', 'Porque por isto foi pregado o evangelho também aos mortos, para que, na verdade, fossem julgados segundo os homens na carne, mas vivessem segundo Deus em espírito;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '7', 'E já está próximo o fim de todas as coisas; portanto sede sóbrios e vigiai em oração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '8', 'Mas, sobretudo, tende ardente caridade uns para com os outros; porque a caridade cobrirá a multidão dos pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '9', 'Sendo hospitaleiros uns para com os outros, sem murmurações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '10', 'Cada um administre aos outros o dom como o recebeu, como bons dispenseiros da multiforme graça de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '11', 'Se alguém falar, fale segundo as palavras de Deus: se alguém administrar, administre segundo o poder que Deus dá; para que em tudo Deus seja glorificado por Jesus Cristo, a quem pertence a glória e poder para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '12', 'Amados, não estranheis a ardente prova que vem sobre vós para vos tentar, como se coisa estranha vos acontecesse;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '13', 'Mas alegrai-vos no facto de serdes participantes das aflições de Cristo, para que também na revelação da sua glória vos regozijeis e alegreis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '14', 'Se pelo nome de Cristo sois vituperados, bem-aventurados sois, porque sobre vós repousa o Espírito da glória de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '15', 'Que nenhum de vós padeça como homicida, ou ladrão, ou malfeitor, ou como o que se entremete em negócios alheios;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '16', 'Mas, se padece como cristão, não se envergonhe, antes glorifique a Deus nesta parte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '17', 'Porque já é tempo que comece o julgamento pela casa de Deus; e, se primeiro começa por nós, qual será o fim daqueles que são desobedientes ao evangelho de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '18', 'E, se o justo apenas se salva, onde aparecerá o ímpio e o pecador?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '4', '19', 'Portanto também os que padecem segundo a vontade de Deus encomendem-lhe as suas almas, como ao fiel Criador, fazendo o bem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '1', 'AOS presbíteros, que estão entre vós, admoesto eu, que sou também presbítero com eles; testemunha das aflições de Cristo, e participante da glória que se há de revelar:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '2', 'Apascentai o rebanho de Deus, que está entre vós, tendo cuidado dele, não por força, mas voluntariamente; nem por torpe ganância, mas de ânimo pronto;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '3', 'Nem como tendo domínio sobre a herança de Deus, mas servindo de exemplo ao rebanho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '4', 'E, quando aparecer o Sumo Pastor, alcançareis a incorruptível coroa de glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '5', 'Semelhantemente vós, mancebos, sede sujeitos aos anciãos; e sede todos sujeitos uns aos outros, e revesti-vos de humildade, porque Deus resiste aos soberbos, mas dá graça aos humildes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '6', 'Humilhai-vos pois debaixo da potente mão de Deus, para que a seu tempo vos exalte;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '7', 'Lançando sobre ele toda a vossa ansiedade, porque ele tem cuidado de vós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '8', 'Sede sóbrios; vigiai; porque o diabo, vosso adversário, anda em derredor, bramando como leão, buscando a quem possa tragar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '9', 'Ao qual resisti firmes na fé: sabendo que as mesmas aflições se cumprem entre os vossos irmãos no mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '10', 'E o Deus de toda a graça, que em Cristo Jesus vos chamou à sua eterna glória, depois de haverdes padecido um pouco, ele mesmo vos aperfeiçoará, confirmará, fortificará e fortalecerá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '11', 'A ele seja a glória e o poderio para todo o sempre. Ámen');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '12', 'Por Silvano, vosso fiel irmão, como cuido, escrevi abreviando e testificando que esta é a verdadeira graça de Deus, na qual estais firmes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '13', 'A vossa co-eleita em Babilónia vos saúda e meu filho Marcos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('22', '5', '14', 'Saudai-vos uns aos outros com ósculo de amor. Paz seja com todos vós que estais em Cristo Jesus. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '1', '1', '¶ O QUE era desde o princípio, o que ouvimos, o que vimos com os nossos olhos, o que temos contemplado, e as nossas mãos tocaram, da Palavra da vida,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '1', '2', '(Porque a vida foi manifestada, e nós a vimos, e testificamos dela, e vos anunciamos a vida eterna, que estava com o Pai, e nos foi manifestada):');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '1', '3', 'O que vimos e ouvimos, isso vos anunciamos, para que também tenhais comunhão connosco; e a nossa comunhão é com o Pai, e com seu Filho Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '1', '4', 'Estas coisas vos escrevemos, para que o vosso gozo se cumpra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '1', '5', 'E esta é a mensagem que dele ouvimos, e vos anunciamos: que Deus é luz, e não há nele trevas nenhumas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '1', '6', 'Se dissermos que temos comunhão com ele, e andarmos em trevas, mentimos, e não praticamos a verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '1', '7', 'Mas se andarmos na luz, como ele na luz está, temos comunhão uns com os outros, e o sangue de Jesus Cristo, seu Filho, nos purifica de todo o pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '1', '8', 'Se dissermos que não temos pecado, enganamo-nos a nós mesmos, e não há verdade em nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '1', '9', 'Se confessarmos os nossos pecados, ele é fiel e justo, para nos perdoar os pecados, e nos purificar de toda a injustiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '1', '10', 'Se dissermos que não pecámos, fazemo-lo mentiroso, e a sua palavra não está em nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '1', 'MEUS filhinhos, estas coisas vos escrevo, para que não pequeis; e, se alguém pecar, temos um Advogado para com o Pai, Jesus Cristo, o justo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '2', 'E ele é a propiciação pelos nossos pecados, e não somente pelos nossos, mas também pelos de todo o mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '3', 'E nisto sabemos que o conhecemos: se guardamos os seus mandamentos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '4', 'Aquele que diz: Eu conheço-o, e não guarda os seus mandamentos, é mentiroso, e nele não está a verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '5', 'Mas qualquer que guarda a sua palavra, o amor de Deus está nele verdadeiramente aperfeiçoado: nisto conhecemos que estamos nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '6', 'Aquele que diz que está nele, também deve andar como ele andou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '7', 'Irmãos, não vos escrevo mandamento novo, mas o mandamento antigo, que desde o princípio tivestes. Este mandamento antigo é a palavra que desde o princípio ouvistes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '8', 'Outra vez vos escrevo um mandamento novo, que é verdadeiro nele e em vós; porque vão passando as trevas, e já a verdadeira luz alumia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '9', 'Aquele que diz que está na luz, e aborrece a seu irmão, até agora está em trevas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '10', 'Aquele que ama a seu irmão está na luz, e nele não há escândalo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '11', 'Mas aquele que aborrece a seu irmão está em trevas, e anda em trevas, e não sabe para onde deva ir; porque as trevas lhe cegaram os olhos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '12', 'Filhinhos, escrevo-vos, porque pelo seu nome vos são perdoados os pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '13', 'Pais, escrevo-vos, porque conhecestes aquele que é desde o princípio. Mancebos, escrevo-vos, porque vencestes o maligno. Eu vos escrevi, filhos, porque conhecestes o Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '14', 'Eu vos escrevi, pais, porque já conhecestes aquele que é desde o princípio. Eu vos escrevi, mancebos, porque sois fortes, e a palavra de Deus está em vós, e já vencestes o maligno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '15', 'Não ameis o mundo, nem o que no mundo há. Se alguém ama o mundo, o amor do Pai não está nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '16', 'Porque tudo o que há no mundo, a concupiscência da carne, a concupiscência dos olhos e a sabedoria da vida não é do Pai, mas do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '17', 'E o mundo passa, e a sua concupiscência; mas aquele que faz a vontade de Deus permanece para sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '18', 'Filhinhos, é já a última hora; e, como ouvistes que vem o anti-cristo, também agora muitos se têm feito anti-cristos: por onde conhecemos que é já a última hora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '19', 'Saíram de nós, mas não eram de nós; porque, se fossem de nós, ficariam connosco: mas isto é para que se manifestasse que não são todos de nós.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '20', 'E vós tendes a unção do Santo, e sabeis tudo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '21', 'Não vos escrevi porque não soubésseis a verdade, mas porque a sabeis, e porque nenhuma mentira vem da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '22', 'Quem é o mentiroso, senão aquele que nega que Jesus é o Cristo? É o anti-cristo esse mesmo que nega o Pai e o Filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '23', 'Qualquer que nega o Filho, também não tem o Pai; e aquele que confessa o Filho, tem também o Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '24', 'Portanto, o que desde o princípio ouvistes permaneça em vós. Se em vós permanecer o que desde o princípio ouvistes, também permanecereis no Filho e no Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '25', 'E esta é a promessa que ele nos fez: a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '26', 'Estas coisas vos escrevi acerca dos que vos enganam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '27', 'E a unção, que vós recebestes dele, fica em vós, e não tendes necessidade de que alguém vos ensine; mas, como a sua unção vos ensina todas os coisas, e é verdadeira, e não é mentira, como ela vos ensinou, assim nele permanecereis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '28', 'E agora, filhinhos, permanecei nele; para que, quando ele se manifestar, tenhamos confiança, e não sejamos confundidos por ele na sua vinda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '2', '29', 'Se sabeis que ele é justo, sabeis que todo aquele que pratica a justiça é nascido dele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '1', 'VEDE quão grande amor nos tem concedido o Pai: que fôssemos chamados filhos de Deus. Por isso o mundo não nos conhece; porque o não conhece a ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '2', 'Amados, agora somos filhos de Deus, e ainda não é manifestado o que havemos de ser. Mas sabemos que, quando ele se manifestar, seremos semelhantes a ele; porque assim como é o veremos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '3', 'E qualquer que nele tem esta esperança purifica-se a si mesmo, como também ele é puro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '4', 'Qualquer que comete pecado, também comete iniquidade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '5', 'E bem sabeis que ele se manifestou para tirar os nossos pecados; e nele não há pecado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '6', 'Qualquer que permanece nele não peca; qualquer que peca não o viu nem o conheceu,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '7', 'Filhinhos, ninguém vos engane. Quem pratica justiça é justo, assim como ele é justo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '8', 'Quem comete o pecado é do diabo; porque o diabo peca desde o princípio. Para isto o Filho de Deus se manifestou: para desfazer as obras do diabo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '9', 'Qualquer que é nascido de Deus não comete pecado; porque a sua semente permanece nele; e não pode pecar, porque é nascido de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '10', 'Nisto são manifestos os filhos de Deus, e os filhos do diabo. Qualquer que não pratica a justiça, e não ama a seu irmão, não é de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '11', 'Porque esta é a mensagem que ouvistes desde o princípio: que nos amemos uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '12', 'Não como Caim, que era do maligno, e matou a seu irmão. E por que causa o matou? Porque as suas obras eram más e as de seu irmão justas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '13', 'Meus irmãos, não vos maravilheis se o mundo vos aborrece.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '14', 'Nós sabemos que passámos da morte para a vida, porque amamos os irmãos. Quem não ama a seu irmão permanece na morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '15', 'Qualquer que aborrece a seu irmão é homicida. E vós sabeis que nenhum homicida tem permanecente nele a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '16', 'Conhecemos a caridade nisto que ele deu a sua vida por nós, e nós devemos dar a vida pelos irmãos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '17', 'Quem pois tiver bens do mundo, e, vendo o seu irmão necessitado, lhe cerrar as suas entranhas, como estará nele a caridade de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '18', 'Meus filhinhos, não amemos de palavra, nem de língua, mas por obra e em verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '19', 'E nisto conhecemos que somos da verdade, e diante dele asseguraremos nossos corações;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '20', 'Sabendo que, se o nosso coração nos condena, maior é Deus do que os nossos corações, e conhece todas as coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '21', 'Amados, se o nosso coração nos não condena, temos confiança para com Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '22', 'E qualquer coisa que lhe pedirmos, dele a receberemos; porque guardamos os seus mandamentos, e fazemos o que é agradável à sua vista.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '23', 'E o seu mandamento é este: que creiamos no nome de seu Filho Jesus Cristo, e nos amemos uns aos outros, segundo o seu mandamento.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '3', '24', 'E aquele que guarda os seus mandamentos nele está, e ele nele. E nisto conhecemos que ele está em nós: pelo Espírito que nos tem');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '1', 'AMADOS, não creiais a todo o espírito, mas provai se os espíritos são de Deus; porque já muitos falsos profetas se têm levantado no mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '2', 'Nisto conhecereis o Espírito de Deus: Todo o espírito que confessa que Jesus Cristo veio em carne é de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '3', 'E todo o espírito que não confessa que Jesus Cristo veio em carne não é de Deus; mas este é o espírito do anti-cristo, do qual já ouvistes que há de vir, e eis que está já no mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '4', 'Filhinhos, sois de Deus, e já os tendes vencido; porque maior é o que está em vós do que o que está no mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '5', 'Do mundo são, por isso falam do mundo, e o mundo os ouve.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '6', 'Nós somos de Deus; aquele que conhece a Deus ouve-nos; aquele que não é de Deus não nos ouve. Nisto conhecemos nós o espírito da verdade e o espírito do erro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '7', 'Amados, amemo-nos uns aos outros; porque o amor é de Deus; e qualquer que ama é nascido de Deus e conhece a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '8', 'Aquele que não ama não conhece a Deus; porque Deus é amor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '9', 'Nisto se manifesta o amor de Deus para connosco: que Deus enviou seu Filho unigénito ao mundo, para que por ele vivamos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '10', 'Nisto está a caridade, não em que nós tenhamos amado a Deus, mas em que ele nos amou a nós, e enviou seu Filho para propiciação pelos nossos pecados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '11', 'Amados, se Deus assim nos amou, também nos devemos amar uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '12', 'Ninguém jamais viu a Deus; se nos amamos uns aos outros, Deus está em nós, e em nós é perfeita a sua caridade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '13', 'Nisto conhecemos que estamos nele, e ele em nós, pois que nos deu do seu Espírito,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '14', 'E vimos, e testificamos que o Pai enviou seu Filho para Salvador do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '15', 'Qualquer que confessar que Jesus é o Filho de Deus, Deus está nele, e ele em Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '16', 'E nós conhecemos, e cremos no amor que Deus nos tem. Deus é amor; e quem está em amor está em Deus, e Deus nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '17', 'Nisto é perfeito o amor para connosco, para que no dia do juízo tenhamos confiança; porque, qual ele é, somos nós também neste mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '18', 'No amor não há temor, antes o perfeito amor lança fora o temor; porque o temor tem consigo a pena, e o que teme não é perfeito em amor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '19', 'Nós o amamos a ele, porque ele nos amou primeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '20', 'Se alguém diz: Eu amo a Deus, e aborrece a seu irmão, é mentiroso. Pois quem não ama a seu irmão, ao qual viu, como pode amar a Deus, a quem não viu?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '4', '21', 'E dele temos este mandamento: que quem ama a Deus, ame também a seu irmão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '1', 'TODO aquele que crê que Jesus é o Cristo, é nascido de Deus; e todo aquele que ama ao que o gerou também ama ao que dele é nascido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '2', 'Nisto conhecemos que amamos os filhos de Deus, quando amamos a Deus e guardamos os seus mandamentos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '3', 'Porque esta é a caridade de Deus que guardemos os seus mandamentos; e os seus mandamentos não são pesados.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '4', 'Porque todo o que é nascido de Deus vence o mundo; e esta é a vitória que vence o mundo, a nossa fé.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '5', 'Quem é que vence o mundo, senão aquele que crê que Jesus é o Filho de Deus?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '6', 'Este é aquele que veio por água e sangue, isto é, Jesus Cristo: não só por água, mas por água e por sangue. E o Espírito é o que testifica, porque o Espírito é a verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '7', 'Porque três são os que testificam no céu: o Pai, a Palavra, e o Espírito Santo; e estes três são um.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '8', 'E três são os que testificam na terra: o Espírito, e a água, e o sangue; e estes três concordam num.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '9', 'Se recebemos o testemunho dos homens, o testemunho de Deus é maior; porque o testemunho de Deus é este, que de seu Filho testificou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '10', 'Quem crê no Filho de Deus, em si mesmo tem o testemunho: quem a Deus não crê mentiroso o fez; porquanto não creu no testemunho que Deus de seu Filho deu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '11', 'E o testemunho é este: que Deus nos deu a vida eterna; e esta vida está em seu Filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '12', 'Quem tem o Filho tem a vida quem não tem o Filho de Deus não tem a vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '13', 'Estas coisas vos escrevi, para que saibais que tendes a vida eterna, e para que creiais no nome do Filho de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '14', 'E esta é a confiança que temos nele, que, se pedirmos alguma coisa, segundo a sua vontade, ele nos ouve.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '15', 'E se sabemos que nos ouve em tudo o que pedirmos, sabemos que alcançamos as petições que lhe fizermos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '16', 'Se alguém vir pecar seu irmão, pecado que não é para morte, orará, e Deus dará a vida àqueles que não pecarem para morte. Há pecado para morte, e por esse não digo que ore.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '17', 'Toda a iniquidade é pecado: e há pecado que não é para morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '18', 'Sabemos que todo aquele que é nascido de Deus não peca; mas o que de Deus é gerado conserva-se a si mesmo, e o maligno não lhe toca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '19', 'Sabemos que somos de Deus, e que todo o mundo está no maligno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '20', 'E sabemos que já o Filho de Deus é vindo, e nos deu entendimento para conhecermos o que é verdadeiro; e no que é verdadeiro estamos, isto é, em seu Filho Jesus Cristo. Este é o verdadeiro Deus e a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('23', '5', '21', 'Filhinhos, guardai-vos dos ídolos. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '1', '¶ O ANCIÃO à senhora eleita, e a seus filhos, aos quais amo na verdade, e não somente eu, mas também todos os que têm conhecido a verdade,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '2', 'Por amor da verdade que está em nós e para sempre estará connosco;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '3', 'Graça, misericórdia, e paz, da parte de Deus Pai e da do SENHOR Jesus Cristo, o Filho do Pai, seja convosco na verdade e amor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '4', 'Muito me alegro por achar que alguns de teus filhos andam na verdade, assim como temos recebido o mandamento do Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '5', 'E agora, senhora, rogo-te, não como escrevendo-te um novo mandamento, mas aquele mesmo que desde o princípio tivemos: que nos amemos uns aos outros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '6', 'E a caridade é esta: que andemos segundo os seus mandamentos. Este é o mandamento, como já desde o princípio ouvistes, que andeis nele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '7', 'Porque já muitos enganadores entraram no mundo, os quais não confessam que Jesus Cristo veio em carne. Este tal é o enganador e o anti-cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '8', 'Olhai por vós mesmos, para que não percamos o que temos ganho, antes recebamos o inteiro galardão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '9', 'Todo aquele que prevarica, e não persevera na doutrina de Cristo, não tem a Deus: quem persevera na doutrina de Cristo, esse tem tanto ao Pai como ao Filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '10', 'Se alguém vem ter convosco, e não traz esta doutrina, não o recebais em casa, nem tampouco o saudeis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '11', 'Porque quem o saúda tem parte nas suas más obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '12', 'Tendo muito que escrever-vos, não quis fazê-lo com papel e tinta; mas espero ir ter convosco e falar de boca a boca, para que o nosso gozo seja cumprido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('24', '1', '13', 'Saúdam-te os filhos de tua irmã a eleita. Ámen');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '1', '¶ O PRESBÍTERO ao amado Gaio, a quem em verdade eu amo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '2', 'Amado, desejo que te vá bem em todas as coisas, e que tenhas saúde, assim como bem vai à tua alma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '3', 'Porque muito me alegrei quando os irmãos vieram, e testificaram da tua verdade, como tu andas na verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '4', 'Não tenho maior gozo do que este: o de ouvir que os meus filhos andam na verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '5', 'Amado, procedes fielmente em tudo o que fazes para com os irmãos, e para com os estranhos,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '6', 'Que em presença da igreja testificaram do teu amor: aos quais, se conduzires como é digno para com Deus, bem farás;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '7', 'Porque pelo seu Nome saíram, nada tomando dos gentios.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '8', 'Portanto aos tais devemos receber, para que sejamos cooperadores da verdade.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '9', 'Tenho escrito à igreja; mas Diótrefes, que procura ter entre eles o primado, não nos recebe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '10', 'Pelo que, se eu for, trarei à memória as obras que ele faz, proferindo contra nós palavras maliciosas; e, não contente com isto, não recebe os irmãos, e impede os que querem recebê-los, e os lança fora da igreja.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '11', 'Amado, não sigas o mal, mas o bem. Quem faz bem é de Deus; mas quem faz mal não tem visto a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '12', 'Todos dão testemunho de Demétrio, até a mesma verdade; e também nós testemunhamos; e vós bem sabeis que o nosso testemunho é verdadeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '13', 'Tinha muito que escrever, mas não quero escrever-te com tinta e pena.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('25', '1', '14', 'Espero, porém, ver-te brevemente e falaremos de boca a boca.15 Paz seja contigo. Os amigos te saúdam. Saúda os amigos pelo seu nome');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '1', '¶ JUDAS, servo de Jesus Cristo, e irmão de Tiago, aos chamados, queridos em Deus Pai, e conservados por Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '2', 'Misericórdia, e paz, e caridade vos sejam multiplicadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '3', 'Amados, procurando eu escrever-vos com toda a diligência acerca da salvação comum, tive por necessidade escrever-vos, e exortar-vos a batalhar pela fé que uma vez foi dada aos santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '4', 'Porque se introduziram alguns, que já antes estavam escritos para este mesmo juízo, homens ímpios, que convertem em dissolução a graça de Deus, e negam a Deus, único dominador e Senhor nosso, Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '5', 'Mas quero lembrar-vos, como a quem já uma vez soube isto, que, havendo o SENHOR salvo um povo, tirando-o da terra do Egipto, destruiu depois os que não creram;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '6', 'E aos anjos que não guardaram o seu principado, mas deixaram a sua própria habitação, reservou na escuridão e em prisões eternas até ao juízo daquele grande dia;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '7', 'Assim como Sodoma e Gomorra, e as cidades circunvizinhas, que, havendo-se corrompido como aqueles, e ido após outra carne, foram postas por exemplo, sofrendo a pena do fogo eterno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '8', 'E, contudo, também estes, semelhantemente adormecidos, contaminam a sua carne, e rejeitam a dominação, e vituperam as dignidades.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '9', 'Mas o arcanjo Miguel, quando contendia com o diabo, e disputava a respeito do corpo de Moisés, não ousou pronunciar juízo de maldição contra ele; mas disse: O SENHOR te repreenda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '10', 'Estes porém dizem mal do que não sabem; e, naquilo que naturalmente conhecem, como animais irracionais se corrompem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '11', 'Ai deles porque entraram pelo caminho de Caim, e foram levados pelo engano do prémio de Balaão, e pereceram na contradição de Coré.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '12', 'Estes são manchas em vossas festas de caridade, banqueteando-se convosco, e apascentando-se a si mesmos sem temor: são nuvens sem água, levadas pelos ventos de uma para outra parte: são como árvores murchas, infrutíferas, duas vezes mortas, desarraigadas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '13', 'Ondas impetuosas do mar, que escumam as suas mesmas abominações: estrelas errantes, para as quais está eternamente reservada a negrura das trevas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '14', 'E destes profetizou também Enoque, o sétimo depois de Adão, dizendo: Eis que é vindo o SENHOR com milhares de seus santos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '15', 'Para fazer juízo contra todos e condenar de entre eles todos os ímpios, por todas as suas obras de impiedade, que impiamente cometeram, e por todas as duras palavras que ímpios pecadores disseram contra ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '16', 'Estes são murmuradores, queixosos da sua sorte, andando segundo as suas concupiscências, e cuja boca diz coisas mui arrogantes, admirando as pessoas por causa do interesse.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '17', 'Mas vós, amados, lembrai-vos das palavras que vos foram preditas pelos apóstolos de nosso SENHOR Jesus Cristo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '18', 'Os quais vos diziam que no último tempo haverá escarnecedores que andariam segundo as suas ímpias concupiscências.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '19', 'Estes são os que causam divisões, sensuais, que não têm o Espírito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '20', 'Mas vós, amados, edificando-vos a vós mesmos sobre a vossa santíssima fé, orando no Espírito Santo,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '21', 'Conservai-vos a vós mesmos no amor de Deus, esperando a misericórdia de nosso SENHOR Jesus Cristo para a vida eterna.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '22', 'E apiedai-vos de alguns, que estão duvidosos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '23', 'E salvai alguns arrebatando-os do fogo; tende deles misericórdia com temor, aborrecendo até a roupa manchada da carne.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '24', 'Ora, àquele que é poderoso para vos guardar de tropeçar; e apresentar-vos irrepreensíveis, com alegria, perante a sua glória,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('26', '1', '25', 'Ao único Deus, Salvador nosso, por Jesus Cristo, nosso SENHOR, seja glória e majestade, domínio e poder, antes de todos os séculos, agora, e para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '1', '¶ REVELAÇÃO de Jesus Cristo, a qual Deus lhe deu, para mostrar aos seus servos as coisas que brevemente devem acontecer; e pelo seu anjo as enviou, e as notificou a João seu servo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '2', 'O qual testificou da palavra de Deus, e do testemunho de Jesus Cristo, e de tudo o que tem visto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '3', 'Bem-aventurado aquele que lê, e os que ouvem as palavras desta profecia, e guardam as coisas que nela estão escritas; porque o tempo está próximo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '4', 'JOÃO, às sete igrejas que estão na Ásia: Graça e paz seja convosco da parte daquele que é, e que era, e que há de vir, e da dos sete espíritos que estão diante do seu trono;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '5', 'E da parte de Jesus Cristo, que é fiel testemunha; o primogénito dos mortos e o príncipe dos reis da terra. Aquele que nos ama, e em seu sangue nos lavou dos nossos pecados,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '6', 'E nos fez reis e sacerdotes para Deus e seu Pai; a ele glória e poder para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '7', 'Eis que vem com as nuvens, e todo o olho o verá, até os mesmos que o traspassaram; e todas as tribos da terra se lamentarão sobre ele. Sim. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '8', 'Eu sou o Alfa e o Omega, o princípio e o fim, diz o SENHOR, que é e que era, e que há de vir, o Todo-poderoso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '9', 'Eu, João, que também sou vosso irmão, e companheiro na aflição, e no reino, e paciência de Jesus Cristo, estava na ilha chamada Patmos, por causa da palavra de Deus, e pelo testemunho de Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '10', 'Eu fui arrebatado em espírito no dia do SENHOR, e ouvi detrás de mim uma grande voz, como de trombeta,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '11', 'Que dizia: O que vês, escreve-o num livro, e envia-o às sete igrejas que estão na Ásia: A Éfeso, e a Smirna, e a Pérgano, e a Tiatira, e a Sardo, e a Filadélfia, e a Laodiceia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '12', 'E virei-me para ver quem falava comigo. E, virando-me, vi sete castiçais de ouro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '13', 'E no meio dos sete castiçais um semelhante ao Filho do homem, vestido até aos pés de um vestido comprido, e cingido pelos peitos com um cinto de ouro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '14', 'E a sua cabeça e cabelos eram brancos como lã branca, como a neve, e os seus olhos como chama de fogo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '15', 'E os seus pés, semelhantes a latão reluzente, como se tivessem sido refinados numa fornalha, e a sua voz como a voz de muitas águas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '16', 'E ele tinha na sua dextra sete estrelas; e da sua boca saía uma aguda espada de dois fios; e o seu rosto era como o sol, quando na sua força resplandece.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '17', 'E eu, quando o vi, caí a seus pés; como morto; e ele pôs sobre mim a sua dextra, dizendo-me: Não temas; Eu sou o primeiro e o último;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '18', 'E o que vivo e fui morto, mas eis aqui estou vivo para todo o sempre. Ámen. E tenho as chaves da morte e do inferno.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '19', 'Escreve as coisas que tens visto, e as que são, e as que depois destas hão de acontecer:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '1', '20', 'O mistério das sete estrelas, que viste na minha dextra, e dos sete castiçais de ouro. As sete estrelas são os anjos das sete igrejas, e os sete castiçais, que viste, são as sete igrejas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '1', 'ESCREVE ao anjo da igreja que está em Éfeso: Isto diz aquele que tem na sua dextra as sete estrelas, que anda no meio dos sete castiçais de ouro:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '2', 'Eu sei as tuas obras, e o teu trabalho, e a tua paciência, e que não podes sofrer os maus; e puseste à prova os que dizem ser apóstolos e o não são, e tu os achaste mentirosos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '3', 'E sofreste, e tens paciência; e trabalhaste pelo meu nome, e não te cansaste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '4', 'Tenho porém contra ti, que deixaste o teu primeiro amor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '5', 'Lembra-te pois donde caíste, e arrepende-te, e pratica as primeiras obras; quando não, brevemente a ti virei, e tirarei do seu lugar o teu castiçal, se não te arrependeres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '6', 'Tens, porém, isto: que aborreces as obras dos nicolaítas, as quais eu também aborreço.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '7', 'Quem tem ouvidos, ouça o que o Espírito diz às igrejas: Ao que vencer, dar-lhe-ei a comer da árvore da vida, que está no meio do paraíso de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '8', 'E ao anjo da igreja que está em Smirna, escreve: Isto diz o primeiro e o último, que foi morto, e reviveu:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '9', 'Eu sei as tuas obras, e tribulação, e pobreza (mas tu és rico), e a blasfémia dos que se dizem judeus, e não o são, mas são a sinagoga de Satanás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '10', 'Nada temas das coisas que hás de padecer. Eis que o diabo lançará alguns de vós na prisão, para que sejais tentados; e tereis uma tribulação de dez dias. Sê fiel até à morte, e dar-te-ei a coroa da vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '11', 'Quem tem ouvidos, ouça o que o Espírito diz às igrejas: O que vencer não receberá o dano da segunda morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '12', 'E ao anjo da igreja que está em Pérgamo escreve: Isto diz aquele que tem a espada aguda de dois fios:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '13', 'Eu sei as tuas obras, e onde habitas, que é onde está o trono de Satanás; e reténs o meu nome, e não negaste a minha fé, ainda nos dias de Antipas, minha fiel testemunha, o qual foi morto entre vós, onde Satanás habita.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '14', 'Mas umas poucas de coisas tenho contra ti: porque tens lá os que seguem a doutrina de Balaão, o qual ensinava Balaque a lançar tropeços diante dos filhos de Israel, para que comessem dos sacrifícios da idolatria e se prostituíssem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '15', 'Assim tens também os que seguem a doutrina dos nicolaítas: o que eu aborreço.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '16', 'Arrepende-te, pois, quando não em breve virei a ti, e contra eles batalharei com a espada da minha boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '17', 'Quem tem ouvidos, ouça o que o Espírito diz às igrejas: Ao que vencer darei a comer do maná escondido, e dar-lhe-ei uma pedra branca, e na pedra um novo nome escrito, o qual ninguém conhece senão aquele que o recebe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '18', 'E ao anjo da igreja de Tiatira escreve: Isto diz o Filho de Deus, que tem seus olhos como chama de fogo, e os pés semelhantes ao latão reluzente:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '19', 'Eu conheço as tuas obras, e a tua caridade, e o teu serviço, e a tua fé, e a tua paciência, e que as tuas últimas obras são mais do que as primeiras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '20', 'Mas tenho contra ti que toleras Jezabel, mulher que se diz profetiza, ensinar a enganar os meus servos, para que se prostituam e comam dos sacrifícios da idolatria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '21', 'E dei-lhe tempo para que se arrependesse da sua prostituição; e não se arrependeu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '22', 'Eis que a porei numa cama, e sobre os que adulteram com ela virá grande tribulação, se não se arrependerem das suas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '23', 'E ferirei de morte a seus filhos, e todas as igrejas saberão que eu sou aquele que sonda os rins e os corações. E darei a cada um de vós segundo as vossas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '24', 'Mas eu vos digo a vós, e aos restantes que estão em Tiatira, a todos quantos não têm esta doutrina, e não conheceram, como dizem, as profundezas de Satanás, que outra carga vos não porei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '25', 'Mas o que tendes retende-o até que eu venha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '26', 'E ao que vencer, e guardar até ao fim as minhas obras, eu lhe darei poder sobre as nações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '27', 'E com vara de ferro as regerá: e serão quebradas como vasos de oleiro; como também recebi de meu Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '28', 'E dar-lhe-ei a estrela da manhã.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '2', '29', 'Quem tem ouvidos, ouça o que o Espírito diz às igrejas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '1', 'E AO anjo da igreja que está em Sardo, escreve: Isto diz o que tem os sete Espíritos de Deus, e as sete estrelas: Eu sei as tuas obras, que tens nome de que vives, e estás morto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '2', 'Sê vigilante, e confirma os restantes, que estavam para morrer; porque não achei as tuas obras perfeitas diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '3', 'Lembra-te pois do que tens recebido e ouvido, e guarda-o, e arrepende-te. E, se não vigiares, virei sobre ti como um ladrão, e não saberás a que hora sobre ti virei.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '4', 'Mas também tens em Sardo algumas pessoas que não contaminaram seus vestidos, e comigo andarão de branco; porquanto são dignas disso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '5', 'O que vencer será vestido de vestes brancas, e de maneira nenhuma riscarei o seu nome do livro da vida; e confessarei o seu nome diante de meu Pai e diante dos seus anjos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '6', 'Quem tem ouvidos, ouça o que o Espírito diz às igrejas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '7', 'E ao anjo da igreja que está em Filadélfia escreve: Isto diz o que é santo, o que é verdadeiro, o que tem a chave de David; o que abre, e ninguém fecha; e fecha, e ninguém abre');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '8', 'Eu sei as tuas obras: Eis que diante de ti pus uma porta aberta, e ninguém a pode fechar; tendo pouca força, guardaste a minha palavra, e não negaste o meu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '9', 'Eis que eu farei aos da sinagoga de Satanás, aos que se dizem judeus e não são, mas mentem, eis que eu farei que venham, e adorem prostrados a teus pés, e saibam que eu te amo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '10', 'Como guardaste a palavra da minha paciência, também eu te guardarei da hora da tentação que há de vir sobre todo o mundo, para tentar os que habitam na terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '11', 'Eis que venho sem demora; guarda o que tens, para que ninguém tome a tua coroa.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '12', 'A quem vencer, eu o farei coluna no templo do meu Deus, e dele nunca sairá; e escreverei sobre ele o nome do meu Deus, e o nome da cidade do meu Deus, a nova Jerusalém, que desce do céu, do meu Deus, e também o meu novo nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '13', 'Quem tem ouvidos, ouça o que o Espírito diz às igrejas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '14', 'E ao anjo da igreja que está em Laodiceia escreve: Isto diz o Ámen, a testemunha fiel e verdadeira, o princípio da criação de Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '15', 'Eu sei as tuas obras, que nem és frio nem quente: oxalá foras frio ou quente!');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '16', 'Assim, porque és morno, e não és frio nem quente, vomitar-te-ei da minha boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '17', 'Como dizes: Rico sou, e estou enriquecido, e de nada tenho falta; e não sabes que és um desgraçado, e miserável, e pobre, e cego, e nu;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '18', 'Aconselho-te que de mim compres ouro provado no fogo, para que te enriqueças; e vestidos brancos, para que te vistas, e não apareça a vergonha da tua nudez; e que unjas os teus olhos com colírio, para que vejas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '19', 'Eu repreendo e castigo a todos quantos amo: sê pois zeloso, e arrepende-te.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '20', 'Eis que estou à porta, e bato: se alguém ouvir a minha voz, e abrir a porta, entrarei em sua casa, e com ele cearei, e ele comigo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '21', 'Ao que vencer lhe concederei que se assente comigo no meu trono; assim como eu venci, e me assentei com meu Pai no seu trono.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '3', '22', 'Quem tem ouvidos, ouça o que o Espírito diz às igrejas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '1', 'DEPOIS destas coisas, olhei, e eis que estava uma porta aberta no céu: e a primeira voz, que como de trombeta ouvira falar comigo, disse: Sobe aqui, e mostrar-te-ei as coisas que depois destas devem acontecer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '2', 'E logo fui arrebatado em espírito, e eis que um trono estava posto no céu, e um assentado sobre o trono.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '3', 'E o que estava assentado era, na aparência, semelhante à pedra jaspe e sardónica; e o arco celeste estava ao redor do trono, e parecia semelhante à esmeralda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '4', 'E ao redor do trono havia vinte e quatro tronos; e vi assentados sobre os tronos vinte e quatro anciãos vestidos de vestidos brancos; e tinham sobre suas cabeças coroas de ouro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '5', 'E do trono saíam relâmpagos, e trovões, e vozes; e diante do trono ardiam sete lâmpadas de fogo, as quais são os sete Espíritos de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '6', 'E havia diante do trono um como mar de vidro, semelhante ao cristal. E no meio do trono, e ao redor do trono, quatro animais cheios de olhos, por diante e por detrás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '7', 'E o primeiro animal era semelhante a um leão, e o segundo animal semelhante a um bezerro, e tinha o terceiro animal o rosto como de homem, e o quarto animal era semelhante a uma águia voando.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '8', 'E os quatro animais tinham, cada um de per si, seis asas, e ao redor, e por dentro, estavam cheios de olhos; e não descansavam nem de dia nem de noite, dizendo: Santo, Santo, Santo é o SENHOR Deus, o Todo poderoso, que era, e que é e que há de vir.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '9', 'E, quando os animais davam glória, e honra, e acções de graças ao que estava assentado sobre o trono, ao que vive para todo o sempre,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '10', 'Os vinte e quatro anciãos prostravam-se diante do que estava assentado sobre o trono, e adoravam o que vive para todo o sempre; e lançavam as suas coroas diante do trono, dizendo:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '4', '11', 'Digno és, Senhor de receber glória, e honra, e poder; porque tu criaste todas as coisas, e por tua vontade são e foram criadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '1', 'E VI na dextra do que estava assentado sobre o trono um livro escrito por dentro e por fora, selado com sete selos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '2', 'E vi um anjo forte, bradando com grande voz: Quem é digno de abrir o livro e de desatar os seus selos?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '3', 'E ninguém no céu, nem na terra, nem debaixo da terra, podia abrir o livro, nem olhar para ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '4', 'E eu chorava muito, porque ninguém fora achado digno de abrir o livro, nem de o ler, nem de olhar para ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '5', 'E disse-me um dos anciãos: Não chores; eis aqui o Leão da tribo de Judá, a raiz de David, que venceu, para abrir o livro e desatar os seus sete selos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '6', 'E olhei, e eis que estava no meio do trono e dos quatro animais viventes e entre os anciãos um Cordeiro, como havendo sido morto, e tinha sete pontas e sete olhos, que são os sete Espíritos de Deus enviados a toda a terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '7', 'E veio, e tomou o livro da dextra do que estava assentado no trono.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '8', 'E, havendo tomado o livro, os quatro animais e os vinte e quatro anciãos prostraram-se diante do Cordeiro, tendo todos eles harpas e salvas de ouro cheias de incenso, que são as orações dos santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '9', 'E cantavam um novo cântico, dizendo: Digno és de tomar o livro, e de abrir os seus selos; porque foste morto, e com o teu sangue compraste para Deus homens de toda a tribo, e língua, e povo, e nação;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '10', 'E para o nosso Deus os fizeste reis e sacerdotes; e eles reinarão sobre a terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '11', 'E olhei, e ouvi a voz de muitos anjos ao redor do trono, e dos animais, e dos anciãos; e era o número deles milhões de milhões, e milhares de milhares,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '12', 'Que com grande voz diziam: Digno é o Cordeiro, que foi morto, de receber o poder, e riquezas, e sabedoria, e força, e honra, e glória, e acções de graças.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '13', 'E ouvi a toda a criatura que está no céu, e na terra, e debaixo da terra, e que está no mar, e a todas as coisas que neles há, dizer: Ao que está assentado sobre o trono, e ao Cordeiro, sejam dadas acções de graças; e honra, e glória, e poder para todo o sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '5', '14', 'E os quatro animais diziam: Ámen. E os vinte e quatro anciãos prostraram-se, e adoraram ao que vive para todo o sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '1', 'E, HAVENDO o Cordeiro aberto um dos selos, olhei, e ouvi um dos quatro animais, que dizia como em voz de trovão: Vem, e vê.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '2', 'E olhei, e eis um cavalo branco e o que estava assentado sobre ele tinha um arco; e foi-lhe dada uma coroa, e saiu vitorioso, e para vencer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '3', 'E, havendo aberto o segundo selo, ouvi o segundo animal, dizendo: Vem, e vê.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '4', 'E saiu outro cavalo, vermelho; e ao que estava assentado sobre ele foi dado que tirasse a paz da terra, e que se matassem uns aos outros; e foi-lhe dada uma grande espada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '5', 'E, havendo aberto o terceiro selo, ouvi dizer ao terceiro animal: Vem, e vê. E olhei, e eis um cavalo preto e o que sobre ele estava assentado tinha uma balança na mão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '6', 'E ouvi uma voz no meio dos quatro animais, que dizia: Uma medida de trigo por um dinheiro e três medidas de cevada por um dinheiro; e não danifiques o azeite e o vinho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '7', 'E, havendo aberto o quarto selo, ouvi a voz do quarto animal, que dizia: Vem e vê.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '8', 'E olhei, e eis um cavalo amarelo, e o que estava assentado sobre ele tinha por nome Morte; e o inferno o seguia; e foi-lhes dado poder para matar a quarta parte da terra, com espada, e com fome, e com peste, e com as feras da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '9', 'E, havendo aberto o quinto selo, vi debaixo do altar as almas dos que foram mortos por amor da palavra de Deus e por amor do testemunho que deram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '10', 'E clamavam com grande voz, dizendo: Até quando, ó verdadeiro e santo Dominador, não julgas e vingas o nosso sangue dos que habitam sobre a terra?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '11', 'E foram dadas a cada um compridas vestes brancas e foi-lhes dito que repousassem ainda um pouco de tempo, até que também se completasse o número de seus conservos e seus irmãos que haviam de ser mortos como eles foram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '12', 'E, havendo aberto o sexto selo olhei, e eis que houve um grande tremor de terra; e o sol tornou-se negro como saco de cilício, e a lua tornou-se como sangue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '13', 'E as estrelas do céu caíram sobre a terra, como quando a figueira lança de si os seus figos verdes, abalada por um vento forte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '14', 'E o céu retirou-se como um livro que se enrola; todos os montes e ilhas foram removidos dos seus lugares.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '15', 'E os reis da terra, e os grandes, e os ricos, e os tribunos, e os poderosos, e todo o servo, e todo o livre, se esconderam nas cavernas e nas rochas das montanhas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '16', 'E diziam aos montes e aos rochedos: Caí sobre nós, e escondei-nos do rosto daquele que está assentado sobre o trono, e da ira do Cordeiro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '6', '17', 'Porque é vindo o grande dia da sua ira; e quem poderá subsistir?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '1', 'E DEPOIS destas coisas vi quatro anjos que estavam sobre os quatro cantos da terra, retendo os quatro ventos da terra, para que nenhum vento soprasse sobre a terra, nem sobre o mar, nem contra árvore alguma.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '2', 'E vi outro anjo subir da banda do sol nascente e que tinha o selo do Deus vivo; e clamou com grande voz aos quatro anjos, a quem fora dado o poder de danificar a terra e o mar,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '3', 'Dizendo: Não danifiqueis a terra, nem o mar, nem as árvores, até que hajamos assinalado nas suas testas os servos do nosso Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '4', 'E ouvi o número dos assinalados, e eram cento e quarenta e quatro mil assinalados, de todas as tribos dos filhos de Israel:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '5', 'Da tribo de Judá, havia doze mil assinalados; da tribo de Rúben, doze mil, da tribo de Gad, doze mil;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '6', 'Da tribo de Aser, doze mil; da tribo de Naftali, doze mil, da tribo de Manassés, doze mil;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '7', 'Da tribo de Simeão, doze mil; da tribo de Leví, doze mil; da tribo de Issacar, doze mil;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '8', 'Da tribo de Zabulon doze mil; da tribo de José, doze mil; da tribo de Benjamim, doze mil.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '9', 'Depois destas coisas olhei, e eis aqui uma multidão, a qual ninguém podia contar, de todas as nações, e tribos, e povos, e línguas, que estavam diante do trono, e perante o Cordeiro, trajando vestidos brancos e com palmas nas suas mãos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '10', 'E clamavam com grande voz, dizendo: Salvação ao nosso Deus, que está assentado no trono, e ao Cordeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '11', 'E todos os anjos estavam ao redor do trono, e dos anciãos e dos quatro animais; e prostraram-se diante do trono sobre seus rostos, e adoraram a Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '12', 'Dizendo: Ámen. Louvor, e glória, e sabedoria, e acção de graças, e honra, e poder, e força ao nosso Deus, para todo o sempre. Ámen.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '13', 'E um dos anciãos me falou, dizendo: Estes que estão vestidos de vestidos brancos, quem são, e donde vieram?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '14', 'E eu disse-lhe: Senhor, tu sabes. E ele disse-me: Estes são os que vieram de grande tribulação, e lavaram os seus vestidos e os branquearam no sangue do Cordeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '15', 'Por isso estão diante do trono de Deus, e o servem de dia e de noite no seu templo; e aquele que está assentado sobre o trono os cobrirá com a sua sombra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '16', 'Nunca mais terão fome, nunca mais terão sede; nem sol nem calma alguma cairá sobre eles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '7', '17', 'Porque o Cordeiro que está no meio do trono os apascentará e lhes servirá de guia para as fontes das águas da vida; e Deus limpará de seus olhos a lágrima.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '1', 'E,HAVENDO aberto o sétimo selo, fez-se silêncio no céu quase por meia hora.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '2', 'E vi os sete anjos que estavam diante de Deus, e foram-lhes dadas sete trombetas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '3', 'E veio outro anjo, e pôs-se junto ao altar, tendo um incensário de ouro; e foi-lhe dado muito incenso, para o pôr com as orações de todos os santos sobre o altar de ouro, que está diante do trono.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '4', 'E o fumo do incenso subiu com as orações dos santos desde a mão do anjo até diante de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '5', 'E o anjo tomou o incensário, e o encheu do fogo do altar, e o lançou sobre a terra; e houve depois vozes, e trovões, e relâmpagos e terremotos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '6', 'E os sete anjos, que tinham as sete trombetas, prepararam-se para tocá-las.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '7', 'E o primeiro anjo tocou a sua trombeta, e houve saraiva, e fogo misturado com sangue, e foram lançados na terra, que foi queimada na sua terça parte; queimou-se a terça parte das árvores, e toda a erva verde foi queimada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '8', 'E o segundo anjo tocou a trombeta; e foi lançada no mar uma coisa como um grande monte ardendo em fogo, e tornou-se em sangue a terça parte do mar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '9', 'E morreu a terça parte das criaturas que tinham vida no mar; e perdeu-se a terça parte das naus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '10', 'E o terceiro anjo tocou a sua trombeta, e caiu do céu uma grande estrela, ardendo como uma tocha, e caiu sobre a terça parte dos rios, e sobre as fontes das águas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '11', 'E o nome da estrela era Absinto, e a terça parte das águas tornou-se em absinto, e muitos homens morreram das águas, porque se tornaram amargas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '12', 'E o quarto anjo tocou a sua trombeta, e foi ferida a terça parte do sol, e a terça parte da lua, e a terça parte das estrelas; para que a terça parte deles se escurecesse, e a terça parte do dia não brilhasse, e semelhantemente a noite.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '8', '13', 'E olhei, e ouvi um anjo, que voava pelo meio do céu, dizendo com grande voz: Ai! ai! ai! dos que habitam sobre a terra! por causa das outras vozes das trombetas dos três anjos que hão de ainda tocar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '1', 'E O quinto anjo tocou a sua trombeta, e vi uma estrela que do céu caiu na terra; e foi-lhe dada a chave do poço do abismo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '2', 'E abriu o poço do abismo, e subiu fumo do poço, como o fumo de uma grande fornalha, e com o fumo do poço escureceu-se o sol e o ar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '3', 'E do fumo vieram gafanhotos sobre a terra; e foi-lhes dado poder, como o poder que têm os escorpiões da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '4', 'E foi-lhes dito que não fizessem dano à erva da terra, nem a verdura alguma, nem a árvore alguma, mas somente aos homens que não têm nas suas testas o sinal de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '5', 'E foi-lhes permitido, não que os matassem; mas que por cinco meses os atormentassem; e o seu tormento era semelhante ao tormento do escorpião, quando fere o homem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '6', 'E naqueles dias os homens buscarão a morte, e não a acharão; e desejarão morrer, e a morte fugirá deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '7', 'E o parecer dos gafanhotos era semelhante ao de cavalos aparelhados para a guerra; e sobre as suas cabeças havia umas como coroas semelhantes ao ouro; e os seus rostos eram como rostos de homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '8', 'E tinham cabelos como cabelos de mulheres, e os seus dentes eram como de leões.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '9', 'E tinham couraças como couraças de ferro; e o ruído das suas asas era como o ruído de carros, quando muitos cavalos correm ao combate.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '10', 'E tinham caudas semelhantes às dos escorpiões, e aguilhões nas suas caudas; e o seu poder era para danificar os homens por cinco meses.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '11', 'E tinham sobre si rei, o anjo do abismo; em hebreu era o seu nome Abadon, em grego Apolion.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '12', 'Passado é já um ai; eis que depois disso vêem ainda dois ais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '13', 'E tocou o sexto anjo a sua trombeta, e ouvi uma voz que vinha das quatro pontas do altar de ouro, que estava diante de Deus,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '14', 'A qual dizia ao sexto anjo, que tinha a trombeta: Solta os quatro anjos, que estão presos junto ao grande rio Eufrates.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '15', 'E foram soltos os quatro anjos, que estavam preparados para a hora, e dia e mês, e ano, a fim de matarem a terça parte dos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '16', 'E o número dos exércitos dos cavaleiros era de duzentos milhões; e ouvi o número deles.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '17', 'E assim vi os cavalos nesta visão; e os que sobre eles cavalgavam tinham couraças de fogo, e de jacinto, e de enxofre; e as cabeças dos cavalos eram como cabeças de leões; e de suas bocas saía fogo e fumo e enxofre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '18', 'Por estas três pragas foi morta a terça parte dos homens, isto é pelo fogo, pelo fumo, e pelo enxofre, que saía das suas bocas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '19', 'Porque o poder dos cavalos está na sua boca e nas suas caudas. Porquanto as suas caudas são semelhantes a serpentes, e têm cabeças, e com elas danificam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '20', 'E os outros homens, que não foram mortos por estas pragas, não se arrependeram das obras de suas mãos, para não adorarem os demónios, e os ídolos de ouro, e de prata, e de bronze, e de pedra, e de madeira, que nem podem ver, nem ouvir, nem andar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '9', '21', 'E não se arrependeram dos seus homicídios, nem das suas feitiçarias, nem da sua prostituição, nem das suas ladroíces.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '1', 'E VI outro anjo forte, que descia do céu, vestido de uma nuvem; e por cima da sua cabeça estava o arco celeste, e o seu rosto era como o sol, e os seus pés como colunas de fogo;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '2', 'E tinha na sua mão um livrinho aberto, e pôs o seu pé direito sobre o mar, e o esquerdo sobre a terra;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '3', 'E clamou com grande voz, como quando brama o leão: e, havendo clamado, os sete trovões fizeram soar as suas vozes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '4', 'E, sendo ouvidas dos sete trovões as suas vozes, eu ia escrevê-las, e ouvi uma voz do céu, que me dizia: Sela o que os sete trovões falaram, e não o escrevas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '5', 'E o anjo que vi estar sobre o mar e sobre a terra levantou a sua mão ao céu,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '6', 'E jurou por aquele que vive para todo o sempre, o qual criou o céu e o que nele há, e a terra e o que nela há, e o mar e o que nele há, que não haveria mais demora;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '7', 'Mas nos dias da voz do sétimo anjo, quando tocar a sua trombeta, se cumprirá o segredo de Deus, como anunciou aos profetas, seus servos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '8', 'E a voz que eu do céu tinha ouvido tornou a falar comigo, e disse: Vai e toma o livrinho aberto da mão do anjo que está em pé sobre o mar e sobre a terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '9', 'E fui ao anjo, dizendo-lhe: Dá-me o livrinho. E ele disse-me: Toma-o, e come-o, e ele fará amargo o teu ventre, mas na tua boca será doce como mel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '10', 'E tomei o livrinho da mão do anjo, e comi-o; e na minha boca era doce como mel; e, havendo-o comido o meu ventre ficou amargo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '10', '11', 'E ele disse-me: Importa que profetizes outra vez a muitos povos, e nações, e línguas e reis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '1', 'E FOI-ME dada uma cana semelhante a uma vara: e chegou o anjo, e disse: Levanta-te, e mede o templo de Deus, e o altar, e os que nele adoram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '2', 'E deixa o átrio que está fora do templo, e não o meças; porque foi dado às nações, e pisarão a cidade santa por quarenta e dois meses.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '3', 'E darei poder às minhas duas testemunhas, e profetizarão por mil duzentos e sessenta dias, vestidas de saco.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '4', 'Estas são as duas oliveiras e os dois castiçais que estão diante do Deus da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '5', 'E, se alguém lhes quiser fazer mal, fogo sairá da sua boca, e devorará os seus inimigos; e, se alguém lhes quiser fazer mal, importa que assim seja morto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '6', 'Estes têm poder para fechar o céu, para que não chova, nos dias da sua profecia; e têm poder sobre as águas para convertê-las em sangue; e para ferir a terra com toda a sorte de pragas, todas quantas vezes quiserem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '7', 'E, quando acabarem o seu testemunho, a besta que sobe do abismo lhes fará guerra, e os vencerá, e os matará.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '8', 'E jazerão os seus corpos mortos na praça da grande cidade que espiritualmente se chama Sodoma e Egipto, onde o seu Senhor também foi crucificado.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '9', 'E homens de vários povos, e tribos, e línguas, e nações verão seus corpos mortos por três dias e meio, e não permitirão que os seus corpos mortos sejam postos em sepulcros.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '10', 'E os que habitam na terra se regozijarão sobre eles, e se alegrarão, e mandarão presentes uns aos outros; porquanto estes dois profetas tinham atormentado os que habitam sobre a terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '11', 'E depois daqueles três dias e meio o espírito de vida, vindo de Deus, entrou neles; e puseram-se sobre seus pés, e caiu grande temor sobre os que os viram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '12', 'E ouviram uma grande voz do céu, que lhes dizia: Subi cá. E subiram ao céu em uma nuvem: e os seus inimigos os viram.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '13', 'E naquela mesma hora houve um grande terremoto, e caiu a décima parte da cidade, e no terremoto foram mortos sete mil homens; e os demais ficaram muito atemorizados, e deram glória ao Deus do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '14', 'É passado o segundo; ai eis que o terceiro ai cedo virá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '15', 'E tocou o sétimo anjo a sua trombeta, e houve no céu grandes vozes, que diziam: Os reinos do mundo vieram a ser de nosso SENHOR e do seu Cristo, e ele reinará para todo o sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '16', 'E os vinte e quatro anciãos, que estão assentados em seus tronos diante de Deus, prostraram-se sobre seus rostos e adoraram a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '17', 'Dizendo: Graças te damos, SENHOR Deus Todo-poderoso, que és, e que eras, e que hás de vir, que tomaste o teu grande poder, e reinaste.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '18', 'E iraram-se as nações, e veio a tua ira, e o tempo dos mortos, para que sejam julgados, e o tempo de dares o galardão aos profetas, teus servos, e aos santos, e aos que temem o teu nome, a pequenos e a grandes, e o tempo de destruíres os que destroem a terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '11', '19', 'E abriu-se no céu o templo de Deus, e a arca do seu concerto foi vista no seu templo: e houve relâmpagos, e vozes, e trovões, e terremotos e grande saraiva.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '1', 'E VIU-SE um grande sinal no céu uma mulher vestida do sol, tendo a lua debaixo dos seus pés, e uma coroa de doze estrelas sobre a sua cabeça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '2', 'E estava grávida, e com dores de parto, e gritava com ânsias de dar à luz.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '3', 'E viu-se outro sinal no céu; e eis que era um grande dragão vermelho, que tinha sete cabeças e dez chifres, e sobre as suas cabeças sete diademas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '4', 'E a sua cauda levou após si a terça parte das estrelas do céu, e lançou-as sobre a terra; e o dragão parou diante da mulher que havia de dar à luz para que, dando ela à luz, lhe tragasse o filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '5', 'E deu à luz um filho, um varão que há de reger todas as nações com vara de ferro; e o seu filho foi arrebatado para Deus e para o seu trono.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '6', 'E a mulher fugiu para o deserto, onde já tinha lugar preparado por Deus, para que ali fosse alimentada durante mil duzentos e sessenta dias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '7', 'E houve batalha no céu: Miguel e os seus anjos batalhavam contra o dragão, e batalhava o dragão e os seus anjos;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '8', 'Mas não prevaleceram, nem mais o seu lugar se achou nos céus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '9', 'E foi precipitado o grande dragão, a antiga serpente, chamada o Diabo, e Satanás, que engana todo o mundo; ele foi precipitado na terra, e os seus anjos foram lançados com ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '10', 'E ouvi uma grande voz no céu, que dizia: Agora chegada está a salvação, e a força, e o reino do nosso Deus, e o poder do seu Cristo; porque já o acusador de nossos irmãos é derribado, o qual diante do nosso Deus os acusava de dia e de noite.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '11', 'E eles o venceram pelo sangue do Cordeiro e pela palavra do seu testemunho; e não amaram as suas vidas até à morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '12', 'Pelo que alegrai-vos, ó céus, e vós que neles habitais. Ai dos que habitam na terra e no mar; porque o diabo desceu a vós, e tem grande ira, sabendo que já tem pouco tempo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '13', 'E, quando o dragão viu que fora lançado na terra, perseguiu a mulher que dera à luz o varão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '14', 'E foram dadas à mulher duas asas de grande águia, para que voasse para o deserto, ao seu lugar, onde é sustentada por um tempo, e tempos, e metade de um tempo, fora da vista da serpente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '15', 'E a serpente lançou da sua boca, atrás da mulher, água como um rio, para que pela corrente a fizesse arrebatar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '16', 'E a terra ajudou a mulher; e a terra abriu a sua boca, e tragou o rio que o dragão lançara da sua boca.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '12', '17', 'E o dragão irou-se contra a mulher, e foi fazer guerra ao resto da sua semente, os que guardam os mandamentos de Deus, e têm o testemunho de Jesus Cristo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '1', 'E EU pus-me sobre a areia do mar, e vi subir do mar uma besta que tinha sete cabeças e dez chifres, e sobre os seus chifres dez diademas, e sobre as suas cabeças um nome de blasfémia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '2', 'E a besta que vi era semelhante ao leopardo, e os seus pés como os de urso, e a sua boca como a de leão; e o dragão deu-lhe o seu poder, e o seu trono, e grande poderio.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '3', 'E vi uma das suas cabeças como ferida de morte, e a sua chaga mortal foi curada; e toda a terra se maravilhou após a besta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '4', 'E adoraram o dragão que deu à besta o seu poder; e adoraram a besta, dizendo: Quem é semelhante à besta? Quem poderá batalhar contra ela?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '5', 'E foi-lhe dada uma boca para proferir grandes coisas e blasfémias; e deu-se-lhe poder para continuar por quarenta e dois meses.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '6', 'E abriu a sua boca em blasfémias contra Deus, para blasfemar do seu nome, e do seu tabernáculo, e dos que habitam no céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '7', 'E foi-lhe permitido fazer guerra aos santos, e vencê-los; e deu-se-lhe poder sobre toda a tribo, e língua e nação.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '8', 'E adoraram-na todos os que habitam sobre a terra, esses cujos nomes não estão escritos no livro da vida do Cordeiro que foi morto desde a fundação do mundo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '9', 'Se alguém tem ouvidos ouça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '10', 'Se alguém leva em cativeiro, em cativeiro irá: se alguém matar à espada, necessário é que à espada seja morto. Aqui está a paciência e a fé dos santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '11', 'E vi subir da terra outra besta, e tinha dois chifres semelhantes aos de um cordeiro; e falava como o dragão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '12', 'E exerce todo o poder da primeira besta na sua presença, e faz que a terra e os que nela habitam adorem a primeira besta, cuja chaga mortal fora curada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '13', 'E faz grandes sinais, de maneira que até fogo faz descer do céu à terra, à vista dos homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '14', 'E engana os que habitam na terra com sinais que lhe foi permitido que fizesse em presença da besta, dizendo aos que habitam na terra que fizessem uma imagem à besta que recebera a ferida da espada e vivia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '15', 'E foi-lhe concedido que desse espírito à imagem da besta, para que também a imagem da besta falasse, e fizesse que fossem mortos todos os que não adorassem a imagem da besta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '16', 'E faz que a todos, pequenos e grandes, ricos e pobres, livres e servos, lhes seja posto um sinal na sua mão direita, ou nas suas testas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '17', 'Para que ninguém possa comprar ou vender, senão aquele que tiver o sinal, ou o nome da besta, ou o número do seu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '13', '18', 'Aqui há sabedoria. Aquele que tem entendimento, calcule o número da besta; porque é o número de um homem, e o seu número é seiscentos e sessenta e seis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '1', 'E OLHEI, e eis que estava o Cordeiro sobre o monte de Sião, e com ele cento e quarenta e quatro mil, que em suas testas tinham escrito o nome dele e o de seu Pai.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '2', 'E ouvi uma voz do céu, como a voz de muitas águas, e como a voz de um grande trovão; e ouvi uma voz de harpistas, que tocavam com as suas harpas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '3', 'E cantavam um como cântico novo diante do trono, e diante dos quatro animais e dos anciãos; e ninguém podia aprender aquele cântico, senão os cento e quarenta e quatro mil que foram comprados da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '4', 'Estes são os que não estão contaminados com mulheres: porque são virgens. Estes são os que seguem o Cordeiro para onde quer que vá. Estes são os que de entre os homens foram comprados como primícias para Deus e para o Cordeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '5', 'E na sua boca não se achou engano; porque são irrepreensíveis diante do trono de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '6', 'E vi outro anjo voar pelo meio do céu, e tinha o evangelho eterno, para o proclamar aos que habitam sobre a terra, e a toda a nação, e tribo, e língua, e povo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '7', 'Dizendo com grande voz: Temei a Deus, e dai-lhe glória; porque vinda é a hora do seu juízo. E adorai aquele que fez o céu, e a terra, e o mar, e as fontes das águas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '8', 'E outro anjo seguiu dizendo: Caiu, caiu Babilónia, aquela grande cidade, que a todas as nações deu a beber do vinho da ira da sua prostituição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '9', 'E seguiu-os o terceiro anjo, dizendo com grande voz: Se alguém adorar a besta, e a sua imagem, e receber o sinal na sua testa, ou na sua mão,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '10', 'Também o tal beberá do vinho da ira de Deus, que se deitou, não misturado, no cálix da sua ira; e será atormentado com fogo e enxofre diante dos santos anjos e diante do Cordeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '11', 'E o fumo do seu tormento sobe para todo o sempre; e não têm repouso nem de dia nem de noite os que adoram a besta e a sua imagem, e aquele que receber o sinal do seu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '12', 'Aqui está a paciência dos santos: aqui estão os que guardam os mandamentos de Deus e a fé de Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '13', 'E ouvi uma voz do céu, que me dizia: Escreve: Bem-aventurados os mortos que desde agora morrem no SENHOR. Sim, diz o Espírito, para que descansem dos seus trabalhos, e as suas obras os sigam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '14', 'E olhei, e eis uma nuvem branca, e assentado sobre a nuvem um semelhante ao Filho do homem, que tinha sobre a sua cabeça uma coroa de ouro, e na sua mão uma foice aguda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '15', 'E outro anjo saiu do templo, clamando com grande voz ao que estava assentado sobre a nuvem: Lança a tua foice, e sega; é já vinda a hora de segar, porque já a seara da terra está madura.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '16', 'E aquele que estava assentado sobre a nuvem meteu a sua foice à terra e a terra foi segada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '17', 'E saiu do templo, que está no céu, outro anjo, o qual também tinha uma foice aguda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '18', 'E saiu do altar outro anjo, que tinha poder sobre o fogo, e clamou com grande voz ao que tinha a foice aguda, dizendo: Lança a tua foice aguda, e vindima, os cachos da vinha da terra, porque já as suas uvas estão maduras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '19', 'E o anjo meteu a sua foice à terra e vindimou as uvas da vinha da terra, e lançou-as no grande lagar da ira de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '14', '20', 'E o lagar foi pisado fora da cidade, e saiu sangue do lagar até aos freios dos cavalos, pelo espaço de mil e seiscentos estádios');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '15', '1', 'E VI outro grande e admirável sinal no céu: sete anjos, que tinham as sete últimas pragas; porque nelas é consumada a ira de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '15', '2', 'E vi um como mar de vidro misturado com fogo; e também os que saíram vitoriosos da besta, e da sua imagem, e do seu sinal, e do número do seu sinal, e do número do seu nome, que estavam junto ao mar de vidro, e tinham as harpas de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '15', '3', 'E cantavam o cântico de Moisés, servo de Deus, e o cântico do Cordeiro, dizendo: Grandes e maravilhosas são as tuas obras, SENHOR Deus Todo-Poderoso! Justos e verdadeiros são os teus caminhos, ó Rei dos santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '15', '4', 'Quem te não temerá, ó Senhor, e não magnificará o teu nome? Porque só tu és santo; por isso todas as nações virão, e se prostrarão diante de ti, porque os teus juízos são manifestos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '15', '5', 'E depois disto olhei, e eis que o templo do tabernáculo do testemunho se abriu no céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '15', '6', 'E os sete anjos que tinham as sete pragas saíram do templo, vestidos de linho puro e resplandecente, e cingidos com cintos de ouro pelos peitos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '15', '7', 'E um dos quatro animais deu aos sete anjos sete salvas de ouro, cheias da ira de Deus, que vive para todo o sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '15', '8', 'E o templo encheu-se com o fumo da glória de Deus e do seu poder; e ninguém podia entrar no templo, até que se consumassem as sete pragas dos sete anjos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '1', 'E OUVI, vinda do templo, uma grande voz, que dizia aos sete anjos: Ide, e derramai sobre a terra as sete salvas da ira de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '2', 'E foi o primeiro, e derramou a sua salva sobre a terra, e fez-se uma chaga má e maligna nos homens que tinham o sinal da besta e que adoravam a sua imagem.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '3', 'E o segundo anjo derramou a sua salva no mar, que se tornou em sangue como de um morto, e morreu no mar toda a alma vivente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '4', 'E o terceiro anjo derramou a sua taça nos rios e nas fontes das águas, e se tornaram em sangue.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '5', 'E ouvi o anjo das águas, que dizia Justo és tu, ó Senhor, que és, e que eras, e santo és, porque julgaste estas coisas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '6', 'Visto como derramaram o sangue dos santos e dos profetas, também tu lhes deste o sangue a beber; porque disto são merecedores.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '7', 'E ouvi outro do altar que dizia: Na verdade, SENHOR Deus Todo-Poderoso, verdadeiros e justos são os teus juízos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '8', 'E o quarto anjo derramou a sua taça sobre o sol, e foi-lhe permitido que abrasasse os homens com fogo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '9', 'E os homens foram abrasados com grandes calores, e blasfemaram o nome de Deus, que tem poder sobre estas pragas; e não se arrependeram para lhe darem glória.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '10', 'E o quinto anjo derramou a sua taça sobre o trono da besta, e o seu reino se fez tenebroso; e eles mordiam as suas línguas de dor.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '11', 'E por causa das suas dores, e por causa das suas chagas, blasfemaram do Deus do céu; e não se arrependeram das suas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '12', 'E o sexto anjo derramou a sua taça sobre o grande rio Eufrates; e a sua água secou-se, para que se preparasse o caminho dos reis do oriente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '13', 'E da boca do dragão, e da boca da besta, e da boca do falso profeta vi sair três espíritos imundos, semelhantes a rãs.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '14', 'Porque são espíritos de demónios, que fazem prodígios; os quais vão ao encontro dos reis de todo o mundo, para os congregar para a batalha, naquele grande dia do Deus Todo-Poderoso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '15', 'Eis que venho como ladrão. Bem-aventurado aquele que vigia, e guarda os seus vestidos, para que não ande nu, e não se vejam as suas vergonhas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '16', 'E os congregaram no lugar que em hebreu se chama Armagedon.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '17', 'E o sétimo anjo derramou a sua taça no ar, e saiu grande voz do templo do céu, do trono, dizendo: Está feito.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '18', 'E houve vozes, e trovões, e relâmpagos, e um grande terremoto, como nunca tinha havido desde que há homens sabre a terra: tal foi este tão grande terremoto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '19', 'E a grande cidade fendeu-se em três partes, e as cidades das nações caíram; e da grande Babilónia se lembrou Deus, para lhe dar o cálix do vinho da indignação da sua ira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '20', 'E toda a ilha fugiu; e os montes não se acharam.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '16', '21', 'E sobre os homens caiu do céu uma grande saraiva, pedras do peso de um talento; e os homens blasfemaram de Deus por causa da praga da saraiva: porque a sua praga era mui grande.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '1', 'E VEIO um dos sete anjos que tinham as sete taças, e falou comigo, dizendo-me: Vem, mostrar-te-ei a condenação da grande prostituta que está assentada sobre muitas águas;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '2', 'Com a qual se prostituíram os reis da terra; e os que habitam na terra se embebedaram com o vinho da sua prostituição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '3', 'E levou-me em espírito a um deserto, e vi uma mulher assentada sobre uma besta de cor de escarlate, que estava cheia de nomes de blasfémia; e tinha sete cabeças e dez chifres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '4', 'E a mulher estava vestida de púrpura e de escarlate, e adornada com ouro, e pedras preciosas e pérolas; e tinha na sua mão um cálix de ouro cheio das abominações e da imundície da sua prostituição;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '5', 'E na sua testa estava escrito o nome: Mistério, a grande Babilónia, a mãe das prostituições e abominações da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '6', 'E vi que a mulher estava embriagada do sangue dos santos, e do sangue das testemunhas de Jesus. E, vendo-a eu maravilhei-me com grande admiração.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '7', 'E o anjo me disse: Porque te admiras? Eu te direi o mistério da mulher, e da besta que a traz, a qual tem sete cabeças e dez chifres.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '8', 'A besta que viste foi e já não é, e há de subir do abismo, e irá à perdição; e os que habitam na terra (cujos nomes não estão escritos no livro da vida, desde a fundação do mundo) se admirarão, vendo a besta que era e já não é, mas que virá.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '9', 'Aqui há sentido, que tem sabedoria. As sete cabeças são sete montes, sobre os quais a mulher está assentada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '10', 'E são também sete reis; cinco já caíram, e um existe; outro ainda não é vindo; e, quando vier, convém que dure um pouco de tempo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '11', 'E a besta que era e já não é, é ela também o oitavo, e é dos sete, e vai à perdição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '12', 'E os dez chifres que viste são dez reis, que ainda não receberam o reino, mas receberão poder como reis por uma hora, juntamente com a besta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '13', 'Estes têm um mesmo intento, e entregarão o seu poder e autoridade à besta.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '14', 'Estes combaterão contra o Cordeiro, e o Cordeiro os vencerá, porque é o SENHOR dos senhores e o Rei dos reis; vencerão os que estão com ele, chamados, e eleitos, e fiéis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '15', 'E disse-me: As águas que viste, onde se assenta a prostituta, são povos, e multidões, e nações, e línguas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '16', 'E os dez chifres que viste na besta são os que aborrecerão a prostituta, e a porão desolada e nua, e comerão a sua carne, e a queimarão no fogo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '17', 'Porque Deus tem posto em seus corações que cumpram o seu intento, e tenham uma mesma ideia, e que dêem à besta o seu reino, até que se cumpram as palavras de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '17', '18', 'E a mulher que viste é a grande cidade que reina sobre os reis da terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '1', 'E DEPOIS destas coisas vi descer do céu outro anjo, que tinha grande poder, e a terra foi iluminada com a sua gloria.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '2', 'E clamou fortemente com grande voz, dizendo: Caiu, caiu a grande Babilónia, e se tornou morada de demónios, e coito de todo o espírito imundo, e coito de toda a ave imunda e aborrecível.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '3', 'Porque todas as nações beberam do vinho da ira da sua prostituição, e os reis da terra se prostituíram com ela; e os mercadores da terra se enriqueceram com a abundância de suas delícias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '4', 'E ouvi outra voz do céu, que dizia: Sai dela, povo meu, para que não sejas participante dos seus pecados, e para que não incorras nas suas pragas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '5', 'Porque já os seus pecados se acumularam até ao céu, e Deus se lembrou das iniquidades dela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '6', 'Tornai-lhe a dar como ela vos tem dado, e retribuí-lhe em dobro conforme as suas obras: no cálix em que vos deu de beber, dai-lhe a ela em dobro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '7', 'Quanto ela se glorificou, e em delícias esteve, foi-lhe outro tanto de tormento e pranto, porque diz em seu coração: Estou assentada como rainha, e não sou viúva, e não verei o pranto.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '8', 'Portanto, num dia virão as suas pragas, a morte, e o pranto, e a fome; e será queimada no fogo; porque é forte o SENHOR Deus que a julga.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '9', 'E os reis da terra, que se prostituíram com ela, e viveram em delícias, a chorarão, e sobre ela prantearão quando virem o fumo do seu incêndio ;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '10', 'Estando de longe pelo temor do seu tormento, dizendo: Ai! ai daquela grande Babilónia, aquela forte cidade! Pois numa hora veio o seu juízo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '11', 'E sobre ela choram e lamentam os mercadores da terra; Porque ninguém mais compra as suas mercadorias:');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '12', 'Mercadorias de ouro, e de prata, e de pedras preciosas, e de pérolas, e de linho fino, e de púrpura, e de seda, e de escarlate; e toda a madeira odorífera, e todo o vaso de marfim. e todo o vaso de madeira preciosíssima, de bronze e de ferro, e de mármore;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '13', 'E cinamomo, e amomo, e perfume, e mirra, e incenso, e vinho, e azeite, e flor de farinha, e trigo, e cavalgaduras, e ovelhas; e mercadorias de cavalos, e de carros, e de corpos e de almas de homens.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '14', 'E o fruto do desejo da tua alma foi-se de ti; e todas as coisas gostosas e excelentes se foram de ti, e não mais as acharás.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '15', 'Os mercadores dessas coisas, que com elas se enriqueceram, estarão de longe, pelo teor do seu tormento, chorando, e lamentando,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '16', 'E dizendo: Ai, ai daquela grande cidade! que estava vestida de linho fino, de púrpura, de escarlate; e adornada com ouro e pedras preciosas e pérolas! Porque numa hora foram assoladas tantas riquezas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '17', 'E todo o piloto, e todo o que navega em naus, e todo o marinheiro, e todos os que negoceiam no mar se puseram de longe;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '18', 'E, vendo o fumo do seu incêndio, clamaram, dizendo: Que cidade é semelhante a esta grande cidade?');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '19', 'E lançaram pó sobre as suas cabeças, e clamaram, chorando, e lamentando, e dizendo: Ai, ai daquela grande cidade! Na qual todos os que tinham naus no mar se enriqueceram em razão da sua opulência: porque numa hora foi assolada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '20', 'Alegra-te sobre ela, ó céu, e vós, santos apóstolos e profetas; porque já Deus julgou a vossa causa quanto a ela.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '21', 'E um forte anjo levantou uma pedra como uma grande mó, e lançou-a no mar, dizendo: Com igual ímpeto será lançada Babilónia, aquela grande cidade, e não será jamais achada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '22', 'E em ti não se ouvirá mais a voz de harpistas, e de músicos, e de flautistas, e de trombeteiros, e nenhum artífice de arte alguma se achará mais em ti; e ruído de mó em ti se não ouvirá mais;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '23', 'E luz de candeia não mais luzirá em ti, a voz de esposo e de esposa não mais em ti se ouvirá; porque os teus mercadores eram os grandes da terra; porque todas as nações foram enganadas pelas tuas feitiçarias.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '18', '24', 'E nela se achou o sangue dos profetas, e dos santos, e de todos os que foram mortos na terra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '1', 'E DEPOIS destas coisas, ouvi no céu como que uma grande voz de uma grande multidão, que dizia: Aleluia: Salvação, e glória, e honra, e poder pertencem ao SENHOR nosso Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '2', 'Porque verdadeiros e justos são os seus juízos, pois julgou a grande prostituta, que havia corrompido a terra com a sua prostituição, e das mãos dela vingou o sangue dos seus servos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '3', 'E outra vez disseram: Aleluia. E o fumo dela sobe para todo o sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '4', 'E os vinte e quatro anciãos, e os quatro animais, prostraram-se e adoraram a Deus, assentado no trono, dizendo: Ámen, Aleluia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '5', 'E saiu uma voz do trono, que dizia: Louvai o nosso Deus, vós, todos os seus servos, e vós que o temeis, assim pequenos como grandes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '6', 'E ouvi como que a voz de uma grande multidão, e como que a voz de muitas águas e como que a voz de grandes trovões, que dizia: Aleluia: pois já o SENHOR Deus Todo-Poderoso reina.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '7', 'Regozijemo-nos, e alegremo-nos, e demos-lhe glória; porque vindas são as bodas do Cordeiro, e já a sua esposa se aprontou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '8', 'E foi-lhe dado que se vestisse de linho fino, puro e resplandecente; porque o linho fino são as justiças dos santos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '9', 'E disse-me: Escreve: Bem-aventurados aqueles que são chamados à ceia das bodas do Cordeiro. E disse-me: Estas são as verdadeiras palavras de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '10', 'E eu lancei-me a seus pés para o adorar; mas ele disse-me: Olha não faças tal: sou teu conservo, e de teus irmãos, que têm o testemunho de Jesus: adora a Deus; porque o testemunho de Jesus é o espirito de profecia.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '11', 'E vi o céu aberto, e eis um cavalo branco; e o que estava assentado sobre ele chama-se Fiel e Verdadeiro; e julga e peleja com justiça.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '12', 'E os seus olhos eram como chama de fogo; e sobre a sua cabeça havia muitos diademas; e tinha um nome escrito, que ninguém sabia senão ele mesmo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '13', 'E estava vestido de uma veste salpicada de sangue; e o nome pelo qual se chama é a Palavra de Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '14', 'E seguiam-no os exércitos no céu em cavalos brancos, e vestes de linho fino, branco e puro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '15', 'E da sua boca saía uma aguda espada, para ferir com ela as nações; e ele as regerá com vara de ferro; e ele mesmo é o que pisa o lagar do vinho do furor e da ira do Deus Todo-Poderoso.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '16', 'E no vestido e na sua coxa tem escrito este nome: Rei dos reis, e Senhor dos senhores,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '17', 'E vi um anjo, que estava no sol, e clamou com grande voz, dizendo a todas as aves que voavam pelo meio do céu: Vinde, e ajuntai-vos à ceia do grande Deus;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '18', 'Para que comais a carne dos reis, e a carne dos tributos, e a carne dos fortes, e a carne dos cavalos e dos que sobre eles se assentam; e a carne de todos os homens, livres e servos, pequenos e grandes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '19', 'E vi a besta, e os reis da terra, e os seus exércitos reunidos, para fazerem guerra àquele que estava assentado sobre o cavalo, e ao seu exército.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '20', 'E a besta foi presa, e com ela o falso profeta, que diante dela fizera os sinais, com que enganou os que receberam o sinal da besta, e adoraram a sua imagem. Estes dois foram lançados vivos no ardente lago de fogo e de enxofre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '19', '21', 'E os demais foram mortos com a espada que saía da boca do que estava assentado sobre o cavalo, e todas as aves se fartaram das suas carnes.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '1', 'E VI descer do céu um anjo, que tinha a chave do abismo, e uma grande cadeia na sua mão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '2', 'Ele prendeu o dragão, a antiga serpente, que é o Diabo e Satanás, e amarrou-o por mil anos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '3', 'E lançou-o no abismo, e ali o encerrou, e pôs selo sobre ele, para que mais não engane as nações, até que os mil anos se acabem. E depois importa que seja solto por um pouco de tempo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '4', 'E vi tronos; e assentaram-se sobre eles, e foi-lhes dado o poder de julgar; e vi as almas daqueles que foram degolados pelo testemunho de Jesus, e pela palavra de Deus, e que não adoraram a besta, nem a sua imagem, e não receberam o sinal em suas testas nem em suas mãos; e viveram e reinaram com Cristo durante mil anos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '5', 'Mas os outros mortos não reviveram, até que os mil anos se acabaram. Esta é a primeira ressurreição.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '6', 'Bem-aventurado e santo aquele que tem parte na primeira ressurreição: sobre este não tem poder a segunda morte; mas serão sacerdotes de Deus e de Cristo, e reinarão com ele mil anos.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '7', 'E, acabando-se os mil anos, Satanás será solto da sua prisão,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '8', 'E sairá a enganar as nações que estão sobre os quatro cantos da terra, Gog e Magog, cujo número é como a areia do mar, para as ajuntar em batalha.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '9', 'E subiram sobre a largura da terra, e cercaram o arraial dos santos e a cidade amada; mas desceu fogo do céu, e os devorou.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '10', 'E o diabo, que os enganava, foi lançado no lago de fogo e enxofre, onde está a besta e o falso profeta; e de dia e de noite serão atormentados para todo o sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '11', 'E vi um grande trono branco, e o que estava assentado sobre ele, de cuja presença fugiu a terra e o céu; e não se achou lugar para ele.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '12', 'E vi os mortos, grandes e pequenos, que estavam diante do trono, e abriram-se os livros; e abriu-se outro livro, que é o da vida: e os mortos foram julgados pelas coisas que estavam escritas nos livros, segundo as suas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '13', 'E deu o mar os mortos que nele havia; e a morte e o inferno deram os mortos que neles havia; e foram julgados cada um segundo as suas obras.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '14', 'E a morte e o inferno foram lançados no lago de fogo: esta é a segunda morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '20', '15', 'E aquele que não foi achado escrito no livro da vida foi lançado no lago de fogo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '1', 'E VI um novo céu, e uma nova terra. Porque já o primeiro céu e a primeira terra passaram, e o mar já não existe.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '2', 'E eu, João, vi a santa cidade, a nova Jerusalém, que de Deus descia do céu, adereçada como uma esposa ataviada para o seu marido.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '3', 'E ouvi uma grande voz do céu, que dizia: Eis aqui o tabernáculo de Deus com os homens, pois com eles habitará, e eles serão o seu povo, e o mesmo Deus estará com eles, e será o seu Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '4', 'E Deus limpará de seus olhos toda a lágrima; e não haverá mais morte, nem pranto, nem clamor, nem dor; porque já as primeiras coisas são passadas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '5', 'E o que estava assentado sobre o trono disse: Eis que faço novas todas as coisas. E disse-me: Escreve; porque estas palavras são verdadeiras e fiéis.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '6', 'E disse-me mais: Está cumprido Eu sou o Alfa e o Omega, o princípio e o fim. A quem quer que tiver sede, de graça lhe darei da fonte da água da vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '7', 'Quem vencer, herdará todas as coisas e eu serei seu Deus, e ele será meu filho.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '8', 'Mas, quanto aos tímidos, e aos incrédulos, e aos abomináveis, e aos homicidas, e aos devassos, e aos feiticeiros, e aos idólatras e a todos os mentirosos, a sua parte será no lago que arde com fogo e enxofre; o que é a segunda morte.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '9', 'E veio um dos sete anjos que tinham as sete taças cheias das últimas sete pragas, e falou comigo, dizendo: Vem, mostrar-te-ei a esposa, a mulher do Cordeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '10', 'E levou-me em espírito a um grande e alto monte, e mostrou-me a grande cidade, a santa Jerusalém, que de Deus descia do céu.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '11', 'E tinha a glória de Deus; e a sua luz era semelhante a uma pedra preciosíssima, como a pedra de jaspe, como o cristal resplandecente.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '12', 'E tinha um grande e alto muro com doze portas, e nas portas doze anjos, e nomes escritos sobre elas, que são os nomes das doze tribos de Israel.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '13', 'Da banda do levante tinha três portas, da banda do norte três portas, da banda do sul três portas, da banda do poente três portas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '14', 'E o muro da cidade tinha doze fundamentos, e neles os nomes dos doze apóstolos do Cordeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '15', 'E aquele que falava comigo tinha uma cana de ouro, para medir a cidade e as suas portas, e o seu muro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '16', 'E a cidade estava situada em quadrado; e o seu comprimento era tanto como a sua largura. E mediu a cidade com a cana até doze mil estádios: e o seu comprimento, largura e altura eram iguais.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '17', 'E mediu o seu muro, de cento e quarenta e quatro côvados, conforme à medida de homem, que é a de um anjo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '18', 'E a fábrica do seu muro era de jaspe, e a cidade de ouro puro, semelhante a vidro puro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '19', 'E os fundamentos do muro da cidade estavam adornados de toda a pedra preciosa. O primeiro fundamento era jaspe; o segundo, safira; o terceiro, calcedónia; o quarto, esmeralda;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '20', 'O quinto, sardónica; o sexto, sárdio; o sétimo, crisólito; o oitavo, berilo; o nono, topázio; o décimo, crisópraso; o undécimo, jacinto; o duodécimo; ametista.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '21', 'E as doze portas eram doze pérolas: cada uma das portas era uma pérola; e a praça da cidade de ouro puro, como vidro transparente,');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '22', 'E nela não vi templo, porque o seu templo é o SENHOR Deus Todo-Poderoso, e o Cordeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '23', 'E a cidade não necessita de sol nem de lua, para que nela resplandeça, porque a glória de Deus a tem alumiado, e o Cordeiro é a sua lâmpada.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '24', 'E as nações andarão à sua luz; e os reis da terra trarão para ela a sua glória e honra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '25', 'E as suas portas não se fecharão de dia, porque ali não haverá noite.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '26', 'E a ela trarão a glória e honra das nações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '21', '27', 'E não entrará nela coisa alguma que contamine, e cometa abominação e mentira; mas só os que estão inscritos no livro da vida do Cordeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '1', 'E MOSTROU-ME o rio puro da água da vida, claro como cristal, que procedia do trono de Deus e do Cordeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '2', 'No meio da sua praça, e de uma e da outra banda do rio, estava a árvore da vida, que produz doze frutos, dando seu fruto de mês em mês; e as folhas da árvore são para a saúde das nações.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '3', 'E ali nunca mais haverá maldição contra alguém; e nela estará o trono de Deus e do Cordeiro, e os seus servos o servirão.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '4', 'E verão o seu rosto, e nas suas testas estará o seu nome.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '5', 'E ali não haverá mais noite, e não necessitarão de lâmpada nem de luz do sol, porque o SENHOR Deus os alumia; e reinarão para todo o sempre.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '6', 'E disse-me: Estas palavras são fiéis e verdadeiras; e o SENHOR, o Deus dos santos profetas, enviou o seu anjo, para mostrar aos seus servos as coisas que em breve hão de acontecer.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '7', 'Eis que presto venho: Bem-aventurado aquele que guarda as palavras da profecia deste livro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '8', 'E eu, João, sou aquele que vi e ouvi estas coisas. E, havendo-as ouvido e visto, prostrei-me aos pés do anjo que mas mostrava para o adorar.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '9', 'E disse-me: Olha não faças tal; porque eu sou conservo teu e de teus irmãos, os profetas, e dos que guardam as palavras deste livro. Adora a Deus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '10', 'E disse-me: Não seles as palavras da profecia deste livro; porque próximo está o tempo.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '11', 'Quem é injusto faça injustiça ainda, e quem está sujo, suje-se ainda; e quem é justo, faça justiça ainda; e quem é santo, seja santificado ainda.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '12', 'E, eis que cedo venho, e o meu galardão está comigo, para dar a cada um segundo a sua obra.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '13', 'Eu sou o Alfa e o Omega, o princípio e o fim, o primeiro e o derradeiro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '14', 'Bem-aventurados aqueles que lavam as suas vestiduras no sangue do Cordeiro, para que tenham direito à árvore da vida, e possam entrar na cidade pelas portas.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '15', 'Ficarão de fora os cães e os feiticeiros, e os que se prostituem, e os homicidas, e os idólatras; e qualquer que ama e comete a mentira.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '16', 'Eu, Jesus, enviei o meu anjo, para vos testificar estas coisas nas igrejas: eu sou a raiz e a geração de David, a resplandecente estrela da manhã.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '17', 'E o Espírito e a esposa dizem: Vem. E quem ouve, diga: Vem. E quem tem sede, venha; e quem quiser, tome de graça da água da vida.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '18', 'Porque eu testifico a todo aquele que ouvir as palavras da profecia deste livro que, se alguém lhes acrescentar alguma coisa, Deus fará vir sobre ele as pragas que estão escritas neste livro;');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '19', 'E se alguém tirar quaisquer palavras do livro desta profecia, Deus tirará a sua parte da árvore da vida, e da cidade santa, que estão escritas neste livro.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '20', 'Aquele que testifica estas coisas diz: Certamente cedo venho. Ámen. Ora vem, Senhor Jesus.');
INSERT INTO novoTestamento (idLivro, capitulo, versiculo, descricao) VALUES ('27', '22', '21', 'A graça de nosso SENHOR Jesus Cristo seja com todos vós. Ámen.');